INSERT INTO property VALUES (1,6084, 'hr', 'definition','Treset se dobiva iz tresetišta tako da ga se izreže iz zemlje; zatim se oblikuje u brikete, koji se mogu upotrebljavati kao gorivo. Treset se nalazi u slojevima debljine nekoliko metara. U nekim zemljama upotrebljavaju se termoelektrane na pogon tresetom. Treset se također upotrebljava za oplemenjivanje tla.', 0);
