INSERT INTO property VALUES (1,6109, 'hr', 'definition','Stabilna, bezbojna, negoriva i neeksplozivna tekućina niske toksičnosti; upotrebljava se pri kemijskom čišćenju i kao industrijsko otapalo, u proizvodnji lijekova i za čišćenje metala.', 0);
