INSERT INTO property VALUES (1,8143, 'hr', 'definition','Površinski kop, u kojem je vrijedna stijena izložena uklanjanjem gornjeg sloja. Ugljen, brojne nemetalne rude i rude bogate metalima (željezom i bakrom) kopaju se na ovaj način. Sinonim: otvoreni kop.', 0);
INSERT INTO property VALUES (1,8144, 'hr', 'definition','Meki srebrnobijeli element alkalno zemljane skupine metala, koji se uglavnom nalazi u obliku celestita i stroncijanita. Njegovi spojevi gore tamnocrvenim plamenom i upotrebljavaju se za izradu pirotehničkih sredstava fire works.', 0);
