INSERT INTO property VALUES (1,8866, 'hr', 'definition','Materijal od odbačenih guma motornih vozila koji se može reciklirati.', 0);
INSERT INTO property VALUES (1,8875, 'hr', 'definition','Kalorična vrijednost je toplina po jedinici mase nastala potpunim izgaranjem određene tvari. Kalorične vrijednosti se upotrebljavaju za izražavanje energetske vrijednosti goriva, a obično su izražene u megadžulima po kilogramu. Također se upotrebljavaju za mjerenje sadržaj energije u prehrambenim proizvodima, tj. energije proizvedene kada hrana oksidira u tijelu. Jedinice su ovdje kilodžuli po gramu. Kalorične vrijednosti se mjere s pomoću kalorimetrijske bombe (uređaja koji se sastoji od čvrstog kontejnera – cilindra – u kojem je uzorak zabrtvljen s viškom kisika, s električnim paljenjem). Toplina izgaranja pri stalnom volumenu može se izračunati iz porasta temperature nakon izgaranja).', 0);
INSERT INTO property VALUES (1,8876, 'hr', 'definition','Upotreba izvora opskrbe iz područja grada/općine ili regije, koji se prema potrebi može odmah upotrijebiti.', 0);
INSERT INTO property VALUES (1,8877, 'hr', 'definition','Upotreba kemijskih ili bioloških tvari za namjerno suzbijanje nepoželjnih (štetnih) životinja i biljaka.', 0);
INSERT INTO property VALUES (1,8886, 'hr', 'definition','Uvjet ili vremenski rok u ugovoru, instrumentu ili sporazumu za čije izvršenje je nadležna vlada, kojemu nedostaje preciznosti, razlikovnih obilježja ili utvrđenih ograničenja.', 0);
INSERT INTO property VALUES (1,8887, 'hr', 'definition','Svako nisko zemljište omeđeno višim zemljištem, osobito duguljasta, relativno velika, lagano nagnuta depresija površine Zemlje, koja je najčešće smještena između dvije planine ili između brdskih ili planinskih lanaca, u kojima se često nalazi potok koji istječe iz doline. Dolina se obično razvija erozijom toka potoka, ali može se formirati i zbog faulting.', 0);
INSERT INTO property VALUES (1,8892, 'hr', 'definition','Ocijenjen, vrednovan ili procijenjen element ili sastavnica biološke zajednice i njezine nežive okoline unutar okoliša.', 0);
INSERT INTO property VALUES (1,8893, 'hr', 'definition','Srebrnobijeli, rastezljivi metal otporan prema koroziji; upotrebljava se u željeznim legurama i kao meta za rendgenske zrake.', 0);
INSERT INTO property VALUES (1,8894, 'hr', 'definition','Namjerno ili obijesno uništavanje osobne ili javne imovine koje počini vandal.', 0);
INSERT INTO property VALUES (1,8895, 'hr', 'definition','Vrsta koja je nestala iz nekog područja zbog nepovoljnih uvjeta u okolišu.', 0);
INSERT INTO property VALUES (1,8898, 'hr', 'definition','Parcijalni tlak vodene pare u atmosferi. Za čvrsto tijelo ili tekućinu, tlak pare u ravnoteži s tekućinom ili čvrstim tijelom.', 0);
INSERT INTO property VALUES (1,8899, 'hr', 'definition','Proziran površinski premaz koji se nanosi u tekućem stanju, a zatim se promijeni u kruto stanje; svi lakovi su otopine smolastih tvari u otapalu.', 0);
INSERT INTO property VALUES (1,8906, 'hr', 'definition','Agens ili organizam koji djeluje kao nositelj ili prenositelj bolesti na čovjeka.', 0);
INSERT INTO property VALUES (1,8908, 'hr', 'definition','Broj biljaka koje rastu na nekom dijelu zemlje.', 0);
INSERT INTO property VALUES (1,8909, 'hr', 'definition','Uzgajanje zeljastih biljaka za hranu.', 0);
INSERT INTO property VALUES (1,8910, 'hr', 'definition','Proučavanje odnosa između biljaka i njihove okoline.', 0);
INSERT INTO property VALUES (1,8912, 'hr', 'definition','Čovjekova selekcija određenih genotipa u biljnoj populaciji jer oni posjeduju poželjne fenotipske značajke.', 0);
INSERT INTO property VALUES (1,8913, 'hr', 'definition','Jestivo, miješano glicerinsko ulje koje se dobiva iz biljaka (voća, listova i sjemenki), uključujući sjemenke pamuka, sjemenke lana, tung i kikiriki; upotrebljava se u jestivim uljima, mekim mastima za prhko tijesto (maslu), sapunima i u medicini, te kao ulje za sušenje boje.', 0);
INSERT INTO property VALUES (1,8918, 'hr', 'definition','Svaki između različitih postupaka, bilo spolnih ili nespolnih, kojima biljka stvara jednu ili više jedinki sličnih sebi.', 0);
INSERT INTO property VALUES (1,8920, 'hr', 'definition','Svaka od različitih zeljastih biljaka koje se upotrebljavaju za hranu.', 0);
INSERT INTO property VALUES (1,8921, 'hr', 'definition','Otpad koji se sastoji uglavnom od povrća, koji mogu razgraditi mikroorganizmi.', 0);
INSERT INTO property VALUES (1,8922, 'hr', 'definition','1) Biljke nekog područja razmatrane općenito ili kao zajednice, ali ne taksonomski; ukupan biljni pokrivač u određenom području ili na Zemlji u cjelini. 
INSERT INTO property VALUES (1,8925, 'hr', 'definition','Zajednica biljaka ili biljnog života koji imaju zajednička razlikovna obilježja.', 0);
INSERT INTO property VALUES (1,8926, 'hr', 'definition','Svako prijevozno sredstvo za prijevoz ljudi ili predmeta.', 0);
INSERT INTO property VALUES (1,8929, 'hr', 'definition','Prozračan fluid od sitnih čestica suspendiranih u zraku, nastao i ispušten kao nusproizvod rada strojeva s kotačima ili vozila koja pokreću vlastiti motori s unutarnjim izgaranjem.', 0);
INSERT INTO property VALUES (1,8931, 'hr', 'definition','Službeno povremeno ispitivanje automobila, kamiona, čamca/brodića, zrakoplova ili drugih prijevoznih sredstava kako bi se utvrdila sukladnost u konstrukciji ili u radu s pravnim standardima za sigurnost ili onečišćujućim emisijama.', 0);
INSERT INTO property VALUES (1,8934, 'hr', 'definition','Sektor gospodarstva u kojem se sva komercijalna poduzeća bave proizvodnjom i prodajom opreme za prijevoz ljudi,  dobara i materijala zemljom, zrakom ili vodom.', 0);
INSERT INTO property VALUES (1,8940, 'hr', 'definition','Proces dovođenja ili odvođenja zraka, prirodnim ili mehaničkim sredstvima, u neki prostor ili iz njega; takav zrak može, ali ne mora biti klimatiziran.', 0);
INSERT INTO property VALUES (1,8944, 'hr', 'definition','Male životinje i insekti koji mogu biti štetni i koje je teško kontrolirati kada se pojave u velikom broju.', 0);
INSERT INTO property VALUES (1,8946, 'hr', 'definition','Svaki svitkovac iz potkoljena kralješnjaka, čija su obilježja koštani ili hrskavični kostur i dobro razvijeni mozak: ovoj skupini pripadaju ribe, vodozemci, gmazovi, ptice i sisavci.', 0);
INSERT INTO property VALUES (1,8951, 'hr', 'definition','Grana medicine u kojoj se liječe bolesti i ozljede životinja.', 0);
INSERT INTO property VALUES (1,8955, 'hr', 'definition','Dugačak visok most, koji obično podupiru mnogi lukovi, preko kojega ide željeznička pruga ili cesta preko doline ili drugoga sličnog područja na nižoj razini.', 0);
INSERT INTO property VALUES (1,8959, 'hr', 'definition','Periodično gibanje male amplitude i visoke frekvencije, koje je značilno za elastična tijela.', 0);
INSERT INTO property VALUES (1,1759, 'hr', 'definition','Iznos ili količina onečišćujućeg materijala koja se nalazi u sredstvu prijenosa koje teče stalnom brzinom toka, za razliku od iznenadnog ili jako velikog priljeva.', 0);
INSERT INTO property VALUES (1,1761, 'hr', 'definition','Uzgajanje kultura duž linija koje povezuju točke jednake nadmorske visine, čime se smanjuje gubitak gornjih slojeva tla zbog erozije, i time povećava kapacitet tla da zadrži vodu i smanjuje se onečišćenje vode tlom.', 0);
INSERT INTO property VALUES (1,1762, 'hr', 'definition','Sporazum ili dogovor dviju ili više osoba kojim se stvara obveza da će se nešto uraditi, ili da se neće uraditi. Njegove osnove su nadležne stranke, predmet ugovora, pravni obziri, uzajamnost dogovora, te uzajamnost obveze.', 0);
INSERT INTO property VALUES (1,1763, 'hr', 'definition','Komercijalni pružatelj usluga, obično vezan pismenim ugovorom, odgovoran za čišćenje i uklanjanje prljavštine, smeća ili drugih materijala koji narušavaju izgled bilo kojeg mjesta.', 0);
INSERT INTO property VALUES (1,1769, 'hr', 'definition','Planirano korištenje pozorno kontrolirane vatre kako bi se ostvarili unaprijed utvrđeni ciljevi gospodarenja. Gorenje se određuje u kombinaciji vremena, vlažnosti goriva, vlažnosti tla, te uvjeta u vezi s organizacijom goriva koji omogućuju ostvarenje ciljeva gospodarenja, a da se požar ipak ograniči na planiranu površinu.', 0);
INSERT INTO property VALUES (1,1772, 'hr', 'definition','Regulirano geografsko područje u kojem je lovljenje, hvatanje i ubijanje divljih životinja radi hrane ili iz zabave dopušteno, često uz određena ograničenja ili propise.', 0);
INSERT INTO property VALUES (1,1773, 'hr', 'definition','Moć osobe ili organiziranog skupa osoba da upravljaju, usmjeravaju, nadziru, ograničavaju, reguliraju, uređuju, vode ili nadgledaju.', 0);
INSERT INTO property VALUES (1,1775, 'hr', 'definition','1) Velika, gusto naseljena, nekontrolirano proširena urbana naselja raštrkanih kuća i zgrada koja su se formirala rastom i srastanjem pojedinih gradova ili naselja.
INSERT INTO property VALUES (1,1776, 'hr', 'definition','Međunarodni sporazum o određenoj temi', 0);
INSERT INTO property VALUES (1,1777, 'hr', 'definition','Energija dobivena iz tradicionalnih izvora, kao što su ugljen, drvo, plin, itd., za razliku od alternativnih izvora energije kao što su solarna energija, energija plime i oseke, energija vjetra itd.', 0);
INSERT INTO property VALUES (1,1781, 'hr', 'definition','Odvajanje visokoradioaktivnog materijala dok se radioaktivnost ne smanji na željenu razinu.', 0);
INSERT INTO property VALUES (1,1782, 'hr', 'definition','Ulje koje se upotrebljava kao sredstvo za hlađenje, bilo uz poticano kruženje ili s prirodnim kruženjem.', 0);
INSERT INTO property VALUES (1,1784, 'hr', 'definition','Uređaj koji pomaže u odstranjivanju topline vode koja se upotrebljava kao sredstvo za hlađenje u elektranama.', 0);
INSERT INTO property VALUES (1,1786, 'hr', 'definition','Voda koja se upotrebljava za snižavanje temperature, na primjer, ozračenih elemenata iz nuklearnog reaktora ili pogona nekog stroja.', 0);
INSERT INTO property VALUES (1,1792, 'hr', 'definition','Kemijski element; jedan od najvažnijih obojenih metala; rastezljiv i savitljiv metal koji se nalazi u različitim rudama i upotrebljava se u industriji, tehnici i umjetnosti, i u čistom obliku i u obliku legure.', 0);
INSERT INTO property VALUES (1,1793, 'hr', 'definition','Rast niskog drveća koje se stalno reže u kratkim intervalima; iz starih panjeva izbijaju nove mladice.', 0);
INSERT INTO property VALUES (1,1794, 'hr', 'definition','Kostur određenih pojedinačnih i kolonijalnih žarnjaka (Anthozoa Coelenterate); građeni uglavnom od kalcijeva karbonata.', 0);
INSERT INTO property VALUES (1,1796, 'hr', 'definition','Koraljni grebeni su izgrađeni od kostura koralja koji grade grebene, malih primitivnih morskih životinja, te drugih morskih životinja i algi tijekom tisuća godina. Oni se pojavljuju u čistim, plitkim i suncem obasjanim morima. Koraljni grebeni jedan su od najproduktivnijih i najraznolikijih ekosustava, a procjenjuje se da prinos iz tih područja iznosi oko 12% svjetskog ulova ribe. Oni su vrlo osjetljivi na bilo kakve promjene u svom okruženju, a posebno onečišćenja, jer ona čine vodu neprozirnom. Koralji moraju imati svjetla kako bi se mogla odvijati fotosinteza algi. Kao i stabla, koralji odražavaju ekološke uvjete u kojima rastu, što upućuje na onečišćenje mora, temperaturu morske površine i druge vodene uvjete.', 0);
INSERT INTO property VALUES (1,1798, 'hr', 'definition','Najviši red u životinjskom carstvu, čija su obilježja svitak, živčani konop i škržne pukotine; obuhvaća plaštenjake (Urochordate), bezlubanjce (Lancelets) i kralješnjake.', 0);
INSERT INTO property VALUES (1,1799, 'hr', 'definition','Slučajno pregrijavanje dijela nuklearnog reaktora gdje se odvija fisija, što uzrokuje taljenje gorivnih elemenata i ostalih dijelova reaktora, i time potencijalno dovodi do katastrofalnih posljedica u kojima opasne razine radioaktivnog materijala mogu biti ispuštene u okoliš.', 0);
INSERT INTO property VALUES (1,1800, 'hr', 'definition','Tanka, lagana, porozna vanjske kore hrasta plutnjaka, koji se uvelike upotrebljava kao izolator i za čepove za boce, bačve itd.', 0);
INSERT INTO property VALUES (1,1807, 'hr', 'definition','Fizička veza koja povezuje dva područja staništa koja se razlikuje od staništa na obje strane. Koridore upotrebljavaju organizmi da se kreću uokolo, a da ne moraju napustiti odabrano stanište.', 0);
INSERT INTO property VALUES (1,1808, 'hr', 'definition','Proces u kojem kruta tvar, osobito metal, nestaje i mijenja se zbog kemijske aktivnosti.', 0);
INSERT INTO property VALUES (1,1809, 'hr', 'definition','Kemijski agens koji usporava ili sprječava korozijsku reakciju.', 0);
INSERT INTO property VALUES (1,1813, 'hr', 'definition','Industrija koja proizvodi tvari kojima se poboljšava izgled tijela.', 0);
INSERT INTO property VALUES (1,1814, 'hr', 'definition','Zračenja koja se sastoje od atomskih jezgri, osobito protona, vrlo visoke energije koje dospijevaju do Zemlje iz svemira. Neka kozmička zračenja imaju vrlo mnogo energije i mogu prodrijeti 1,6 km ili više u Zemlju.', 0);
INSERT INTO property VALUES (1,1817, 'hr', 'definition','Odnos između troškova određene aktivnosti i njezinih koristi za određenu zajednicu.', 0);
INSERT INTO property VALUES (1,1818, 'hr', 'definition','Pokušaj da se procijeni, usporedi i često i opravda ukupna cijena ili gubitak od određene aktivnosti ili izdatka uz prednost ili uslugu koju ona osigurava.', 0);
INSERT INTO property VALUES (1,1820, 'hr', 'definition','Povećanje ili povišenje iznosa novca koji treba platiti ili koji se potražuje u razmjeni robe i usluga.', 0);
INSERT INTO property VALUES (1,1822, 'hr', 'definition','Norma koja se upotrebljava za osiguravanje naknade troškova pojedincima ili organizacijama za bilo koji nastali trošak ili pruženu uslugu.', 0);
INSERT INTO property VALUES (1,1823, 'hr', 'definition','Smanjenje nastalih troškova ili sniženje nastalih troškova u iznosu novca ili iznosu novca koji se potražuje u razmjeni robe i usluga.', 0);
INSERT INTO property VALUES (1,1824, 'hr', 'definition','U  ekonomiji, vrijednost proizvodnih faktora kojom se koristi tvrtka u proizvodnji ili distribuciji robe i usluga ili bavljenju tim objema aktivnostima.', 0);
INSERT INTO property VALUES (1,1825, 'hr', 'definition','Iznos novca koji treba platiti zbog promjene fizičkog, biološkog, kemijskog  i radiološkog integriteta zraka, vode  i drugih medija koje je izazvao ili potakao čovjek.', 0);
INSERT INTO property VALUES (1,1826, 'hr', 'definition','Troškovi koji nastaju pri gospodarenju nacionalnim financijskim sredstvima.', 0);
INSERT INTO property VALUES (1,1827, 'hr', 'definition','Najekonomičnije prirodno vlakno, koje se dobiva iz biljaka iz roda Gossypium, a upotrebljava se za tkanine, užad, materijal za podlaganje, kao i za izradu umjetnih tkanina i celuloze.', 0);
INSERT INTO property VALUES (1,1837, 'hr', 'definition','Područje koje obuhvaća više od jednog grada i čije granice su oblikovane prema nekim biološkim, političkim, administrativnim, ekonomskim i demografskim kriterijima.', 0);
INSERT INTO property VALUES (1,1842, 'hr', 'definition','Sud nadležan za prizive i kontrolu sudskih odluka, uključujući ovlasti poništavanja odluka nižih sudova ili prvostupanjskih sudova.', 0);
INSERT INTO property VALUES (1,1850, 'hr', 'definition','Zanimanje ili djelatnost u kojoj je potrebna spretnost ruku ili vještina u umjetničkom izražavanju.', 0);
INSERT INTO property VALUES (1,1854, 'hr', 'definition','Pomoć i potpora banaka i drugih financijskih institucija u pribavljanju novca ili robe a da se pri tome ne zahtijeva trenutačno plaćanje.', 0);
INSERT INTO property VALUES (1,1860, 'hr', 'definition','Kršenje zakona za koje je predviđena kazna države putem kaznenog postupka.', 0);
INSERT INTO property VALUES (1,1862, 'hr', 'definition','Dio  zakonodavstva koji se bavi ponašanjem koje je toliko štetno za društvo u cjelini da ga država zabranjuje zakonskim propisom, kazneno goni i kažnjava.', 0);
INSERT INTO property VALUES (1,1864, 'hr', 'definition','Opći izraz koji se odnosi na ograničenje koncentracije iznad koje tvar može izazvati opasne učinke na žive organizme.', 0);
INSERT INTO property VALUES (1,1865, 'hr', 'definition','Maksimalno opterećenje koje određeni sustav može podnijeti prije kvara odnosno sloma.', 0);
INSERT INTO property VALUES (1,1870, 'hr', 'definition','Velik tropski gmaz porodice Crocodylidae: red Crocodylia. Imaju široku glavu, gubicu koja se sužava prema kraju, veliku čeljust i debeo, čvrsti vanjski oklop.', 0);
INSERT INTO property VALUES (1,1874, 'hr', 'definition','Problem zaštite bilja uvelike se promijenio od 1945. god. Sada postoji čitav arsenal kemikalija za suzbijanje poljoprivrednih štetnika i bolesti, ali taj razvoj događaja sam po sebi ima mnoge nedostatke. Takve sofisticirane tehnike dostupne su samo manjem dijelu poljoprivrednika; u većem dijelu svijeta standard zaštite bilja i dalje je izuzetno nizak. Osim toga, suvremene metode zaštite bilja kritizirane su zbog prevelikog oslanjanja na kemijske kontrole. Biološke kontrole, i prirodne i umjetne, zanemarene su. U nekim slučajevima koji uključuju zlouporabu poljoprivrednih kemikalija, kulture se moraju zaštititi od samih mjera namijenjenih njihovoj zaštiti. U međuvremenu štetnici i bolesti koje su već bile lokalizirane nastavljaju se širiti u cijelom svijetu.', 0);
INSERT INTO property VALUES (1,1875, 'hr', 'definition','Poljoprivredna tehnika kod koje se, od jednoga godišnjeg doba do drugoga, svako polje zasijava nekom kulturom (usjevom) u redovitim rotacijama, s tim da se svaka kultura ponavlja u razmacima od po nekoliko godina. Plodored smanjuje rizik od iscrpljivanja pojedinih hranjivih tvari u tlima. U sustavima rotacija neka žitarica često se uzgaja u prvoj godini, nakon čega slijedi kultura lisnatog povrća u drugoj godini, te pašnjak u trećem. Posljednji usjev su obično mahunarke; takve biljke mogu vratiti dušik u tlo. Unatoč tome, visoki prinosi obično su ovisni o stalnom dodavanju kemijskih gnojiva na tlo.', 0);
INSERT INTO property VALUES (1,1877, 'hr', 'definition','Svi neupotrebljivi dijelovi biljaka koji ostanu na poljima poslije žetve.', 0);
INSERT INTO property VALUES (1,1880, 'hr', 'definition','Mjesto, često označeno oznakama, svjetlima ili stupovima, na kojem se može prijeći preko ceste, željezničke pruge itd.', 0);
INSERT INTO property VALUES (1,1881, 'hr', 'definition','Mostovi i tuneli osigurani za prijelaz životinja preko cesta i željezničke pruge. Željeznička i cestovna infrastruktura zapreka su migraciji životinja u divljini.', 0);
INSERT INTO property VALUES (1,1885, 'hr', 'definition','Relativno hlapljiv tekući bitumen koji se sastoji uglavnom od ugljikovodika, s tragovima sumporovih, dušikovih ili kisikovih spojeva; može se vaditi iz zemlje u tekućem stanju.', 0);
INSERT INTO property VALUES (1,1888, 'hr', 'definition','Putovanje brodom iz zabave, obično uz pristajanje u više luka.', 0);
INSERT INTO property VALUES (1,1889, 'hr', 'definition','Razred životinja koje pripadaju člankonošcima (Arthropoda) i imaju člankaste noge i čeljusti, dva para ticala i člankasto tijelo u hitinskom oklopu.', 0);
INSERT INTO property VALUES (1,1890, 'hr', 'definition','Velika skupina biljaka, koja obuhvaća steljnjače  (Thallophyta), mahovine (Bryophyta) i papratnjače (Pteridophyta), od kojih su posljednje kriptogami.', 0);
INSERT INTO property VALUES (1,1891, 'hr', 'definition','Stvaranje kristalnih tvari iz otopina ili talina.', 0);
INSERT INTO property VALUES (1,1892, 'hr', 'definition','Grana znanosti koja se bavi geometrijskim opisom kristala i njihovom unutarnjom strukturom.', 0);
INSERT INTO property VALUES (1,1898, 'hr', 'definition','Posebno uzgojene ili gojenjem poboljšane biljke.', 0);
INSERT INTO property VALUES (1,1899, 'hr', 'definition','Uzgajanje i njegovanje bilja izvan njihovih prirodnih staništa (tj. U vrtovima, rasadnicima, arboretumima).', 0);
INSERT INTO property VALUES (1,1902, 'hr', 'definition','Svaki postupak ili pristup koji se upotrebljava za pripremu zemljišta ili tla za uzgoj novih kultura ili za pospješevanje i poboljšanje rasta postojećih kultura.', 0);
INSERT INTO property VALUES (1,1909, 'hr', 'definition','Proces kojim se ispoljavaju ili unaprjeđuju sposobnosti ili mogućnosti svojstvene uvjerenjima, običajima, umjetničkom izražavanju i znanju  nekog naroda.', 0);
INSERT INTO property VALUES (1,1911, 'hr', 'definition','Svaka zgrada ili konstrukcija koja se upotrebljava za izvođenje programa i djelatnosti koje uključuju umjetničko ili drugo izražavanje koje potiče produbljivanje ili razvijanje duha.', 0);
INSERT INTO property VALUES (1,1912, 'hr', 'definition','Sveukupna naslijeđena uvjerenja, običaji, umjetničko izražavanje i znanje koje su prenijeli preci.', 0);
INSERT INTO property VALUES (1,1915, 'hr', 'definition','Kulturni pokazatelji daju informacije o društvima, što može biti zanimljivo čak i kada se ne pokušavaju ocijeniti kulture tih društava s bilo kojega normativnog stajališta. Kulturni pokazatelji također mogu imati evaluacijsku svrhu, a ona uključuju eksplicitne ili implicitne normativne kriterije.', 0);
INSERT INTO property VALUES (1,1921, 'hr', 'definition','Sva uvriježena vjerovanja, oblici društva i materijalne značajke koje čine poseban kompleks tradicije neke rasne ili društvene skupine.', 0);
INSERT INTO property VALUES (1,1926, 'hr', 'definition','Svi školski predmeti odnosno kolegiji ili tečajevi za obrazovanje koji se osiguravaju u određenoj školi, koledžu, sveučilištu, programu obrazovanja odraslih, tehničkoj instituciji ili nekom drugom obrazovnom programu.', 0);
INSERT INTO property VALUES (1,1928, 'hr', 'definition','Skupni obrazac uobičajenih aktivnosti koji se obično prenosi s naraštaja na naraštaj i, u nekim slučajevima, koji ima snagu zakona.', 0);
INSERT INTO property VALUES (1,1929, 'hr', 'definition','Carine koje se zaračunavaju na robu pri uvozu u zemlju ili izvozu iz zemlje.', 0);
INSERT INTO property VALUES (1,1930, 'hr', 'definition','Radnja ili proces sječe ili iskorjenjivanja drveća koje raste u svrhu proizvodnje drva kao materijala.', 0);
INSERT INTO property VALUES (1,1931, 'hr', 'definition','Sol ili ester cijanatne kiseline, koji sadrži radikal OCN.', 0);
INSERT INTO property VALUES (1,1933, 'hr', 'definition','Svaki spoj u skupini spojeva koji sadrže CN skupinu i koji se dobivaju iz cijanovodične kiseline, HCN.', 0);
INSERT INTO property VALUES (1,1941, 'hr', 'definition','Oluja čija je značajka konvergiranje i povećanje vrtnje vjetra oko zone niskog tlaka (središta) prema kojoj se snažno povlači iz zone visokog tlaka. Njezino kruženje je suprotno smjeru kretanja kazaljki na satu oko centra na sjevernoj polutki, a odvija se u smjeru kazaljke na satu na južnoj polutki.', 0);
INSERT INTO property VALUES (1,1945, 'hr', 'definition','Grana bioloških znanosti koja se bavi strukturom, ponašanjem, rastom i razmnožavanjem stanica te djelovanjem i kemijom sastavnica stanice.', 0);
INSERT INTO property VALUES (1,1946, 'hr', 'definition','Stupanj do kojeg neki agens posjeduje određeno destruktivno djelovanje prema određenim stanicama ili posjedovanje takvih djelovanja; upotrebljava se osobito u odnosu na lizu stanica imunološkim fenomenima i na lijekove protiv novotvorina koji selektivno ubijaju stanice koje se dijele.', 0);
INSERT INTO property VALUES (1,1950, 'hr', 'definition','Komercijalni subjekt za preradu ili prodaju mlijeka i mlječnih proizvoda.', 0);
INSERT INTO property VALUES (1,1951, 'hr', 'definition','Proizvodnja hrane iz mlijeka ili mliječnih proizvoda.', 0);
INSERT INTO property VALUES (1,1952, 'hr', 'definition','Proizvodi koji se proizvode od mlijeka, npr. maslac, sir, laktoza itd.', 0);
INSERT INTO property VALUES (1,1955, 'hr', 'definition','Konstrukcija izgrađena preko vodotoka ili kanala.', 0);
INSERT INTO property VALUES (1,1956, 'hr', 'definition','Ozljeda ili oštećenje koje narušava funkciju ili stanje osobe ili stvari.', 0);
INSERT INTO property VALUES (1,1958, 'hr', 'definition','Ozljeda ili oštećenje koje je rezultat planiranog kretanja oružanih snaga ili taktičkih vježbi koje simuliraju ratne  operacije, a izvode se u svrhu obuke i vrednovanja.', 0);
INSERT INTO property VALUES (1,1959, 'hr', 'definition','Sveukupni pristupi i mjere kojima se osigurava da ljudsko djelovanje ili prirodni fenomeni neće izazvati štetu. To podrazumijeva osmišljavanje i provedbu dugogodišnjih politika i programa za uklanjanje ili sprječavanje štete prouzročene katastrofama.', 0);
INSERT INTO property VALUES (1,1968, 'hr', 'definition','Prijevoz tvari koje mogu imati negativne učinke na zdravlje, prouzročiti požare ili eksplozije, u druge države ili područja u svrhu obavljanja međunarodne trgovine.', 0);
INSERT INTO property VALUES (1,1969, 'hr', 'definition','Roba ili proizvodi pri čijem se korištenju, prijevozu i sl. Pojavljuju mnoge opasnosti ili rizici.', 0);
INSERT INTO property VALUES (1,1971, 'hr', 'definition','Propis o rukovanju stvarima ili tvarima koje mogu predstavljati značajan rizik za zdravlje, sigurnost ili imovinu, i kojima je pri prijevozu obično potrebno obratiti posebnu pozornost.', 0);
INSERT INTO property VALUES (1,1974, 'hr', 'definition','Prikupljanje i skupljanje pojedinačnih činjenica, statistike ili drugih informacija.', 0);
INSERT INTO property VALUES (1,1975, 'hr', 'definition','Vrednovanje digitalnih podataka, npr. podataka predstavljenih nizom kodnih znakova.', 0);
INSERT INTO property VALUES (1,1979, 'hr', 'definition','Kompilacija podataka, činjenica i zapisa s pomoću računala koja je organizirana za pogodan pristup, upravljanje i ažuriranje.', 0);
INSERT INTO property VALUES (1,1981, 'hr', 'definition','Medij na kojem se podaci mogu zapisati, i koji se obično može lagano prenositi, kao što su kartice, traka, papir ili diskovi.', 0);
INSERT INTO property VALUES (1,1984, 'hr', 'definition','Recipročni prijenos pojedinačnih činjenica, statistike ili pojedinih informacija između dviju ili više strana u svrhu unaprjeđenja znanja sudionika razmjene.', 0);
INSERT INTO property VALUES (1,1986, 'hr', 'definition','Bilo koja operacija ili kombinacija operacija s podacima, koja uključuje sve što se događa s podacima od vremena kada su opaženi ili prikupljeni do vremena kada se unište.', 0);
INSERT INTO property VALUES (1,1988, 'hr', 'definition','Politike, postupci ili uređaji namijenjeni održavanju integriteta ili sigurnosti informacijskih elemenata pri njihovom pohranjivanju ili prijenosu.', 0);
INSERT INTO property VALUES (1,1989, 'hr', 'definition','Sveukupni specijalizirani postupci i metode koji se upotrebljavaju za čuvanje, prikupljanje ili bilježenje pojedinačnih elemenata informacija.', 0);
INSERT INTO property VALUES (1,1992, 'hr', 'definition','Svaka od nekoliko tehnika, kao što su radioaktivno datiranje, dendrokronologija ili datiranje s pomoću sedimentnih slojeva, za utvrđivanje starosti stijena, paleontoloških ili arheoloških uzoraka itd.', 0);
INSERT INTO property VALUES (1,1993, 'hr', 'definition','Izotop koji nastaje radioaktivnim raspadanjem nekoga drugog izotopa. Taj novonastali izotop ima određena fizikalna i kemijska svojstva koja se razlikuju od onih na matičnom izotopu, a može biti i radioaktivan.', 0);
INSERT INTO property VALUES (1,1996, 'hr', 'definition','Postojani organoklorovih insekticid, također poznat kao diklor-difenil-trikloretan, koji je uveden četrdesetih godina 20. stoljeća i naširoko se upotrebljavao zbog njegove postojanosti (što znači da ga je nepotrebno ponovno primijeniti), njegove niske toksičnosti za sisavce, te jednostavnosti upotrebe i jeftine proizvodnje. Proširio se po cijelom svijetu i, s drugim organoklorinima, imao je razoran učinak na vrste koje se nalaze visoko u hranidbenim lancima, a posebno na uspješno razmnožavanje određenih grabljivica. DDT je vrlo stabilan, relativno netopiv u vodi, ali visoko topiv u mastima. Zdravstveni učinci na ljude nisu jasni, ali je manje toksičan od njemu srodnih spojeva. Otrovan je za druge kralješnjake, osobito ribe, i pohranjuju se u masnom tkivu životinja kao nesmrtonosna količina manje toksičnog DDE. Zbog njegovog utjecaja na biljni i životinjski svijet njegova je upotreba u većini zemalja danas zabranjena ili strogo ograničena.', 0);
INSERT INTO property VALUES (1,2000, 'hr', 'definition','Nešto što netko nekome duguje.', 0);
INSERT INTO property VALUES (1,2001, 'hr', 'definition','Naknade ili iznos novca koji je potreban za plaćanje kamata na nepodmireni dug, glavnice dospjelih serijskih obveznica, kao i potrebnih doprinosa za amortizacijski ili otplatni fond za terminske obveznice.', 0);
INSERT INTO property VALUES (1,2002, 'hr', 'definition','Mehaničko odstranjivanje vode iz mokrog krutog tijela odlijevanjem tekućine bez narušavanja donjeg sedimenta ili  taloga.', 0);
INSERT INTO property VALUES (1,2003, 'hr', 'definition','Osnovni organizacijski koncept vodstva i proces prenošenja i delegiranja moći i ovlasti iz više razine prema podređenim razinama unutar administrativne/upravljačke hijerarhije u cilju promicanja neovisnosti, odgovornosti i bržeg donošenja odluka u primjeni ili tumačenju pravila i postupaka na potrebe tih razina.', 0);
INSERT INTO property VALUES (1,2004, 'hr', 'definition','Šume umjerenog pojasa sastoje se od stabala koja ujesen odbacuju svoje lišće, koje se nalaze na istoku SAD-a, u Zapadnoj Europi od Alpa do Skandinavije, te u istočnoj Aziji. Tvrdo drvo ovih šuma iskorištava se od 16. stoljeća. Stabla bjelogoričnih šuma obično rode orašastim plodovima i krilatim sjemenkama.', 0);
INSERT INTO property VALUES (1,2005, 'hr', 'definition','Drvo koje ujesen odbacuje svoje lišće i na kojem raste novo u proljeće.', 0);
INSERT INTO property VALUES (1,2006, 'hr', 'definition','Izvršenje ovlasti zastupanja u bilo kojoj fazi nekog poduhvata u kojoj se mogu provesti promjene da bi se izmijenio njegov utjecaj na povijesna i kulturna dobra.', 0);
INSERT INTO property VALUES (1,2015, 'hr', 'definition','Uklanjanje ili neutralizacija kemijske, biološke ili radioaktivne kontaminacije s osobe, objekta ili područja.', 0);
INSERT INTO property VALUES (1,2020, 'hr', 'definition','Sudska oduka kojom se najavljuju pravne posljedice utvrđenih činjenica.', 0);
INSERT INTO property VALUES (1,2022, 'hr', 'definition','Područje otvorenog mora izvan epikontinentalnog pojasa (kontinentalnog šelfa).', 0);
INSERT INTO property VALUES (1,2024, 'hr', 'definition','Odlaganje krutog otpada ili mulja tako da se otpad odveze na otvoreno more, obično teglenicom, te bacanjem tog otpada u duboko more.', 0);
INSERT INTO property VALUES (1,2025, 'hr', 'definition','Ribolov u najdubljim dijelovima mora.', 0);
INSERT INTO property VALUES (1,2029, 'hr', 'definition','Zajednički naziv za 41 vrstu papkara koji čine porodicu jelena (Cervidae) u redu parnoprstaša (Artiodactyla); mužjaci imaju rogovlje.', 0);
INSERT INTO property VALUES (1,2031, 'hr', 'definition','Radnja ili proces zaštite građana ili bilo kojega geografskog područja pripremom za upotrebu ili upotrebom vojnih sredstava za odupiranje napadu neprijatelja.', 0);
INSERT INTO property VALUES (1,2035, 'hr', 'definition','1) Otpadanje lišća s biljaka izazvano herbicidima kao što su Agent Orange, diuron, triazini, koji svi ometaju fotosintezu. Korištenje defolijanata, kao u Vijetnamu ili pri krčenju džungle u poljoprivredne svrhe, može trajno uništiti tropske šume. Nakon što se pokrov od drveća ukloni, tlo je izloženo eroziji i dragocjene hranjive tvari brzo nestaju ispiranjem.
INSERT INTO property VALUES (1,2036, 'hr', 'definition','Uklanjanje šuma i niskog raslinja u cilju povećanja površine obradivog zemljišta ili iskorištavanja drva za izgradnju ili u industrijske svrhe. Šuma i nisko raslinje posjeduju vrlo visok kapacitet zadržavanja vode, čime se sprječava otjecanja oborinskih voda.', 0);
INSERT INTO property VALUES (1,2039, 'hr', 'definition','Sposobnost kemijske ili biološke razgradnje.', 0);
INSERT INTO property VALUES (1,2040, 'hr', 'definition','Vrsta organske kemijske reakcije u kojoj se neki spoj po fazama pretvara u jednostavniji spoj.', 0);
INSERT INTO property VALUES (1,2041, 'hr', 'definition','Rezultat kumulativne aktivnosti poljoprivrednika, kućanstava i industrije, koji svi pokušavaju poboljšati svoje socio-ekonomsko blagostanje. Te aktivnosti imaju tendenciju da budu kontraproduktivne iz nekoliko razloga. Ljudi možda ne mogu u potpunosti razumjeti dugoročne posljedice svojih aktivnosti na prirodnim resursima. Najvažniji načini na koji se ljudsko djelovanje upliće u globalni ekološki sustav su sljedeći: a) sagorijevanje fosilnih goriva koje može udvostručiti koncentraciju ugljičnog dioksida u atmosferi do sredine sljedećeg stoljeća, kao i daljnje vrlo značajno povećanje emisija sumpora i dušika; b) širenje poljoprivrede i šumarstva i s njim povezana upotreba gnojiva (dušika i fosfora) značajno mijenjaju prirodnu cirkulaciju tih hranjivih tvari, c) povećano iskorištavanje slatkovodnih sustava, i u svrhu navodnjavanja u poljoprivredi i industriji, i za zbrinjavanje otpada.', 0);
INSERT INTO property VALUES (1,2042, 'hr', 'definition','Kemikalije koje nastaju kao posljedica djelomičnog raspadanja ili kemijskog raspadanja tvari.', 0);
INSERT INTO property VALUES (1,2044, 'hr', 'definition','1) Uklanjanje masnoća s vune s pomoću kemikalija.
INSERT INTO property VALUES (1,2048, 'hr', 'definition','Niz procesa kojima se razne vrste tiskarske tinte i boja uklanjaju s vlakana celuloze za izradu papira tijekom pred-obrade i recikliranje recikliranih proizvoda od papira. To je osobito potrebno gdje se zahtijeva visoka kvaliteta i bjelina gotovog proizvoda.', 0);
INSERT INTO property VALUES (1,2052, 'hr', 'definition','Delta je golema tvorevina zemlje u obliku lepeze, ili niske ravnice, formirana od uzastopnih slojeva sedimenta koji se spustio iz viših krajeva do ušća nekih rijeka, kao što su Nil, Mississippi i Ganges. Talog bogat hranjivim tvarima nanose rijeke na mjestu gdje, odnosno ispred kojeg, rijeka utječe u more. Delte nastaju kada rijeke donose i nanose sedimente brže da nego što ih mogu ukloniti valovi oceanskih struja. Važnost delta je prvi otkrio pračovjek, kojeg su privukle zbog svog obilja biljnog i životinjskog svijeta. Povezivanje vodenih putova kroz delte kasnije je osiguralo prirodne putove za navigaciju i trgovinu, te je otvorilo pristup u unutrašnjost. Delte su vrlo plodna, a često i vrlo gusto naseljena područja. One bi bile ozbiljno ugrožene poplavama u slučaju bilo kakvog porasta razine mora.', 0);
INSERT INTO property VALUES (1,2053, 'hr', 'definition','Želja, sposobnost i spremnost pojedinca na kupnju proizvoda ili usluge. Potrošač mora imati sredstva ili mogućnost pribavljanja sredstava kako bi pretvorio tu želju u potražnju. Potražnja kupca za određenom robom je raspored količina te robe koju bi pojedinac kupio po mogućim alternativnim cijenama u određenom trenutku u vremenu.', 0);
INSERT INTO property VALUES (1,2054, 'hr', 'definition','Sustav vladanja u kojem najveću vlast imaju ljudi i ljudi izravno izvršavaju tu vlast, ili to rade njihovi zastupnici izabrani na slobodnim izborima.', 0);
INSERT INTO property VALUES (1,2055, 'hr', 'definition','Porast broja jedinki u nekoj populaciji.', 0);
INSERT INTO property VALUES (1,2056, 'hr', 'definition','Postupan uzorak promjena u rastu ljudske populacije u određenoj regiji ili državi, od brzog porasta stope rađanja i stope smrtnosti, do izravnavanja stope rasta zbog smanjene plodnosti i drugih čimbenika.', 0);
INSERT INTO property VALUES (1,2060, 'hr', 'definition','Statističko proučavanje najvažnijih statističkih pokazatelja populacije i populacijske dinamike.', 0);
INSERT INTO property VALUES (1,2062, 'hr', 'definition','Djelatnost rušenja zgrada ili drugih konstrukcija.', 0);
INSERT INTO property VALUES (1,2064, 'hr', 'definition','Otpaci građevinskog materijala ili šuta nastala rušenjem zgrada ili drugih građevinskih konstrukcija.', 0);
INSERT INTO property VALUES (1,2069, 'hr', 'definition','Znanost o određivanju starosti stabala proučavanjem godova. Također se upotrebljava za tumačenje prethodnih okolišnih i klimatskih varijacija ispitivanjem određenih vrsta drveća. Temelji se na teoriji da širina godova pokazuje količinu  oborina i temperaturu u godini u kojoj su formirani.', 0);
INSERT INTO property VALUES (1,2070, 'hr', 'definition','Mjerenje promjera stabala koje raste od zemlje s pomoću dendrometra koji se može upotrebljavati i za mjerenje visine stabala.', 0);
INSERT INTO property VALUES (1,2071, 'hr', 'definition','1) Gubitak dušika iz tla biološkim ili kemijskim sredstvima. To je gubitak plinova, nepovezan s gubicima u fizikalnim procesima kao što su putem iscjedaka (ocjednih voda).
INSERT INTO property VALUES (1,2072, 'hr', 'definition','Trenutne metode za kontrolu emisije NOx-a u motornim vozilima uključuju usporavanje paljenja, čime se povećava omjer zrak/gorivo, ubrizgavanje vode u cilindre, smanjivanje omjera kompresije, te ponovnu povratnu cirkulaciju ispušnih plinova. Za stacionarne izvore, jedna metoda smanjenja emisija je korištenje goriva koje stvara manje NOx-a ili  izmjena procesa izgaranja ubrizgavanjem pare u komoru za izgaranje.', 0);
INSERT INTO property VALUES (1,2084, 'hr', 'definition','Proces kojim se onečišćujući materiial taloži iz atmosfere i akumulira u ekološkim sustavima.', 0);
INSERT INTO property VALUES (1,2088, 'hr', 'definition','Ukidanje ili smanjenje državne kontrole nad gospodarskim djelatnostima nekoga komercijalnog subjekta, industrije ili gospodarskog sektora.', 0);
INSERT INTO property VALUES (1,2098, 'hr', 'definition','Odstranjivanje soli, npr. Iz vode ili tla.', 0);
INSERT INTO property VALUES (1,2099, 'hr', 'definition','1) Pogoni/postrojenja za vađenje pitke vode iz morske vode uklanjanjem soli, obično destilacijom.
INSERT INTO property VALUES (1,2103, 'hr', 'definition','Širok, otvoren, relativno pust dio zemljišta s malim brojem životnih oblika i malo oborina.', 0);
INSERT INTO property VALUES (1,2105, 'hr', 'definition','1) Razvoj pustinjskih uvjeta kao rezultat ljudske aktivnosti ili klimatskih promjena.
INSERT INTO property VALUES (1,2109, 'hr', 'definition','Korektivne i preventivne mjere usvojene protiv širenje pustinja, uključuju navodnjavanje, sadnju stabala i trave, podizanje ograda za osiguranje pješčanih dina, kao i razborito gospodarenje vodnim resursima.', 0);
INSERT INTO property VALUES (1,2115, 'hr', 'definition','Grafički prikaz, osobito detaljan plan za neku građevinu ili proizvodni objekt.', 0);
INSERT INTO property VALUES (1,2119, 'hr', 'definition','Proces uklanjanja tvari sa sorpcijskim značajkama procesom suprotnim od adsorpcije ili apsorpcije.', 0);
INSERT INTO property VALUES (1,2124, 'hr', 'definition','Uklanjanje sumpora, npr. iz rastaljenih metala i naftnog ulja.  Ostaci sumpora u gorivu završavaju kao sumporni dioksid, kada gorivo izgori, što uzrokuje kisele kiše.', 0);
INSERT INTO property VALUES (1,2125, 'hr', 'definition','Uklanjanje sumpora iz fosilnih goriva (ili uklanjanje sumpornog dioksida iz ispušnih plinova pri izgaranju) u svrhu smanjenja onečišćenja.', 0);
INSERT INTO property VALUES (1,2127, 'hr', 'definition','Radnja ili proces otkrivanja dokaza o kršenju državnih, pravnih ili etičkih propisa i normi.', 0);
INSERT INTO property VALUES (1,2129, 'hr', 'definition','Mehanički, električni ili kemijski uređaj koji automatski prepoznaje i bilježi ili registrira podražaj, kao što su promjene tlaka ili temperature u okolišu, električni signal ili zračenja iz radioaktivnog materijala.', 0);
INSERT INTO property VALUES (1,2131, 'hr', 'definition','Sredstvo aktivno na površini koje se upotrebljava za uklanjanje prljavštine i masti s različitih površina i materijala. Rani deterdženti sadržavali su alkil sulfonate, koji su se pokazali otpornima na bakterijsku razgradnju, što je uzrokovalo pjenušanje u rijekama i poteškoće u postrojenjima za pročišćavanje otpadnih voda. Ti tvrdi deterdženti zamijenjeni su tijekom 1960-ih mekim biorazgradivim deterdžentima. Ljudi se i dalje pribojavaju korištenja fosfata u deterdžentima, čime se pospješuje proces eutrofikacije. Još se nije pojavila zadovoljavajuća zamjena.', 0);
INSERT INTO property VALUES (1,2139, 'hr', 'definition','Metoda koja se upotrebljava za ocjenu ili vrednovanje količine, kvalitete, djelovanja, događanja itd.', 0);
INSERT INTO property VALUES (1,2140, 'hr', 'definition','Svaka mjera, sredstvo provođenja ili politika osmišljena tako da oslabi ili ograniči djelovanje ili napredovanje drugog sredstva, organizacije ili države.', 0);
INSERT INTO property VALUES (1,2143, 'hr', 'definition','Radnja ili proces uklanjanja otrova ili toksičnih svojstava neke tvari u tijelu.', 0);
INSERT INTO property VALUES (1,2147, 'hr', 'definition','Država koja posjeduje relativno visok stupanj industrijalizacije, infrastrukture i drugih kapitalnih ulaganja, sofisticiranu tehnologiju, vrlo veliku stopu pismenosti i napredan životni standard među svojom populacijom u cjelini.', 0);
INSERT INTO property VALUES (1,2152, 'hr', 'definition','Zemlja čiji ljudi počinju iskorištavati raspoložive resurse kako bi doveli do kontinuiranog povećanja proizvodnje robe i usluga po stanovniku.', 0);
INSERT INTO property VALUES (1,2155, 'hr', 'definition','Gospodarska pomoć ili druge vrste potpore koje se daju zemljama u razvoju za promicanje ili poticanje poboljšanja životnog standarda, institucija, infrastrukture, poljoprivredne prakse i drugih aspekata gospodarstva, kao i za rješavanje problema koji se obično povezuju sa zemljama u razvoju.', 0);
INSERT INTO property VALUES (1,2156, 'hr', 'definition','Područje koje dobiva posebnu pomoć od države za poticanje poduzetništva i tvornica koje će se tamo izgraditi.', 0);
INSERT INTO property VALUES (1,2158, 'hr', 'definition','Opis, prikaz ili koncepcija procesa ekonomskog unaprjeđenja neke regije ili ljudi.', 0);
INSERT INTO property VALUES (1,2159, 'hr', 'definition','Kombinacija kvalitete, strukture, djelovanja i tendencija koje  obilježavaju gospodarski i društveni razvoj neke zajednice ili ljudske skupine.', 0);
INSERT INTO property VALUES (1,2160, 'hr', 'definition','Čin ili proces formuliranja smjera djelovanja kojim se promiče gospodarski napredak regije ili naroda, osobito u zemljama za koje je poznato da imaju nisku razinu ekonomske produktivnosti i tehnološke sofisticiranosti.', 0);
INSERT INTO property VALUES (1,2167, 'hr', 'definition','Proces selektivne difuzije kroz membranu; obično se upotrebljava za odvajanje otopljenih tvari niske molekularne težine koje prodiru kroz membranu od koloidnih otopljenih tvari i otopljenih tvari visoke molekularne težine koje ne prodiru kroz membranu.', 0);
INSERT INTO property VALUES (1,2168, 'hr', 'definition','Jednostanične alge, od kojih su neke kolonijalne, zelene ili smeđe boje (ali sve sadrže klorofil) i sa silikatnim i često vrlo oblikovanim staničnim stijenkama. Dijatomeje čine velik udio u razini proizvođača u morskim i slatkovodnim prehrambenim lancima, i one su pridonijele stvaranju rezervi nafte. Naslage zemlje s dijatomejama bile su formirane nakupljanjem stijenki stanica kremenjašica.', 0);
INSERT INTO property VALUES (1,2173, 'hr', 'definition','Priručnik koji sadrži abecedni popis riječi s objašnjenjima, kao knjiga  u kojoj se navodi sveobuhvatan ili ograničen izbor riječi nekog jezika; obično uz identificiranje fonetskih, gramatičkih i semantičkih vrijednosti svake riječi, često s etimologijom, citatima, te smjernicama za upotrebu i ostalih informacija.', 0);
INSERT INTO property VALUES (1,2175, 'hr', 'definition','Umijeće ili znanost o poučavanju.', 0);
INSERT INTO property VALUES (1,2177, 'hr', 'definition','Motor s unutarnjim izgaranjem koji radi na termodinamičkom ciklusu u kojem je omjer kompresije naboja zraka dovoljno visok da zapali gorivo koje se naknadno ubrizgava u komoru za izgaranje.', 0);
INSERT INTO property VALUES (1,2179, 'hr', 'definition','Talog teškog ulja koji se upotrebljava kao gorivo za određene vrste dizelskih motora.', 0);
INSERT INTO property VALUES (1,2182, 'hr', 'definition','Takav razvoj stanica da su sposobne za obavljanje specijaliziranih funkcija u organima i tkivima organizama kojima pripadaju.', 0);
INSERT INTO property VALUES (1,2185, 'hr', 'definition','Onečišćenje koje proizlazi iz različitih aktivnosti  bez određenog izvora.', 0);
INSERT INTO property VALUES (1,2186, 'hr', 'definition','Spontano gibanje nepravilno raspršenih čestica (atoma i molekula) u tekućinama, plinovima i krutim tvarima.', 0);
INSERT INTO property VALUES (1,2187, 'hr', 'definition','Mulj ili zgusnuta mješavina krutih tvari iz kanalizacije s vodom koji su razgradile anaerobe bakterije.', 0);
INSERT INTO property VALUES (1,2188, 'hr', 'definition','Stroj koji uzima otpatke i od njih stvara plin kao što je metan.', 0);
INSERT INTO property VALUES (1,2190, 'hr', 'definition','Smanjenje obujma i razgradnja organskih tvari s velikom sposobnošću gnjilenja na relativno stabilne ili inertne organske i anorganske spojeve.  Probavu (digestiju) mulja obično obavljaju aerobni organizmi bez prisutnosti slobodnog kisika.', 0);
INSERT INTO property VALUES (1,2195, 'hr', 'definition','Prikaz topografije neke površine pohranjen u numeričkom  formatu. Svakom pikselu pridružene su koordinate i nadmorska visina.', 0);
INSERT INTO property VALUES (1,2196, 'hr', 'definition','Proces pretvaranja podataka u oblik koji se upotrebljava u računalima, i ti podaci se prenose ili pohranjuju digitalnom tehnologijom i izražavaju se kao niz nula (0) i jedinica (1).', 0);
INSERT INTO property VALUES (1,2200, 'hr', 'definition','Kiselina manje koncentracije.', 0);
INSERT INTO property VALUES (1,2207, 'hr', 'definition','Nusproizvod koji nastaje tijekom pripreme herbicida 2, 4, 5-T, a ponekad se stvara i pri spaljivanju kloriranih organskih spojeva. Također se može pojaviti u prirodnom obliku i široko je rasprostranjen u okolišu, osim na lokalnoj razini u izuzetno niskim koncentracijama. Značajne količine ispuštene su u industrijskoj nesreći u Sevesu 1976. god.', 0);
INSERT INTO property VALUES (1,2211, 'hr', 'definition','Tvornice i industrijski koncerni koji ne ispuštaju svoje otpadne vode izravno u javnu kanalizaciju, već izravno u vodotoke.', 0);
INSERT INTO property VALUES (1,2212, 'hr', 'definition','Drugi rang upravnih akata (ispod razine uredaba, nadređen u odnosu prema odlukama) koje donosi Vijeće ili Komisija Europskih zajednica kako bi obavljali svoje zadaće u skladu s Ugovorima. Direktive moraju biti upućene državama, a ne pojedincima, ali mnogim direktivama se daju prava pojedincima ili se dopušta pozivanje na direktivu pred općinskim sudom.', 0);
INSERT INTO property VALUES (1,2213, 'hr', 'definition','Osoba koja nema jednu ili više fizičkih sposobnosti, kao što je sposobnost hodanja ili koordinacija pokreta, npr. kao posljedica bolesti ili nesreće, ili zbog psihičkog oštećenja.', 0);
INSERT INTO property VALUES (1,2215, 'hr', 'definition','Rezultat velikoga ekološkog sloma u odnosima između čovjeka i njegovog okoliša, ozbiljan i nagli događaj (ili spor, kao što su suše) takvih razmjera da pogođena zajednica mora uložiti izvanredne napore da se s njim nosi, često uz pomoć vanjskih aktera ili međunarodnu pomoć.', 0);
INSERT INTO property VALUES (1,2216, 'hr', 'definition','Tijek ili postupak aktivnosti osmišljene u svrhu raščišćavanja krhotina ili uklanjanja štetnih tvari koje su preostale nakon ekološke nesreće, bilo prirodnog porijekla ili takve koju je izazvao čovjek, na određenom području.', 0);
INSERT INTO property VALUES (1,2218, 'hr', 'definition','Plan predviđenih intervencija kojeg se treba pridržavati u slučaju očekivanih ili eventualnih katastrofa, na temelju procjene rizika, dostupnosti ljudskih i materijalnih resursa, pripremljenosti zajednice, lokalne i međunarodne reakcije, sposobnosti itd.', 0);
INSERT INTO property VALUES (1,2219, 'hr', 'definition','Rad koji je obavljen ili agencija osnovana za analizu, planiranje, dodjelu i koordiniranje raspoloživih resursa, u svrhu pripreme, reakcije, ublažavanja i sanacije štete prouzročene ekološkom nesrećom, prirodne ili one koja je uzrokovana ljudskim djelovanjem.', 0);
INSERT INTO property VALUES (1,2220, 'hr', 'definition','Ukupne mjere koje treba poduzeti u pogledu katastrofa ili nesreća, koje se sastoje od planova i akcijskih programa osmišljenih za smanjenje izgubljenih života i štete, kako bi se organiziralo i olakšalo djelotvorno spašavanje i pomoć, te za obnovu nakon katastrofe.', 0);
INSERT INTO property VALUES (1,2221, 'hr', 'definition','Skup pristupa i mjera kojima se osigurava da ljudsko djelovanje ili prirodni fenomeni neće prouzročiti ili rezultirati katastrofom ili sličnom nesrećom. To podrazumijeva formuliranje i provedbu dugoročnih politika i programa za uklanjanje ili sprječavanje pojave katastrofa.', 0);
INSERT INTO property VALUES (1,2222, 'hr', 'definition','Novac, hrana ili druga pomoć namijenjena osobama koje su preživjele iznenadnu katastrofalnu situaciju koja je prouzročila gubitak ljudskih života, štetu ili nedaće.', 0);
INSERT INTO property VALUES (1,2227, 'hr', 'definition','Stopa protoka rijeke u određenom trenutku, povezana s količinom i brzinom protoka.', 0);
INSERT INTO property VALUES (1,2232, 'hr', 'definition','Određen patološki proces koji ima karakterističan skup znakova i simptoma koji su štetni za dobrobit pojedinca.', 0);
INSERT INTO property VALUES (1,2237, 'hr', 'definition','Agens, kao što su toplina, zračenje, ili kemijski, koji dezinficira uništavanjem, neutraliziranjem ili suzbijanjem  rasta mikroorganizama koji prenose bolesti.', 0);
INSERT INTO property VALUES (1,2238, 'hr', 'definition','Kompleks fizikalnog, kemijskog ili mehaničkog djelovanja koji se poduzima u svrhu uništavanja patogenih klica.', 0);
INSERT INTO property VALUES (1,2245, 'hr', 'definition','Razmještaj sitnih čestica u nekom mediju.', 0);
INSERT INTO property VALUES (1,2246, 'hr', 'definition','Izračun disperzije onečišćujuće tvari temelji se na korištenju modela zračne disperzije koji matematički simuliraju atmosferske uvjete i ponašanja. Modelima disperzije mogu se dobiti procjene koncentracije ili taloženja, a oni se mogu upotrebljavati i za vrednovanje scenarija i postojećih i hipotetičnih emisija.', 0);
INSERT INTO property VALUES (1,2247, 'hr', 'definition','Osobe koje su, iz različitih razloga ili zbog različitih okolnosti, bile prisiljene napustiti svoje domove.', 0);
INSERT INTO property VALUES (1,2254, 'hr', 'definition','Odlaganje i zbrinjavanje materijalnih ostataka rata, koji mogu ozbiljno ometati razvoj i prouzročiti ozljede i gubitak ljudskih života i imovine. Odlaganje ratnog otpada je problematično, jer on može biti vrlo opasan, otrovan, dugotrajan i za njega je potrebno korištenje specifične i sofisticirane tehnologije, osobito u slučaju mina i neeksplodiranih bombi koje su ostavljene na ratnim područjima.', 0);
INSERT INTO property VALUES (1,2257, 'hr', 'definition','Otapanje nekog materijala.', 0);
INSERT INTO property VALUES (1,2259, 'hr', 'definition','Dio ukupnoga organskog ugljika (svih ugljikovih atoma s kovalentnom vezom u organskim molekulama) u vodi koji prolazi kroz filter s porama promjera 0,45 mikrona.', 0);
INSERT INTO property VALUES (1,2260, 'hr', 'definition','Proces proizvodnje plina ili pare iz tekućine grijanjem tekućine u posudi i prikupljanjem i kondenzacijom pare u tekućinu.', 0);
INSERT INTO property VALUES (1,2262, 'hr', 'definition','Sektor gospodarstva u kojem se velik broj komercijalnih poduzeća bavi proizvodnjom i prodajom alkoholnih pića koja se proizvode destilacijskim procesom isparavanja i kondenzacije, kao što su votka, rum, viski i druga srodna pića.', 0);
INSERT INTO property VALUES (1,2263, 'hr', 'definition','Na temelju članka 85.(1) Ugovora o osnivanju Europske zajednice zabranjeni su svi sporazumi između poduzeća, odluke udruženja poduzeća i zajedničko djelovanje koji bi mogli utjecati na trgovinsku razmjenu između država članica i koji za cilj ili posljedicu imaju sprječavanje, ograničavanje ili narušavanje tržišnog natjecanja unutar zajedničkog tržišta. Svi sporazumi ili odluke zabranjeni na temelju članka 85.(2), automatski su ništavni ako ne postoji izuzeće Komisije temeljem članka 85.(3). Tekst članka 85. glasi: "1. Zbog nespojivosti sa zajedničkim tržištem, zabranjeni su svi sporazumi između poduzeća, odluke udruženja poduzeća i zajedničko djelovanje koji bi mogli utjecati na trgovinsku razmjenu između država članica i koji za cilj ili posljedicu imaju sprječavanje, ograničavanje ili narušavanje tržišnog natjecanja unutar zajedničkog tržišta, a osobito oni kojima se: (a) izravno ili neizravno određuju kupovne ili prodajne cijene ili drugi trgovinski uvjeti; (b) ograničavaju ili nadziru proizvodnja, tržišta, tehnički razvoj ili ulaganja; (c) dijeli tržište ili izvori nabave; (d) primjenjuju nejednaki uvjeti na iste poslove s različitim trgovačkim partnerima, čime ih se stavlja u nepovoljan položaj u odnosu na konkurenciju; (e) sklapanje ugovora uvjetuje pristankom drugih stranaka na dodatne obveze koje, po svojoj prirodi ili prema trgovačkim običajima, nemaju nikakve veze s predmetom tih ugovora."', 0);
INSERT INTO property VALUES (1,2264, 'hr', 'definition','U kontekstu zaštite okoliša, ovaj se pojam odnosi na raspršivanje onečišćujućih tvari u zrak, i ovisi o vrsti izvora onečišćenja (točkastih izvora, linijskih izvora, difuznih izvora), brzini vjetra i smjeru vjetra. Širenje može biti aktivno ili pasivno.', 0);
INSERT INTO property VALUES (1,2269, 'hr', 'definition','Opskrba toplinom, bilo u obliku pare ili tople vode, iz središnjeg izvora do skupine zgrada.', 0);
INSERT INTO property VALUES (1,2270, 'hr', 'definition','Postrojenje za grijanje svih kuća u nekom okrugu, koje se sastoji od velike, učinkovite, centralizirane kotlovnice ili od "otpadne" pare iz elektrane. Toplina se potrošačima distribuira putem niskotlačne pare ili vode visoke temperature.', 0);
INSERT INTO property VALUES (1,2275, 'hr', 'definition','Vojna lokacija na kojoj su prestale sve aktivnosti. Takva područja, koja su vrlo dobro zaštićena od vanjskih smetnji i na mnogo način manje su pogođena upotrebom zemljišta od strane ljudi nego što je to slučaj s mnogih drugim otvorenim krajobrazima, mogu sadržavati značajna prirodna staništa i rijetke ili ugrožene biljke ili životinje koje žive u divljini. Napuštena vojna područja važan su izvor prirodnih krajobraza kojima se treba upravlja i obnavljati na ekološki prihvatljiv način.', 0);
INSERT INTO property VALUES (1,2276, 'hr', 'definition','Dugi, uzak iskop umjetno iskopan u tlu; posebno, otvoreni i obično nepopločen plovni put, kanal, ili rov kojim se transportira voda za odvodnju i navodnjavanje, i obično je manji od kanala. Neki jarci mogu biti prirodne vodotoci.', 0);
INSERT INTO property VALUES (1,2279, 'hr', 'definition','Glavni nasljedni materijal. On se nalazi u kromosomima i sastoji se od molekula koje su dugački nerazgranati lanci sastavljeni od mnogih nukleotida. Svaki nukleotid je kombinacija fosforne kiseline, monosaharida deoksiriboze i jedne od četiri dušične baze: timina, citozina, adenina i guanina. Broj mogućih razmještaja nukleotida duž DNK lanca je golem. Obično su dvije DNK niti zajedno povezane paralelno specifičnim uparivanjem baza i zavijeni su poput spirale odnosno zavojnice. Replikacija DNA molekula postiže se razdvajanjem dvije niti, nakon čega slijedi izgradnja odgovarajućih niti pomoću uparivanja baza, korištenjem dviju polovica kao predloška. Mehanizmom koji uključuje RNK, struktura DNA prevodi se u proteinsku strukturu tijekom sinteze iz aminokiselina.', 0);
INSERT INTO property VALUES (1,2282, 'hr', 'definition','Materijal bilo koje vrste, bez obzira na fizički oblik, kojim se daju informacije, dokazi ili zamisli, uključujući i stavke kao što su ugovori, zaključnice o kupoprodaji, pisma, audio i video snimke, te strojno čitljive datoteke podataka.', 0);
INSERT INTO property VALUES (1,2284, 'hr', 'definition','Proces skupljanja, razvrstavanja i širenja informacija, često u potporu nekom zahtjevu ili podacima navedenim u nekoj knjizi ili članku.', 0);
INSERT INTO property VALUES (1,2285, 'hr', 'definition','Centar za prikupljanje, kodiranje i širenje zapisanog znanja koje se sveobuhvatno obrađuje u cjelovitom postupku, korištenjem raznih tehnika za osiguravanje maksimalne  dostupnosti i upotrebljivosti dokumentarnih informacija.', 0);
INSERT INTO property VALUES (1,2286, 'hr', 'definition','Vrsta različitih tipova pisanih, tiskanih ili digitaliziranih podataka kojima se prenose informacije ili dokazi, i koji se razlikuju po sadržaju, obliku ili funkciji.', 0);
INSERT INTO property VALUES (1,2287, 'hr', 'definition','Česta četveronožna životinja, osobito takva koju ljudi drže kao kućne ljubimce ili za lov ili za čuvanje imovine.', 0);
INSERT INTO property VALUES (1,2290, 'hr', 'definition','Stroj ili naprava, osobito električna, koja se upotrebljava u kućanstvu.', 0);
INSERT INTO property VALUES (1,2291, 'hr', 'definition','1) Divlja životinja koja je naučena da živi u blizini kuće i da se ne boji ljudi, 2) vrsta koja je nekada bila divlja, a sada se selektivno uzgaja za zadovoljavanje ljudskih potreba.', 0);
INSERT INTO property VALUES (1,2293, 'hr', 'definition','Goriva dobivena iz različitih izvora koja se upotrebljavaju za ogrjev u domaćinstvima.', 0);
INSERT INTO property VALUES (1,2294, 'hr', 'definition','Tekući naftni derivat koji se upotrebljava u grijalicama u domaćinstvima.', 0);
INSERT INTO property VALUES (1,2298, 'hr', 'definition','Buka koja se stvara u kućanstvu i od aktivnosti u kućanstvu.', 0);
INSERT INTO property VALUES (1,2300, 'hr', 'definition','Trgovina koja se u cijelosti odvija u zemlji; za razliku od vanjske trgovine.', 0);
INSERT INTO property VALUES (1,2301, 'hr', 'definition','Otpad koji nastaje u kućanstvima, a sastoji se od materijala i stvari koje ljudi više ne žele ili im više nisu potrebne.', 0);
INSERT INTO property VALUES (1,2304, 'hr', 'definition','Lokalitet namijenjen odlaganju otpada iz kućanstava.', 0);
INSERT INTO property VALUES (1,2305, 'hr', 'definition','Otpadne vode uglavnom iz kućanstava, poslovnih zgrada, institucija itd., koje mogu, ali ne moraju sadržavati vodu iz površinskog otjecanja, podzemnu vodu ili oborinsku vodu.', 0);
INSERT INTO property VALUES (1,2307, 'hr', 'definition','Količina neke tvari koja je potrebna za nastanak nekog učinka.', 0);
INSERT INTO property VALUES (1,2308, 'hr', 'definition','Količina ispitne tvari koja se primjenjuje. Doza se izražava kao težina ispitne tvari (g, mg) po jediničnoj težini pokusne životinje (npr. mg/kg), ili kao težina hrane ili vode za piće.', 0);
INSERT INTO property VALUES (1,2309, 'hr', 'definition','Odnos između količine neke tvari i učinka koji se može izmjeriti ili opaziti.', 0);
INSERT INTO property VALUES (1,2315, 'hr', 'definition','Početni, nepotpisani sporazum, ugovor ili međunarodni ugovor, ili zakonski propis koji još nije stupio na snagu.', 0);
INSERT INTO property VALUES (1,2316, 'hr', 'definition','Insekti koji tvore šest porodica podreda Anisoptera sa četiri velika, opnasta krila i složenim očima koji im osiguravaju oštar vid.', 0);
INSERT INTO property VALUES (1,2319, 'hr', 'definition','1) Uklanjanje podzemne ili površinske vode, ili vode iz građevina, gravitacijom ili crpljenjem.
INSERT INTO property VALUES (1,2323, 'hr', 'definition','Povremene površinske vode iz različitih izvora, kao što su oborine, topljenje snijega i topljenje vječnog leda.', 0);
INSERT INTO property VALUES (1,2326, 'hr', 'definition','Rastresiti materijal uklonjen iz rijeke, potoka i morskog plićaka pomoću strojeva kao što su bager vedričar s istovarnom katarkom, bager sajlaš ili usisni bager.', 0);
INSERT INTO property VALUES (1,2327, 'hr', 'definition','Uklanjanje krutog materijala s dna vodenog područja.', 0);
INSERT INTO property VALUES (1,2330, 'hr', 'definition','Vodeni talog nastao u procesu ekstrakcije ulja iz maslina;  sastoji se od  kombinacije vode i maslina i vode koja se upotrebljava u procesima ekstrakcije i pranja. Također sadrži određeni postotak mineralnih spojeva i organskih tvari koje su samo djelomično biorazgradive.', 0);
INSERT INTO property VALUES (1,2332, 'hr', 'definition','Upotreba ribarskih mreža velike duljine i dubine koje je prikladno opisati kao "zidovi smrti" zbog velikog broja morskih sisavaca, ptica i kornjača koje su postale zarobljene u njima. Deklaracija iz Tarawe iz 1989. god. formulirana je na 20. Južnopacifičkom forumu, a njezin je cilj zabrana mreža potegača u južnom Pacifiku. U lipnju 1992. god. UN je zabranio mreže potegače u svim svjetskim oceanima.', 0);
INSERT INTO property VALUES (1,2333, 'hr', 'definition','Radnja bušenja rupa u zemlji u cilju pronalaženja vode ili nafte, za potrebe geoloških istraživanja itd.', 0);
INSERT INTO property VALUES (1,2336, 'hr', 'definition','Osnovna konstrukcija na koju se za vrijeme bušenja montira stroj za bušenje i pomoćna oprema.', 0);
INSERT INTO property VALUES (1,2339, 'hr', 'definition','Voda koja je prikladna za pijenje, nije opasna za zdravlje i njezina kakvoća je obično uređena zakonom.', 0);
INSERT INTO property VALUES (1,2340, 'hr', 'definition','Područje koje okružuje postrojenje za crpljenje vode u kojem su neki oblici korištenja tla ograničeni ili zabranjeni u svrhu zaštite podzemne vode.', 0);
INSERT INTO property VALUES (1,2343, 'hr', 'definition','Osiguravanje i čuvanje pitke vode, odnosno količine pohranjene pitke vode, za korištenje od strane općine ili grada, ili nekoga drugog korisnika pitke vode.', 0);
INSERT INTO property VALUES (1,2344, 'hr', 'definition','Direktiva o kakvoći površinske vode namijenjene za pitku vodu definira tri kategorije za pročišćavanje vode (A1, A2, A3), od jednostavne fizičke obrade i dezinfekcije do intenzivne fizikalne i kemijske obrade. Obrada koja će se upotrijebiti ovisi o kakvoći iscrpljene vode. Direktiva se koristi imperativnim vrijednostima za parametre za koje je poznato da imaju negativan učinak na zdravlje, a  također i preporučenim vrijednostima za one koji su manje štetni. Postoji i direktiva koja dopunjuje Direktivu "o crpljenju površinske vode" navođenjem metoda mjerenja i učestalosti uzorkovanja, kao i potrebnih analiza.', 0);
INSERT INTO property VALUES (1,2347, 'hr', 'definition','Razdoblje neuobičajeno suhog vremena koje je dovoljno produženo da nedostatak vode uzrokuje ozbiljnu hidrološku neravnotežu (kao što su štete na usjevima, nestašica vode) u sušom pogođenom području.', 0);
INSERT INTO property VALUES (1,2350, 'hr', 'definition','Mjere poduzete u cilju suzbijanja, ublažavanja ili uklanjanja štete uzrokovane u ekološkom sustavu, osobito usjeva, produženim razdobljem suhog vremena.', 0);
INSERT INTO property VALUES (1,2354, 'hr', 'definition','Kemijska tvar koja se uzima kroz usta ili koja služi za vanjsku primjenu kao lijek za prevenciju, dijagnozu, liječenje ili izlječenje bolesti, za olakšanje bolova ili za kontrolu ili poboljšanje tjelesnog ili mentalnog blagostanja.', 0);
INSERT INTO property VALUES (1,2358, 'hr', 'definition','Čišćenje tkanina itd. otapalom osim vode.', 0);
INSERT INTO property VALUES (1,2359, 'hr', 'definition','Nakupljanje čestica i plinova dok dolaze u dodir s tlom,  vodom ili raslinjem na površini Zemlje.', 0);
INSERT INTO property VALUES (1,2360, 'hr', 'definition','Sustav ekstenzivne poljoprivrede koji omogućuje proizvodnju usjeva bez navodnjavanja u područjima s ograničenim oborinama. Suho ratarenje podrazumijeva očuvanje vlažnosti tla pokrivanjem, čestim ugarom, održavanje kultivirane površine unakrsnim oranjem, ponovnom obradom tla nakon kiše i odstranjivanjem sveg korova koji bi potrošio dio vlage.', 0);
INSERT INTO property VALUES (1,2361, 'hr', 'definition','Proces kojim djelomično ili u cijelosti odstranjujemo vodu ili druge tekućine iz krutih tvari.', 0);
INSERT INTO property VALUES (1,2363, 'hr', 'definition','Odstranjivanje vode iz bilo koje tvari.', 0);
INSERT INTO property VALUES (1,2364, 'hr', 'definition','Gospodarstvo se temelji na dva odvojena, odnosno različita gospodarska sustava koji istodobno postoje u istom zemljopisnom prostoru. Dualizam je značajka mnogih zemalja u razvoju u kojima neki dijelovi zemlje nalikuju razvijenim gospodarstvima, dok drugi dijelovi nalikuju tradicionalnim gospodarstvima, tj. postoje ciklusi proizvodnje i razmjene.', 0);
INSERT INTO property VALUES (1,2365, 'hr', 'definition','Kako bi se smanjila količina ambalažnog otpada, a time i ukupnoga komunalnog krutog otpada, Njemačka je uvela dalekosežne propise s ciljem smanjenja otpada, temeljene na načelu odgovornosti proizvođača. Industrija je dobila mogućnost uspostaviti organizaciju treće strane koja bi obavljala prikupljanje (odvoz) i sortiranje prodajne ambalaže za proizvođače i trgovce. Tako je u 1990. god. oko 600 poduzeća stvorilo "Duales System Deutschland" ("Dual", jer je to značilo stvaranje drugog sustava prikupljanja paralelno s postojećim sustavom prikupljanja otpada koji je bio u nadležnosti lokalnih vlasti). Duales System Deutschland (DSD) sada ima ukupnu odgovornost za odvojeno prikupljanje i recikliranje ambalaže. Trenutačno je dvojni sustav jedini nacionalni sustav za prikupljanje i sortiranje prodajne ambalaže. Ambalaža obuhvaćena ovim sustavom odvoza označena je Zelenom točkom.', 0);
INSERT INTO property VALUES (1,2367, 'hr', 'definition','Odlaganje otpada na svakakve načine, često bez obzira na nadzor u zaštiti okoliša.', 0);
INSERT INTO property VALUES (1,2372, 'hr', 'definition','Ispuštanje, odlaganje ili ubrizgavanje bilo kakvog otpada na tlo ili u tlo ili druga zemljišta.', 0);
INSERT INTO property VALUES (1,2377, 'hr', 'definition','Nizak humak, greben, kos nasip, ili brdo rastresitog, vjetrom nanesenog zrnatog materijala (uglavnom pijeska, a ponekad vulkanskog pepela), ili golo ili prekriveno vegetacijom, sposobno za kretanje s mjesta, ali koje uvijek zadržava svoj karakterističan oblik.', 0);
INSERT INTO property VALUES (1,2380, 'hr', 'definition','Razdoblje u danu kada sja sunce.', 0);
INSERT INTO property VALUES (1,2381, 'hr', 'definition','Svaka vrsta krute tvari podijeljena na dijelove vrlo male veličine.', 0);
INSERT INTO property VALUES (1,2384, 'hr', 'definition','Odstranjivanje prašine iz zraka provjetravanjem ili ispušnim sustavima.', 0);
INSERT INTO property VALUES (1,2386, 'hr', 'definition','Dužnost formalnog upisivanja pri državnoj agenciji ili tijelu kako bi se odobrila određena prava, a posebice privilegije zaštitnog znaka ili autorskih prava, odnosno dozvola za prodaju i distribuciju proizvoda.', 0);
INSERT INTO property VALUES (1,2387, 'hr', 'definition','Svaki zatvoreni prostor u cjelini ili djelomično namijenjen za stanovanje, spavanje, kuhanje i jedenje.', 0);
INSERT INTO property VALUES (1,2389, 'hr', 'definition','Materijal za bojenje.', 0);
INSERT INTO property VALUES (1,2393, 'hr', 'definition','Umjetni zid, obala, greben, ili humak, obično od zemljanog ili kamenog nabačaja, izgrađen oko relativno ravnog, niskog područja da ga zaštiti od poplava; nasip uz rijeku. Nasip može isto tako biti izgrađen na obali ili granici jezera kako bi se spriječio priljev nepoželjne vode.', 0);
INSERT INTO property VALUES (1,2394, 'hr', 'definition','Nanošenje materijala za ojačanje strukture nasipa.', 0);
INSERT INTO property VALUES (1,2398, 'hr', 'definition','Bilo koji niz postupaka i uređaja osmišljen za otkrivanje naglih ili potencijalnih prijetnji osobama, imovini ili okolišu na prvi znak opasnosti; osobito sustav koji se koristi radarskom tehnologijom.', 0);
INSERT INTO property VALUES (1,2400, 'hr', 'definition','Vanjski slojevi Zemlje strukture, čija debljina varira između 6 i 48 km, a obuhvaća sve materijale iznad Mohorovičićevog diskontinuiteta (seizmički diskontinuitet do kojeg dolazi između kore na Zemlji i slojem ispod nje, diskontinuitet se pojavljuje na prosječnoj dubini od 35 km ispod kontinenata, te na oko 10 km ispod oceana). Ranija ideja o hladnoj krutoj ''koži'' koja se nalazi iznad rastaljene vruće unutrašnjosti sada je zamijenjena konceptom kore koja se sastoji od dvije školjke: unutarnja osnovna jedinica sastoji se od oceanske kore (sima) i vanjske granitne cjeline koja se sastoji od kontinentalne kore (sial).', 0);
INSERT INTO property VALUES (1,2402, 'hr', 'definition','Snažno potresanje tla koje proizvode duboki seizmički valovi, ispod epicentra, koje izaziva nagli pad ili otpuštanje volumena stijena elastičnih deformacija akumuliranih tijekom dugog vremena u regijama sa seizmičkom aktivnosti (tektonski potresi). Magnituda potresa prikazuje se na Richterovoj ljestvici, a intenzitet na Mercallijevoj.', 0);
INSERT INTO property VALUES (1,2405, 'hr', 'definition','Znanost koja se bavi proučavanjem Zemlje ili dijelom njezine površine; između ostaloga, uključuje discipline geologiju, geografiju, oceanografiju i meteorologiju.', 0);
INSERT INTO property VALUES (1,2406, 'hr', 'definition','Zemlja ovisi o Suncu za svoje postojanje kao planet povoljan za život, a solarna energija je glavni faktor koji određuje klimu. Dakle, uvjeti na Suncu i uvjeti na Zemlji nerazdvojno su povezani. Iako  se može činiti da su Sunčeve zrake nepromjenjive, njegovo zračenje se mijenja. Mnogi znanstvenici sumnjaju da aktivnost Sunčevih pjega ima veći utjecaj na klimatske promjene nego varijacije koje se pripisuju efektu staklenika.', 0);
INSERT INTO property VALUES (1,2408, 'hr', 'definition','Svaki od brojnih crva maločekinjaša (Oligochaete) iz rodova Lumbricus, Allolobophora, Eisenia, itd., koji se ukopaju u tlo i pomažu prozračiti i rahliti zemlju.', 0);
INSERT INTO property VALUES (1,2409, 'hr', 'definition','Svaki od različitih kukaca iz reda kožaša (Dermaptera), osobito uholaža, štriga ili škarica (Forficula auricularia), koje obično imaju izduljeno tijelo s malim kožnatim prednjim krilima, polukružnim opnastim stražnjim krilima, te savijenim škaricama na vrhu abdomena.', 0);
INSERT INTO property VALUES (1,2411, 'hr', 'definition','Geografska regija afričkog kontinenta koja uključuje Burundi, Keniju, Ruandu, Tanzaniju, Ugandu, Etiopiju i Somaliju, kao i planinu Kilimandžaro i jezero Viktorija.', 0);
INSERT INTO property VALUES (1,2412, 'hr', 'definition','Geografska regija azijskog kontinenta koju omeđuje Tihi ocean na istoku, a uključuje Kinu, Japan, Koreju, Makao, Tajvan i Siberiju.', 0);
INSERT INTO property VALUES (1,2413, 'hr', 'definition','Geografska regija europskog kontinenta zapadno od Azije i istočno od Njemačke i Jadranskog mora, a tradicionalno se sastoji od zemalja koje su nekada bile dio Sovjetskog Saveza, kao što su Poljska, Češka, Slovačka, Mađarska, Rumunjska, Srbija, Hrvatska i Bugarska.', 0);
INSERT INTO property VALUES (1,2415, 'hr', 'definition','Robna razmjena između zemalja i tvrtaka zapadne hemisfere s onima na istočnoj hemisferi (obično se odnosi na bivše komunističke zemlje istočne Europe).', 0);
INSERT INTO property VALUES (1,2419, 'hr', 'definition','Organ EU-a koji se ponajprije bavi formuliranjem politike i usvajanjem zakonodavstva Zajednice. Vijeće se sastoji od po jednog člana vlade svake od država članica Zajednice, a svaka država njime predsjedava naizmjenično svakih šest mjeseci.', 0);
INSERT INTO property VALUES (1,4686, 'hr', 'definition','Novčana ili materijalna vrijednost u trgovini ili razmjeni neke površine zemljišta koja se smatra imovinom.', 0);
INSERT INTO property VALUES (1,4691, 'hr', 'definition','Velika građevina sa strojevima koji pretvaraju energiju ispuštenu iz brzog izgaranja smjese goriva i zraka u mehaničku energiju.', 0);
INSERT INTO property VALUES (1,4698, 'hr', 'definition','Akronim za ''Light Amplification by Stimulated Emission of Radiation'' (pojačanje svjetla poticanjem emisije zračenja), uređaj koji proizvodi snažnu, vrlo usmjerenu, monokromatsku, koherentnu zraku svjetlosti. Laser se sastoji od transparentnog cilindra s reflektirajućom površinom na jednom kraju, a djelomično reflektirajućom površinom na drugom kraju. Svjetlosni valovi se reflektiraju natrag i naprijed, a neki od njih pojavljuju na kraju s djelomično reflektirajućom površinom. Izvor svjetla može biti rubin, čije atome kroma pobuđuje bljeskalica, tako da oni emitiraju impulse visoko-koherentnog svjetla, ili mješavina inertnih plinova koji stvaraju kontinuiranu zraku, ili kocka obrađenog galij arsenida koji emitira infracrveno zračenje kada električna struja prolazi kroz njega.', 0);
INSERT INTO property VALUES (1,4702, 'hr', 'definition','Pranje i glačanje odjeće, rublja itd.', 0);
INSERT INTO property VALUES (1,4707, 'hr', 'definition','Jedno od pravila koje čine sveukupne zakone.', 0);
INSERT INTO property VALUES (1,4708, 'hr', 'definition','Promjena ili dodatak u bilo kojem zakonu sa zakonskom snagom koji, ako ga odobri odgovarajuće zakonodavno tijelo, zamjenjuje izvorni zakon.', 0);
INSERT INTO property VALUES (1,4710, 'hr', 'definition','Sve razne aktivnosti vezane uz promicanje sukladnosti i poštivanje obvezujućih pravila države, a posebno prevencija, istrage, uhićenja ili pritvaranje osoba osumnjičenih ili osuđenih za kršenje tih pravila.', 0);
INSERT INTO property VALUES (1,4715, 'hr', 'definition','Obvezujuće pravilo ili pravilnik koje propiše država za zaštitu ljudskog zdravlja i okoliša, upravljanje rastom i razvojem ili poboljšavanje kvalitete života u malom geografskom i društvenom području u gradovima u kojima stanovnici imaju jednake vrijednosti i zabrinjavaju ih ista pitanja, te svakodnevno stupaju u međusobne odnose.', 0);
INSERT INTO property VALUES (1,4719, 'hr', 'definition','Sklop pravila utvrđenih zakonom ili običajima koji uređuje društvene odnose.', 0);
INSERT INTO property VALUES (1,4722, 'hr', 'definition','1) Proces odvajanja tekućine od krute tvari (kao u otpadnim tekućinama cijeđenjem u okolno tlo. 2) Vađenje topljivih komponenata čvrste smjese procjeđivanjem otapala kroz nju. 3) Gubljenje ili  uzrokovanje gubljenja topljivih tvari djelovanjem filtrirajuće tekućine.', 0);
INSERT INTO property VALUES (1,4723, 'hr', 'definition','Težak, toksičan, plavkasto-bijeli metalni element koji je vrlo kovak; pojavljuje se uglavnom kao galenit i upotrebljava se u legurama, akumulatori, kablovskim oblogama, bojama i kao štiti protiv zračenja.', 0);
INSERT INTO property VALUES (1,4724, 'hr', 'definition','Olovni spojevi su prisutni kao aditivi benzina, u bojama, keramičkim proizvodima, krovištima, brtvljenju, električnim uređajima, cijevima ili spremnicima. Izloženost olovu može nastati zbog zraka, vode, hrane, ili tla. Uzrok olova u zraku su ponajprije goriva na bazi olova i izgaranje krutog otpada, ugljena, ulja i emisije iz pogona proizvođača alkil olova, vulkanska prašina puhana vjetrom, spaljivanje površina lakiranih olovom, te dim cigarete. Olovo u vodi za piće dolazi iz procjeđivanja iz olovnih cijevi, priključaka i legura za lemljenje, i u sustavu distribucije, i u kućanskim vodinstalacijama.', 0);
INSERT INTO property VALUES (1,4725, 'hr', 'definition','Prisutnost i puštanje u zrak, vodu i tlo olova, toksičnog metala koji se upotrebljava u vodovodu, benzin i olovnim baterijama.', 0);
INSERT INTO property VALUES (1,4727, 'hr', 'definition','Obvezujuće pravilo ili pravilnik koji propisuje država za smanjenje ili uklanjanje sadržaja olova u naftnim gorivima  upotrebljavanima u motorima vozila i drugim motorima koji onečišćuju zrak ispušnim plinovima koji sadrže olovo.', 0);
INSERT INTO property VALUES (1,4728, 'hr', 'definition','Mjera količine olova ili olovnih soli koju tijelo apsorbira kao mogući znak akutnog ili kroničnog trovanja olovom, što može utjecati na živčani, probavni ili mišićni sustav.', 0);
INSERT INTO property VALUES (1,4730, 'hr', 'definition','Glavni organ fotosinteze i transpiracije u viših biljaka, koji se obično sastoji od ravne zelene oštrice pričvršćene izravno ili pomoću peteljke  na stabljiku.', 0);
INSERT INTO property VALUES (1,4731, 'hr', 'definition','Slučajno, nekontrolirano ispuštanje ili curenje tekućine, plinova i ostalih tvari na slučajne i neželjene lokacije, što često uzrokuje rizik od oštećenja ili štete odnosno ozljede za osobe, imovinu ili okoliš.', 0);
INSERT INTO property VALUES (1,4735, 'hr', 'definition','Učinjena ili štavljena životinjska koža, obično s uklonjenom dlakom ili krznom.', 0);
INSERT INTO property VALUES (1,4738, 'hr', 'definition','Industrija za proizvodnju kožne robe, kao što je odjeća, torbe itd.', 0);
INSERT INTO property VALUES (1,4739, 'hr', 'definition','Temeljni zakon ili sudski presedan koji jamči ili podržava naknadnu odluku ili radnje bilo kojega državnog, korporativnog ili privatnog subjekta.', 0);
INSERT INTO property VALUES (1,4741, 'hr', 'definition','Opravdani zahtjev da se nešto ima ili dobije ili da se djeluje na određeni način, koji je podržan zakonom i pokriven ili zaštićen od opasnosti da bude ukinut ili odbijen.', 0);
INSERT INTO property VALUES (1,4743, 'hr', 'definition','Svaki nalog ili pravilo koje izda vlada u kojem se utvrđuju njezini postupci za izradu ili provedbu zakona ili sudske postupke u vezi sa zakonima.', 0);
INSERT INTO property VALUES (1,4744, 'hr', 'definition','Sredstvo kojim se provodi neko pravo ili se sprječava, otklanja ili daje naknada za povredu prava.', 0);
INSERT INTO property VALUES (1,4746, 'hr', 'definition','Točan tekst ili jezik nekog zakona ili drugog dokumenta sukladnog zakonu ili koji ima snagu zakona.', 0);
INSERT INTO property VALUES (1,4749, 'hr', 'definition','Djelovanje ili proces izrade zakona.', 0);
INSERT INTO property VALUES (1,4752, 'hr', 'definition','Pravila o ograničenjima emisija onečišćivača.', 0);
INSERT INTO property VALUES (1,4758, 'hr', 'definition','Obvezujuće pravilo ili pravilnik koji propisuje država u vezi s  upravljanjem i zaštitom područja prirodnih izvora za opskrbu vodom i vodenih putova.', 0);
INSERT INTO property VALUES (1,4759, 'hr', 'definition','Ovlast skupštine osoba ili zastupnika koji donose odluke da donesu zakon, rezoluciju ili poseban akt i daju mu službeni, pravno obvezujući status.', 0);
INSERT INTO property VALUES (1,4760, 'hr', 'definition','Umijeće, znanje, kvalifikacije, sposobnosti ili ovlast da se izrade, daju ili donose pravila s obvezujućom snagom za stanovništvo ili područje nadležnosti.', 0);
INSERT INTO property VALUES (1,4762, 'hr', 'definition','Znanje ili služba koja pruža informacije o postojećim i predloženim zakonima, uključujući i status odobrenja, povijest i sadržaj postupaka odlučivanja i poseban jezik tih zakona.', 0);
INSERT INTO property VALUES (1,4764, 'hr', 'definition','Ministarstvo, skupština ili tijelo sastavljeno od osoba koje donosi zakonske propise neke države ili naroda.', 0);
INSERT INTO property VALUES (1,4767, 'hr', 'definition','Športovi i rekreacija kojima se ljudi bave u slobodno vrijeme, kada ne rade ili ne obavljaju druge dužnosti.', 0);
INSERT INTO property VALUES (1,4771, 'hr', 'definition','Slobodno vrijeme, kada ljudi ne rade ili ne obavljaju druge dužnosti.', 0);
INSERT INTO property VALUES (1,4777, 'hr', 'definition','Veliki red kukaca s ljuskastim krilima, koji obuhvaća leptire, leptire iz porodice debeloglavaca i moljce; odrasli se odlikuju sa dva para opnastih krila i čeljustima, s istaknutim, zavojitim rilcem.', 0);
INSERT INTO property VALUES (1,4782, 'hr', 'definition','Progresivna, maligna bolest krvotvornih organa; poremećaj u bujanju i razvoju leukocita i njihovih prekurzora u krvi i koštanoj srži.', 0);
INSERT INTO property VALUES (1,4784, 'hr', 'definition','Oporezivi dio proizvoda od imovine i rada pojedinih građana, koje država, u ostvarivanju svojih suverenih prava, uzima za potporu državi, za provedbu zakona, te kao sredstvo za nastavak rada različitih legitimnih funkcija države.', 0);
INSERT INTO property VALUES (1,4785, 'hr', 'definition','Vokabular pojedinih područja djelovanja, regije, društvene klase ili osobe, odnosno ukupan skup morfema ili smislenih jedinice jezika i njegovih riječi.', 0);
INSERT INTO property VALUES (1,4787, 'hr', 'definition','Podlijeganje pravnoj obvezi. Odgovornost je građanska ili kaznena, ovisno o tome provode li je građanski ili kazneni sudovi.', 0);
INSERT INTO property VALUES (1,4788, 'hr', 'definition','Podlijeganje zakonskoj obvezi, kao što su financijsko obeštećenje ili ekološke naknade, za bilo kakvo oštećenje ili štetu nanesenu osobama, imovini ili okolišu tijekom komercijalne ili rekreacijske aktivnosti u moru, na moru ili u blizini mora.', 0);
INSERT INTO property VALUES (1,4789, 'hr', 'definition','Podlijeganje zakonskoj obvezi, kao što su financijsko obeštećenje ili ekološke naknade, za bilo kakvo oštećenje ili štetu nanesenu osobama, imovini ili okolišu tijekom proizvodnje, upotrebe ili prijevoza radioaktivnih materijala koji se upotrebljavaju kao izvor energije ili u naoružanju.', 0);
INSERT INTO property VALUES (1,4790, 'hr', 'definition','Doneseni zakon ili skup zakona koji se odnose na, ili uspostavljaju, obvezu, dug ili odgovornost za gubitak/štetu, kaznu, nedjelo/nesreću, trošak ili teret.', 0);
INSERT INTO property VALUES (1,4793, 'hr', 'definition','Mjesto gdje se čuvaju knjige i drugi književni materijali.', 0);
INSERT INTO property VALUES (1,4796, 'hr', 'definition','Bilo koji proces kojim se daje odobrenje i kojim se potvrđuje pravno ili upravno dopuštenje za neku osobu ili organizaciju da se mogu baviti nekim zanimanjem ili obavljati neke  djelatnosti ili poslovanje.', 0);
INSERT INTO property VALUES (1,6381, 'hr', 'definition','Politika, stajalište ili načelo koje se zagovara, o kojem se poučava ili stavlja na snagu u vezi sa stjecanjem i ostvarivanjem moći vladanja ili upravljanja u društvu.', 0);
INSERT INTO property VALUES (1,6383, 'hr', 'definition','The study of the effects of political actions on human geography, involving the spatial analysis of political phenomena. Proučavanje učinaka političkog djelovanja na ljudsku geografiju, koje uključuje prostornu analizu političkih  pojava.', 0);
INSERT INTO property VALUES (1,6384, 'hr', 'definition','Skupina osoba udružena u organizaciju koja traži ili obnaša moć u državnim ili javnim poslovima, podržavanjem kandidata koji bi mogli doći na vlast ili lobiranjem za akciju i mobiliziranjem potpore za zakone ili vladine politike.', 0);
INSERT INTO property VALUES (1,6387, 'hr', 'definition','Organizirana skupina čiji je temeljni cilj stjecanje političke moći i javne dužnosti (na vlasti) za svoje odabrane određene vođe. Obično, političke stranke će objavljivati da imaju zajedničku predanost svojih vođa i svog članstva nizu političkih, socijalnih, ekonomskih i/ili kulturnih vrijednosti.', 0);
INSERT INTO property VALUES (1,6388, 'hr', 'definition','Moć, sposobnost ili ovlasti vlada, skupina građana i drugih zainteresiranih strana u donošenju promjena ili utjecaju ili kontroli ishoda državnih ili javnih politika koje utječu na narod, regiju ili općine', 0);
INSERT INTO property VALUES (1,6392, 'hr', 'definition','Mikrospore sjemenjača. Svako zbrnce peludi sadrži jako smanjen muški gametofit. Zrnca peludi prenosi vjetar, voda, ptice ili druge životinje.', 0);
INSERT INTO property VALUES (1,6395, 'hr', 'definition','Svaka tvar, obično zaostala nakon neke ljudske aktivnosti, koja ima nepoželjan učinak na okoliš.', 0);
INSERT INTO property VALUES (1,6396, 'hr', 'definition','Proces kojim se neka onečišćujuća tvar fizički unosi u neku drugu tvar ili tijelo.', 0);
INSERT INTO property VALUES (1,6397, 'hr', 'definition','Proces kojim se koncentracije onečišćujućih tvari postupno povećavaju u tkivima živih organizama u okolinama u kojima su te onečišćujuće tvari prisutne.', 0);
INSERT INTO property VALUES (1,6399, 'hr', 'definition','Određivanje sastava bilo koje tvari koja uzrokuje onečišćenje, korištenjem klasičnih laboratorijskih tehnika i drugih metoda koje uključuju analitičku kemiju.', 0);
INSERT INTO property VALUES (1,6404, 'hr', 'definition','Mjera količine onečišćujuće tvari u danoj količini vode, tla, zraka, hrane ili nekoga drugoge medija.', 0);
INSERT INTO property VALUES (1,6405, 'hr', 'definition','Fizikalno, kemijsko ili biološko raspadanje složene onečišćujuće tvari na jednostavnije sastavnice.', 0);
INSERT INTO property VALUES (1,6406, 'hr', 'definition','Djelovanje ili proces kojim se onečišćujuće tvari prirodno talože ili nakupljaju u ekološkim sustavima.', 0);
INSERT INTO property VALUES (1,6407, 'hr', 'definition','Širenje onečišćujućih tvari iz točke ispuštanja u zrak, tlo i vodu. Na disperziju onečišćujućih tvari u zrak jako utječe to kako i gdje se onečišćujuća tvar ispušta, na primjer, stalnim ispuštanjima niske razine za razliku od slučajnih ispuštanja (tj. U akcidentima i nesrećama, op. Prev.), ima li mnogo tvorničkih dimnjaka u odnosu prema nekoliko, ili visina dimnjaka. Priroda meteorologije lokalnih terena i kemije ispuštenih materijala snažno utječu na uzorak regionalne i, naposlijetku, globalnu rasprostranjenost i transport.', 0);
INSERT INTO property VALUES (1,6409, 'hr', 'definition','Razmještaj, ili obrazac, povezan s pojavom onečišćujućih tvari  u nekom geografskom području.', 0);
INSERT INTO property VALUES (1,6412, 'hr', 'definition','Postupak potpunog uklanjanja izvora onečišćujuće tvari , kao i same onečišćujuće tvari.', 0);
INSERT INTO property VALUES (1,6413, 'hr', 'definition','Ispuštanje onečišćujućih tvari u zrak, vodu i tlo iz određenog izvora i mjereno na točke imisije.', 0);
INSERT INTO property VALUES (1,6414, 'hr', 'definition','Čin ili stanje podvrgnutosti tvari koja štetno utječe na ljudsko zdravlje, imovinu ili okoliš.', 0);
INSERT INTO property VALUES (1,6415, 'hr', 'definition','Čin ili proces u kojem se onečišćujuće tvari stvaraju, proizvode ili nastaju.', 0);
INSERT INTO property VALUES (1,6417, 'hr', 'definition','Prijenos krutih, tekućih ili plinovitih onečišćujućih tvari u zrak, vodu i tlo.', 0);
INSERT INTO property VALUES (1,6418, 'hr', 'definition','Postupak obrade koji se upotrebljava radi smanjenja topivosti onečišćujućih tvari kako bi se smanjila mogućnost migracije ili ispiranja, ili da se pripremi njihovo odlaganje.', 0);
INSERT INTO property VALUES (1,6421, 'hr', 'definition','Vrijednost koja predstavlja koncentraciju onečišćujuće tvari u određenom području, često određena mjernim uređajima i uređima za bilježenje.', 0);
INSERT INTO property VALUES (1,6422, 'hr', 'definition','Količina onečišćujuće tvari koju sredstvo prijenosa, kao što je potok, ledenjak ili vjetar stvarno nosi uodređenom vremenu.', 0);
INSERT INTO property VALUES (1,6425, 'hr', 'definition','Povremeno ili stalno određivanje količine onečišćujućih tvari prisutnih u okolišu.', 0);
INSERT INTO property VALUES (1,6428, 'hr', 'definition','Sljedljivi put onečišćujuće tvari, od njezinog izvora, kroz međusobno djelovanje s okolišem, i na kraju njezin učinak na ciljni ekosustav ili ciljne organizme.', 0);
INSERT INTO property VALUES (1,6429, 'hr', 'definition','Sve mjere usmjerene na smanjenje onečišćujućih tvari, često fizičkim ili kemijskim uklanjanjem toksičnih ili potencijalno toksičnih materijala.', 0);
INSERT INTO property VALUES (1,6441, 'hr', 'definition','Kruta tvar, tekućina ili plin koji je kontaminiran, postao nečist ili nesiguran za uporabu.', 0);
INSERT INTO property VALUES (1,6443, 'hr', 'definition','Načelo da onaj tko prouzroči onečišćenje mora pokriti troškove nastale zbog tog onečišćenje.', 0);
INSERT INTO property VALUES (1,6445, 'hr', 'definition','Izravna ili neizravna promjena bioloških, toplinskih, fizikalnih ili radioaktivnih svojstava bilo kojeg medija na takav način da se stvori opasnosti ili potencijalna opasnost za ljudsko zdravlje, ili za zdravlje, sigurnost ili dobrobit svih živih vrsta.', 0);
INSERT INTO property VALUES (1,6446, 'hr', 'definition','Smanjenje stupnja ili intenziteta onečišćenja u tlu, rijekama, jezerima, morima, u zraku itd.', 0);
INSERT INTO property VALUES (1,6447, 'hr', 'definition','Oprema za smanjenje onečišćenja stupnja ili intenziteta onečišćenja.', 0);
INSERT INTO property VALUES (1,6451, 'hr', 'definition','Kemijske i fizičke metode za smanjenje ispuštanja većine onečišćujućih tvari; za ugljični dioksid trenutno  nema ekonomičnog ni praktičnog načina da se smanje ispuštene količine osim smanjenjem upotrebe fosilnih goriva. Većina specifičnih sredstava za uklanjanje onečišćujućih tvari iz emisija uključuju postupke odsumporavanja dimnih plinova, fluidizirano izgaranje, katalizatorske pretvarače i redizajn opreme, kao što su gorionici u visokim pećima i automobilski motori, u svrhu smanjenja proizvodnje onečišćivača.', 0);
INSERT INTO property VALUES (1,6452, 'hr', 'definition','Uređaji za smanjenje i/ili uklanjanja emisija u okoliš koje imaju potencijal da prouzroče onečišćenje.', 0);
INSERT INTO property VALUES (1,6453, 'hr', 'definition','Skup pravila ili naredbi koje propisuje Vlada, uprava ili međunarodna organizacija ili ugovor u kojima su utvrđena ograničenja za emisije tvari koje štete ili negativno utječu na okoliš i ljudsko zdravlje.', 0);
INSERT INTO property VALUES (1,7082, 'hr', 'definition','Primjer ili certifikat kojim se potvrđuje činjenica upisa različitih informacija u neki službeni popis kako bi se olakšalo uređivanje ili odobrenje, uključujući ime, podatke za kontakt i, u nekim slučajevima, podaci o određenom posjedu ili imovini.', 0);
INSERT INTO property VALUES (1,7083, 'hr', 'definition','Radnja ili upisivanje tijekom kojeg se pojedinac, tvrtka ili organizacija formalno upisuje pri vladinoj agenciji ili tijelu kako bi mu se odobrila određena prava, a posebice pravo na zaštitni znak ili zaštita autorskih prava, odnosno dozvola za prodaju i distribuciju proizvoda.', 0);
INSERT INTO property VALUES (1,7084, 'hr', 'definition','Resursi koji imaju prirodnu stopu dostupnosti i daju stalan protok usluga koje se mogu konzumirati u bilo kojem remenskom razdoblju, bez ugrožavanja mogućnosti budućih potrošnje, sve dok trenutno korištenje ne premaši neto obnovu tijekom razdoblja koje se razmatra.', 0);
INSERT INTO property VALUES (1,7086, 'hr', 'definition','Skup pravila ili naredbi koje propisuje država ili međunarodna organizacija ili ugovor kojima se utvrđuju razine opasnih tvari u okolišu ili u tvarima koje se apsorbiraju u organizmu iznad kojih se izloženost ljudi smatra opasnom za zdravlje.', 0);
INSERT INTO property VALUES (1,7087, 'hr', 'definition','Pravilo koje utvrđuje tijelo; trajno pravilo djelovanja, zakon ili statut. U najčešćem značenju ovaj pojam se upotrebljava za akt zakonodavnog tijela.', 0);
INSERT INTO property VALUES (1,7090, 'hr', 'definition','Vladin nadzor nad obvezama i pravima neke industrije ili poduzeća u svrhu pružanja javnih usluga javnosti koje se smatraju važnima, od najvećeg značenja ili potrebnima za većinu članova zajednice ili područja.', 0);
INSERT INTO property VALUES (1,7091, 'hr', 'definition','Mjera očuvanja okoliša koja uključuje ispravak zlouporaba iz prošlosti koje su smanjile produktivnost baze resursa.', 0);
INSERT INTO property VALUES (1,7092, 'hr', 'definition','Osiguravanje nove ili različite stambene jedinice.', 0);
INSERT INTO property VALUES (1,7093, 'hr', 'definition','Ponovno uvođenje istrijebljenih vrsta u nekom području; to neće uspjeti ako su izabrane životinje izumrle u području previše davno, te ako je sama površina prošla kroz previše promjena. Za ponovno uvođenje potrebne su godine pomnog planiranja - odobrenja lokalnog stanovništva, tehničkih uvjeta za puštanje, sustava hranjenja, zaštite i kontrole uzgoja - pa čak se i onda mogu pojaviti neke neočekivani problemi.', 0);
INSERT INTO property VALUES (1,7094, 'hr', 'definition','Povezivanje uzroka s posljedicama koje ih prouzročuju.', 0);
INSERT INTO property VALUES (1,7097, 'hr', 'definition','Puštanje organizama u okoliš stvara rizik da bi nakon puštanja oni mogli pokazivati neku ranije nepoznatu patogenost, zauzeti mjesto neku prirodne bakterije ili prenijeti neka neželjena svojstva takvim autohtonim bakterijama.', 0);
INSERT INTO property VALUES (1,7099, 'hr', 'definition','Izraz čovjekove vjere u, i strahopoštovanja prema, nadčovječnoj snazi koja se prepoznaje kao stvoritelj i onaj koji upravlja svemirom.', 0);
INSERT INTO property VALUES (1,7102, 'hr', 'definition','1) Znanstvene spoznaja, prepoznavanje, popis i analiza zemljišta i vodene površine pomoću udaljenih senzora ili uređaja za snimanje, kao što su fotografije, toplinska skeneri, radar itd.
INSERT INTO property VALUES (1,7103, 'hr', 'definition','Opći izraz koji znači uklanjanje tvari iz nekog medija ili iz okoliša.', 0);
INSERT INTO property VALUES (1,7104, 'hr', 'definition','Proces povratka prirodnih ekoloških sustava i staništa na njihove izvorne strukture i sastav vrsta. Za obnovu je potrebno detaljno poznavanje izvorne vrste, funkcija ekološkog sustava i  procesa  interakcije koji su ovdje uključeni.', 0);
INSERT INTO property VALUES (1,7107, 'hr', 'definition','Izvori energije koji se ne oslanjaju na goriva od kojih postoje samo konačne zalihe. Najšire upotrebljavani obnovljivi izvor je hidroelektrana; ostali obnovljivi izvori energije su biomasa, solarna energija, energija plime i oseke, energija valova i energija vjetra; energije biomase ne izbjegava opasnost od efekta staklenika.', 0);
INSERT INTO property VALUES (1,7108, 'hr', 'definition','Resursi koji se mogu stalno obnavljati ili zamijeniti procesima kao što su organska reprodukcija i uzgoj poput onih u poljoprivredi, stočarstvu, šumarstvu i ribarstvu.', 0);
INSERT INTO property VALUES (1,7110, 'hr', 'definition','Stambene jedinice u kojima žive stanari koji u određenim razdobljima plaćaju stanodavcima ili vlasnicima za korištenje objekata za stanovanje.', 0);
INSERT INTO property VALUES (1,7112, 'hr', 'definition','Bilo kakva komercijalna aktivnost, položaj ili lokalitet koji uključuje rad na obnovi i popravljanju neke materijalne stvari ili strukture, kao što je zamjena dijelova ili slaganje nečega potrganog, slomljenog ili samostojećeg', 0);
INSERT INTO property VALUES (1,7114, 'hr', 'definition','Zamjena atoma ili atomske skupine nekim različitima.', 0);
INSERT INTO property VALUES (1,7115, 'hr', 'definition','Iznos novca potreban da se jedan artikl zamijeni drugim ili da jedan artikl zauzme mjesto drugoga, ili da se netko pobrine da se to uradi.', 0);
INSERT INTO property VALUES (1,7118, 'hr', 'definition','Svako djelovanje ili radnja poduzeta u ime neke osobe, skupine, poduzetnika ili vlade, često u svojstvu izabrane ili imenovane osobe.', 0);
INSERT INTO property VALUES (1,7119, 'hr', 'definition','Pretvaranje kontaminiranoga nuklearnog goriva u upotrebljivo stanje.', 0);
INSERT INTO property VALUES (1,7120, 'hr', 'definition','Bilo koji od različitih procesa, bilo spolnog ili nespolnog, kojima životinja ili biljka proizvodi jednu ili više osoba sličnih sebi.', 0);
INSERT INTO property VALUES (1,7122, 'hr', 'definition','Tehnologija uključena u mijenjanje genetskog sastava organizma na neki propisani način. Obično se "korisni" geni, tj. vrlo kratak slijed DNK, izoliraju iz jednog organizma i umetnu u DNK bakterije kvasca. Ti se mikroorganizmi brzo množe i mogu se lako uzgojiti, što omogućuje dobivanje velikih količina genskog proizvoda. Reproduktivna manipulacija se upotrebljava za masovnu proizvodnju antibiotika, enzima i hormona (npr. inzulina). Organizmi u koje je strani DNK umjetno umetnut  nazivaju se "transgeničnim organizmima" (ili "genetski modificiranim organizmima", op. Prev.).', 0);
INSERT INTO property VALUES (1,7124, 'hr', 'definition','Red kopnenih kralježnjaka, čije su značajke nedostatak dlaka, perja i mliječnih žlijezda; koža je prekrivena ljuskama, imaju tri srčane klijetke, a pleuralna i peritonealna šupljina su neprekinute.', 0);
INSERT INTO property VALUES (1,7126, 'hr', 'definition','Služba organizirana za pružanje trenutačne pomoći nastradalim osobama ili osobama u nevolji.', 0);
INSERT INTO property VALUES (1,7127, 'hr', 'definition','Znanstveno ispitivanje usmjereno na otkrivanje i primjenu novih činjenica, metoda i prirodnih zakona.', 0);
INSERT INTO property VALUES (1,7129, 'hr', 'definition','Mjesto na kojem se provode sustavna istraživanja u cilju utvrđivanja činjenica ili načela ili prikupljanja informacija o nekoj temi.', 0);
INSERT INTO property VALUES (1,7135, 'hr', 'definition','Prijedlog, plan ili projekt koji sadrži potrebne informacije i podatke za obavljanje određenog istraživanja.', 0);
INSERT INTO property VALUES (1,7137, 'hr', 'definition','Kopneno i/ili vodeno područje određeno da ima zaštićeni status u svrhu očuvanja određenih bioloških obilježja. Rezervatima se upravlja ponajprije kako bi se zaštitile i očuvale te značajke i kako bi se pružile mogućnosti za istraživanje problema koji prate upravljanje prirodnim znamenitostima, kao i raslinjem i životinjskim populacijama. Obično se donose pravilnici kojima se kontrolira pristup javnosti i uznemiravanje.', 0);
INSERT INTO property VALUES (1,7138, 'hr', 'definition','Umjetni ili prirodni spremnik vode, kao što je jezero ili ribnjak, iz kojeg se voda može biti iscrpiti i upotrijebiti npr. za navodnjavanje, komunalnu vodoopskrbu ili kontrolu poplave.', 0);
INSERT INTO property VALUES (1,7143, 'hr', 'definition','Područje u kojem su samo privatne kuće, bez ureda i tvornica.', 0);
INSERT INTO property VALUES (1,7144, 'hr', 'definition','Zgrada namijenjena stanovanju.', 0);
INSERT INTO property VALUES (1,7146, 'hr', 'definition','Stambene zone u kojima su izgrađeni povišeni dijelovi na cestama zbog kojih su vozila prisiljena polako se kretati preko njih.', 0);
INSERT INTO property VALUES (1,7149, 'hr', 'definition','Količina vode koja preostane u vodotoku nakon što je iz vodotoka potrošena voda za hidroelektranu kako bi se zadržao zadovoljavajući protok u suhim vremenskim uvjetima koji omogućuje opstanak biotičkih zajednica.', 0);
INSERT INTO property VALUES (1,7155, 'hr', 'definition','Preostali potencijal da bi moglo doći do štete za ljude, imovinu ili okoliš nakon što su se poduzeli svi mogući napori da se smanje predvidljive opasnosti.', 0);
INSERT INTO property VALUES (1,7156, 'hr', 'definition','Materijal koji preostane nakon što su provedeni svi postupci obrade otpada, uključujući industrijske, urbane, poljoprivredne, rudarske ili druge slične postupke obrade.', 0);
INSERT INTO property VALUES (1,7159, 'hr', 'definition','Analiza ostataka iz poljoprivrednih kemikalija koje se upotrebljavaju u usjevima za hranu i koji se nalaze u hrani. Analize se koriste plinskom kromatografijom, kromatografijom tekućina, masenom spektrometrijom, imunoispitivanjem itd.', 0);
INSERT INTO property VALUES (1,7160, 'hr', 'definition','Recikliranje materijala ili energije koji su preostali ili postali otpad u industrijskim procesima i drugim ljudskim aktivnostima. Primjeri uključuju otpadnu toplinu i plinovite onečišćujuće tvari iz proizvodnje električne energije, šljaku iz prerade metalnih rudača i smeće. Ostatak postaje izlazna ili ulazna veličina kada tehnološki napredak stvori ekonomske mogućnosti za korištenje otpada.', 0);
INSERT INTO property VALUES (1,7161, 'hr', 'definition','Svi proizvodi iz vrste krutih ili polukrutih organskih proizvoda prirodnoga ili sintetičkog podrijetla bez čvrsto određenog tališta, općenito velike molekularne težine; većina smola su polimeri.', 0);
INSERT INTO property VALUES (1,9138, 'hr', 'definition','Stanje potrošene ili upotrijebljene vode koja sadrži otopljene ili suspendirane tvari iz kućanstava, poljoprivrednih gospodarstava u zajednici ili iz industrije.', 0);
INSERT INTO property VALUES (1,9139, 'hr', 'definition','Radnja ili proces smanjivanja volumena upotrijebljene vode koja se ispušta iz kućanstava, poduzeća ili industrije.', 0);
INSERT INTO property VALUES (1,9140, 'hr', 'definition','Uklonjeni materijali koji nastaju fizikalnom, biološkom i kemijskom obradom otpadnih voda.', 0);
INSERT INTO property VALUES (1,9143, 'hr', 'definition','Svaki proces kojemu se podvrgavaju otpadne vode koji bi uklonio, ili na neki drugi način onesposobio razne vrste otpada koje se u njima nalaze, tako da one postanu neopasne za ljudsko zdravlje i okoliš.', 0);
INSERT INTO property VALUES (1,9144, 'hr', 'definition','Postrojenje u kojem se, putem fizikalno-kemijskih i bioloških procesa, organske tvari, bakterije, virusi i tijela uklanjaju iz stambenih, poslovnih i industrijskih otpadnih voda prije nego što se ispuste u rijeke, jezera i mora.', 0);
INSERT INTO property VALUES (1,9147, 'hr', 'definition','Proučavanje kemijskih, fizikalnih i bioloških svojstava vode.', 0);
INSERT INTO property VALUES (1,9149, 'hr', 'definition','Količina ulazne i izlazne vode u sustavu, za koje se pretpostavlja da će dugoročno biti jednake, tako da su količine vode u ravnoteži.', 0);
INSERT INTO property VALUES (1,9151, 'hr', 'definition','Svaka vodena masa s određenim hidrološkim, fizikalnim, kemijskim i biološkim značajkama i koja se može upotrijebiti za jednu ili više svrha.', 0);
INSERT INTO property VALUES (1,9153, 'hr', 'definition','Podloga na kojoj se nalazi vodena masa.', 0);
INSERT INTO property VALUES (1,9157, 'hr', 'definition','Hvatanje vode, osobito kišnice, u strukturu kao što je bazen ili spremnik.', 0);
INSERT INTO property VALUES (1,9159, 'hr', 'definition','Zaštita, razvijanje i učinkovito gospodarenje vodenim resursima za korisne namjene.', 0);
INSERT INTO property VALUES (1,9160, 'hr', 'definition','Obrasci korištenja i količine vode pri upotrebi vode u nekoj zajednici ili ljudskoj skupini u svrhu preživljavanja, komfora i užitka.', 0);
INSERT INTO property VALUES (1,9161, 'hr', 'definition','Prirodni potok koji nastaje u nekom slivu, ali njegov protok ne ovisi potpuno o površinskoj odvodnji u njegovoj bližoj okolini, i koji teče u kanalu s dobro utvrđenim koritom između vidljivih obala ili kroz formiranu depresiju u zemlji, te ima određenu i stalnu ili povremenu opskrbu vodom, i obično, ali ne i nužno, ima zamjetljivu struju u određenom smjeru koja se prazni na određenoj točki u drugo vodno tijelo.', 0);
INSERT INTO property VALUES (1,9165, 'hr', 'definition','Sustav cijevi za opskrbu zajednica i industrija vodom.', 0);
INSERT INTO property VALUES (1,9166, 'hr', 'definition','Ugrožavanje vode može biti prouzročeno različitim sredstvima, npr. onečišćenjima na poljoprivrednim gospodarstvima iz životinjskog otpada i otpadnom vodom iz silaže (tekućine od zeleno-lisnato stočne hrane kojoj je dodana melasa radi pospješivanja fermentacije i očuvanja; one su vrlo onečišćujuće i mogu biti sezonski uzrok smrti riba u malim potocima), mogu ga prouzročiti procjedne vode iz odlagališta otpada, te hrpe zamuljene iskopane zemlje, ispuštanja otapala u kanalizaciju ili na zemljište i neodgovarajući radovi na kanalizaciji.', 0);
INSERT INTO property VALUES (1,9168, 'hr', 'definition','Lomljenje žive stijene na manje čestice i njezino uklanjanje vodom. Kao trošenje zbog vemena, erozija je prirodan geološki proces, ali brža erozije tla rezultat je loše prakse korištenja zemljišta, što dovodi do gubitka plodnoga površinskog tla i zamuljavanja brana, jezera, rijeke i luke. Postoje tri vrste erozije vodom. A) Erozija prskanjem nastaje kada kapljice kiše udaraju ogoljeno tlo, zbog čega dolazi do prskanja kapljica, u obliku blata, na mjesta na tlu i pretvaranja gornjeg sloja tla u amorfnu, zbijenu masu koja se suši tako da nastaje tvrda, uglavnom nepropusna kora. B) Površinski tok događa se kada se tlo ukloni površinskim otjecanjem za vrijeme jake kiše. C) Tok u obliku kanala nastaje kada mješavina vode i tla koja teče izdubi kanal, koji se zatim i produbljuje daljnjim scouring. Manji erozijski kanal zove se potočić, a veći kanal vododerina, jarak ili gudura.', 0);
INSERT INTO property VALUES (1,9169, 'hr', 'definition','Crpljenje vode za različite namjene (tj. za poljoprivredu, melioraciju, upotrebu u kućanstvima i industriji itd.).', 0);
INSERT INTO property VALUES (1,9170, 'hr', 'definition','Okomito ili strmo spuštanje vode nekog potoka, npr. gdje on prelazi površinski sloj otpornih stijena koji nadvisuje mekšu stijenu koja je erodirala ili teče preko ruba visoravni obale sa strmim liticama.', 0);
INSERT INTO property VALUES (1,9171, 'hr', 'definition','Slatkovodni rakovi škrgonošci čija je značajka proziran oklop s dva dijela.', 0);
INSERT INTO property VALUES (1,9172, 'hr', 'definition','Karakteristično ponašanje i ukupna količina vode u slivu, koja se određuje mjerenjem količina kao što su oborine, površinske i podzemne zalihe i protok, te evapotranspiracija.', 0);
INSERT INTO property VALUES (1,9176, 'hr', 'definition','Voda koja se upotrebljava u poljoprivredi za navodnjavanje i stočarstvo. Na napajanje stoke odlazi samo 1 posto od ukupne količine crpljene vode za poljoprivredno korištenje. Od svih funkcionalnih upotreba vode u poljoprivredi, najviše vode se upotrebljava za navodnjavanje.', 0);
INSERT INTO property VALUES (1,9178, 'hr', 'definition','Voda za potrošnju počinje s povlačenjem, ali u ovom slučaju bez povratka, npr. navodnjavanjem, odlaskom pare u atmosferu, voda koja se nalazi u gotovim proizvodima, odnosno voda više nije izravno dostupna za naknadno korištenje.', 0);
INSERT INTO property VALUES (1,9180, 'hr', 'definition','Voda koja se upotrebljava u industriji za namjene kao što su izrada, obrada, pranje i hlađenje, a dobiva se iz javnog vodovoda ili izvora samoopskrbe.', 0);
INSERT INTO property VALUES (1,9183, 'hr', 'definition','Vodne ptice reda Anseriformes, koji obuhvaća labudove, patke, guske i bukače.', 0);
INSERT INTO property VALUES (1,9184, 'hr', 'definition','Količina kalcijevih i magnezijevih soli otopljenih u vodi.', 0);
INSERT INTO property VALUES (1,9186, 'hr', 'definition','Plutajuća vodena biljka, Eichornia crassipes koja uspijeva u tropskoj Americi, koja ima upadljive plavkasto-ljubičaste cvjetove i napuhnute peteljke, iz porodice Pontederiaceae. Ona stvara guste mase u rijekama, ribnjacima itd., te je ozbiljan štetnik u južnim dijelovima SAD-a, na Javi, u Australiji, na Novom Zelandu i u dijelovima Afrike.', 0);
INSERT INTO property VALUES (1,9193, 'hr', 'definition','Poplavljivanje je učinak kanala za navodnjavanje; on se događa kada se gornja granica podzemne vode diže unutar 3 metra od korijena usjeva, zbog čega se smanjuje njihova sposobnost da apsorbiraju kisik i na kraju se ugrožavaju prinosi. Mnogi čimbenici pridonose poplavljivanju. Oni uključuju neadekvatnu odvodnju, neprimjerenu ravnotežu u korištenju podzemnih i površinskih voda, procjeđivanje i cijeđenje percolation iz nebetoniranih kanala, pretjerano zalijevanje, sadnju usjeva koji su neprikladni za određena tla, kao i neodgovarajuću pripremu zemljišta prije navodnjavanja.', 0);
INSERT INTO property VALUES (1,9195, 'hr', 'definition','Mjere poduzete da se osigura primjerena opskrba vodom i odgovorno korištenje vodnih resursa.', 0);
INSERT INTO property VALUES (1,9196, 'hr', 'definition','Istraživanja koja se provode u cilju procjene količine i kvalitete onečišćivača, hranjivih tvari i suspendiranih krutih tvari sadržanih u vodenim tijelima, te procjene izvora i čimbenika povezanih s poljoprivrednom praksom, industrijskim djelatnostima ili drugim ljudskim aktivnostima.', 0);
INSERT INTO property VALUES (1,9199, 'hr', 'definition','Kemijski ili fizikalni čimbenik unesen u bilo koje vodno tijelo koje može štetno promijeniti prirodno stanje tog vodnog tijela i drugih povezanih vodnih tijela.', 0);
INSERT INTO property VALUES (1,9202, 'hr', 'definition','Promjena kemijske, fizikalne, biološke i radiološke cjelovitosti vode koju je izazvao ili potakao čovjek.', 0);
INSERT INTO property VALUES (1,9203, 'hr', 'definition','Mjere opreza, radnje ili postrojenja koja se provode ili izgrađuju u svrhu sprječavanja ili otežavanja promjena  fizikalne, biološke, kemijske i radiološke cjelovitosti vode koju je izazvao ili potakao čovjek.', 0);
INSERT INTO property VALUES (1,9206, 'hr', 'definition','Mjere za očuvanje površinskih i podzemnih voda; svrha vodozaštite je osigurati stalnu dostupnost vode za sve veće potrebe kućanstava, komercijalne i industrijske namjene i osigurati dovoljno vode za prirodne ekosustave.', 0);
INSERT INTO property VALUES (1,9209, 'hr', 'definition','Stroj ili uređaj koji služi za podizanje vode, obično iz izvora ili bušotine, na ručni ili motorni pogon, pogon vjetrom ili s pomoću nekoga drugog izvora.', 0);
INSERT INTO property VALUES (1,9210, 'hr', 'definition','Različiti postupci za uklanjanje ili neutraliziranje neželjenih nečistoća u vodi.', 0);
INSERT INTO property VALUES (1,9211, 'hr', 'definition','Postrojenje u kojem se voda, uz pomoć fizikalnih i kemijskih  procesa, učini prikladnom za ljudsku potrošnju i u druge svrhe.', 0);
INSERT INTO property VALUES (1,9214, 'hr', 'definition','Ocijenjena vrijednost sastojaka vode (organskih, anorganskih, kemijskih ili fizičkih).', 0);
INSERT INTO property VALUES (1,9215, 'hr', 'definition','Direktive EZ-a kojima se utvrđuju pravila o vodi za ljudsku potrošnju.', 0);
INSERT INTO property VALUES (1,10985, 'hr', 'definition','Upravno tijelo ili sustav u kojem se provodi državna politika ili kontrola nad određenim područjem ili administrativnim dijelom grada, županije ili nekoga većeg zemljopisnog područja.', 0);
INSERT INTO property VALUES (1,10988, 'hr', 'definition','Vještine, znanje, kvalifikacije, sposobnosti ili ovlasti povezane s glavnom osobom upravnog odjela odnosno ministarstva ili drugom visokom službenom osobom koju bira šef države.', 0);
INSERT INTO property VALUES (1,10991, 'hr', 'definition','Domaći ili naturalizirani član države ili nacije koja duguje lojalnost, ima odgovornosti i dobiva prava, uključujući zaštitu, od države.', 0);
INSERT INTO property VALUES (1,10992, 'hr', 'definition','Upravno tijelo ili sustav u kojem se provodi državna politika i kontrola nad nekom zajednicom ili gradom, mjestom ili malim okrugom.', 0);
INSERT INTO property VALUES (1,10993, 'hr', 'definition','Infrastruktura, zajednica ljudi ili homogena jedinica koja je fizički smještena u lokaliziranom ladanjskom području manjih naselja dalje od grada.', 0);
INSERT INTO property VALUES (1,10999, 'hr', 'definition','Formalna rasprava ili spor o određenoj stvari između zastupnika.', 0);
INSERT INTO property VALUES (1,11007, 'hr', 'definition','Temeljni razlog ili uzrok, psihološki ili društveni čimbenik, koji potiče ili ohrabruje menadžere, rukovoditelje ili nadzornike za dovršenje zadataka kojima se ostvaruju ciljevi organizacije ili tvrtke.', 0);
INSERT INTO property VALUES (1,11016, 'hr', 'definition','Nacrt, shema ili projekt koji se odnose na proizvodnju, distribuciju i korištenje prihoda, bogatstva i robe.', 0);
INSERT INTO property VALUES (1,11018, 'hr', 'definition','Temeljni nauk ili načelo da se donošenje odluka o politikama treba odvijati na najdecentraliziranijoj razini, u kojoj središnje upravno tijelo ne bi poduzimalo nikakvo djelovanje osim ako je takvo djelovanje učinkovitije od poduzete radnje na nižoj razini vlasti.', 0);
INSERT INTO property VALUES (1,11030, 'hr', 'definition','Prijenos financijskih sredstava iz razvijenih država u nerazvijene.', 0);
INSERT INTO property VALUES (1,11031, 'hr', 'definition','Ekonomska, vojna, tehnička i financijska pomoć ili potpora koja se daje narodima ili zemljama u potrebi, često od drugih država ili međunarodnih ili međuvladinih organizacija.', 0);
INSERT INTO property VALUES (1,11032, 'hr', 'definition','Državna potpora u obliku novca ili bonova za hranu za siromašne, osobe s invaliditetom, starije osobe ili za uzdržavanu djecu.', 0);
INSERT INTO property VALUES (1,11034, 'hr', 'definition','Organizirana metoda bilježenja svih poslovnih transakcija u nacionalnom gospodarstvu.', 0);
INSERT INTO property VALUES (1,11035, 'hr', 'definition','Posebna financijska evidencija ili izvod koji otkriva financijsku aktivnost u određenom području i dopunjuje postojeće financijske evidencije.', 0);
INSERT INTO property VALUES (1,11038, 'hr', 'definition','Svi izdaci neke osobe koja živi sama ili skupina ljudi koji žive zajedno u zajedničkom kućanstvu i sa zajedničkim izdacima kućanstva.', 0);
INSERT INTO property VALUES (1,11039, 'hr', 'definition','Potrošnja nacionalne ili lokalne vlasti, poduzeća u državnom vlasništvu ili kvazi-autonomnih nevladinih organizacija.', 0);
INSERT INTO property VALUES (1,11043, 'hr', 'definition','Novčana ili financijska sredstva namijenjena za posredovanje ili miješanje u neko poslovanje s ciljem da se utječe na neki ishod.', 0);
INSERT INTO property VALUES (1,11044, 'hr', 'definition','Novčana sredstva namijenjena za neku svrhu.', 0);
INSERT INTO property VALUES (1,11045, 'hr', 'definition','Fond u organizaciji Europskog monetarnog sustava u kojem članovi Europske zajednice polažu pričuve da bi osigurali masu sredstava za stabilizaciju tečaja, te za financiranje platne bilance kao potporu do ostvarenja pune Europske monetarne unije.', 0);
INSERT INTO property VALUES (1,11046, 'hr', 'definition','Međunarodna organizacija osnovana 1944. god., povezana s  Ujedinjenim narodima. Djeluje kao međunarodna banka u olakšavanju razmjene nacionalnih valuta i daje zajmove zemljama članicama. Također vrednuje uspješnost gospodarstava zemalja u cijelom svijetu.', 0);
INSERT INTO property VALUES (1,11047, 'hr', 'definition','Nagrade ili kazne koje daje država ili upravljačka struktura za poticanje gospodarskog sektora, tvrtke ili skupine radnika da djeluju na takav način da ostvare planirane rezultate odnosno  ciljeve politike.', 0);
INSERT INTO property VALUES (1,11050, 'hr', 'definition','Ukupno proizvedena dobra i usluge nekoga nacionalnog gospodarstva u određenom razdoblju, najčešće godini, vrednovano prema tržišnim cijenama. Iskazuje se kao bruto vrijednost, jer se ne uračunava vrijednost zamjene kapitalnih dobara.', 0);
INSERT INTO property VALUES (1,11051, 'hr', 'definition','Obvezne naknade koje uvodi jedinica državne uprave u posebnom ili jedinstvenom slučaju u svrhu podizanja prihoda za plaćanje usluga ili poboljšanja za opće javno dobro.', 0);
INSERT INTO property VALUES (1,11057, 'hr', 'definition','Povremena ili kontinuirana provjera računa, imovine i obveza (aktive i pasive) trgovačkog društva ili druge organizacije, često da se potvrdi sukladnost s pravnim i profesionalnim standardima.', 0);
INSERT INTO property VALUES (1,11061, 'hr', 'definition','Procjena prirodnih struktura, resursa i krajolika kako bi se osiguralo njihovo razborito upravljanje i očuvanje.', 0);
INSERT INTO property VALUES (1,11063, 'hr', 'definition','Vrijednost ili iznos novca koji se daje u zamjenu za  proizvodnju i stalnu opskrbu vodom.', 0);
INSERT INTO property VALUES (1,11065, 'hr', 'definition','Kupnja, prodaja, oglašavanja, otprema i skladištenje robe u skladu s ekološkim načelima.', 0);
INSERT INTO property VALUES (1,11069, 'hr', 'definition','Vrsta stila vođenja i upravljanja kojim se pokušava steći lojalnost i podrška podređenih pretjeranim skrbljenjem o njihovim potrebama i interesima.', 0);
INSERT INTO property VALUES (1,11070, 'hr', 'definition','Vrijednosnice koje se drže za stvaranje prihoda u obliku kamata i dividendi s ciljem kontrole i smanjenje onečišćenja ili tvari u okolišu koje se smatraju štetnima za ljudsko zdravlje i prirodne resurse.', 0);
INSERT INTO property VALUES (1,11073, 'hr', 'definition','Nagrada ili bonus dani kao poticaj ili nagrada za napore na smanjenju prisutnosti onečišćenja ili tvari u okolišu koji se smatraju štetnima za ljudsko zdravlje i prirodne resurse.', 0);
INSERT INTO property VALUES (1,11082, 'hr', 'definition','Primjena novčane stope ili vrijednosti po kojoj se voda može kupovati ili prodavati.', 0);
INSERT INTO property VALUES (1,11087, 'hr', 'definition','Strukturirana simulacija ili objašnjenje temeljeno na promatranju, eksperimentiranju i zaključivanju kojim se nastoje pokazati, iznijeti značajke ili objasniti akcije i interakcije sveukupnih okolnih uvjeta danog sustava.', 0);
INSERT INTO property VALUES (1,11095, 'hr', 'definition','Faza u rastu i napretku u bilo kojem aspektu zajednice koja je definirana ili ograničena na određeni i obično mali okrug ili područje.', 0);
INSERT INTO property VALUES (1,11097, 'hr', 'definition','Prikupljanje i proučavanje podataka radi utvrđivanja prognoza o potražnji za nekim artiklom ili uslugom.', 0);
INSERT INTO property VALUES (1,11100, 'hr', 'definition','Trgovina koja je neopterećena zaprekama kao što su carine, uvozne i izvozne kvote te druge mjere koje ometaju slobodno kretanje robe i usluga između država.', 0);
INSERT INTO property VALUES (1,11103, 'hr', 'definition','Jedna od  najsiromašnijih zemalja na svijetu, obično male površine i s malobrojnim stanovništvom, s niskim dohotkom po stanovniku, niskom razinom pismenosti i medicinskim standardima, poljoprivredom za vlastite potrebe i nedostatkom iskoristivih minerala i konkurentne industrije.', 0);
INSERT INTO property VALUES (1,11106, 'hr', 'definition','Veze, udruživanja ili uključivanje razvijenih zemalja, koje se uglavnom nalazi na sjevernoj hemisferi, sa zemljama u razvoju, koje se uglavnom nalaze na južnoj hemisferi.', 0);
INSERT INTO property VALUES (1,2422, 'hr', 'definition','Vrsta propisa koje izdaje Europska unija i koji su obvezujući za države članice u pogledu rezultata koji se žele postići, ali koji prepuštaju državama članicama izbor metoda.', 0);
INSERT INTO property VALUES (1,2423, 'hr', 'definition','Direktiva kojom se uređuje stavljanje biocidnih proizvoda na tržište.', 0);
INSERT INTO property VALUES (1,2424, 'hr', 'definition','Direktiva EZ-a predložena 15. srpnja 1992. god., usmjerena na usklađivanje nacionalnih mjera u vezi s gospodarenjem ambalažom i ambalažnim otpadom; ova direktiva odnosi se na svu ambalažu puštenu u promet.', 0);
INSERT INTO property VALUES (1,2425, 'hr', 'definition','Direktiva EZ-a čiji se glavni cilj odnosi se prevenciju otpada, recikliranje i pretvaranje otpada u alternativne izvore energije.', 0);
INSERT INTO property VALUES (1,2426, 'hr', 'definition','Direktiva koja se odnosi na korištenje i gospodarenje vodnim resursima za racionalan gospodarski i društveni razvoj i zaštitu s njima povezanih značajki okoliša.', 0);
INSERT INTO property VALUES (1,2427, 'hr', 'definition','Inicijativa Europske zajednice (EZ) za poticanje promicanja ekološki prihvatljivih proizvoda. Taj program se počeo upotrebljavati krajem 1992. god. i bio je osmišljen kako bi se identificirali proizvodi koji su manje štetni za okoliš od ekvivalentnih proizvoda s robnim markama. Na primjer, eko-oznake će se  dodjeljivati proizvodima koji ne sadrže klorofluorougljike (CFC), koji oštećuju ozonski omotač, onim proizvodima koji se mogu reciklirati ili su reciklirani, te onima koji su energetski učinkoviti. Oznake se dodjeljuju prema ekološkim kriterijima Europske zajednice. One pokrivaju cijeli životni ciklus nekog proizvoda, od dobivanja sirovina, preko proizvodnje, distribucije, uporabe i zbrinjavanja proizvoda. Prvi proizvodi koji će nositi eko-oznake EZ-a su perilice rublja, papirnati ručnici, papir za pisanje, žarulje i lakovi za kosu.', 0);
INSERT INTO property VALUES (1,2428, 'hr', 'definition','Morske celomatske životinje razlikuju se od svih ostalih po unutarnjem kosturu sastavljenom od kalcitnih ploča, i vodeno-vaskularnog sustava koji služi za kretanje, disanje, prehranu i percepciju.', 0);
INSERT INTO property VALUES (1,2434, 'hr', 'definition','1) Konzervativni razvoj temeljen na dugoročnoj optimalizaciji resursa biosfere.
INSERT INTO property VALUES (1,2435, 'hr', 'definition','Inicijativa Europske zajednice (EZ) za poticanje promicanja ekološki prihvatljivih proizvoda. Taj program se počeo upotrebljavati krajem 1992. god. i bio je osmišljen kako bi se identificirali proizvodi koji su manje štetni za okoliš od ekvivalentnih proizvoda s robnim markama. Smatralo se da će kupnjom robe s eko-oznakama potrošači moći izvršiti  pritisak na proizvođače i maloprodajne trgovce i za izradu i za prodaju "zelenijih" proizvoda. To uključuje učinke koje oni imaju na okoliš u svim fazama. Eko-oznake se dodjeljuju prema ekološkim kriterijima Europske zajednice.', 0);
INSERT INTO property VALUES (1,2437, 'hr', 'definition','Broj pojedinačnih primjeraka životinja ili biljaka koji se uoči u određenom vremenskom razdoblju na određenom mjestu.', 0);
INSERT INTO property VALUES (1,2438, 'hr', 'definition','Takva promjena u organizmu da on ima bolje mogućnosti opstanka ili reprodukcije, čime pridonosi svojoj otpornosti.', 0);
INSERT INTO property VALUES (1,2440, 'hr', 'definition','Ekološka procjena sastoji se u praćenju postojećih i promjenjivih uvjeta u ekološkim resursima iz kojih se uspjeh ili neuspjeh ekološkog sustava može procijeniti bez pristranosti ili predrasuda; potpunijeg razumijevanja strukture i funkcija ekoloških sustava u cilju razvoja boljih mogućnosti upravljanja; razvoju modela kojima se predviđaju reakcije ekoloških sustava na promjene koje proizlaze iz stresa što ga izaziva čovjek, iz kojih je moguće ocijeniti strategije upravljanja ekološkim sustavima, a mogu se i procijeniti ekološke posljedice poteza uprave, tako da donositelji odluka najbolje mogu razumjeti ishode izbora određene strategije upravljanja.', 0);
INSERT INTO property VALUES (1,2441, 'hr', 'definition','Stanje ravnoteže između sastavnica prirodne zajednice, tako da njihov relativni broj ostaje manje-više konstantan, a da je njihov ekološki sustav stabilan. Stalno se odvijaju postupne ponovne prilagodbe sastavu uravnotežene zajednice kao odgovor na prirodnu ekološku sukcesiju i na promjene u klimatskim i drugim utjecajima.', 0);
INSERT INTO property VALUES (1,2442, 'hr', 'definition','Sustavno računovodstvo ili vođenje evidencije o utjecaju na okoliš neke tvrtke ili njezinom napretku prema ekološki razboritoj poslovnoj praksi.', 0);
INSERT INTO property VALUES (1,2450, 'hr', 'definition','Ekološki faktor koji, u određenim uvjetima, može ostvarivati  znatan utjecaj na organizme ili njihove zajednice, a to je uzrokom povećanja ili smanjenja broja organizama i/ili promjena u zajednicama.', 0);
INSERT INTO property VALUES (1,2547, 'hr', 'definition','Čin ili proces prenošenja ili stjecanja znanja ili vještina.', 0);
INSERT INTO property VALUES (1,2551, 'hr', 'definition','Organizacija ili ustanova koja se bavi radnjama ili procesima prenošenja ili stjecanja znanja ili vještina.', 0);
INSERT INTO property VALUES (1,2554, 'hr', 'definition','Proces organiziranja ili pripreme za pospješivanje obuke, nastave ili studija, koji vode do stjecanja znanja ili vještina, ili do razvoja razmišljanja i prosudbe.', 0);
INSERT INTO property VALUES (1,2555, 'hr', 'definition','Tijek djelovanja koji usvoji i provodi vlada ili neka druga organizacija, koja promiče i određuje ciljeve, metode i programe koji će se upotrebljavati za obuku, nastavu ili studij koji vodi do stjecanja vještina ili znanja, ili razvoj razmišljanja i prosudbe.', 0);
INSERT INTO property VALUES (1,2557, 'hr', 'definition','Svaka formulirana, redovna ili posebna organizacija nastave, obuke ili otkrivanja znanja, a osobito institucionalnih struktura koji podupiru te napore.', 0);
INSERT INTO property VALUES (1,2562, 'hr', 'definition','Učinci uključuju: a) izravne učinke, koji su prouzročeni djelovanjem i pojavljuju se u isto vrijeme i na istom mjestu, b) neizravne učinke, koji su prouzročeni djelovanjem i pojavljuju se kasnije u vremenu ili su više udaljeni, a još se uvijek mogu razumno predvidjeti.', 0);
INSERT INTO property VALUES (1,2566, 'hr', 'definition','Rezultanta prirodnih učinaka ili onih koje je izazvao čovjek  u fizičkim, kemijskim ili biološkim sastavnicama koje tvore okoliš.', 0);
INSERT INTO property VALUES (1,2568, 'hr', 'definition','Istraživanje koje se provodi radi procjenjivanja rezultata koji proizlaze iz neke radnje ili stanja; općeniti izraz koji se primjenjuje u mnogim različitim područjima.', 0);
INSERT INTO property VALUES (1,2569, 'hr', 'definition','Parametar ili pravilo za procjenu sposobnosti u uspješnosti proizvodnje u odnosu prema ulaznim veličinama resursa.', 0);
INSERT INTO property VALUES (1,2570, 'hr', 'definition','Omjer outputa prema inputu - izlaznih veličina prema ulaznima - koji se obično navodi u obliku postotka.', 0);
INSERT INTO property VALUES (1,2571, 'hr', 'definition','Otpadne tekućine iz kanalizacije iz kućanstava, industrijskih postrojenja ili iz poljoprivrednih procesa. Otpadne vode su štetne kada uđu u okoliš, osobito u slatkovodni, zbog njihovog kemijskog sastava koji izaziva onečišćenje.', 0);
INSERT INTO property VALUES (1,2573, 'hr', 'definition','Velika, ženska spolna stanica zatvorena u poroznoj, vapnenoj ili kožastoj ljusci, koje stvaraju ptice i gmazovi.', 0);
INSERT INTO property VALUES (1,2578, 'hr', 'definition','Direktiva Vijeća od 27. lipnja 1985. god. o procjeni učinaka određenih javnih i privatnih projekata na okoliš (85/337/EEZ). Direktiva se primjenjuje na projekte za koje je vjerojatno da će imati značajne učinke na okoliš s obzirom na njihovu prirodu, veličinu ili lokaciju.', 0);
INSERT INTO property VALUES (1,2579, 'hr', 'definition','Zakon o procjeni učinaka određenih javnih i privatnih projekata na okoliš, na temelju direktive Vijeća EZ-a br. 85/337.', 0);
INSERT INTO property VALUES (1,2580, 'hr', 'definition','Utvrđivanje, evaluacija i procjena ekoloških posljedica predloženog projekta ili razvoja grada, mjesta ili regije, te mjera koje su potrebne kako bi se smanjili štetni učinci.', 0);
INSERT INTO property VALUES (1,2582, 'hr', 'definition','Sposobnost materijala da nakon deformacije ponovno poprimi prvobitne dimenzije.', 0);
INSERT INTO property VALUES (1,2584, 'hr', 'definition','Netko tko je dosegao kasniju životnu fazu ili tko je navršio određenu dob u toj fazi.', 0);
INSERT INTO property VALUES (1,2586, 'hr', 'definition','Inženjering koji se bavi praktičnim primjenama električne energije; općenito je ograničen na primjene koje uključuju protok električne struje kroz vodič, kao u slučaju motora i generatora.', 0);
INSERT INTO property VALUES (1,2587, 'hr', 'definition','Industrija za proizvodnju električne energije.', 0);
INSERT INTO property VALUES (1,2592, 'hr', 'definition','Opći izraz koji se upotrebljava za sve pojave izazvane električnim nabojem, statičkim ili u pokretu.', 0);
INSERT INTO property VALUES (1,2593, 'hr', 'definition','Poduzeće odgovorno za opskrbu i distribuciju električne energije na određenom području.', 0);
INSERT INTO property VALUES (1,2594, 'hr', 'definition','Količina električne energije koju troši neki uređaj.', 0);
INSERT INTO property VALUES (1,2595, 'hr', 'definition','Radnja ili proces pretvaranja drugih oblika energije u električnu energiju.', 0);
INSERT INTO property VALUES (1,2596, 'hr', 'definition','Vrijednost ili iznos novca koji se naplaćuje za proizvodnju i trajno napajanje strujom nabijenih iona koji se upotrebljavaju kao izvor energije.', 0);
INSERT INTO property VALUES (1,2597, 'hr', 'definition','Industrija opskrbe i distribucije električne energije.', 0);
INSERT INTO property VALUES (1,2599, 'hr', 'definition','Žice koje provode električnu energiju s jednog mjesta na drugo; također poznate pod nazivom elektroenergetske linije.', 0);
INSERT INTO property VALUES (1,2601, 'hr', 'definition','Brzina kojom se električna energija pretvara u druge oblike energije, jednaka umnošku struje i napona.', 0);
INSERT INTO property VALUES (1,2606, 'hr', 'definition','Stacionarno postrojenje koje sadrži uređaj za veliku pretvorbu nekog oblika energije (kao što su hidroenergija, parna, kemijska ili nuklearna energija) u električnu energiju.', 0);
INSERT INTO property VALUES (1,2617, 'hr', 'definition','Proučavanje kretanja električnog naboja, pogotovo nepromjenjivom strujom u strujnim krugovima, i kretanja nabijenih čestica u električnim ili magnetskim poljima.', 0);
INSERT INTO property VALUES (1,2618, 'hr', 'definition','Izazivanje kemijske reakcije propuštanjem električne struje kroz elektrolit. Pri elektrolizi se pozitivni ioni kreću prema katodi, a negativni prema anodi.', 0);
INSERT INTO property VALUES (1,2624, 'hr', 'definition','Proučavanje, kontrola i primjena provođenja električne struje kroz plinove ili vakuum ili kroz poluvodiče ili vodiče.', 0);
INSERT INTO property VALUES (1,2625, 'hr', 'definition','Svaki materijal od elektroničkih uređaja i sustava koji nastane kao vrsta otpada tijekom procesa proizvodnje ili je odbačen nakon uporabe.', 0);
INSERT INTO property VALUES (1,2626, 'hr', 'definition','Pravilo koje propiše država ili uprava o odlaganju i recikliranju elektroničkih dijelova, sklopova i sustava, a posebno računalnih uređaja.', 0);
INSERT INTO property VALUES (1,2628, 'hr', 'definition','Onečišćenje izazvano električnim i magnetskim poljima koja stvaraju električni vodovi, električna oprema, mobilni i bežični telefoni, radar, električni kućanski aparati, mikrovalne pećnice, radioaparati, računala, električni satovi itd.', 0);
INSERT INTO property VALUES (1,2633, 'hr', 'definition','Sva oprema povezana s tehnološkom upotrebom električne energije.', 0);
INSERT INTO property VALUES (1,2634, 'hr', 'definition','Sektor gospodarstva u kojemu se većina komercijalnih poduzeća bavi konstruiranjem, proizvodnjom i prodajom strojeva, uređaja i potrošnog materijala za proizvodnju, skladištenje i upotrebu električne energije, kao što su kućanski aparati, radijski i televizijski prijemnici te rasvjeta i žičana oprema.', 0);
INSERT INTO property VALUES (1,2635, 'hr', 'definition','Skupina jednoatomskih plinovitih elemenata koji čine 18. skupinu (prijašnja skupina 0) periodnog sustava: helij (He), neon (Ne), argon (Ar), kripton (Kr), ksenon (Xe) i radon (Rn).', 0);
INSERT INTO property VALUES (1,2636, 'hr', 'definition','Svaki monovalentni metal od sljedećih: litij, natrij, kalij, rubidij, cezij i francij, koji pripada 1.A skupini periodnog sustava elemenata. Svi su veoma reaktivni i elektropozitivni.', 0);
INSERT INTO property VALUES (1,2637, 'hr', 'definition','Svaki od dvovalentnih elektropozitivnih metala: berilij, magnezij, kalcij, stroncij, barij ili radij, koji pripadaju 2.A skupini periodnog sustava elemenata.', 0);
INSERT INTO property VALUES (1,2638, 'hr', 'definition','Treća skupina sastoji se od dvije podskupine: 3.a skupine i 3.b skupine. U 3.a skupinu pripadaju skandij, itrij i lantan, koji se obično svrstava u lantanide, i aktinij, koji se svrstava s aktinoidima. Glavna, 3.b skupina sastoji se od bora, aluminija, galija, indija i talija.', 0);
INSERT INTO property VALUES (1,2639, 'hr', 'definition','Četvrta se skupina sastoji od dvije podskupine: 4.b skupina, koja je glavna, i 4.a skupina. 4.a skupina sastoji se od titanija, cirkonija i hafnija, koji se inače svrstavaju u prijelazne metale. U glavnu skupinu svrstavaju se ugljik, silicij, germanij, kositar i olovo. Glavna je valencija ovih elemenata 4, a članovi skupine variraju od nemetala do metala od vrha prema dnu skupine. Reaktivnost ovih elemenata povećava se u skupini od ugljika prema olovu. Svi reagiraju na kisik i zagrijavanje.', 0);
INSERT INTO property VALUES (1,2640, 'hr', 'definition','Peta skupina sastoji se od dvije podskupine: glavne, 5.b skupina i 5.a skupine. 5.a skupina sastoji se od vanadija, niobija i tantala, koji se inače svrstavaju u prijelazne elemente. Glavna se skupina sastoji od dušika, fosfora, arsena, antimona i bizmuta.', 0);
INSERT INTO property VALUES (1,2641, 'hr', 'definition','Šesta se skupina sastoji od dvije podskupine: glavne, 6.b skupine i 6.a skupine. 6.a skupina sastoji se od kroma, molibdena i volframa. Glavna se skupina sastoji od kisika, sumpora, selenija, telurija i polonija.', 0);
INSERT INTO property VALUES (1,2642, 'hr', 'definition','Svaki element iz skupine halogenih elemenata, koja se sastoji od fluora, klora, broma, joda i astata.', 0);
INSERT INTO property VALUES (1,2643, 'hr', 'definition','Tvar koja se sastoji od atoma s istim atomskim brojem; poznati primjeri su vodik, zlato i željezo.', 0);
INSERT INTO property VALUES (1,2651, 'hr', 'definition','Stanje slobode od društvenih ili političkih ograničenja ili od inhibicije moralnih ili društvenih konvencija.', 0);
INSERT INTO property VALUES (1,2653, 'hr', 'definition',' Rani stadij razvoja kod višestaničnih organizama.', 0);
INSERT INTO property VALUES (1,2654, 'hr', 'definition','Stvaranje i razvoj zametka iz jajne stanice.', 0);
INSERT INTO property VALUES (1,2656, 'hr', 'definition','Program postupaka koje treba poduzeti u slučaju iznenadne, hitne i obično neočekivane pojave koja zahtijeva hitno djelovanje, osobito kad se radi o incidentu koji potencijalno može ugroziti ljudski život, imovinu ili okoliš.', 0);
INSERT INTO property VALUES (1,2657, 'hr', 'definition','Novac, hrana ili druge vrste pomoći za osobe koje prežive naglu i obično neočekivanu pojavu koja zahtijeva hitno djelovanje, osobito kad se radi o incidentu koji potencijalno može ugroziti ljudski život, imovinu ili okoliš.', 0);
INSERT INTO property VALUES (1,2661, 'hr', 'definition','Sklonište za ljude koji su nakon katastrofe ostali bez osnovnih sredstava za život.', 0);
INSERT INTO property VALUES (1,2663, 'hr', 'definition','Ispuštanje čestica plinovitih ili topivih materijala u otpadu ili onečišćenja u zrak iz izvora onečišćenja.', 0);
INSERT INTO property VALUES (1,2665, 'hr', 'definition','Postupci kojima se nastoji smanjiti ili spriječiti šteta prouzročena emisijama u zrak.', 0);
INSERT INTO property VALUES (1,2666, 'hr', 'definition','Podaci koji se odnose na onečišćujuće tvari ispuštene u okoliš iz stalnih ili mobilnih postrojenja ili proizvoda.', 0);
INSERT INTO property VALUES (1,2667, 'hr', 'definition','Odnos između količine proizvedenih onečišćujućih tvari i količine obrađenih sirovina ili potrošenog goriva u bilo kojem procesu onečišćenja.', 0);
INSERT INTO property VALUES (1,2668, 'hr', 'definition','Posljednji korak u planu za čist zrak jest predvidjeti kakvoću zraka u budućnosti kako bismo  pokazali da možemo (ako možemo) dosegnuti zdravstvene standarde provedbom mjera predloženih u planu. To se radi na način da se prvo izradi prognoza svih emisija u budućnosti uzimajući u obzir promjene u populaciji, stanovanju, zapošljavanju u posebnim poslovnim sektorima, kao i kilometražu vozila. Ovi podaci dobivaju se iz različitih izvora, emisije nastale iz takvih izvora prilagođavaju se da bi se uračunali propisi i mjere nadzora predviđene za provedbu u istom vremenskom razdoblju. Rade se i dodatne prilagodbe da bi se obuhvatili i veliki objekti (postrojenja) za koje se očekuje da će se otvoriti, mijenjati ili zatvoriti. Time se dobije prognoza ''inventara'' emisija, koja se obično izražava u tonama po danu pojedinih onečišćujućih tvari za određenu godinu. Mogu biti potrebni dodatni koraci da se utvrdi kako će predviđena količina onečišćenja zraka utjecati na ukupnu kakvoću zraka. Jedan od načina da se to uradi jest računalno modeliranje. Računalni model simulira kako se onečišćujuće tvari šire, reagiraju i gibaju po zraku. Ulazne vrijednosti za takav računalni model su složene. One uključuju vremenske obrasce, teren i kemijsku prirodu onečišćujućih tvari u zraku.', 0);
INSERT INTO property VALUES (1,2670, 'hr', 'definition','Čin ili proces ograničavanja ili smanjenja ispuštanja onečišćujućih tvari ili kontaminanata, npr. utvrđivanjem ograničenja emisija ili prilagodbom izvora emisije.', 0);
INSERT INTO property VALUES (1,2671, 'hr', 'definition','Sustav za bilježenje smanjenja emisije koja će se upotrijebiti kasnije u trgovanju emisijama. Pogonski kompleksi koji značajno smanjuju emisiju mogu "unovčiti" svoje "kredite" ili ih prodati drugim industrijama.', 0);
INSERT INTO property VALUES (1,2672, 'hr', 'definition','Popis po izvorima dnevnih količina onečišćujućih tvari u zraku koje neka zajednica dnevno ispusti u atmosferu.', 0);
INSERT INTO property VALUES (1,2674, 'hr', 'definition','Kemijski proces, zgrada, peć, postrojenje ili drugi subjekt odgovoran za ispuštanje onečišćujućih tvari u okoliš.', 0);
INSERT INTO property VALUES (1,2677, 'hr', 'definition','Najveća dopuštena količina ispuštanja iz jednog izvora, pomičnog ili nepomičnog.', 0);
INSERT INTO property VALUES (1,2679, 'hr', 'definition','Ispuštanje krutih, tekućih ili plinovitih onečišćujućih tvari u vodno tijelo.', 0);
INSERT INTO property VALUES (1,2683, 'hr', 'definition','Posao ili zanimanje u kojem je osoba zaposlena.', 0);
INSERT INTO property VALUES (1,2684, 'hr', 'definition','Problematika ili inicijative koje se odnose na međusobni odnos između ekoloških problema i ekonomije zapošljavanja, uključujući održivi razvoj siguran za okoliš; učinak okolišnog aktivizma na poslove, te stvaranje zanimanja (profesija) vezanih uz okoliš.', 0);
INSERT INTO property VALUES (1,2687, 'hr', 'definition','Rezultat ili učinak određene politike, djelovanja ili događaja na broj radno aktivnog stanovništva u nekoj regiji, državi ili gospodarskom sektoru.', 0);
INSERT INTO property VALUES (1,2689, 'hr', 'definition','Proces raspršenja jedne tekućine u drugoj, koja se s njom ne miješa.', 0);
INSERT INTO property VALUES (1,2691, 'hr', 'definition','Stabilna disperzija jedne tekućine u drugoj, koja se s njom ne miješa, npr. Mlijeko (ulja/masnoće raspršene u vodi).', 0);
INSERT INTO property VALUES (1,2693, 'hr', 'definition','Oblaganje bilo kojeg onečišćujućeg proizvoda materijalom koji sprječava njegovo ispuštanje u okoliš.', 0);
INSERT INTO property VALUES (1,2696, 'hr', 'definition','Opsežno referentno djelo, često u više dijelova, koje sadržava obično abecedno poredane članke o širokom spektru tema ili o raznim aspektima određenog područja.', 0);
INSERT INTO property VALUES (1,2697, 'hr', 'definition','Životinje, ptice, ribe ili drugi živi organizmi kojima prijeti izumiranje zbog prirodnih promjena ili promjena uzrokovanih ljudskim djelovanjem u njihovoj okolini.', 0);
INSERT INTO property VALUES (1,2700, 'hr', 'definition','Biljne vrste kojima prijeti izumiranje zbog prirodnih promjena ili promjena uzrokovanih ljudskim djelovanjem u okolišu.', 0);
INSERT INTO property VALUES (1,2702, 'hr', 'definition','Jedan od tri stupnja "rijetkosti"  koje opisuje Međunarodno udruženje za očuvanje prirode (The International Union for the Conservation of Natural Resources). Svim biljnim i životinjskim vrstama u tim kategorijama potrebna je posebna zaštita. Ugrožene vrste su one vrste kojima prijeti izumiranje ako se ne poduzmu koraci da se promijeni uzrok prijetnje i smanjenja njihovog broja.', 0);
INSERT INTO property VALUES (1,2704, 'hr', 'definition','Prijetnja za kakvoću i količinu podzemnih voda zbog aktivnosti povezanih s iskorištavanjem tla. S obzirom na to da neke aktivnosti (npr. Odlagališta) predstavljaju poseban rizik od onečišćenja, što se aktivnost obavlja bliže vrelu vode ili bušotini, veća je opasnost od onečišćenja vode koja se crpi.  U obzir se mora uzeti vrsta tla, geologija, količina padalina i količina vode koja se crpi.', 0);
INSERT INTO property VALUES (1,2705, 'hr', 'definition','Sustav usklađivanja kemikalija kod životinja, odnosno endokrine žlijezde koje proizvode hormone.', 0);
INSERT INTO property VALUES (1,2706, 'hr', 'definition','Znanost o endokrinim žlijezdama i hormonima koje one sintetiziraju i izlučuju.', 0);
INSERT INTO property VALUES (1,2707, 'hr', 'definition','Pristup kontroli onečišćenja koji se usredotočuje na pročišćavanje otpadnih voda ili filtraciju prije ispuštanja u okoliš, za razliku od promjena u procesu koji uzrokuje porast količine otpada.', 0);
INSERT INTO property VALUES (1,2712, 'hr', 'definition','Sposobnost da se obavi neki rad, a uključuje toplinsku energiju (toplinu), svjetlosnu energiju (svjetlost), kinetičku energiju (gibanje) ili kemijsku energiju. Mjeri se u džulima (eng. Joul).', 0);
INSERT INTO property VALUES (1,2713, 'hr', 'definition','Energetsko stanje nekog sustava u bilo kojem trenutku', 0);
INSERT INTO property VALUES (1,2714, 'hr', 'definition','Strategija za smanjenje potreba za energijom po jedinici industrijske proizvodnje ili individualno blagostanje bez utjecaja na napredak socioekonomskog razvoja ili uzrokovanja poremećaja u načinu života. U zemljama srednjeg razvoja većina energije upotrebljava se za grijanje i rasvjetu industrijskih i stambenih zgrada. Industrijski procesi, promet i poljoprivreda ostali su glavni korisnici. U sedamdesetim godinama dvadesetog stoljeća pokazalo se da se značajna ušteda može postići prikladnom tehnologijom gradnje i upotrebom energetski učinkovite opreme za grijanje, klimatiziranje i rasvjetu. Većina dobara može se i treba se proizvoditi i napraviti tako da rade učinkovitije.', 0);
INSERT INTO property VALUES (1,2715, 'hr', 'definition','Količina energije koju potroši neka osoba ili naprava prikazana kao jedinica.', 0);
INSERT INTO property VALUES (1,2716, 'hr', 'definition','Proces pretvorbe energije iz jednog oblika u drugi.', 0);
INSERT INTO property VALUES (1,2720, 'hr', 'definition','Svaki gubitak energije, obično pretvorbom u toplinu.', 0);
INSERT INTO property VALUES (1,2722, 'hr', 'definition','Svaki javni ili privatni sustav koji osigurava dopremanje upotrebljive energije u kućanstva ili poslovne subjekte.', 0);
INSERT INTO property VALUES (1,2723, 'hr', 'definition','Proizvodnja, distribuiranje i upotreba energije kao što su fosilna goriva, električna struja ili solarno zračenje.', 0);
INSERT INTO property VALUES (1,2724, 'hr', 'definition','Odnosi se na aktivnosti koje se poduzimaju s ciljem da se štede goriva boljim građevinskim projektima, modificiranjem proizvodnih procesa, boljim izborom cestovnih vozila i prometne politike, prihvaćanjem planova toplinskog grijanja povezanih s proizvodnjom električne energije te upotrebom izolacije i dvostrukog ostakljenja u kućanstvima.', 0);
INSERT INTO property VALUES (1,2730, 'hr', 'definition','Upravljanje ili rukovanje energijom koju dobivamo iz izvora poput fosilnih goriva, električna struja i solarnog zračenja.', 0);
INSERT INTO property VALUES (1,2731, 'hr', 'definition','Trgovina energetskim izvorima ili promet energetskih izvora koji se tretiraju kao roba (npr. Fosilna goriva, električna struja ili solarno zračenje).', 0);
INSERT INTO property VALUES (1,2733, 'hr', 'definition','Izjava o namjerama neke države u energetskom sektoru.', 0);
INSERT INTO property VALUES (1,2734, 'hr', 'definition','Bilo koja prirodna pojava ili niz događaja kojima dolazi do pretvorbe energije ili ona postaje iskoristivija.', 0);
INSERT INTO property VALUES (1,2735, 'hr', 'definition','Dobivanje energije u elektrani na ugljen, naftu, ili u nuklearnoj elektrani itd.', 0);
INSERT INTO property VALUES (1,2736, 'hr', 'definition','Oblik obnavljanja izvora pri kojem se organski dio otpada pretvara u neki oblik iskoristive energije.  Obnavljanje se može postići spaljivanjem obrađenog ili sirovog otpada, pri čemu pirolizom otpadnog materijala nastaje para ili ulje, te anaerobnim probavljanjem organskog otpada, pri čemu se stvara plin metan.', 0);
INSERT INTO property VALUES (1,2738, 'hr', 'definition','Potencijalne zalihe energije koje još nisu iskorištene (npr. Ugljen pod zemljom, sunčeva toplina, energija vjetra, geotermalna energija itd.).', 0);
INSERT INTO property VALUES (1,2740, 'hr', 'definition','Izbjegavanje rasipanja energije.', 0);
INSERT INTO property VALUES (1,2741, 'hr', 'definition','Potencijalne zalihe energije, uključujući fosilna i nuklearna goriva, kao i solarnu, vodenu energiju, energiju vjetra, energiju plime i oseke te geotermalnu energiju.', 0);
INSERT INTO property VALUES (1,2742, 'hr', 'definition','Izvori iz kojih se može dobiti energija za grijanje i rasvjetu te električna energija. Energetski izvori, uključujući fosilne i nuklearne energiju, kao i solarnu, vodenu energiju, energiju plime i oseke i geotermalnu energiju, mogu se sačuvati ili obnoviti i pretvoriti u ostale oblike energije za razne primjene u kućanstvu, komercijalne, prometne i industrijske primjene.', 0);
INSERT INTO property VALUES (1,2744, 'hr', 'definition','Količina energetskih rezervi; često se odnosi na zalihe neobnovljivih izvora, npr. Naftu, koju, na primjer, posjeduje neka država', 0);
INSERT INTO property VALUES (1,2746, 'hr', 'definition','Opskrba energijom i skladištenje energije (sposobnost da se obavi neki rad ili izazove neka promjena), ili količina uskladištene energije za upotrebu lokalne zajednice (grada, općine) ili drugog korisnika energije.', 0);
INSERT INTO property VALUES (1,2748, 'hr', 'definition','Energija s obzirom na svoj izvor može biti hidroenergija, solarna energija, energija plime i oseke, energija vjetra, energija valova, geotermalna energija itd. Prema tipu goriva koje se upotrebljava za njezinu proizvodnju, energija može biti nuklearna, dobivena iz ugljena, nafte, biomase itd.', 0);
INSERT INTO property VALUES (1,2753, 'hr', 'definition','Izvršenje, provođenje ili stavljanje na snagu naloga, propisa, zakona ili službene uredbe.', 0);
INSERT INTO property VALUES (1,2754, 'hr', 'definition','Stroj u kojemu se snaga primjenjuje da obavi neki rad pretvorbom različitih oblika energije u mehaničku silu i gibanje.', 0);
INSERT INTO property VALUES (1,2758, 'hr', 'definition','obogaćeni uran - Uran čija se koncentracija urana -235, koji može podržati nuklearnu lančanu reakciju, povećava odstranjivanjem urana-238.', 0);
INSERT INTO property VALUES (1,2759, 'hr', 'definition','Proces povećanja broja određenog izotopa u mješavini izotopa. Obično se primjenjuje na povećanje udjela urana U-235, ili dodavanje Pu-239 prirodnom uranu za korištenje u nuklearnom reaktoru ili oružju.', 0);
INSERT INTO property VALUES (1,2760, 'hr', 'definition','Količina hranjivih tvari koja pokriva osnovne prehrambene potrebe, a stalno se osigurava za pojedinca, skupinu ljudi ili zajednicu.', 0);
INSERT INTO property VALUES (1,2766, 'hr', 'definition','Neočekivani događaj, kvar ili nedostatak, koji potencijalno može prouzročiti štetu za ekosustav ili prirodne resurse.', 0);
INSERT INTO property VALUES (1,2769, 'hr', 'definition','Konzultacije ili preporuke koje se daju kao smjernice o djelovanju u vezi s očuvanjem ekološke cjelovitosti i obranom ili zaštitom prirodnih resursa.', 0);
INSERT INTO property VALUES (1,2772, 'hr', 'definition','Arhitektura koja ne šteti okolišu zasniva se na sljedećih pet načela: 1) zdrav unutarnji okoliš; 2) energetska učinkovitost; 3) ekološki neškodljivi materijali; 4) ekološki prihvatljiv oblik; 5) dobar nacrt.', 0);
INSERT INTO property VALUES (1,2773, 'hr', 'definition','Naselja ljudi imaju negativan učinak na mnoge ekosustave i na same sebe zbog otrovnih ili štetnih tvari koje dospijevaju u vanjsku litosferu, hidrosferu i atmosferu. Glavne vrste onečišćivača okoliša su kanalizacija, metali u tragovima, naftni ugljikovodici, sintetički organski spojevi i emisije u obliku plinova. Većina, ako ne i sve, od dodataka potencijalno štetnih tvari u okoliš rezultat su rasta stanovništva i tehnološkog napretka industrijskih društava.', 0);
INSERT INTO property VALUES (1,2774, 'hr', 'definition','Vrednovanje ili procjena ekoloških ili prirodnih resursa.', 0);
INSERT INTO property VALUES (1,2775, 'hr', 'definition','Načelo ili norma za vrednovanje ili procjenu ekoloških ili prirodnih resursa.', 0);
INSERT INTO property VALUES (1,2776, 'hr', 'definition','Procjena o prirodi i opsegu bilo koje štete ili pogoršanja, ili eventualne štete ili pogoršanja, koja može biti nanesena bilo kojem vidu okoliša procesom bilo kakve aktivnosti, programa razvoja, ili bilo kojim proizvodom, kemikalijom ili otpadnom tvari. Revizije mogu biti osmišljene da se: potvrdi ili na drugi način uskladi s ekološkim zahtjevima, ocijeni učinkovitost postojećih sustava upravljanja okolišem; općenito procijene rizici, ili da se pomogne u planiranju za daljnja poboljšanja u zaštiti okoliša i kontroli onečišćenja.', 0);
INSERT INTO property VALUES (1,2778, 'hr', 'definition','Rast i razvoj osviještenosti, razumijevanja i svijesti o  biofizičkom okolišu i njegovim problemima, uključujući međusobno djelovanje i utjecaje ljudi.  Razmišljanje  "na ekološki način" ili o ekološkoj svijesti.', 0);
INSERT INTO property VALUES (1,2779, 'hr', 'definition','Konačni dio studije utjecaja na okoliš i procjena koja uspoređuje troškove i koristi od zaštite okoliša na temelju ujednačenih kriterija.', 0);
INSERT INTO property VALUES (1,2782, 'hr', 'definition','Promjene koje mogu nastupiti u ekosustavima, klimi, tlu, staništima itd. Zbog pritisaka raznih porijekla.', 0);
INSERT INTO property VALUES (1,2784, 'hr', 'definition','Znanost koja se bavi fizikalnim, kemijskim i biokemijskim procesima kroz koja prolaze onečišćujuće tvari kad se unose u okoliš.', 0);
INSERT INTO property VALUES (1,2786, 'hr', 'definition','Stanje određenih proizvoda ili projekata koji imaju smanjeni učinak ili su manje opterećenje za prirodni okoliš.', 0);
INSERT INTO property VALUES (1,2789, 'hr', 'definition','Rezultanta prirodnih poremećaja ili poremećaja koje je izazvao čovjek na fizičkim, kemijskim ili biološkim komponentama od kojih se sastoji okoliš.', 0);
INSERT INTO property VALUES (1,2790, 'hr', 'definition','Napori i djelovanja da se održa i zadržavaju značajke prirodnog i urbanog okoliša koje su ključne i za čovjekovo fizičko i za mentalno zdravlje i za uživanje u životu.', 0);
INSERT INTO property VALUES (1,2793, 'hr', 'definition','Izrada organiziranih, programskih i koordiniranih postupaka za slučaj neke nesreće, katastrofe ili događaja koji ugrožava ekosustav i zdravlje čovjeka ili prirodnih resursa u njemu.', 0);
INSERT INTO property VALUES (1,2795, 'hr', 'definition','Zaštita okoliša politikama koje se odnose na kontrolu otpada, poboljšanje okoliša koji je stvorio čovjek, zaštitu vrijednosti baštine, osnivanje nacionalnih parkova i rezervata, zaštitu biljnog i životinjskog svijeta, očuvanje šuma i krajobraza itd.', 0);
INSERT INTO property VALUES (1,2796, 'hr', 'definition','Troškovi nastali kao posljedica nekog narušavanja cjelovitosti okoliša, bilo takvog koje počini neko poduzeće koje provodi  program za popravljanje stanja, ili društvo ili ekosustav u cjelini, u kojem slučaju se smatra odgovornim nijedna određena osoba ili poduzeće.', 0);
INSERT INTO property VALUES (1,2797, 'hr', 'definition','Nezakonito djelovanje protiv okoliša, kao što je onečišćenje vode, odlaganje opasnog otpada, onečišćenje zraka, nedopuštena gradnja postrojenja, izljevi nafte itd.', 0);
INSERT INTO property VALUES (1,2798, 'hr', 'definition','Svi zakonski akti koji se odnose na djelovanja ili pojedinačne slučajeve zanemarivanja okoliša koji se smatraju štetnim za javno blagostanje ili državne interese i koji su zabranjeni zakonom.', 0);
INSERT INTO property VALUES (1,2799, 'hr', 'definition','Norme fizikalnih, kemijskih ili bioloških (ali ponekad uključujući i društvene, estetske itd.) sastavnica koje definiraju određenu kakvoću nekog okoliša.', 0);
INSERT INTO property VALUES (1,2801, 'hr', 'definition','Sveukupno naučeno ponašanje, stavovi, praksa i znanje nekog društva u pogledu održavanja ili zaštite njegovih prirodnih resursa, ekološkog sustava i svih ostalih vanjskih uvjeta koji utječu na čovjekov život.', 0);
INSERT INTO property VALUES (1,2802, 'hr', 'definition','Šteta nanesena okolišu, npr. nestanak močvara, onečišćenje rijeka itd.', 0);
INSERT INTO property VALUES (1,2803, 'hr', 'definition','Informacije o situaciji u okolišu ili stanju okoliša.', 0);
INSERT INTO property VALUES (1,2805, 'hr', 'definition','Rast, napredak ili uspjeh na području brige o okolišu.', 0);
INSERT INTO property VALUES (1,2810, 'hr', 'definition','Pitanje od javne važnosti koje obuhvaća i upravljanje financijskim sredstvima neke zajednice ili države, i njihovu zaštitu prirodnih resursa.', 0);
INSERT INTO property VALUES (1,2811, 'hr', 'definition','Priznato polje specijalizacije u disciplini ekonomije koja obuhvaća pitanja kontrole onečišćenja i zaštite okoliša, u kojem je teško ili nemoguće procijeniti troškove i koristi, u kojoj se velik dio ove problematike nalazi izvan konkurentnoga tržišnog sustava. Pa ipak, to je područje u kojem se golema  sredstva zajedničke imovine moraju racionalno dodjeljivati za sveopće javno dobro. Ovo pitanje se  također umnogome bavi načinima i sredstvima za postizanje te razumne raspodjele, kao što su naknade za emisije i otpadne vode, korisničke naknade za obradu ili odlaganje otpada, ekološke pristojbe, proizvodne naknade, povrati depozita, prava na trgovanje emisijama, jamstva za dobro izvršenje posla, računovodstvo prirodnih resursa, kao i ekonomske implikacije održivog razvoja.', 0);
INSERT INTO property VALUES (1,2814, 'hr', 'definition','Proces izobrazbe koji se bavi međusobnim odnosima ljudi i okoliša, koji se koristi interdisciplinarnim pristupom rješavanju problema s objašnjenjem vrijednosti. Ovaj proces bavi se napretkom u pogledu izobrazbe o znanjima, razumijevanju, stavovima, vještinama i predanosti problemima i razmatranjima problematike okoliša. Potrebe za izobrazbom o okolišu su stalne, budući da svaki novi naraštaj za sebe treba učiti o očuvanju.', 0);
INSERT INTO property VALUES (1,2817, 'hr', 'definition','Organizacije specijalizirane za pružanje savjeta o pitanjima zaštite okoliša, na primjer, istraživanje i saniranje potencijalno onečišćene zemlje, vode i zraka, te u procjeni utjecaja na okoliš; one zapošljavaju profesionalce s kvalifikacijama u tehnici/inženjeringu, geologiji, kemiji, hidrogeologiji, uređenju krajobraza, ekonomika okoliša itd.', 0);
INSERT INTO property VALUES (1,2818, 'hr', 'definition','Ekološka savjest ili moral koji upućuje na predanost i odgovornost prema okolišu, uključujući biljke i životinje, kao i sadašnje i buduće naraštaje ljudi. Usmjerena je prema ljudskim društvima koja žive u suglasju sa svijetom prirode o kojem ovisi njihov opstanak i blagostanje.', 0);
INSERT INTO property VALUES (1,2821, 'hr', 'definition','Financijska sredstva namijenjena mjerama koje uključuju održavanje okoliša, odnosno zaštitu, obranu ili sklonište za prirodne resurse.', 0);
INSERT INTO property VALUES (1,2823, 'hr', 'definition','Fizikalni ili kemijski čimbenik koji može prouzročiti štetu za ekosustav ili prirodne resurse.', 0);
INSERT INTO property VALUES (1,2824, 'hr', 'definition','Umijeće i znanost o zaštiti dobrog zdravlja, prevenciji bolesti i ozljeda posredstvom kontrole pozitivnih okolišnih čimbenika, kao i smanjenje potencijalnih fizičkih, bioloških, kemijskih i radioloških opasnosti.', 0);
INSERT INTO property VALUES (1,2825, 'hr', 'definition','Svaki fizički, kemijski ili neki drugi čimbenik koji može prouzročiti štetu za odnos između ljudi i vanjskih uvjeta koji ih okružuju, a koji predstavljaju prijetnju i boljitku ljudi i ekološkoj ravnoteži.', 0);
INSERT INTO property VALUES (1,2826, 'hr', 'definition','Procjena učinaka na zdravlje stanovništva izazvanih nekim djelovanjem.', 0);
INSERT INTO property VALUES (1,2827, 'hr', 'definition','Mjere osmišljene ili naprave namijenjene smanjenju rizika od narušavanja ljudskog zdravlja koji predstavljaju onečišćujuće tvari ili drugi rizični uvjeti u ekološkom sustavu.', 0);
INSERT INTO property VALUES (1,2828, 'hr', 'definition','Sustavan i kronološki prikaz događaja i uvjeta u prošlosti povezanih s ekološkim sustavom, njegovim prirodnim resursima ili, u širem smislu, vanjskim čimbenicima koji okružuju i utječu na život ljudi.', 0);
INSERT INTO property VALUES (1,2829, 'hr', 'definition','Svako mijenjanje uvjeta u okolišu ili stvaranje nove skupine ekoloških uvjeta, štetnih ili korisnih, što ih uzrokuju ili pokreću djelovanja ili nizovi aktivnosti koje se razmatraju.', 0);
INSERT INTO property VALUES (1,2830, 'hr', 'definition','Analiza i prosudba učinaka na okoliš, i privremenih i trajnih, od nekog značajnog djelovanja ili projekta. Ona također mora uzeti u obzir posljedice za društvo i alternativna djelovanja.', 0);
INSERT INTO property VALUES (1,2831, 'hr', 'definition','Poljoprivredne aktivnosti imaju značajan utjecaj na kakvoću vode, uključujući i povećanje taloženja u vodotocima zbog erozije, te povećanje količine hranjivih tvari, pesticida i koncentracija soli u vodi koja otječe s površine. U nekim regijama zloupotrebe pesticida dovele su do razvoja sojeva štetnika  otpornih na pesticide, uništavanja prirodnih grabežljivaca, uništavanja lokalnog biljnog i životinjskog svijeta, te kontaminiranih zaliha vode za ljudsku potrošnju. Nepravilna primjena umjetnih gnojiva promijenila je vrste raslinja i vrste ribe koje nastavaju obližnje plovne putove i rijeke.', 0);
INSERT INTO property VALUES (1,2832, 'hr', 'definition','Uzgoj riba onečišćuje vodu hranjivim tvarima, metanom i  sumporovodikom, što ugrožava i uzgojenu ribu, i ostali život u moru. Opasni pesticidi se upotrebljavaju za uništavanje morskih uši.', 0);
INSERT INTO property VALUES (1,2833, 'hr', 'definition','Energetika i ekološki problemi usko su povezani, jer je gotovo nemoguće proizvoditi, prenositi ili trošiti energiju bez značajnih utjecaja na okoliš. Ekološki problemi koji su izravno povezani s proizvodnjom i potrošnjom energije su onečišćenje zraka, onečišćenje vode, toplinsko onečišćenje, kao i odlaganje krutog otpada. Emisija onečišćujućih tvari koje onečišćuju zrak, a nastaju izgaranjem fosilnih goriva glavni je uzrok onečišćenja zraka u urbanim sredinama. Razni problemi onečišćenja vode povezani su s potrošnjom energije. Jedan od glavnih problema su izljevi nafte. U svim manipulacijama naftom postoji velika vjerojatnost da će doći do izlijevanja nafte, bilo na zemlju ili u neko vodno tijelo. Ugljeno rudarstvo također može izazvati onečišćenja vode. Promjene u tokovima podzemnih voda zbog rudarskih operacija često dovode inače neonečišćenu vodu u dodir s određenim mineralnim tvarima koje se ispiru sa zemlje i stvaraju onečišćenje kiselim rudničkim vodama. Kruti otpad je isto tako nusproizvod nekih oblika korištenja energije. U vađenju ugljena potrebno je uklanjanje velikih količina zemlje, kao i ugljena. Općenito govoreći, ekološki problemi povećavaju se s korištenjem energije, a to je, u kombinaciji s ograničenim izvorima energije, osnovni uzrok energetske krize. Procjena utjecaja energije na okoliš treba usporediti te troškove s koristima koje mogu proizaći iz korištenja energije.', 0);
INSERT INTO property VALUES (1,2834, 'hr', 'definition','Ribolov može imati različite negativne utjecaje na okoliš: otpadne vode i otpad iz uzgajališta riba može ugroziti divlju ribu, tuljane i školjke. Uzgajatelji riba upotrebljavaju male količine vrlo otrovnih kemikalija za uništavanje uši: jedna prevelika doza može biti pogubna. Zbog takozvanog usputnog ulova, ili slučajnog hvatanja nekomercijalnih vrsta u mreže potegače, operacija koćarenja i ribolova parangalom ugibaju velike morske životinje i oni su jedan od faktora koji su rizik za izumiranje nekih vrsta. Neke tehnike ribolova, kao mreže potegače, ne samo da donose ulov tona ribe, nego i ubijaju milijune ptica, kitovi i tuljana i slučajno se hvataju milijuni ribe. U male rupe u mreži često se uhvate mlade ribe koje nikada neće imati priliku za reprodukciju. Neki oblici opreme uništavaju prirodna staništa, na primjer koćarenje po dnu može uništiti prirodne grebene. Ostale destruktivne ribolovne tehnike su ilegalan ribolov pomoću dinamita i ribolov pomoću cijanida.', 0);
INSERT INTO property VALUES (1,2835, 'hr', 'definition','Šumarski resursi u svijetu smanjuju se alarmantnom brzinom. Potreba za devizama potiče mnoge zemlje u razvoju da iskorištavaju drvo brže nego što se šume mogu obnoviti. To preveliko iskorištavanje ne samo da osiromašuje resurs koji se nalazi u osnovi svjetske trgovine drvom, nego ono prouzročuje i gubitak zarađivanja za život na temelju djelatnosti u vezi sa šumama, povećava eroziju tla i nizvodno poplavljivanje, te ubrzava gubitak vrsta i genetskih resursa.', 0);
INSERT INTO property VALUES (1,2836, 'hr', 'definition','Utjecaji kućanstava na okoliš obuhvaćaju emisije iz grijanja domaćinstava (topli zrak, ugljični dioksid, ugljični monoksid, vodena para i oksid dušika, sumpora i drugih plinova u tragovima); kanalizacija iz domaćinstava sastoji se od tvari koje ispušta ljudsko tijelo, voda iz kuhinja, kupaonica i praonica; odlaganje glomaznog otpada, kao što su stari strojevi za pranje rublja, hladnjaci, automobili i drugi predmeti koji se ne mogu staviti u standardne kante za smeće, i koji se često odbacuju u prirodi i sl.', 0);
INSERT INTO property VALUES (1,2837, 'hr', 'definition','Utjecaji na okoliš povezan s industrijskim aktivnostima uglavnom su povezani s proizvodnjom industrijskog otpada koja se može podijeliti u različite vrste: kruti otpad, kao što su čestice prašine ili šljake iz ugljena; tekući otpad iz različitih procesa, uključujući i radioaktivne rashladne tvari iz elektrana; i otpadni plin, koji se uglavnom proizvodi u kemijskoj industriji.', 0);
INSERT INTO property VALUES (1,2838, 'hr', 'definition','Rekreaciju i turizam često prati velika šteta za okoliš. Vodeni ekosustavi posebno su osjetljivi na učinke povećanog turizma i popratnu izgradnju hotelskih kapaciteta, zbrinjavanje otpadnih voda, ceste, parkirališta i pristaništa na obalama i priobalnom području; kao i povećano pecanje, plivanje, skijanje na vodi, snimanje ili korištenje motornih čamaca u vodnom tijelu. Sve to proizvodi izravne štetne učinke kada se provodi masovno, uključujući i oštećenja obale, kemijske promjene u vodi, kao i sedimente i biološke promjene u biljnim i životinjskim zajednicama.', 0);
INSERT INTO property VALUES (1,2839, 'hr', 'definition','Velika šteta u okolišu kao posljedica rekreacije i turizma, uključujući ogoljivanje obala izgradnjom turističkih objekata; onečišćenja mora, rušenja povijesnih građevina kako bi se napravilo mjesta za turističke objekte; gubitak poljoprivrednog zemljišta za izgradnju zračnih luka itd.', 0);
INSERT INTO property VALUES (1,2840, 'hr', 'definition','Utjecaj aktivnosti u vezi s prijevozom na okoliš, a osobito onih koje se bave utjecajem onečišćenja zraka, bukom, raseljavanjem ljudi i premještanjem poduzeća, ometanjem staništa divljih životinja, te sveukupnim učincima poticanja rasta.', 0);
INSERT INTO property VALUES (1,2841, 'hr', 'definition','Iscrpna izjava koja, koliko god je to moguće, utvrđuje i analizira, između ostaloga, predviđeni učinak na okoliš od predloženog djelovanja i raspravlja o tome kako će se ublažiti negativni učinci.', 0);
INSERT INTO property VALUES (1,2844, 'hr', 'definition','Istraživanje provedeno da se utvrde uvjeti na nekom lokalitetu prije realizacije projekta, da se analiziraju njegovi mogući učinci i kompenzatorne mjere.', 0);
INSERT INTO property VALUES (1,2847, 'hr', 'definition','Mjerilo, statistički podatak ili vrijednost koja daje približnu osnovu ili dokaz na temelju kojeg utvrđujemo učinke programa upravljanja okolišem ili stanje ili uvjete u okolišu.', 0);
INSERT INTO property VALUES (1,2848, 'hr', 'definition','Indeks dostupnih članaka o okolišu od 1972. do danas; također poznat kao sažetak Godišnjeg izvješća o stanju okoliša (Environmental Abstract Annual).', 0);
INSERT INTO property VALUES (1,2849, 'hr', 'definition','Industrije uključene u razvoj čišćih tehnologija, obradu otpada i pročišćavanje otpadnih voda, procese recikliranja, biotehnološke procese, katalizatore, membrane, postrojenja za odsumporavanje, smanjenje buke, te proizvodnju ostalih proizvoda čija je namjena zaštita okoliša.', 0);
INSERT INTO property VALUES (1,2850, 'hr', 'definition','Znanost i tehnike razrade podataka i računalne obrade informacija o ekološkim sustavima i ekologiji.', 0);
INSERT INTO property VALUES (1,2851, 'hr', 'definition','Preneseno ili dobiveno znanje o bilo kojem aspektu ekosustava, prirodnih resursa u njemu ili, općenitije rečeno, vanjskim čimbenicima koji okružuju život ljudi i utječu na njega.', 0);
INSERT INTO property VALUES (1,2852, 'hr', 'definition','Sustav međusobno povezanih osoba i uređaja povezanih tako da omogućuju razmjenu podataka ili znanje u vezi s prirodnim resursima, zdravljem ljudi i drugim ekološkim pitanjima.', 0);
INSERT INTO property VALUES (1,2853, 'hr', 'definition','Usklađen skup ljudi, uređaja ili drugih sredstava osmišljenih za razmjenu podataka ili znanja u vezi s bilo kojim aspektom ekosustava, prirodnim resursima u njemu ili, općenitije rečeno, vanjskim čimbenicima koji okružuju život ljudi i utječu na njega.', 0);
INSERT INTO property VALUES (1,2856, 'hr', 'definition','Vrijednosni papiri kojima se stvaraju prihodi u obliku kamata i dividendi kojima se ostvaruju koristi za okoliš.', 0);
INSERT INTO property VALUES (1,2857, 'hr', 'definition','1) Briga o okolišu i njegovoj zaštiti. 
INSERT INTO property VALUES (1,2859, 'hr', 'definition','Širok spektar opcija od obvezujućih "tvrdih" zakona, kao što su međunarodni ugovori i nacionalno zakonodavstvo, do "mekih" (neobvezujućih) propisa, koji obuhvaćaju  smjernice, preporučenu praksu i postupke, te standarde. Zakonodavstvo zaštite okoliša također nastoji uskladiti međunarodne stavove s problematikom usredotočenom na vrlo specifične probleme kao što je degradacija tla, onečišćenje mora ili iscrpljivanje neobnovljivih izvora.', 0);
INSERT INTO property VALUES (1,2860, 'hr', 'definition','Sve raznovrsne aktivnosti povezane s promicanjem sukladnosti i poštivanja obvezujućih pravila neke države koja su proglašena u svrhu zaštite cjelovitosti okoliša, očuvanja prirodnih resursa i zaštite zdravlja ljudi.', 0);
INSERT INTO property VALUES (1,2862, 'hr', 'definition','Grana prava povezana s kontrolom onečišćenja; nacionalnim parkovima, prirodom, faunom i florom, divljinom i biološkom raznolikosti; zdravim stanjem okoliša i radnog mjesta; okolišnim planiranjem; očuvanjem baštine i velikim brojem međunarodnih konvencija u vezi s okolišem.', 0);
INSERT INTO property VALUES (1,2863, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propiše vlada u svrhu uređivanja bilo kojeg aspekta poljodjelstva i stočarstva koji predstavlja opasnost za cjelovitost okoliša i zdravlje ljudi, a osobito upotrebu pesticida, umjetnih gnojiva i zemljišta.', 0);
INSERT INTO property VALUES (1,2864, 'hr', 'definition','Sustavan postupak u kojem se prijedlog zakona kojim bi se očuvali ili zaštitili ekološki resursi može usvojiti kao zakon.', 0);
INSERT INTO property VALUES (1,2865, 'hr', 'definition','Kazna koju plaća organizacija za štetu izazvanu onečišćenjem i povratom u prvobitno stanje koji je nužan kao rezultat nastale štete, bilo zbog nenamjernih ispuštanja iz tankera, ispuštanja industrijskog otpada u vodene tokove ili na zemljište, ili namjernim ili slučajnim ispuštanjem radioaktivnih materijala.', 0);
INSERT INTO property VALUES (1,2866, 'hr', 'definition','Licenca ili dozvola dobivena od vlade koja omogućuje i uređuje ispuštanje emisija onečišćivača zraka iz nekog poduzeća, obično iz komercijalnog ili industrijskog postrojenja.', 0);
INSERT INTO property VALUES (1,2869, 'hr', 'definition','Tvar koja uzrokuje neželjene promjene u fizikalnim, kemijskim ili biološkim svojstvima zraka, vode ili tla koje mogu imati štetan učinak na čovjekovo zdravlje, opstanak ili aktivnosti čovjeka ili za druge žive organizme.', 0);
INSERT INTO property VALUES (1,2870, 'hr', 'definition','Proces pribavljanja proizvoda i usluga na način koji ne šteti okolišu.', 0);
INSERT INTO property VALUES (1,2871, 'hr', 'definition','Proizvod koji nije štetan za okoliš.', 0);
INSERT INTO property VALUES (1,2872, 'hr', 'definition','Usvajanje cjelovite i preventivne prakse upravljanja usmjerene na smanjenje učinaka industrijskih i trgovinskih aktivnosti na okoliš; ta praksa uključuje, između ostaloga, analizu životnog ciklusa u ciklusu razvoja proizvoda, uvođenje čiste procesne tehnologija i mjera smanjenja stvaranja otpada.', 0);
INSERT INTO property VALUES (1,2876, 'hr', 'definition','Tvrtke koje ne djeluju u skladu s propisima o okolišu za odlaganje štetnog otpada koji nastaje u ciklusu proizvodnje.', 0);
INSERT INTO property VALUES (1,2877, 'hr', 'definition','Mjere i kontrole za očuvanje okoliša, racionalnu i održivu raspodjelu i korištenje prirodnih resursa, optimalizaciju međusobnih odnosa između društva i okoliša, te poboljšanje ljudskog blagostanja za sadašnje i buduće naraštaje.', 0);
INSERT INTO property VALUES (1,2880, 'hr', 'definition','Umijeće i znanost zaštite dobrog zdravlja, promicanja estetskih vrijednosti, suzbijanja bolesti i ozljeda kontrolom  pozitivnih okolišnih čimbenika, te smanjenjem potencijalnih fizikalnih, bioloških, kemijskih i radioloških opasnosti.', 0);
INSERT INTO property VALUES (1,2882, 'hr', 'definition','Povremeno i/ili trajno mjerenje, vrednovanje i utvrđivanje ekoloških parametara i/ili razina onečišćenja u svrhu sprječavanja negativnih i štetnih učinaka na okoliš. Također uključuje predviđanje mogućih promjena u ekosustavu i/ili biosferi u cjelini.', 0);
INSERT INTO property VALUES (1,2884, 'hr', 'definition','Zvukovi i značajke zvukova iz svih izvora u nekom okruženju.', 0);
INSERT INTO property VALUES (1,2886, 'hr', 'definition','Intuitivno prepoznavanje ili razumijevanje ekosustava i prirodnih resursa u njemu, koje se često zasniva na ljudskim iskustvima ili kulturnim stavovima ili uvjerenjima.', 0);
INSERT INTO property VALUES (1,2889, 'hr', 'definition','Utvrđivanje poželjnih ciljeva za fizički okoliš, uključujući društvene i ekonomske ciljeve, te stvaranje upravnih postupaka i programa za ostvarivanje tih ciljeva.', 0);
INSERT INTO property VALUES (1,2890, 'hr', 'definition','Formulirana/usvojena ili sustavna metoda za zaštitu prirodnih ili ekoloških resursa.', 0);
INSERT INTO property VALUES (1,2892, 'hr', 'definition','Službene izjave o načelima, svrhama, vrijednostima i cilju koje se temelje na zakonodavstvu i nadležnom tijelu vlasti u državi i koje služe kao naputak za djelovanje državnih i privatnih subjekata u poslovima zaštite okoliša.', 0);
INSERT INTO property VALUES (1,2893, 'hr', 'definition','Tehnološke, gospodarske i zakonske mjere za sprječavanje ili kontrolu onečišćenja ili štete u okolišu.', 0);
INSERT INTO property VALUES (1,2895, 'hr', 'definition','Čovjekovo unošenje u okoliš tvari ili energije koja bi mogla uzrokovati opasnosti za ljudsko zdravlje, štetu za životne resurse i ekološke sustave, štetu za strukturu ili izgled okoliša, ili naštetiti propisanim upotrebama okoliša.', 0);
INSERT INTO property VALUES (1,2897, 'hr', 'definition','Organizirana skupina aktivnosti i postupaka, koje često provodi državna agencija ili neprofitna organizacija, za zaštitu prirodnih ili ekoloških resursa i zagovaranje ekološkog  napretka.', 0);
INSERT INTO property VALUES (1,2900, 'hr', 'definition','Mjere i kontrole za sprječavanje štete u okolišu i degradacije okoliša, uključujući održivost njegovih životnih resursa.', 0);
INSERT INTO property VALUES (1,2901, 'hr', 'definition','Agencija za zaštitu okoliša (Environmental Protection Agency, EPA) je agencija američke vlade odgovorna za kontrolu onečišćenja zraka i vode, pesticide, opasnost od radijacije i onečišćenje bukom. Agencija je također uključena i u istraživanja kojima se nastoje ispitati učinci onečišćenja.', 0);
INSERT INTO property VALUES (1,2902, 'hr', 'definition','Udruženja čiji je cilj zaštita prirodnog okoliša.', 0);
INSERT INTO property VALUES (1,2903, 'hr', 'definition','Iznos novca za troškove nastale u očuvanju, obrani ili zaštiti prirodnih resursa.', 0);
INSERT INTO property VALUES (1,2905, 'hr', 'definition','Preventivna djelovanja, postupci ili postrojenja nevladinih, poslovnih ili industrijskih subjekata u svrhu sprječavanja ili smanjenja štete za ekosustav i ljudsko zdravlje.', 0);
INSERT INTO property VALUES (1,2908, 'hr', 'definition','Vladina agencija, odbor ili skupina odgovorna za očuvanje i zaštitu ekoloških ili prirodnih resursa.', 0);
INSERT INTO property VALUES (1,2909, 'hr', 'definition','Propis koji izdaje vlada ili uprava o očuvanju prirodnih resursa  i sprječavanju štete u ekosustavu ili degradacije ekosustava.', 0);
INSERT INTO property VALUES (1,2910, 'hr', 'definition','Tehnologije sukladne ciljevima zaštite okoliša koje uključuju koncepte sprječavanja onečišćenja u svoje projektne nacrte. Strategije kontrole okoliša uvedene u rane faze projektiranja nekog procesa, umjesto opcija kontrole na kraju ciklusa koje bi se uključivale u kasnijim fazama projektiranja, poboljšavaju tehničke i ekonomske rezultate nekog procesa.', 0);
INSERT INTO property VALUES (1,2911, 'hr', 'definition','Grana eksperimentalne psihologije koja proučava odnose između ponašanja i konteksta okoliša u kojem se ono događa. U središtu pozornosti psihologije okoliša nalazi se utjecaj fizičkog okoliša i zbog toga veli dio istraživanja u ovom području bavi se utjecajima buke, onečišćenja zraka, klimatskih promjena itd.', 0);
INSERT INTO property VALUES (1,2912, 'hr', 'definition','Svojstva i značajke okoliša, bilo općenitog ili lokalnog, koje utječu na ljudska bića i druge organizme. Kvaliteta okoliša je općenit izraz koji se može odnositi na: raznovrsne značajke okoliša kao što su čistoća ili onečišćenje zraka i vode, buka, pristup otvorenom prostoru i vizualni efekti zgrada, te potencijalni efekti koje takve značajke mogu imati na fizičko i mentalno zdravlje.', 0);
INSERT INTO property VALUES (1,2913, 'hr', 'definition','Kriteriji kojih se pridržava u utvrđivanju standarda za izlaganje onečišćivačima i buci, u vezi s pesticidima, deterdžentima, sastavom otpadnih voda, ispuštanjem komercijalnog otpada itd.', 0);
INSERT INTO property VALUES (1,2915, 'hr', 'definition','Normativni dokumenti i smjernice za utvrđivanje stupnja okolišnih uvjeta i zahtjeva radi izbjegavanja negativnih i štetnih efekata, utjecaja i posljedica.', 0);
INSERT INTO property VALUES (1,2917, 'hr', 'definition','Prikaz ili izjava, obično u pisanom obliku, u kojoj se detaljno opisuju događaji, situacija ili uvjeti  koji se odnose na ekosustav, njegove prirodne resurse ili bilo koji vanjski čimbenik koji okružuje život ljudi i utječe na njega.', 0);
INSERT INTO property VALUES (1,2918, 'hr', 'definition','Istraživanje okoliša i njegovih modifikacija uzrokovanih djelovanjem ljudi.', 0);
INSERT INTO property VALUES (1,2920, 'hr', 'definition','Kvalitativna i kvantitativna ocjena rizika za okoliš od stvarne ili potencijalne prisutnosti i/ili upotrebe posebnih onečišćivača.', 0);
INSERT INTO property VALUES (1,2921, 'hr', 'definition','Vjerojatnost ozljede, bolesti ili smrti kao rezultata izlaganja potencijalnoj opasnosti za okoliš.', 0);
INSERT INTO property VALUES (1,2922, 'hr', 'definition','Interdisciplinarno istraživanje o problemima okoliša, u okviru uspostavljenih fizičkih i bioloških načela, tj. Orijentirano prema znanstvenom pristupu.', 0);
INSERT INTO property VALUES (1,2923, 'hr', 'definition','Mjere poduzete ili politike ustanovljene za zaštitu i promicanje sigurnosti vanjskih uvjeta koji djeluju na život, razvoj i preživljavanje nekog organizma.', 0);
INSERT INTO property VALUES (1,2924, 'hr', 'definition','Mjesta na kojima su odabrani uzorci (ribe, školjaka, mlijeka, tla i ljudskog tkiva itd.) pohranjeni tako da se spriječi njihovo raspadanje.', 0);
INSERT INTO property VALUES (1,2925, 'hr', 'definition','Procjena koji obavi tvrtka ili organizacija o financijskoj koristi i nedostacima koji će se izvesti usvajanjem politike primjerenije zaštiti okoliša.', 0);
INSERT INTO property VALUES (1,2927, 'hr', 'definition','Kupovanje, prodaja ili razmjena robe iz okoliša.', 0);
INSERT INTO property VALUES (1,2928, 'hr', 'definition','Vladina plaćanja kojima se pomaže ili poboljšava uspješnost u ekološkom održavanju ili zaštiti, obrani ili očuvanju prirodnih resursa.', 0);
INSERT INTO property VALUES (1,2929, 'hr', 'definition','Elementi okoliša priznate važnosti koji se mogu izmijeniti dovršavanjem nekog projekta.', 0);
INSERT INTO property VALUES (1,2931, 'hr', 'definition','Upute, izobrazba ili prenošenje znanja o vanjskim uvjetima koji utječu na život, razvoj i preživljavanje organizama, uključujući potencijalne opasnosti za ekološki sustav i sredstva očuvanja njegove cjelovitosti.', 0);
INSERT INTO property VALUES (1,2934, 'hr', 'definition','Vokabular tehničkih izraza i upotrebe prikladan za skupine u zajednici, korporativne ili državne skupine koja se bave zaštitom prirodnih resursa, očuvanjem cjelovitosti ekosustava i zaštitu zdravlja ljudi.', 0);
INSERT INTO property VALUES (1,2936, 'hr', 'definition','Osposobljavanje stručnjaka i kvalificiranih radnika koji stječu znanje i vještine potrebne za rješavanje problema povezanih sa zaštitom okoliša.', 0);
INSERT INTO property VALUES (1,2938, 'hr', 'definition','Ocjena, vrednovanje ili procjena poslovanja u stvarima koje se odnose na ekologiju i financije.', 0);
INSERT INTO property VALUES (1,2941, 'hr', 'definition','Izravno štetno djelovanje ekoloških resursa druge strane ili njihovo uništavanje, bilo kao politička prijetnja ili za ostvarivanje stvarne vojne premoći.', 0);
INSERT INTO property VALUES (1,2943, 'hr', 'definition','Ljudsko djelovanje, poduzeća ili proizvodi koji pojačavaju, a ne narušavaju cjelovitost ekološkog sustava.', 0);
INSERT INTO property VALUES (1,2944, 'hr', 'definition','Pojam koji uključuje sve aspekte okoline u kojoj živi čovjek, koji djeluju na pojedince i društvene skupine. Europska unija je definirala okoliš kao "kombinaciju elemenata čiji složeni međusobni odnosi čine okvire, okolinu i uvjete za život pojedinca i društva, kakvi jesu ili kako ih se osjeća". Okoliš tako obuhvaća izgrađeni okoliš, prirodni okoliš i sve prirodne resurse, uključujući zrak, zemlju i vodu. On također uključuje radno okruženje.', 0);
INSERT INTO property VALUES (1,2945, 'hr', 'definition','Svi proteini odnosno bjelančevine koje imaju svojstva katalizatora, proizvode ih žive stanice, i koje posreduju i unaprjeđuju kemijske procese života a da se pri tome same ne mijenjaju ni ne uništavaju.', 0);
INSERT INTO property VALUES (1,2947, 'hr', 'definition','Iznenadan porast u obolijevanjima od neke bolesti na vrijednost iznad uobičajene, koja pogađa velik broj ljudi i zahvaća široko područje.', 0);
INSERT INTO property VALUES (1,2949, 'hr', 'definition','1) Proučavanje aspekata masovnosti neke bolesti.
INSERT INTO property VALUES (1,2955, 'hr', 'definition','Životinje iz porodice Equidae (konji).', 0);
INSERT INTO property VALUES (1,2956, 'hr', 'definition','Svaki skup materijala, pribora ili naprava koji se pohranjuju, dobavljaju ili osiguravaju za neko poduzeće ili djelatnost.', 0);
INSERT INTO property VALUES (1,2958, 'hr', 'definition','Količina koja se upotrebljava u zaštiti od zračenja, a koja izražava sve zračenje na zaajedničkoj ljestvici za izračunavanje efektivno apsorbirane doze. Jedinica ekvivalentne doze je rem. Koji je brojčano jednak apsorbiranoj dozi u radima pomnoženoj određenim faktorima modifikacije kao što su kvalitativni faktor, distribucijski faktor itd.', 0);
INSERT INTO property VALUES (1,2960, 'hr', 'definition','Proučavanje čovjekovih sposobnosti i psihologije u odnosu prema radnom okruženju i opremi kojom radnik upravlja.', 0);
INSERT INTO property VALUES (1,2963, 'hr', 'definition','Opći proces ili skupina procesa kojim materijali Zemljine kore postaju rahli, rastapaju se ili ispiru i troše i istodobno pomiču s jednog mjesta na drugo, prirodnim postupcima, koji uključuju trošenje i starenje zbog vremenskih uvjeta, topljenje, koroziju i prenošenje, ali obično ne uključuje masovno trošenje.', 0);
INSERT INTO property VALUES (1,2964, 'hr', 'definition','Radnje tijekom izgradnje ili drugih aktivnosti kojima se djeluje na zemlju, usmjerene smanjenju ili sprječavanju erozije tla. Uobičajene prakse uključuju sadnju drveća i trave koja brzo raste na pogođenim područjima i druge načine za usporavanje kretanja vode kroz lokaciju podvrgnutu eroziji i  zadržavanje tla koje ne odnosi otjecanje vode.', 0);
INSERT INTO property VALUES (1,2969, 'hr', 'definition','Znanstveno proučavanje ili karakteristični životni procesi živih organizama koji se nalaze u poluzatvorenim priobalnim vodama sa slobodnim vezama prema otvorenom moru i unutar kojega je morska voda mjerljivo razrijeđena slatkom vodom.', 0);
INSERT INTO property VALUES (1,2970, 'hr', 'definition','Područje riječnog ušća koje je rezerviranom zakonskim propisima za zaštitu dijela ili cijelog obuhvaćenog okoliša u svrhu očuvanja, za znanstvene, obrazovne i/ili rekreacijske svrhe.', 0);
INSERT INTO property VALUES (1,2973, 'hr', 'definition','Proučavanje fizičkih, kemijskih, bioloških i geoloških značajki poluzatvorenih priobalnih voda sa slobodnim vezama prema otvorenom moru i unutar kojega je morska voda mjerljivo razrijeđena slatkom vodom.', 0);
INSERT INTO property VALUES (1,2974, 'hr', 'definition','Područje na riječnom ušću gdje se ono proširuje do mora, te gdje se slatka i morska voda miješaju i postaju bočata voda. Okoliš riječnog ušća vrlo je bogat biljnim i životinjskim svijetom, osobito vodenim, ali je vrlo podložan šteti nastaloj ljudskim djelovanjem.', 0);
INSERT INTO property VALUES (1,2977, 'hr', 'definition','Urezivanje linija na metalnoj ili staklenoj ploči odnosno ploči od drugim materijala tako da se ta ploča prekrije premazom otpornim na kiseline, grebenjem premaza, a zatim se predmet  stavi u kiselu kupku da kiselina izgrize izložene dijelove ploče.', 0);
INSERT INTO property VALUES (1,2978, 'hr', 'definition','Tvar koja kemijskim djelovanjem može istopiti površinu metala, stakla itd.', 0);
INSERT INTO property VALUES (1,2979, 'hr', 'definition','Bezbojna tekućina, neznatno topljiva u vodi; upotrebljava se kao reagens, poluproizvod, anestetik i otapalo.', 0);
INSERT INTO property VALUES (1,2980, 'hr', 'definition','Filozofsko proučavanje moralnih vrijednosti čovjekovog ponašanja te pravila i načela kojima se ono treba rukovoditi.', 0);
INSERT INTO property VALUES (1,2982, 'hr', 'definition','Znanost koja se bavi proučavanjem porijekla, razmještaja i odnosa rasa ili etničkih skupina čovječanstva.', 0);
INSERT INTO property VALUES (1,2984, 'hr', 'definition','Proučavanje ponašanja životinja u prirodnom okruženju.', 0);
INSERT INTO property VALUES (1,2989, 'hr', 'definition','Vijeće Europske unije je institucija koja provodi zakonodavne ovlasti i ovlasti za donošenje odluka. Istodobno, to je forum na kojem predstavnici vlada 15 država članica mogu izraziti svoje interese i pokušati doći do kompromisa. Vijeće osigurava opću koordinaciju aktivnosti Europske zajednice, čiji je glavni cilj je uspostavljanje unutarnjeg tržišta, odnosno područja bez unutarnjih granica koje jamči četiri slobode kretanja - za robu, ljude, usluge i kapital - kojima bi uskoro trebala biti dodana jedinstvena valuta. Osim toga, Vijeće je odgovorno za međuvladinu suradnju, u zajedničkoj vanjskoj i sigurnosnoj politici (CFSP) i u području pravosuđa i unutarnjih poslova (JHA), uključujući, na primjer, pitanja imigracije i azila, borbe protiv terorizma i droga i pravosudne suradnje.', 0);
INSERT INTO property VALUES (1,2991, 'hr', 'definition','Preteču Europske zajednice, Europsku zajednicu za atomsku energiju osnovalo je 1958. god Europsko zajedničko tržište za provođenje istraživanja, razvoj nuklearne energije, stvaranje zajedničkog tržišta za nuklearna goriva i nadziranje nuklearne industrije kako bi se spriječilo zlouporaba i zaštitilo zdravlje.', 0);
INSERT INTO property VALUES (1,2992, 'hr', 'definition','Drugi najmanji kontinent, koji formira zapadno proširenje Euroazije: granica s Azijom ide od Urala do Kaspijskog i Crnog mora. Obala je općenito izrazito razvedena i postoji nekoliko poluotoka (osobito Skandinavija, Italija i Iberija) i otoci (uključujući Britansko otočje i Island). Ona sadrži niz velikih planinskih sustava na jugu (Pireneje, Alpe, Apenine, Karpate, Kavkaz), veliku središnju ravnicu, te na sjeveru područje jezera i planina u Skandinaviji.', 0);
INSERT INTO property VALUES (1,2993, 'hr', 'definition','Upravno tijelo Europske unije, sastavljeno od dvadeset neovisnih članova koje imenuju države članice na petogodišnji mandat s ovlastima za pokretanje, provedbu, upravljanje i nadzor prema ovlastima utvrđenim ugovorima o Europskoj uniji ili koje im je dodijelilo Vijeće Europske unije.', 0);
INSERT INTO property VALUES (1,2996, 'hr', 'definition','Vrhovni sud Europske unije koji nadzire primjenu ugovora EU-a, odlučuje o valjanosti i značenju zakonodavstva Zajednice i utvrđuje predstavlja li neki čin ili propust od strane Europske komisije, Vijeća ministara ili bilo koje države članice kršenje prava Zajednice.', 0);
INSERT INTO property VALUES (1,2997, 'hr', 'definition','Europska agencija za okoliš (EEA) osnovana je da bi pružila Europskoj zajednici i njezinim državama članicama objektivne, pouzdane i standardizirane informacije o okolišu. Ona će procijeniti uspješnost postojećih politika zaštite okoliša, a podaci će se upotrebljavati za razvoj novih politika za mjere zaštite okoliša. Ona će prikupljati informacije koje obuhvaćaju sadašnje stanje okoliša i stanje okoliša u doglednoj budućnosti. Prioritetna područja su kakvoća zraka i emisije; kakvoća vode, onečišćivači i resursi; kakvoća, flora i fauna, te biotopi; korištenje zemljišta i prirodni resursi; gospodarenje otpadom; zagađenje bukom; kemikalije; te zaštita priobalnih područja. Agencija će također uzeti u obzir društveno-ekonomske dimenzije, obuhvatiti prekogranična i međunarodna pitanja, te izbjegavati dupliciranje aktivnosti drugih tijela.', 0);
INSERT INTO property VALUES (1,3002, 'hr', 'definition','Nekadašnja "Skupština" EEZ-a. Obuhvaća oko 520 "predstavnika naroda" država Europske zajednice, izravno izabranih, sa sjedištem u Strasbourgu. Provodi savjetodavne i nadzorne ovlasti; raspravlja i donosi rezolucije i može staviti veto na ulazak novih država članica.', 0);
INSERT INTO property VALUES (1,3006, 'hr', 'definition','15 zemalja (Belgija, Danska, Francuska, Njemačka, Grčka, Irska, Italija, Luksemburg, Nizozemska, Portugalska, Španjolska, Ujedinjeno Kraljevstvo, Austrija, Finska i Švedska) koje su se udružile kako bi formirale gospodarsku zajednicu sa zajedničkim monetarnim, političkim i društvenim težnjama. Europska unija nastala je 1. studenoga 1993. god. U skladu s uvjetima Ugovora iz Maastrichta; ona obuhvaća tri Europske zajednice, proširene usvajanjem zajedničke vanjske i sigurnosne politike koja zahtijeva suradnju između država članica u vanjskoj politici i sigurnosti te suradnju u pravosuđu i unutarnjim poslovima.', 0);
INSERT INTO property VALUES (1,3007, 'hr', 'definition','Proces onečišćenja koji se odvija kada jezero ili potok postane prebogat biljnim hranjivim tvarima; kao posljedica toga u njemu se prekomjerno razvijaju alge i ostale vodene biljke. Biljke ugibaju i raspadaju se. Pri raspadanju biljke oduzimaju kisik iz vode, pa jezero, rijeka ili potok postaju beživotni. Nitratna gnojiva koja se ocjeđuju s polja, nutrijenti (hranjive tvari) iz životinjskih otpadaka i kanalizacije glavni su uzroci eutrofikacije. Ona imaju visoku biološku potrošnju kisika (BOD/BPK).', 0);
INSERT INTO property VALUES (1,3012, 'hr', 'definition','Standard, norma, vrijednost ili mjera kojom se utvrđuje količina ili kvaliteta nekog procesa, predmeta ili radne uspješnosti neke osobe posredstvom analize i procjene mjerodavnih informacija u kontekstu, i imajući na umu, postavljene kratkoročne i dugoročne ciljeve i  standarde.', 0);
INSERT INTO property VALUES (1,3019, 'hr', 'definition','Pretvorba iz tekućeg ili krutog stanja u paru.', 0);
INSERT INTO property VALUES (1,3022, 'hr', 'definition','Ispuštanje vode s površine Zemlje u atmosferu u obliku ishlapljivanja iz jezera, potoka i tala te transpiracijom iz biljaka. Poznato i pod nazivom "fly-off" (na engleskom jeziku).', 0);
INSERT INTO property VALUES (1,3026, 'hr', 'definition','Biološke teorije ili proces kojim se vrste biljaka i životinja mijenjaju s vremenom, tako da se njihovi potomci razlikuju od svojih predaka, tj. razvoj od ranijih oblika nasljednim prenošenjem malih varijacija u sljedećim generacijama.', 0);
INSERT INTO property VALUES (1,3027, 'hr', 'definition','Matematika i druge znanosti koje se zasnivaju na računskim operacijama.', 0);
INSERT INTO property VALUES (1,3028, 'hr', 'definition','Jama, šupljina ili neki drugi nepokriveni iskopani prostor nastali iskopavanjem.', 0);
INSERT INTO property VALUES (1,3029, 'hr', 'definition','Uklanjanje zemlje iz njezinoga prirodnog položaja.', 0);
INSERT INTO property VALUES (1,3031, 'hr', 'definition','Ostatak u obliku hrpe, koja se sastoji od zemlje ili drugog materijala, nastao iskopavanjem.', 0);
INSERT INTO property VALUES (1,3032, 'hr', 'definition','Mjesto odabrano za iskopavanje, tj. radnju ili proces uklanjanja tla i/ili kamenog materijala kopanjem, miniranjem, lomljenjem, tovarenjem, bilo na površini ili ispod površine Zemlje.', 0);
INSERT INTO property VALUES (1,3040, 'hr', 'definition','Nalog ili uredba koju izdaje predsjednik ili neko upravno tijelo vlasti prema njegovom nalogu u svrhu tumačenja, provedbe ili davanja administrativnog učinka nekoj odredbi ustava ili nekog zakona ili ugovora.', 0);
INSERT INTO property VALUES (1,3044, 'hr', 'definition','1) Vod ili cijev kroz koju se ispušta otpadni materijal.
INSERT INTO property VALUES (1,3045, 'hr', 'definition','Otpadni plin koji nastaje u procesima izgaranja koji se izravno ili na kraju procesa ispušta u atmosferu.', 0);
INSERT INTO property VALUES (1,3047, 'hr', 'definition','Kemijski proizvodi koji su postojali prije 18. rujna 1981. god.', 0);
INSERT INTO property VALUES (1,3048, 'hr', 'definition','Biljke, životinje ili mikroorganizmi koje uvode  ljudi u područja gdje nisu izvorni. Egzotične vrste često se povezuju s negativnim ekološkim posljedicama za autohtone vrste i ekološke sustave.', 0);
INSERT INTO property VALUES (1,3049, 'hr', 'definition','Potrošnja potrošača, ulagača ili vlada na robu ili usluge.', 0);
INSERT INTO property VALUES (1,3050, 'hr', 'definition','Ispitivanje u kontroliranim uvjetima koje se provodi kako bi se pokazala poznata istina, ispitala valjanost neke hipoteze ili utvrdilo djelovanje nečega što nije bilo pokušano ranije.', 0);
INSERT INTO property VALUES (1,3054, 'hr', 'definition','Računalna konfiguracija hardvera i softvera koja simulira prosudbu i ponašanje čovjeka ili organizacije s velikim znanjem u određenom području, često davanjem odgovora, rješenja ili dijagnoza.', 0);
INSERT INTO property VALUES (1,3057, 'hr', 'definition','Potraga za nalazištima minerala, rude, plina, nafte ili ugljena za iskorištavanje u gospodarske svrhe pomoću geoloških istraživanja, geofizičkih ispitivanja, bušotina i pokusnih jama ili površinskih ili podzemnih kopova, nanosa ili tunela.', 0);
INSERT INTO property VALUES (1,3058, 'hr', 'definition','Snažno, naglo oslobađanje energije iz praha ili plinova iz trenutačnog paljenja ili pomoću nekih drugih sredstava detonacije, često popraćeno silom koja stvara veliku količinu topline, velika oštećenja strukture, udarne valove i leteće šrapnele.', 0);
INSERT INTO property VALUES (1,3061, 'hr', 'definition','Tvar, kao što je trinitrotoluen, ili smjesa, kao što je barut, koja se odlikuje kemijskom stabilnosti, ali može se podvrgnuti brzoj kemijskoj promjeni bez vanjskog izvora kisika, nakon čega se stvara velika količina energije koju općenito prati stvaranje vrućih plinova.', 0);
INSERT INTO property VALUES (1,3063, 'hr', 'definition','Poslati, uzeti ili odnijeti predmet ili proizvod iz kojim se trguje izvan zemlje. Prevoziti robu iz jedne zemlje u drugu prilikom trgovanja.', 0);
INSERT INTO property VALUES (1,3064, 'hr', 'definition','Vladina dozvola za iznošenje ili slanje izvan zemlje i prodaju nekog proizvoda proizvedenog unutar njezinih granica.', 0);
INSERT INTO property VALUES (1,3065, 'hr', 'definition','Prijevoz nusproizvoda društva koje ima najmanje jednu od četiriju značajki (zapaljivost, korozivnost, reaktivnost  ili toksičnost) u druge zemlje ili područja radi obavljanja trgovine s inozemstvom.', 0);
INSERT INTO property VALUES (1,3067, 'hr', 'definition','Vrijeme tijekom kojeg se neki materijal osvijetli ili ozrači.', 0);
INSERT INTO property VALUES (1,3071, 'hr', 'definition','Oduzeti vlasniku posjed, osobito u svrhu javne upotrebe.', 0);
INSERT INTO property VALUES (1,3073, 'hr', 'definition','Sustav uzgoja koji se provodi na vrlo velikim poljoprivrednim gospodarstvima, koji se odlikuje niskim razinama inputa po jedinici površine; u takvim situacijama je broj grla stoke po površini nizak.', 0);
INSERT INTO property VALUES (1,3079, 'hr', 'definition','Neusklađenost između privatnih troškova i društvenih troškova ili privatnih koristi i društvenih koristi; osnovni koncept eksternalija je međuovisnost bez naknade.', 0);
INSERT INTO property VALUES (1,3081, 'hr', 'definition','1) Potpuno nestajanje neke vrste biljaka ili životinja s planeta.
INSERT INTO property VALUES (1,3084, 'hr', 'definition','Životinjske ili biljne vrste koje su potpuno nestale s planeta.', 0);
INSERT INTO property VALUES (1,3087, 'hr', 'definition','Proces kojim se iz rude dobiva čisti metal.', 0);
INSERT INTO property VALUES (1,3090, 'hr', 'definition','Primarne djelatnosti uključene u ekstrakciju neobnovljivih izvora.', 0);
INSERT INTO property VALUES (1,3097, 'hr', 'definition','Svaka tkanina načinjena od pređe ili vlakana tkanjem, pletenjem, filcanjem itd.', 0);
INSERT INTO property VALUES (1,3099, 'hr', 'definition','Značajni elementi ili razlozi za neki ishod u kupovanju, prodaji i trgovanju određenom robom ili uslugama.', 0);
INSERT INTO property VALUES (1,3103, 'hr', 'definition','Tehnika kapitalno-intenzivnog uzgoja životinja u umjetnom okruženju, a upotrebljava se za proizvodnju piletine, jaja, puretine, govedine, teletine i svinjetine. Životinje se zadržavaju u kontroliranim zatvorenim prostorima i njihova hrana im se donosi. Zgrade izgledaju kao industrijska postrojenja.', 0);
INSERT INTO property VALUES (1,3106, 'hr', 'definition','Bakterije koje se nalaze u ljudskom i životinjskom izmetu.', 0);
INSERT INTO property VALUES (1,3110, 'hr', 'definition','Taloženje krutih ili tekućih čestica na zemlju, koje se događa kada brzina kojom one padaju zbog gravitacije premašuje brzinu bilo kojeg kretanja okolnog zraka prema gore.', 0);
INSERT INTO property VALUES (1,3113, 'hr', 'definition','Površina koja se uobičajeno upotrebljava za proizvodnju usjeva, ali se ostavi neposijana tijekom jedne ili više sezona uzgoja.', 0);
INSERT INTO property VALUES (1,3115, 'hr', 'definition','Obradivo zemljište koje nije pod plodoredom, a koje je ostavljeno za odmaranje na neko vrijeme od jedne do pet godina prije ponovnog uzgoja, ili zemljište obično pod stalnim usjevima, livadama ili pašnjacima, koje se ne upotrebljava za tu svrhu u razdoblju od najmanje jedne godine. Uključuje obradivo zemljište koje se obično upotrebljava za uzgoj privremenih usjeva, ali koje se privremeno upotrebljava za ispašu.', 0);
INSERT INTO property VALUES (1,3117, 'hr', 'definition','Skupina sastavljena od roditelja, potomaka i ostalih osoba u bliskom srodstvu ili usko povezanih s njima.', 0);
INSERT INTO property VALUES (1,3119, 'hr', 'definition','Kontrola broja djece u obitelji i dobnog razmaka između djece, osobito upotrebom sredstava za kontracepciju.', 0);
INSERT INTO property VALUES (1,3120, 'hr', 'definition','Ozbiljna nestašica hrane, npr. zbog slabog uroda usjeva ili prevelikog broja stanovništva. Uzrok može biti slaba žetva nakon suše, poplava, potresa, rata, sukoba u društvu itd.', 0);
INSERT INTO property VALUES (1,3122, 'hr', 'definition','Životinje koje se uzgajaju na poljoprivrednim gospodarstvima za rad i proizvodnju hrane kao što je meso, jaja i mlijeko.', 0);
INSERT INTO property VALUES (1,3123, 'hr', 'definition','Nastamba za stanovanje na poljoprivrednom gospodarstvu, za razliku od pomoćnih zgrada kao što su staja, hambar, zgrada za mužnju.', 0);
INSERT INTO property VALUES (1,3130, 'hr', 'definition','Svako zemljište ili zgrada koja se upotrebljava za poljoprivrednu proizvodnju, npr. uzgoj usjeva i stoke.', 0);
INSERT INTO property VALUES (1,3138, 'hr', 'definition','Sve životinje u nekoj regiji, staništu ili geološkom sloju.', 0);
INSERT INTO property VALUES (1,3143, 'hr', 'definition','Vlast agencije središnje države ili njezinih upravitelja da provode slovo zakona na temelju kojeg je agencija osnovana te da provode propise, zakone i državne politike.', 0);
INSERT INTO property VALUES (1,3144, 'hr', 'definition','Sustav u kojem zemljom ili državom koja je stvorena unijom ili konfederacijom nezavisnih država upravlja središnja vlast ili organizacija.', 0);
INSERT INTO property VALUES (1,3147, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje je uspostavila vlada koja je ustrojena kao unija nezavisnih političkih jedinica ili država.', 0);
INSERT INTO property VALUES (1,3149, 'hr', 'definition','Zakonski utvrđen novčani iznos koji se plaća za usluge javnih službenika ili za upotrebu neke povlastice pod kontrolom države.', 0);
INSERT INTO property VALUES (1,3151, 'hr', 'definition','Radnja i učinak opskrbljivanja životinja hranom.', 0);
INSERT INTO property VALUES (1,3154, 'hr', 'definition','Vodom natopljeno, spužvasto zemljište koje sadrži alkalnu  vegetaciju koja truli, a odlikuje se trskom, koje se može pretvoriti u treset. Ponekad se pojavljuje u ponikvama u krškom području.', 0);
INSERT INTO property VALUES (1,3159, 'hr', 'definition','Svaka enzimska transformacija organskih supstrata, posebice ugljikohidrata, koja je uglavnom popraćena razvojem plinova; fiziološki pandan oksidacije, koji omogućuje određenim organizmima da žive i rastu bez prisutnosti zraka; upotrebljava se u različitim industrijskim procesima za proizvodnju proizvoda kao što alkoholi, kiseline, i sir djelovanjem kvasaca, plijesni i bakterija; alkoholna fermentacija je najpoznatiji primjer. Također se naziva vrenjem.', 0);
INSERT INTO property VALUES (1,3162, 'hr', 'definition','Svaka od mnogobrojnih vaskularnih biljaka koje tvore odjeljak Polypodiophyta, bez cvijeća i voća.', 0);
INSERT INTO property VALUES (1,3169, 'hr', 'definition','Tvar koja se dodaje tlu za pospješivanje života biljke, a obično sadrži dušik, kalij i fosfor, npr. stajsko gnojivo, guano, kameni fosfati.', 0);
INSERT INTO property VALUES (1,3174, 'hr', 'definition','Vrlo dug, podatan, čvrst prirodni ili umjetno proizveden predmet sličan niti, iz kojega je predenjem izrađena pređa koja se tka u tekstil.', 0);
INSERT INTO property VALUES (1,3175, 'hr', 'definition','Materijal načinjen od malih prepletenih staklenih vlakana, koji se upotrebljava za očuvanje topline u zgradama, ili  plastika ojačana tim vlaknima koja se upotrebljava za izradu konstrukcija kao što su vanjski dijelovi automobila i brodova.', 0);
INSERT INTO property VALUES (1,3176, 'hr', 'definition','Ograničeno područje zemljišta na kojem raste trava ili usjevi, obično ograđeno ogradama ili gusto zasađenim grmljem kada se nalazi na poljoprivrednom gospodarstvu (farmi).', 0);
INSERT INTO property VALUES (1,3177, 'hr', 'definition','Pad proizvodnosti neke površine zemljišta ili njegove sposobnosti da podrži prirodne ekosustave ili vrste poljoprivrede. Propadanje može biti izazvano različitim čimbenicima, uključujući i neprimjerene tehnike upravljanja zemljištem, eroziju tla, slanost, poplave, krčenje, štetnike, onečišćenje, klimatske faktore ili progresivnu urbanizaciju.', 0);
INSERT INTO property VALUES (1,3178, 'hr', 'definition','Pokus izveden na nekoj tvari ili organizmu na otvorenom terenu, za razliku od pokusa koji se obavljaju u laboratoriju.', 0);
INSERT INTO property VALUES (1,3179, 'hr', 'definition','Znanstveno istraživanje koje se provodi na otvorenom u svrhu prikupljanja informacija koje se ne mogu pribaviti u laboratoriju.', 0);
INSERT INTO property VALUES (1,3181, 'hr', 'definition','Svaka tvar koja se upotrebljava za popunjavanje rupa i nepravilnosti u izravnatim ili pjeskarenim površinama kako bi se smanjila poroznost površine za završne premaze.', 0);
INSERT INTO property VALUES (1,3182, 'hr', 'definition','Mjesto na kojem prodaje gorivo i druga roba, oprema i pribor za vozače.', 0);
INSERT INTO property VALUES (1,3183, 'hr', 'definition','Film kao umjetničko djelo; tanka i elastična celulozna traka premazana fotografskom emulzijom od koje se izrađuju  negativi i dijapozitivi.', 0);
INSERT INTO property VALUES (1,3184, 'hr', 'definition','Porozan materijal za odvajanje suspendiranih čestica od tekućina propuštanjem tekućine kroz pore u filtru i prosijavanjem krutih tvari.', 0);
INSERT INTO property VALUES (1,3186, 'hr', 'definition','Akumulirane krute tvari, mokre ili suhe, koje nastaju u procesu filtracije, uključujući nakupljanje na filtrima od tkanine u procesima filtriranja zraka, ili nakupljanjem mokrih krutih tvari u procesima filtriranja tekućina.', 0);
INSERT INTO property VALUES (1,3190, 'hr', 'definition','Odvajanje suspendiranih čestica iz tekućine, plina itd., djelovanjem filtra.', 0);
INSERT INTO property VALUES (1,3192, 'hr', 'definition','Sustav u kojem se inertni materijali, koji se ne smiju mobilizirati prirodnim procesima čak ni za duga vremenska razdoblja, ograničavaju s pomoću tri prepreke: prirodnim nepropusnim okruženjem, umjetnom barijerom (kao što je obloga) koja se može kontrolirati i, što je najvažnije, samim inertnim materijalom. Pojam konačnog skladištenja uključuje mogućnost pohranjivanja materijala u rudnik u budućnosti ako su takvi materijali dovoljno "čisti" ("mono" odlagališta) i ako postane ekonomično pohranjivanje takvih ruda u rudnik.', 0);
INSERT INTO property VALUES (1,3194, 'hr', 'definition','Novčana sredstva ili prihodi države, trgovačkog društva, organizacije ili pojedinca.', 0);
INSERT INTO property VALUES (1,3195, 'hr', 'definition','Pomoć i potpora u poslovima povezanim s novcem.', 0);
INSERT INTO property VALUES (1,3196, 'hr', 'definition','Financijska odšteta koju podnositelj odštetnog zahtjeva zatraži ili sud dodijeli za pretrpljenu ozljedu ili materijalnu štetu nastalu krivnjom druge strane.', 0);
INSERT INTO property VALUES (1,3197, 'hr', 'definition','Nešto dano, uključujući bilo koji oblik dohodovne ili cjenovne potpore; novčano davanje ili doprinos pojedinačnog ulagača zajedničkom fondu ili dionicama; subvencija, potpora ili neki drugi doprinos državne agencije ili kreditne agencije za pomoć poticanju gospodarstva.', 0);
INSERT INTO property VALUES (1,3199, 'hr', 'definition','Generički izraz koje se odnosi na mnoge različite oblike financiranja koje neko poduzeće može upotrebljavati. Na primjer - zajmovi, dionice i obveznice se smatraju financijskim instrumentima.', 0);
INSERT INTO property VALUES (1,3201, 'hr', 'definition','Mjesto ili institucija u kojoj se kupci i prodavatelji sastaju i trguju monetarnom imovinom, uključujući dionice, obveznice, vrijednosnice i novac.', 0);
INSERT INTO property VALUES (1,3205, 'hr', 'definition','Nabava novčanih sredstava ili kredita za vođenje poslovanja ili stjecanje imovine.', 0);
INSERT INTO property VALUES (1,3207, 'hr', 'definition','Novčana kazna koju odredi zakonski tribunal odnosno sud osobi koja je osuđena zbog kaznenog djela ili prekršaja.', 0);
INSERT INTO property VALUES (1,3209, 'hr', 'definition','Leteće krute čestice nastale ljudskom aktivnošću i iz prirodnih izvora, npr. tlo koje nanese vjetar ili požari, koje se naposljetku natalože zbog sile gravitacije, i mogu izazvati ozljedu dišnih sustava ljudi i životinja zbog prevelikog udisanja.', 0);
INSERT INTO property VALUES (1,3212, 'hr', 'definition','Stanje izgaranja u kojem zapaljivi materijal gori, pri čemu nastaje toplina, plamenovi i često i dim.', 0);
INSERT INTO property VALUES (1,3218, 'hr', 'definition','Mjera, djelovanje ili oprema/uređaj koji se unaprijed provodi odnosno postavlja radi izbjegavanja mogućnosti bilo kakvog neočekivanog i potencijalno štetnog izgaranja materijala.', 0);
INSERT INTO property VALUES (1,3219, 'hr', 'definition','Kemikalija koja se upotrebljava kao premaz za izgorivi materijal ili njegova komponenta  da se smanji ili isključi mogućnost gorenja; upotrebljava se s tekstilima, plastikom, gumama, bojama i drugim materijalima.', 0);
INSERT INTO property VALUES (1,3220, 'hr', 'definition','Sve nužne mjere predostrožnosti kojima se postiže da ne dođe do požara, time što se osigura da su svi potrebni protupožarni uređaji u ispravnom stanju i dostupni za upotrebu u slučaju pojave požara, te time što se osigura da je osoblje primilo odgovarajuću obuku i uvježbano u gašenju požara.', 0);
INSERT INTO property VALUES (1,3225, 'hr', 'definition','Pravila koja se moraju slijediti i sigurnosni sustavi koji se moraju usvojiti radi sprječavanja ili suzbijanja požara.', 0);
INSERT INTO property VALUES (1,3226, 'hr', 'definition','Tehnička organizacija s obučenim osobljem za suzbijanje požara i drugih incidenata i za suradnju u njihovom sprječavanju.', 0);
INSERT INTO property VALUES (1,3229, 'hr', 'definition','Proces paljenja vatre ili dovođenja topline, npr. pri stvrdnjavanju glazure na keramici.', 0);
INSERT INTO property VALUES (1,3232, 'hr', 'definition','Trgovinsko partnerstvo dviju ili više osoba, osobito kada se radi o osnivanju poduzeća (dioničkog društva, poduzeća, tvrtke).', 0);
INSERT INTO property VALUES (1,3237, 'hr', 'definition','Djelatnost ulova, prerade i prodaje ribe.', 0);
INSERT INTO property VALUES (1,3238, 'hr', 'definition','Upravljanje i vođenje u području ribarske industrije, uključujući ulov, preradu i prodaju ribe.', 0);
INSERT INTO property VALUES (1,3243, 'hr', 'definition','Proizvodnja, distribucija i potrošnja ribe i morskih plodova i svi financijski aspekti proizvodnje ribe i morskih plodova.', 0);
INSERT INTO property VALUES (1,3246, 'hr', 'definition','Zajednička ribarska politika koja obuhvaća sve ribarske aktivnosti, uzgoj živih vodenih resursa, te njihovu preradu i marketing, na pravnom temelju članka 39. Rimskog ugovora. Dogovorena je između članica Europske zajednice 1983. god. Ona propisuje ograničenja godišnjeg ulova za glavne vrste riba, oko 19,3 km dugu isključivu ribolovnu zonu za svaku državu, te zonu jednakog pristupa od 200 nautičkih milja od svojih obala unutar kojeg je svakoj državi članici dopušteno loviti ribu.', 0);
INSERT INTO property VALUES (1,3252, 'hr', 'definition','Hladnokrvni vodeni kralješnjaci.', 0);
INSERT INTO property VALUES (1,3254, 'hr', 'definition','Uzgoj ribe u kopnenim vodama, riječnim ušćima ili priobalnim vodama.', 0);
INSERT INTO property VALUES (1,3256, 'hr', 'definition','Pokušaj da se ulovi riba ili neka druga vodena životinja s pomoću udice ili mreža, zamki itd.', 0);
INSERT INTO property VALUES (1,3260, 'hr', 'definition','Industrija koja se bavi rukovanjem, preradom i pakiranjem ribe ili školjaka za tržište ili otpremu.', 0);
INSERT INTO property VALUES (1,3261, 'hr', 'definition','Pravila o ribarskoj djelatnosti; u međunarodnom pravu ovaj predmet uređuje Ženevska konvencija iz 1958. god.', 0);
INSERT INTO property VALUES (1,3263, 'hr', 'definition','Ograničeni dio voda u kojem je dopušteno pecanje.', 0);
INSERT INTO property VALUES (1,3269, 'hr', 'definition','Bolesti ribe uočene u protekla tri desetljeća koje se pripisuju onečišćenjima obuhvaćaju krvarenja, tumore, truljenje peraja, deformirane peraje i nedostatak ljusaka i repa. U industrijaliziranim zemljama sve veći broj ribe smatra se nejestivim. Mnogi mali pomori se ne uočavaju ili se ne prijavljuju, a veliki pomori često nisu uključeni zbog nedovoljne informiranosti da se utvrdi jesu li pomori uzrokovani onečišćenjem ili prirodnim čimbenicima. Niske razine otopljenog kisika koje su rezultat prekomjerne kanalizacije jedan je od vodećih uzroka. Drugi najčešći uzrok su pesticidi.', 0);
INSERT INTO property VALUES (1,3273, 'hr', 'definition','Količina ribe na raspolaganju za upotrebu u budućnosti.', 0);
INSERT INTO property VALUES (1,3277, 'hr', 'definition','Vodoinstalaterska oprema u zgradi.', 0);
INSERT INTO property VALUES (1,3281, 'hr', 'definition','Praksa registracije trgovačkih brodova u zemljama s povoljnijim (tj. manje restriktivnim) sigurnosnim zahtjevima, pristojbama za registraciju itd.', 0);
INSERT INTO property VALUES (1,3282, 'hr', 'definition','1) Baklje upotrebljavaju otvoreni plamen tijekom normalnog rada i / ili rada u izvanrednim uvjetima  za spaljivanje opasnih plinova. Sustav nema posebne značajke za kontrolu temperature ili vremena  izgaranja, međutim, može biti potrebno dodatno gorivo za održavanje izgaranja. Povijesno gledano, baklje su se upotrebljavale za uništavanje otpadnih plinova u industriji nafte i plina i u postrojenjima za pročišćavanje otpadnih voda koje imaju anaerobne digestore. Uredbe o toplinskom uništavanju opasnog otpada ograničavaju praktičnu primjenu baklji za izgaranja relativno jednostavnih ugljikovodika, kao što je metan iz digestora ili sustava prikupljanja plina u odlagalištima.
INSERT INTO property VALUES (1,3283, 'hr', 'definition','Medicinske pretrage (testovi) čiji rezultati su dostupni vrlo brzo.', 0);
INSERT INTO property VALUES (1,3287, 'hr', 'definition','Tvar kao što je ekstrakt ili začin koji daje okus hrani.', 0);
INSERT INTO property VALUES (1,3288, 'hr', 'definition','Insekt bez krila koji čini red Siphonaptera (buhe); većina njih su ektoparaziti sisavaca i ptica.', 0);
INSERT INTO property VALUES (1,3291, 'hr', 'definition','Planovi, koji se u raznim pravilima nazivaju uprosječivanjem emisija, ili fleksibilnim planovima usklađenosti, omogućuju da se premalo kontroliraju neke točke emisije koje su preskupe za kontrolu na propisanim razinama sve dok su ove jedinice uravnotežene prevelikom kontrolom drugih emisijskih jedinica koje su isplativije za kontrolu.', 0);
INSERT INTO property VALUES (1,3294, 'hr', 'definition','Reagens koji se dodaje raspršenim krutim česticama u tekućini kako bi se skupile fine čestice koje tvore nakupine (flokule).', 0);
INSERT INTO property VALUES (1,3295, 'hr', 'definition','Proces kontakta i prijanjanja pri kojem čestice raspršene tvari tvore velike nakupine ili agregiranje čestica u koloidu, čime se oblikuju male kvržice, koji se zatim talože.', 0);
INSERT INTO property VALUES (1,3298, 'hr', 'definition','Neuobičajeno nakupljanje vode iznad zemlje prouzročeno plimom, jakom kišom, topljenjem snijega ili brzim otjecanjem kišnice s popločenih područja.', 0);
INSERT INTO property VALUES (1,3300, 'hr', 'definition','Mjere poduzete radi sprječavanja ili smanjenja štete izazvane neuobičajenim nakupljanjem vode iznad zemlje, koje često uključuje izgradnju spremnika i kanalskih konstrukcija.', 0);
INSERT INTO property VALUES (1,3301, 'hr', 'definition','Opće i privremeno stanje djelomične ili potpune poplavljenosti obično suhih površina zemljišta od prelijevanja vode iz unutrašnjosti i/ili plimne vode, i/ili neuobičajeno i brzo akumuliranje ili otjecanje površinskih voda iz bilo kojeg izvora. Velik protok uz vodotok ili protok koji uzrokuje plavljenje zemljišta koje obično nije pokriveno vodom.', 0);
INSERT INTO property VALUES (1,3305, 'hr', 'definition','Biljke koje su značajka određene geografske regije ili okoliša.', 0);
INSERT INTO property VALUES (1,3307, 'hr', 'definition','Proces vraćanja biljnih ekosustava i staništa u njihove izvorne uvjete.', 0);
INSERT INTO property VALUES (1,3308, 'hr', 'definition','Postupak odvajanja čestica krutih tvari time što se jedna skupina čestica ostavlja da pluta; u postupku se upotrebljavaju razlike u površinskim kemijskim svojstvima čestica, od kojih su neke potpuno namočene vodom, a druge nisu.', 0);
INSERT INTO property VALUES (1,3311, 'hr', 'definition','Stalno gibanje tekućine prema naprijed kroz zatvorene ili otvorene kanale ili vodove.', 0);
INSERT INTO property VALUES (1,3312, 'hr', 'definition','Reproduktivna struktura kritosjemenjača (angiospermae), koja se sastoji od prašnika i plodnih listića (karpela) okruženih laticama i  lapovima, a svi se nalaze na cvjetnoj osi.', 0);
INSERT INTO property VALUES (1,3313, 'hr', 'definition','Biljke sposobne proizvoditi uočljive cvjetove.', 0);
INSERT INTO property VALUES (1,3315, 'hr', 'definition','Brzina i gustoća tekućine kao funkcije položaja i vremena.', 0);
INSERT INTO property VALUES (1,3316, 'hr', 'definition','Vode koje teku, poput rijeka i potoka.', 0);
INSERT INTO property VALUES (1,3319, 'hr', 'definition','Plinoviti proizvod izgaranja koji nastaje u pećima i koji često izlazi iz dimnjaka.', 0);
INSERT INTO property VALUES (1,3322, 'hr', 'definition','Kontrolna tehnologija koja upotrebljava pojave dinamike za izvođenje očitavanja, kontrole, davanja informacija, obrade i pokretanja funkcija bez korištenja pokretnih mehaničkih dijelova.', 0);
INSERT INTO property VALUES (1,3324, 'hr', 'definition','Postupak pečenja u kojem su fino odijeljene krute tvari  suspendirane u toku zraka koji se diže (ili drugom fluidu), čime nastaje fluidizirani sloj; upotrebljava se u kalcinaciji različitih materijala, u industriji ugljena itd.', 0);
INSERT INTO property VALUES (1,3325, 'hr', 'definition','1) Sustav za izgaranje krutog goriva bogatog ugljikom učinkovito i pri relativno niskoj temperaturi, čime se u najvećoj mjeri smanjuje emisija štetnih tvari. Gorivo se razbija u vrlo male čestice ili prah i miješa sa česticama inertnog materijala. Mješavina se ubacuje u sloj kroz koji se zrak upumpava okomito prema gore, te miješa čestice koje se ponašaju kao tekućina. Prisilno strujanje zraka i mala veličine i odvajanje čestica goriva osigurava učinkovito spaljivanje.
INSERT INTO property VALUES (1,3328, 'hr', 'definition','Dodavanje fluora gradskom vodoopskrbnom sustavu u konačnoj koncentraciji od 0,8-1,6 ppm (dijelova po milijunu) kako bi se spriječila pojava zubnog karijesa kod djece.', 0);
INSERT INTO property VALUES (1,3334, 'hr', 'definition','Plinoviti ili tekući kemijski element, član porodice halogenida, to je najviše elektronegativni element i kemijski najviše energetski od nemetalnih elemenata; vrlo je otrovan, korozivan i zapaljiv; upotrebljava se u raketnim gorivima i kao kemijski poluproizvod.', 0);
INSERT INTO property VALUES (1,3340, 'hr', 'definition','Svaki izvor opskrbe iz rijeke, osobito riječna voda, koja se sakuplja, pohranjuje i obrađuje, a zatim distribuira za upotrebu u domaćinstvima, industriji, na poljoprivrednim gospodarstvima i u druge svrhe.', 0);
INSERT INTO property VALUES (1,3341, 'hr', 'definition','Prijevoz robe ili ljudi brodovima koji plove po rijekama.', 0);
INSERT INTO property VALUES (1,3342, 'hr', 'definition','Fino raspršene čestice pepela koje se nalaze u dimnim plinovima nastalim izgaranjem goriva ili drugog materijala. Čestice pepela mogu sadržavati nepotpuno izgoreno gorivo i druge onečišćivače.', 0);
INSERT INTO property VALUES (1,3343, 'hr', 'definition','Tvari koje omogućuju stvaranje homogenog raspršenja plinovite faze u tekućem ili krutom mediju.', 0);
INSERT INTO property VALUES (1,3345, 'hr', 'definition','Velike količine hrane za stoku, osobito sijeno, slama itd.', 0);
INSERT INTO property VALUES (1,3347, 'hr', 'definition','Vodene kapljice ili, rjeđe, ledeni kristali koji lebde u zraku u dovoljnim koncentracijama da značajno smanje vidljivost.', 0);
INSERT INTO property VALUES (1,3348, 'hr', 'definition','Sitne kapljice vode koje lebde u zraku, koje smanjuju vidljivost. Obično se izmaglica stvara noću, kada temperatura pada jer je nebo vedro. Ako vidljivost opadne na ispod 1.000 metara, izmaglica postaje magla.', 0);
INSERT INTO property VALUES (1,3350, 'hr', 'definition','Zeleni listovi biljke.', 0);
INSERT INTO property VALUES (1,3354, 'hr', 'definition','Tvari koje organizam može probaviti i iskoristiti kao izvor prehrane i energije.', 0);
INSERT INTO property VALUES (1,3356, 'hr', 'definition','Tvari koje nemaju nikakvu hranjivu vrijednost  same po sebi (ili se ne upotrebljavaju kao hranjive tvari) koje se dodaju hrani tijekom prerade radi poboljšavanja svojstava boje, teksture, okusa  ili trajnosti.', 0);
INSERT INTO property VALUES (1,3358, 'hr', 'definition','Niz organizama na uzastopnim trofičkim razinama u zajednici, kroz koje se energija prenosi hranom; energija ulazi u hranidbeni lanac tijekom fiksacije prema primarnim proizvođačima (uglavnom zelenim biljkama) i prelazi na biljojede (primarne potrošače), a zatim na zvijeri (sekundarne i tercijarne potrošače).', 0);
INSERT INTO property VALUES (1,3360, 'hr', 'definition','Svaka probavljiva tvar, obično sintetička boja, koju proizvođači dodaju hrani kako bi joj dale boju i učinile je privlačnijom.', 0);
INSERT INTO property VALUES (1,3362, 'hr', 'definition','Razmjena bilo koje hrane kao robe ili povezanih prehrambenih proizvoda, obično na veliko.', 0);
INSERT INTO property VALUES (1,3367, 'hr', 'definition','Dio znanosti higijene koji se bavi sanitarnim načelima i metodama primijenjenima na kvalitetu hrane, njezinu preradu ili obradu, pripremu, čuvanje i potrošnju od strane čovjeka.', 0);
INSERT INTO property VALUES (1,3368, 'hr', 'definition','Komercijalna proizvodnja i pakiranje hrane koja se proizvodi preradom, kombiniranjem raznih sastojaka ili oboje.', 0);
INSERT INTO property VALUES (1,3370, 'hr', 'definition','Najnoviji dodatak tehnologiji očuvanja hrane je korištenje izvora ionizirajućeg zračenja, koji ima određene posebne prednosti u odnosu prema konvencionalnim metodama. Kod iradijacije, hrana se može biti tretirati nakon pakiranja, čime se eliminira kontaminacija nakon obrade. Osim toga, namirnice su sačuvane u svježem stanju i mogu se čuvati dulje bez primjetnog gubitka kvalitete. Ozračenje hrane ne ostavlja ostatak, a promjene u prehrambenoj vrijednosti zbog zračenja usporedive su s onima koje nastaju u drugim procesima. Ozračenje je proces primjene velike energije na materijal, kao što su hrana, za sterilizaciju ili produženje roka trajanja ubijanjem mikroorganizama, insekata i drugih štetnika koji borave na njemu. Izvori ionizirajućeg zračenja koji se upotrebljavaju su gama zrake, elektronski snopovi i X-zrake. Gama zrake su proizvodi radioaktivnih izotopa kao što je kobalt-60. Elektronski snopovi se proizvode u linearnim akceleratorima, koji same pokreće električna energija. Doza primijenjena na proizvod najvažniji je čimbenik u procesu. U velikim dozama, hrana se u osnovi sterilizira, baš kao što se to događa u konzerviranju namirnica. Proizvodi koji su obrađeni na taj način mogu biti pohranjeni na sobnoj temperaturi gotovo zauvijek. Prijeporni su i zabranjeni u nekim zemljama.', 0);
INSERT INTO property VALUES (1,3372, 'hr', 'definition','Potencijalno štetne tvari u bilo kojoj hrani koju konzumiraju ljudi ili životinje, uključujući neorganske i organske kemikalije, viruse i bakterije.', 0);
INSERT INTO property VALUES (1,3375, 'hr', 'definition','Obrada hrane u cilju zaštite hrane od kvarenja izazvanog  mikrobima, encimima i autooksidacijom.', 0);
INSERT INTO property VALUES (1,3376, 'hr', 'definition','Komercijalno poduzeće u kojem se proizvodi ili pakira hrana namijenjena za ljude.', 0);
INSERT INTO property VALUES (1,3381, 'hr', 'definition','Minimalna količina hrane potrebna za zadovoljavanje osnovnih potreba organizma.', 0);
INSERT INTO property VALUES (1,3384, 'hr', 'definition','Primijenjena znanost koja se bavi kemijskim, biokemijskim, fizikalnim, fiziokemijskim i biološkim svojstvima hrane.', 0);
INSERT INTO property VALUES (1,3387, 'hr', 'definition','Zalihe hrane uskladištene kao nacionalna mjera za osiguranje  protiv fluktuacija u opskrbi hranom.', 0);
INSERT INTO property VALUES (1,3388, 'hr', 'definition','Tvar koje se može upotrebljavati ili pripremiti za upotrebu kao hrana.', 0);
INSERT INTO property VALUES (1,3391, 'hr', 'definition','Primjena znanosti i tehnike u poboljšavanju, proizvodnji i rukovanju hranom; mnogi prehrambeni tehnolozi su više znanstvenici za hranu nego inženjeri.', 0);
INSERT INTO property VALUES (1,3395, 'hr', 'definition','Uzak put namijenjen samo pješacima.', 0);
INSERT INTO property VALUES (1,3396, 'hr', 'definition','Unos opasnih ili otrovnih tvari kao što su arsen ili olovo u, ili na, hranu za životinje. Životinje pojedu onečišćenu hranu i razbole se, a mogu i umrijeti.', 0);
INSERT INTO property VALUES (1,3400, 'hr', 'definition','Procjena ili predviđanje budućeg stanja.', 0);
INSERT INTO property VALUES (1,3401, 'hr', 'definition','Isključiti, zabraniti, uništiti kapital za iskup. Prestanak svih prava hipotekarnog (založnog) dužnika ili njegovog primatelja imovine u vlasništvu pokrivenom hipotekom. Proces kojim je hipotekarni (založni) dužnik stvarne ili osobne imovine, ili drugi vlasnik imovine koja je predmet založnog prava, lišen interesa u njoj. Postupak kojim se nekretnina pod hipotekom prodaje kada dužnik propusti podmiriti hipotekarni dug. U uobičajenoj upotrebi ovaj izraz odnosi se na provedbu založnog prava, fiducijarnog pravnog posla ili hipoteke na bilo koji način predviđen zakonom.', 0);
INSERT INTO property VALUES (1,3402, 'hr', 'definition','Suradnja sa stranim državama u ekonomskim ili monetarnim pitanjima.', 0);
INSERT INTO property VALUES (1,3404, 'hr', 'definition','Diplomatska politika neke države u njezinim odnosima s drugim državama.', 0);
INSERT INTO property VALUES (1,3405, 'hr', 'definition','Trgovanje između država i poduzeća iz različitih država.', 0);
INSERT INTO property VALUES (1,3406, 'hr', 'definition','Biljna zajednica u kojoj prevladava drveće i drugo drvenasto grmlje, koje raste dovoljno gusto da se vrhovi drveća dodiruju ili preklapaju, čime se stvaraju razni stupnjevi sjene na šumskom tlu. Iz šume se dobiva oblovina (drveni trupci), a služi i za rekreaciju, kao stanište životinja i biljaka itd.', 0);
INSERT INTO property VALUES (1,3411, 'hr', 'definition','Uništavanje šuma provodi se u mnogim zemljama radi osiguravanja nove zemlje za poljoprivredne ili stočarske svrhe. To se često radi bez dovoljnog proučavanja čimbenika kao što su klima i topografija, te na zemljištima gdje nagibi tla ili druge fiziografske značajke jasno pokazuju da je zemljište o kojem se radi pogodno samo za šume. Iako takva praksa može dovesti do privremenog povećanja produktivnosti, postoje i mnoge indicije da je dugoročno produktivnost po jedinici površine obično opada, te da erozija tla i nepovratno pogoršanje tla često prate ovaj proces. Uništavanju šumskog pokrova pridonose mnogi čimbenici: proizvodnja drva (oblovine - drvenih trupaca), krčenje šume u poljoprivredne svrhe, rezanje šumskog drveća za ogrjev i pravljenje ugljena, požari, suše, dnevni kop, onečišćenje, urbani razvoj, pritisci stanovništva i rat.', 0);
INSERT INTO property VALUES (1,3414, 'hr', 'definition','Smanjenje broja drveća u šumama zbog kiselih padalina, šumskih požara, onečišćenje zraka, krčenje šuma, štetnika i bolesti drveća, životinja i biljaka itd.', 0);
INSERT INTO property VALUES (1,3416, 'hr', 'definition','Svako šumsko okruženje u kojem biljke i životinje međusobno djeluju s kemijskim i fizičkim značajkama okoliša u kojem žive.', 0);
INSERT INTO property VALUES (1,3419, 'hr', 'definition','Veliki požar ili uništenje velikih šumovitih površina s gustim drvećem i biljkama.', 0);
INSERT INTO property VALUES (1,3423, 'hr', 'definition','Gospodarski sektor u kojem se sva poduzeća bave gospodarenjem velikim šumskim područjem, često radi proizvodnje proizvoda i drugih koristi kao što su oblovina (drveni trupci), staništa životinja koje žive u divljini i  biljaka, čista voda, bioraznolikost i rekreacija.', 0);
INSERT INTO property VALUES (1,3425, 'hr', 'definition','Planiranje upotrebe šuma za proizvodnju drva, u svrhu očuvanja, zaštite životinjskog i biljnog svijeta, rekreacije i vodoopskrbe.', 0);
INSERT INTO property VALUES (1,3426, 'hr', 'definition','Organizmi koji nanose štetu drveću.', 0);
INSERT INTO property VALUES (1,3430, 'hr', 'definition','Djelovanje koje usvoji i provodi vlada ili neka druga  organizacija, kojim se nastoji očuvati ili zaštititi velika površina šumskog područja, često radi proizvodnje proizvoda i drugih koristi kao što su oblovina (drveni trupci), staništa životinja koje žive u divljini i  biljaka, čista voda, bioraznolikost i rekreacija.', 0);
INSERT INTO property VALUES (1,3431, 'hr', 'definition','Šume proizvode cijeli niz proizvoda, uključujući drvo za loženje i ugljen, oblovinu, papir i kulture kao što su kava, uljna palma i guma. Uz pažljivo planiranje rasta i berbe, drvo i drugih šumskih proizvodi su, u načelu, obnovljivi izvori energije. Međutim, za postizanje obnovljivosti potrebno je vrijeme - često desetljeća, a ponekad i stoljeća. Bez pažljivog gospodarenja pritisak na kratkoročne eksploatacije može dovesti do uklanjanja stabla, degradaciju tla i pretvorbu šumskog tla u druge svrhe. Potrošnja šumskih resursa može prouzročiti ekološke probleme, kao i gubitak kritičnih staništa i vrsta.', 0);
INSERT INTO property VALUES (1,3432, 'hr', 'definition','Svi materijali kojih ima u šumi u dovoljnim količinama za komercijalnu upotrebu, kao što su proizvodi od drva i krmivo za stoku.', 0);
INSERT INTO property VALUES (1,3435, 'hr', 'definition','Šumsko područje odvojeno u svrhu zaštite određene faune i flore ili i jednoga i drugoga.', 0);
INSERT INTO property VALUES (1,3438, 'hr', 'definition','Šumski resursi sastoje se od dva odijeljena, ali usko povezana dijela: šumskog zemljišta i drveća (oblovine - drvenih trupaca) na tom zemljištu.', 0);
INSERT INTO property VALUES (1,3439, 'hr', 'definition','Gospodarenje šumskim zemljištem za dobivanje drva, stočne hrane, vode, životinja koje žive u prirodi i biljaka te u svrhu  rekreacije.', 0);
INSERT INTO property VALUES (1,3440, 'hr', 'definition','Proizvodnja, distribucija i potrošnja robe i usluga iz industrije povezane s postupkom osnivanja i gospodarenja šumama.', 0);
INSERT INTO property VALUES (1,3441, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koji propiše vlada, a kojim se uređuju sve veće površine pod šumama radi zaštite i očuvanja divljači, oblovine (drvenih trupaca) i ostalih šumskih resursa.', 0);
INSERT INTO property VALUES (1,3442, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koji propiše vlada, a kojim se uređuje upotreba i očuvanje područja pod šumom, najčešće onih u državnom vlasništvu.', 0);
INSERT INTO property VALUES (1,3444, 'hr', 'definition','The farming of trees to ensure a continuing supply of timber and other forest products. Foresters care for existing trees, protecting them from fire, pests and diseases, and felling where trees are overcrowded or dying and when ready for cropping. They also plant new areas (afforestation) and replant felled areas (reafforestation).', 0);
INSERT INTO property VALUES (1,3455, 'hr', 'definition','Oblik vlasti u kojem pojedinac ili skupina pojedinaca ostvaruju vlast nad većinom.', 0);
INSERT INTO property VALUES (1,3460, 'hr', 'definition','Ostaci, tragovi ili otisci  biljke ili životinje koji su sačuvani u Zemljinoj kori iz nekoga prošlog geološkog ili pretpovijesnog vremena.', 0);
INSERT INTO property VALUES (1,3462, 'hr', 'definition','Energetski bogate tvari koje su se pretvorile tijekom mnogih tisućljeća od svog izvornog oblika drveća, biljaka i drugih  organizama nakon što su bile zakopane u zemlji. U Zemljinoj kori su se događali fizički i kemijski procesi koji su ih pretvorili u ugljen, treset, naftu ili prirodni plin.', 0);
INSERT INTO property VALUES (1,3465, 'hr', 'definition','Prijanjanje različitih morskih organizama na podvodnom dijelu brodova, zbog čega brodovi gube brzinu.', 0);
INSERT INTO property VALUES (1,3469, 'hr', 'definition','Motor s unutarnjim izgaranjem čiji ciklus ima četiri takta klipa; uključuje usisni takt, komprimiranje, ekspanziju i ispušni takt.', 0);
INSERT INTO property VALUES (1,3474, 'hr', 'definition','Skup pravila koje propisuje vlada, često sastavljenih u nizu međusobno povezanih dijelova, kojima se uspostavlja ili postavlja temelj za novi projekt, agenciju ili organizacijsku  strukturu.', 0);
INSERT INTO property VALUES (1,3477, 'hr', 'definition','Mogućnost, pravo i dopuštenje da se pristupi izvorima kojima se prenose znanja i da se oni upotrijebe, ili njihova opća dostupnost.', 0);
INSERT INTO property VALUES (1,3481, 'hr', 'definition','Prijevoz robe brodom, avionima ili drugim prijevoznim sredstvima.', 0);
INSERT INTO property VALUES (1,3483, 'hr', 'definition','Komercijalno ime za skupinu polihalogenih ugljikovodika, koji sadrže fluor i klor; primjer je triklorofluorometan.', 0);
INSERT INTO property VALUES (1,3485, 'hr', 'definition','Voda koja ima relativno mali sadržaj mineralnih tvari, općenito manje od 500 mg/l otopljenih krutih tvari.', 0);
INSERT INTO property VALUES (1,3487, 'hr', 'definition','Znanstveno proučavanje ili karakteristični životni procesi živih organizama koji se nalaze u prirodnim vodama koje ne sadrže značajne količine otopljenih soli i minerala, kao što je jezero ili rijeka.', 0);
INSERT INTO property VALUES (1,3488, 'hr', 'definition','Onečišćenje odmah ili na kraju uključuje hidrološki ciklus zemlje, jer čak i onečišćivači koji se ispuštaju u zrak i oni koji su prisutni u tlu ispiru oborine. Voda se smatra onečišćenom kada se njegov  sastav ili stanje izmijene tako da ona postaje manje prikladna za bilo koju ili sve funkcije i svrhe za koje će biti prikladne u svome prirodnom stanju. Ova definicija uključuje promjene u fizičkim, kemijskim i biološkim svojstvima vode, ili takva ispuštanja tekućih, plinovitih ili krutih tvari u vodu za koje je vjerojatno da će stvoriti neugodnosti ili učiniti takvu vodu štetnom za javno zdravlje, sigurnost ili dobrobit, ili da se neće moći upotrijebiti u domaćinstvima, za komercijalne, industrijske, poljoprivredne svrhe, za ribe ili druge oblike života u vodi. Ona također uključuje i promjene u temperaturama zbog ispuštanja tople vode.', 0);
INSERT INTO property VALUES (1,3489, 'hr', 'definition','Živi organizmi i neživa priroda vodenog okoliša na kopnu.', 0);
INSERT INTO property VALUES (1,3492, 'hr', 'definition','Organizmi koji žive u slatkoj vodi.', 0);
INSERT INTO property VALUES (1,3493, 'hr', 'definition','Izravni ili neizravni čovjekovi utjecaji na biološku, fizikalnu, kemijsku ili radiološko cjelovitost slatke vode.', 0);
INSERT INTO property VALUES (1,3494, 'hr', 'definition','Mreža rijeka, jezera i ostalih površinskih voda iz kojih se dobiva voda za pripremu hrane i ostale osnovne čovjekove sustave.', 0);
INSERT INTO property VALUES (1,3495, 'hr', 'definition','Vodozemac koji jede insekte, reda Anura, iz porodice Ranidae, kao što je mrka žaba (Rana temporaria), koja ima kratko, bezrepo tijelo na kojem čuči, s vlažnom, glatkom kožom i vrlo dugim stražnjim nogama specijaliziranim za skakanje.', 0);
INSERT INTO property VALUES (1,3496, 'hr', 'definition','Naslaga međusobno prepletenih ledenih kristala nastalih neposrednom sublimacijom na predmetima.', 0);
INSERT INTO property VALUES (1,3498, 'hr', 'definition','Potpuno dozrela plodnica biljke sa ili bez drugih cvjetnih dijelova ili dijelova mladice koji se s njom spajaju kada dozore.', 0);
INSERT INTO property VALUES (1,3499, 'hr', 'definition','Uzgoj voćaka za domaćinstvo ili komercijalnu upotrebu.', 0);
INSERT INTO property VALUES (1,3502, 'hr', 'definition','Svako stablo koje rađa jestivo voće.', 0);
INSERT INTO property VALUES (1,3505, 'hr', 'definition','Krute, tekuće ili plinovite tvari, npr. plin, benzin, nafta, ugljen ili drvo, koje se upotrebljavaju za proizvodnju topline ili energije procesom izgaranja.', 0);
INSERT INTO property VALUES (1,3506, 'hr', 'definition','Tvar (npr. olovni tetraetil) koja se dodaje benzinu za sprječavanje lupanja u motoru.', 0);
INSERT INTO property VALUES (1,3507, 'hr', 'definition','Alternativni izvor energije za motorna vozila. Proizvodi se fermentacijom šećerne trske s pomoću kvasca (Saccharomyces cerevisiae).', 0);
INSERT INTO property VALUES (1,3510, 'hr', 'definition','Količina upotrijebljenog goriva.', 0);
INSERT INTO property VALUES (1,3512, 'hr', 'definition','Tekući proizvod koji se upotrebljavao kao gorivo za stvaranje topline, osim ulja s temperaturom paljenja nižom od 38°C; uključuje loživo ulje, ulje za štednjake, ulje za (industrijske) peći, teška goriva.', 0);
INSERT INTO property VALUES (1,3516, 'hr', 'definition','Radna komponenta u sustavu goriva za pohranu goriva.', 0);
INSERT INTO property VALUES (1,3517, 'hr', 'definition','Drvo koje se upotrebljava za grijanje.', 0);
INSERT INTO property VALUES (1,3518, 'hr', 'definition','Krute tvari u zraku stvorene kondenzacijom pare, kemijskim reakcijama ili sublimacijom (izravna promjena iz krutog stanja u plinovito). Često su to metalni oksidi ili metali, promjer tih čestica je manji od 1 mikrometra, a mogu biti otrovne', 0);
INSERT INTO property VALUES (1,3521, 'hr', 'definition','Upotreba kemijskog spoja u plinovitom agregatnom stanju za suzbijanje insekata, nematoda, paukova, glodavaca, korova i gljiva u zatvorenim ili nedostupnim mjestima; također se upotrebljava za suzbijanje korova, nematoda i insekata na poljima.', 0);
INSERT INTO property VALUES (1,3522, 'hr', 'definition','Tvar u smislu njezine funkcije ili svrhe, na primjer sredstvo za bojenje ili konzervans.', 0);
INSERT INTO property VALUES (1,3524, 'hr', 'definition','Glavna funkcija tla je sudjelovanje u procesima preobrazbe i migracije materijala koji se odvijaju u prirodnom okolišu o kojima ovisi funkcioniranje ekosustava. Najaktivniji sudionici u tim procesima su mikroorganizmi i beskralješnjaci, čije djelovanje, različite sorte, složene strukture i obilje točno odražavaju vrstu tla i njegove značajke, tako da su oni važni pokazatelji ekološke stabilnosti. Raznolikost organizama u tlu određuje njegov samoregulatorni kapacitet i kapacitet samočišćenja.', 0);
INSERT INTO property VALUES (1,3528, 'hr', 'definition','Nukleotidni organizmi, običano nitasti, sa sporama, bez klorofila.', 0);
INSERT INTO property VALUES (1,3529, 'hr', 'definition','Nukleotidni organizmi, običano nitasti, sa sporama  sporebearing, bez klorofila.', 0);
INSERT INTO property VALUES (1,3530, 'hr', 'definition','Kemikalije koje se upotrebljavaju za uništavanje ili zaustavljanje razvoja gljivica koje prouzročuju bolesti biljaka, kao što su: truljenje pohrane, bolesti sadnica; truljenje korijena, vaskularno uvenuće, medljika, rđanje, snijet i plijesan na lišću, te virusne bolesti. Te bolesti mogu se kontrolirani ranom i stalnom primjenom odabranih fungicida koji ili ubijaju patogene, ili ograničavaju njihov razvoj.', 0);
INSERT INTO property VALUES (1,3533, 'hr', 'definition','Dlakom pokriveno, učinjeno krzno sisavaca koje se upotrebljava u izradi odjeće i kao modni dodatak ili ukras.', 0);
INSERT INTO property VALUES (1,3535, 'hr', 'definition','Životinje koje se uzgajaju i kolju zbog krzna.', 0);
INSERT INTO property VALUES (1,3536, 'hr', 'definition','Bezbojni, zapaljivi, toksični i tekući heterociklički spoj, koji se upotrebljava pri sintezi najlona.', 0);
INSERT INTO property VALUES (1,3538, 'hr', 'definition','Konstrukcija ili naprava u kojoj se izgaranjem goriva stvara toplina, često za grijanje kuća, taljenje metala, proizvodnju pare i pečenje keramike.', 0);
INSERT INTO property VALUES (1,3539, 'hr', 'definition','Pomični predmeti u prostoriji ili ustanovi/poduzeću koji u njima omogućuju boravak ili življenje.', 0);
INSERT INTO property VALUES (1,3542, 'hr', 'definition','Poslovna djelatnost ili trgovanje učinjenim krznom i odjećom, koje se proizvode od krzna nekih životinja.', 0);
INSERT INTO property VALUES (1,3548, 'hr', 'definition','Red ptica koji uključuje lještarke, alpske kokoši, tetrijebe, jarebice, fazane, prepelice, pure i paune. To su uglavnom ptice koje se hrane zrnjem, koje imaju teško tijelo, svijaju gnijezda na zemlji, koje su u stanju letjeti samo na brzim, kratkim letovima. Mužjaci su obično šareniji od ženki.', 0);
INSERT INTO property VALUES (1,3550, 'hr', 'definition','Pocinčavanje željeza ili čelika, bilo uranjanjem u kupelj od rastaljenog cinka ili taloženjem iz otopine cinkova sulfata, radi zaštite od korozije.', 0);
INSERT INTO property VALUES (1,3553, 'hr', 'definition','Divlje životinje, uključujući ptice i ribe, koje se love radi sporta, hrane ili zarade.', 0);
INSERT INTO property VALUES (1,3554, 'hr', 'definition','Zračenje gama zraka.', 0);
INSERT INTO property VALUES (1,3559, 'hr', 'definition','Komad zemlje u blizini kuće gdje se uzgajaju cvijeće i ostale biljke i koji često imaju tratinu.', 0);
INSERT INTO property VALUES (1,3562, 'hr', 'definition','Natural organic matter discarded from gardens and yards including leaves, grass clippings, prunings, brush and stumps.', 0);
INSERT INTO property VALUES (1,3564, 'hr', 'definition','Mediteransko grmlje koje se sastoji od niskih zimzelenih grmova i obilnih travolikih biljaka.', 0);
INSERT INTO property VALUES (1,3566, 'hr', 'definition','Tvar koja trajno zauzima cijeli prostor u kojem se nalazi, bez obzira na to koliko je taj prostor velik ili malen, pri konstantnoj temperaturi.', 0);
INSERT INTO property VALUES (1,3567, 'hr', 'definition','Separacijska metoda kojom plinovita pokretna faza prolazi kroz stupac koji sadrži nepokretnu fazu; najčešće se upotrebljava kao kvantitativna analitička metoda za hlapljive spojeve.', 0);
INSERT INTO property VALUES (1,3570, 'hr', 'definition','Poduzeće zaduženo za proizvodnju i distribuciju plina za upotrebu u domaćinstvima.', 0);
INSERT INTO property VALUES (1,3571, 'hr', 'definition','Motor s unutarnjim izgaranjem koji upotrebljava plinsko gorivo.', 0);
INSERT INTO property VALUES (1,3572, 'hr', 'definition','Nekondenzirani ili lako hlapljivi plinovi, uglavnom sastavljeni od kemijskih spojeva koji su ispušteni u atmosferu.', 0);
INSERT INTO property VALUES (1,3577, 'hr', 'definition','Agregatno stanje tvari u kojem tvar o kojoj se radi zauzima sav prostor u kojem se nalazi, neovisno o njegovoj veličini.', 0);
INSERT INTO property VALUES (1,3578, 'hr', 'definition','Prije nego što se može isporučiti na tržištu, plin se obrađuje. U kolikoj mjeri plin treba obraditi, ovisi o njegovoj kvaliteti, količini prisutnih nečistoća kao što su voda, ugljični dioksid i sumporni spojevi, te krajnjoj namjeni plina. Uobičajene plinovite primjese koje se nalaze u prirodnom plinu su ugljični dioksid i sumporni spojevi. Obje te primjese imaju kiselu reakciju i dobile su su generički naziv ''kiseli plinovi''. Ovi plinovi mogu se odstraniti pomoću više raznih komercijalnih procesa, korištenjem fizičkih ili kemijskih otapala. Procesi u kojima se upotrebljavaju fizička otapala obično se upotrebljavaju tamo gdje su pritisci plina visoki i za plinove s nižim razinama propana i težih ugljikovodika.', 0);
INSERT INTO property VALUES (1,3581, 'hr', 'definition','1) Svaki kemijski ili toplinski proces koji se upotrebljava za pretvorbu tvari u plin.
INSERT INTO property VALUES (1,3582, 'hr', 'definition','Pretvaranje plina u tekuće agregatno stanje hlađenjem ili sabijanjem.', 0);
INSERT INTO property VALUES (1,3586, 'hr', 'definition','Sustav međusobno povezanih cijevi za distribuciju i opskrbu plinom.', 0);
INSERT INTO property VALUES (1,3587, 'hr', 'definition','Smjesa 80- ili 90-postotnog benzina i 20- ili 10-postotnog etilnog alkohola, koja se upotrebljava kao gorivo za motore su unutarnjim izgaranjem.', 0);
INSERT INTO property VALUES (1,3589, 'hr', 'definition','Motor s unutarnjim izgaranjem, koji kao gorivo upotrebljava  smjesu zraka i benzinskih para.', 0);
INSERT INTO property VALUES (1,3591, 'hr', 'definition','Duga cijev, obično ispod zemlje, koja se upotrebljava za prijenos plina na velike daljine.', 0);
INSERT INTO property VALUES (1,3593, 'hr', 'definition','Elektrana na pogon plinom, za razliku od elektrana na ugljen ili nuklearnih elektrana.', 0);
INSERT INTO property VALUES (1,3594, 'hr', 'definition','Uklanjanje onečišćujućih tvari ili kontaminanata nastalih spaljivanjem otpada ili u drugim procesima izgaranja.', 0);
INSERT INTO property VALUES (1,3595, 'hr', 'definition','Naprava za ponovnu upotrebu plinova: za vrijeme ponovnog punjenja gorivom benzinske pare se usisavaju i odvode natrag u spremnik.', 0);
INSERT INTO property VALUES (1,3596, 'hr', 'definition','Veliki spremnik za pohranu ugljenog plina ili prirodnog plina.', 0);
INSERT INTO property VALUES (1,3599, 'hr', 'definition','Dobavljanje i pohranjivanje plinskih goriva, ili količina pohranjenog plinskog goriva za potrebe grada ili nekog drugog korisnika plinskog goriva.', 0);
INSERT INTO property VALUES (1,3600, 'hr', 'definition','Mekušci razreda Gastropoda, obično sa spljoštenim mišićnim stopalom koja im služi za gibanje i glavom na kojoj se nalaze oči na ticalima.', 0);
INSERT INTO property VALUES (1,3604, 'hr', 'definition','Mjesto gdje se proizvodi plin, osobito ugljeni plin.', 0);
INSERT INTO property VALUES (1,3611, 'hr', 'definition','Stajalište, tema ili spor u vezi sa sposobnostima, društvenim ulogama ili drugim razlikama i podjelama između žena i muškaraca, osobito u pogledu relativne težine bioloških i fizičkih razlika nasuprot kulturi i socijalizaciji kao uzrocima tih razlika.', 0);
INSERT INTO property VALUES (1,3613, 'hr', 'definition','Skladišta sjemena ili vegetativnog tkiva, koje se čuva pri niskoj temperaturi i vlažnosti, za pomoć u održavanju genetske raznolikosti. Ponekad se nazivaju i bankama sjemena ili bankama genetskog materijala. Njihov sadržaj uglavnom potječe iz širokog spektra primitivnih sojeva i sorti  divljih usjeva. Međunarodni odbor za genetske izvore biljnog materijala (IBPGR), koji je osnovan 1974. god., promiče prikupljanje, dokumentiranje, vrednovanje, očuvanje i konačnu uporabu genetskih izvora značajnih biljnih vrsta. Banke gena su predmet međunarodnih prijepora jer one sadrže sjemenke koje su industrijski bogate zemlje uglavnom dobile iz zemalja u razvoju, gdje su korištene u programima uzgoja za razvoj novih sojeva. Umjesto tradicionalnog programa uzgoja bilja gnojidbom za koji su potrebna desetljeća, sada je moguće izravno manipulirati genima biljaka, pri čemu se stvaraju genetski modificirani organizmi (GMO), koji su biljke promijenjene tako da budu otpornije na bolesti i imaju poboljšani rast i prinos, a time se i povećava dobit uzgajivača i poljoprivrednika.', 0);
INSERT INTO property VALUES (1,3614, 'hr', 'definition','Administrativno ovlaštenje ili nalog u kojem se utvrđuje postupak kojim određena organizacija ili državna agencija treba provesti u praksi neki koncept, plan, uredbu ili zakon.', 0);
INSERT INTO property VALUES (1,3615, 'hr', 'definition','Proučavanje elemenata i spojeva koje oni tvore.', 0);
INSERT INTO property VALUES (1,3617, 'hr', 'definition','Jedinica nasljeđivanja koja se sastoji od DNK, a zauzima fiksni položaj na kromosomu. Gen može odrediti obilježje pojedinca određivanjem polipeptidnog lanca koji tvori protein ili dio proteina (strukturni gen); a može i potisnuti takvo djelovanje (represorski gen).', 0);
INSERT INTO property VALUES (1,3620, 'hr', 'definition','Razlike među jedinkama i između populacija određene vrste', 0);
INSERT INTO property VALUES (1,3621, 'hr', 'definition','Nasljedna promjena, uglavnom mutacije koje proizvode kemijske tvari, herbicidi, zračenja itd.', 0);
INSERT INTO property VALUES (1,3624, 'hr', 'definition','1) Kompleks tehnoloških metoda za proizvodnju novih gena i mijenjanje strukture kromosoma radi proizvodnje učinaka korisnih za čovjeka, u poljoprivredi i medicini.
INSERT INTO property VALUES (1,3628, 'hr', 'definition','Informacije za sintezu bjelančevina koje se nalaze u nizovima nukleotida polinukleotidnog lanca DNK.', 0);
INSERT INTO property VALUES (1,3630, 'hr', 'definition','Nasljedne promjene koje stvara ionizirajuće zračenje, izlaganje određenim kemikalijama, unos nekih lijekova i koje nastaju zbog drugih uzroka.', 0);
INSERT INTO property VALUES (1,3632, 'hr', 'definition','Genetski bazen (genetska zaliha) prirodnih i kultiviranih zaliha organizama koji su dostupni za ljudsko iskorištavanje. Poželjno je održavati što je moguće raznovrsniji raspon organizama, posebno udomaćenih sorti (kultivara) i njihovih predaka, kako bi se održala široka genetska baza. Što je šira genetska baza, veća je sposobnost prilagodbe određenim uvjetima okoliša.', 0);
INSERT INTO property VALUES (1,3633, 'hr', 'definition','Znanost koja se bavi proučavanjem biološkog nasljeđivanja.', 0);
INSERT INTO property VALUES (1,3634, 'hr', 'definition','Promjena jedne ili više fenotipskih značajki zbog genske mutacije ili genske preraspodjele, učinaka okoliša itd.', 0);
INSERT INTO property VALUES (1,3638, 'hr', 'definition','Grana geofizike koja uključuje utvrđivanje veličine i oblika Zemlje, Zemljinoga gravitacijskog polja te lokacija točaka pridruženim Zemljini površini u Zemljinom koordinacijskom sustavu.', 0);
INSERT INTO property VALUES (1,3640, 'hr', 'definition','Geogeni čimbenici su oni koji nastaju u tlu, za razliku od onih antropskog podrijetla (antropogenskih).', 0);
INSERT INTO property VALUES (1,3645, 'hr', 'definition','Organizirana zbirka računalnog hardvera, softvera, geografskih podataka i osoblja čija je namjena učinkovito pribavljanje, pohranjivanje, ažuriranje, manipuliranje, analiziranje te prikaz svih oblika geografskih vrsta informacija koje se mogu pribaviti iz različitih izvora, uključujući statističke i kartografske.', 0);
INSERT INTO property VALUES (1,3646, 'hr', 'definition','Proučavanje prirodnih značajki Zemljine površine, uključujući  topografiju, klimu, tlo, biljni pokrov itd. i čovjekovu reakciju na njih.', 0);
INSERT INTO property VALUES (1,3647, 'hr', 'definition','Katastrofe prouzročene pomicanjem i deformacijama Zemljine kore.', 0);
INSERT INTO property VALUES (1,3648, 'hr', 'definition','Dinamička djelovanja ili događaji koji se zbivaju na površini Zemlje zbog primjene prirodnih sila koje proizlaze iz Zemljine sile teže, temperaturnih promjena, zamrzavanja i odmrzavanja, kemijskih reakcija, seizmičkih potresa te djelovanja vjetra i kretanje vode, snijega i leda. Gdje i kada sila nadilazi snagu materijala u Zemlji, materijal se mijenja zbog deformacije, premještanja ili kemijskih reakcija.', 0);
INSERT INTO property VALUES (1,3650, 'hr', 'definition','Proučavanje ili znanost o Zemlji, njezinoj povijesti i njezinom životu kako su zabilježeni u stijenama; uključuje proučavanje geoloških značajki nekog područja, kao što je geometrija oblika stijena, vremenski utjecaji i erozija, te sedimentacija.', 0);
INSERT INTO property VALUES (1,3652, 'hr', 'definition','Fizikalne i kemijske interakcije između Zemljine površine i prirodnih sila koje djeluju na nju, čime nastaju razne vrste terena. Procese određuju prirodne ekološke varijable kao što je geologija, klima, vegetacije i najniža razina nastala erozijom, da se utjecaj ljudi i ne spominje. Priroda ovog procesa i brzina kojom se on odvija bit će pod utjecajem promjene u bilo kojoj od tih varijabli.', 0);
INSERT INTO property VALUES (1,3654, 'hr', 'definition','Proučavanje klasifikacije, opisa, prirode, porijekla i razvoja trenutnih oblika površine te njihovih odnosa prema strukturama ispod njih, te povijesti geoloških promjena kako su ih te značajke površine očuvale.', 0);
INSERT INTO property VALUES (1,3655, 'hr', 'definition','Fizika Zemlje i njezinog okoliša, tj. Zemlje, zraka i svemira.', 0);
INSERT INTO property VALUES (1,3657, 'hr', 'definition','Primjena znanstvenih metoda i inženjerskih tehnika na iskorištavanje i korištenje prirodnih resursa.', 0);
INSERT INTO property VALUES (1,3658, 'hr', 'definition','Energija proizvedena iskorištavanjem Zemljine unutarnje topline. Trenutno su jedine raspoložive tehnologije za to one kojima se toplina izvlači iz hidrotermalnih konvekcijskih sustava, gdje voda ili para prenose toplinu iz dubljeg dijela Zemlje na područja iz kojih se energija može odvoditi. Količina onečišćujućih tvari koja se nalazi u geotermalnoj energiji razlikuje se od područja do područja, ali mogu sadržavati arsen, bor, selen, olovo, kadmij i fluoride. One također mogu sadržavati sumporovodik, živu, amonijak, radon, ugljični dioksid i metan.', 0);
INSERT INTO property VALUES (1,3661, 'hr', 'definition','1) Patogeni mikroorganizam.
INSERT INTO property VALUES (1,3663, 'hr', 'definition','Početak ili proces razvoja spore ili sjemena.', 0);
INSERT INTO property VALUES (1,3664, 'hr', 'definition','Nasljedni materijal koji se prenosi na potomke preko gameta.', 0);
INSERT INTO property VALUES (1,3673, 'hr', 'definition','Mase leda koje se sporo kreću i koje su se nakupile bilo na planinama ili u polarnim područjima. One se nalaze tamo gdje se topao, vlažan zrak ili topla voda miješa s hladnim zrakom ili vodom. Pomiču se, pod utjecajem gravitacijske sile i pritiska leda, iznad  slojeva otopljenog leda i spuštaju se nizbrdo, i na kraju se tope na nižim razinama te stvaraju rijeke ili dolaze do razine mora, gdje oblikuju ledene grebene ili padaju u vodu u obliku ledenjaka.', 0);
INSERT INTO property VALUES (1,3676, 'hr', 'definition','1) Proučavanje svih vidova snijega i leda; znanost koja kvantitativno proučava cijeli niz procesa povezanih sa svim oblicima postojeće vode u krutom agregatnom stanju. 
INSERT INTO property VALUES (1,3678, 'hr', 'definition','Tvrda, amorfna, anorganska, obično prozirna, krhka tvar koja nastaje spajanjem silikata, ponekad i borata i fosfata, s određenim osnovnim oksidima, a zatim se te tvari brzo hlade da se spriječi kristalizacija.', 0);
INSERT INTO property VALUES (1,3681, 'hr', 'definition','Industrija koja se bavi proizvodnjom staklenih proizvoda.', 0);
INSERT INTO property VALUES (1,3686, 'hr', 'definition','Aspekti prema kojima se cijeli svijet smatra blisko povezanim modernim telekomunikacijama kao međusobno ovisnima, i u društvenom i u političkom smislu.', 0);
INSERT INTO property VALUES (1,3687, 'hr', 'definition','Svjetska skupština nacionalnih izaslanika, izaslanika političkih stranaka ili organizacija, ili pakt ili sporazum koji proizađe iz takve skupštine i koji je često prethodnik međunarodnog ugovora.', 0);
INSERT INTO property VALUES (1,3688, 'hr', 'definition','Međunarodna organizacija osnovana 1990. god. kako bi se pružila praktična pomoć vladama u ostvarivanju poboljšanja zaštite okoliša. GEF-om upravlja Svjetska banka, koja osigurava 2/3  sredstava za ovaj fond, a preostalom 1/3 upravlja Program Ujedinjenih naroda za razvoj (UNDP).', 0);
INSERT INTO property VALUES (1,3690, 'hr', 'definition','Modeli koji se odnose na različite aspekte stvarnosti koji se mogu primijeniti na globalnoj razini.', 0);
INSERT INTO property VALUES (1,3692, 'hr', 'definition','Promjene u temperaturi na površini Zemlje, koja se naziva globalnom temperaturom, koje nastaju zbog efekta staklenika kojega potiče emisija stakleničkih plinova u zrak.', 0);
INSERT INTO property VALUES (1,3694, 'hr', 'definition','Abecedni popis izraza koji se odnose na određenu temu, polje ili područje upotrebe koji uključuje popratne definicije.', 0);
INSERT INTO property VALUES (1,3695, 'hr', 'definition','Sirov, nečist oblik komercijalne želatine, jantarne boje, čiji potpuni sastav nije poznat, a nastaje hidrolizom životinjskog kolagena; želatinira se u vodenim otopinama i sušenjem stvara jaki, prijanjajući sloj.', 0);
INSERT INTO property VALUES (1,3698, 'hr', 'definition','Kratkoročni cilj, svrha, dugoročni cilj ili krajnja namjena nekog poduzeća koje želi ostvariti dobit, a bavi se trgovinom, proizvodnjom ili nekom uslugom.', 0);
INSERT INTO property VALUES (1,3701, 'hr', 'definition','Igra koja se igra na otvorenom, na velikoj površini, čiji je cilj  udariti lopticu s pomoću posebnih palica, sa što manje udaraca, u svaku od najčešće 18 rupa.', 0);
INSERT INTO property VALUES (1,3705, 'hr', 'definition','Kompetentan, vješt i uspješan proces planiranja, koji vodi i djeluje u smjeru ostvarenja ili dovršenja krajnjih ciljeva, kratkoročnih ciljeva i misije neke organizacije ili institucije.', 0);
INSERT INTO property VALUES (1,3714, 'hr', 'definition','Zgrada u kojoj se nalaze uredi glavnih ministarstava (odjela) vlade.', 0);
INSERT INTO property VALUES (1,3715, 'hr', 'definition','Tijelo sastavljeno od najviših državnih dužnosnika koji su imenovani za savjetovanje predsjednika države ili predsjednika vlade neke države, a obično se sastoji od ministara ili voditelja agencija.', 0);
INSERT INTO property VALUES (1,3717, 'hr', 'definition','Dug ili druga zakonska obveza javnog tijela koja proizlazi iz  transakcija u prošlosti koje se moraju likvidirati, produžiti ili refinancirati na neki datum u budućnosti.', 0);
INSERT INTO property VALUES (1,3718, 'hr', 'definition','Svako djelovanje koje usvoji i provodi vladajuće političko tijelo ili sustav, a kojim se utvrđuju poslovni za cijelu naciju, državu ili regiju.', 0);
INSERT INTO property VALUES (1,3721, 'hr', 'definition','Jestiva, škrobna zrna iz porodice trava (Graminae) koje služe za hranu čovjeka i domaćih životinja.', 0);
INSERT INTO property VALUES (1,3729, 'hr', 'definition','Vrlo velika i raširena obitelj jednosupnica (Monocotyledoneae), s više od 10.000 vrsta, od kojih je većina zeljasta, ali manji broj njih su drvenaste. Stabljike su člankovite, s dugim, uskim listovima koji rastu iz čvorova. Cvjetovi su neupadljivi, sa znatno smanjenim cvjetnim omotačem, a oprašuje ih vjetar ili se samooprašuju. Plod je jednosjemeni, obično zrno (kariopsa, caryopsis). Trave su najvažnije od svih biljaka za hranu.', 0);
INSERT INTO property VALUES (1,3730, 'hr', 'definition','Velik požar ili uništavanje velike površine raslinja koje pripada porodici Gramineae (trave), koje se nalaze na poljima, livadama, savanama i drugim travnjacima.', 0);
INSERT INTO property VALUES (1,3732, 'hr', 'definition','Insekt biljojed s dugim stražnjim nogama koji može skočiti vrlo visoko i proizvodi oštar visok zvuk pomoću svojih stražnjih nogu ili krila.', 0);
INSERT INTO property VALUES (1,3733, 'hr', 'definition','Travnjaci pokrivaju gotovo jednu petinu kopna na Zemljinoj površini. Oni uključuju savane, prerije Sjeverne Amerike, te stepe Rusije i srednje Azije. Ekosustavi travnjaka podržavaju tisuće različitih vrsta, iznad i ispod zemlje, a imaju iznimno važnu ulogu u održavanju ekološke ravnoteže u svijetu.', 0);
INSERT INTO property VALUES (1,3734, 'hr', 'definition','Sustav bioloških zajednica koje međusobno djeluju jedne na druge, a nalaze se u biomima čije je obilježje prevladavanje autohtonih trava, travnatih biljaka i korova, te njihova neživog okruženja.', 0);
INSERT INTO property VALUES (1,3738, 'hr', 'definition','Mješavina sitnog kamenja i oblutaka koji su krupniji od pijeska.', 0);
INSERT INTO property VALUES (1,3739, 'hr', 'definition','Dobivanje mješavine krupnijeg pijeska i sitnog šljunka kojeg je oblikovala voda ili tucanog kamena (upotrebljava se za staze i ceste i kao agregat) iz zemlje.', 0);
INSERT INTO property VALUES (1,3742, 'hr', 'definition','Mjesto gdje se šljunak iskapa iz zemlje.', 0);
INSERT INTO property VALUES (1,3748, 'hr', 'definition','Raslinje na pašnjacima na kojima pase stoka radi ishrane.', 0);
INSERT INTO property VALUES (1,3754, 'hr', 'definition','1) Površina zemljišta, ne nužno neprekidna, u blizini, a ponekad i oko velikih izgrađenih područja. Otvorenost područja zadržava se trajnim i strogim ograničenjem izgradnje.
INSERT INTO property VALUES (1,3755, 'hr', 'definition','Samodostatna nastamba, gdje čovjek simulira razne ekosustave, odnosno njegov otpad se pretvara u gorivo anaerobnom digestijom za proizvodnju metana, a ostaci iz probave se upotrebljavaju za uzgoj hrane. Ostaci hrane se kompostiraju i/ili se upotrebljavaju za proizvodnju metana. Solarna energija se zadržava pomoću efekta staklenika i upotrebljava za grijanje kuće, usjeva i vode. Struja bi se proizvodila pomoću vjetrenjače. Dakle, s obzirom na dovoljno prostora, sunca, kiše i vjetra, zelena kuća je, u teoriji, samodostatan sustav koji reciklira vlastiti otpad i koristi se suncem kao izvorom energije.', 0);
INSERT INTO property VALUES (1,3756, 'hr', 'definition','Koridori duž kojih se životinje koje žive u divljini mogu kretati, biljke se mogu razmnožavati, može se razmjenjivati genetske materijal, populacije se mogu kretati kao reakcija na promjene u okolišu i prirodne katastrofe, a ugrožene vrste mogu se nadopuniti životinjama iz drugih područja.', 0);
INSERT INTO property VALUES (1,3757, 'hr', 'definition','Iznos novca koji je potrebno platiti državi za financiranje čišćenja, sprječavanja, smanjenja, prisilne provedbe ili u svrhu obrazovanja i izobrazbe radi promicanja ekološke cjelovitosti i očuvanja prirodnih resursa.', 0);
INSERT INTO property VALUES (1,3760, 'hr', 'definition','Uzgoj biljaka, osobito vansezonskih biljaka, u staklenim konstrukcijama s kontroliranim klimatskim uvjetima.', 0);
INSERT INTO property VALUES (1,3761, 'hr', 'definition','Zagrijavanje Zemljine atmosfere izazvano povećanom koncentracijom atmosferskih plinova, kao što je vodena para i ugljični dioksid. Ti plinovi apsorbiraju zračenje koje emitira Zemlja, i time usporavaju gubitak energije zračenja sa Zemlje natrag u svemir.', 0);
INSERT INTO property VALUES (1,3763, 'hr', 'definition','Zbirni izraz za komponente atmosfere koje utječu na efekt staklenik, naime, ugljični dioksid, metan, didušikovi oksidi, ozon, CFC-i i vodena para.', 0);
INSERT INTO property VALUES (1,3766, 'hr', 'definition','Biljni travoliki materijal koji se zaore u tlo još dok je zelen.', 0);
INSERT INTO property VALUES (1,3768, 'hr', 'definition','Ime dano široko rasprostranjenom razvoju sojeva pšenica, kukuruza i riže s velikim prinosom tijekom 60-ih i ranih 70-ih godina 20. stoljeća. Ovaj razvoj formalnije je bio poznat pod nazivom Indikativni svjetski plan za razvoj poljoprivrede. Revolucija je došla nakon što je Organizacija za hranu i poljoprivredu održala Svjetski kongres hrane 1963. god. Pokrenuta je kampanja "Sloboda od gladi" s ciljem povećanja zaliha hrane i rješavanja problema gladi u cijelom svijetu.', 0);
INSERT INTO property VALUES (1,3770, 'hr', 'definition','Parcela zemljišta na kojem rastu biljke koja odvaja ili okružuje područja intenzivne stambene ili industrijske izgradnje, namijenjena rekreaciji ili funkciji parka.', 0);
INSERT INTO property VALUES (1,3771, 'hr', 'definition','Povrće čiji jestivi dijelovi su bogati klorofilom i važan su izvor  vitamina i mikronutrijenata.', 0);
INSERT INTO property VALUES (1,3774, 'hr', 'definition','Smrviti u prah ili manje dijelove.', 0);
INSERT INTO property VALUES (1,3777, 'hr', 'definition','Bruto nacionalni proizvod prilagođen za strane transakcije, tj. brojci bruto domaćeg proizvoda mora se  dodati svaki dohodak ostvaren za stanovnike zemlje koji je rezultat ulaganja i drugih faktorskih dohodaka u inozemstvu, a od nje se mora oduzeti svaki dohodak ostvaren na domaćem tržištu faktorima u vlasništvu stranaca u inozemstvu.', 0);
INSERT INTO property VALUES (1,3780, 'hr', 'definition','Voda koja ulazi u pore i pukotine u stijenama i tlu, ispod površine i iznad sloja nepropusnog materijala. Ona se može slobodno kretati gravitacijski, bilo dolje prema nepropusnom sloju ili slijedeći nagnutu površinu.', 0);
INSERT INTO property VALUES (1,3781, 'hr', 'definition','Proces, namjeran ili nehotičan, vađenja podzemne vode iz izvora brzinom koja je toliko veća od nadopunjavanja da razina podzemnih voda stalno opada, te prijeti da će se zalihe iscrpiti ili barem da će se razine podzemnih voda koje je moguće crpiti smanjiti na neekonomične dubine.', 0);
INSERT INTO property VALUES (1,3784, 'hr', 'definition','Onečišćenje bilo koje vode koja se nalazi ispod Zemljine površine procjeđivanjem bilo kojih onečišćujućih tvari, kao što su anorganski spojevi (kloridi, nitrati, teški metali itd.), sintetičke organske kemikalije (pesticidi, umjetna gnojiva itd.) i patogeni (bakterije, paraziti itd.).', 0);
INSERT INTO property VALUES (1,3785, 'hr', 'definition','Preventivno djelovanje, postupci ili mjere/uređaji za sprječavanje ili smanjenje štete za ekološku cjelovitost slatke vode koja se nalazi ispod Zemljine površine obično u vodonosnicima, koji opskrbljuju vodom bunare i izvore.', 0);
INSERT INTO property VALUES (1,3788, 'hr', 'definition','Uočljiv obrazac djelovanja prisutan kod osoba koje pripadaju nekoj skupini i takve skupine u cjelini.', 0);
INSERT INTO property VALUES (1,3800, 'hr', 'definition','Velika površina mora usječena u kopno, razvedenija od malog zaljeva ili zatona i općenito više zatvorena prema otvorenom moru.', 0);
INSERT INTO property VALUES (1,3805, 'hr', 'definition','Sjemenjače iz odjeljka Gymnospermae (golosjemenjače), kod kojih se sjemeni zameci nalaze goli na površini velikih sporofila, koji se često nalaze u češerima.', 0);
INSERT INTO property VALUES (1,3806, 'hr', 'definition','Bezbojni ili bijeli mineral koji se upotrebljava u građevinarstvu i u proizvodnji cementa, gume, papira i gipsa.', 0);
INSERT INTO property VALUES (1,3808, 'hr', 'definition','1) Mjesto na kojem biljka ili životinja raste ili živi u prirodnom okolišu. To može biti ili zemljopisno područje u kojem se proteže, ili osobito mjesto u kojoj se primjerak takvih organizama nalazi.
INSERT INTO property VALUES (1,3810, 'hr', 'definition','Grana medicinske znanosti koja se bavi bolestima krvi.', 0);
INSERT INTO property VALUES (1,3812, 'hr', 'definition','Padaline u obliku kuglica ili nepravilnih grudica leda, koje uvijek nastaju u konvektivnim oblacima, a gotovo uvijek u kumulonimbusima.', 0);
INSERT INTO property VALUES (1,3815, 'hr', 'definition','Vrijeme potrebno za radioaktivni raspad jedne polovine atoma dane količine radioaktivnog materijala.', 0);
INSERT INTO property VALUES (1,3817, 'hr', 'definition','Haloalkan, koji sadrži tri halogena atoma, na primjer jodoform, CHI3; haloformna reakcija je reakcija kojom nastaju haloformi iz ketona. Na primjer, ako se propanon tretira  bjelilom, nastane klorirani keton, koji reagira tako da nastane kloroform.', 0);
INSERT INTO property VALUES (1,3819, 'hr', 'definition','Halogeni derivati bifenila.', 0);
INSERT INTO property VALUES (1,3820, 'hr', 'definition','Svaki iz skupine halogenih derivata organskog vodika i spojeva koji sadrže ugljik; skupina uključuje monohalogene spojeve (alkil- ili aril-halide) i polihalogene spojeve koji sadrže iste ili različite halogene atome.', 0);
INSERT INTO property VALUES (1,3821, 'hr', 'definition','Halogeni derivati fenola.', 0);
INSERT INTO property VALUES (1,3822, 'hr', 'definition','Organski spoj vezan s jednim od pet halogenih elemenata (astatom, bromom, klorom, fluorom ili jodom). Nekoliko od tih spojeva utječe na sniženja koncentracije stratosferskog ozona.', 0);
INSERT INTO property VALUES (1,3828, 'hr', 'definition','Profesija, komercijalna tvrtka ili trgovina koja uključuje  proizvodnju i distribuciju ručno izrađenih predmeta.', 0);
INSERT INTO property VALUES (1,3830, 'hr', 'definition','Posebna vještina koja se obavlja rukama.', 0);
INSERT INTO property VALUES (1,3834, 'hr', 'definition','Vodena površina u blizini obale, često okružena debelim zidovima, gdje brodovi i čamci mogu naći zaklon.', 0);
INSERT INTO property VALUES (1,3837, 'hr', 'definition','Otpornost krute tvari na udarce, grebenje, abraziju ili rezanje.', 0);
INSERT INTO property VALUES (1,3838, 'hr', 'definition','Odbačeni materijal, često opasan ili velike zapremine, za koji ne postoji uobičajen način zbrinjavanja.', 0);
INSERT INTO property VALUES (1,3842, 'hr', 'definition','Pogoršanje kakvoće nekog medija zbog unošenja  upotrijebljene odnosno otpadne vode iz zajednice ili industrije.', 0);
INSERT INTO property VALUES (1,3847, 'hr', 'definition','Proces kojim dvije ili više država, ponekad pod pokroviteljstvom međudržavne ili međunarodne  organizacije, mijenjaju svoje zakonodavstvo koje se odnosi na neko područje od zajedničkog interesa kako bi uskladili svoje zakone i olakšali sukladnost i provođenje propisa preko granica.', 0);
INSERT INTO property VALUES (1,3849, 'hr', 'definition','Količina ili mjera usjeva prikupljenih u jednoj sezoni.', 0);
INSERT INTO property VALUES (1,3852, 'hr', 'definition','Fizičko ili kemijsko sredstvo koje može naškoditi ili izazvati štetu za ljude, imovinu, životinje, biljke ili druge prirodne resurse.', 0);
INSERT INTO property VALUES (1,3853, 'hr', 'definition','Rizik ili opasnost za ljudsko zdravlje, imovinu ili okoliš zbog unošenja opasnih tvari u ekološki sustav.', 0);
INSERT INTO property VALUES (1,3854, 'hr', 'definition','Svaki materijal koji predstavlja prijetnju za zdravlje ljudi i/ili okoliš. Uobičajene opasne tvari su toksične, korozivne, zapaljive, eksplozivne ili kemijski reaktivne.', 0);
INSERT INTO property VALUES (1,3855, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propisuje država u svrhu uređivanja prozvodnje, upotrebe ili čišćenja materijala odnosno tvari koje predstavljaju prijetnju za ljudsko zdravlje i okoliš, a osobito tvari koje su toksične, korozivne, zapaljive, eksplozivne ili kemijski reaktivne.', 0);
INSERT INTO property VALUES (1,3857, 'hr', 'definition','Zemljište ili objekt za odlaganje opasnog otpada, na zemlju ili u zemlju. Propisno projektirana i vođena odlagališta imaju podlogu kojom se sprječava propuštanje i sadrže sustave za prikupljanje potencijalno onečišćene površinske vode koja otječe.', 0);
INSERT INTO property VALUES (1,3858, 'hr', 'definition','Svaki otpad ili kombinacija raznih vrsta otpada koji bi mogli biti štetni za zdravlje ljudi, žive organizme ili okoliš. Za opasni otpad obično su potrebni posebni postupci rukovanja i odlaganja koji su uređeni nacionalnim i međunarodnim zakonima.', 0);
INSERT INTO property VALUES (1,3859, 'hr', 'definition','Otrov, korozivno sredstvo, zapaljiva tvar, eksploziv, radioaktivna kemikalija ili bilo koji drugi materijal odnosno tvar koja može ugroziti zdravlje ili dobrobit ljudi ako se njima na rukuje na nepropisan način.', 0);
INSERT INTO property VALUES (1,3860, 'hr', 'definition','Smanjena vidljivost u atmosferi kao rezultat kondenzirane vodene pare, prašine itd.', 0);
INSERT INTO property VALUES (1,3864, 'hr', 'definition','Dio zemljišta na kraju njive namijenjen okretanju pluga.', 0);
INSERT INTO property VALUES (1,3865, 'hr', 'definition','Stanje dinamičke ravnoteže između organizma i njegovog okoliša u kojem su sve tjelesne i duševne funkcije normalne.', 0);
INSERT INTO property VALUES (1,3869, 'hr', 'definition','Odnos između kakvoće okoliša i zdravstvenih uvjeta pojedinaca.', 0);
INSERT INTO property VALUES (1,3871, 'hr', 'definition','Objekt ili mjesto gdje ljudi ili životinje mogu dobiti pomoć liječnika, stomatologa, kirurga ili medicinske sestre.', 0);
INSERT INTO property VALUES (1,3874, 'hr', 'definition','Zakoni, pravilnici ili kodeksi kojima se propisuju sanitarne norme, norme o čistom zraku, te propisi kojima se promiče i čuva zdravlje zajednice i radni uvjeti u poduzećima.', 0);
INSERT INTO property VALUES (1,3878, 'hr', 'definition','Skup pravila ili uredba vlade ili uprave u svrhu promicanja ili zaštite zdravlja ljudskog tijela i psihe na radnom mjestu, kod kuće ili u bilo kojem drugom okruženju.', 0);
INSERT INTO property VALUES (1,3880, 'hr', 'definition','Biotehnologije povezane sa zdravljem bave se farmaceutskim proizvodima koji sadrže velike molekule proteina, genetskim inženjeringom itd.', 0);
INSERT INTO property VALUES (1,3881, 'hr', 'definition','Osiguravanje zdravstvene skrbi za javnost.', 0);
INSERT INTO property VALUES (1,3884, 'hr', 'definition','Smanjenje snage ili abnormalnost ili djelomičan ili potpuni gubitak sluha ili funkcije uha ili slušnog sustava, izravno ili sekundarno prouzročeno patologijom ili ozljedom; može biti privremeno ili trajno.', 0);
INSERT INTO property VALUES (1,3885, 'hr', 'definition','Svaki propisani postupak ili način djelovanja kojim se uređuje preliminarno ispitivanje od strane suca o temeljnim dokazima i optužbama kojima se određuje jesu li kazneni postupak, suđenje ili druge pravosudne radnje opravdane.', 0);
INSERT INTO property VALUES (1,3886, 'hr', 'definition','Sve mjere i naprave koje služe da se ljudima pomogne očuvati sposobnost sluha.', 0);
INSERT INTO property VALUES (1,3887, 'hr', 'definition','Opće opažanje zvučnih podražaja i posebne reakcije na njega.', 0);
INSERT INTO property VALUES (1,3890, 'hr', 'definition','Oblik energije koji se prenosi zbog razlike u temperaturi. Ta energija jednaka je ukupnoj količini kinetičke energije atoma ili molekula nekog sustava.', 0);
INSERT INTO property VALUES (1,3891, 'hr', 'definition','Elektroenergana koja proizvodi i električnu struju i toplu vodu za lokalno stanovništvo. Kogeneracijsko postrojenje (CHP) može biti na pogon bilo kojim gorivom, uključujući otpatke.', 0);
INSERT INTO property VALUES (1,3892, 'hr', 'definition','Uređak koji zagrijava ili daje toplinu.', 0);
INSERT INTO property VALUES (1,3893, 'hr', 'definition','Područje sa siromašnim, kiselim tlom, na kojem prevladava vrijes/vrišt (Calluna) ili vrijesci (Erica).', 0);
INSERT INTO property VALUES (1,3894, 'hr', 'definition','Sustav opskrbe grijanjem za zgrade, osobito centralno grijanje.', 0);
INSERT INTO property VALUES (1,3900, 'hr', 'definition','Postrojenje za proizvodnju i opskrbu toplinom.', 0);
INSERT INTO property VALUES (1,3904, 'hr', 'definition','Naprava koja dovođenjem mehaničke energije prenosi toplinu iz hladnijeg spremnika u topliji, osobito kada je glavna svrha grijati topli spremnik, a ne hladiti hladni.', 0);
INSERT INTO property VALUES (1,3906, 'hr', 'definition','Čuvanje topline stvorene tijekom razdoblja male potrošnje do razdoblja najveće potrošnje, kada je ona potrebna.', 0);
INSERT INTO property VALUES (1,3907, 'hr', 'definition','Opskrba gorivom za grijanje, ugljena ili drugih sredstava za grijanje, ili količina kapaciteta grijanja, za upotrebu od strane općina/grad ili drugi korisnik grijanja.', 0);
INSERT INTO property VALUES (1,3911, 'hr', 'definition','Promet velikim motornim vozilima konstruiranim za prijevoz teških tereta.', 0);
INSERT INTO property VALUES (1,3914, 'hr', 'definition','Količina pritiska na ekološki sustav zbog onečišćenja teškim metalima koji se u njega ispuštaju.', 0);
INSERT INTO property VALUES (1,3915, 'hr', 'definition','Metal čija je specifična težina približno 5,0 ili više.', 0);
INSERT INTO property VALUES (1,3917, 'hr', 'definition','Red gusto zasađenog grmlja ili grmova, koji obilježava granice polja. Vrsta živice varira između dijelova zemlje, a njihova starost se utvrđuje na temelju broja vrsta postojećeg drveća i grmlja. Tijekom posljednjih trideset godina uklanjanje živica ima značajan vizualni utjecaj na nizinske poljoprivredne krajobraze. Sa stajališta poljoprivrednika, u područjima gdje prevladava obradivo tlo ili intenzivna ispaša, ima malo ekonomske opravdanosti za zadržavanje živice, ili je uopće nema.', 0);
INSERT INTO property VALUES (1,3933, 'hr', 'definition','Kemikalija kojom se kontrolira ili uništava neželjeno raslinje.', 0);
INSERT INTO property VALUES (1,3935, 'hr', 'definition','Životinja koja se hrani travom i drugim biljkama.', 0);
INSERT INTO property VALUES (1,3937, 'hr', 'definition','Spoj u kojem je prstenasta struktura sastavljena od više od jedne vrste atoma.', 0);
INSERT INTO property VALUES (1,3945, 'hr', 'definition','Izobrazba nakon srednje škole u ustanovi koja nudi programe čijim završavanjem se stječe dodiplomski i diplomski stupanj obrazovanja.', 0);
INSERT INTO property VALUES (1,3947, 'hr', 'definition','Sustavi bioloških zajednica i njihove nežive okoline koji međusobno djeluju jedni na druge u područjima relativno velikih nadmorskih visina, koja se obično odlikuju smanjenim tlakom zraka i temperaturom, smanjenom dostupnošću kisika i povećanom izolacijom.', 0);
INSERT INTO property VALUES (1,3954, 'hr', 'definition','Visoka konstrukcija ili građevina s mnogo katova, opremljena dizalima.', 0);
INSERT INTO property VALUES (1,3955, 'hr', 'definition','Izraz "brzi promet" obuhvaća sve vlakove koji s ekreću brzinama većim od 200 km/h, ali i vlakove koji se kreću brzinom od 200 km/h ako se veće brzine ne mogu opravdati terenom, gustoćom stanovništva ili ekonomskim razlozima.', 0);
INSERT INTO property VALUES (1,3956, 'hr', 'definition','Vlak koji se na posebnim željezničkim prugama kreće brzinom od oko 300 km/h.', 0);
INSERT INTO property VALUES (1,3958, 'hr', 'definition','Razina vode kada plima doseže najvišu točku.', 0);
INSERT INTO property VALUES (1,3960, 'hr', 'definition','Električni vod napona od više tisuća volti.', 0);
INSERT INTO property VALUES (1,3963, 'hr', 'definition','Javna cesta, osobito važna cesta koja povezuje gradove.', 0);
INSERT INTO property VALUES (1,3966, 'hr', 'definition','Prirodna uzvisina na zemaljskoj površini, koja se visinom vidljivo uzdiže nad okolnim krajem, obično ograničenog opsega i s tipičnim zaobljenim obrisom, bez izrazitih vrhova ili nepravilnog oblika, bez posebno definirane apsolutne nadmorske visine.', 0);
INSERT INTO property VALUES (1,3971, 'hr', 'definition','Proces kojim se događaju male ali kumulativne promjene u naučenom, ne-nasumičnom, sustavnom ponašanju, od naraštaja do naraštaja.', 0);
INSERT INTO property VALUES (1,3974, 'hr', 'definition','Spomenik izgrađen kao obilježje u spomen nekog povijesnog događaja.', 0);
INSERT INTO property VALUES (1,3975, 'hr', 'definition','Proučavanje događaja u odnosu prema njihovom  razvoju tijekom vremena.', 0);
INSERT INTO property VALUES (1,3977, 'hr', 'definition','Mjesto na kojem su se dogodili značajni povijesni događaji i koje je važno za autohtonu kulturu ili zajednicu.', 0);
INSERT INTO property VALUES (1,3983, 'hr', 'definition','Sustavno napisan prikaz koji obuhvaća kronološku evidenciju događaja (koji su utjecali na neki grad, državu, narod, instituciju, znanost ili umjetnost) i koji obično uključuje filozofsko objašnjenje uzroka i porijekla takvih događaja.', 0);
INSERT INTO property VALUES (1,3984, 'hr', 'definition','Mjesto na kojem su osigurani smještajni, rekreacijski i drugi  objekti za odmor.', 0);
INSERT INTO property VALUES (1,3986, 'hr', 'definition','1) Razdoblje u kojem se uzima slobodno vrijeme od rada ili studija za odmor, putovanje ili rekreaciju
INSERT INTO property VALUES (1,3995, 'hr', 'definition','Velika četveronožna životinja koju ljudi jašu ili se njome koriste za nošenje stvari ili vuču vozila.', 0);
INSERT INTO property VALUES (1,3998, 'hr', 'definition','Umijeće i i znanost o uzgajanju bilja.', 0);
INSERT INTO property VALUES (1,3999, 'hr', 'definition','Mjesto na kojem liječnici i medicinske sestre liječe i skrbe o osobama koje su bolesne ili ozlijeđene.', 0);
INSERT INTO property VALUES (1,4002, 'hr', 'definition','Biološki i nebiološki kruti otpaci koji nastaje u bolnicama i nisu namijenjeni za buduću upotrebu.', 0);
INSERT INTO property VALUES (1,4008, 'hr', 'definition','Djelatnost povezana s osiguravanjem smještaja i obično i obroka i ostalih usluga za putnike i ostale goste koji plaćaju.', 0);
INSERT INTO property VALUES (1,4015, 'hr', 'definition','Skupina osoba koje žive u istom domu ili prostoru za življenje, koji zajednički dijele svoj dohodak, što se vidi po tome što redovno jedu zajedno.', 0);
INSERT INTO property VALUES (1,4018, 'hr', 'definition','Predmeti potrebni za život u kućanstvu.', 0);
INSERT INTO property VALUES (1,4022, 'hr', 'definition','1) Zajedničko ime za kuće i stanove za stanovanje i njihovo pribavljanje.
INSERT INTO property VALUES (1,4027, 'hr', 'definition','Broj stambenih jedinica ili stanovništva koje živi u određenom geografskom prostoru.', 0);
INSERT INTO property VALUES (1,4030, 'hr', 'definition','Dogradnja, obnova ili popravak stana ili kuće koje povećava njezinu estetsku, funkcionalnu ili financijsku vrijednost.', 0);
INSERT INTO property VALUES (1,4032, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propiše država radi uređivanja kupovanja, prodaje, iznajmljivanja, izgradnje ili održavanja kuća ili stanova.', 0);
INSERT INTO property VALUES (1,4037, 'hr', 'definition','Planirani sustav projekata, usluga ili aktivnosti čija je svrha dati potporu pojedincima ili obiteljima u potrebi za skloništem, uključujući privremeni smještaj ili trajne stambene jedinice i sigurna mjesta za osobe s malim primanjima, starije osobe i beskućnike.', 0);
INSERT INTO property VALUES (1,4039, 'hr', 'definition','Norma ili mjerilo primjenljivo u pravnim slučajevima za koju se smatra da odražava relativno visok stupanj ili razinu izvrsnosti u gradnji, održavanju, upravljanju, stanovanju, korištenju ili izgledu stambenih jedinica.', 0);
INSERT INTO property VALUES (1,4040, 'hr', 'definition','Proces popravljanja ili obnove građevine kako bi se ona vratila u prvobitno stanje.', 0);
INSERT INTO property VALUES (1,4045, 'hr', 'definition','Proučavanje življenja i značaja čovjeka.', 0);
INSERT INTO property VALUES (1,4046, 'hr', 'definition','Nagli prekid, prestanak ili poremećaj u funkcijama ljudskog tijela, njegovim sustavima ili organima kao posljedica genetskih ili razvojnih grešaka, infekcije, nepravilne prehrane, toksičnosti, bolesti ili nepovoljnih ekoloških čimbenika.', 0);
INSERT INTO property VALUES (1,4047, 'hr', 'definition','Proučavanje rasta, raspodjele i organizacije zajednica ljudi u odnosu prema njihovim međusobnim vezama s drugim ljudima i ostalim vrstama te s njihovim okolišem.', 0);
INSERT INTO property VALUES (1,4051, 'hr', 'definition','Izbjegavanje bolesti i ozljeda i promicanje normalnog stanja  učinkovitom upotrebom okoliša, društva koje uredno funkcionira, te unutarnjeg osjećanja blagostanja.', 0);
INSERT INTO property VALUES (1,4052, 'hr', 'definition','Silovita, iznenadna i destruktivna promjena u okolišu koju je prouzročio čovjek.', 0);
INSERT INTO property VALUES (1,4054, 'hr', 'definition','Grana medicine koja se bavi uzrokom, porijeklom i prirodom bolesti, uključujući promjene koje se pojave kao rezultat bolesti.', 0);
INSERT INTO property VALUES (1,4055, 'hr', 'definition','Grana bioloških znanosti koja proučava djelovanje ljudskih organa i tkiva.', 0);
INSERT INTO property VALUES (1,4056, 'hr', 'definition','Skupina pojedinaca sa zajedničkim značajkama.', 0);
INSERT INTO property VALUES (1,4060, 'hr', 'definition','Prava pojedinaca na slobodu, pravdu itd.', 0);
INSERT INTO property VALUES (1,4062, 'hr', 'definition','Gradovi, mjesta, sela i druge koncentracije stanovništva koje nastanjuje određeni segment ili područje okoliša. Ljudska naselja povezana su s brojnim i složenim problemima prisutnima pri planiranju i upravljanju, a povezani su s  okolišem, onečišćenjem te životnim uvjetima.', 0);
INSERT INTO property VALUES (1,4070, 'hr', 'definition','Više ili manje razgrađena organska tvar u tlu. Osim što je izvor većine mineralnih soli koje su potrebne biljkama, humus poboljšava teksturu tla i zadržava vodu, čime se smanjuju gubici hranjivih tvari procjeđivanjem.', 0);
INSERT INTO property VALUES (1,4072, 'hr', 'definition','Gonjenje i ubijanje ili hvatanje divljih životinja, koje se smatra sportom.', 0);
INSERT INTO property VALUES (1,4073, 'hr', 'definition','Područje gdje je dopušteno gonjenje i ubijanje ili hvatanje divljači i divljih životinja.', 0);
INSERT INTO property VALUES (1,4077, 'hr', 'definition','Tropski ciklon velikog intenziteta; za svaki vjetar koji dosegne brzinu veću od 73 milja na sat (117 kilometara na sat) kaže se da ima orkansku snagu.', 0);
INSERT INTO property VALUES (1,4080, 'hr', 'definition','Radnja ili postupak proizvodnje hibrida, tj. životinje ili biljke koja je rezultat križanja između genetski različitih jedinki. Hibridi između različitih vrsta obično su sterilni.', 0);
INSERT INTO property VALUES (1,4082, 'hr', 'definition','Svaka građevina izgrađena da se usmjeri tok vode, ili da se podupre težina i pritisak vodnog tijela.', 0);
INSERT INTO property VALUES (1,4084, 'hr', 'definition','Grana građevinarstva koja se bavi projektiranjem, montažom i izgradnjom postrojenja za zbrinjavanje otpadnih voda, vodnih građevina, brana, hidroelektrana itd.', 0);
INSERT INTO property VALUES (1,4085, 'hr', 'definition','Grana znanosti i tehnologije koja se bavi mehanikom fluida, posebno tekućina.', 0);
INSERT INTO property VALUES (1,4088, 'hr', 'definition','Proučavanje organizama koji žive u vodi.', 0);
INSERT INTO property VALUES (1,4091, 'hr', 'definition','Vrlo velika skupina kemijskih spojeva sastavljenih samo od ugljika i vodika.', 0);
INSERT INTO property VALUES (1,4094, 'hr', 'definition','Otopina plina klorovodika u vodi; otrovna, jetka tekućina koja tvori smjesu sa stalnim vrelištem pri 20% koncentracije u vodi; često se upotrebljava kao reagens, u organskoj sintezi, pri kiselinskoj obradi naftnih bušotina, redukciji ruda, u preradi hrane te čišćenju i poliranju metala. Također poznata pod nazivom solna kiselina.', 0);
INSERT INTO property VALUES (1,4095, 'hr', 'definition','Uzgoj biljaka bez upotrebe tla. Umjesto tla se upotrebljava pijesak ili vermikulit ili drugi zrnati materijal, te se upotrebljava tekuća otopina hranjivih tvari za prihranu.', 0);
INSERT INTO property VALUES (1,4098, 'hr', 'definition','Elektrana s pogonom na besplatan obnovljivi izvor energije - vodu koja pada.', 0);
INSERT INTO property VALUES (1,4103, 'hr', 'definition','Zapaljivi bezbojni plin koji je najlakši i najobilniji element u svemiru. Pojavljuje se uglavnom u vodi i u većini organskih spojeva i upotrebljava se u proizvodnji amonijaka i drugih kemikalija, u hidrogenaciji masti i ulja, te pri zavarivanju.', 0);
INSERT INTO property VALUES (1,4107, 'hr', 'definition','Zapaljiv, otrovan plin s karakterističnim mirisom trulih jaja, koji se može osjetiti u zraku u razrjeđenju od 0,002 mg / l. Upotrebljava se kao reagens u kemijskoj analizi; iznimno je opasan; u roku od nekoliko sekundi nakon jednog ili dva udisaja može izazvati kolaps, komu i smrt zbog zatajenja respiratornog sustava; niske koncentracije stvaraju iritaciju konjunktive i sluznica. Nakon izlaganja sumporovodiku može se pojaviti glavobolja, vrtoglavica, mučnina i umor.', 0);
INSERT INTO property VALUES (1,4108, 'hr', 'definition','Znanost koja se bavi pojavom površinskih i podzemnih voda, njihovim korištenjem i funkcijama i mijenjanju Zemlje, ponajprije erozijom i nanosima.', 0);
INSERT INTO property VALUES (1,4109, 'hr', 'definition','Znanost koja se bavi mjerenjem i opisom fizičkih svojstava oceana, jezera, rijeka i ostalih susjednih priobalnih područja, a osobito njihovim nadzorom i korištenjem.', 0);
INSERT INTO property VALUES (1,4111, 'hr', 'definition','Silovita, iznenadna i destruktivna promjena bilo u kakvoći vode na Zemlji ili u raspodjeli ili kretanju vode na zemlji, ispod površine ili u atmosferi.', 0);
INSERT INTO property VALUES (1,4115, 'hr', 'definition','Bilanca priljeva u hidrološku jedinicu, odljeva iz nje i pohrane u hidrološkoj jedinici kao što je sliv, vodonosnik, zona tla, jezero ili spremnik; odnos između ishlapljivanja, oborina, otjecanja i promjene u vodi za pohranu.', 0);
INSERT INTO property VALUES (1,4116, 'hr', 'definition','Gibanje vode između oceana, Zemljine površine i atmosfere ishlapljivanjem, putem oborina i djelovanja živih organizama, kao jedan od glavnih biogeokemijskih ciklusa. Svaki dan voda isparava iz oceana i odlazi u zrak s mora iznad zemlje, koja je prima u obliku oborina, a na kraju se vraća s kopna na more putem rijeka, i time ciklus završava.', 0);
INSERT INTO property VALUES (1,4118, 'hr', 'definition','Znanost koja se bavi proučavanjem pojavnosti, kruženja, raspodjele i svojstava voda na Zemlji, kao i njihove reakcije s okolišem.', 0);
INSERT INTO property VALUES (1,4119, 'hr', 'definition','1) Razlaganje ili promjena kemijskih tvari vodom.
INSERT INTO property VALUES (1,4120, 'hr', 'definition','Dio meteorologije koja se neposredno bavi hidrološkim  problemima, osobito kontrolom poplava, hidroenergijom, navodnjavanjem i sličnim područjima tehnike i vodnog bogatstva.', 0);
INSERT INTO property VALUES (1,4122, 'hr', 'definition','Energija koja se dobiva iz prirodnih ili umjetnih slapova, bilo izravno, okretanjem vodeničnog kola ili turbine, ili neizravno, stvaranjem električne energije u dinamu koji pokreće  turbina.', 0);
INSERT INTO property VALUES (1,4124, 'hr', 'definition','Vode na Zemlji, za razliku od stijena (litosfere), živih bića (biosfere) i zraka (atmosfera). Uključuje vode u oceanima, rijekama, jezerima i drugim tijelima površinskih voda u tekućem obliku na kontinentima; snijeg, led i ledenjake; kao i tekuću vodu, led i vodenu paru, i u nezasićenim i u zasićenim zonama ispod zemaljske površine. Neki u hidrosferu uključuju, ali neki isključuju, vodu u atmosferi, koja obuhvaća vodenu paru, oblake te sve oblike oborina dok su još u atmosferi.', 0);
INSERT INTO property VALUES (1,4126, 'hr', 'definition','Znanost koja se bavi načelima i načinima očuvanja zdravlja.', 0);
INSERT INTO property VALUES (1,4127, 'hr', 'definition','Insekti koji obuhvaćaju pčele, ose, mrave i širokozačane insekte, a imaju dva para opnastih krila i leglicu specijaliziranu za ubadanje, urezivanje i probijanje.', 0);
INSERT INTO property VALUES (1,4131, 'hr', 'definition','Gusta tvar koja nastaje zamrzavanjem vode u kruto agregatno stanje; često se pojavljuje u obliku šesterokutnih kristala.', 0);
INSERT INTO property VALUES (1,4132, 'hr', 'definition','Velika masa leda koja se odvojila od kopna i pluta u moru ili se nasukala u plitkoj vodi.', 0);
INSERT INTO property VALUES (1,4133, 'hr', 'definition','Velika područja leda koji pluta, a obično se pojavljuje u polarnim morima i sastoji se od odvojenih dijelova koji su se spojili u veliku masu.', 0);
INSERT INTO property VALUES (1,4136, 'hr', 'definition','Utvrđivanje posebne tvari ili posebnih tvari koje su uzrok onečišćenja.', 0);
INSERT INTO property VALUES (1,4137, 'hr', 'definition','Skup ideja koji odražava uvjerenja i interese neke nacije, političkog sustava itd. i na njemu se zasniva političko djelovanje.', 0);
INSERT INTO property VALUES (1,4140, 'hr', 'definition','Proces pretvaranja ''sirovih'' podataka prikupljenih ''na daljinu'' u iskoristiv oblik primjenom različitih preobrazbi kao što su kontrolirane i nekontrolirane sheme razvrstavanja.', 0);
INSERT INTO property VALUES (1,4141, 'hr', 'definition','Kodeksi što ih je izdala Međuvladina pomorska savjetodavna organizacija (IMCO) u vezi s međunarodnim brodarstvom, osobito u vezi sa sigurnošću i onečišćenjem mora.', 0);
INSERT INTO property VALUES (1,4143, 'hr', 'definition','Jedna od mogućih posljedica uvođenja životinjskih vrsta u neko područje gdje nisu autohtone. Takvo preseljenje vrsta uvijek uključuje element rizika, pa čak i ozbiljnih opasnosti. Novonastale vrste mogu biti vrlo konkurentne ili na drugi način negativno utjecati na autohtone vrste i zajednice.', 0);
INSERT INTO property VALUES (1,4144, 'hr', 'definition','Jedna od mogućih posljedica uvođenja biljnih vrsta u neko područje gdje nisu autohtone.', 0);
INSERT INTO property VALUES (1,4146, 'hr', 'definition','Primanje tvari kao što su onečišćujuće tvari u okoliš iz bilo kojeg izvora.', 0);
INSERT INTO property VALUES (1,4147, 'hr', 'definition','Zakonski i upravni postupci namijenjeni smanjenju štete izazvane emisijama. Programi smnanjenja onečišćenja obično se zasnivaju na čovjeku prihvatljiva ograničenja doza. Vrlo važna mjera odnosi se organizaciju popisa emisija.', 0);
INSERT INTO property VALUES (1,4149, 'hr', 'definition','Šteta izazvana onečišćenjem iz određenog izvora emisije.', 0);
INSERT INTO property VALUES (1,4150, 'hr', 'definition','Predviđanje imisija izračunava se na temelju opterećenja nekom onečišćujućom tvari, visine izvora, brzine vjetra i koeficijenta raspršenja.', 0);
INSERT INTO property VALUES (1,4151, 'hr', 'definition','Najveće količine određenih onečišćujućih tvari koja bi  prouzročile neprihvatljivu kakvoću zraka.', 0);
INSERT INTO property VALUES (1,4152, 'hr', 'definition','Ukupna količina imisija unesenih u neki okoliš.', 0);
INSERT INTO property VALUES (1,4154, 'hr', 'definition','Tjelesni sustav koji pomaže organizmu da se odupre bolestima, zahvaljujući djelovanju specijaliziranih krvnih stanica ili antitijela koje one proizvode kao reakcija na prirodno izlaganje ili cijepljenje.', 0);
INSERT INTO property VALUES (1,4155, 'hr', 'definition','Sposobnost organizma da se odupre bolesti ili toksinima prirodnim ili umjetnim sredstvima.', 0);
INSERT INTO property VALUES (1,4156, 'hr', 'definition','Svaka od nekoliko metoda za kvantitativno određivanje kemijskih tvari kao što su hormoni, lijekovi, te određene bjelančevine koje upotrebljavaju visokospecifične veze između antigena i antitijela.', 0);
INSERT INTO property VALUES (1,4157, 'hr', 'definition','Poremećaj u složenom sustavu stanica koje međusobno djeluju jedna na druge, staničnih proizvoda i tkiva koja tvore stanice i koje zaštićuje tijelo od patogena, uništava zaražene i maligne stanice te uklanja ostatke stanica. cellular debris.', 0);
INSERT INTO property VALUES (1,4158, 'hr', 'definition','Grana biologije koja se bavi prirodnom ili stečenom otpornošću viših oblika životinja i ljudi prema infekcijama s pomoću mikroorganizama.', 0);
INSERT INTO property VALUES (1,4159, 'hr', 'definition','Vrednovanje učinka određenog projekta na okoliš.', 0);
INSERT INTO property VALUES (1,4160, 'hr', 'definition','Radnje, postupci ili mjere? Installations poduzete radi smanjenja opsega ili stupnja negativnih učinaka na zdravlje ljudi i ekosustav do kojih dolazi zbog planova i djelovanja ljudi ili međusobnog djelovanja ljudi i okoliša.', 0);
INSERT INTO property VALUES (1,4161, 'hr', 'definition','Instrument za uzorkovanje lebdećih čestica u zraku zbijanjem; takvi instrumenti sastoje se od kućišta koje ograničava protok zraka iza osjetljive ploče za uzorkovanje.', 0);
INSERT INTO property VALUES (1,4162, 'hr', 'definition','Mjere predostrožnosti, radnje ili installations? koje se provode radi sprječavanja negativnih učinaka na okoliš.', 0);
INSERT INTO property VALUES (1,4163, 'hr', 'definition','Elementi djelovanja koji prouzročuju štetu u obližnjem okolišu.', 0);
INSERT INTO property VALUES (1,4167, 'hr', 'definition','Radnja unošenja robe i dobara u zemlju iz inozemstva.', 0);
INSERT INTO property VALUES (1,4169, 'hr', 'definition','Dozvola vlade za unos unutar granica države i prodaju proizvoda proizvedenih u inozemstvu.', 0);
INSERT INTO property VALUES (1,4172, 'hr', 'definition','Materijal koji se upotrebljava za popunjavanje rupa u drvenim, gipsanim ili drugim površinama prije nanošenja premaza kao što je boja ili lak.', 0);
INSERT INTO property VALUES (1,4173, 'hr', 'definition','Utiskivanje tekuće tvari u prostore porozne krute tvari kako bi se promijenila njezina svojstva, na primjer, impregnacija drva kreozotom radi zaštite njegove otpornosti na vodu.', 0);
INSERT INTO property VALUES (1,4176, 'hr', 'definition','Povoljan razvoj događaja ili napredek u opsegu proizvodnje postignut u smislu produktivnosti i ulaznih vrijednosti (inputa), ili postizanje najveće moguće proizvodnje na osnovi danih ili dodijeljenih sredstava.', 0);
INSERT INTO property VALUES (1,4177, 'hr', 'definition','Iznenadna imisija u značajnoj količini jedne ili više onečišćujućih tvari  u atmosferu, u vodno tijelo ili u tlo.', 0);
INSERT INTO property VALUES (1,4178, 'hr', 'definition','Buka koja se odlikuje prolaznim kratkotrajnim poremećajima koji se uglavnom jednoliko šire kroz upotrebljivi propusni pojas prijenosnog sustava.', 0);
INSERT INTO property VALUES (1,4182, 'hr', 'definition','Kontrolirani proces kojim se gorivi kruti, tekući ili plinoviti otpad spaljuje i pretvara u plinove; nastali ostatak sadržava malo ili nimalo gorivih tvari.', 0);
INSERT INTO property VALUES (1,4183, 'hr', 'definition','Kontrolirano spaljivanje gorivoga krutog, tekućeg ili plinovitog otpada, kojim nastaju plinovi i kruti ostaci koji sadrže malo ili nimalo gorivih tvari, radi smanjenja količine izvornog otpada.', 0);
INSERT INTO property VALUES (1,4184, 'hr', 'definition','Uređaj za spaljivanje otpada.', 0);
INSERT INTO property VALUES (1,4186, 'hr', 'definition','Nagnuta površina bilo kojeg dijela Zemljine površine, npr. padina (obronak) brda; također, široki dio kontinenta koji se spušta prema oceanu, npr. Pacifički rub.', 0);
INSERT INTO property VALUES (1,4187, 'hr', 'definition','Dobitak od kapitala, rada ili napora na nečemu, ili njihove kombinacije, uključujući profit ili dobitak stečen prodajom ili konverzijom kapitala.', 0);
INSERT INTO property VALUES (1,4194, 'hr', 'definition','Financijska naknada, povrat novca ili zalog za naknadu štete ili gubitak koji daje država, polica osiguranja ili ugovorni sporazum prema utvrđenim uvjetima  i za utvrđene osobe koje su pretrpjele štetu.', 0);
INSERT INTO property VALUES (1,4195, 'hr', 'definition','Vodno tijelo između kontinenata Afrike, Antarktike, Azije i Australije, uključujući Bengalski zaljev na istoku i Arapsko more (s Crvenim morem, Adenskim zaljevom i Perzijskim zaljevom) na zapadu, a sadrži nekoliko otoka i otočja, kao što su Andaman, Nicobar i Sejšeli.', 0);
INSERT INTO property VALUES (1,4196, 'hr', 'definition','Nešto što osobito upućuje na kretanja (trendove).', 0);
INSERT INTO property VALUES (1,4202, 'hr', 'definition','Kvalitativni ili kvantitativni parametar koji se upotrebljava kao mjerilo stanja okoliša, npr. Kakvoće zraka ili vode.', 0);
INSERT INTO property VALUES (1,4204, 'hr', 'definition','Šume koje su autohtone za neko područje (zavičajne šume).', 0);
INSERT INTO property VALUES (1,4205, 'hr', 'definition','Tehnologije kojima se koriste domaći stanovnici neke zemlje i koje čine važan dio njezine kulturne baštine, pa ih stoga treba zaštititi od iskorištavanja od strane industrijaliziranih zemalja; o problemu autohtonih znanja raspravljalo se tijekom Konferencije u Riju, ali im se ne daje velika zaštita prema Konvenciji o biološkoj raznolikosti. Članak 8. nalaže da stranke moraju "poštovati, štititi i podržavati znanja, inovacije i običaje zavičajnih i lokalnih zajednica koje utjelovljuju tradicionalan način života ... te promicati njihovu širu primjenu uz odobrenje i uključivanje nositelja tih znanja, inovacija i običaja i poticati pravednu razdiobu dobiti nastalih korištenjem takvih znanja, inovacija i običaja".', 0);
INSERT INTO property VALUES (1,4206, 'hr', 'definition','Izvor onečišćenja koji se ne nalazi u kućanstvima, a unosi onečišćujuće tvari u javni sustav obrade otpada. Posredni onečišćivači okoliša mogu biti komercijalna ili industrijska postrojenja čiji otpad ulazi u lokalnu kanalizaciju.', 0);
INSERT INTO property VALUES (1,4209, 'hr', 'definition','Kemijske, mehaničke ili biološke onečišćujuće tvari u zraku unutar zgrada i drugih zatvorenih prostora u kojima borave ili žive ljudi. To onečišćenje može nastati zbog dima cigareta, pesticida, sredstava za čišćenje, plinova ispuštenih iz građevinskog materijala, tepiha, kućanskih proizvoda itd.', 0);
INSERT INTO property VALUES (1,4211, 'hr', 'definition','Okoliš u unutrašnjosti kuće ili neke druge građevine.', 0);
INSERT INTO property VALUES (1,4213, 'hr', 'definition','Područja namijenjena za industriju u urbanističkom planu ili planu zaštite okoliša. Raspon industrija uključenih u plan mogu uključivati laku industriju, uslužne djelatnosti, opću industriju, opasnu, štetnu ili prljavu industriju, lučku industriju, ekstraktivnu industriju. Za industrijska područja obično se definiraju standardi koji su vezi s pristupom i cestama, odvodnjom, parkiralištima, estetikom, uređenjem okoliša, tampon zonama, razinama buke te onečišćenjem zraka i vode.', 0);
INSERT INTO property VALUES (1,4217, 'hr', 'definition','Zgrada koja se neposredno upotrebljava u proizvodnji ili tehnički produktivnim poduzećima. Industrijski objekti općenito ili obično nisu dostupni drugim osobama osim radnika. Industrijski objekti uključuju zgrade koje se upotrebljavaju neposredno za proizvodnju energije, proizvodnju proizvoda, vađenje sirovina, kao i za skladištenje tekstila, naftnih derivata, drva i proizvoda od papira, kemikalija, plastike, te metala.', 0);
INSERT INTO property VALUES (1,4224, 'hr', 'definition','Svaki usjev koji daje materijale za industrijske procese i proizvode kao što su soja, pamuk (vlakno i sjeme), lan i duhan.', 0);
INSERT INTO property VALUES (1,4227, 'hr', 'definition','Odlaganje otpada koji je nastao tijekom procesa proizvodnje ili prerade u ustanovi ili gospodarskom (industrijskom) subjektu koji ga je proizveo.', 0);
INSERT INTO property VALUES (1,4228, 'hr', 'definition','Proizvodnja, distribucija i potrošnja dobara i usluga koje su rezultat cijele proizvodne djelatnosti.', 0);
INSERT INTO property VALUES (1,4300, 'hr', 'definition','1) Voda, osim otpadnih voda koje ulaze u kanalizacijski sustav (uključujući i kanalizacijske priključke) iz izvora kao što su, ali nisu ograničeni na, oluke na krovovima, podrumske odvode, dvorišnu kanalizaciju, površinske odvode, odvode od izvora i močvarnih područja, kanalizacijske poklopce, poprečne priključke između odvoda za oborinsku vodu i sanitarnu kanalizaciju, taložnike/ulične slivnike, rashladne tornjeve, oborinske vode, površinsko otjecanje, vodu od pranja ulica ili odvodnju. Dotok ne uključuje infiltraciju i razlikuje se od nje.
INSERT INTO property VALUES (1,4302, 'hr', 'definition','Znanost i tehnika razrade podataka i automatske obrade informacija.', 0);
INSERT INTO property VALUES (1,4303, 'hr', 'definition','Sve činjenice, zamisli ili proizvodi mašte koji su preneseni, objavljeni ili distribuirani formalnim ili neformalnim kanalima u bilo kojem obliku, ili saznanja koja su prenesena ili primljena.', 0);
INSERT INTO property VALUES (1,4307, 'hr', 'definition','Sustavan niz radnji koje obavlja osoba ili računalo na podatkovnim elementima uključujući njihovo razvrstavanje, sortiranje, računanje, izradu sažetaka, prijenos, dohvat i primanje.', 0);
INSERT INTO property VALUES (1,4309, 'hr', 'definition','Organiziran sustav pružanja pomoći ili potpore pojedincima koji žele dobiti informacije, npr. korištenjem baza podataka i drugih izvora informacija radi prenošenja ili dostave znanja ili činjeničnih podataka.', 0);
INSERT INTO property VALUES (1,4310, 'hr', 'definition','Općenito, svaki izvor koji pokreće i potkrjepljuje primanje znanja ili, u užem smislu, to je porijeklo prenesenih podataka.', 0);
INSERT INTO property VALUES (1,4311, 'hr', 'definition','Koordinirana skupina osoba, uređaja i ustanova koji prenose ili razmjenjuju znanja ili podatke, npr. jednostavnom verbalnom komunikacijom, ili potpuno računalnim  metodama pohranjivanja, pretraživanja i dohvata informacija.', 0);
INSERT INTO property VALUES (1,4313, 'hr', 'definition','Sustavi, oprema, komponente i softver potreban da se osigura dohvat, obrada i pohranjivanje informacija u svim središtima ljudske aktivnosti (domu, uredu, tvornici itd.), za čija je primjenu općenito potrebna upotreba elektronike ili slične tehnologije.', 0);
INSERT INTO property VALUES (1,4315, 'hr', 'definition','Kršenje, prijestup ili povreda; npr. Zakona, ugovora, prava ili dužnosti.', 0);
INSERT INTO property VALUES (1,4317, 'hr', 'definition','Elektroničko magnetsko zračenje čije su valne duljine u rasponu između 0,75 ili 0,8 mikrometara i 1000 mikrometara.', 0);
INSERT INTO property VALUES (1,4320, 'hr', 'definition','Vibracije zraka na frekvencijama koje su suviše niske a da bi se opazile kao zvuk ljudskim uhom, ispod približno 15 hertza.', 0);
INSERT INTO property VALUES (1,4321, 'hr', 'definition','Osnovna mreža ili temelj kapitalnih objekata ili ulaganja u zajednici koji su nužni za potporu gospodarskih aktivnosti i aktivnosti u zajednici.', 0);
INSERT INTO property VALUES (1,4323, 'hr', 'definition','Osoba koja živi u određenoj regiji, gradu, kući, državi itd.', 0);
INSERT INTO property VALUES (1,4325, 'hr', 'definition','Pritisak na organizam koji narušava njegov ustroj ili funkciju i ima za posljedicu neki patološki proces.', 0);
INSERT INTO property VALUES (1,4326, 'hr', 'definition','Raspršivanje pigmenta ili otopine boje u sredstvu - nositelju, čime nastaje fluid/tekućina, ljepilo ili prah koji se treba nanijeti i osušiti na podlozi; tinta (crnilo) za pisanje, označavanje, crtanje i ispis nanose se pomoću nekoliko metoda na papir, metal, plastiku, drvo, staklo, tkaninu ili neku drugu podlogu.', 0);
INSERT INTO property VALUES (1,4329, 'hr', 'definition','Ribolovna područja u jezerima, potocima itd.', 0);
INSERT INTO property VALUES (1,4331, 'hr', 'definition','Plovidba unutarnjim plovnim putovima, tj. Plovnim rijekama, kanalima, tjesnacima, jezerima, uvalicama itd.', 0);
INSERT INTO property VALUES (1,4333, 'hr', 'definition','Jezero, rijeka ili neko drugo vodno tijelo potpuno unutar  državne granice.', 0);
INSERT INTO property VALUES (1,4336, 'hr', 'definition','Prijevoz ljudi i robe brodovima koji plove na rijekama, kanalima ili jezerima.', 0);
INSERT INTO property VALUES (1,4340, 'hr', 'definition','Nešto novouvedeno, npr. nova metoda ili naprava.', 0);
INSERT INTO property VALUES (1,4342, 'hr', 'definition','Grana kemije koja se bavi  kemijskim reakcijama i svojstvima svih anorganskih tvari.', 0);
INSERT INTO property VALUES (1,4345, 'hr', 'definition','Anorganska kemikalija koja pospješuje rast biljaka poboljšanjem opskrbe osnovnim hranjivim tvarima kao što su amonijev sulfat ili kalcijev karbonat.', 0);
INSERT INTO property VALUES (1,4347, 'hr', 'definition','Onečišćujuća tvar mineralnog porijekla u čijoj strukturi ne prevladava ugljik.', 0);
INSERT INTO property VALUES (1,4350, 'hr', 'definition','Kemijski spojevi u kojima ugljik nije prevladavajući element (osim karbonata, cijanida i cijanata), drugim riječima, to je tvar koja nije ni biljna ni životinjska.', 0);
INSERT INTO property VALUES (1,4354, 'hr', 'definition','Kemikalija za uništavanje beskralješnjaka štetnika.', 0);
INSERT INTO property VALUES (1,4356, 'hr', 'definition','Placentalni sisavci reda kukcojeda, obično mali, s jednostavnim zubima, koji se hrane beskralješnjacima. Ova skupina obuhvaća rovke, krtice i ježeve.', 0);
INSERT INTO property VALUES (1,4358, 'hr', 'definition','Razred člankonožaca (Arthropoda) koji obično imaju člankasto tijelo s vanjskim hitinskim oklopom, par sastavljenih očiju, par ticala, tri para usnih organa i dva para krilaca.', 0);
INSERT INTO property VALUES (1,4359, 'hr', 'definition','Na prirodnom ili uobičajenom mjestu.', 0);
INSERT INTO property VALUES (1,4360, 'hr', 'definition','Službeno ispitivanje i vrednovanje u kojoj mjeri su utvrđeni krajnji i kratkoročni ciljevi, norme, politike ili postupci neke agencije, organizacije, odjela ili jedinice ostvareni prema planu.', 0);
INSERT INTO property VALUES (1,4362, 'hr', 'definition','Organizacija zadužena za pregled, nadzor i izvještavanje o članovima osoblja i radu neke ustanove ili odjela, ili sukladnost sa zakonima i propisima nekog segmenta društva ili druge skupine.', 0);
INSERT INTO property VALUES (1,4363, 'hr', 'definition','Službeno ovlaštenje potrebno da se sastavi i  na određeno mjesto postavi bilo koji uređaj, objekt, vojni položaj ili strojevi.', 0);
INSERT INTO property VALUES (1,4365, 'hr', 'definition','Uspostavljanje i normalizacija zakona, običaja, upotrebe, prakse, sustava ili regulativnog načela u nekoj aktivnosti ili cilju neke skupine ili organizacije.', 0);
INSERT INTO property VALUES (1,4371, 'hr', 'definition','Materijal koji se upotrebljava za smanjenje prijenosa zvuka prema nekom tijelu, uređaju, prostoriji ili od njih itd.', 0);
INSERT INTO property VALUES (1,4374, 'hr', 'definition','Radnja, sustav ili poslovna djelatnost davanja osiguranja u obliku financijske zaštite za slučaj smrti, gubitka ili štete, koja uključuje plaćanje redovnih premija u zamjenu za policu osiguranja kojom se takva zaštita jamči.', 0);
INSERT INTO property VALUES (1,4375, 'hr', 'definition','Komercijalna služba koja daje jamstvo za većinu gubitaka ili štete za ljude, imovinu ili tvrtku u zamjenu za plaćanje premije.', 0);
INSERT INTO property VALUES (1,4376, 'hr', 'definition','Zaštita od jednog ili više rizika, često kako je utvrđeno vrstom zaštite ili osiguranim predmetom.', 0);
INSERT INTO property VALUES (1,4378, 'hr', 'definition','Područja dodijeljena za očuvanje i zaštitu određenih životinja i biljaka, ili oboje. Ona se razlikuju od nacionalnih parkova, koji su uglavnom mjesta za javnu rekreaciju, jer se ona osiguravaju isključivo za zaštitu vrsta zbog njih samih. Ugrožene vrste se sve više čuvaju u rezervatima prirode kako bi se spriječilo njihovo izumiranje. Rezervati prirode također služe kao mjesto za odmor, razmnožavanje i prezimljavanje mnogobrojnih vrsta.', 0);
INSERT INTO property VALUES (1,4382, 'hr', 'definition','Tehnologije koje ispunjavaju ciljeve zaštite okoliša time što uključuju koncepte sprječavanja onečišćenja u svoj dizajn. Strategije objedinjene kontrole zaštite okoliša uvedene u ranim fazama projektiranja nekog procesa, a ne opcija kontrole uvedena u kasnijim fazama, poboljšavaju tehničke i ekonomske performanse nekog procesa.', 0);
INSERT INTO property VALUES (1,4383, 'hr', 'definition','Sustavan, sveobuhvatan pristup suzbijanju štetnika upotrebljava vlastite bioške značajke i ponašanje kukca ili glodavca  da se pronađu najmanje otrovne metode kontrole uz najniže troškove.', 0);
INSERT INTO property VALUES (1,4386, 'hr', 'definition','Postupak kojim se sve velike emisije u zemlju, zrak i vodu razmatraju istodobno, a ne pojedinačno, kako bi se izbjegle situacije u kojima jedna kontrolna mjera za jedan medij negativno utječe na drugu.', 0);
INSERT INTO property VALUES (1,4390, 'hr', 'definition','Specijaliziran sustav uzgoja životinja u kojem se stoka drži u zavtvorenom prostoru i hrani koncentratima, uz čestu upotrebu lijekova za suzbijanje bolesti koje su stalan rizik u takvim uvjetima.', 0);
INSERT INTO property VALUES (1,4391, 'hr', 'definition','Poljoprivredna proizvodnja u kojoj se upotrebljava što je više zemlja moguće uzgajanjem usjeva tako da su gusto posijani jedni do drugih ili uzgojem nekoliko usjeva tijekom godine ili korištenjem velikih količina umjetnog gnojiva.', 0);
INSERT INTO property VALUES (1,4395, 'hr', 'definition','Povećan učinak pesticida kada se upotrebljavaju u kombinaciji s drugima.', 0);
INSERT INTO property VALUES (1,4398, 'hr', 'definition','Prijenos podataka zapisanih u binarnom obliku između dva ili više računala preko bilo kojeg komunikacijskog kanala koji može prenositi elektromagnetske signale.', 0);
INSERT INTO property VALUES (1,4400, 'hr', 'definition','Upotreba, kombinacija i koordinacija dviju ili više odgovarajućih disciplina, tehnologija i društvenih znanosti u cjelovitom pristupu problemima zaštite okoliša.', 0);
INSERT INTO property VALUES (1,4401, 'hr', 'definition','Iznos plaćen ili zaračunat za upotrebu novca ili za pozajmljivanje novca na određeno vremensko razdoblje.', 0);
INSERT INTO property VALUES (1,4402, 'hr', 'definition','Skupina ljudi koji imaju zajedničke značajke, stavove, uvjerenja ili ciljeve i koji su stvorili formalnu organizaciju da  se bavi specifičnim pitanjima od interesa za članstvo.', 0);
INSERT INTO property VALUES (1,4408, 'hr', 'definition','Pokusi koji se istodobno obavljaju u različitim laboratorijima kako bi se potvrdila kvaliteta rezultata.', 0);
INSERT INTO property VALUES (1,4410, 'hr', 'definition','Djelomično dovršena roba ili proizvodi koji ponovo ulaze u proizvodnju na drugom mjestu.', 0);
INSERT INTO property VALUES (1,4412, 'hr', 'definition','Buka koja se pojavljuje u redovnim ili neredovnim vremenskim razmacima.', 0);
INSERT INTO property VALUES (1,4417, 'hr', 'definition','Suradnja u međunarodnim naporima na potpori  globalnih ciljeva zaštite okoliša. Rješenja za ekološke probleme, kao što su prekogranično onečišćenje koje se prenosi zrakom i vodom, uništavanje ozonskog omotača i klimatske promjene zahtijevaju djelovanje svih odgovornih država.', 0);
INSERT INTO property VALUES (1,4419, 'hr', 'definition','Sposobnost poduzetnika da natječu s takmacima u proizvodnji i prodaji robe na svjetskim tržištima.', 0);
INSERT INTO property VALUES (1,4420, 'hr', 'definition','Ugovori i drugi sporazumi ugovorne naravi između različitih država ili organizacija država kojima se stvaraju zakonska prava i obveze između strana.', 0);
INSERT INTO property VALUES (1,4421, 'hr', 'definition','Suradnja među vladama, poduzećima ili pojedincima u kojoj postoji dogovor da se radi zajedno na sličnim ciljevima ili strategijama, osobito u istraživanju ili utvrđivanju industrijskih standarda.', 0);
INSERT INTO property VALUES (1,4422, 'hr', 'definition','Pravosudno tijelo Ujedinjenih naroda. Sud je nadležan za davanje savjetodavnih mišljenja o pitanjima izrade zakona i ugovora na zahtjev Opće skupštine, Vijeća sigurnosti ili bilo koje druge međunarodne agencije koju je ovlastila Opća skupština da zatraži takvo mišljenje. On  također ima nadležnosti za rješavanje sudskih sporova među državama kada mu se one dobrovoljno podvrgnu.', 0);
INSERT INTO property VALUES (1,4423, 'hr', 'definition','Raspodjela sredstava ili robe u cijelom svijetu.', 0);
INSERT INTO property VALUES (1,4427, 'hr', 'definition','Politički ili diplomatski međusobni odnosi ili djelovanje između dviju neovisnih država, koja se odnosi na ekološke probleme.', 0);
INSERT INTO property VALUES (1,4429, 'hr', 'definition','Usklađivanje međusobnih odnosa suverenih država primjenom općih načela koja priznaju civilizirani narodi.', 0);
INSERT INTO property VALUES (1,4430, 'hr', 'definition','Pravni sustav koji uređuje međusobne odnose suverenih država i njihova prava i dužnosti prema drugima.', 0);
INSERT INTO property VALUES (1,4432, 'hr', 'definition','Ekosustavi čija važnost je priznata na međunarodnoj razini. U nekim slučajevima su zaštićeni međunarodnim konvencijama.', 0);
INSERT INTO property VALUES (1,4434, 'hr', 'definition','Savez neovisnih država čiji predstavnici se sastaju radi promicanja zajedničkih interesa, uključujući obranu i trgovinu.', 0);
INSERT INTO property VALUES (1,4436, 'hr', 'definition','Upotreba metoda, strategija, tajnih dogovora, odlučivanja i moći od strane vlada i njihovih predstavnika kako bi se ostvarili ciljevi u donošenju politika ili državnim poslovima na svjetskoj ili međunarodnoj sceni.', 0);
INSERT INTO property VALUES (1,4438, 'hr', 'definition','Politička ili diplomatska suradnja ili poslovi između neovisnih država.', 0);
INSERT INTO property VALUES (1,4440, 'hr', 'definition','Kopneno područje u poriječju neke rijeke i njezinih pritoka čije vode se nalaze u dvije ili više država i koje te države iskorištavaju.', 0);
INSERT INTO property VALUES (1,4441, 'hr', 'definition','Sloboda od opasnosti ili kvaliteta uklanjanja rizika od štete za osobe, imovinu ili okoliš dijeli preko jedne ili više nacionalnih granica; stoga i zajednički napori više od jednog naroda kako bi se postiglo ili očuvalo to stanje.', 0);
INSERT INTO property VALUES (1,4442, 'hr', 'definition','Proces uspostavljanja ili usklađivanja nečega s normom ili mjerom koja se priznaje izvan granica jedne države ili nacije.', 0);
INSERT INTO property VALUES (1,4444, 'hr', 'definition','Protok roba i dobara između država.', 0);
INSERT INTO property VALUES (1,4448, 'hr', 'definition','Dijelovi geografskog prostora koji čini hidrogeološku cjelinu kao poriječje jedne rijeke koje je pod upravnim nadzorom dviju ili više država.', 0);
INSERT INTO property VALUES (1,4451, 'hr', 'definition','Metoda koja se upotrebljava za vrednovanje značenja i važnosti podataka, rezultata, činjenica itd.', 0);
INSERT INTO property VALUES (1,4454, 'hr', 'definition','1) Područje između kopna i mora koje se redovito izložen zraku zbog plime i oseke mora. Morski organizmi koji nastanjuju pojaseve plime i oseke moraju se prilagoditi razdobljima izloženosti zraku i valovima koje stvara vjetar, što ga čini najviše fizički zahtjevnim od svih morskih staništa.
INSERT INTO property VALUES (1,4455, 'hr', 'definition','Uključivanje ili sudjelovanje u naporima na rješavanju problema za problematične ili zbunjujuće situacije u vezi sa svijetom prirode ili krajobrazom.', 0);
INSERT INTO property VALUES (1,4466, 'hr', 'definition','Iscrpan popis predmeta, robe, imovine itd.', 0);
INSERT INTO property VALUES (1,4468, 'hr', 'definition','Pregled šumskih površina kako bi se utvrdilo osiromašenje šume. Cilj sastavljanja takvog popisa je dati pregled šumskih uvjeta. Posebno ako popis treba otkriti bilo kakve promjene u šumskim uvjetima, u njemu isto tako treba navesti i distribuciju šumskih šteta i utvrditi u kakvoj je ona vezi sa stanjem staništa i sastojina.', 0);
INSERT INTO property VALUES (1,4473, 'hr', 'definition','Obrat u uobičajenom smjeru nekog procesa, kao u promjeni gustoće vode pri 4° C.', 0);
INSERT INTO property VALUES (1,4474, 'hr', 'definition','Sloj atmosfere u kojem se događa inverzija.', 0);
INSERT INTO property VALUES (1,4475, 'hr', 'definition','Životinje bez kralježnice, uključujući sve vrste koje nisu razvrstane kao kralježnjaci.', 0);
INSERT INTO property VALUES (1,4478, 'hr', 'definition','Predmet određene vrijednosti koji se kupi s namjerom da se njime ostvari neka dobit, kao što je dohodak, kamata ili povećanje vrijednosti kapitala.', 0);
INSERT INTO property VALUES (1,4480, 'hr', 'definition','Pokus koji se odvija u umjetnim uvjetima.', 0);
INSERT INTO property VALUES (1,4481, 'hr', 'definition','Pokusi koji se izvode u živim organizmima.', 0);
INSERT INTO property VALUES (1,4483, 'hr', 'definition','Nemetalni halogeni element; otrovne, korozivne tamne pločice ili granule se lako sublimiraju (prelaze iz krutog u plinovito stanje); netopljiv u vodi, topljiv u običnim otapalima; upotrebljava se kao germicid i antiseptik, u bojama, tinkturama i farmaceutskim proizvodima, u graviranju litografija, te kao katalizator i analitički reagens.', 0);
INSERT INTO property VALUES (1,4485, 'hr', 'definition','Proces izmjene iona između otopine i netopljive krute tvari, obično smole.', 0);
INSERT INTO property VALUES (1,4487, 'hr', 'definition','Trajni netopljivi materijal (obično umjetna smola) koja sadrži ione koji će se reverzibilno izmjenjivati  s drugim ionima u okolnoj otopini. I kationski i anionski izmjenjivači upotrebljavaju se u kondicioniranju vode. Volumen ionskog izmjenjivača mjeri se u kubnim litrama izmjenjivača nakon ispiranja i sušenja te taloženja izmjenjivačkog sloja.', 0);
INSERT INTO property VALUES (1,4491, 'hr', 'definition','Zračenje koje može dovoljno energizirati atome da iz njih ukloni elektrone. U tom stanju atomi postaju reaktivniji, tako da ionizirajuće zračenje povećava kemijsku aktivnost i tako proizvodi biološke učinke, uključujući i posljedice pri kojima dolazi do promjena DNK. Rendgenske zrake (X-zrake) i gama-zrake su jedini elektromagnetski valovi koji prouzročuju ionizaciju u biološkom materijalu.', 0);
INSERT INTO property VALUES (1,4492, 'hr', 'definition','Sloj  Zemljine atmosfere koji se prostire između 60 i 1000 kilometara iznad Zemljine površine, u kojem se nalazi visoka koncentracija slobodnih elektrona, koji nastaju kao rezultat ionizirajućeg zračenja koje prodire iz svemira u atmosferu.', 0);
INSERT INTO property VALUES (1,4493, 'hr', 'definition','Atom ili skupina atoma s električnim nabojem, koji nastanu gubljenjem ili dobivanjem jednoga ili više elektrona.', 0);
INSERT INTO property VALUES (1,4498, 'hr', 'definition','Kovak, rastezljiv, srebrnobijel feromagnetski metalni  element koji se nalazi najviše u hematitu i magnetitu. Uvelike se upotrebljava u graditeljstvu i tehnici.', 0);
INSERT INTO property VALUES (1,4501, 'hr', 'definition','Gospodarski sektor kojem pripadaju komercijalna poduzeća koja se bave vađenjem i preradom željezne rude za proizvodnju lijevanog željeza, kovanog željeza i čelika.', 0);
INSERT INTO property VALUES (1,4503, 'hr', 'definition','Industrija u kojoj se proizvode željezni predmeti.', 0);
INSERT INTO property VALUES (1,4504, 'hr', 'definition','Podvrgnuti ili izložiti svjetlosnom ili nekom drugom elektromagnetskom zračenju ili zrakama čestica.', 0);
INSERT INTO property VALUES (1,4505, 'hr', 'definition','1) Dovođenje vode na zemlju da se omogući ili poboljša rast usjeva.', 0);
INSERT INTO property VALUES (1,4507, 'hr', 'definition','Trajni dovod nadvodnjavanja izgrađen tako da prenosi vodu iz izvora opskrbe do jednog ili više poljoprivrednih gospodarstava.', 0);
INSERT INTO property VALUES (1,4509, 'hr', 'definition','Ratarstvo koje se zasniva na umjetnoj raspodjeli i upotrebi vode na oranicama kako bi se potakao i održao rast bilja.', 0);
INSERT INTO property VALUES (1,4514, 'hr', 'definition','Kopena masa, osobito manja od kontinenta, potpuno  okružena vodom.', 0);
INSERT INTO property VALUES (1,4515, 'hr', 'definition','Jedinstven, ali krhak i ranjivi ekosustavi s obzirom na činjenicu da se evolucija njihove flore i faune odvijala u relativnoj izolaciji. Mnogi udaljeni otoci imaju neke od najviše jedinstvenih flora u svijetu; neke imaju vrste biljaka i životinja koje se ne nalaze nigdje drugdje, koje su se razvile na specijaliziran način, zaštićene od žestoke konkurencije s kojom se vrste suočavaju na kopnu.', 0);
INSERT INTO property VALUES (1,4518, 'hr', 'definition','Proces sprječavanja ili smanjenja prijenosa električne struje, topline ili zvuka iz nekog tijela, naprave ili okoline tako da ih obložimo materijalom koji nije vodič.', 0);
INSERT INTO property VALUES (1,4519, 'hr', 'definition','1) Dva ili više spojeva koji imaju istu molekularnu formulu, ali različit raspored atoma unutar molekule.
INSERT INTO property VALUES (1,4523, 'hr', 'definition','Jedan, dva ili više atoma s istim atomskim brojem koji sadrže različiti broj neutrona.', 0);
INSERT INTO property VALUES (1,4524, 'hr', 'definition','Sitnozrnati, kremastobijeli dentin, od kojega su građene slonove kljove, kao i zubi ili kljove nekih drugih velikih životinja kao što su morževi. Već dugo se cijeni i upotrebljava za izradu raznovrsnih ukrasnih predmeta.', 0);
INSERT INTO property VALUES (1,4530, 'hr', 'definition','Osobe koje su zajedno preuzele iste obveze ili su se zajedno zadužile. Dvije ili više osoba koje su preuzele isti dug.', 0);
INSERT INTO property VALUES (1,4534, 'hr', 'definition','Program što ga financira ili vodi vlada, a namijenjen je  zastupanju i vođenju kroz sudski proces osoba koje su slabog financijskog stanja i ne mogu sebi priuštiti pravnog savjetnika.', 0);
INSERT INTO property VALUES (1,4535, 'hr', 'definition','Svaka javna organizacija ili grana vlasti koja je odgovorna za pravosuđe i provođenje zakona.', 0);
INSERT INTO property VALUES (1,4538, 'hr', 'definition','Posebne norme, propisi i precedenti kojima se regulira vođenje, postupak i uređenje pravosudnog sustava, njegovih raznih dijelova i pravosudnih službenika.', 0);
INSERT INTO property VALUES (1,4541, 'hr', 'definition','Ovlasti suda da provodi suđenja i donosi odluke o predmetima ili da izda neki nalog.', 0);
INSERT INTO property VALUES (1,4543, 'hr', 'definition','Znanost ili filozofija prava.', 0);
INSERT INTO property VALUES (1,4545, 'hr', 'definition','1) Njemački oblik hrvatskog odnosno srpskog pojma koji se odnosi na teren nastao otapanjem vapnenca, koji se odlikuje nepostojanjem površinskog otjecanja, nizom površinskih udubljenja, depresija i pukotina, šupljim strukturama i opsežnom mrežom podzemnih kanala za otjecanje.
INSERT INTO property VALUES (1,4548, 'hr', 'definition','Držanje zaliha robe za upotrebu u budućnosti.', 0);
INSERT INTO property VALUES (1,4550, 'hr', 'definition','Lako ulje destilirano iz nafte ili nafte iz naftnog škriljevca, a upotrebljava se kao gorivo za grijanje i kuhanje, u svjetiljkama, te kao denaturant za alkohol.', 0);
INSERT INTO property VALUES (1,4566, 'hr', 'definition','Stavljanje oznake na proizvod ili kontejner na kojoj su informacije o njegovom sadržaju, pravilnoj upotrebi, proizvođaču i bilo kakva upozorenja ili opasnostima koje se mogu pojaviti tijekom upotrebe.', 0);
INSERT INTO property VALUES (1,4567, 'hr', 'definition','Prostorija ili zgrada sa znanstvenom opremom za izvođenje znanstvenih pokusa, za poučavanje prirodnih znanosti ili za proizvodnju kemikalija i lijekova.', 0);
INSERT INTO property VALUES (1,4571, 'hr', 'definition','Pokusi ili istraživanja koja se izvode u laboratoriju.', 0);
INSERT INTO property VALUES (1,4573, 'hr', 'definition','Istraživanje koje se provodi u laboratoriju za ispitivanje kemijskih tvari, uzgajanje tkiva u kulturama ili izvođenje mikrobioloških, biokemijskih, hematoloških, mikroskopskih, imunoloških i parazitoloških pokusa itd.', 0);
INSERT INTO property VALUES (1,4575, 'hr', 'definition','Otpadni materijali koji nastaju pri analitičkim i istraživačkim aktivnostima u laboratoriju.', 0);
INSERT INTO property VALUES (1,4577, 'hr', 'definition','Jedan od čimbenika proizvodnje. Uključuje sve vrste rada - manualni, fizički ili umni - koji obavlja pojedinac, a usmjeren je prema stvaranju bogatstva.', 0);
INSERT INTO property VALUES (1,4580, 'hr', 'definition','Grana pravnog sustava koji utvrđuje pravila kojima se uređuju radni odnosi, odnosi sa sindikatima, kao i interveniranje državne radi zaštite od određenih situacija koja je potrebna građanima - radnicima.', 0);
INSERT INTO property VALUES (1,4584, 'hr', 'definition','Dinamika ili opći stupanj povezanosti između uprave i zaposlenicima u poduzeću, industriji ili državi, a osobito pozornost prema poštivanju ugovora, kolektivnih ugovora i statusu sindikata.', 0);
INSERT INTO property VALUES (1,4586, 'hr', 'definition','Materijal koji sadrži znatnu količinu celuloznog derivata, najčešće nitroceluloze, ali ponekad celuloznog estera, kao što su celulozni acetat ili celulozni butirat, ili celulozni eter kao što je etil celuloza; upotrebljava se za sjajnu obradu, osobito za mjed i druge svijetle metale.', 0);
INSERT INTO property VALUES (1,4589, 'hr', 'definition','Vodno tijelo odvojeno od otvorenog mora koraljnim grebenima ili pješčanim sprudovima.', 0);
INSERT INTO property VALUES (1,4590, 'hr', 'definition','1) Depresija u zemaljskoj površini u kojoj se nalazi ili se nekad nalazilo jezero, koje ima značajke njegove obale. 
INSERT INTO property VALUES (1,4593, 'hr', 'definition','Čovjekovo neposredno ili posredno mijenjanje biološkog, fizičkog, kemijskog ili radiološkog sastava jezerske vode, ili jezerskog ekosustava.', 0);
INSERT INTO property VALUES (1,4594, 'hr', 'definition','Zatvoreno vodno tijelo koje obično, ali ne nužno, sadrži slatku vodu, i nije povezano s morem.', 0);
INSERT INTO property VALUES (1,4597, 'hr', 'definition','Naprava koja stvara svjetlo, na primjer električna svjetiljka.', 0);
INSERT INTO property VALUES (1,4599, 'hr', 'definition','Određeni geografski pojas Zemljine površine, uključujući sva njezina svojstva, koji obuhvaća njezinu  geologiju, površinske nanose, topografiju, hidrologiju, tla, biljni i životinjski svijet, zajedno s rezultatima prošlog i sadašnjeg ljudskog djelovanja, do te mjere da ta svojstva imaju značajan utjecaj na sadašnje i buduće korištenje zemljišta.', 0);
INSERT INTO property VALUES (1,4600, 'hr', 'definition','Dozvola ili pristanak da se upotrebljava, uđe, približi ili prođe prema i od  pojasa zemljišta, koji je često zemljišni posjed (nekretnina).', 0);
INSERT INTO property VALUES (1,4602, 'hr', 'definition','Postupak razdjeljivanja većih zemljišnih posjeda na manje parcele.', 0);
INSERT INTO property VALUES (1,4603, 'hr', 'definition','Sustav uknjižbe određenih zemljišnih posjeda ili zemljišnog vlasništva. U njemu se opisuje zemljište i sva dodatna prava koja se na njega odnose, kao što su prava puta nad susjednim zemljištem.', 0);
INSERT INTO property VALUES (1,4607, 'hr', 'definition','Najveće moguće iskorištavanje zemljišta ili tla bez njegove degradacije ili osiromašenja.', 0);
INSERT INTO property VALUES (1,4609, 'hr', 'definition','Uklanjanje drveća, žbunja itd. u pripremi za oranje, gradnju itd.', 0);
INSERT INTO property VALUES (1,4610, 'hr', 'definition','Skrb, očuvanje i ponovna upotreba dijelova Zemljine površine, a osobito predjela s tlima koja su vrijedni prirodni resursi ili se upotrebljavaju kao poljoprivredni resurs.', 0);
INSERT INTO property VALUES (1,4611, 'hr', 'definition','Spajanje malih zemljišnih parcela radi stvaranje većih poljoprivrednih gospodarstava ili velikih polja.', 0);
INSERT INTO property VALUES (1,4612, 'hr', 'definition','Zemljani pokrov je fizičko stanje zemljana površine. To je kombinacija vegetacije, tla, stijena, vode i objekata koje je izgradio čovjek, koji čine krajolik Zemlje. Zemljani pokrov je sučelje između Zemljine kore i atmosfere, koji utječe na izmjenu energije i tvari u klimatskim sustavu i biogeokemijske cikluse.', 0);
INSERT INTO property VALUES (1,4614, 'hr', 'definition','Planiranje infrastrukture, službi i industrijskih naselja u svrhu unaprjeđenja socioekonomskog rasta nekog područja.', 0);
INSERT INTO property VALUES (1,4615, 'hr', 'definition','Proučavanje odnosa između kopnenih organizama i njihovog okoliša.', 0);
INSERT INTO property VALUES (1,4621, 'hr', 'definition','Najstariji način zbrinjavanja otpada za krute tvari odbačena u kante za smeće u domaćinstvima, zajedno s ambalažom i papirima iz trgovina i ureda. Odlagališta su obično iskorišteni kamenoloma i šljunčare. Kada su ispunjeni, dotadašnja praksa bila da ih se prekrije tlom i napusti. Na starim odlagalištima smeća izgrađene su stambene zgrade, često s pogubnim posljedicama. Zakapanje otpada sada je postalo ozbiljna tehnologije i potencijalni izvor energije. Odlagališta se mogu projektirati tako da budu reaktori, koji namjerno proizvode metan, plin kao izvor biogoriva ili alternativne energije. Tradicionalno, hrpe otpada ostajale su izložene zraku i aerobnim mikroorganizmima - onima koji se razmnožavaju u zraku - kako bi pretvorili neke vrste otpada u kompost. Međutim, otkrivene hrpe pogoduju štetnicima, neugodnom mirisu u vrućim vremenskim prilikama i nagrđuju krajobraz. U 1960-im godinama kao urednija i sigurnija opcija operateri odlagališta počeli su zatvarati otpad od svakog dana u glinene spremnike. Glina je onemogućavala pristup ne samo štetnicima, nego i zraku. Za raspadanje otpada bili su potrebni anaerobni mikroorganizmi, koji ugibaju u zraku. Međutim, u tom procesu stvarao se metan (prirodni plin), što je bilo opasno za sigurnost. Metan se sada izvlači spuštanjem mreže perforiranih cijevi u odlagalište.', 0);
INSERT INTO property VALUES (1,4623, 'hr', 'definition','Zaštitna obloga, koja se sastoji od tla ili nekoga drugog materijala, koja prekriva mjesta odlaganja otpada za sabijeni, neopasni kruti otpad, ili osigurava odlagališta za opasni otpad kako bi se što više smanjile mogućnosti ispuštanja opasnih tvari u okoliš.', 0);
INSERT INTO property VALUES (1,4624, 'hr', 'definition','Odlagališni plin vrlo je opasan jer je metan vrlo eksplozivan, pa se mora držati pod kontrolom na svim aktivnim odlagalištima otpada, bilo aktivnim ili pasivnim prozračivanjem ili i jednim i drugim, a osobito kad se radi o dubokim odlagalištima. Postoje sustavi za provjetravanje za plitka odnosno duboka mjesta.', 0);
INSERT INTO property VALUES (1,4625, 'hr', 'definition','Odlagališni plin nastaje na odlagalištima otpada anaerobnom razgradnjom kućnog smeća (krutog komunalnog otpada). Sastoji se od smjese plinova i bezbojan je te ima neugodan miris zbog tragova organskih sumpornih spojeva. Osim što je neugodnog mirisa, odlagališni plin vrlo je opasan jer je metan eksplozivan u koncentracijama u zraku između 5 posto, što je donja granica eksplozivnosti (DGE), i gornje granice eksplozivnosti (GGE) od 15 posto. Odlagališni plin mora se držati pod kontrolom na svim aktivnim odlagalištima otpada, bilo s aktivnim ili pasivnim prozračivanjem ili i jednim i drugim, a osobito kad se radi o dubokim odlagalištima.', 0);
INSERT INTO property VALUES (1,4626, 'hr', 'definition','Tekućina koja se procjeđuje kroz kruti otpad u odlagalištu, a pri tome sadrži topljive razgrađene ili suspendirane tvari.', 0);
INSERT INTO property VALUES (1,4629, 'hr', 'definition','Svaki fizički, prepoznatljiv oblik ili svojstvo Zemljine površine, koji ima karakterističan oblik i nastaje zbog prirodnih uzroka; to uključuje i glavne oblike, kao što su ravnina, plato i planina, i manje oblike, kao što je brdo, dolina, nagib, esker i dina. Svi oblici reljefa zajedno čine konfiguraciju površine Zemlje.', 0);
INSERT INTO property VALUES (1,4633, 'hr', 'definition','Korištenje, naseljavanje ili posjedovanje kopnenih područja na Zemljinoj površini.', 0);
INSERT INTO property VALUES (1,4635, 'hr', 'definition','Djelatnost projektiranja, organiziranja ili pripreme za buduće korištenje kopnenih područja na Zemljinoj površini, a posebno područja vrijednih zbog prirodnih resursa, iskorištena kao poljoprivredni resurs ili za ljudska naselja.', 0);
INSERT INTO property VALUES (1,4636, 'hr', 'definition','Prisutnost jedne ili više onečišćujućih tvari na ili u nekom području zemlje ili njegovih sastavnih dijelova.', 0);
INSERT INTO property VALUES (1,4641, 'hr', 'definition','Poboljšanje zemljišta za njegovu intenzivniju upotrebu promjenom njegovih općih svojstava, npr. odvodnjavanjem suviše vlažne zemlje; navodnjavanjem sušnih ili polusušnih zemljišta; ili oporabom (isušivanjem) potopljenog zemljišta pod morem, jezerima i rijekama.', 0);
INSERT INTO property VALUES (1,4644, 'hr', 'definition','Upisnik (registar) ili pregled zemljišta, koji sadrži informacije o površini posjeda, imenima zakupaca, počevši s najstarijim vlasnicima preko sljedećih vlasništva i dijeljenja zemljišta i slično.', 0);
INSERT INTO property VALUES (1,4646, 'hr', 'definition','Obrada bilo kojeg neupotrebljivog zemljišta, obično  odlaganjem otpadnog materijala ili izravnavanjem  neravnih dijelova zemljišta sve dok se zemljište opet ne bude moglo iskoristiti za određenu namjenu.', 0);
INSERT INTO property VALUES (1,4648, 'hr', 'definition','Značajke, obrasci i struktura određenoga geografskog područja, uključujući njegov biološki sastav, njegov fizički okoliš i njegove antropogene ili društvene obrasce. Područje u kojima se ekosustavi koji međusobno djeluju jedni na druge grupiraju i ponavljaju u sličnom obliku.', 0);
INSERT INTO property VALUES (1,4649, 'hr', 'definition','Rudarenje izobličuje površinu zemljišta, a ako se ne provede sanacija, dovodi do trajnog uništavanja tla. Rudarenjem se kvare plodna površinska tla, remeti se način odvodnje, uništava se proizvodna sposobnost poljoprivrednog i šumskog zemljišta i smanjuju se njihove estetske i društvene vrijednosti.', 0);
INSERT INTO property VALUES (1,4650, 'hr', 'definition','Krajobrazi se mogu mijenjati tijekom vremena kao rezultat ljudske aktivnosti ili prirodnih procesa kao što su požari ili prirodne katastrofe. Promjene u strukturi krajobraza mogu se dokumentirati s pomoću podataka iz fotografija iz zraka ili satelitskih snimaka te novih tehnologija, kao što su daljinska očitavanje i geografski informacijski sustavi.', 0);
INSERT INTO property VALUES (1,4651, 'hr', 'definition','Stvaranje, razvoj i ukrasna sadnja vrtova, terena, parkova i drugih otvorenih prostora. Pejzažno vrtlarstvo upotrebljava se za unaprjeđivanje prirode, što pomaže stvoriti prirodno okruženje za pojedinačne stambene objekte i zgrade, pa čak i gradove, osobito kada su potrebni posebni pristupi i specifičan izgled.', 0);
INSERT INTO property VALUES (1,4652, 'hr', 'definition','U vizualnom ocjenjivanju krajobrazi se mogu se podijeliti u četiri glavna elementa. a) Oblik je percipirana masa ili oblik objekta koji izgleda ujednačen, a koji dovodi do svijesti o njegovim razlikama i odnosu cjeline i sastavnih dijelova. b) Linija je stvarna ili izmišljena staza, granice, međa, odnosno križanju dviju ravnina, kao što je silueta, koju oko prati kada zapaža naglo razlike u obliku, boji ili teksturi. c) Boja je vizualna percepcija koja omogućuje oku da razlikuje inače identične predmete na temelju valne duljine reflektirane svjetlosti. d) Tekstura je vizualni dojam krajobraza.', 0);
INSERT INTO property VALUES (1,4653, 'hr', 'definition','Čuvanje, za javnu upotrebu, krajobraza i mogućnosti za rekreaciju na otvorenom, turizam i slične aktivnosti; ovaj koncept uključuje očuvanje i poboljšanje ne samo onoga što je naslijeđeno, nego i  pružanje novih sadržaja i objekata.', 0);
INSERT INTO property VALUES (1,4657, 'hr', 'definition','Proučavanje krajobraza uzimajući u obzir ekologiju njihovih  bioloških populacija. Pri tome se povezuju geomorfologija i ekologija i primjenjuju se na oblikovanje i arhitekturu krajobraza.', 0);
INSERT INTO property VALUES (1,4658, 'hr', 'definition','Mjere za očuvanje krajobraza usmjerene na očuvanje krajobraza ili nadzor njegovih preobrazbi izazvanih čovjekovim djelovanjem ili prirodnim događajima.', 0);
INSERT INTO property VALUES (1,4659, 'hr', 'definition','Aspekt procesa planiranja korištenja zemljišta koji se bavi fizikalnim, biološkim, estetskim, kulturnim i povijesnim vrijednostima, kao i odnosima i planiranjem između tih vrijednosti, korištenja zemljišta i okoliša.', 0);
INSERT INTO property VALUES (1,4660, 'hr', 'definition','Izrada i provedba strategija i mjera za očuvanje, zaštitu, prikladno korištenje i obnovu prirodnih resursa i prirode ili sastavnice krajobraza koje je stvorio čovjek, osobito životinjskog i biljnog svijeta u divljini i prirodnih sustava s različitim stanjima.', 0);
INSERT INTO property VALUES (1,4661, 'hr', 'definition','Područje u kojem je krajobraz zaštićen zbog njegovih osobitih značajki kako bi se zadržala njegova uloga u pridonošenju većem uživanju u seoskom krajobrazu.', 0);
INSERT INTO property VALUES (1,4662, 'hr', 'definition','Korištenje dijelova krajobraza na način da se značajno mijenjaju njegova obilježja.', 0);
INSERT INTO property VALUES (1,4663, 'hr', 'definition','Korištenje krajobraza ili njegovih dijelova u  turističke, sportske ili poljoprivredne svrhe.', 0);
INSERT INTO property VALUES (1,4666, 'hr', 'definition','Oblikovanje regionalnih ciljeva, planova  i programa te usklađivanje regionalnih učinaka sektorskog planiranja.', 0);
INSERT INTO property VALUES (1,4668, 'hr', 'definition','Velika pomicanja zemlje i procesi koji uključuju klizanje nizbrdo pod utjecajem sile teže na pomiješanu masu zemlje i kamenje.', 0);
INSERT INTO property VALUES (1,4676, 'hr', 'definition','Prijevoz ljudi i robe po cestovnoj ili željezničkoj mreži.', 0);
INSERT INTO property VALUES (1,4678, 'hr', 'definition','Pojam upotrebe zemljišta odnosi se na prostorne aspekte svih ljudskih aktivnosti na kopnu i na način na koji se površina zemljišta prilagođava, ili bi se mogla prilagoditi, tako da služe ljudskim potrebama.', 0);
INSERT INTO property VALUES (1,4680, 'hr', 'definition','Razvrstavanje zemljišnih jedinica u razne kategorije na temelju svojstava zemljišta ili njegove pogodnosti za određenu svrhu. Ono je postalo važan alat u plraniranju ruralnih zemljišnih resursa.', 0);
INSERT INTO property VALUES (1,4682, 'hr', 'definition','Interdisciplinarni postupak vrednovanja, organiziranje i kontroliranje sadašnjeg i budućeg razvoja i korištenje zemljišta i njihovih resursa u smislu njihove prikladnosti na osnovi održivih prinosa. Uključuje ukupnu ekološku ocjenu u smislu posebnih vrsta upotreba, kao i vrednovanja socijalnog, ekonomskog i fizičkog konteksta zemljišta o kojem se radi.', 0);
INSERT INTO property VALUES (1,4684, 'hr', 'definition','Oblik upravljanja tlom i upotrebe tla.', 0);
INSERT INTO property VALUES (1,4797, 'hr', 'definition','Postupci koje provode upravne agencije odnosno uredi u vezi s izdavanjem različitih vrsta dozvola.', 0);
INSERT INTO property VALUES (1,4798, 'hr', 'definition','Obveza da se pribavi dozvola ili licenca za obavljanje nekog zanimanja ili djelatnosti.', 0);
INSERT INTO property VALUES (1,4799, 'hr', 'definition','Kompozitni organizam stvoren simbiozom vrsta gljivica i algi. Oni su ili skorene plohe ili grmovite izrasline na deblu, kamenim zidovima, krovovima i vrtnim stazama. Budući da oni nemaju prave korijene, žive od atmosfere i kišnice. Lišajevi imaju važnu ulogu u otkrivanju i praćenju onečišćenja, osobito sumporova dioksida, budući da su vrlo osjetljivi na onečišćenja i različite vrste nestaju ako onečišćenje dosegne određenu razinu.', 0);
INSERT INTO property VALUES (1,4802, 'hr', 'definition','Faze, promjene ili stupnjevi kroz koje organizam prolazi tijekom života.', 0);
INSERT INTO property VALUES (1,4805, 'hr', 'definition','Znanost koja se temelji na živim organizmima.', 0);
INSERT INTO property VALUES (1,4808, 'hr', 'definition','Posebni stavovi, navike ili ponašanje povezano s pojedincem ili skupinom.', 0);
INSERT INTO property VALUES (1,4810, 'hr', 'definition','Elektromagnetsko zračenje sposobno izazvati vidni osjet.', 0);
INSERT INTO property VALUES (1,4814, 'hr', 'definition','Opskrba osvjetljenjem na ulicama i u stanovima i kućama.', 0);
INSERT INTO property VALUES (1,4815, 'hr', 'definition','Mehanički uređaj za odvajanje i uklanjanje ostataka iz goriva i ulja za podmazivanje iz otpadnih voda koje dolaze iz benzinskih postaja i industrijskih postrojenja kako bi se izbjeglo onečišćenje vodnih tijela; ovaj sustav temelji se na različitim specifičnim težinama vode i ostataka goriva koji plutaju na vodi i mogu se lako ukloniti.', 0);
INSERT INTO property VALUES (1,4818, 'hr', 'definition','Ugljen relativno nedavnog podrijetla koji se sastoji od nagomilanih slojeva djelomično razgrađenog raslinja, a prijelaz je između treseta i bitumenskog ugljena; često sadrži komadiće drva iz kojeg je nastao.', 0);
INSERT INTO property VALUES (1,4819, 'hr', 'definition','Vađenje mrkog ugljena iz prirodnih naslaga; lignit je smeđe-crno kruto gorivo u drugoj fazi u razvoju ugljena. Ima malo više od pola ogrjevne vrijednosti u usporedbi s bitumenskim ili antracit ugljenom.', 0);
INSERT INTO property VALUES (1,4820, 'hr', 'definition','Razni minerali i industrijski oblici kalcijeva oksida koji se razlikuju uglavnom prema sadržaju vode i postotku sastojaka kao što su silicij, glinica i željezo.', 0);
INSERT INTO property VALUES (1,4823, 'hr', 'definition','Sedimentne stijene koje se sastoje uglavnom od kalcijeva karbonata, ponajprije u obliku mineralnih kalcita, i sa ili bez magnezijeva karbonata. Vapnenci nastaju bilo organskim ili anorganskim procesima, i mogu biti detritični, kemijski, oolitski, zemljani, kristalni ili rekristalizirani; mnogi vapnenci sadrže mnogo fosila i jasno je da se nalaze na obalama od školjaka ili koraljnim grebenima.', 0);
INSERT INTO property VALUES (1,4828, 'hr', 'definition','Kriterij ili norma izloženosti radnog mjesta koja određuje postoji li u nekom objektu ili zgradi koncentracija neke tvari kojoj većina radnika može biti izložena bez štetnih ili negativnih učinaka.', 0);
INSERT INTO property VALUES (1,4830, 'hr', 'definition','Proučavanje slatkovodnih vodnih tijela u pogledu njihovoga biljnog i životinjskog svijeta, fizičkih svojstava, geografskih obilježja itd.', 0);
INSERT INTO property VALUES (1,4832, 'hr', 'definition','Točkasti izvori buke postavljeni jedan iza drugoga, na primjer, u koloni automobila koja se kreću cestom.', 0);
INSERT INTO property VALUES (1,4834, 'hr', 'definition','Sloj sintetičkih ili prirodnih materijala, sa strana ili ispod odlagališta otpada, kazete na odlagalištu ili akumulaciji, koji ograničava otjecanje tekućina s procjednim vodama prema dolje ili sa strane u okoliš oko odlagališta.', 0);
INSERT INTO property VALUES (1,4835, 'hr', 'definition','Linijski izvor onečišćenja znači jednodimenzionalan izvor. Primjer linijskog izvora je širenje čestica s neasfaltirane ceste (seoskog puta).', 0);
INSERT INTO property VALUES (1,4838, 'hr', 'definition','Vrsta spojeva koji sadrže alifatske ugljikovodike s dugim lancima i njihove derivate, kao što su masne kiseline, alkoholi, amini, amino alkoholi i aldehidi; uključujući voskove, masnoće i derivirane spojeve.', 0);
INSERT INTO property VALUES (1,4839, 'hr', 'definition','Tvari koje privlače lipide.', 0);
INSERT INTO property VALUES (1,4840, 'hr', 'definition','Plinoviti spoj ili smjesa pretvorena u tekuću fazu hlađenjem ili kompresijom; primjeri su ukapljeni naftni plin (UNP), ukapljeni prirodni plin (UPP/LNG), tekući kisik i tekući amonijak.', 0);
INSERT INTO property VALUES (1,4843, 'hr', 'definition','Sva umjetna gnojiva u čijim tvarima je sadržaj vlage veći od devedeset posto, a obično se sastoji od životinjskog izmeta i dodane vode.', 0);
INSERT INTO property VALUES (1,4844, 'hr', 'definition','Stanje tvari između kristalnih tvari i plinova u kojima tvar ima sposobnost da teče pod izrazito malim posmičnim naprezanjima i prilagođuje se obliku posude u kojoj je zatvorena, ali je relativno slabo stišljiva, nema sposobnost proširenja bez ograničenja, i može imati slobodnu površinu.', 0);
INSERT INTO property VALUES (1,4845, 'hr', 'definition','Otpad u obliku tekućine, koji se sastoji od kanalizacije i otpadnih voda iz kućanstava, ili procesne vode, ili drugih tekućina, koja nastaje u industrijskoj aktivnosti, a posebno u industrijama kao što su industrija celuloze i papira, prehrambena industrija i proizvodnja kemikalija.', 0);
INSERT INTO property VALUES (1,4850, 'hr', 'definition','Pisani materijal kao što je poezija, romani, eseji, a posebno fikcija koje se odlikuje izvrsnošću stila i izraza, kao i temama općega ili trajnog interesa.', 0);
INSERT INTO property VALUES (1,4851, 'hr', 'definition','Fond informacija o određenoj temi ili povezanoj tematici, podijeljenih u posebne dokumente i obično pohranjeni u računalnom sustavu, pomoću kojega se i upotebljavaju.', 0);
INSERT INTO property VALUES (1,4852, 'hr', 'definition','Vrednovanje ili prosuđivanje o kvaliteti i značaju pisanih materijala kao što su poezija, eseji, romani, biografije o povijesni radovi.', 0);
INSERT INTO property VALUES (1,4853, 'hr', 'definition','Identifikacija, opis, analiza i razvrstavanje knjiga i drugih materijala koji se upotrebljavaju ili konzultiraju u pripremi nekog djela.', 0);
INSERT INTO property VALUES (1,4855, 'hr', 'definition','Zemaljska kora, tvrdi dio Zemlje, za razliku od atmosfere i hidrosfere.', 0);
INSERT INTO property VALUES (1,4857, 'hr', 'definition','Slama, sijeno ili sličan materijal koji se upotrebljava za ležaj životinja.', 0);
INSERT INTO property VALUES (1,4858, 'hr', 'definition','Područje plime i oseke na morskoj obali.', 0);
INSERT INTO property VALUES (1,4862, 'hr', 'definition','Goveda, konji i slične domaće životinje, obično na poljoprivrednim gospodarstvima.', 0);
INSERT INTO property VALUES (1,4863, 'hr', 'definition','Uzgoj stoke križanjem različitih pasmina kako bi se dobile nove pasmine sa željenim značajkama.', 0);
INSERT INTO property VALUES (1,4864, 'hr', 'definition','Uzgoj  goveda, konja i sličnih životinja.', 0);
INSERT INTO property VALUES (1,4866, 'hr', 'definition','Sastavni dio ili značajka nekog staništa s obzirom na njegovu sposobnost da održi i unaprjeđuje zdravlje i opće blagostanje stanovnika.', 0);
INSERT INTO property VALUES (1,4868, 'hr', 'definition','Svaka prostorija, građevina ili područje koje se upotrebljava kao stambeni prostor i povezan je s aktivnostima življenja, uključujući spavanje, odmaranje ili jedenje.', 0);
INSERT INTO property VALUES (1,4871, 'hr', 'definition','Gmaz iz podreda Lacertilia, osobito oni iz porodice Lacertidae, čije su uobičajene značajke izduženo tijelo, četiri uda i mali rep: uključuje gekone, iguane, kameleone, monitor guštere i sljepiće.', 0);
INSERT INTO property VALUES (1,4873, 'hr', 'definition','Najveće opterećenje koje sustav može podržati prije raspada.', 0);
INSERT INTO property VALUES (1,4879, 'hr', 'definition','Ovlasti vladine agencije ili njezinih administratora da provode zakone i državnih politika za grad, mjesto ili mali okrug.', 0);
INSERT INTO property VALUES (1,4884, 'hr', 'definition','Teorija i praksa javnih novčanih poslova u vezi s nekim gradom, mjestom ili malom okružnom jedinicom uprave.', 0);
INSERT INTO property VALUES (1,4885, 'hr', 'definition','Djelovanje koje usvoji i provodi vladajuća politička vlast ili sustav, koji utvrđuje poslove za neki grad, mjesto, županiju ili regionalno područje.', 0);
INSERT INTO property VALUES (1,4886, 'hr', 'definition','Opskrba toplinom u obliku pare ili vrele vode iz središnjeg izvora do skupine zgrada.', 0);
INSERT INTO property VALUES (1,4889, 'hr', 'definition','Sustav prijevoza putnika za ograničeno lokalno područje.', 0);
INSERT INTO property VALUES (1,4891, 'hr', 'definition','Promet unutar nekog grada, mjesta ili područja, sa čestim stajanjima, za razliku od međugradskog prometa.', 0);
INSERT INTO property VALUES (1,4893, 'hr', 'definition','Određeno mjesto koje izgleda prikladnim za izgradnju  novog pogona; izbor mjesta ovisi o više gospodarskih i okolišnih čimbenika.', 0);
INSERT INTO property VALUES (1,4897, 'hr', 'definition','Stroj s vlastitim pogonom na paru, električni pogon ili dizelsko gorivo, koji se upotrebljava za vuču vlakova po željezničkoj pruzi.', 0);
INSERT INTO property VALUES (1,4902, 'hr', 'definition','Prijevoz koji se odvija preko širokih područja, na duge udaljenosti i obično s malo postaja.', 0);
INSERT INTO property VALUES (1,4903, 'hr', 'definition','Prijevoz materijala ili robe preko kopna, vode ili zrakom u kojem se prelaze velike udaljenosti.', 0);
INSERT INTO property VALUES (1,4906, 'hr', 'definition','Učinci odnosno posljedice koje će trajati dugo nakon što njihov uzrok prestane.', 0);
INSERT INTO property VALUES (1,4908, 'hr', 'definition','1) Pokus koji se provodi tijekom relativno dugoga vremenskog razdoblja.
INSERT INTO property VALUES (1,4909, 'hr', 'definition','Radnja ili proces predviđanja i izračunavanja vjerojatnih uvjeta ili pojava za duže vrijeme u budućnosti, koji često uključuje proučavanje i analizu relevantnih podataka.', 0);
INSERT INTO property VALUES (1,4911, 'hr', 'definition','Prevladavajuća tendencija ili opći smjer koji se očekuje za neku promatranu vrijednost tijekom dužega vremenskog razdoblja, koji se često određuju proučavanjem i analiziranjem statističkih podataka.', 0);
INSERT INTO property VALUES (1,4913, 'hr', 'definition','Veliko motorno vozilo namijenjeno prijevozu teških tereta, osobito takvo vozilo s ravnom platformom.', 0);
INSERT INTO property VALUES (1,4914, 'hr', 'definition','Rezultat poslovne operacije u kojoj su režijski troškovi veći od primitaka ili prihoda.', 0);
INSERT INTO property VALUES (1,4916, 'hr', 'definition','Uništenje biotopa prouzročeno degradacijom okoliša zbog onečišćenja zraka ili vode.', 0);
INSERT INTO property VALUES (1,4917, 'hr', 'definition','Veličina fiziološkog osjećaja što ga proizvodi zvuk, koji  izravno varira s fizičkim intenzitetom zvuka, ali i ovisi o frekvenciji zvuka i valnom obliku.', 0);
INSERT INTO property VALUES (1,4918, 'hr', 'definition','Stambene zgrade izgrađene uz minimalne troškove i namijenjene tome da cijene najma ili cijena kupnje budu pristupačne za osobe s ograničenim financijskim sredstvima, što se obično određuje prema razini godišnjeg dohotka utvrđenoj ispod lokalnog medijana.', 0);
INSERT INTO property VALUES (1,4920, 'hr', 'definition','Tijelo dvodomnoga zakonodavnog tijela sastavljeno od zastupnika izabranih na općim izborima i organizirano u izborne jedinice ili okruge, koji svi imaju jednak broj građana.', 0);
