INSERT INTO property VALUES (1,4988, 'hr', 'definition','Planirano korištenje prirodnih resursa, osobito neobnovljivih izvora, u skladu s načelima kojima se osiguravaju njihove  optimalne dugoročne ekonomske i socijalne koristi.', 0);
