INSERT INTO property VALUES (1,5161, 'hr', 'definition','Sve kemijske reakcije koje se odvijaju u živom organizmu, uključujući i anabolizam i katabolizam. Bazalni metabolizam je razmjena energije životinje koja se odmara. Katabolizam je sinteza složenih molekula iz jednostavnijih.', 0);
