INSERT INTO property VALUES (1,5169, 'hr', 'definition','Proces u kojem se kemijska ili neka druga tvar nanosi na metale kao način za čišćenje, zaštitu, mijenjanje ili modificiranje izgleda ili fizikalnih svojstava, posebno površinskih svojstava.', 0);
INSERT INTO property VALUES (1,5171, 'hr', 'definition','Minerali koji sadrže metale, na primjer boksit, pirit itd.', 0);
INSERT INTO property VALUES (1,5175, 'hr', 'definition','Industrija dobivanja, pročišćavanja, legiranja i izrade metala.', 0);
INSERT INTO property VALUES (1,5178, 'hr', 'definition','Svaki binarni spoj u kojem je kisik vezan s jednim ili više atoma metala.', 0);
INSERT INTO property VALUES (1,5179, 'hr', 'definition','Nanošenje tankog, prijanjanjućeg sloja metala ne neki predmet.', 0);
INSERT INTO property VALUES (1,5181, 'hr', 'definition','Industrija povezana s osnovnom obradom metala i proizvodnjom proizvodnju od metala. Najvažnije krajnje upotrebe proizvoda metalnih industrija su automobili, strojevi, uređaji, električna oprema, konstrukcije, namještaj i spremnici.', 0);
INSERT INTO property VALUES (1,5184, 'hr', 'definition','Neprozorna kristalna tvar obično velike snage, s dobrom električnom i toplinskom provodljivošću, rastezljivošću i reflektivnošću.', 0);
INSERT INTO property VALUES (1,5186, 'hr', 'definition','Metalurški postupak u kojem se mješavine ruda zagrijavaju iznad tališta kako bi se izlučio ili proizveo sirovi metal.', 0);
INSERT INTO property VALUES (1,5193, 'hr', 'definition','Silovita, iznenadna i destruktivna promjena okoliša koja je povezana sa Zemljinom atmosferom ili je ona stvara ili na nju utječe, osobito kada se radi o vremenskim pojavama.', 0);
INSERT INTO property VALUES (1,5194, 'hr', 'definition','Grana znanosti koja proučava dinamiku atmosfere i izravne učinke atmosfere na Zemljinu površinu, oceane i stanovništvo,  posebno u vezi s vremenom i vremenskim uvjetima.', 0);
INSERT INTO property VALUES (1,5195, 'hr', 'definition','Varijable, kao što su tlak, temperatura, snaga vjetra, vlažnost zraka itd. Iz kojih se stvaraju zaključci o predstojećim vremenskim prilikama.', 0);
INSERT INTO property VALUES (1,5196, 'hr', 'definition','Pojave u troposferi i stratosferi, kao što su padaline, vjetar, temperatura itd.', 0);
INSERT INTO property VALUES (1,5197, 'hr', 'definition','Znanost koja se bavi atmosferom i njezinim pojavama.', 0);
INSERT INTO property VALUES (1,5199, 'hr', 'definition','Bezbojan plin bez mirisa i okusa, lakši od zraka, koji snažno reagira klorom i bromom pri Sunčevoj svjetlosti, glavni sastavni dio zemnog plina; upotrebljava se kao izvor metanola, acetilena i ugljičnog monoksida. Također poznat kao metil-hidrid.', 0);
INSERT INTO property VALUES (1,5203, 'hr', 'definition','Sustav metoda i načela koja se upotrebljavaju u određenoj struci.', 0);
INSERT INTO property VALUES (1,5210, 'hr', 'definition','Izraz koji se općenito upotrebljava za svaki veliki grad, ali osobito za grad u nekoj državi koji je sjedište vlade, crkvenih vlasti ili trgovačke aktivnosti.', 0);
INSERT INTO property VALUES (1,5215, 'hr', 'definition','Svaki raspoloživi izvor opskrbe deriviran iz mikroba, koji bi se upotrebljavao u korisne svrhe, kao što je proizvodnja hrane i lijekova.', 0);
INSERT INTO property VALUES (1,5216, 'hr', 'definition','Analiza za identifikaciju virusa, bakterija, gljiva i parazita.', 0);
INSERT INTO property VALUES (1,5218, 'hr', 'definition','Znanost i proučavanje mikroorganizama, uključujući protozoa, alge, gljive, bakterije, viruse i rikecije.', 0);
INSERT INTO property VALUES (1,5219, 'hr', 'definition','Lokalna, ne jedinstvena klima određenog mjesta ili staništa, u usporedbi s klimom cijelog područja čije je sastavni dio.', 0);
INSERT INTO property VALUES (1,5221, 'hr', 'definition','Proučavanje mikroklime, uključujući i proučavanje profila temperature, vlage i vjetra u najnižem sloju zraka, utjecaja vegetacije i vjetrobrana, kao i učinaka koji mijenjaju gradove i zgrade.', 0);
INSERT INTO property VALUES (1,5222, 'hr', 'definition','Mikroprocesor u kombinaciji s uređajima ulaznih/izlaznih sučelja, nekom vrstom vanjske memorije, te drugim elementima potrebnima da se formira radni računalni sustav; on je manji, jeftiniji i obično sporiji od miniračunala.', 0);
INSERT INTO property VALUES (1,5224, 'hr', 'definition','Eksperimentalni ekosustav malih razmjera, pojednostavljen, izveden u laboratoriju ili na terenu, koji može biti: a) izveden izravno iz prirode (npr. ako se uzorak vode iz ribnjaka naknadno održava unosom umjetnog svjetla i razmjenom plinova), ili b) izgrađen od akseničnih kultura (kultura organizma koja se sastoji samo od jedne vrste organizma, tj. da ne sadrži bilo kakav onečišćujući organizam) dok se ne postignu potrebni uvjeti organizama i okoliša. Također se naziva mikrokozmosom.', 0);
INSERT INTO property VALUES (1,5225, 'hr', 'definition','Tehnologija sastavljanja strujnih krugova i uređaja u izuzetno malim paketima, različitim tehnikama. Također poznata pod nazivom mikrominijaturizacija; elektronika mikrosistema.', 0);
INSERT INTO property VALUES (1,5226, 'hr', 'definition','Odvajanje ili odstranjivanje iz tekućine čestica i mikroorganizama veličine promjera u rasponu od 0,1 do 0,2 mikrona.', 0);
INSERT INTO property VALUES (1,5228, 'hr', 'definition','Mikroskopski organizmi, koji uključuju bakterije, protozoe, gljivice, virusi i alge.', 0);
INSERT INTO property VALUES (1,5229, 'hr', 'definition','Onečišćujuća tvar koja postoji u vrlo malim tragovima u vodi.', 0);
INSERT INTO property VALUES (1,5230, 'hr', 'definition','Interpretativna primjena mikroskopskog povećanja na proučavanje materijala koji se ne može jasno vidjeti golim okom.', 0);
INSERT INTO property VALUES (1,5231, 'hr', 'definition','Elektromagnetski val s valnom duljinom između 0,3 i 30 centimetara, što odgovara frekvencijama od 1 do 100 gigaherca; međutim, ne postoje oštre granice između mikrovalova i infracrvenih i radio valova.', 0);
INSERT INTO property VALUES (1,5234, 'hr', 'definition','Privremeno zapošljavanje osoba koje se sele iz jednog mjesta u drugo, kao što su poljoprivredni radnici nakon žetvene sezone.', 0);
INSERT INTO property VALUES (1,5236, 'hr', 'definition','Trajna ili polutrajna promjena prebivališta osobe.', 0);
INSERT INTO property VALUES (1,5237, 'hr', 'definition','Redovna učestala kretanja životinja između mjesta rađanja i zimske ispaše.', 0);
INSERT INTO property VALUES (1,5238, 'hr', 'definition','Ptice koje se sele u skupinama.', 0);
INSERT INTO property VALUES (1,5243, 'hr', 'definition','Aktivnosti i kretanja koje izvode ili se odnose na oružane snage.', 0);
INSERT INTO property VALUES (1,5245, 'hr', 'definition','Zračni promet oružanih snaga ili zračni promet koji je s njima povezan.', 0);
INSERT INTO property VALUES (1,5253, 'hr', 'definition','Područje namijenjeno isključivo vojnoj upotrebi.', 0);
INSERT INTO property VALUES (1,5254, 'hr', 'definition','Bjelkasta tekućina izlučena iz mliječnih žlijezda za hranjenje mladunaca; sastoji se od ugljikohidrata, bjelančevina, masti, mineralnih soli, vitamina i antitijela.', 0);
INSERT INTO property VALUES (1,5258, 'hr', 'definition','Zgrada u kojoj se žito melje u brašno.', 0);
INSERT INTO property VALUES (1,5261, 'hr', 'definition','Otvor ili iskop u zemlji za vađenje minerala.', 0);
INSERT INTO property VALUES (1,5262, 'hr', 'definition','Zasipavanje iskorištenih rudnika zemljom, drobljenim kamenom ili otpadni materijalom kako bi se povratile geološke, poljoprivredne i ekološke značajke tog područja.', 0);
INSERT INTO property VALUES (1,5264, 'hr', 'definition','Nakupina mineralnih tvari koje se nalaze u prirodi, na primjer, metalne rude ili nemetalni minerali, obično koje imaju tržišnu vrijednost, bez obzira na način nastanka.', 0);
INSERT INTO property VALUES (1,5265, 'hr', 'definition','Postupak vađenja metalnih ili nemetalnih mineralnih nalazišta iz Zemlje.', 0);
INSERT INTO property VALUES (1,5267, 'hr', 'definition','Vlakno proizvedeno iz stakla, kamena ili šljake, koje se većinom upotrebljava u izradi toplinske izolacije.', 0);
INSERT INTO property VALUES (1,5268, 'hr', 'definition','Industrija iskorištavanja minerala iz zemaljskih nalazišta podzemnim iskopavanjima ili iz otvorenih kopova, uz upotrebu primjerenih postrojenja i opreme.', 0);
INSERT INTO property VALUES (1,5269, 'hr', 'definition','Proces fosilizacije kojim anorganski materijali zamjenjuju organski sastojak organizma.', 0);
INSERT INTO property VALUES (1,5270, 'hr', 'definition','Znanost koja se bavi proučavanjem prirodnih anorganskih tvari koje se zovu minerali.', 0);
INSERT INTO property VALUES (1,7163, 'hr', 'definition','1) Sposobnost biljke da prevlada, uspori, suzbije ili spriječi infekciju ili kolonizaciju patogena, nametnika ili nepovoljnog abiotičkog faktora.
INSERT INTO property VALUES (1,7165, 'hr', 'definition','Formalni izraz mišljenja službenog tijela ili javne skupštine, usvojen glasovanjem, kao zakonodavna rezolucija.', 0);
INSERT INTO property VALUES (1,7167, 'hr', 'definition','Apsorpcija ili, manje uobičajeno, adsorpcija tvari od strane tijela ili sustava iz kojeg je materijal prethodno ispušten.', 0);
INSERT INTO property VALUES (1,7168, 'hr', 'definition','Svaka sastavnica okoliša koju organizam može iskoristiti.', 0);
INSERT INTO property VALUES (1,7169, 'hr', 'definition','Ocjena dostupnosti resursa u određenom okolišu.', 0);
INSERT INTO property VALUES (1,7170, 'hr', 'definition','Smanjenje sveukupne potrošnje resursa i iskorištavanje obnovljenih resursa kako bi se izbjeglo rasipanje.', 0);
INSERT INTO property VALUES (1,7175, 'hr', 'definition','Svjestan proces donošenja odluka kojim se prirodni i kulturni resursi raspodjeljuju tijekom vremena i prostora radi optimalizacije postizanja navedenih ciljeva društva, u okviru njegove tehnologije, političkih i društvenih institucija, te pravnih i administrativnih mjera. Važan cilj je očuvanje resursa, što podrazumijeva blizak i cjelovit odnos između ekološke osnove i društveno-ekonomskog sustava.', 0);
INSERT INTO property VALUES (1,7176, 'hr', 'definition','Proces kojim  živi organizmi kojim iz okoline uzimaju kisik i ispuštaju ugljični dioksid.', 0);
INSERT INTO property VALUES (1,7178, 'hr', 'definition','Količine zraka udahnute i izdahnute kroz pluća.', 0);
INSERT INTO property VALUES (1,7180, 'hr', 'definition','Svaki iz skupine uređaja koji štite respiratorni sustav od izlaganja onečišćujućim tvarima u zraku; obično maska oblikovana tako da štiti nos i usta.', 0);
INSERT INTO property VALUES (1,7181, 'hr', 'definition','Strukture i prolazi uključeni u udisanje, izdisanje i razmjenu kisika i ugljičnog dioksida u tijelu kralježnjaka.', 0);
INSERT INTO property VALUES (1,7182, 'hr', 'definition','Strukture i prolazi uključeni u udisanje, izdisanje i razmjenu kisika i ugljičnog dioksida u tijelu kralježnjaka.', 0);
INSERT INTO property VALUES (1,7184, 'hr', 'definition','Obveza prihvaćanja sankcija za nešto učinjeno i da se ispravi ili na drugi način nadoknadi šteta koju je taj čin mogao izazvati.', 0);
INSERT INTO property VALUES (1,7185, 'hr', 'definition','Neutralizacija ili ispravljanje  negativnih učinaka ili utjecaja na okoliš.', 0);
INSERT INTO property VALUES (1,7187, 'hr', 'definition','Otporna struktura koja organizmu omogućuje da preživi nepovoljne uvjete okoliša.', 0);
INSERT INTO property VALUES (1,7188, 'hr', 'definition','Postupak obnavljanja ili vraćanja nečega u izvorno, uobičajeno ili netaknuto stanje, osobito kad se radi o umjetničkim djelima, kulturnim artefaktima, namještaju ili zgradama.', 0);
INSERT INTO property VALUES (1,7189, 'hr', 'definition','Postupak ili radnja poduzeta da se ponovno uspostavi ili vrati stanje okolišnog ili ekološkog zdravlja.', 0);
INSERT INTO property VALUES (1,7191, 'hr', 'definition','Bilo koja odluka, radnja ili politika koja ograničava izradu vrijedne robe ili usluga.', 0);
INSERT INTO property VALUES (1,7192, 'hr', 'definition','Ograničenje korištenja zemljišta ili nekog drugog posjeda, što je često upisano u vlasnički list ili dokument o najmu.', 0);
INSERT INTO property VALUES (1,7193, 'hr', 'definition','Prodaja robe krajnjim potrošačima, obično u malim količinama.', 0);
INSERT INTO property VALUES (1,7194, 'hr', 'definition','Spremnik koji je konstruiran za takvu svrhu i kojim se upravlja tako da osigura privremeno pohranjivanje vode, i time u času visokog vodostaja smanji količinu vode u nekom toku te tako spriječi poplavu nekog vodotoka.', 0);
INSERT INTO property VALUES (1,7195, 'hr', 'definition','1) Dodavanje uređaja za kontrolu onečišćenja na postojećim objektima bez velikih promjena na pogonu za proizvodnju.
INSERT INTO property VALUES (1,7198, 'hr', 'definition','Svaki spremnik koji je osmišljen i dizajniran da ostvari unutar životnog ciklusa minimalni broj putovanja ili obrtanja, kako bi se ponovno punio za istu svrhu za koju je bio zamišljen.', 0);
INSERT INTO property VALUES (1,7201, 'hr', 'definition','Ponovna uporaba proizvoda ili materijala u izvornom obliku, npr. kad se staklene boce steriliziraju i ponovno pune za ponovnu upotrebu.', 0);
INSERT INTO property VALUES (1,7202, 'hr', 'definition','Sadnja novih stabala i, posebno, autohtonih biljaka u narušenim lokalitetima na kojima je vegetacijski pokrov uništen, kako bi se stabilizirale kopnene površine od vjetra i vodene erozije i zemljište nasipalo za druge svrhe. Obnova vegetacije provodi se na miniranim zemljama, cestama, parkovima, močvarama, komunalnih koridora, priobalnim područjima itd.', 0);
INSERT INTO property VALUES (1,7204, 'hr', 'definition','Metoda dobivanja čiste vode iz vode koja sadrži sol, kao što je desalinizacija. Čista voda i slana voda odvajaju se polupropusnom membranom i tlak slane vode se podiže iznad osmotskog tlaka, što dovodi do toga da voda iz slane otopine prolazi kroz membranu u čistu vodu. Za ovaj proces potreban je pritisak od približno 25 atmosfera, zbog čega ga je teško primijeniti u velikim razmjerima.', 0);
INSERT INTO property VALUES (1,7214, 'hr', 'definition','Uspravna trava, Oryza sativa, koja uspijeva u Istočnoj Aziji na vlažnim tlima i ima obješeno klasje i žuta duguljasta jestiva zrna koja poliranjem postaju bijela.', 0);
INSERT INTO property VALUES (1,7217, 'hr', 'definition','Pravno jamstvo ili opravdani zahtjev koji omogućuje građaninu ili zaposleniku da sastavi i podnese formalan pismeni zahtjev nekom tijelu vlasti kojim se zahtijeva neka povlastica ili usluga ili intervencija i ispravljanje neke nepravde.', 0);
INSERT INTO property VALUES (1,7218, 'hr', 'definition','Pravno jamstvo ili opravdani zahtjev kojim građaninu automatski pripada pravo na neku fizičku stvar, ali posebno česticu zemljišta, uključujući pravo da ga posjeduje, upotrebljava i raspolaže njime.', 0);
INSERT INTO property VALUES (1,7221, 'hr', 'definition','Pravno obvezujući zahtjev za plaćanje ili povrat troškova za štetu, gubitak ili ozljedu, ili za naknadu za pružene usluge, bilo u obliku pristojbi, provizija ili plaće.', 0);
INSERT INTO property VALUES (1,7222, 'hr', 'definition','Pravo pojedinca da općenito bude upoznat s postojanjem banaka podataka, pravo na informiranost na zahtjev i opće pravo na ispis zabilježenih podataka i na to da se zna stvarna upotreba informacija.', 0);
INSERT INTO property VALUES (1,7223, 'hr', 'definition','Pričvršćivanje brojem označenog prstena na nogu ptice tako da se može evidentirati njezino kretanje. Prstenovanje je vrlo uobičajena metoda praćenja kretanja ptica i osiguravanja  informacija o starosti ptica. Ono isto tako može prouzročiti stres kod ptica.', 0);
INSERT INTO property VALUES (1,7227, 'hr', 'definition','Uklanjanje tankih slojeva površinskog materijala više ili manje ravnomjerno iz prostranog područja s blago strmim zemljištem. Učinkovitije je ispiranje širokim kontinuiranim ''pločama'' tekuće vode, a ne tokovima koji prolaze oblikovanim kanalima; npr. Erozija koja nastaje kada kiša ispire tanki sloj tla na površini.', 0);
INSERT INTO property VALUES (1,7230, 'hr', 'definition','1) Kopnena područja u kojima su vegetacijski kompleksi i mikroklima proizvodi kombiniranog pritiska i utjecaja trajne i/ili povremene vode i tla koji pokazuju neke značajke vlage.
INSERT INTO property VALUES (1,7231, 'hr', 'definition','Spora vertikalna nestabilnost Zemljine kore koja uključuje pomicanja prema gore i prema dolje poput onih u vulkanskom području zapadno od Napulja u Italiji.', 0);
INSERT INTO property VALUES (1,7232, 'hr', 'definition','Podizanja razine mora moguća su posljedica globalnog zatopljenja. Kako se količina slobodne vode u oceanu povećava, i što voda postaje toplija, globalno zagrijavanje će se povećavati. Osim toga, prema teoriji, zagrijavanje na polovima može smanjiti količinu vode zarobljene u ledenjacima i ledenim kapama. Do 3000. godine mora bi mogla porasti između jednog i dva metra. U tom slučaju bila bi svakako ugrožena niska područja, posebice u Aziji, gdje milijun ljudi živi i bavi se poljoprivredom u riječnim deltama i naplavnim ravnicama.', 0);
INSERT INTO property VALUES (1,7233, 'hr', 'definition','Očekivani broj poginulih, ozlijeđenih osoba, štete na imovini i poremećaja ekonomske aktivnosti, zbog posebnoga prirodnog fenomena, a time i produkt vjerojatnosti za pojavljivanje i očekivane razmjere oštećenja.', 0);
INSERT INTO property VALUES (1,7235, 'hr', 'definition','Tehnika koja se upotrebljava za određivanje vjerojatnosti ili mogućnosti opasnog događaja (kao što je ispuštanje određene količine otrovnih plinova) i njihovih vjerojatnih posljedica. Izvorno je razvijena za upotrebu u nuklearnoj i kemijskoj industriji, gdje određeni mogući događaji, niske vjerojatnosti, mogu imati vrlo ozbiljne rezultate. Postoje pokušaji da se upotrijebe pojmove iz vjerojatnosne analize rizika kako bi se okarakterizirali utjecaji na okoliš, čije događanje i prirodu nije lako predvidjeti s bilo kojim stupnjem točnosti.', 0);
INSERT INTO property VALUES (1,7236, 'hr', 'definition','Kvalitativno i kvantitativno vrednovanje za označavanje  rizika što ga za ljudsko zdravlje i/ili okoliš predstavlja djelovanje odnosno prisutnost ili upotreba određene tvari ili onečišćujuće tvari.', 0);
INSERT INTO property VALUES (1,9074, 'hr', 'definition','Postrojenje za proizvodnju topline i električne energije na pogon otpacima (biomasom).', 0);
INSERT INTO property VALUES (1,9075, 'hr', 'definition','Toplana na pogon gorivom iz otpadaka.', 0);
INSERT INTO property VALUES (1,9077, 'hr', 'definition','Sve neupotrebljive tekućine u zraku ili lebdeće fine čestice u zraku nastale u proizvodnim procesima ili spaljivanjem neke tvari u zatvorenom prostoru.', 0);
INSERT INTO property VALUES (1,9078, 'hr', 'definition','Izravno ili neizravno ispuštanje plina u zrak.', 0);
INSERT INTO property VALUES (1,9079, 'hr', 'definition','Kvalitativna i kvantitativna analiza ispušnih plinova iz vozila, industrijskih postrojenja itd. Radi procjene njihovog sastava.', 0);
INSERT INTO property VALUES (1,9080, 'hr', 'definition','Smanjenje količine plinovitih emisija u atmosferu iz motornih vozila, industrijskih pogona i toplana itd. Usvajanjem čistih tehnologija, učinkovitosti proizvodnih procesa, poboljšanjem kvalitete goriva i gradnjom tvorničkih dimnjaka dovoljno visokih da se osigura disperzija plinova.', 0);
INSERT INTO property VALUES (1,9082, 'hr', 'definition','Odbačeni materijal iz procesa proizvodnje stakla ili iz upotrijebljenih predmeta široke potrošnje načinjenih od stakla.', 0);
INSERT INTO property VALUES (1,9083, 'hr', 'definition','Nusproizvod procesa odsumporavanja dimnih plinova od vlažnog vapnenca.', 0);
INSERT INTO property VALUES (1,9085, 'hr', 'definition','Toplota nastala postupkom hlađenja u elektranama, koja može izazvati toplinsko onečišćenje vodotokova, što prouzročuje cvjetanje algi.', 0);
INSERT INTO property VALUES (1,9086, 'hr', 'definition','Ispuštanje topline koja nastaje kao nusproizvod iz industrijskih procesa ili procesa proizvodnje električne struje.', 0);
INSERT INTO property VALUES (1,9087, 'hr', 'definition','Primjene otpadne topline uključuju grijanje prostora i hlađenje u urbanim područjima, otapanja leda zaleđenih  morskih putova, upotrebu u poljoprivredi za poticanje rasta  vegetacije i produženje sezone uzgoja, te u akvakulturi za poticanje rasta algi, školjki i drugih potencijalnih izvora morske hrane.', 0);
INSERT INTO property VALUES (1,9088, 'hr', 'definition','Ovlaštenje, dozvola ili ekvivalentni kontrolni dokument koji izdaje vladina agencija ili ured, a kojim se odobrava uvoz iz stranih zemalja otpadaka ili neželjenih materijala koji su ostaci iz proizvodnih procesa.', 0);
INSERT INTO property VALUES (1,9090, 'hr', 'definition','Postrojenje za spaljivanje otpada.', 0);
INSERT INTO property VALUES (1,9091, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propisuje država, a kojima se uređuje odlaganje neželjenih materijala koji su ostaci u procesu proizvodnje ili otpadaka iz čovjekovih naselja ili životinjskih staništa.', 0);
INSERT INTO property VALUES (1,9092, 'hr', 'definition','Sveukupan nadzor nad proizvodnjom, rukovanjem, obradom, pohranjivanjem i prijevozom otpada od točke nastajanja do konačnog prihvatljivog zbrinjavanja.', 0);
INSERT INTO property VALUES (1,9095, 'hr', 'definition','Mjere ili tehnike, uključujući planove i direktive, kojima se smanjuje količina stvorenog otpada. Primjeri smanjenja otpada su ekološki prihvatljive prakse recikliranje i smanjenje stvaranja otpada na izvoru.', 0);
INSERT INTO property VALUES (1,9096, 'hr', 'definition','Sposobnost mjera ili tehnika kojima se smanjuje količina otpadaka ili neželjenih materijala koja se stvara, osobito tijekom industrijskih proizvodnih procesa.', 0);
INSERT INTO property VALUES (1,9098, 'hr', 'definition','Ulje koje nastaje kao otpadni produkt od uporabe ulja u širokom rasponu industrijskih i komercijalnih aktivnosti, kao što su inženjering/tehnika, proizvodnja električne energije i održavanja vozila i koje treba pravilno odlagati odnosno obraditi da bi se moglo ponovno upotrijebiti.', 0);
INSERT INTO property VALUES (1,9099, 'hr', 'definition','Novine, časopisi, karton i ostali papir odvojen od krutog otpada radi recikliranja.', 0);
INSERT INTO property VALUES (1,9105, 'hr', 'definition','Postupak prikupljanja i odvajanja otpada u pripremi za oporabu otpada.', 0);
INSERT INTO property VALUES (1,9106, 'hr', 'definition','Postupak dobivanja materijala ili energenata iz otpada.', 0);
INSERT INTO property VALUES (1,9107, 'hr', 'definition','Metoda oporabe otpada kao resursa koja obuhvaća prikupljanje, a često uključuje i obradu, otpadnih proizvoda za uporabu kao zamjena svih ili dijela sirovina u proizvodnom procesu.', 0);
INSERT INTO property VALUES (1,9108, 'hr', 'definition','Postupci kojima se smanjuje količina otpada iz određenog izvora redizajniranjem proizvoda ili obrazaca proizvodnje ili potrošnje.', 0);
INSERT INTO property VALUES (1,9110, 'hr', 'definition','Skup komercijalnih poduzeća čija je primarna djelatnost uklanjanje ili odvoz otpada iz mjesta ljudskog stanovanja ili životinjskih staništa odnosno neželjenih materijala zaostalih iza proizvodnog procesa.', 0);
INSERT INTO property VALUES (1,9116, 'hr', 'definition','Odvajanje otpada prema različitim materijalima, kao što je staklo, metal, papir, plastika itd.', 0);
INSERT INTO property VALUES (1,9118, 'hr', 'definition','Privremeni smještaj otpada prije postupka obrade ili odlaganja.  Metode pohrane uključuju spremnike, cisterne, hrpe otpada i akumulacije.', 0);
INSERT INTO property VALUES (1,9119, 'hr', 'definition','Prijevoz otpada posebnim vozilima.', 0);
INSERT INTO property VALUES (1,9121, 'hr', 'definition','Svaki proces ili kombinacija procesa koja mijenja kemijski, fizikalni ili biološki sastav ili vrstu bilo kojeg otpada, ili smanjuje ili uklanja njegova štetna svojstva ili značajke za bilo koju svrhu.', 0);
INSERT INTO property VALUES (1,9123, 'hr', 'definition','Mjesto na kojem se otpadni materijal obrađuje kako bi se mogao ponovno upotrijebiti ili sigurno odložiti.', 0);
INSERT INTO property VALUES (1,9125, 'hr', 'definition','Uključivanje otpada u prirodne ili umjetne cikluse, uglavnom radi oporabe sekundarnih sirovina ili energije.', 0);
INSERT INTO property VALUES (1,9126, 'hr', 'definition','Ukupna količina otpadaka ili neupotrebljivog materijala nastalog na bilo kojem izvoru.', 0);
INSERT INTO property VALUES (1,9127, 'hr', 'definition','Upotrijebljena voda, ili voda koja nije potrebna, kojoj se dopusti da se ispusti, ili neminovno izlazi iz kanala, jaraka, spremnika i drugih vodnih tijela vode, za koja su vlasnici tih objekata zakonski odgovorni.', 0);
INSERT INTO property VALUES (1,9128, 'hr', 'definition','Naknada, izdatak ili trošak koji se zaračunava za gospodarenje potrošene ili upotrijebljene vode koja sadrži otopljene ili suspendirane čestice iz kućanstava, velikih poljoprivrednih gospodarstava ili industrije.', 0);
INSERT INTO property VALUES (1,9129, 'hr', 'definition','Tok obrađenih otpadnih voda iz bilo kojeg procesa pročišćavanja otpadnih voda.', 0);
INSERT INTO property VALUES (1,9130, 'hr', 'definition','Prikupljanje i uklanjanje otpadnih voda nastalih u industrijskim pogonima i urbanim naselja sustavom cijevi i postrojenja za pročišćavanje.', 0);
INSERT INTO property VALUES (1,9132, 'hr', 'definition','Tekući otpad ili otpaci koji plivaju na vodi, koji su onečišćeni ili zaprljani u obavljanju trgovinske djelatnosti.', 0);
INSERT INTO property VALUES (1,9133, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propisuju vlade u svrhu reguliranja odljeva i zbrinjavanja potrošene ili iskorištene vode iz kućanstava, zajednice, poljoprivrednih gospodarstava ili industrije koja sadrži otopljene ili suspendirane tvari.', 0);
INSERT INTO property VALUES (1,9135, 'hr', 'definition','Količina potrošene ili iskorištene vode, koja često sadrži otopljene i suspendirane tvari, koja se nalazi u potoku ili nekom drugom vodnom tijelu.', 0);
INSERT INTO property VALUES (1,9136, 'hr', 'definition','Obrada otpadne vode za ponovnu upotrebu.', 0);
INSERT INTO property VALUES (1,10640, 'hr', 'definition','Zbirka stabala iz različitih dijelova svijeta, koja se uzgajaju za znanstvena istraživanja.', 0);
INSERT INTO property VALUES (1,10644, 'hr', 'definition','Proučavanje uzročno-posljedičnih veza između geografskih pojava koje se događaju u određenom području.', 0);
INSERT INTO property VALUES (1,10646, 'hr', 'definition','Zbirka kultiviranih biljaka koje se razlikuju po bilo kojem obilježju (morfološkom, fiziološkom itd.) značajnom za potrebe hortikulture, poljoprivrede ili šumarstva.', 0);
INSERT INTO property VALUES (1,10651, 'hr', 'definition','Područja za očuvanje rijetkih ili ugroženih životinjskih vrsta.', 0);
INSERT INTO property VALUES (1,10652, 'hr', 'definition','Vrtovi za čuvanje rijetkih biljnih vrsta.', 0);
INSERT INTO property VALUES (1,10660, 'hr', 'definition','Životinje koje su ljudi preselili na zemljišta ili u vode u kojima nisu živjele prije, barem u povijesti. Takvo preseljenje vrsta uvijek uključuje element rizika, pa čak i ozbiljnih opasnosti. Novopridošle vrste, ovisno o njihovim posebnim odnosima među vrstama i svojstvima, mogu djelovati kao paraziti ili nositi parazite ili bolesti, vrebati na autohtone organizme, imati otrovne reakcije, ili biti vrlo konkurentni, ili na drugi način negativno utjecati na autohtone vrste i zajednice.', 0);
INSERT INTO property VALUES (1,10661, 'hr', 'definition','Biljke koje su ljudi preselili na zemljišta ili u vode u kojima nisu živjele prije, barem u povijesti. Takvo preseljenje vrsta uvijek uključuje element rizika, pa čak i ozbiljnih opasnosti. Novopridošle vrste mogu biti vrlo konkurentne ili na drugi način negativno utjecati na autohtone vrste i zajednice. Neke mogu smetati već zbog same svoje prevelike brojnosti. One mogu postati podložne brzim genetskim promjenama u novoj sredini. Mnoga uvođenja biljaka pokazala su se štetnima, a obavile su ih osobe koje nisu bile kvalificirane da predvide često složene ekološke interakcije koje mogu uslijediti. S druge strane, mnoge biljke uvedene u promijenjeni ili degradirani okoliš mogu biti korisnije od autohtonih vrsta u kontroliranju erozije ili obavljanju drugih pozitivnih funkcija.', 0);
INSERT INTO property VALUES (1,10666, 'hr', 'definition','Svi višestanični organizmi na Zemlji ili u određenoj regiji koji nisu ljudski i ne pripadaju raslinju, a smatraju se baštinom sadašnjeg naraštaja, osobito životinjske vrste koje se smatraju vrijednima očuvanja i zaštite od izumiranja.', 0);
INSERT INTO property VALUES (1,10667, 'hr', 'definition','Sve bilje, povrće, grmlje i drveće na Zemlji ili u određenoj regiji, koje se smatraju baštinom sadašnjeg naraštaja, osobito biljne vrste koje se smatraju vrijednima očuvanja i zaštite od izumiranja.', 0);
INSERT INTO property VALUES (1,10671, 'hr', 'definition','Pokušaji sprječavanja izumiranja ugroženih vrsta i populacija njihovim ponovnim uvođenjem u njihovom prirodnom staništu. Za ponovno uvođenje vrsta u nekoj regiji potrebna su preliminarna istraživanja kojima se utvrđuju razlozi njihova nestanka te izmjene koje su se možda dogodile u biotopima.', 0);
INSERT INTO property VALUES (1,10672, 'hr', 'definition','Ponovno uvođenje divljih biljnih vrsta u njihovo prirodno stanište. Za ponovnog uvođenje vrsta u nekoj regiji potrebna su preliminarna istraživanja kojima se utvrđuju razlozi njihova nestanka te izmjene koje su se možda dogodile u biotopima.', 0);
INSERT INTO property VALUES (1,10683, 'hr', 'definition','Sadnja drveća u nekom području, ili gospodarenje nekim područjem tako da se omogući drveću da se prirodno regenerira ili kolonizira, kako bi se razvilo u šumu.', 0);
INSERT INTO property VALUES (1,10685, 'hr', 'definition','1) Padanje starih stabala u šumi zbog oluje ili jakog vjetra. Ono igra važnu ulogu u spontanoj regeneraciji šumskih ekosustava.
INSERT INTO property VALUES (1,10691, 'hr', 'definition','Rezanje ili uklanjanje mrtvih ili živih dijelova ili grana biljaka za poboljšanje oblika ili rasta.', 0);
INSERT INTO property VALUES (1,10692, 'hr', 'definition','Šume se već stoljećima iskorištavaju kao izvor drva i za dobivanje zemljišta za poljoprivredno korištenje. Pogrešno upravljanje šumskim zemljištem i šumskim resursima dovelo je do situacije u kojoj se šume sada brzo povlače. Glavni su aspekti ove situacije sljedeći: ozbiljne nestašice u opskrbi industrijskim drvom; katastrofalne erozije i poplave koje prate nestajanje šuma s planinske zemlje; akutne nestašice drva za ogrjev u većem dijelu svijeta u razvoju; širenje pustinjskih uvjeta alarmantnom stopom u sušnim i polu-sušnim regijama svijeta; te mnogi utjecaji na okoliš zbog uništavanja tropskih prašuma.', 0);
INSERT INTO property VALUES (1,10693, 'hr', 'definition','Prostrano područje šumskog zemljišta koje se nalazi na prirodnim visinama obično višima od 660 metara.', 0);
INSERT INTO property VALUES (1,10694, 'hr', 'definition','Šuma u vlasništvu i pod upravom države.', 0);
INSERT INTO property VALUES (1,10695, 'hr', 'definition','Vrsta šume kakva se susreće u Sredozemlju, a sastoji se uglavnom od kserofilnog zimzelenog drveća.', 0);
INSERT INTO property VALUES (1,10698, 'hr', 'definition','Šuma čija su sva stabla u odrasloj fazi i dosegla su reproduktivno razdoblje.', 0);
INSERT INTO property VALUES (1,10699, 'hr', 'definition','Niske zimzelene grmolike formacije, koje se obično nalaze na silicijskim tlima u mediteranskim zemljama, gdje se klimatske značajke odlikuju zimskim oborinama i ljetnim sušama. Sastoji se od obilje aromatičnih vrsta, kao što su lavanda, mirta, oleandar i ružmarin, a često uključuje obilje trnovitog grmlja. Neki smatraju da je makija sekundarna vegetacija, budući da zauzima zemljišta u kojima zbog ljudskog djelovanja više nema njihovih prirodnih zimzelenih šuma hrasta.', 0);
INSERT INTO property VALUES (1,10706, 'hr', 'definition','Grana šumarstva koja se bavi prevencijom i suzbijanjem štete na šumama koje proizlaze iz djelovanja ljudi ili stoke, štetnika i abiotičkih agenata.', 0);
INSERT INTO property VALUES (1,10707, 'hr', 'definition','Zamjena od strane samog organizma tkiva ili organa koji su izgubljeni ili teško ozlijeđeni.', 0);
INSERT INTO property VALUES (1,10708, 'hr', 'definition','Biljke koje ispuštaju ili proizvode smolu.', 0);
INSERT INTO property VALUES (1,10711, 'hr', 'definition','Tradicionalni sustav upravljanja šumama kojim se stabla za proizvodnju trupaca uzgajaju iznad posječene šume (okresina/guštare/šikare/panjevaca). On se osobito upotrebljava kao metoda iskorištavanja hrastovog drva, u kojem se sijeku sva stabla osim prilično otvorene mreže visokih dobro oblikovanih hrastova – oko pedeset izbojaka po hektaru, čime ostaje dosta prostora za rast lijeski i druge šikare koje se mogu kresati u razmacima od deset do petnaest godina.', 0);
INSERT INTO property VALUES (1,10721, 'hr', 'definition','Zabava ili razonoda; razbibriga.', 0);
INSERT INTO property VALUES (1,10726, 'hr', 'definition','Velike životinje koje žive u divljini i čija je težina kao odraslih jedinki obično više od 15 kg, a love se za hranu, iz sporta ili radi zarade.', 0);
INSERT INTO property VALUES (1,10744, 'hr', 'definition','Uzgajanje školjkaša u unutarnjim morskim vodama, ušćima ili priobalnim vodama, u komercijalne svrhe. Sva dna za komercijalni uzgoj školjkaša moraju se pratiti da se utvrdi ima li kontaminacije mikrobima. Uzorci vode i mesa školjkaša moraju se ispitati da se utvrdi jesu li prisutni toksini iz algi. Mora se provoditi povremeno praćenje riba i školjkaša radi provjere prisutnosti onečišćujućih tvari.', 0);
INSERT INTO property VALUES (1,10753, 'hr', 'definition','Postoje dvije vrste uzgoja uzgoj kamenica: plutajuća kultura, u kojoj se uzgajaju kamenice iznad dna, na plutajućim linijama ili kašetama, a to je radno-intenzivan oblik kultiviranja za koji je potrebno kontinuirano njegovanje i čišćenje i opreme i školjaka, i kultura uzgoja na dnu, koja je slična konvencionalnom uzgoju kultura na kopnu; on uključuje odabir područja morskog dna koja osiguravaju prirodnu opskrbu hranom, potrebne struje, minimalni izloženost predatorima, kao i odgovarajuće temperature, a zatim "sijanje" dna populacijom školjaka (''matičnim stokovima'') koje se ostavljaju da narastu do konzumne veličine. Tada se beru s dna povlačenjem sa čamaca. I plutajuća kultura i kultura uzgoja na dnu ovise o prirodnim zalihama hrane za uzgoj školjkaša.', 0);
INSERT INTO property VALUES (1,10758, 'hr', 'definition','Ovakav ribolov pripada u jednu od dvije skupine: jedna je ulov pridnenih riba (koje žive na dnu), npr. Bakalara, koljaka, ivera, lista, a drugi je ulov pelagijske ribe (koja živi pri površini), npr. Inčun, tuna, haringa.', 0);
INSERT INTO property VALUES (1,10760, 'hr', 'definition','Ograničen dio vodnog tijela u vlasništvu države u kojem je dopušten ribolov.', 0);
INSERT INTO property VALUES (1,10767, 'hr', 'definition','Nastava ili događanja osmišljena tako da ponude učenje ili kulturne doživljaje populaciji bez pristupa tradicionalnim obrazovnim ustanovama zbog socijalnih ili ekonomskih prepreka.', 0);
INSERT INTO property VALUES (1,10773, 'hr', 'definition','Test koji se daje kandidatu za potvrdu ili položaj i koji se obično sastoji od  problema koje treba riješiti, vještina koje treba pokazati, ili zadataka koje treba obavljati.', 0);
INSERT INTO property VALUES (1,10778, 'hr', 'definition','Različiti oblici, metode i procesi formalnoga i neformalnog obrazovanja i izobrazbe za kontinuirano učenje za sve uzraste i kategorija stanovništva. Orijentirani su prema kontinuiranom učenju/razvojnim procesima pojedinca tijekom cijelog života.', 0);
INSERT INTO property VALUES (1,10780, 'hr', 'definition','Svaka edukacija, poučavanje ili disciplina na početku neke aktivnosti, zadatka, zanimanja ili trajanja ciklusa aktivnosti.', 0);
INSERT INTO property VALUES (1,10785, 'hr', 'definition','Nastavna načela, praksa, odnosno struka.', 0);
INSERT INTO property VALUES (1,10818, 'hr', 'definition','Plaćeno zaposlenje ili aktivnost povezani s poslom koji se odnose na ekološku problematiku, uključujući očuvanje prirodnih resursa i cjelovitost ekosustava.', 0);
INSERT INTO property VALUES (1,10833, 'hr', 'definition','Djelatnost koja se obavlja za javno dobro.', 0);
INSERT INTO property VALUES (1,10837, 'hr', 'definition','Koordinacija ili strukturiranje radne prakse i proizvodnih procesa kako bi se utjecalo na način na koji su poslovi oblikovani i kako se obavljaju na radnom mjestu.', 0);
INSERT INTO property VALUES (1,10847, 'hr', 'definition','Zgrada s bazenom i velikom dvoranom ili drugim prostorijama namijenjenim sportskim aktivnostima.', 0);
INSERT INTO property VALUES (1,5274, 'hr', 'definition','Vrijedna mineralna nalazišta u području koje se u današnje vrijeme mogu iskorištavati, a možda će to biti moguće i u budućnosti; uključuje poznata  i potencijalna nalazišta ruda.', 0);
INSERT INTO property VALUES (1,5277, 'hr', 'definition','Tvari koja se pojavljuje u prirodi, s karakterističnim kemijskim sastavom izraženim kemijskom formulom; može se pojavljivati u obliku pojedinačnih kristala ili može biti raspršen u nekom drugom materijalu ili stijeni.', 0);
INSERT INTO property VALUES (1,5279, 'hr', 'definition','Voda koja sadrži prirodno ili umjetno dodane minerale ili plinove.', 0);
INSERT INTO property VALUES (1,5282, 'hr', 'definition','Proces planiranja ili pripreme kojim se olakšava proizvodnja robe ili usluga da bi se postigli proizvodni rezultati za koje će biti potreban najmanji mogući utrošak novca, vremena ili rada.', 0);
INSERT INTO property VALUES (1,5283, 'hr', 'definition','Aktivnost kojom se smanjenje narušavanje ili šteta učinjena u okolišu ili ekosustavu.', 0);
INSERT INTO property VALUES (1,5286, 'hr', 'definition','Područje u kojem se iskorištavaju mineralne sirovine.', 0);
INSERT INTO property VALUES (1,5287, 'hr', 'definition','Inženjering koji se bavi otkrivanjem, razvojem i eksploatacijom ugljena, ruda i minerala, kao i čišćenjem, sortiranjem po veličini i obradom proizvoda.', 0);
INSERT INTO property VALUES (1,5288, 'hr', 'definition','Proučavanje geoloških aspekata mineralnih naslaga, s posebnim naglaskom na probleme povezane s rudarstvom.', 0);
INSERT INTO property VALUES (1,5289, 'hr', 'definition','Sektor gospodarstva koji okuplja komercijalna poduzeća koja se bave vađenjem prirodnih nalazišta minerala, što često uključuje iskopavanje (kao u kamenolomima), vađenje iz bušotina, mljevenje, istraživanje i razvoj.', 0);
INSERT INTO property VALUES (1,5290, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propiše vlada u svrhu uređivanja potencijalno štetnog djelovanja poduzeća koja se bave vađenjem i obradom plemenitih ili vrijednih metala.', 0);
INSERT INTO property VALUES (1,5293, 'hr', 'definition','Pravilo ili nalog što ga propiše vlada ili uprava u svrhu promicanja sigurnosti, zakonitosti i ekološke odgovornosti u pogledu bilo kojeg aspekta procesa ili industrije vađenja ruda.', 0);
INSERT INTO property VALUES (1,5295, 'hr', 'definition','Rudarstvo je intenzivna vrsta upotrebe zemljišta s potencijalnim utjecajem na okoliš na ograničenom području. Kada se neki rudnik zatvori, trebalo bi rješavati i aspekte zaštite okoliša i sigurnosne aspekte. Sanacija rudnika je postojeći program osmišljen kako bi se vratila u prihvatljivo stanje fizička, kemijska i biološka svojstva ili potencijalni režimi zraka, zemlje i vode koji su narušeni zbog rudarstva. Cilj sanacije rudnika jest spriječiti ili što je moguće više smanjiti štetne dugoročne utjecaje na okoliš, te stvoriti samoodrživ ekosustav tako da bude što sličniji onom koji je postojao prije rudarske djelatnosti.', 0);
INSERT INTO property VALUES (1,5297, 'hr', 'definition','Svi ostaci preostali nakon iskorištavanje sirovina iz Zemlje.', 0);
INSERT INTO property VALUES (1,5298, 'hr', 'definition','Formalno mišljenje ili odluka kojom o određenom pitanju ili problematici donosi glavno upravno ministarstvo države, obično pod nadležnošću  glavnog ministra, tajnika ili dužnosnika tog ministarstva.', 0);
INSERT INTO property VALUES (1,5299, 'hr', 'definition','Tijelo najviših vladinih dužnosnika ili drugih visokih javnih dužnosnika koje je odabrao šef države da upravlja određenim aspektima državnih poslova, za razliku od državnih službenika, čiji opseg poslova se ne mijenja zbog   promjena u državi koje su rezultat demokratskih izbora ili nekoga drugog procesa.', 0);
INSERT INTO property VALUES (1,5303, 'hr', 'definition','Skupina koja se po rasi, političkom uvjerenju i na druge načine razlikuje od veće skupine, čiji je sastavni dio.', 0);
INSERT INTO property VALUES (1,5307, 'hr', 'definition','Sklonost ili sposobnost dviju ili više tekućina da stvore jednoliku smjesu, to jest, da se otope jedna u drugoj; stupnjevi su ukupna mješljivost, djelomična mješljivost i nemješljivost.', 0);
INSERT INTO property VALUES (1,5310, 'hr', 'definition','Red malih paučnjaka (Arachnida) sa zaobljenim tijelima. Crvići se nalaze u vrlo velikim količinama u tlu, hrane se biljnim materijalom i beskralješnjacima. Neki parazitski crvići (npr. Crveni pauk) štete usjevima i mogu biti ozbiljni štetnici. Drugi su uzročnici bolesti kod životinja. Krpelji su krvopije, a neki su vektori bolesti kao što je tifus Stjenovitih planina kod ljudi i ptica, te encefalomijelitis kod goveda i ovaca.', 0);
INSERT INTO property VALUES (1,5311, 'hr', 'definition','Svaki postupak ili djelovanje poduzeto da se smanje negativni učinci koje neki projekt ili aktivnosti može imati na okoliš.', 0);
INSERT INTO property VALUES (1,5313, 'hr', 'definition','Oblik poljoprivrede koji se temelji na kombinaciji proizvodnje usjeva i stočarstva.', 0);
