INSERT INTO property VALUES (1,5332, 'hr', 'definition','1) Sadržaj vodene pare u atmosferi, odnosno ukupnih vodenih tvari (plinovitih, tekućih i krutih) koje su prisutne u određenom volumenu zraka.
INSERT INTO property VALUES (1,5333, 'hr', 'definition','Proučavanje kemijskih struktura i procesa bioloških fenomena na molekularnoj razini; ova disciplina posebno se bavi proučavanjem bjelančevina, nukleinskih kiselina i enzima, makromolekula bitnih za životne procese. Ona nastoji razumjeti molekularnu osnovu genetskih procesa. Tehnike uključene u difrakciju rendgenskih zraka i rad s elektronskim mikroskopom.', 0);
INSERT INTO property VALUES (1,5339, 'hr', 'definition','Svaki od različitih beskralješnjaka koji imaju meko nesegmentirano tijelo i često oklop, prekriven slojem kože.', 0);
INSERT INTO property VALUES (1,5343, 'hr', 'definition','Financijsko utvrđivanje, podešavanje, procjena, ili ocjena u svrhu ubiranja poreza, naknade ili novčane kazne.', 0);
INSERT INTO property VALUES (1,5346, 'hr', 'definition','Redovna kontrola kako bi se uočila promjena u nekom svojstvu (kvaliteti) ili količini.', 0);
INSERT INTO property VALUES (1,5349, 'hr', 'definition','Posebna oprema za daljinsko očitavanje.', 0);
INSERT INTO property VALUES (1,5350, 'hr', 'definition','Skupina međusobno povezanih stanica za praćenje uspostavljenih za nadzor onečišćenja.', 0);
INSERT INTO property VALUES (1,5352, 'hr', 'definition','Stanica u kojoj se kvantitativno ili kvalitativno mjeri prisutnost, djelovanje ili razina bilo koje onečišćujuće tvari u zraku ili vodi, buka i eksplozija, zračenja, prometna gibanja, slijeganja tla ili promjena u vrsti raslinja.', 0);
INSERT INTO property VALUES (1,5353, 'hr', 'definition','Usklađen skup senzornih i komunikacijskih uređaja koji prati, prepoznaje ili bilježi izlazne vrijednosti i ili djelovanje bilo prirodnog ili umjetnog sustava kako bi se prikazala povijest ili budućnost događaja.', 0);
INSERT INTO property VALUES (1,5354, 'hr', 'definition','Tehnike koje se upotrebljavaju u procesu provjere, promatranja i mjerenja događaja, procesa ili fizikalnih, kemijskih, bioloških i ekoloških pojava.', 0);
INSERT INTO property VALUES (1,5357, 'hr', 'definition','Stanje tržišta u kojem samo jedan prodavatelj prodaje određenu robu ili uslugu, bilo zato što prodavatelj ima isključivi posjed neke bitne ulazne vrijednosti zato što velika ekonomija razmjera sprječava ulazak konkurenta na tržište.', 0);
INSERT INTO property VALUES (1,5360, 'hr', 'definition','Predmet, osobito velik ili izrađen od kamena, koji je izrađen u spomen i da bi se ukazalo poštovanje prema nekoj osobi ili skupini ljudi, ili posebno mjesto za tu svrhu.', 0);
INSERT INTO property VALUES (1,5362, 'hr', 'definition','Područje neograđenog neplodnoga zemljišta, obično prekriveno  vrijeskom, grubom travom, paprati i mahovinom.', 0);
INSERT INTO property VALUES (1,5364, 'hr', 'definition','Obraćati se etičkim načelima ili uvjerenjima protivnika ili javnosti kako bi se protivnika uvjerilo da promijeni ponašanje i stavove.', 0);
INSERT INTO property VALUES (1,5369, 'hr', 'definition','Grana biologije koja se bavi oblikom i građom organizama.', 0);
INSERT INTO property VALUES (1,5371, 'hr', 'definition','Broj umrlih u određenoj populaciji u određenom vremenskom razdoblju.', 0);
INSERT INTO property VALUES (1,5377, 'hr', 'definition','Biljka iz razreda Bryophyta, koja se pojavljuje u gotovo svim vlažnim staništima.', 0);
INSERT INTO property VALUES (1,5388, 'hr', 'definition','Svako plinovo ili tekuće zapaljivo gorivo koje izgara motoru s u unutarnjim izgaranjem.', 0);
INSERT INTO property VALUES (1,5390, 'hr', 'definition','Cestovna vozila na pogon motorom, osobito motorom s unutarnjim izgaranjem.', 0);
INSERT INTO property VALUES (1,5391, 'hr', 'definition','Stvaranje i ispuštanje plinovitih onečišćujućih tvari i  onečišćujućih tvari u obliku čestica u okoliš, uglavnom iz ispušnih cijevi automobila, kamiona i autobusa.', 0);
INSERT INTO property VALUES (1,5392, 'hr', 'definition','Plinovi ispušteni u atmosferu iz vozila na pogon motorima s unutarnjim izgaranjem.', 0);
INSERT INTO property VALUES (1,5395, 'hr', 'definition','Široka cesta izgrađena za brzi promet pri putovanjima na dugim relacijama, s ograničenim brojem mjesta na kojima vozači mogu ući na autocestu i sići s nje.', 0);
INSERT INTO property VALUES (1,5401, 'hr', 'definition','Obilježje Zemljine površine; uzdiže se visoko iznad podnožja i općenito ima strme padine i relativno mali prostor vrha. Planine su važan izvor vode, energije, minerala, šuma i poljoprivrednih proizvoda, kao i rekreacije. One su ''skladišta'' biološke raznolikosti i ugroženih vrsta, te važan dio globalnog ekosustava. Oko 10% svjetskog stanovništva ovisi o planinskim izvorima, a gotovo polovica tih ljudi je pogođena degradacijom planinskih razvodnih područja.', 0);
INSERT INTO property VALUES (1,5403, 'hr', 'definition','Ekosustavi koji se nalaze na visokim planinama, na niskim geografskim širinama. Planinski ekosustavi vrlo su ranjivi. Oni su sve osjetljiviji na eroziju tla, klizišta i brzi gubitak staništa i genetske raznolikosti. Rasprostranjeno siromaštvo i povećanje broja stanovnika na planinama imaju za posljedicu krčenje šuma, obradu marginalnih zemljišta, pretjeranu ispašu stoke, gubitak pokrova biomase i druge oblike degradacije okoliša. Budući da se ne zna mnogo o planinskim ekosustavima, program Agenda 21 je predložio osnivanje globalne baze podataka o planinama. To je nužno za pokretanje programa koji će pridonijeti održivom razvoju planinskih ekosustava. Prijedlozi su također usmjereni na promicanje razvoja razvođa rijeka i alternativnog zapošljavanja za osobe čiji opstanak je povezan s djelatnostima kojima se planine degradiraju.', 0);
INSERT INTO property VALUES (1,5405, 'hr', 'definition','Područje koje se odlikuje uočljivim vrhovima, grebenima ili planinskim lancima.', 0);
INSERT INTO property VALUES (1,5409, 'hr', 'definition','Jedinstvena, velika masa koja se sastoji od niza planina ili usko postavljenih planinskih grebena, s vrhovima ili bez njih, usko povezanih prema položaju, smjeru, formaciji i starosti.', 0);
INSERT INTO property VALUES (1,5412, 'hr', 'definition','Rezanje trave, usjeva ili žita s pomoću kose ili mehaničke naprave.', 0);
INSERT INTO property VALUES (1,5415, 'hr', 'definition','Relativno ravno područje finog mulja duž obale (npr. U zaštićenom ušću) ili oko otoka, u kojem se naizmjence pojavljuje plima i oseka, ili koje je pokriveno plitkim vodama.', 0);
INSERT INTO property VALUES (1,5417, 'hr', 'definition','Mješavina gline i/ili mulja s vodom koja tvori plastičnu masu s veličinom čestica pretežno promjera manjeg od 0,06 mm. Nataložena je u niskoenergetskim okolinama u jezerima, estuarijima i lagunama. Također se može nataložiti u dubokomorskom okolišu.', 0);
INSERT INTO property VALUES (1,5418, 'hr', 'definition','Sloj organskog materijala nanesen na površinu tla da zadrži vlagu; nanošenje nastora je nanošenje lišća, slame ili ostalih rahlih materijala na zemlju kako bi se spriječila erozija, isparavanje ili zamrzavanje korijenja biljaka.', 0);
INSERT INTO property VALUES (1,5419, 'hr', 'definition','Multilateralizam znači dugo željeni, ali rijetko postignut ideal, naime, dobrovoljnu suradnju naroda za mir i razvoj. Ultranacionalističke, bilateralne i regionalne inicijative potkopavaju ili oslabljuju multilateralne inicijative. Multilateralizam može biti oslabljen nekoordiniranim odlukama onih koji mu pridonose. Multilateralizam predstavlja demokraciju međunarodnog društva. Prosvijetljeni multilateralizam jača specifične interese država, a istodobno unaprjeđuje njihove zajedničke ciljeve borbe.', 0);
INSERT INTO property VALUES (1,5421, 'hr', 'definition','Veliko poduzeće koje posluje u nekoliko država.', 0);
INSERT INTO property VALUES (1,5424, 'hr', 'definition','1) Koordinirano upravljanje za najrazboritije i najskladnije korištenje zemljišta na dugoročnoj osnovi prema konceptu kombiniranja dvije ili više koristi i/ili namjene, s naglaskom na održivosti i neiscrpljivanju prirodnih resursa i zemljišta.
INSERT INTO property VALUES (1,5428, 'hr', 'definition','Sveukupne usluge koje nudi grad ili mjesto u kojima se ulice i drugi javni područjima održavaju čistima, kao što su odvoz smeća, čišćenje ulica i uklanjanje onečišćenja iz vode, tla i drugih prirodnih resursa.', 0);
INSERT INTO property VALUES (1,5429, 'hr', 'definition','Uklanjanje radi obrade ili odlaganja ostataka koji se mogu smatrati  otpadom, uključujući uklanjanje smeća s javnih prostora, javnih prometnica ili na selu.', 0);
INSERT INTO property VALUES (1,5430, 'hr', 'definition','Postupak, filozofija ili tijek djelovanja kojim se lokalne vlasti rukovode u zaštiti prirodnih resursa u lokalnom okruženju, gradovima ili mjestima.', 0);
INSERT INTO property VALUES (1,5431, 'hr', 'definition','Grad, mjesto ili druga jedinica uprave koja ima ovlasti za samoupravu.', 0);
INSERT INTO property VALUES (1,5432, 'hr', 'definition','Općenito, zakon ili skup zakona koji se odnosi samo na građane i stanovnici države; u užem smislu, zakon ili skup zakona koji se odnose na gradove, mjesta, sela i njihove lokalne samouprave.', 0);
INSERT INTO property VALUES (1,5433, 'hr', 'definition','Nadležnost, položaj ili status grada, mjesta ili jedinice lokalne uprave.', 0);
INSERT INTO property VALUES (1,5437, 'hr', 'definition','Kombinacija otpadnih materijala iz domaćinstava i komercijalnih otpadnih materijala koji nastaje u određenom području općine odnosno grada.', 0);
INSERT INTO property VALUES (1,5439, 'hr', 'definition','Svaki javni ili privatni organizirani sustav u kojem se voda obrađuje u središnjem postrojenju i cijevima dostavlja u domove i poslovne subjekte putem cijevi.', 0);
INSERT INTO property VALUES (1,5440, 'hr', 'definition','Komunalno vodno gospodarstvo bavi se aspektima vodoopskrbe i vodne tehnologije u vezi s planiranjem, obradom, izgradnjom i proizvodnjom. Također se odnosi na probleme prikupljanja otpadnih voda, zbrinjavanje otpadnih voda, pročišćavanje otpadnih voda u ruralnim područjima, mjerama štednje vode, upravljanje kvalitetom vodnog tijela.', 0);
INSERT INTO property VALUES (1,5443, 'hr', 'definition','Mišićne stanice, tkiva i organi pomoću koji se svi kralježnjaci kreću.', 0);
INSERT INTO property VALUES (1,5444, 'hr', 'definition','Mjesto ili zgrada u kojoj se izlažu, čuvaju ili proučavaju predmeti od povijesne, umjetničke ili znanstvene važnosti.', 0);
INSERT INTO property VALUES (1,5448, 'hr', 'definition','Porodica Basidiomycetes čije je obilježje proizvodnja spora na listićima.', 0);
INSERT INTO property VALUES (1,5449, 'hr', 'definition','Umjetnička organizacija zvukova ili tonova koji izražavaju zamisli i osjećaje elementima ritma, melodije, suglasja i tonova boja.', 0);
INSERT INTO property VALUES (1,5450, 'hr', 'definition','Uzgajanje dagnji za prodaju kao hrana.', 0);
INSERT INTO property VALUES (1,5452, 'hr', 'definition','Velika, raznolika porodica sisavaca mesoždera s niskim dugim tijelom, uključujući kanadske kune zlatice, lasice i jazavce; njihovo razlikovno obilježje je što imaju jedan kutnjak u svakoj gornjoj čeljusti, a najviše dva u donjoj čeljusti.', 0);
INSERT INTO property VALUES (1,5456, 'hr', 'definition','Svojstvo kemijskih ili fizikalnih agensa da potiču  promjene u genetskom materijalu koje se prenose tijekom stanične diobe.', 0);
INSERT INTO property VALUES (1,5457, 'hr', 'definition','Ispitivanje svojstva tvari u smislu sposobnosti za poticanje mutacije gena.', 0);
INSERT INTO property VALUES (1,5459, 'hr', 'definition','Agens koji podiže učestalost mutacije iznad spontane razine. Agens koji izaziva promjene na biljkama i životinjama, posebno na njihovom genetskom materijalu, a osobito u vrijeme razmnožavanja. Određene kemikalije i oblici zračenja snažne su mutagene tvari koje oštećuju DNK, ili genetski materijal u središtu svake stanice živog organizma.', 0);
INSERT INTO property VALUES (1,5460, 'hr', 'definition','Pojedinac koji nosi alel koji je doživio mutaciju i izražen je u fenotipu.', 0);
INSERT INTO property VALUES (1,5461, 'hr', 'definition','Ispuštanje mutiranih mikroorganizama stvara rizik da oni mogu pokazati neku ranije nepoznatu patogenost, mogu preuzeti od nekih bakterija koje se pojavljuju u prirodi (a moguće je da imaju i druge pozitivne funkcije koje su tako izgubile) ili prenijeti neke neželjene značajke takvih autohtonim bakterijama. Isto tako postoji i bojazan da nekontrolirane genetske mutacije od takvoga promijenjenog mikroorganizma mogu proizvesti oblik s opasnim posljedicama za okoliš.', 0);
INSERT INTO property VALUES (1,5462, 'hr', 'definition','Promjene u kemijskom sastavu DNK u kromosomima organizma: promjene su obično ograničene na pojedine gene, ali povremeno uključuju značajne promjene cijelog kromosoma. Kada se mutacija javlja u gametama ili gametocitima, može se stvoriti nasljedna promjena u obilježjima organizama koji se od njih razvijaju. Mutacija je jedan od načina na koje se stvaraju genetske varijacije u organizmima. Somatska mutacija je takva mutacija koja se pojavljuje u tjelesnoj stanici, pa se stoga prenosi na sve stanice koje iz nje nastaju mitozom. Prirodne mutacije, u ovoj fazi biološke evolucije, kada se pojave u stanicama viših životinja, gotovo uvijek proizvode štetne osobine. I prirodne i umjetne mutacije mogu biti prouzročene ionizirajućim zračenjem (odatle genetske i kancerogene opasnosti od nuklearnog oružja) i određenim kemijskim tvarima koje se nazivaju mutagenima.', 0);
INSERT INTO property VALUES (1,5463, 'hr', 'definition','Grana botanike koja se bavi proučavanjem gljiva.', 0);
INSERT INTO property VALUES (1,5465, 'hr', 'definition','Simbioza korijena više biljke s gljivom. Pri ektotrofnoj mikorizi (npr., vrijes, borovi) micelij gljiva obavija korijenje izvana; pri endotrofnoj mikorizi (npr. Orhideje) gljiva raste unutar stanica  kore korijena.', 0);
INSERT INTO property VALUES (1,5475, 'hr', 'definition','Nacionalna financijska sredstva i njihovo financijsko upravljanje, usmjereno na produktivnost.', 0);
INSERT INTO property VALUES (1,5476, 'hr', 'definition','Prikupljanje i obrada financijskih informacija o troškovima za ekološke izazove i mogućnosti za države ili zemlje.', 0);
INSERT INTO property VALUES (1,5479, 'hr', 'definition','Prijenos vlasništva nad privatnom tvrtkom ili druge privatne imovine na nacionalnu vladu, bilo zapljenom bez naknade (izvlaštenjem) ili prisilnom prodajom po cijenu koju odredi vlada.', 0);
INSERT INTO property VALUES (1,5482, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propiše vlada u svrhu uređenja suverene države koji su na snazi u svim regijama i teritorijima pod vlašću te vlade.', 0);
INSERT INTO property VALUES (1,5484, 'hr', 'definition','Područja izuzetne prirodne ljepote, namijenjena za očuvanje flore, faune i krajobraza, kao i za rekreaciju, ako to nije u sukobu s ciljevima očuvanja parkova i njihovih krajobraza. Lov, sječa drveta, komercijalni ribolov, poljoprivreda i ispaša stoke su pod kontrolom unutar nacionalnih parkova, kao i industrijske aktivnosti.', 0);
INSERT INTO property VALUES (1,5486, 'hr', 'definition','Postupna metoda i proces definiranja, razvijanja i oblikovanja različitih mogućih smjerova aktivnosti kako bi se zadovoljile postojeće ili buduće potrebe i bliži i dalji ciljevi za zemlju ili velik broj ljudi povezanih s određenim područjem, koji često dijele slično etničko podrijetlo, običaje i jezik.', 0);
INSERT INTO property VALUES (1,5494, 'hr', 'definition','Područje u kojem prevladavaju prirodni procesi, dopušteno je slobodno kolebanje u broju organizama, a čovjekove intervencije su minimalne.', 0);
INSERT INTO property VALUES (1,5496, 'hr', 'definition','Snažne, nagle i destruktivne promjene u okolišu koje nisu prouzročene ljudskim aktivnostima, zbog pojava kao što su poplave, potresi, vatre i uragani.', 0);
INSERT INTO property VALUES (1,5498, 'hr', 'definition','Kompleks atmosferskih, geoloških i bioloških značajki koje se nalaze u nekom području, bez rukotvorina ili utjecaja dobro razvijene tehnološke, ljudske kulture.', 0);
INSERT INTO property VALUES (1,5500, 'hr', 'definition','Organski materijal dodan tlu radi opskrbe kemijskim elementima potrebnim za ishranu biljaka.', 0);
INSERT INTO property VALUES (1,5501, 'hr', 'definition','Tekstilno vlakno mineralnog, biljnog ili životinjskog porijekla.', 0);
INSERT INTO property VALUES (1,5502, 'hr', 'definition','Šumsko područje koje se razvilo bez utjecaja čovjeka i ostaje uglavnom nepogođeno njegovim aktivnostima. Prirodna šuma može uključiti staru šumu, ali nije nužno njezin ekvivalent.', 0);
INSERT INTO property VALUES (1,5503, 'hr', 'definition','Prirodno gorivo koje sadrži metan i ugljikovodik koji se javlja u određenim geološkim formacijama.', 0);
INSERT INTO property VALUES (1,5504, 'hr', 'definition','Traženje podzemnih nalazišta različitim metodama za otkrivanje nalazišta prirodnog plina koja se obično nalaze u neposrednoj blizini sirove nafte.', 0);
INSERT INTO property VALUES (1,5505, 'hr', 'definition','Crpljenje prirodnog plina iz bušotina koje se nalaze pod morem i općenito iz podzemnih izvora, često uz nalazišta nafte; upotrebljava se kao gorivo, i uvelike je zamijenio ugljeni plin za tu svrhu, te kao izvor međuproizvoda za organsku sintezu.', 0);
INSERT INTO property VALUES (1,5506, 'hr', 'definition','Vjerojatnost pojavljivanja, u određenom vremenskom razdoblju i na određenom području, potencijalno štetne prirodne pojave.', 0);
INSERT INTO property VALUES (1,5507, 'hr', 'definition','Općenito, svjetski prirodni resursi u obliku u kojem su predani sadašnjem naraštaju, a posebno Zemljine izvanredne fizičke, biološke i geološke formacije, te staništa ugroženih vrsta životinja i biljaka i područja sa znanstvenom vrijednosti, vrijednosti očuvanja ili estetskom vrijednosti.', 0);
INSERT INTO property VALUES (1,5508, 'hr', 'definition','Pravilo ili skup pravila koja proizlazi iz prirode i vjeruje se da su obvezujuća za ljudsko društvo, za razliku od zakona koje je donio čovjek, kao što su zakonski akti i sudske odluke.', 0);
INSERT INTO property VALUES (1,5511, 'hr', 'definition','Prirodno/kulturno obilježje koje je od izuzetne ili jedinstvene vrijednosti zbog svoje inherentne rijetkosti, koje predstavlja estetske kvalitete ili kulturni značaj. Upute za odabir prirodnog spomenika su sljedeće: a) Područje treba sadržavati jedno ili više obilježja od izuzetne važnosti (odgovarajuće prirodne značajke uključuju spektakularne slapove, špilje, kratere, fosilne podloge, pješčane dine i morske značajke, zajedno s jedinstvenom ili reprezentativnom faunom i florom; povezane kulturne značajke mogu uključivati špiljske nastambe, utvrde na vrhu hridi, arheološka nalazišta, ili prirodne znamenitosti koje imaju značaj baštine za autohtone narode). b) Područje treba biti dovoljno veliko kako bi se zaštitio integritet značajke i okoline u neposrednoj blizini.', 0);
INSERT INTO property VALUES (1,5513, 'hr', 'definition','Oznaka zemljišta koja čuva prirodne resurse zbog njihove znanstvene, krajobrazne, kulturne i/ili obrazovne vrijednosti ograničavanjem razvoja i  praksama upravljanja. Zemljišta kojim se upravlja tako da se  zaštite rijetke i ugrožene vrste flore i faune će biti osmišljena kao prirodna područja.', 0);
INSERT INTO property VALUES (1,5518, 'hr', 'definition','Zračenje koje potječe uglavnom iz urana, prisutnog u malim količinama u mnogim stijenama, tlima, građevinskom materijalu itd.', 0);
INSERT INTO property VALUES (1,5520, 'hr', 'definition','Upravljanje živima i neživim resursima na takav način da se održi maksimalna korist za sadašnje i buduće naraštaje.', 0);
INSERT INTO property VALUES (1,5521, 'hr', 'definition','Značajka ili sastavnica prirodnog okoliša koja je vrijedna za služenje ljudskim potrebama, npr. tlo, voda, biljni i životinjski svijet itd. Neki prirodni resursi imaju ekonomsku vrijednost (npr. drvo), dok drugi imaju "ne-ekonomsku" vrijednost (npr. ljepotu).', 0);
INSERT INTO property VALUES (1,5523, 'hr', 'definition','Područje u kojem učinak čovjekovog djelovanja, ako ga ima, nije značajno za krajobraz u cjelini.', 0);
INSERT INTO property VALUES (1,5524, 'hr', 'definition','Grane znanosti koja se bavi objektivno mjerljivim pojavama koje se odnose na preobrazbu i odnose energije i materije; uključuju biologiju, fiziku i kemiju.', 0);
INSERT INTO property VALUES (1,5529, 'hr', 'definition','Dragulj koji se pojavljuje u prirodi, za razliku od umjetnih nadomjestaka koje izrađuje čovjek.', 0);
INSERT INTO property VALUES (1,5537, 'hr', 'definition','Aktivno upravljanje Zemljinim prirodnim resursima i okolišem kako bi se osiguralo da je njihova kvaliteta održana i da se razborito iskorištavaju.', 0);
INSERT INTO property VALUES (1,5538, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propiše vlada radi zaštite, očuvanja ili obnove prirodnih staništa ili ekosustava nekog područja.', 0);
INSERT INTO property VALUES (1,5539, 'hr', 'definition','Organizirana skupina radnji i postupaka, koje često vodi vladina agencija ili neprofitna organizacija, za očuvanje i zaštitu elemenata prirodnog svijeta, kao što su planine, drveće, životinje ili rijeke.', 0);
INSERT INTO property VALUES (1,5544, 'hr', 'definition','Radnje i postupci predostrožnosti ili postrojenja/instalacije namijenjena sprječavanju ili smanjenju štete za elemente materijalnog svijeta koje postoje neovisno o ljudskim aktivnostima.', 0);
INSERT INTO property VALUES (1,5545, 'hr', 'definition','Područja dodijeljena za očuvanje i zaštitu određenih životinja i biljaka, ili oboje. Ona se razlikuju od nacionalnog parka, koji su uglavnom mjesto za javnu rekreaciju, jer se osiguravaju isključivo za zaštitu vrsta, zbog njih samih. Ugrožene vrste sve se više čuvaju u prirodnim rezervatima kako bi se spriječilo njihovo izumiranje, a osobito u Indiji, Indoneziji i nekim afričkim zemljama. Prirodni rezervati su se nekada upotrebljavali za očuvanje životinja koje su zemljoposjednici lovili, ali u 19. stoljeću postali su mjesta gdje se životinje drže da se spriječi njihovo izumiranje. Posebna skloništa i utočišta također su često određena za zaštitu pojedinih vrsta ili skupina divljih životinja ili biljaka, osobito ako su njihov broj i rasprostranjenost značajno smanjeni. Ona također služe kao mjesto za odmor, razmnožavanje i prezimljavanje mnogih vrsta. Mnogi dijelovi svijeta također imaju morske i vodne rezerve za zaštitu različitih vrsta morskog ili slatkovodnog biljnog i životinjskog svijeta.', 0);
INSERT INTO property VALUES (1,5550, 'hr', 'definition','Svaka prepreka s kojom se susreće plovilo tijekom plovidbe ili koja predstavlja rizik ili opasnost za plovilo, njegov sadržaj ili okoliš.', 0);
INSERT INTO property VALUES (1,5554, 'hr', 'definition','Patološka smrt živog tkiva biljke ili životinje.', 0);
INSERT INTO property VALUES (1,5555, 'hr', 'definition','Činjenica ili slučaj kada osjećamo da nešto nedostaje.', 0);
INSERT INTO property VALUES (1,5565, 'hr', 'definition','Skupina nečlankastih crva koji se smatraju različitim kategorijama - redom, razredom i koljenom.', 0);
INSERT INTO property VALUES (1,5569, 'hr', 'definition','Sustav koordiniranja i integriranja koji djeluje u prilagodbi organizma na svoju okolinu; kod kralježnjaka, sustav se sastoji od mozga, moždanog debla, leđne moždine, lubanje i perifernih živaca te ganglija.', 0);
INSERT INTO property VALUES (1,5571, 'hr', 'definition','Ukupno smanjenje količine prirodnih materijala dostupnih za korištenje ljudima i drugim živim bićima.', 0);
INSERT INTO property VALUES (1,5572, 'hr', 'definition','Svi izvori emisije u istom području koji su u vlasništvu ili pod kontrolom jedne tvrtke smatraju se jednim velikim izvorom, a to omogućuje fleksibilnost u kontroli pojedinačnih izvora kako bi se zadovoljio standard pojedinačne emisije ispušnih plinova.', 0);
INSERT INTO property VALUES (1,5575, 'hr', 'definition','Pojava negativnih učinaka na živčani sustav nakon izlaganja kemikaliji.', 0);
INSERT INTO property VALUES (1,5577, 'hr', 'definition','Učiniti otopinu neutralnom dodavanjem lužine u kiselu otopinu, ili kiseline u lužnatu otopinu.', 0);
INSERT INTO property VALUES (1,5580, 'hr', 'definition','Društveno-politička, vjerska, strukovna ili druga skupina zajedničkih osobina i interesa oformljena kao alternativa za socijalne i, često stambene, opcije koje su trenutno na raspolaganju.', 0);
INSERT INTO property VALUES (1,5581, 'hr', 'definition','Nedavno instaliran, montiran ili izgrađen uređaj, sustav ili dio opreme.', 0);
INSERT INTO property VALUES (1,5583, 'hr', 'definition','Novi materijali velike učinkovitosti ili poboljšanih svojstava  dobiveni interdisciplinarnim istraživanjima kemije, primijenjene kemije, kemijskog inženjerstva i strojarstva.', 0);
INSERT INTO property VALUES (1,5585, 'hr', 'definition','Elektronički instrumenti i uređaji koji su nedavno razvijeni i uvedeni u industriju. Nove tehnologije često se uvode u gotovo potpunom neznanju ili nepoštivanju bioloških i ekoloških sustava koje oni kasnije narušavaju, te dinamične i razvojne prirode živih sustava.', 0);
INSERT INTO property VALUES (1,5589, 'hr', 'definition','Kovak, rastezljiv srebrno-bijeli metalni element jak i otporan na koroziju, koji se javlja uglavnom u pentlanditu i nikelitu: upotrebljava se u legurama, a osobito u povećanju žilavosti čelika, u galvanizaciji i kao katalizator u organskoj sintezi.', 0);
INSERT INTO property VALUES (1,5593, 'hr', 'definition','Sve soli ili esteri dušične kiseline, na primjer natrijev nitrat.', 0);
INSERT INTO property VALUES (1,5598, 'hr', 'definition','Proces kojim se amonijakovi spojevi, uključujući i umjetno gnojivo i humus koji se dobiva iz organskih tvari biljnog i životinjskog podrijetla, pretvaraju u nitrite, a zatim nitrate, koji se onda apsorbiraju kao hranjiva za usjeve.', 0);
INSERT INTO property VALUES (1,5601, 'hr', 'definition','Sol ili ester dušične kiseline, uključeni u spojeve kao što su kalijev nitrit, natrijev nitrit i butil nitrit.', 0);
INSERT INTO property VALUES (1,5604, 'hr', 'definition','Bilo koji od vrste obično organskih spojeva koji sadrže monovalentnu skupinu,-NO2 (nitro-skupina ili radikal) vezan za ugljikov atom.', 0);
INSERT INTO property VALUES (1,5605, 'hr', 'definition','Važan nutrijent (hranjiva tvar) u opskrbi biljaka hranom i ishrani životinja. Životinje ga dobivaju u spojevima koji sadrže dušik, osobito aminokiselinama. Iako se zrak sastoji od gotovo 80% dušika u plinovitom stanju, vrlo malo organizama je sposobno upotrijebiti ga u tom obliku. Više biljke obično ga dobivaju iz tla nakon što mikroorganizmi pretvore dušik u amonijak ili nitrate, koje zatim mogu apsorbirati.', 0);
INSERT INTO property VALUES (1,5607, 'hr', 'definition','Složen skup procesa kojima usjevi dobivaju veliku količinu dušika koja im je potrebna za proizvodnju bjelančevina, nukleinskih kiselina i drugih biokemijskih tvari od kojih se sastoje, te kojima se dušik vraća u atmosferu.', 0);
INSERT INTO property VALUES (1,5608, 'hr', 'definition','Crvenkasto-smeđi plin; postoji u različitim stupnjevima koncentracije u ravnoteži s drugim dušikovim oksidima; upotrebljava se za proizvodnju dušične kiseline.', 0);
INSERT INTO property VALUES (1,5611, 'hr', 'definition','Asimilacija atmosferskog dušika od strane raznih mikroorganizama koji žive slobodno u tlu. Nakon što se dušik unese u neki mikroorganizam, postoje mnogi različiti načini na koje se on upotrebljava. Jedan dio se zadržava u tlu kao raspadnuta biljna tvar, do ispuštanja, te ga preuzimaju novi usjevi kao nitrat. Taj nitrat proizvode dušične bakterije koje žive u tlu i koje se razvijaju na amonijaku, koji nastaje propadanjem biljnog i životinjskog materijala. U obradi dušika dušične bakterije proizvode nitrat, a njega može apsorbirati korijenje biljaka.', 0);
INSERT INTO property VALUES (1,5612, 'hr', 'definition','Bezbojni plin, topiv u vodi, etanolu i eteru. Nastaje u mnogim reakcijama koje uključuju redukciju dušične kiseline, ali praktičnije reakcije za pripremu umjereno čistog NO su reakcije natrijevog nitrita, sumporne kiseline itd.', 0);
INSERT INTO property VALUES (1,5613, 'hr', 'definition','Oksidi koji nastaju i ispuštaju se u svim uobičajenim vrstama izgaranja; oni nastaju oksidacijom atmosferskog dušika pri visokim temperaturama. Ispušteni u atmosferu iz automobilskih ispušnih cijevi, dimnjaka peći, spalionica, elektrana i sličnih izvora, oksidi uključuju didušikov oksid, dušikov oksid, dušikov dioksid, dušik pentoksid i dušičnu kiselinu. Oksidi dušika podvrgnuti su mnogim reakcijama u atmosferi te stvaraju fotokemijski smog.', 0);
INSERT INTO property VALUES (1,5618, 'hr', 'definition','Svaki iz vrste neutralnih, obično žutih, uljnih spojeva, koji sadrže dvovalentnu skupinu = NNO.', 0);
INSERT INTO property VALUES (1,5623, 'hr', 'definition','Akronim za ''najveća doza/koncentracija koja ne uzrokuje nikakav vidljiv štetni učinak'', ''razina (koncentracija) neprimijećenog učinka''.', 0);
INSERT INTO property VALUES (1,5624, 'hr', 'definition','Zvuk koji je nepoželjan, bilo zbog njegovih učinaka na ljude, njegov učinak na zamor ili kvar fizičke opreme, ili smetnje za percepciju ili otkrivanje drugih zvukova.', 0);
INSERT INTO property VALUES (1,5626, 'hr', 'definition','Mjere za smanjenje buke na izvoru, za poticanje tiših tehnologija ili opreme ili za sprječavanje ili smanjenje širenja zvuka. Mjere mogu uključivati izolaciju i prigušenje izvora vibracija; zamjenu komponenti tišim dijelovima i materijalima; zatvaranje (kućištima) posebno glasnih komponenti; postavljanje barijera za zaštitu od buke itd.', 0);
INSERT INTO property VALUES (1,5629, 'hr', 'definition','Određivanje sastavnih dijelova frekvencije koje čine određenu buku koja se proučava.', 0);
INSERT INTO property VALUES (1,5630, 'hr', 'definition','Pregrade za smanjenje širenja zvuka: one se uvelike upotrebljavaju u industriji i uz ceste i željezničke pruge za zaštitu prijemnika od izvora buke. Prepreke neće smanjiti buku na strani prijemnika, nego će je povećati, ako barijera  nije pokrivena i apsorbirajućim materijalom.', 0);
INSERT INTO property VALUES (1,5631, 'hr', 'definition','Postupak smanjenja čujnog zvuka na prihvatljivu razinu.', 0);
INSERT INTO property VALUES (1,5633, 'hr', 'definition','Buka ometa komunikaciju i ometa misaone procese. Buka ometa spavanje, izaziva bijes i frustraciju, a navodi se i kao jedan od uzročnika koji pridonose različitim psihološkim i fiziološkim problemima. Buka umanjuje kvalitetu života i okoliša.', 0);
INSERT INTO property VALUES (1,5635, 'hr', 'definition','Ispuštanje buke u okoliš iz različitih izvora koji se mogu grupirati u aktivnosti prijevoza, industrijske djelatnosti i uobičajene dnevne aktivnosti.', 0);
INSERT INTO property VALUES (1,5637, 'hr', 'definition','Obvezni iznos novca koje je kao plaćanje uvela vlada za proizvođače uznemirujućih, štetnih ili neželjenih zvukova, često u prijevoznoj djelatnosti ili građevinskoj industriji, radi poticanja smanjenje razina buke.', 0);
INSERT INTO property VALUES (1,5638, 'hr', 'definition','Zvuk se emitira i kao zvuk koji se širi po zraku i kao vibracijski; većina izvora proizvodi obje vrste zvuka, pa se moraju upotrebljavati razna načela slabljenja buke. Mjere uključuju: zamjenu komponenti tišim dijelovima i materijalima; zatvaranje (u kućišta) posebno glasnih komponenti; izbor tiših vrsta ventilatora; zamjenu bučnih mlaznica komprimiranog zraka tišim vrstama; izbor tiših sustava prijenosa i hlađenja.', 0);
INSERT INTO property VALUES (1,5640, 'hr', 'definition','Imisija u okoliš akustičnih vibracija koje negativno utječu na ljude, životinje, raslinje i druge predmete.', 0);
INSERT INTO property VALUES (1,5643, 'hr', 'definition','Zakonodavstvo koje su uvele mnoge vlade radi sprječavanja ili ograničenja emisije buke iz industrijskih, poslovnih i kućanskih prostora; od motornih vozila i zrakoplova; te od kućanskih aparata i opreme.', 0);
INSERT INTO property VALUES (1,5644, 'hr', 'definition','Fizikalna količina izmjerenog neželjenog zvuka, obično izraženog u decibelima.', 0);
INSERT INTO property VALUES (1,5646, 'hr', 'definition','Postupak kvantitativnog određivanja jednog ili više svojstava akustičke buke.', 0);
INSERT INTO property VALUES (1,5648, 'hr', 'definition','Sustavno raspoređivanje/upotreba opreme za praćenje radi otkrivanja ili kvantitativnog ili kvalitativnog mjerenja prisutnosti, učinka ili razine buke.', 0);
INSERT INTO property VALUES (1,5650, 'hr', 'definition','Buka u okolišu koja može biti štetna za ljude, životinje i biljke.', 0);
INSERT INTO property VALUES (1,5651, 'hr', 'definition','Štetni ili neželjeni zvukovi u okolišu, koji se na određenim mjestima mogu izmjeriti i uprosječiti tijekom nekoga vremenskog razdoblja.', 0);
INSERT INTO property VALUES (1,5653, 'hr', 'definition','Usvajanje mjera za kontrolu buke, kao što je ograničenje emisije buke iz industrijskih, poslovnih i kućanskih prostora, od motornih vozila i zrakoplova, postavljanje bukobrana i tampon zona, ugradnja opreme za smanjenje buke itd.', 0);
INSERT INTO property VALUES (1,5654, 'hr', 'definition','Smanjenje razine pritiska zvuka neke buke ili ublažavanje neželjenog zvuka bilo kojim sredstvom.', 0);
INSERT INTO property VALUES (1,5662, 'hr', 'definition','1) Pripadnik naroda ili plemena koje se seli od mjesta do mjesta u potrazi za ispašom i hranom. 
INSERT INTO property VALUES (1,5664, 'hr', 'definition','Sustav naziva/imena ili izraza, osobito onih koji su povezani s posebnim područjem znanosti ili umjetnosti, ili davanje naziva stvarima.', 0);
INSERT INTO property VALUES (1,5666, 'hr', 'definition','Organski spoj, obično sintetički, koji nije razgrađen ili  mineraliziran mikroorganizmima ili drugim biološkim  procesima.', 0);
INSERT INTO property VALUES (1,5667, 'hr', 'definition','Područja u kojima nema intenzivne izgradnje stambenih naselja, trgovina, industrijskih objekata itd.', 0);
INSERT INTO property VALUES (1,5668, 'hr', 'definition','Energija koja je obnovljiva i ekološki sigurna, kao na primjer energija plime i oseke, vjetra itd.', 0);
INSERT INTO property VALUES (1,5669, 'hr', 'definition','Industrija prerade metala koji nisu željezo ni željezne legure.', 0);
INSERT INTO property VALUES (1,5670, 'hr', 'definition','Svi metali koji nisu željezo ni željezne legure.', 0);
INSERT INTO property VALUES (1,5671, 'hr', 'definition','Privatne, dobrovoljne, neprofitne organizacije, koje djeluju kao skupine za pritisak. Diljem svijeta postoji više od 5.000 međunarodnih nevladinih organizacija koje se bave okolišem i razvojem, s milijunima pristaša.', 0);
INSERT INTO property VALUES (1,5674, 'hr', 'definition','Zračenje koje ne mijenja strukturu atoma, ali zagrijava tkivo i može prouzročiti štetne biološke učinke.', 0);
INSERT INTO property VALUES (1,5675, 'hr', 'definition','Minerali koji sadrže nemetale, kao što je kvarc, granat itd.', 0);
INSERT INTO property VALUES (1,5676, 'hr', 'definition','Nemetalni materijal, kao što su arsen ili silikon, koji ima neka obilježja metala.', 0);
INSERT INTO property VALUES (1,5677, 'hr', 'definition','Energija koja je ekološki sigurna i dobiva se iz obnovljivih izvora. Najčešće korišteni takav izvor su hidroelektrane, koje trenutno zadovoljavaju približno 6,6% svjetskih potreba za energijom. Ostali izvori energije koji ne onečišćuju okoliš su solarna energija, energija plime i oseke, energija valova i vjetra. Za većinu izvora energije koji ne onečišćuju okoliš potrebne su visoke kapitalne investicije, ali su tekući troškovi niski.', 0);
INSERT INTO property VALUES (1,5679, 'hr', 'definition','Čisto gorivo, koje u okoliš ne ispušta onečišćujuće tvari, kao što je npr. Metan.', 0);
INSERT INTO property VALUES (1,5684, 'hr', 'definition','Neobnovljivi izvori energije su izgrađeni ili su se razvili tijekom geoloških razdoblja i ne mogu se upotrebljavati bez osiromašenja zaliha; također se postavlja pitanje njhove iscrpljivosti, budući da je brzina njihovog stvaranja tako spora da se uopće ne može usporediti s trajanjem ljudskog života.', 0);
INSERT INTO property VALUES (1,5685, 'hr', 'definition','Prirodni resurs koji, u smislu trajanja ljudskog života, se nalazi unutar Zemlje u fiksnoj količini i stoga se može upotrijebiti samo jednom u doglednoj budućnosti (iako se može reciklirati nakon njegove prve uporabe). Ti resursi uključuju fosilna goriva, a obuhvaćaju i mineralne resurse, a ponekad i podzemne vode, iako se voda i mnoge minerale u konačnici obnavljaju.', 0);
INSERT INTO property VALUES (1,5686, 'hr', 'definition','Područje s trgovačkim, industrijskim i javnim objektima.', 0);
INSERT INTO property VALUES (1,5687, 'hr', 'definition','Svaka ambalaža za koju nije predviđen povrat od kupca niti njezina konačna upotreba.', 0);
INSERT INTO property VALUES (1,5689, 'hr', 'definition','Biljka ili životinja koja nije ona zbog koje se upotrebljava  pesticid.', 0);
INSERT INTO property VALUES (1,5690, 'hr', 'definition','Utvrđeni standard, smjernica ili propis. Načelo ili propis što ga donesu vlasti, a propisuje i usmjerava djelovanja ili oprost od nečega; npr. pravila zakonodavnog tijela, poduzeća, suda, javne funkcije, zakona/prava ili etike.', 0);
INSERT INTO property VALUES (1,5692, 'hr', 'definition','Geografska regija afričkog kontinenta južno od Europe i Sredozemnog mora, te sjeverno od afričke tropske prašume, koja uključuje Maroko, Alžir, Tunis, Libiju i egipatsku regiju zapadno od Sueskog kanala, kao i pustinje Saharu i gorje Atlas.', 0);
INSERT INTO property VALUES (1,5693, 'hr', 'definition','Kontinent na sjevernoj polovici zapadne hemisfere, omeđen Arktičkim oceanom na sjeveru, Tihim oceanom i Beringovim morem na zapadu, te Atlantskim oceanom i Meksičkim zaljevom na istoku, povezan s Južnom Amerikom Panamskom prevlakom, a obuhvaća Sjedinjene Države, Kanadu, Meksiko i nekoliko malih otočkih država.', 0);
INSERT INTO property VALUES (1,5694, 'hr', 'definition','Sjeverni dio Atlantika, koji se prostire sjeverno od ekvatora do Arktičkog mora.', 0);
INSERT INTO property VALUES (1,5695, 'hr', 'definition','Ocean sjeverno od ekvatora između istočne obale Azije i zapadne obale Sjeverne i Južne Amerike, koji se širi prema sjeveru do arktičke regije; glavni rukavci uključuju Aljaski zaljev, Okhotsko more, Japansko more te Beringovo, Žuto, Istočno kinesko, Južno kinesko i Filipinsko more, te Aleutske otoke, Midway, Marshallovo otočje i havajsko otočje, japanski otočki luk i Malajski arhipelag', 0);
INSERT INTO property VALUES (1,5701, 'hr', 'definition','Genetski modificirane namirnice. Nova hrana, uključujući i onu koja je promijenjena korištenjem biotehnologije, ne bi se trebala ''značajno'' razlikovati od hrane koju ona treba zamijeniti. Oznake ne bi trebale dovoditi u zabludu, ali se na njima moraju jasno istaći bilo kakve razlike između nove hrane i njezine ''konvencionalne'' alternativne, i mora se navesti kako je ta razlika postignuta. Hrana koja sadrži genetski modificirani živi organizam, kao što je ''živi'' jogurt načinjen s promijenjenom kulturom, uvijek bi morala biti označena. Svaka hrana čija modifikacija može izazvati zabrinutost u smislu morala ili bojazan za zdravlje potrošača također bi morala nositi oznaku. Oznaka bi morala uključiti gene iz životinje koju neke religije smatraju nečistom, ili iz biljke koja bi mogla izazvati alergijske reakcije. Međutim, namirnice koje su, iako su proizvedene pomoću nove metode, identične konvencionalnoj hrani, ne bi trebale biti označene.', 0);
INSERT INTO property VALUES (1,5708, 'hr', 'definition','Događaj koji nastane u nuklearnoj elektrani ili na bilo kojem mjestu gdje se radioaktivni materijali upotrebljavaju, pohranjuju ili prevoze, i kod kojeg je uključeno puštanje potencijalno opasne razine radioaktivnog materijala u okoliš.', 0);
INSERT INTO property VALUES (1,5712, 'hr', 'definition','Energija koja se oslobađa nuklearnom fisijom ili nuklearnom fuzijom.', 0);
INSERT INTO property VALUES (1,5714, 'hr', 'definition','Nuklearna energija se upotrebljava u industrijskom sektoru, u proizvodnji ostalih vrsta energije, u području medicinskih i znanstvenih istraživanja, prijevozu, proizvodnji nuklearnog oružja itd.', 0);
INSERT INTO property VALUES (1,5718, 'hr', 'definition','Nenamjerno oslobađanje energije iz brze reakcije atomskih jezgri pri kojoj nastaju visoke temperature i zračenja potencijalno štetna za ljudsko zdravlje i okoliš.', 0);
INSERT INTO property VALUES (1,5721, 'hr', 'definition','Mjesto, uključujući zgrade, na kojem se obavljaju sve aktivnosti u vezi s nuklearnim istraživanjima.', 0);
INSERT INTO property VALUES (1,5722, 'hr', 'definition','Podjela atomske jezgre u dijelove usporedive mase; obično je  ograničena na teže jezgre, kao što su izotopi urana, plutonija i torija.', 0);
INSERT INTO property VALUES (1,5724, 'hr', 'definition','Nuklearna goriva dobivaju se iz anorganskih minerala koja se vade putem rudarstva. Iako se ona barem djelomično iskoriste upotrebom u nuklearnim reaktorima za proizvodnju topline, razlikuju se od fosilnih goriva prema načinu na koji oslobađaju energiju. Izgaranje fosilnih goriva, kao što su ugljen, nafta i plin, kemijska je reakcija. Nuklearna goriva, kao što je uran, uništavaju se procesom spontanog raspadanja, koji se zove fisija, a potaknut je prirodnom radioaktivnosti. Ako se proces ostavi da se dogodi prirodno u stijenama koje sadrže uran, brzina promjene je tako mala da se ne primjećuje. U nuklearnom reaktoru koji je stvorio čovjek procesi raspadanja pri kojima se oslobađa energija, koji se u prirodnom stanju događaju polako, tijekom tisuća milijuna godina, sažeti su u nekoliko minuta. Oslobađanje energije se iskorištava za stvaranje pare koja pokreće generatore električne energije.', 0);
INSERT INTO property VALUES (1,5727, 'hr', 'definition','Dio nuklearnog goriva, koji je oblikovan i obložen, i koji je spreman da se stavi u sklop nuklearnog reaktora.', 0);
INSERT INTO property VALUES (1,5728, 'hr', 'definition','Spajanje dvije lake atomske jezgre kojim nastaje teža jezgra i pri tome se oslobađa nešto vezne energije.', 0);
INSERT INTO property VALUES (1,5733, 'hr', 'definition','Proučavanje značajki, ponašanja i unutarnjih struktura atomske jezgre.', 0);
INSERT INTO property VALUES (1,5734, 'hr', 'definition','Javna rasprava i spor o upotrebi energije koji još traju.', 0);
INSERT INTO property VALUES (1,5736, 'hr', 'definition','Elektrane u kojima se nuklearna energija pretvara u toplinu za uporabu u proizvodnji pare za turbine, koje, pak, pokreću generatore koji proizvode struju.', 0);
INSERT INTO property VALUES (1,5741, 'hr', 'definition','Reakcija koje uključuju promjene u atomskoj jezgri, kao što su fisija, fuzija, hvatanje neutrona ili radioaktivno raspadanje, za razliku od kemijske reakcije, koja je ograničena na promjene u elektronskoj strukturi oko jezgre.', 0);
INSERT INTO property VALUES (1,5742, 'hr', 'definition','Naprava koja stvara toplinu i energiju započinjanjem i kontroliranjem atomske fisije.', 0);
INSERT INTO property VALUES (1,5745, 'hr', 'definition','Objekt u kojem znanstvenici i drugi istraživači proučavaju ponašanje i značajke atomskih jezgara ispitivanjem i drugim  oblicima eksperimentiranja, često kako bi izmislili nove tehnologije sa znanstvenim, medicinskim i industrijskim svrhama.', 0);
INSERT INTO property VALUES (1,5746, 'hr', 'definition','Mjere i tehnike koje se provode kako bi se smanjila mogućnost učestalosti i potencijalne štete koju predstavljaju radioaktivne tvari koje se upotrebljavaju kao izvor energije,  materijal za ispitivanje ili u naoružanju.', 0);
INSERT INTO property VALUES (1,5747, 'hr', 'definition','Pokus za ocjenjivanje nuklearnog oružja.', 0);
INSERT INTO property VALUES (1,5750, 'hr', 'definition','Svaka bomba, bojna glava ili projektil koji upotrebljava aktivni nuklearni materijal da izazove lančanu reakciju nakon detonacije.', 0);
INSERT INTO property VALUES (1,5751, 'hr', 'definition','Svaka od nekoliko organskih kiselina u kombinaciji s proteinima (DNK ili RNK) koji postoje u jezgri i protoplazmi svih stanica.', 0);
INSERT INTO property VALUES (1,5752, 'hr', 'definition','Sve što utječe na zdravlje ili mu škodi.', 0);
INSERT INTO property VALUES (1,5755, 'hr', 'definition','Mjesto na kojem se biljke uzgajaju dok nisu dovoljno velike da se zasade na konačnom mjestu.', 0);
INSERT INTO property VALUES (1,5756, 'hr', 'definition','Podloga koja osigurava ishranu ili joj pridonosi.', 0);
INSERT INTO property VALUES (1,5757, 'hr', 'definition','Stanje u kojem postoji ravnoteža između unosa i izlučivanja hranjivih tvari.', 0);
INSERT INTO property VALUES (1,5758, 'hr', 'definition','Količina bjelančevina, ugljikohidrata, anorganskih soli (npr. nitrata, fosfata), minerala (npr. kalcija, željeza) i vode.', 0);
INSERT INTO property VALUES (1,5759, 'hr', 'definition','Biogeokemijsko kruženje, u kojem se anorganske hranjive tvari kreću kroz tlo, žive organizme, vode i zrak ili kroz neke od njih.', 0);
INSERT INTO property VALUES (1,5761, 'hr', 'definition','Uklanjanje hranjivih tvari, na primjer, iz kanalizacije kako bi se spriječila eutrofikacija vode u spremnicima.', 0);
INSERT INTO property VALUES (1,5763, 'hr', 'definition','Kemijski elementi koji su uključeni u izgradnju živog tkiva i koji su potrebni i biljkama i životinjama. Prema količini su najvažniji  ugljik, vodik i kisik, a drugi bitni elementi uključuju dušik, kalij, kalcij, sumpor i fosfor.', 0);
INSERT INTO property VALUES (1,5765, 'hr', 'definition','Proces u životinjama i biljkama koji uključuje unos hranjivih tvari i njihovu naknadnu asimilaciju u tkiva.', 0);
INSERT INTO property VALUES (1,5769, 'hr', 'definition','Mjera količine ili dostupnost hranjivih tvari koje se nalaze u materijalima što ih gutaju i iskorištavaju ljudi ili životinje kao izvor prehrane i energije.', 0);
INSERT INTO property VALUES (1,5774, 'hr', 'definition','Bilo koje stablo roda Quercus u redu Fagales, koja se odlikuju jednostavnim lišćem obično s resicama, zimskim pupoljcima pokrivenim ljuskama, srčikom u obliku zvijezde, te imaju plod, žir, koji je vrsta oraha; drvo je teška, tvrdo i trajno i općenito imaju izrazit uzorak.', 0);
INSERT INTO property VALUES (1,5776, 'hr', 'definition','Čin stranke koja se protivi nekoj stvari ili postupku u tijeku suđenja ili argument ili razlog koji ona navodi u potporu svoje tvrdnje da je stvar ili postupak na koji je uložila prigovor nepravilna ili nezakonita.', 0);
INSERT INTO property VALUES (1,5778, 'hr', 'definition','Pravna odgovornost ili dužnost kojom se  proizvođači prisiljavaju da stave neke oznake ili druge pisane identifikacije na svoje proizvode, kako to nalažu zakoni, propisi ili vladini standardi.', 0);
INSERT INTO property VALUES (1,5781, 'hr', 'definition','Funkcionalna ili organska bolest prouzročena čimbenicima koji proizlaze iz poslovanja ili materijala s kojima pojedinac radi u industriji, trgovini ili zanimanju u kojem je zaposlen.', 0);
INSERT INTO property VALUES (1,5783, 'hr', 'definition','Skup ljudi koji zarađuju za život služeći se sličnim ili istim sredstvima rada.', 0);
INSERT INTO property VALUES (1,5784, 'hr', 'definition','Područje zakonske obveze propisane za poslodavce i zaposlenike u većini zemalja, u svrhu zaštite radne snage od profesionalnih bolesti i stresa te fizičkih opasnosti putem odgovarajućeg planiranja, prozračivanja, rasvjete, zaštite, sigurnosti i postupaka u hitnim situacijama, redovnih pregleda, nadzora, osobne zaštite itd.', 0);
INSERT INTO property VALUES (1,5786, 'hr', 'definition','Grana medicine koja se bavi odnosom ljudi i njihovih zanimanja, u svrhu prevencije bolesti i ozljeda i promicanja optimalnog zdravlja, produktivnosti i socijalne prilagodbe.', 0);
INSERT INTO property VALUES (1,5787, 'hr', 'definition','Područje zakonske obveze propisane za poslodavce i zaposlenike u većini zemalja, u svrhu zaštite radne snage od profesionalnih bolesti i stresa te fizičkih opasnosti putem odgovarajućih mjera.', 0);
INSERT INTO property VALUES (1,5788, 'hr', 'definition','Zakon donesen kako bi se smanjila učestalost ozljeda, obolijevanja i smrtnih slučajeva radnika kao posljedica rada.', 0);
INSERT INTO property VALUES (1,5789, 'hr', 'definition','Vodena  masa koja zauzima cijelu površinu Zemlje koja nije kopno, ali isključujući sva jezera i mora u unutrašnjosti.', 0);
INSERT INTO property VALUES (1,5791, 'hr', 'definition','Strujanje toka vode u zatvorenom kružnom gibanju unutar oceana.', 0);
INSERT INTO property VALUES (1,5792, 'hr', 'definition','Čisti protok oceanske vode duž određenog puta.', 0);
INSERT INTO property VALUES (1,5793, 'hr', 'definition','Proces kojim se onečišćujuće tvari, uključujući kanalizaciju, industrijski otpad, potrošački otpad, te otjecanje s poljoprivrednih površina i iz gradova ispuštaju u svjetske oceane. Te tvari pojavljuju se iz mnogobrojnih izvora.', 0);
INSERT INTO property VALUES (1,5794, 'hr', 'definition','Otoci južnog, zapadnog i srednjeg područja Tihog oceana, uključujući Melaneziju, Mikroneziju i Polineziju. Ovaj izraz ponekad se proširuje tako da obuhvaća i Australiju, Novi Zeland i Malajski arhipelag.', 0);
INSERT INTO property VALUES (1,5797, 'hr', 'definition','Znanstveno proučavanje i istraživanje svih vidova oceana i mora.', 0);
INSERT INTO property VALUES (1,5799, 'hr', 'definition','Mjera topline ili hladnoće u oceanskom vodnom tijelu u odnosu prema standardnoj vrijednosti.', 0);
INSERT INTO property VALUES (1,5804, 'hr', 'definition','Svojstvo tvari koja djeluje na osjetilo mirisa; bilo kakav miris; ugodan miris; parfem.', 0);
INSERT INTO property VALUES (1,5809, 'hr', 'definition','Nezakonite radnje protiv okoliša, kao što je onečišćenje vode, odlaganje opasnog otpada, onečišćenje zraka, gradnja postrojenja bez potrebnih dozvola, izlijevanja nafte itd.', 0);
INSERT INTO property VALUES (1,5810, 'hr', 'definition','Svaka prostorija, skup prostorija ili zgrade koja služi za obavljanje državne službe, poslovne transakcije i druge poslovne aktivnosti.', 0);
INSERT INTO property VALUES (1,5812, 'hr', 'definition','Relativno formalan postupak, s određenim činjeničnim pitanjima ili pravnim pitanjima o kojima treba provesti suđenje, u kojem se saslušavaju svjedoci i optužene stranke u postupku imaju pravo biti saslušane.', 0);
INSERT INTO property VALUES (1,5814, 'hr', 'definition','Redovno putovanje na nekoj udaljenosti i natrag, izvan sati najveće gustoće prometa.', 0);
INSERT INTO property VALUES (1,5816, 'hr', 'definition','U vezi s putovanjem izvan najprometnijih sati sredstvima javnog prijevoza radi izbjegavanja gužve.', 0);
INSERT INTO property VALUES (1,5820, 'hr', 'definition','Politika kojom se emisije predloženih novih ili modificiranih stacionarnih izvora uravnotežuju smanjenjem iz postojećih izvora radi stabiliziranja ukupnih emisija.', 0);
INSERT INTO property VALUES (1,5823, 'hr', 'definition','Vađenje nafte s platformi postavljenih u blizini obale.', 0);
INSERT INTO property VALUES (1,5824, 'hr', 'definition','Radnja ili postupak vađenja nafte iz nalazišta ispod dna oceana ili nekoga drugog velikoga vodnog tijela.', 0);
INSERT INTO property VALUES (1,5825, 'hr', 'definition','Aktivnosti koje se odvijaju ili su smještene dalje od neke (uobičajene) lokacije.', 0);
INSERT INTO property VALUES (1,5827, 'hr', 'definition','Visoko upijajuća sredstva koja se upotrebljavaju za fizičko uklanjanje izlivene nafte u slučaju istjecanja nafte i nesreća s naftom u vodnim tijelima, industriji, radionicama, na cestama itd. Materijali za koje je utvrđeno da su korisni za to mogu biti različiti, od jednostavnih materijala koji se pojavljuju u prirodi kao što su slama, piljevina i treset, do sintetičkih tvari, kao što su poliuretanska pjena i polistirenski prah.', 0);
INSERT INTO property VALUES (1,5829, 'hr', 'definition','Energija proizvedena pomoću nafte kao goriva.', 0);
INSERT INTO property VALUES (1,5830, 'hr', 'definition','Plutajući uređaj za zadržavanje nafte na vodnom tijelu (plutajuća barijera, privremena brana, vodobran). Nakon što se uređaj napumpa, tegli se niz vjetar od naftne mrlje i oblikuje se u oblik slova U; pod utjecajem vjetra nafta ostaje ''zarobljena'' unutar te barijere. Oprema za uklanjanje nafte se postavlja unutar barijere, a nafta se crpljenjem prebacuje u spremnike.', 0);
INSERT INTO property VALUES (1,5831, 'hr', 'definition','Katastrofa prouzročena odlaganjem i slučajnim izlijevanjem nafte u plovne putove iz brodova i postrojenja na kopnu ili u priobalju. Onečišćenje naftom može uništiti ili nanijeti štetu životu u vodi te životinjskom i biljnom svijetu koji živi u divljini, kao što su ptice, onečistiti opskrbu vodom i stvoriti opasnost od vatre.', 0);
INSERT INTO property VALUES (1,5834, 'hr', 'definition','Vađenje nafte površinskim kopovima, kao kod katranskog pijeska ili naftnog škriljevca, ili iz tunela u plitkom spremniku.', 0);
INSERT INTO property VALUES (1,5835, 'hr', 'definition','Industrija za proizvodnju i preradu jestivih ulja i masti.', 0);
INSERT INTO property VALUES (1,5836, 'hr', 'definition','Cjevovod za prijenos nafte, spojen s ventilima i drugim kontrolnim uređajima.', 0);
INSERT INTO property VALUES (1,5837, 'hr', 'definition','Onečišćenje bilo kojeg ekosustava, ali obično slatkovodnih i morskih ekosustava, naftom ili naftnim derivatima.', 0);
INSERT INTO property VALUES (1,5838, 'hr', 'definition','Postoje različiti sustavi za smanjenje onečišćenja naftom na moru: sustav "punjenja s vrha" uključuje propuštanje prljave vode od čišćenja iz spremnika za pranje i ostataka od ispuštanja balastnih voda u prazan spremnik za teret, neku vrstu "prljavog spremnika". Teret svježe nafte ukrcava se na vrhu konačnog ostatka koji je preostao nakon daljnjih ispuštanja vode, a smjesa koja tako nastaje prihvatljiva je za rafinerije, unatoč nekim dodatnim troškovima za uklanjanje soli i vode. Prema Međunarodnoj konvenciji o sprječavanju onečišćenja s brodova iz 1973. god., svi brodovi za prijevoz nafte morat će biti u stanju raditi s ovom metodom zadržavanja, ili, pak, ispuštati nečiste ostatke u objekte za njihov prihvat. Druga metoda sastoji se u raspršivanju raspršivača na naftu i/ili puhanju  slame i piljevine, koje funkcioniraju kao "upijajući papir (bugačica)", na vodu, plaže, stijene i pristaništa. Sustav Vikoma za zadržavanje naftnih mrlja na moru, što ga je razvio British Petroleum, a to je plutajuća barijera (vodobran, hvatač onečišćenja) dužine od oko 500 metara, napuše se i dotegli niz vjetar od naftne mrlje i formira se u obliku slova U; pod utjecajem vjetra nafta ostaje ''zarobljena'' unutar plutajuće barijere. Oprema za uklanjanje nafte dovozi se u prostor ograđen plutajućom barijerom i nafta se crpljenjem prenosi u spremnike.', 0);
INSERT INTO property VALUES (1,5839, 'hr', 'definition','Brodice za skupljanje nafte koju su tankeri prolili na moru. Preporučeni postupak je ograničavanje i fizičko skupljanje izlivene nafte, uz uporabu apsorbenata ili bez njih. Ovaj pristup uključuje tri procesa:) ograničavanje izlijevanja s pomoću plutajućih barijera, b) skupljanje izlivene nafte s pomoću apsorbenata, c) fizičkim uklanjanjem onečišćene nafte uređajima za prikupljanje.', 0);
INSERT INTO property VALUES (1,5840, 'hr', 'definition','Sustav procesnih jedinica koje se upotrebljavaju za pretvaranje sirove nafte u goriva, maziva i ostale naftne derivate.', 0);
INSERT INTO property VALUES (1,5841, 'hr', 'definition','Razdvajanje mješavina nafte na njihove sastavne dijelove.', 0);
INSERT INTO property VALUES (1,5843, 'hr', 'definition','Oporaba nafte koja je zaostala ili preostala, obično nakon prvotnih operacija zadržavanja razlijevanja naftne mrlje.', 0);
INSERT INTO property VALUES (1,5847, 'hr', 'definition','Fino laminirana smeđa ili crna sedimentna stijena koja sadrži kerogen, čijom destilacijom nastaju tekući ili plinoviti ugljikovodici.', 0);
INSERT INTO property VALUES (1,5848, 'hr', 'definition','Velika količina nafte koja pluta po površini vode, koja je ispuštena slučajno, prirodnim putem ili namjerno, i koju mogu pokretati struje, plima i oseka te vjetar.', 0);
INSERT INTO property VALUES (1,5852, 'hr', 'definition','Slučajno ispuštanje nafte ili naftnih derivata obično u slatkovodne ili morske ekosustave, i obično u velikim količinama. Može se kontrolirati raspršivanjem kemikalija, izgaranjem, mehaničkim zadržavanjem i apsorpcijom.', 0);
INSERT INTO property VALUES (1,5854, 'hr', 'definition','Vrlo veliki brod koji prevozi sirovu naftu ili naftne derivate u velikim spremnicima.', 0);
INSERT INTO property VALUES (1,5856, 'hr', 'definition','Odlagalište koja je bila popunjeno i pokriveno tlom i zasijano. Najčešća krajnja upotreba odlagališta otpada su otvoreni prostori na kojima se ne održava nikakva aktivna rekreacija na zatvorenom odlagalištu. Očigledan razlog za takvu upotrebu je to što je površina zatvorenog odlagališta strmo nagnuta pa s nje mogu brzo otjecati procjedne vode. Također, ne bi se smjelo dopustiti nikakvo navodnjavanje pokrovne trave. Postoji vrlo mala vjerojatnost da će na zatvorenom odlagalištu biti izgrađeni komercijalni ili industrijski objekti. Ako je krajnja upotreba takva da će ljudi hodati po lokaciji starog odlagališta, važno je da svi šahtovi budu propisno osigurani, lagune procjednih voda ograđene, te da druge potencijalne opasnosti budu uklonjene.', 0);
INSERT INTO property VALUES (1,5857, 'hr', 'definition','Bilo koje mjesto koje je onečišćeno ili nesigurno ispuštanjem visokih koncentracija opasnih i štetnih tvari u svoje vode, tlo ili atmosferu.', 0);
INSERT INTO property VALUES (1,5858, 'hr', 'definition','Staro postrojenje koje ne reagira na nova pravila za sprječavanje onečišćenja okoliša i za čiji novi razvoj su potrebna ulaganja za usvajanje tehnologija za zaštitu plovnih putova, gospodarenje otpadom, smanjenje buke i kontrolu emisija.', 0);
INSERT INTO property VALUES (1,5862, 'hr', 'definition','Onečišćenje koje proizvode plinovite emisije u atmosferu koje, čak i u vrlo malim količinama, može prouzročiti ozljede ili stanje opće nelagode ili bolesti osobama koje žive u blizini.', 0);
INSERT INTO property VALUES (1,5863, 'hr', 'definition','Ispitivanje i mjerenje osjetljivosti osjeta mirisa.', 0);
INSERT INTO property VALUES (1,5869, 'hr', 'definition','Infekcija parazitom Onchocerca volvulus; njezine posljedice su tumori kože, bubuljičast dermatitis i očne komplikacije.', 0);
INSERT INTO property VALUES (1,5870, 'hr', 'definition','Proučavanje uzroka, razvoja, značajki i liječenja tumora.', 0);
INSERT INTO property VALUES (1,5874, 'hr', 'definition','Vađenje metalne rude i minerala koji se nalaze blizu površine uklanjanjem materijala koji leži iznad njih te razbijanjem i tovarenjem rude.', 0);
INSERT INTO property VALUES (1,5875, 'hr', 'definition','Ribolov na najdubljim dijelovima mora.', 0);
INSERT INTO property VALUES (1,5877, 'hr', 'definition','Podaci koji se odnose na praktično izvođenje procesa.', 0);
INSERT INTO property VALUES (1,5880, 'hr', 'definition','Prosudba ili uvjerenje koje se ne temelji na izvjesnosti ili dokazu.', 0);
INSERT INTO property VALUES (1,5885, 'hr', 'definition','Prilagodbe na zgradi ili mehaničkom ili električnom sustavu ili uređaju kako bi se maksimalno povećala njegova funkcionalnost i učinkovitost.', 0);
INSERT INTO property VALUES (1,5887, 'hr', 'definition','Mineral ili mineralni agregat, više ili manje pomiješan s  jalovinom, koji se može korisno obraditi u neku svrhu.', 0);
INSERT INTO property VALUES (1,5890, 'hr', 'definition','Potpuno diferencirana strukturna i funkcionalna jedinica, kao što je bubreg ili korijen, kod životinje ili biljke.', 0);
INSERT INTO property VALUES (1,5892, 'hr', 'definition','Ugljik koji nastaje u životinji ili biljci.', 0);
INSERT INTO property VALUES (1,5893, 'hr', 'definition','Grana kemije koja se bavi proučavanjem sastava, reakcije, svojstava, itd. organskih spojeva.', 0);
INSERT INTO property VALUES (1,5897, 'hr', 'definition','Uzgoj bez upotrebe umjetnih gnojiva ili pesticida.', 0);
INSERT INTO property VALUES (1,5902, 'hr', 'definition','Onečišćujuća tvar koju proizvode biljke ili životinje, koja sadrži uglavnom ugljik, vodik i kisik.', 0);
INSERT INTO property VALUES (1,5910, 'hr', 'definition','Organski materijali, uključujući razrjeđivače, koji su tekućine pri standardnim uvjetima, a koji se upotrebljavaju kao otapala, reduktori viskoznosti, odnosno sredstva za čišćenje.', 0);
INSERT INTO property VALUES (1,5912, 'hr', 'definition','Kemijski spojevi, koje se temelje na ugljikovim lancima ili prstenovima i koji također sadrže vodik, sa kisikom, dušikom ili drugim elementima ili bez njih.', 0);
INSERT INTO property VALUES (1,5913, 'hr', 'definition','Otpad koji sadrži ugljikove spojeve; dobiva se iz tvari životinjskog i biljnog porijekla.', 0);
INSERT INTO property VALUES (1,5914, 'hr', 'definition','Jedinka izgrađena tako da obavlja sve životne funkcije.', 0);
INSERT INTO property VALUES (1,5921, 'hr', 'definition','Organski spojevi koji sadrže vezu C-Cl.', 0);
INSERT INTO property VALUES (1,5924, 'hr', 'definition','Organski spoj koji sadrži vezu C-halogena.', 0);
INSERT INTO property VALUES (1,5927, 'hr', 'definition','Molekule koje sadrže vezu ugljika i metala; spoj koji sadrži  alkil radikal ili aril radikal vezan s metalom.', 0);
INSERT INTO property VALUES (1,5928, 'hr', 'definition','Organski spoj s vezom C-N.', 0);
INSERT INTO property VALUES (1,5929, 'hr', 'definition','Alifatski i aromatski spojevi s vezom C-O, uključujući alkohole, aldehide itd.', 0);
INSERT INTO property VALUES (1,5931, 'hr', 'definition','Pesticidi koji sadrže fosfor; kratkotrajni, ali neki pri prvoj upotrebi mogu biti otrovni.', 0);
INSERT INTO property VALUES (1,5935, 'hr', 'definition','Svaka prirodna tvar koja se sastoji od dva ili više različita atoma koje zajedno drže kemijske veze i koja sadrži silicij, nemetalni element koji se često nalazu u stijenama i mineralima.', 0);
INSERT INTO property VALUES (1,5936, 'hr', 'definition','Vrsta skupine tvari koje sadrže i ugljik i sumpor.', 0);
INSERT INTO property VALUES (1,5937, 'hr', 'definition','Kemijski spojevi koji se upotrebljavaju u bojama protiv obraštaja za zaštitu trupova brodica i brodova, plutača i stupova od morskih organizama kao što su lupari.', 0);
INSERT INTO property VALUES (1,5942, 'hr', 'definition','Grana zoologije koja se bavi proučavanjem ptica, uključujući njihovu fiziologiju, klasifikaciju, ekologiju i ponašanje.', 0);
INSERT INTO property VALUES (1,5944, 'hr', 'definition','Rijetko upotrebljavana riječ za proučavanje planinskih sustava i opis njihovog reljefa.', 0);
INSERT INTO property VALUES (1,5945, 'hr', 'definition','Heterogeni red kukaca koji se postupno preobražavaju, s ustima za  žvakanje i četiri krila.', 0);
INSERT INTO property VALUES (1,5949, 'hr', 'definition','Prolaz otapala kroz polupropusnu membranu koja odvaja dvije otopine različitih koncentracija. Polupropusna membrana je takva membrana kroz koju mogu proći molekule otapala, ali molekule većine otopljenih tvari ne mogu. Postoji termodinamička sklonost da otopine odvojene takvom membranom postanu jednake prema koncentraciji, pri čemu voda (ili drugo otapalo) teče od slabije otopine prema jačoj. Osmoza će prestati kada se izjednače koncentracije dvije otopine, a također se može zaustaviti primjenom tlaka na tekućinu na strani membrane s jačom otopinom. Tlak potreban da se zaustavi tok iz čistog otapala u otopinu je obilježje otopine, a zove se osmotski tlak. Osmotski tlak ovisi samo o koncentraciji čestica u otopini, a ne o njihovoj vrsti.', 0);
INSERT INTO property VALUES (1,5953, 'hr', 'definition','Razonoda, zabava, vježbe ili drugo sredstvo uživanja i opuštanja koje se provodi u određenom gradu, mjestu ili malom okrugu.', 0);
INSERT INTO property VALUES (1,5956, 'hr', 'definition','Materijal kao što je tlo i stijena koja leži iznad mineralnih nalazišta koja se mora ukloniti da bi se došlo do nalazišta minerala.', 0);
INSERT INTO property VALUES (1,5957, 'hr', 'definition','Prevelik broj ljudi okupljen na jednom mjestu ograničene veličine.', 0);
INSERT INTO property VALUES (1,5958, 'hr', 'definition','Pretjerano iskorištavanje sirovina bez uzimanja u obzir dugoročnih ekoloških učinaka takvog iskorištavanja.', 0);
INSERT INTO property VALUES (1,5959, 'hr', 'definition','Stavljanje previše gnojiva na zemljište; otjecanje s prekomjerno pognojene zemlje može prouzročiti onečišćenje vode.', 0);
INSERT INTO property VALUES (1,5960, 'hr', 'definition','Uzimanje iz mora više ulova nego što to prirodni prirast može izdržati. Prekomjerni izlov ima cijeli niz uzroka, a najbezobzirniji je taj što "kronična prekapacitiranost" modernih ribarskih flota da zapravo izlove daleko više ribe nego što se može zamijeniti (novim količinama).', 0);
INSERT INTO property VALUES (1,5962, 'hr', 'definition','Intenzivna ispaša životinja, na primjer goveda, ovaca ili koza na nekom pašnjačkom području. Ona je postala ozbiljna prijetnja pašnjacima i travnjacima u svijetu. Do prekomjerne ispaše dovelo je nekoliko čimbenika. Ona dovodi do degradacije tla i podložnosti eroziji vjetra i kiše, pa čak i širenja pustinja. Glavni pritisak koji dovodi do rasprostranjene prekomjerne ispaše su potreba da se poveća veličinu i broj stada da bi se proizvelo više hrane za sve veću ljudsku populaciju, kao i preobrazba tradicionalnih pašnjaka u plantažama za uzgoj komercijalnih usjeva. U suhim tropskim uvjetima, gdje su se stada tradicionalno slobodno kretala po golemim područjima, prevladavaju načini intenzivnog uzgoja stoke, uglavnom kako bi se osiguralo meso za izvozno tržište. Kopanje bunara također je dovelo do velikih koncentracija životinja u malim područjima.', 0);
INSERT INTO property VALUES (1,5963, 'hr', 'definition','Viseći kablovi kojima se električna energija prenosi po cijeloj zemlji.', 0);
INSERT INTO property VALUES (1,5965, 'hr', 'definition','Gustoća stanovništva koja premašuje kapacitet okoliša za ispunjavanje zdravstvenih potreba pojedinih organizma.', 0);
INSERT INTO property VALUES (1,5967, 'hr', 'definition','Kruženje, posebno u jesen i proljeće, slojeva vode u jezeru ili moru, pri čemu površinska voda propada i miješa se s vodom na dnu; ono je posljedica promjena gustoće zbog promjena temperature, a posebno je često gdje su jezera zaleđena zimi.', 0);
INSERT INTO property VALUES (1,5968, 'hr', 'definition','Provođenje zime u određenom kraju.', 0);
INSERT INTO property VALUES (1,5971, 'hr', 'definition','Skup prava na korištenje i uživanje imovine, uključujući i pravo prijenosa drugima. Potpuna vlast, naslov, ili vlasničko pravo u nekoj stvari ili potraživanju.', 0);
INSERT INTO property VALUES (1,5972, 'hr', 'definition','Kemijska reakcija koja povećava sadržaj kisika u nekom spoju.', 0);
INSERT INTO property VALUES (1,6111, 'hr', 'definition','Podzemna voda koja prolazi, pod silom gravitacije, kroz  stijene ili tlo duž linije manjeg otpora.', 0);
INSERT INTO property VALUES (1,6115, 'hr', 'definition','Biljni ili životinjski organizam koji je pričvršćen ili prijanja na površine lišća ili stabljika biljaka s korijenjem iznad sloja dna.', 0);
INSERT INTO property VALUES (1,6118, 'hr', 'definition','Sustav interakcije bioloških zajednica i njihovog neživog  okruženja u klimatskom području u kojem je podzemlje vječno smrznuto.', 0);
INSERT INTO property VALUES (1,6120, 'hr', 'definition','Sposobnost membrane ili drugog materijala da omogući tvari da prođe kroz nju.', 0);
INSERT INTO property VALUES (1,6123, 'hr', 'definition','Granica izloženosti koja je utvrđena za izlaganje opasnoj tvari ili štetnom sredstvu i koja se provodi putem OSHA (Zakona o zaštiti na radu i zdravlju) kao zakonski standard. Ona se temelji na vremenski ponderiranom prosjeku koncentracija za normalan 8-satni radni dan i 40-satni radni tjedan.', 0);
INSERT INTO property VALUES (1,6124, 'hr', 'definition','Licenca, formalni pristanak ili ovlaštenje da se djeluje u nekoj stvari, koji često potvrđuju to djelovanje kao zakonito ili proceduralno ispravno.', 0);
INSERT INTO property VALUES (1,6128, 'hr', 'definition','Onečišćujuća tvar stvorena djelovanjem Sunčeve svjetlosti na ugljikovodika i dušikovih oksida u zraku. Sastojak smoga.', 0);
INSERT INTO property VALUES (1,6129, 'hr', 'definition','Kapacitet tvari da ostane kemijski stabilna. To je važan čimbenik u procjeni utjecaja na okoliš od tvari ispuštenih u okoliš. Određene otrovne tvari (npr. cijanidi) imaju nisku postojanost, dok  ostale manje neposredno otrovne tvari (npr., mnogi organoklorini) imaju visoku postojanost i stoga mogu proizvesti ozbiljnije posljedice.', 0);
INSERT INTO property VALUES (1,6131, 'hr', 'definition','Sposobnost neke kemikalije da zadrži svoju molekularnu  cjelovitost i time svojoj fizikalna, kemijska i funkcionalna svojstva u okruženju u kojem se takva kemikalija može se prevoziti i distribuirati tijekom znatnog vremenskog razdoblja.', 0);
INSERT INTO property VALUES (1,6138, 'hr', 'definition','Svaki organizam koji šteti usjevima, nanosi ozljedu ili uznemiruje životinju ili čovjeka, ili smanjuje plodnost zemlje.', 0);
INSERT INTO property VALUES (1,6139, 'hr', 'definition','Smanjenje broja štetnika njihovim uništavanjem ili sprječavanjem njihovih napada.', 0);
INSERT INTO property VALUES (1,6140, 'hr', 'definition','Opći pojam za kemijska sredstva koja se upotrebljavaju kako bi se uništile neželjene biljke, životinjski štetnici ili gljivice koje uzrokuju bolesti, te za prihvaćanje insekticida, herbicida, fungicida, nematocida itd. Neki pesticidi imaju velike uništavajuće učinke među ne-ciljanim vrstama.', 0);
INSERT INTO property VALUES (1,6143, 'hr', 'definition','Zakonska norma ili mjera koja se odnosi na proizvodnju, distribuciju ili uporabu tvari namijenjenih ublažavanju ili uklanjanju kukaca ili malih životinja koje štete raslinju.', 0);
INSERT INTO property VALUES (1,6144, 'hr', 'definition','Određen ili poznati put ili vektor bilo koje kemijske tvari ispuštene u okoliš kako bi se spriječilo, uništilo ili ublažilo djelovanje bilo kojeg štetnika koji je izravno ili neizravno štetan za žetvu usjeva i druge ljudske interese.', 0);
INSERT INTO property VALUES (1,6146, 'hr', 'definition','Pesticid koji u okolišu ostane vrlo dugo i nastavlja djelovati još danima, tjednima i mjesecima.', 0);
INSERT INTO property VALUES (1,6149, 'hr', 'definition','1) Pojava jedne ili više vrsta štetnika na području ili mjestu gdje su njihov broj i utjecaj trenutno ili potencijalno na nepodnošljivim razinama.
INSERT INTO property VALUES (1,6152, 'hr', 'definition','Životinja koja se čuva u kući kao družbenik i prema kojoj se brižno ponašamo.', 0);
INSERT INTO property VALUES (1,6154, 'hr', 'definition','Proizvodnja materijala iz nafte ili prirodnog plina.', 0);
INSERT INTO property VALUES (1,6156, 'hr', 'definition','Goriva za motore s unutarnjim izgaranjem koja se sastoje uglavnom od hlapljivih zapaljivih tekućih ugljikovodika; i koja se deriviraju iz sirove nafte procesima kao što su destilacijsko reformiranje, polimerizacija, katalitički kreking i alkilacija.', 0);
INSERT INTO property VALUES (1,6161, 'hr', 'definition','Relativno lako hlapljiv tekući bitumen koji se sastoji uglavnom od ugljikovodika, s tragovima sumpornih, dušikovih ili kisikovih spojeva; mogu se ukloniti iz zemlje u tekućem stanju.', 0);
INSERT INTO property VALUES (1,6162, 'hr', 'definition','Nafta pripada neobnovljivim izvorima energije; to je složena tvar derivirana iz pougljenjenih ostataka stabala, paprati, mahovina i drugih vrsta biljnih tvari. Glavni kemijski sastojci nafte su ugljik, vodik i sumpor. Različita goriva načinjena od nafte su mlazno gorivo, benzin, kerozin, dizel gorivo i teška loživa ulja. Potrošnja nafte najveća je u sljedećim područjima: prijevozu, stambeno-poslovnim djelatnostima, industriji i proizvodnji električne energije.', 0);
INSERT INTO property VALUES (1,6163, 'hr', 'definition','Grana ekonomske geologije koji se odnosi na podrijetlo, migraciju i akumulaciju nafte i plina, te na otkrića komercijalnih nalazišta. Njezina praksa uključuje primjenu geokemije, geofizike, paleontologije, strukturne geologije i stratigrafije na probleme u pronalaženju ugljikovodika.', 0);
INSERT INTO property VALUES (1,6164, 'hr', 'definition','Prerađivačka industrija u kojoj se upotrebljava složena kombinacija međusobno zavisnih aktivnosti skladištenja i prijevoza, odvajanja sirovih molekularnih sastojaka, razbijanja molekula, molekularne obnove i dorade otapala za proizvodnju petrokemijskih proizvoda.', 0);
INSERT INTO property VALUES (1,6169, 'hr', 'definition','Kontrolirano korištenje, zaštita i razvoj eksploatiranih i potencijalno iskoristivih izvora sirove nafte kako bi se zadovoljila trenutna potražnja i pokrile buduće potrebe.', 0);
INSERT INTO property VALUES (1,6176, 'hr', 'definition','Biljke koje proizvode sjemenke. Skupina obuhvaća golosjemenjače (Gymnospermae) i kritosjemenjače (Angiospermae).', 0);
INSERT INTO property VALUES (1,6177, 'hr', 'definition','Zajedničke aktivnosti koje se bave proizvodnjom farmaceutskih proizvoda.', 0);
INSERT INTO property VALUES (1,6179, 'hr', 'definition','Odbačeni lijekovi i srodni proizvodi iz ljekarni, bolnica, klinika, proizvođača farmaceutskih proizvoda itd.', 0);
INSERT INTO property VALUES (1,6180, 'hr', 'definition','Proučavanje brzina apsorpcije, distribucije tkiva, biotransformacije i izlučivanja.', 0);
INSERT INTO property VALUES (1,6181, 'hr', 'definition','Znanost koja se bavi prirodom i svojstvima lijekova, a osobito njihovim djelovanjem.', 0);
INSERT INTO property VALUES (1,6183, 'hr', 'definition','Bijeli, kristalni, topiv, otrovan, kiseli derivat benzena, koji se upotrebljava kao antiseptik i dezinficijens te u proizvodnji smole, najlona, boja, eksploziva i farmaceutskih proizvoda.', 0);
INSERT INTO property VALUES (1,6184, 'hr', 'definition','Svaka tvar koju izlučuje životinja a koja utječe na ponašanje drugih jedinki iste vrste.', 0);
INSERT INTO property VALUES (1,6187, 'hr', 'definition','Akademska disciplina koja se eksplicitnim objašnjenjem prirode i značenja običnih i znanstvenih vjerovanja i istraživanjem razumljivosti pojmova pomoću racionalnog argumenta o njihovim pretpostavkama, implikacijama i međusobnim odnosima; a osobito, racionalno istraživanje prirode i strukture stvarnosti (metafizika), izvora i granica znanja (epistemologija), načela i smisla moralne prosudbe (etika), kao i odnosa između jezika i stvarnosti (semantika).', 0);
INSERT INTO property VALUES (1,6189, 'hr', 'definition','1) Generički izraz za svaki spoj koji sadrži fosfatnu skupinu.
INSERT INTO property VALUES (1,6190, 'hr', 'definition','Zamjena fosfata u deterdžentima ekološki sigurnijim tvarima, kao što je zeolit​​. Zamjena neće djelovati kao hranjiva tvar, stoga neće prouzročiti eutrofikaciju kao posljedicu ubrzanog rasta biljaka i mikroorganizama ako se ispusti u vodotoke.', 0);
INSERT INTO property VALUES (1,6192, 'hr', 'definition','Sve tvari koje su u stanju zamijeniti fosfatne spojeve u deterdžentima; one moraju imati ista kemijska i fizikalna svojstva i moraju manje onečišćavati okoliš.', 0);
INSERT INTO property VALUES (1,6196, 'hr', 'definition','Nemetalni element koji se upotrebljava za proizvodnju fosforne kiseline, u fosfornim broncama, zapaljiivim sredstvima, u pirotehnici, za proizvodnju šibica, te otrova za štakore; bijeli ili žuti alotrop je mekana voštana krutina, topljiva u ugljik disulfidu, netopljiva u vodi i alkoholu, i otrovna je i samozapaljiva u zraku; crveni alotrop je amorfni prah, netopljiv u svim otapalima i neotrovan; crni alotrop obuhvaća sjajne kristale slične grafitu, i netopljiv je u većini otapala.', 0);
INSERT INTO property VALUES (1,6197, 'hr', 'definition','Sredstva koja pokreću fotokemijske reakcije.', 0);
INSERT INTO property VALUES (1,6198, 'hr', 'definition','Rezultat ili posljedica kemijske reakcije koju izaziva svjetlost ili ultraljubičasto zračenje.', 0);
INSERT INTO property VALUES (1,6199, 'hr', 'definition','Sve kemikalije koje stupaju u oksidacijske reakcije u prisutnosti svjetlosti i drugih energija zračenja.', 0);
INSERT INTO property VALUES (1,6200, 'hr', 'definition','Proizvodi degradacije koji su nastali djelovanjem svjetlosnog zračenja.', 0);
INSERT INTO property VALUES (1,6201, 'hr', 'definition','Kemijska reakcija koju pokreće svjetlost određene valne duljine. U kontekstu zaštite okoliša primjer je mogućnost djelovanja ultraljubičastog svjetla na CFC-e, što može dovesti do štetne razgradnje ozonskog omotača. Fotokemijske reakcije pokreću proces fotosinteze u kojima biljke pretvaraju ugljični dioksid u šećere, koji se ugrađuju u stanične materijale.', 0);
INSERT INTO property VALUES (1,6202, 'hr', 'definition','Kombinacija magle i kemikalija koje izlaze iz automobila i tvorničkih emisija i na koju djeluje sunce. Dušikov dioksid se u prisutnosti sunca i ugljikovodika pretvara u dušikov oksid i atomski kisik. Atomski kisik reagira s molekulama kisika i drugim sastojcima iz ispušnih plinova automobila pa nastaju različiti proizvodi, uključujući ozon. Ozon je sam po sebi štetan, a također je uključen u vrlo složeni niz daljnjih reakcija. Dok god postoji prisutnost ozona ili dušikovog dioksida i sunca, događat će se druge nepoželjne reakcije.', 0);
INSERT INTO property VALUES (1,6204, 'hr', 'definition','Proces izrade mjerenja na temelju fotografija, a upotrebljava se osobito u izradi karata iz aerosnimaka i u vojnoj obavještajnoj djelatnosti, medicinskim i industrijskim istraživanjima itd.', 0);
INSERT INTO property VALUES (1,6205, 'hr', 'definition','Prikaz snimljen fotografskim aparatom ili nekim drugim uređajem i reproduciran kao slika, obično na fotoosjetljivoj površini i stvoren kemijskim djelovanjem svjetla ili energije zračenja.', 0);
INSERT INTO property VALUES (1,6211, 'hr', 'definition','Proces kojim biljke pretvaraju ugljični dioksid i vodu u ugljikohidrate i druge spojeve, koristeći se energijom iz sunca uhvaćenom u klorofilu u biljci. Kisik je nusproizvod tog procesa. Fotosinteza je osnova svega biljnog života (autotrofna proizvodnja), a time i svega životinjskog svijeta (heterotrofna proizvodnja) na planetu Zemlji. Brzina fotosinteze ovisi o klimi, intenzitetu i trajanje Sunčeve svjetlosti, raspoloživom prostoru na listu, dostupnosti hranjivih tvari u tlima, temperaturi, koncentraciji ugljičnog dioksida, kao i režimima vlažnosti tla.', 0);
INSERT INTO property VALUES (1,6222, 'hr', 'definition','Proučavanje prostornih i vremenskih značajki i odnosa svih pojava u fizičkom okolišu Zemlje.', 0);
INSERT INTO property VALUES (1,6224, 'hr', 'definition','Proučavanje fizičkih vidova oceana, gibanja mora i promjenjivosti tih čimbenika u odnosu prema atmosferi i oceanskom dnu.', 0);
INSERT INTO property VALUES (1,6225, 'hr', 'definition','Oblik urbanističkog planiranja korištenja zemljišta kojim se nastoji ostvariti optimalna prostornu koordinacija različitih ljudskih aktivnosti radi poboljšanja kvalitete života.', 0);
INSERT INTO property VALUES (1,6226, 'hr', 'definition','Onečišćujuća tvar čija je značajke njezin utjecaj na ekološke uvjete uzrokovan silama i djelovanjem fizike, kao što su buka, mikrovalna zračenja, vibracije itd.', 0);
INSERT INTO property VALUES (1,6227, 'hr', 'definition','Uvođenje ili prisutnost štetnih tvari ili sila u okolišu koje uzrokuju štetu u okolišu i njegovim procesima zbog svojih materijalnih aktivnosti, npr. putem vibracija, toplinske izmjene ili elektromagnetskog zračenja.', 0);
INSERT INTO property VALUES (1,6228, 'hr', 'definition','Stalno djelovanje ili niz promjena koje mijenjaju materijalni (fizički) oblik tvari.', 0);
INSERT INTO property VALUES (1,6229, 'hr', 'definition','Svojstvo spoja koje se može promijeniti a da se ne promijeni kemijski sastav.', 0);
INSERT INTO property VALUES (1,6230, 'hr', 'definition','Znanosti koje se bave neživom tvari, energijom i fizikalnim svojstvima svemira, kao što su fizika, kemija, astronomija i geologija.', 0);
INSERT INTO property VALUES (1,6233, 'hr', 'definition','Procesi koji uključuju promjene u fizikalnim svojstvima i kemijskoj strukturi tvari.', 0);
INSERT INTO property VALUES (1,6234, 'hr', 'definition','Upotrebljava se za koncentriranje otpade slane vode i uklanjanje krutih organskih tvari i amonijaka iz vodenih otopina. Fizikalno tretiranje se sastoji od reverzne osmoze, dijalize, elektrodijalize, isparavanja, karbonizacije, adsorpcije, uklanjanja amonijaka, filtriranja, taloženja i flokulacije. Kemijski tretman se sastoji od ionske izmjene, neutralizacije, oksidacije, redukcije, precipitacije i kalcinacije.', 0);
INSERT INTO property VALUES (1,6235, 'hr', 'definition','Proučavanje vidova prirode koji se mogu razumjeti na temeljni način u smislu elementarnih načela i zakona.', 0);
INSERT INTO property VALUES (1,6240, 'hr', 'definition','Biološko proučavanje funkcija živih organizama i njihovih dijelova.', 0);
INSERT INTO property VALUES (1,6243, 'hr', 'definition','Biomasa porijeklom iz biljaka; svaka kvantitativna procjena ukupne mase biljaka u neposječenoj šumi, populaciji ili unutar određenog područja u određenom vremenu.', 0);
INSERT INTO property VALUES (1,6244, 'hr', 'definition','Proučavanje bolesti biljaka i njihov nadzor.', 0);
INSERT INTO property VALUES (1,6247, 'hr', 'definition','Biljni plankton.', 0);
INSERT INTO property VALUES (1,6248, 'hr', 'definition','Proučavanje raslinja, uključujući organizaciju, međuovisnosti, razvoj, geografsku rasprostranjenost i razvrstavanje biljnih zajednica.', 0);
INSERT INTO property VALUES (1,6252, 'hr', 'definition','Pogon u kojem se obloga (metalna pjena) uklanja sa željeza i čelika, obično uranjanjem u vruću kupku od klorovodične ili sumporne kiseline.  Otpad uključuje upotrijebljenu tekućinu za dekapiranje, muljeve i vodu od ispiranja.', 0);
INSERT INTO property VALUES (1,6253, 'hr', 'definition','Slikovni prikaz podataka projiciranih na dvodimenzionalnu mrežu pojedinačnih elemenata slike (piksela), koji se dobije iz umjetnog objekta koji se nalazi u orbiti oko nekog planeta, mjeseca ili zvijezde.', 0);
INSERT INTO property VALUES (1,6261, 'hr', 'definition','Posao koji obavlja pilot, koji upravlja kretanjem broda ili letjelica vizualnim ili elektroničkim sredstvima.', 0);
INSERT INTO property VALUES (1,6262, 'hr', 'definition','Umanjena verzija planiranog industrijskog postrojenja, izgrađena kako bi se steklo iskustvo u upravljanju konačnog postrojenja.', 0);
INSERT INTO property VALUES (1,6263, 'hr', 'definition','Pokus manjeg opsega ili eksperiment ili niz opažanja poduzetih da bi se odlučilo kako pokrenuti i treba li pokrenuti cijeli projekt.', 0);
INSERT INTO property VALUES (1,6268, 'hr', 'definition','Pripadaju redu Pinnipedia, ovo je red vodenih sisavaca koji imaju posteljicu, te tijelo aerodinamičnog oblika i udove specijalizirane kao peraje: uključuje tuljane, morske lavove i morževe.', 0);
INSERT INTO property VALUES (1,6270, 'hr', 'definition','Niz cijevi povezanih ventilima i ostalim kontrolnim uređajima, a služe za prijenos tekućina, plinova ili sitnozrnatih krutih tvari.', 0);
INSERT INTO property VALUES (1,6274, 'hr', 'definition','Cijev od metala, gline, plastike, drva ili betona, koja se upotrebljava za prijenos tekućina, plinova ili sitnozrnatih krutih tvari.', 0);
INSERT INTO property VALUES (1,6276, 'hr', 'definition','Prostrano, široko ravno ili valovito područje zemljišta gotovo bez drveća, s grmolikim raslinjem, obično na niskim nadmorskim visinama.', 0);
INSERT INTO property VALUES (1,6279, 'hr', 'definition','Okvirni nacrt djelovanja, unaprijed razrađena metoda djelovanja.', 0);
INSERT INTO property VALUES (1,6280, 'hr', 'definition','Onečišćenje koje se pojavljuje u raznim djelatnostima i nema nekoga određenog izvora.', 0);
INSERT INTO property VALUES (1,6282, 'hr', 'definition','Mali organizmi (životinjice, biljke ili mikrobi) koji pasivno plutaju u vodi.', 0);
INSERT INTO property VALUES (1,6285, 'hr', 'definition','Svako fizičko proširenje ili promjene u postojećem korištenju zemljišta u područjima velikih gradova nakon određenih priprema ili nacrta.', 0);
INSERT INTO property VALUES (1,6287, 'hr', 'definition','Izrada detaljnog plana za ostvarenje nekog cilja.', 0);
INSERT INTO property VALUES (1,6290, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koja propisuje vlada za organiziranje, određivanje i reguliranje korištenja zemljišta u svom području ovlasti putem zakona o prostornom planiranju, propisima o podjeli, nadzoru iznajmljivanja i znakova, upravljanju rastom i drugim mjerama za zaštitu zdravlja ljudi i cjelovitost okoliša.', 0);
INSERT INTO property VALUES (1,6291, 'hr', 'definition','Postupak ili tijek djelovanja poduzeti u svrhu izrade nacrta, organizacije ili pripreme za budućnost.', 0);
INSERT INTO property VALUES (1,6292, 'hr', 'definition','Odobrenje, dozvola ili ekvivalentni kontrolni dokument koji je izdala vladina agencija odnosno ured, a kojim se odobrava ''korak po korak'' metoda i proces definiranja, razvijanja i strukturiranja različitih mogućih načina djelovanja kako bi se zadovoljile postojeće ili buduće potrebe te kratkoročni i dugoročni ciljevi.', 0);
INSERT INTO property VALUES (1,6293, 'hr', 'definition','Sustav za postizanje dugoročnih ciljeva ili kratkoročnih ciljeva pomoću analize i evaluacije alternativa. Sustav planiranja, programiranja i izrade proračuna je osmišljen za rješavanje problema pronalaženjem najdjelotvornijeg i najučinkovitijeg rješenja na temelju objektivnih kriterija.', 0);
INSERT INTO property VALUES (1,6298, 'hr', 'definition','Uzgoj određene vrste biljke križanjem jedne sorte s drugom kako bi se proizvela nova sorta, u kojoj su željene značajke najjače.', 0);
INSERT INTO property VALUES (1,6299, 'hr', 'definition','Svaka skupina biljaka koje pripadaju mnogobrojnim različitim vrstama koje se zajedno pojavljuju u istom staništu ili području i međusobno djeluju putem trofičkih i prostornih odnosa; njihove značajke obično se određuju usporedbom u odnosu prema jednoj ili više dominantnih vrsta.', 0);
INSERT INTO property VALUES (1,6300, 'hr', 'definition','Sastavni dijelovi biljke.', 0);
INSERT INTO property VALUES (1,6305, 'hr', 'definition','Oprema, uključujući strojeve, alate, instrumente i uređaje potrebne za industrijski ili proizvodni postupak.', 0);
INSERT INTO property VALUES (1,6307, 'hr', 'definition','Znanstveno proučavanje nasljednog materijala biljaka za namjene kao što su hibridizacija, bolji hranidbeni resursi i povećanje proizvodnje.', 0);
INSERT INTO property VALUES (1,6310, 'hr', 'definition','Bilo koja tvar ili mješavina tvari koja fiziološkim djelovanjem štiti biljke od nametnika, gljivica, virusa ili drugih štetnih čimbenika.', 0);
INSERT INTO property VALUES (1,6312, 'hr', 'definition','Odnosi se na sisavce koji hodaju tako da cijeli taban stopala dodiruje tlo.', 0);
INSERT INTO property VALUES (1,6313, 'hr', 'definition','Zasađivanje stabala sadnjom sadnica, presadnica ili reznica.', 0);
INSERT INTO property VALUES (1,6316, 'hr', 'definition','Proučavanje djelovanja i kemijskih reakcija u različitim  organima biljaka.', 0);
INSERT INTO property VALUES (1,6318, 'hr', 'definition','Očuvanje biljnih vrsta koje mogu biti rijetke ili ugrožene, te drugih biljaka osobite važnosti.', 0);
INSERT INTO property VALUES (1,6319, 'hr', 'definition','Ispitivanja koja se provode kako bi se utvrdila učinkovitost pesticida u raznovrsnim klimatskim i drugim okolišnim uvjetima; kako bi se ocijenile moguće nuspojave na životinje, biljke i ljude i kako bi se utvrdila postojanost ostataka pesticida u okolišu.', 0);
INSERT INTO property VALUES (1,6323, 'hr', 'definition','Svaki živi organizam koji obično sintetizira svoju hrane iz anorganskih tvari, posjeduje celulozne stijenke stanica, reagira sporo i često trajno na poticaj, nema specijalizirane osjetilne organe i živčani sustav, i nema sposobnost kretanja.', 0);
INSERT INTO property VALUES (1,6325, 'hr', 'definition','Naravna tekstilna vlakna rastlinskega izvora.', 0);
INSERT INTO property VALUES (1,6326, 'hr', 'definition','Trgovanje biljem podliježe propisima koji su uspostavljeni na temelju Konvencije  o međunarodnoj trgovini ugroženim vrstama (CITES).', 0);
INSERT INTO property VALUES (1,6327, 'hr', 'definition','1) Zajedničko ime za niz industrijskih primjena plazme, kao što su: jetkanje poluvodičkih čipova, taloženja silikona za proizvodnju solarnih ćelija, taloženja silicij dioksida za pasivizaciju površina, aktiviranje površina, taljenje i zavarivanje s lukovima plazme, kao i kemija plazme.
INSERT INTO property VALUES (1,6329, 'hr', 'definition','Polimerni materijal (obično organski) velike molekularne težine koji se može oblikovati protokom; obično se odnosi na konačni proizvod s punilima, omekšavalima, pigmentima i stabilizatorima (nasuprot smoli, homogenom polimernom početnom materijalu); primjeri su polivinil klorid, polietilen i urea-formaldehid.', 0);
INSERT INTO property VALUES (1,6336, 'hr', 'definition','Svaka odbačena plastika (organska ili sintetička, materijal izveden iz polimera, smola ili celuloze) nastala u bilo kojem industrijskom procesu, ili od strane potrošača.', 0);
INSERT INTO property VALUES (1,6338, 'hr', 'definition','Kovak, savitljiv srebrno-bijeli metalik element vrlo otporan na toplinu i kemikalije. Pojavljuje se u u slobodnom obliku i zajedno s drugim platinskim metalima, osobito u osmiridiju (osmij i iridij); upotrebljava se za nakit, laboratorijske uređaje, električne kontakte, u stomatologiji, galvanizaciji i kao katalizator.', 0);
INSERT INTO property VALUES (1,6339, 'hr', 'definition','Zemljište namijenjeno rekreaciji, osobito djece, često opremljeno njihaljkama, toboganima i sl.', 0);
INSERT INTO property VALUES (1,6346, 'hr', 'definition','Visokotoksičan metalni transuranski element. Pojavljuje se u tragovima u uranijevim rudama, a proizvodi se u nuklearnom reaktoru neutronskim bombardiranjem urana-238. U najstabilnijiem i najvažnijem izotopu, plutoniju-239, lako dolazi do fisije, pa se upotrebljava kao reaktorsko gorivo u nuklearnim elektranama i u nuklearnom oružju.', 0);
INSERT INTO property VALUES (1,6355, 'hr', 'definition','Ilegalno hvatanje divljači, ribe itd. nedozvoljenim pristupom na privatne posjede.', 0);
INSERT INTO property VALUES (1,6357, 'hr', 'definition','Onečišćenja iz nekog izvora kao što su septičke jame, kanalizacija, ispust, odlagalište otpada, tvornice ili postrojenje za pročišćavanje otpadnih voda koji ispušta otpadne vode u vodotok; emisije iz dimnjaka iz industrijskih procesa; ili izlijevanje iz podzemnog spremnika kod kojeg je došlo do procjeđivanja u podzemne vode.', 0);
INSERT INTO property VALUES (1,6358, 'hr', 'definition','Tvar koja, kad se proguta, udiše ili apsorbira, ili kada se nanese, ubrizga, ili se razvija u tijelu, u relativno malim količinama, može izazvati ozljede, štete ili uništenja organa i  tkiva ili ugroziti život.', 0);
INSERT INTO property VALUES (1,6359, 'hr', 'definition','Bolesno stanje izazvano otrovom koji se može progutati, udahnuti, ubrizgati ili apsorbirati kroz kožu.', 0);
INSERT INTO property VALUES (1,6362, 'hr', 'definition','Međusobno djelovanje sustava bioloških zajednica i njihovih neživih okoliša, u okolici koja se nalazi u regijama u kojima je temperatura zraka uvijek ispod 10° C, obično na Sjevernom i Južnom polu i u njihovoj blizini.', 0);
INSERT INTO property VALUES (1,6364, 'hr', 'definition','Područje povezano sa Zemljinim polovima ili područje unutar Arktičkog ili Antarktičkog polarnog kruga.', 0);
INSERT INTO property VALUES (1,6365, 'hr', 'definition','Općenito plodan pojas ravnog, niskog zemljišta (kao u Nizozemskoj i Belgiji), koje je nasuto i zaštićeno od mora, jezera, rijeke ili drugog vodnog tijela pomoću raznih vrsta nasipa, brana ili pregrada. Izraz je obično rezerviran za obalna područja koje se nalaze na razini mora ili ispod nje i koja se stalno štite organiziranim sustavom održavanja i obrane.', 0);
INSERT INTO property VALUES (1,6367, 'hr', 'definition','Grana vlasti zadužena za očuvanje javnog reda, promicanje javnog zdravlja i sigurnosti, te sprječavanje, otkrivanje i kažnjavanje kaznenih djela.', 0);
INSERT INTO property VALUES (1,6368, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koja propisuje vlade za reguliranje zapošljavanja i taktike policije ili državnih agenata organiziranih za održavanje reda, sprječavanje i otkrivanje kaznenih djela i promicanje poštivanja propisa o građanskim pravima i ovlastima.', 0);
INSERT INTO property VALUES (1,6371, 'hr', 'definition','Teorija i praksa stjecanja i ostvarivanja moći upravljanja u društvu kako bi se posredovale vrijednosti, dodjeljivali resursi i uspostavila i provodila pravila.', 0);
INSERT INTO property VALUES (1,6373, 'hr', 'definition','Metoda ili mehanizam što ga upotrebljavaju vlada, političke stranke, poslovni subjekti ili pojedinci kako bi postigli željeni učinak, pravnim ili ekonomskim sredstvima.', 0);
INSERT INTO property VALUES (1,6374, 'hr', 'definition','Proces koordinacije, usklađivanja i objedinjavanja ciljeva i postupaka različitih ureda ili jedinica neke organizacije.', 0);
INSERT INTO property VALUES (1,6376, 'hr', 'definition','Postupak usmjeravanja, filozofija ili način djelovanja usmjeren očuvanju i obnovi ljudskih i prirodnih resursa.', 0);
INSERT INTO property VALUES (1,6378, 'hr', 'definition','Proces organiziranja ili pripreme kojim se olakšava bilo kakvo djelovanje koje može biti usvojiti i ostvarivati vlada, poslovna ili neka druga organizacija.', 0);
INSERT INTO property VALUES (1,6454, 'hr', 'definition','Metode za smanjenje količine onečišćujućih tvari ispuštenih iz nekog izvora.', 0);
INSERT INTO property VALUES (1,6458, 'hr', 'definition','Norma utvrđena za određene onečišćujuće tvari koja ograničava njihovu koncentraciju.', 0);
INSERT INTO property VALUES (1,6459, 'hr', 'definition','Glavni učinci onečišćenja povezani su s ljudskim zdravljem, a obuhvaćaju sve vrste fizičkog okoliša - zrak, vodu i zemljište, uključujući učinke klimatskih promjena. Ljudske aktivnosti koje su izvori onečišćenja odvijaju se u kućanstvima, komercijalnom, industrijskom i vojnom sektoru i na njihove učinke djeluje razna problematika, trendovi i programi javnog sektora, kao što su sigurna hrana i voda, gospodarenje otpadom, povećanje uporabe kemikalija u poljoprivredi, te urbanizacija. Vrste onečišćujućih tvari koje negativno utječu na zdravlje uključuju smeće, otrovne kemikalije, nuklearni otpad, olovo, otpatke iz rudarstva, onečišćujuće tvari iz hrane i vode, kao i učinke onečišćenja zbog prenapučenosti.', 0);
INSERT INTO property VALUES (1,6462, 'hr', 'definition','Organizmi, uglavnom biljke, koje su izuzetno osjetljive na male promjene u ekološkim čimbenicima. Kada se identificira, njihova reakcija može poslužiti kao rano upozorenje o ugroženosti zdravlja neke zajednice.', 0);
INSERT INTO property VALUES (1,6464, 'hr', 'definition','Odgovornost za povrede koje su rezultat ispuštanja opasnih  ili onečišćujućih tvari ili kontaminanata.', 0);
INSERT INTO property VALUES (1,6465, 'hr', 'definition','Količina stresa kojom je opterećen ekosustav zbog fizikalnog ili kemijskog onečišćenja ispuštenog u njega zbog djelovanja ljudi ili iz prirodnih izvora.', 0);
INSERT INTO property VALUES (1,6467, 'hr', 'definition','Ocjena koncentracije onečišćujućih tvari tijekom određenog vremena na određenom mjestu.', 0);
INSERT INTO property VALUES (1,6469, 'hr', 'definition','Kvantitativno i kvalitativno mjerenje prisutnosti, učinka ili razine bilo koje onečišćujuće tvari u zraku, vodi i tlima.', 0);
INSERT INTO property VALUES (1,6472, 'hr', 'definition','Vjerojatnost štete za zdravlje ljudi, imovinu ili okoliš zbog  uvođenja nepoželjne tvari u neki ekosustav.', 0);
INSERT INTO property VALUES (1,6473, 'hr', 'definition','Medij za uklanjanje kemikalije ili plina iz sustava atmosfere, biosfere i oceana, u kojem je tvar apsorbirana u stalnom ili polutrajnom repozitoriju, ili se na drugi način pretvorila u drugu tvar. Na primjer, ugljik se može uskladištiti u oceanu (koji apsorbira i zadržava ugljik iz drugih dijelova ugljikovog ciklusa) ili fotosintezom (koja pretvara ugljik iz atmosfere u biljni materijal). Skladištenja onečišćenja su temeljni čimbenik u postojećoj ravnoteži koja određuje koncentraciju svakog stakleničkog plina u atmosferi. Ako je skladištenja onečišćenja veće od izvora plina, njegova koncentracija u atmosferi će se smanjiti; ako je izvor onečišćenja veći od skladištenja onečišćenja, koncentracija će se povećati.', 0);
INSERT INTO property VALUES (1,6474, 'hr', 'definition','Mjesto, mjesta ili područja odakle se onečišćujuća tvar ispušta u atmosferu ili vodu, ili gdje nastaje buka. Izvor može biti razvrstan kao točkasti izvor, tj. veliki pojedinačni izazivač  onečišćenja, izvor na nekoj površini ili linijski izvor, npr. emisija iz vozila i buka.', 0);
INSERT INTO property VALUES (1,6481, 'hr', 'definition','Kemijska tvar u čijem su sastavu, ne računajući nečistoće, molekule bromiranog bifenila.', 0);
INSERT INTO property VALUES (1,6485, 'hr', 'definition','PCB-i su skupina kemijskih spojeva koji ne postoje u prirodi, nego su stvoreni umjetnim putem. Komercijalne smjese su bistre, blijedožute tekućine, proizvedene zamjenom vodikovih atoma na molekuli bifenila klorom.
INSERT INTO property VALUES (1,6486, 'hr', 'definition','PCDD nastaju (zajedno s varijantama koje uključuju i furane) kada se spojevi koji sadrže klor spaljuju na niskim temperaturama u spalionicama kućnog i  industrijskog otpada koje nepravilno rade ili su nepropisno dizajnirane, gdje se PCDD mogu naći i u dimnim plinovima i u letećem pepelu.', 0);
INSERT INTO property VALUES (1,6487, 'hr', 'definition','Skupina sa 135 pojedinačnih, bezbojnih spojeva poznatih pod nazivom kongeneri s različitim štetnim učincima na zdravlje i okoliš. Nastaju kao neželjeni spojevi u proizvodnji nekoliko kemikalija i potrošačkih proizvoda kao što su kemikalije za obradu drva, neki metali i proizvodi od papira, a također nastaju i pri spaljivanju komunalnog i industrijskog otpada u spalionicama, iz ispuha olovnog benzina, topline ili pri proizvodnji električne energije. Opasni su za dišni sustav, probavni sustav, jetru, mišićno-koštani sustav, kožu i živčani sustav; a otrovni su ako se udišu, progutaju i dodiruju. Simptomi izloženosti uključuju učestali kašalj, teške respiratorne infekcije, kronični bronhitis, bolove u trbuhu, bol u mišićima, aknaste osipe, promjenu boje kože, neočekivani gubitak tjelesne težine, nemaligne ili maligne bolesti jetre.', 0);
INSERT INTO property VALUES (1,6488, 'hr', 'definition','Spojevi koji se sastoje od tri prstena benzena povezana jednih s drugima bilo u orto, meta ili para položajima i zamijenjeni atomima klora.', 0);
INSERT INTO property VALUES (1,6492, 'hr', 'definition','Ugljikovodici koji sadržavaju dva ili više zatvorenih prstenova atoma.', 0);
INSERT INTO property VALUES (1,6493, 'hr', 'definition','Molekula ugljikovodika s dvije ili više jezgri; primjeri su naftalin, s dva usporedna prstena benzena, ili difenil, s dva benzenska prstena povezana vezom. Također poznat kao polinuklearni ugljikovodik.', 0);
INSERT INTO property VALUES (1,6497, 'hr', 'definition','1) Termoplastična poliesterska smola izrađena od etilen glikola i tereftalatne kiseline; topi se pri 265° C, a upotrebljava se za izradu filmova ili vlakana.
INSERT INTO property VALUES (1,6498, 'hr', 'definition','1) Veza dva ili više monomera za nastanak polimera. 
INSERT INTO property VALUES (1,6499, 'hr', 'definition','Tvar koja se sastoji od golemih molekula nastalih spajanjem jednostavnih molekula (monomera).', 0);
INSERT INTO property VALUES (1,6505, 'hr', 'definition','Polimer vinil klorida; bez okusa, bez mirisa; netopljiv u većini organskih otapala; član skupine vinil smola.', 0);
INSERT INTO property VALUES (1,6507, 'hr', 'definition','Prirodno slatkovodno tijelo - voda stajaćica koja zauzima malu površinsku depresiju, obično manje od jezera i veće od mlake.', 0);
INSERT INTO property VALUES (1,6508, 'hr', 'definition','Svaki skup tekućih otpadnih voda ili otpadnih voda iz odvodnje ili odvojenih tijekom obrade usjeva ili mineralnih ruda.', 0);
INSERT INTO property VALUES (1,6510, 'hr', 'definition','Mala, prirodna voda stajaćica, obično slatkovodna, npr. Ustajalo vodno tijelo u močvari, ili stajaća voda u spilji.', 0);
INSERT INTO property VALUES (1,6517, 'hr', 'definition','Gustoća, obrazac raspršenosti i raspodjela ukupnog broja stanovnika u bilo kojem području.', 0);
INSERT INTO property VALUES (1,6518, 'hr', 'definition','Proces numeričke i strukturne promjene unutar populacija koje proizlaze iz broja rođenih, broja umrlih i kretanja stanovništva.', 0);
INSERT INTO property VALUES (1,6519, 'hr', 'definition','Skupina organizama jedne vrste koja naseljava određeno područje.', 0);
INSERT INTO property VALUES (1,6520, 'hr', 'definition','Proučavanje međusobnog djelovanja pojedine vrste ili rodova populacije s njezinim okolišem.', 0);
INSERT INTO property VALUES (1,6523, 'hr', 'definition','Porast u ukupnom broju stanovnika zemlje, grada, okruga ili područja.', 0);
INSERT INTO property VALUES (1,6525, 'hr', 'definition','Svaki pomak ili migracija statistički značajnog broja ljudi nastanjenih u nekoj zemlji, okrugu ili području.', 0);
INSERT INTO property VALUES (1,6527, 'hr', 'definition','Organizacija, i međusobni odnosi, stanovnika neke regije, zemlje ili grada.', 0);
INSERT INTO property VALUES (1,6528, 'hr', 'definition','Smjer promjene u ukupnom broju ljudi nastanjenih u nekoj zemlji, gradu, okrugu ili području.', 0);
INSERT INTO property VALUES (1,6536, 'hr', 'definition','Obrada pročišćene vode ili otpadnih voda radi poboljšanja kakvoće vode.', 0);
INSERT INTO property VALUES (1,6540, 'hr', 'definition','Svaki od nekoliko spojeva koji sadržavaju kalij, osobito topljivi spojevi kao što su kalijev oksid, kalijev klorid i razni kalijevi sulfati, koji se upotrebljavaju uglavnom u umjetnim gnojivima.', 0);
INSERT INTO property VALUES (1,6541, 'hr', 'definition','Eksploatacija kamene soli je proces podzemnog rudarstva u kojem se sol fizički kopa iz zemlje postupkom koji uključuje bušenje, miniranje i drobljenje kamena. Najveći  postotak ovakve proizvodnje upotrebljava se za održavanje cesta zimi.', 0);
INSERT INTO property VALUES (1,6548, 'hr', 'definition','Domaća perad koja se uzgaja za meso i jaja.', 0);
INSERT INTO property VALUES (1,6549, 'hr', 'definition','Jedno od najčešćih poljoprivrednih zanimanja. Mnoga urbana kućanstava i mnoga poljoprivredna gospodarstva uzgajaju nešto pilića zbog mesa i jaja.', 0);
INSERT INTO property VALUES (1,6551, 'hr', 'definition','Stanje u kojem pojedincu nedostaju sredstva potrebna za preživljavanje.', 0);
INSERT INTO property VALUES (1,6552, 'hr', 'definition','Tvrtka koja je odgovorna za opskrbu i distribuciju električne energije na nekom području.', 0);
INSERT INTO property VALUES (1,6558, 'hr', 'definition','Omjer rada motora i dovedene vrućine.', 0);
INSERT INTO property VALUES (1,6561, 'hr', 'definition','Stacionarno postrojenje u kojem se nalazi uređaj za pretvaranje velikih razmjera nekog oblika energije (kao što je hidraulična, parna, kemijska ili nuklearna energija) u električnu energiju.', 0);
INSERT INTO property VALUES (1,6570, 'hr', 'definition','Proces proizvodnje odvojive čvrste faze unutar tekućeg medija; predstavlja stvaranje nove kondenzirane faze, kao što je para ili plin koji se kondenzira u kapljice tekućine; nova čvrsta faza postupno se taloži unutar čvrste legure kao rezultat spore, unutarnje kemijske reakcije; u analitičkoj kemiji taloženje se upotrebljava za odvajanje čvrste faze u vodenoj otopini.', 0);
INSERT INTO property VALUES (1,6571, 'hr', 'definition','Povećanje oborina koji proizlazi iz promjena u koloidnoj stabilnosti oblaka. Ono može biti bilo namjerno, kao što je to slučaj pri stimulaciji oblaka, ili nenamjerno, kao pri onečišćenju zraka, što povećava koncentracije aerosola i smanjuje sunčevo svjetlo.', 0);
INSERT INTO property VALUES (1,6577, 'hr', 'definition','Životinja koja ubija i jede druge životinje.', 0);
INSERT INTO property VALUES (1,6579, 'hr', 'definition','Zgrada čiji dijelovi su izrađeni u specijaliziranim tvornicama i kasnije prevezeni i montirani na gradilištu.', 0);
INSERT INTO property VALUES (1,6583, 'hr', 'definition','Svaka uvodna radnja u pravosudni proces osmišljena tako da se utvrdi potreba za daljnjim uključivanjem suda ili kako bi se ubrzao prijedlog koji zahtijeva trenutačnu pozornost.', 0);
INSERT INTO property VALUES (1,6584, 'hr', 'definition','Iznos koji treba platiti za ugovor o osiguranju ili životnom osiguranju.', 0);
INSERT INTO property VALUES (1,6586, 'hr', 'definition','Održavanje i čuvanje na sigurnom mjestu, sigurnih od oštećenja, uništenja ili propadanja, svih vrsta dokaza pravno predstavljenih na suđenju u nekom predmetu, uključujući svjedoke, evidenciju, dokumente, dokazne materijale  i konkretne predmete.', 0);
INSERT INTO property VALUES (1,6587, 'hr', 'definition','Kemikalija dodana hrani kako bi se spriječila oksidacija, fermentacija ili druga oštećenja, obično sprječavanjem odnosno zaustavljanjem rasta bakterija.', 0);
INSERT INTO property VALUES (1,6591, 'hr', 'definition','Sve tiskovine, a osobito novine i časopisi.', 0);
INSERT INTO property VALUES (1,6592, 'hr', 'definition','Primjena pritiska kako bi se istisnuo sok ili sadržaj voćke, sjemena itd.', 0);
INSERT INTO property VALUES (1,6593, 'hr', 'definition','Vrsta pritiska koji se primjenjuje jednako u svim smjerovima; njegova mjera je upotrijebljena sila po jediničnoj površini.', 0);
INSERT INTO property VALUES (1,6594, 'hr', 'definition','Svaka politički aktivna skupina sa zajedničkim vrijednostima u vezi s raspodjelom resursa. Skupine za pritisak nastoje utjecati na odluke o raspodjeli resursa koji se dodjeljuju povrh njihove razmjerne zastupljenosti u društvu tražeći da njihov izbor korištenja resursa dobije prioritet.', 0);
INSERT INTO property VALUES (1,6602, 'hr', 'definition','Novčani iznos koji se plaća za jedinicu robe ili usluge.', 0);
INSERT INTO property VALUES (1,6607, 'hr', 'definition','Postupak kojim se daju smjernice ili filozofija za odluke o monetarnoj stopi ili vrijednosti resursa zemlje ili regije, uključujući prirodne resurse, ljudske resurse i kapital, te robu koju je proizveo čovjek.', 0);
INSERT INTO property VALUES (1,6610, 'hr', 'definition','Prvih pet ili šest godina školovanja u osnovnoj školi. 
INSERT INTO property VALUES (1,6614, 'hr', 'definition','Potrošnja energije koja se upotrebljava u istom obliku kakva je u svom prirodnom stanju, na primjer, sirova nafta, ugljen, prirodni plin, npr. Prije nego što se pretvori u električnu struju.', 0);
INSERT INTO property VALUES (1,6619, 'hr', 'definition','Dio gospodarstva zemlje ili regije koji čini izravno korištenje prirodnih resursa, uključujući poljoprivredu, šumarstvo, ribarstvo i industriju goriva, metala i rudarsku industriju.', 0);
INSERT INTO property VALUES (1,6621, 'hr', 'definition','Red sisavaca koji obuhvaća majmune, čovjekolike majmune i ljudska bića.', 0);
INSERT INTO property VALUES (1,6622, 'hr', 'definition','Šuma koja je prvobitno pokrivala neku regiju prije promjena u okolišu koje su izazvali ljudi.', 0);
INSERT INTO property VALUES (1,6623, 'hr', 'definition','Načelo što ga je usvojila Konferencija UN-a o okolišu i razvoju (1992. god.) da u cilju zaštite okoliša treba široko primjenjivati pristup predostrožnosti odnosno opreza, što znači da tamo gdje postoje prijetnje od ozbiljne ili nepovratne štete za okoliš, nedostatak pune znanstvene sigurnosti ne smije se upotrijebiti kao razlog za odgađanje troškovno učinkovitih mjera za sprječavanje degradacije okoliša.', 0);
INSERT INTO property VALUES (1,6624, 'hr', 'definition','Načelo što ga je utvrdila Svjetska komisija za okoliš i razvoj (Brundtland komisija) 1987. god.: razvoj koji zadovoljava potrebe sadašnjosti bez ugrožavanja potreba budućih naraštaja. Održivi razvoj je proces integracije gospodarskih, društvenih i ekoloških ciljeva, a ne bi trebao značiti kompromis između okoliša i razvoja. Održivi razvoj treba značiti ravnotežu, a ne sukob.', 0);
INSERT INTO property VALUES (1,6627, 'hr', 'definition','Sektor gospodarstva koji okuplja komercijalna poduzeća koja se bave reprodukcijom pisanog teksta ili slika u više primjeraka, kao što su knjige, časopisi, novine ili drugi slični oblici.', 0);
INSERT INTO property VALUES (1,6630, 'hr', 'definition','Umjetnost, proces ili djelatnost izrade reprodukcija pisanog teksta ili slika u više primjeraka, u obliku knjiga, časopisa ili novina, ili u drugim sličnim formatima.', 0);
INSERT INTO property VALUES (1,6632, 'hr', 'definition','Formalna najava i, često, zahtjev za dozvolu podnesen  odgovarajućim državnim vlastima o namjeri da se preko političkih granica prenesu potencijalno štetne tvari koje su zaostale iz procesa proizvodnje ili ispitivanja.', 0);
INSERT INTO property VALUES (1,6634, 'hr', 'definition','Prijevozno sredstvo u vlasništvu pojedinca.', 0);
INSERT INTO property VALUES (1,6636, 'hr', 'definition','Životni prostor u kojem skupina osoba (obitelj) živi zajedno.', 0);
INSERT INTO property VALUES (1,6637, 'hr', 'definition','Grana prava koja se bavi vidovima odnosa između pojedinaca koji se ne tiču izravno države.', 0);
INSERT INTO property VALUES (1,6640, 'hr', 'definition','Segment gospodarstva kojim ne upravlja vlada, uključujući i kućanstva, trgovce pojedince, partnerstva i trgovačka društva.', 0);
INSERT INTO property VALUES (1,6641, 'hr', 'definition','Prijevoz koji se obavlja privatnim sredstvima.', 0);
INSERT INTO property VALUES (1,6643, 'hr', 'definition','Prijenos vlasništva ili kontrole državnog poduzeća ili neke druge državne imovine na ne-javno, ne-službeno trgovačko društvo, organizaciju ili pojedinca, bilo prodajom ili osnivanjem posebnog poduzeća izvan izravne kontrole države.', 0);
INSERT INTO property VALUES (1,6645, 'hr', 'definition','Red biljojeda sisavaca s posteljicom koji se odlikuju surlom, proširenim sjekutićima koji postaju kljove, te stupastim  nogama s pet prstiju povezanima širokom šapom.', 0);
INSERT INTO property VALUES (1,6646, 'hr', 'definition','Organizmi (tj. prokarioti) čiji genetski materijal (DNK niti) nije zatvoren nuklearnom membranom, i koji ne posjeduju mitohondrije ni plastide. Bakterije i cijanofite su jedini prokariotski organizmi.', 0);
INSERT INTO property VALUES (1,6647, 'hr', 'definition','Pravo koje propisuje način provođenja prava ili dobivanje pravnog lijeka za njihovo narušavanje. Prava koja utvrđuju dužnosti, uspostavljaju prava i obveze između osoba i za osobe, prirodna ili drugačija, su prema karakteru "materijalna prava", dok su ona koji samo propisuju način na koji se takva prava i odgovornosti mogu ostvarivati i provoditi u sudu "procesna/postupovna prava".', 0);
INSERT INTO property VALUES (1,6650, 'hr', 'definition','Čin pretvaranja materijala iz jednog oblika u drugi željeni oblik.', 0);
INSERT INTO property VALUES (1,6654, 'hr', 'definition','Svaka tehnička strategija, metoda i alat koji se upotrebljavaju za osmišljavanje, projektiranje, razvoj ili provedbu bilo kojeg sustava.', 0);
INSERT INTO property VALUES (1,6655, 'hr', 'definition','Voda koja se upotrebljava u proizvodnji ili postupku obrade ili u stvarnom proizvedenom proizvodu. Primjeri bi uključili vodu za pranje, ispiranje, izravan dodir, hlađenje, sastav otopina, kemijske reakcije, ispiranje plina (tzv. Scrubbing) u primjenama u industriji i preradi hrane. U mnogim slučajevima voda se posebno tretira za postizanje kvalitete vode potrebne za taj proces.', 0);
INSERT INTO property VALUES (1,6658, 'hr', 'definition','Obveze, odgovornosti ili dugovi nametnuti svim članovima neke industrije koja prerađuje ili proizvodi neki proizvod ili uslugu koji izazivaju ozljede ili štetu potrošačima i koji su raspoređeni prema tržišnom udjelu svakog člana za dotični proizvod.', 0);
INSERT INTO property VALUES (1,6660, 'hr', 'definition','Nešto što je proizvedeno radom ljudi ili mehanički, ili je nastalo prirodnim procesom.', 0);
INSERT INTO property VALUES (1,6661, 'hr', 'definition','Usporedba proizvoda ili procesa za utvrđivanje onih koji imaju smanjen utjecaj na okoliš.', 0);
INSERT INTO property VALUES (1,6663, 'hr', 'definition','Pričvršćivanje obavijesti na proizvod ili spremnik na kojoj su  informacije o njegovom sadržaju, pravilnom korištenju, proizvođaču i eventualna upozorenja ili opasnosti korištenja.', 0);
INSERT INTO property VALUES (1,6664, 'hr', 'definition','Činjenične, posredne i, često, usporedna znanja o različitoj robi, uslugama ili događajima, njihovoj kvaliteti i subjektima koji ga proizvode.', 0);
INSERT INTO property VALUES (1,6670, 'hr', 'definition','Količina proizvodnje ili prinosa po jedinici inputa ili izdataka koje ostvari neko poduzeće, industrija ili država.', 0);
INSERT INTO property VALUES (1,6672, 'hr', 'definition','Opći smjer ili tendencija u mjerenju proizvodnje dobara i usluga čija je vrijednost utrživa.', 0);
INSERT INTO property VALUES (1,6675, 'hr', 'definition','1) Pravna odgovornost proizvođača i prodavača da nadoknade kupcima, korisnicima, pa čak i promatračima, štetu ili ozljede koje su pretrpjeli zbog nedostataka u kupljenoj robi.
INSERT INTO property VALUES (1,6676, 'hr', 'definition','Standard koji propisuje vidove fizikalnog ili kemijskog sastava proizvoda koji potencijalno mogu izazvati oštećenja okoliša ili rukovanje, prezentaciju i pakiranje proizvoda, osobito onih koji su otrovni.', 0);
INSERT INTO property VALUES (1,6678, 'hr', 'definition','Višak primitaka nad izdacima, troškovima i izdacima  poduzeća ili drugog komercijalnog subjekta tijekom bilo kojeg razdoblja.', 0);
INSERT INTO property VALUES (1,6708, 'hr', 'definition','Prikaz u obliku dijagrama opažanih podataka u nizu njihovog pojavljivanja u vremenu, u kontekstu protoka vode.', 0);
INSERT INTO property VALUES (1,6709, 'hr', 'definition','Uskraćivanje ili zabrana neke djelatnosti ili radnje od strane nekog  tijela ili temeljem zakona.', 0);
INSERT INTO property VALUES (1,6710, 'hr', 'definition','Skup radnji koje potencijalno mogu rezultirati fizičkom promjenom u okolišu.', 0);
INSERT INTO property VALUES (1,6718, 'hr', 'definition','Proces u kojem se poremećaj u jednoj točki širi na drugu točku udaljenija od izvora, bez protoka samog materijala po mediju; primjeri uključuju gibanje elektromagnetskih valova, zvuk valova, hidrodinamičke valove u tekućinama, kao i vibracije valova u krutim tvarima.', 0);
INSERT INTO property VALUES (1,6720, 'hr', 'definition','Plin koji se upotrebljava u aerosolskim pripravcima za potisak  tekućeg sadržaja kroz raspršivač.', 0);
INSERT INTO property VALUES (1,6724, 'hr', 'definition','Fizikalna i kemijska svojstva tvari ili dijelova od kojih su neka stvar ili predmet napravljeni.', 0);
INSERT INTO property VALUES (1,6728, 'hr', 'definition','Korist ili sigurnosti koju vlada pruža građanima kako bi osigurala njihovo pravo kao pojedinca ili kao poslovnog subjekta na ekskluzivna uživanja i prodaju nekretnina, novca i bilo koje druge stvari, materijalne ili nematerijalne, koji predstavlja izvor prihoda ili bogatstva.', 0);
INSERT INTO property VALUES (1,6736, 'hr', 'definition','Tehnika kojom se na tijelo djeluje tako da se ono pomiče primjenom sile prema tijelu.', 0);
INSERT INTO property VALUES (1,6737, 'hr', 'definition','Provođenje pravnih postupaka, osobito kaznenih postupaka.', 0);
INSERT INTO property VALUES (1,6739, 'hr', 'definition','Prosperitet, bogatstvo ili uspjeh.', 0);
INSERT INTO property VALUES (1,6740, 'hr', 'definition','Dijelovi zemljišta zaštićeni posebnim ograničenjima i zakonima za očuvanje prirodnog okoliša. Oni uključuju velike površine zemljišta izdvojenih za zaštitu divljih životinja i njihova staništa; područja velikih prirodnih ljepota ili jedinstvenih interesa; područja s rijetkim oblicima biljnog i životinjskog svijeta, područjima koja predstavljaju neobične geološke formacije; mjesta od povijesnih i pretpovijesnih interesa; područja koja sadrže ekosustave od posebne važnosti za znanstvena ispitivanje i istraživanja; te područja koja štite potrebe biosfere.', 0);
INSERT INTO property VALUES (1,6743, 'hr', 'definition','Prirodna ili umjetno stvorena područja rezervirana za očuvanje, znanstvene, obrazovne i/ili rekreacijske svrhe.', 0);
INSERT INTO property VALUES (1,6746, 'hr', 'definition','Rizične, ranjive ili ugrožene vrste koje su zaštićene od izumiranja preventivnim mjerama.', 0);
INSERT INTO property VALUES (1,6747, 'hr', 'definition','Područje oko vodocrpilišta u kojem su određeni oblici korištenja tla ograničeni ili zabranjeni u cilju zaštite podzemnih voda.', 0);
INSERT INTO property VALUES (1,6752, 'hr', 'definition','Mjere usvojene radi očuvanja vrsta, njihovih ekosustava i biološke raznolikosti.', 0);
INSERT INTO property VALUES (1,6753, 'hr', 'definition','Niz postupaka i uređaja namijenjen zaštiti ljudi i očuvanju imovine ili okoliša od ozljeda ili šteta.', 0);
INSERT INTO property VALUES (1,6755, 'hr', 'definition','Svaki iz vrste polimernih spojeva velike molekularne mase građen od različitih alfa-aminokiselina  povezanih peptidnim vezama.', 0);
INSERT INTO property VALUES (1,6758, 'hr', 'definition','1) Izvorni nacrt dokumenta.
INSERT INTO property VALUES (1,6759, 'hr', 'definition','Raznovrsno koljeno eukariotskih mikroorganizama; njihova struktura varira od jednostavnog protoplasta s jednom staničnom jezgrom do kolonijalnih oblika, tijela je ili golo ili pokriveno opnom, kreću se  pomoću lažnih nožica (pseudopodija) ili treplji (cilija) ili bičeva (flagela), postoji tendencija prema univerzalnoj simetriji u plutajućim vrstama i radijalnoj simetriji u sesilnim vrstama, a prehrana može biti fagotrofna, autotrofna ili saprozoična.', 0);
INSERT INTO property VALUES (1,6760, 'hr', 'definition','Zemljopisno područje veće površine, manje od kontinenta, a veće od regije, koje je ujednačeno prema nekim ili svim svojim značajkama i koje se stoga može proučavati kao cjelina. Na primjer, pokrajina sa životinjama ima poseban skup životinjskih vrsta, koji se razlikuje od skupova u različitim istodobnim okruženjima na drugim mjestima.', 0);
INSERT INTO property VALUES (1,6766, 'hr', 'definition','Ovlast vladine agencije/ureda ili njegovih administratora da upravljaju i provode zakone i vladine politike primjenjive na određeno zemljopisno područje, koje obično pripada pod nadležnost dviju ili više država.', 0);
INSERT INTO property VALUES (1,6773, 'hr', 'definition','Rezultat ili posljedice mentalnih procesa koji stvaraju ili utječu na ljudsku savjest i osjećaje.', 0);
INSERT INTO property VALUES (1,6775, 'hr', 'definition','Napetost ili neravnoteža uma, osobito u afektivnim ili kognitivnim funkcijama, ili tjelesni ili mentalni podražaj, sredstvo ili iskustvo koje prouzročuje takvu neravnotežu.', 0);
INSERT INTO property VALUES (1,6776, 'hr', 'definition','Znanost koja se bavi funkcijama uma i ponašanja organizma u odnosu prema njegovoj okolini.', 0);
INSERT INTO property VALUES (1,6777, 'hr', 'definition','Svaki rezultat koji se odnosi na utjecaj uma ili viših funkcija mozga na operacije tijela, osobito tjelesni poremećaji ili bolesti.', 0);
INSERT INTO property VALUES (1,6778, 'hr', 'definition','Bolest koja nastaje zbog odnosa uma i tijela ili koju taj odnos pogoršava.', 0);
INSERT INTO property VALUES (1,6784, 'hr', 'definition','Pravo ili dopuštenje za sve osobe u nekoj zajednici da se koriste geografskim područjima u vlasništvu države kao što su parkovi, kampovi i povijesne znamenitosti.', 0);
INSERT INTO property VALUES (1,6785, 'hr', 'definition','Mjera ili nešto što se poduzima u ime i uz privolu široke javnosti.', 0);
INSERT INTO property VALUES (1,6787, 'hr', 'definition','Prostor s kupalištem za javnu uporabu.', 0);
INSERT INTO property VALUES (1,6788, 'hr', 'definition','Zgrada u koju javnost ima slobodan pristup i na raspolaganju je zajednici za upotrebu.', 0);
INSERT INTO property VALUES (1,6790, 'hr', 'definition','Sve čemu cijela javnost može pristupiti i čime se može koristiti i podijeliti bez ograničenja, globa ili naknada  globa.', 0);
INSERT INTO property VALUES (1,6793, 'hr', 'definition','Teorija i praksa pitanja u vezi s državnim novcem, uključujući i oporezivanje, potrošnju, prijenos i  prihod od imovine, zaduživanja, dug i upravljanje prihodima.', 0);
INSERT INTO property VALUES (1,6794, 'hr', 'definition','Čin pribavljanja ili osiguravanja novca ili kapitala za program, kupnju ili poduzetništvo od opće populacije u nekoj zajednici ili državi, obično državnom raspodjelom poreznih prihoda.', 0);
INSERT INTO property VALUES (1,6798, 'hr', 'definition','Disciplina u zdravstvenoj znanosti čiji je cilj, na razini zajednice ili javnosti, promicanje prevencije bolesti, sanitarnih uvjeta života, zakona, prakse i zdravijeg okoliša.', 0);
INSERT INTO property VALUES (1,6799, 'hr', 'definition','Pravo na pojavljivanje i podnošenje dokaza, te također pravo na saslušanje i ispitivanje svjedoka koji svjedoče za suprotnu stranu.', 0);
INSERT INTO property VALUES (1,6801, 'hr', 'definition','Činjenično ili posredno znanje ili služba, ured ili stanica koje pružaju to znanje za cijelu populaciju ili zajednicu, bez ograničenja.', 0);
INSERT INTO property VALUES (1,6802, 'hr', 'definition','Opća podjela prava, koje se općenito sastoji od ustavnog, upravnog, kaznenog i međunarodnog prava, koja se bave organizacijom države, odnosima između države i ljudi koji u njoj žive, odgovornošću javnih službenika prema državi i jednih prema drugima, te prema privatnim osobama, kao i međusobni odnosi država. Grana ili dio prava koji se bavi državom i njezinom politikom i suverenošću, uključujući ustavno i upravno pravo, i definiranjem, uređivanjem i provedbom prava u slučajevima gdje se država smatra predmetom prava ili predmetom dužnosti, - uključujući kazneno pravo i kazneni postupak, - i pravo države? the law of the state, koja se razmatra u svojoj kvazi-privatni osobnosti, tj. kao sposobna za posjedovanje ili ostvarivanje prava, odnosno stjecanjem imovine i bavljenjem imovinom, u liku pojedinca.', 0);
INSERT INTO property VALUES (1,6804, 'hr', 'definition','Navodno kolektivno stajalište javnosti o nekom pitanju ili problemu, obično formulirano selektivnim anketama ili uzorkovanjem, i koje se često upotrebljava kao vodič za djelovanje ili odluke.', 0);
INSERT INTO property VALUES (1,6805, 'hr', 'definition','Ispitivanje na reprezentativnom uzorku od velike skupine ljudi o nekim pitanjima u svrhu utvrđivanja općeg mišljenja grupe.', 0);
INSERT INTO property VALUES (1,6806, 'hr', 'definition','Park s velikim drvećem, ukrasnim biljem, alejama obrubljenim drvećem ili grmljem, fontanama i kipovima koji se nalazi u gradu i kojem je pristup slobodan.', 0);
INSERT INTO property VALUES (1,6808, 'hr', 'definition','Uključivanje, kao građana s pravom glasa, u javne poslove, radi ostvarivanja utjecaja.', 0);
INSERT INTO property VALUES (1,6809, 'hr', 'definition','Zajedničko ulaganje između korporacija i vlade ili između članova zajednice i vlasti ili poduzetnika izvan normalne interakcije.', 0);
INSERT INTO property VALUES (1,6810, 'hr', 'definition','Vladin proces nabave roba, opreme i usluga, ili  ugovori o kupnji kojima se osigurava pribavljanje pomagala, opreme i usluga, koje često prodaje privatni sektor.', 0);
INSERT INTO property VALUES (1,6812, 'hr', 'definition','Vladina služba za koju izabrane ili imenovan odvjetnik ili osoblja odvjetnika imaju ovlasti prema ustavu ili zakonu voditi postupke u ime vlade, zastupati javni interes ili poduzeti pravne radnje protiv osoba koje su prekršile federalne, državne ili lokalne zakone.', 0);
INSERT INTO property VALUES (1,6816, 'hr', 'definition','Gospodarski sektor koji do neke mjere vodi vlada, uključujući nacionalnu i lokalnu upravu, poduzeća u državnom vlasništvu i kvazi-autonomne nevladine organizacije.', 0);
INSERT INTO property VALUES (1,6817, 'hr', 'definition','Poduzeće koje se bavi osiguravanjem osnovnih stvari, kao što su električna struja ili voda, za javnost.', 0);
INSERT INTO property VALUES (1,6818, 'hr', 'definition','Radnja ili sredstvo prevoženja velikog broja ljudi za razliku od prijevoza u privatnim vozilima.', 0);
INSERT INTO property VALUES (1,6821, 'hr', 'definition','Vozila za prevoženje velikog broja putnika koji plaćaju za vožnju s jednog mjesta na drugo.', 0);
INSERT INTO property VALUES (1,6822, 'hr', 'definition','Poduzeće koje se bavi osiguravanjem osnovnih stvari, kao što su električna struja ili voda, za javnost.', 0);
INSERT INTO property VALUES (1,6824, 'hr', 'definition','Konstrukcije odnosno zgrade, kao što su ceste, brane ili poštanski uredi, koje se plaćaju iz državnih sredstava za javnu upotrebu.', 0);
INSERT INTO property VALUES (1,6827, 'hr', 'definition','Svaka bolest od koje obolijevaju pluća.', 0);
INSERT INTO property VALUES (1,6828, 'hr', 'definition','Celulozni materijal koji nastaje mehaničkom ili kemijskom redukcijom drva, a upotrebljava se pri izradi papirnih i celuloznih proizvoda. Također se naziva drvenom pulpom ili drvenjačom.', 0);
INSERT INTO property VALUES (1,6830, 'hr', 'definition','Sektor gospodarstva koji okuplja komercijalna poduzeća koja se bave proizvodnjom i prodajom mekane, vlažne, rahlo povezane mase nastale od drva koja se upotrebljava za proizvodnju papirnih listova, kartona i drugih proizvoda od papira.', 0);
INSERT INTO property VALUES (1,6831, 'hr', 'definition','Stroj koji uvlači tekućinu u sebe kroz usisni dio i tjera tekućinu van kroz ispusni otvor.', 0);
INSERT INTO property VALUES (1,6833, 'hr', 'definition','Uklanjanje plinova i para iz vakuumskog sustava.', 0);
INSERT INTO property VALUES (1,6835, 'hr', 'definition','Stjecanje ili čin kupovanja nečega plaćanjem novčanim sredstvima ili ekvivalentnim sredstvom.', 0);
INSERT INTO property VALUES (1,6838, 'hr', 'definition','Uklanjanje neželjenih sastojaka iz neke tvari.', 0);
INSERT INTO property VALUES (1,6841, 'hr', 'definition','Oprema za uklanjanje nečistoća ili neželjenih sastojaka iz medija.', 0);
INSERT INTO property VALUES (1,6842, 'hr', 'definition','Postrojenje u kojem se iz otpadnih voda odstranjuju nečistoće.', 0);
INSERT INTO property VALUES (1,6846, 'hr', 'definition','Sve dušikove baze, kao što su guanin i adenin, koje su derivati ​​purina i sastojci nukleinskih kiselina i određenih koenzima.', 0);
INSERT INTO property VALUES (1,6855, 'hr', 'definition','Razbijanje složenih molekula u jednostavnije jedinice pomoću topline.', 0);
INSERT INTO property VALUES (1,6860, 'hr', 'definition','Inspekcijski pregled, analiza i druge relevantne radnje poduzete kako bi osigurala kontrola nad onim što se uradi, proizvede ili izradi, tako da se se postigne i održava poželjna razina kvalitete.', 0);
INSERT INTO property VALUES (1,6861, 'hr', 'definition','Kvaliteta života je uvelike stvar individualnih preferencija i percepcije i preklapa se s konceptom društvenog blagostanja. Općenito, naglasak je na količini i raspodjeli zajednički konzumiranih javnih dobara, kao što su zdravstvo i socijalna služba, zaštita od kriminala, reguliranje onečišćenja, očuvanje ljepote krajolika i povijesnih krajobraza.', 0);
INSERT INTO property VALUES (1,6867, 'hr', 'definition','Otvorena ili površinska obrada ili iskop za vađenje građevnog kamena, rudača, ugljena, šljunka ili minerala.', 0);
INSERT INTO property VALUES (1,6870, 'hr', 'definition','Površinsko iskopavanje i uklanjanje kamena ili mineralnih nalazišta iz Zemljine kore.', 0);
INSERT INTO property VALUES (1,6882, 'hr', 'definition','Povezivanje, napetosti ili sklad između dviju ili više skupina ljudi koje se razlikuju po povijesti, kulturi, religiji ili tjelesnom izgledu: razlike koje se pogrešno tumače kao da se temelje na stalnim biološkim razlikama i da, u stvari, predstavljaju vrste ljudskog roda.', 0);
INSERT INTO property VALUES (1,6883, 'hr', 'definition','Sustav koji se koristi snopom elektromagnetskih valova i reflektiranjem radiofrekvencijske energije za otkrivanje i lociranje objekata, mjerenje udaljenosti ili visine, navigaciju, navođenje, bombardiranje i ostale svrhe.', 0);
INSERT INTO property VALUES (1,6884, 'hr', 'definition','Emisija bilo kojih zraka, ili prirodnog ili umjetnog podrijetla, kao što su radio valovi, zrake sunca, medicinske rendgenske zrake (X-zrake) i talog onečišćujućih tvari i nuklearni otpad proizveden nuklearnim oružjem i nastao u proizvodnji nuklearne energije. Zračenje se obično dijeli na neionizirajuće zračenje, kao što su toplinsko zračenje (toplina) i svjetlo, i nuklearno zračenje. Neionizirajuće zračenje uključuje ultraljubičasto zračenje od sunca koje, iako može oštetiti stanice i tkiva, ne uključuje ionizaciju kod nuklearnog zračenja.', 0);
INSERT INTO property VALUES (1,6887, 'hr', 'definition','Somatska i genetska oštećenja živih organizama prouzročene izlaganjem ionizirajućem zračenju.', 0);
INSERT INTO property VALUES (1,6888, 'hr', 'definition','Ukupan iznos zračenja apsorbiran u materijalu ili tkivu, u smislu apsorbirane doze, doze izloženosti , ili ekvivalenta doze.', 0);
INSERT INTO property VALUES (1,6889, 'hr', 'definition','Dugotrajno izlaganje ionizirajućem zračenju iz različitih izvora može biti štetno. Nuklearno zračenje od nataloženih čestica od nuklearnog oružja ili iz elektrana, pozadinsko zračenje iz tvari koje su prirodno prisutne u tlu, kao i izlaganje rendgenskim zrakama mogu prouzročiti bolesti zračenja. Velika  izloženost zračenju može ubiti brzo, i za bilo koju osobu izloženu zračenju više je vjerojatno da će se kod nje razviti određene vrste raka u usporedbi s drugim članovima populacije.', 0);
INSERT INTO property VALUES (1,6890, 'hr', 'definition','Radnja ili stanje podvrgnutosti elektromagnetskoj energiji dovoljno jakoj da ionizira atome, čime predstavlja opasnost za ljudsko zdravlje ili okoliš.', 0);
INSERT INTO property VALUES (1,6892, 'hr', 'definition','Povremeni ili kontinuirani nadzor ili analiza razine energije zračenja prisutne u određenom području, kako bi se utvrdilo da njegova propisana razina nije prekoračena ili da udovoljava  prihvatljivim sigurnosnim standardima.', 0);
INSERT INTO property VALUES (1,6894, 'hr', 'definition','Proučavanje ionizirajućeg zračenja i njegovih učinaka na stvari.', 0);
INSERT INTO property VALUES (1,6896, 'hr', 'definition','Radnje ili mjere predostrožnosti ili opreme za zaštitu i obranu ljudi, imovine i prirodnih resursa od štetnih učinaka ionizirajuće energije.', 0);
INSERT INTO property VALUES (1,6897, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propisuje vlada radi uspostavljanja mjera kako bi ljudi i prirodni resursi bili sigurni od štetnih izlaganja energetskim valovima koje ispuštaju nuklearni materijali, elektromagnetske struje i drugih izvori.', 0);
INSERT INTO property VALUES (1,6899, 'hr', 'definition','Skup simptoma koji su obilježja bolesti poznate kao radijacijske ozljede, a koje su posljedica prekomjerne izloženosti cijelog tijela (ili većeg dijela) ionizirajućem zračenju. Najraniji od tih simptoma su mučnina, umor, povraćanje i proljev, a nakon njih mogu uslijediti gubitak kose (epilacija), krvarenje, upale usta i grla, te opći gubitak energije.', 0);
INSERT INTO property VALUES (1,6901, 'hr', 'definition','Postupak, oprema ili priprema programa uključena u prijenos i primanje zvučnih signala elektromagnetskim valovima.', 0);
INSERT INTO property VALUES (1,6904, 'hr', 'definition','Onečišćenje tvari, živog organizma ili lokaliteta izazvano radioaktivnim materijalom.', 0);
INSERT INTO property VALUES (1,6908, 'hr', 'definition','Uklanjanje radioaktivnog onečišćenja koje se nataložilo na površini ili se proširilo u cijelom radnom prostoru. Također uključuje dekontaminaciju osoblja.  Dvije su osnovne metode dekontaminacije prema načinu onečišćenja, mehanička i kemijska.', 0);
INSERT INTO property VALUES (1,6909, 'hr', 'definition','Otpad koji nastaje emisijom čestica ili elektromagnetskim zračenjem koje je posljedica raspadanja jezgre nestabilnih elemenata.', 0);
INSERT INTO property VALUES (1,6911, 'hr', 'definition','Ispuštanje u okoliš radioaktivnih tvari nastalih u nuklearnim postrojenjima te iz aktivnosti eksploatacije, pročišćavanja i obogaćivanja radioaktivnih elemenata.', 0);
INSERT INTO property VALUES (1,6912, 'hr', 'definition','Materijal koji se spusti na zemlju ili u vodu daleko od lokacije površinske ili podzemne nuklearne eksplozije.', 0);
INSERT INTO property VALUES (1,6915, 'hr', 'definition','Tvar koja se spontano raspada ili se raspada njezina atomska jezgra i koja emitira energiju zračenja u obliku čestica ili valova, često povezana s eksplozijom nuklearnog oružja ili sa slučajnim ispuštanjem iz nuklearne elektrane, skladišnog objekta ili prijevoza kontejnera.', 0);
INSERT INTO property VALUES (1,6918, 'hr', 'definition','Svaka tvar koja sadrži jedan ili više radionuklida čija se aktivnosti ili koncentracija ne može zanemariti u smislu zaštite od zračenja.', 0);
INSERT INTO property VALUES (1,6919, 'hr', 'definition','Radioaktivni izotop koji se, kada se ubrizga u biološki ili fizički sustav, može pratiti uređajima za otkrivanje zračenja, koji omogućuju utvrđivanje raspodjele ili mjesta tvari s kojom je povezan.', 0);
INSERT INTO property VALUES (1,6921, 'hr', 'definition','Svaki otpad koji emitira zračenje veće od normalne pozadinske razine, uključujući otrovne nusproizvode iz nuklearne energetike.', 0);
INSERT INTO property VALUES (1,6923, 'hr', 'definition','Ukupan nadzor proizvodnje, rukovanja, prerade, skladištenja i prijevoza materijala koji sadrže radioaktivne nuklide i za koje nije praktično moguće korištenje, ponovna uporaba ili oporaba.', 0);
INSERT INTO property VALUES (1,6925, 'hr', 'definition','Svojstvo koje posjeduju neke atomske jezgre da se spontano raspadaju, uz gubitak energije putem emisije nabijenih čestica i/ili gama zračenja.', 0);
INSERT INTO property VALUES (1,6928, 'hr', 'definition','Nuklid koji je radioaktivan.', 0);
INSERT INTO property VALUES (1,6935, 'hr', 'definition','Radioaktivan plinoviti element koji zrači prirodno, iz stijena i minerala u kojima su prisutni radioaktivni elementi. Ispušta se u rudnicima iz kojih se na vadi ugljen, nego npr. kositar, željezo, fluorit, uran. Radon emitira alfa čestice, kao i proizvodi njegovog raspadanja te se navodi kao uzrok izuzetno velike pojave raka pluća kod rudara u rudnicima urana.  Postoji zabrinutost zbog razina radona u nekim stambenim zgradama, obično koje se nalaze uz granitne stijene ili regijama sa starim rudnicima kositra.', 0);
INSERT INTO property VALUES (1,6937, 'hr', 'definition','Odbačeni tekstilni otpad, bilo kao onaj koji su odbacili potrošači ili otpad koji nastaje prije prodaje, kao što su otpaci u procesu proizvodnje.', 0);
INSERT INTO property VALUES (1,6940, 'hr', 'definition','Gibanje i kretanje vozila koja prevoze robu i ljude na željezničkim sustavima.', 0);
INSERT INTO property VALUES (1,6941, 'hr', 'definition','Prijevoz robe i ljudi željeznicom.', 0);
INSERT INTO property VALUES (1,6942, 'hr', 'definition','Trajna pruga sastavljena od paralelnih metalnih šina pričvršćenih za željezničke pragove, namijenjene prijevozu putnika i robe vlakovima.', 0);
INSERT INTO property VALUES (1,6947, 'hr', 'definition','Padalina u obliku tekućih vodenih kapljica promjera većeg od 0,5 milimetara.', 0);
INSERT INTO property VALUES (1,6949, 'hr', 'definition','Šume širokolisnog, uglavnom zimzelenog, drveća koje se nalaze u stalno vlažnim klimama u tropima, suptropskim područjima, i nekim dijelovima zona umjerene klime.', 0);
INSERT INTO property VALUES (1,6950, 'hr', 'definition','Proces kojim čestice u atmosferi djeluju kao središta oko kojih voda može oblikovati kapljice koje zatim padaju u obliku kiše.', 0);
INSERT INTO property VALUES (1,6951, 'hr', 'definition','Voda koja pada iz oblaka u obliku kiše.', 0);
INSERT INTO property VALUES (1,6953, 'hr', 'definition','Navoženje alohtonog materijala kao što je tucani kamen, šljunak itd. na zemlju.', 0);
INSERT INTO property VALUES (1,6959, 'hr', 'definition','Ispitivanja koja ne pokažu uvijek jednake rezultate kada se ponavljaju u istim uvjetima.', 0);
INSERT INTO property VALUES (1,6962, 'hr', 'definition','Euroazijska biljka iz roda krstašica, Brassica napus, koja se uzgaja zbog sjemena, iz kojega se dobivaju korisna ulja, te služi kao krmno bilje.', 0);
INSERT INTO property VALUES (1,6965, 'hr', 'definition','Vrste koje se u svijetu pojavljuju u ograničenom opsegu.', 0);
INSERT INTO property VALUES (1,6967, 'hr', 'definition','Sirov, neobrađen ili djelomično prerađen materijal koji se upotrebljava kao sirovina za preradu.', 0);
INSERT INTO property VALUES (1,6968, 'hr', 'definition','Razvijene zemlje ovise o stabilnoj opskrbi sirovinama za svoje industrije. Ukupni zahtjevi za resursima sve više rastu u cijelom svijetu. U razvijenim zemljama, iako stanovništvo polako raste, upotreba po stanovniku sve više raste, a u zemljama u razvoju događa se suprotno. Tradicionalno, sirovine su razvrstavane u neobnovljive resurse, ali može postojati važna razlika između resursa "koji se gube", kao što su nafta i ugljen, i resursa "koji se ne gube", kao što su metali, koji se mogu upotrebljavati više puta zahvaljujući procesu recikliranja.', 0);
INSERT INTO property VALUES (1,6970, 'hr', 'definition','Mjere koje se upotrebljavaju kako bi se osigurala opskrba ili pristup sirovoj nafti, neobrađenim ili djelomično obrađenim materijalima koji se upotrebljavaju kao sirovine za preradu ili proizvodnju.', 0);
INSERT INTO property VALUES (1,6975, 'hr', 'definition','Grana fizikalne kemije koja se bavi mehanizmima i stupnjevima kemijskih reakcija.', 0);
INSERT INTO property VALUES (1,6977, 'hr', 'definition','Naprava koja uvodi bilo induktivnu ili kapacitivnu reaktanciju u strujni krug, kao što je zavojnica ili kondenzator.', 0);
INSERT INTO property VALUES (1,6978, 'hr', 'definition','Proučavanja i aktivnosti kojima se nastoji na najmanju moguću mjeru smanjiti rizik od nesreće u nuklearnom reaktoru.', 0);
INSERT INTO property VALUES (1,6979, 'hr', 'definition','Sadnja stabala u šumskim područjima koja su iskrčena. Pošumljavanje postaje sve važnije za sprječavanje ili zaustavljanje smjera degradacije okoliša i za pomoć u povećanju ekonomskog povrata od komercijalnih šumskih površina.', 0);
INSERT INTO property VALUES (1,6988, 'hr', 'definition','Tehnike i praktične primjene povezane s rekombinantnom DNK (deoksiribonukleinskom kiselinom koja je umjetno uvedena u stanicu koja mijenja genotip i fenotip stanice i replicira se zajedno s prirodnom DNK).', 0);
INSERT INTO property VALUES (1,6989, 'hr', 'definition','Preporuka se odnosi na djelovanje koje je po svojoj prirodi savjetodavno, a ne obvezujećeg učinka.', 0);
INSERT INTO property VALUES (1,6997, 'hr', 'definition','Mjere sanacije poduzete kako bi se povratila razina kvalitete okoliša nekog krajobraza u njegovo stanje prije narušavanja.', 0);
INSERT INTO property VALUES (1,6999, 'hr', 'definition','Proces ponovnog uspostavljanja izvornih uvjeta u prirodnim ekosustavima i staništima.', 0);
INSERT INTO property VALUES (1,7000, 'hr', 'definition','Formuliran ili sustavan način obnove prirodnih resursa ili ponovne uporabe materijala i predmeta.', 0);
INSERT INTO property VALUES (1,7001, 'hr', 'definition','Aktivnosti kojima se promiče poboljšanje zdravlja i podmlađivanje duha relaksacijom i uživanjem.', 0);
INSERT INTO property VALUES (1,7003, 'hr', 'definition','Dio zemlje u javnom vlasništvu, osobito u gradu, koji se upotrebljava za sportove i igre.', 0);
INSERT INTO property VALUES (1,7009, 'hr', 'definition','Značajka materijala koji još uvijek imaju korisna fizikalna ili kemijska svojstva nakon što su poslužila za njihove izvorne namjene i koji se stoga, mogu ponovno upotrijebiti ili preraditi u dodatne proizvode.', 0);
INSERT INTO property VALUES (1,7010, 'hr', 'definition','Plastični otpad koji se može pretvoriti u nove proizvode.', 0);
INSERT INTO property VALUES (1,7012, 'hr', 'definition','Otpadni materijal koji se pretvara u nove proizvode na takav način da izvorni proizvodi mogu izgubiti svoj ​​identitet.', 0);
INSERT INTO property VALUES (1,7014, 'hr', 'definition','Papir koji je odvojen od krutog otpada za korištenje kao sirovine u proizvodnji novog proizvoda. Nije moguće reciklirati sav papir u krutom otpadu. Papir može biti jako onečišćen ili na drugi način neupotrebljiv.', 0);
INSERT INTO property VALUES (1,7015, 'hr', 'definition','Metoda oporabe resursa uključuje prikupljanje i obradu otpadnih proizvoda za uporabu kao sirovine u proizvodnji istog ili sličnog proizvoda.', 0);
INSERT INTO property VALUES (1,7018, 'hr', 'definition','Upravljanje svim fazama uključenim u život proizvoda kao što je nabava sirovina, proizvodnja, distribucija i maloprodaja, korištenje i ponovno korištenje i održavanje, recikliranje i gospodarenje otpadom, kako bi se stvorili proizvodi manje štetne za okoliš.', 0);
INSERT INTO property VALUES (1,7019, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propisuje vlada da uspostavi i regulira načine za minimiziranje otpada putem oporabe i preradu ponovno iskoristivih proizvoda.', 0);
INSERT INTO property VALUES (1,7028, 'hr', 'definition','Upravno sredstvo ili pravni lijek koji pokušava povratiti osobu u svoj ​​izvorni ili očekivani položaj prije gubitka ili ozljede, uključujući i raskid ugovora.', 0);
INSERT INTO property VALUES (1,7029, 'hr', 'definition','Morska voda koja je pokrivena ili izgubila boju zbog naglog rasta cvjetanja algi ili velikog povećanja u jednostaničnim organizmima, dinoflagelatima. Crvena plima i oseka često su kobne za mnoge oblike morskog života i, u nekim slučajevima, može rezultirati ljudskom smrću, budući da dinoflagelate jedu dagnje i druge školjke u kojima se koncentriraju paralizirajući toksini koje dinoflagelati proizvode.', 0);
INSERT INTO property VALUES (1,7030, 'hr', 'definition','Kemijska reakcija u kojoj neki element dobiva elektron.', 0);
INSERT INTO property VALUES (1,7032, 'hr', 'definition','Svi različiti oblici visokog raslinja sličnog travi koji rastu u skupinama blizu vode.', 0);
INSERT INTO property VALUES (1,7034, 'hr', 'definition','Niz stijena u području plime i oseke na obali, potopljeni kad je plima, ali djelomično otkriveni kad je oseka.', 0);
INSERT INTO property VALUES (1,7035, 'hr', 'definition','Tvornica za pročišćavanje nekih sirovina kao što su rude, šećer, ulje itd.', 0);
INSERT INTO property VALUES (1,7036, 'hr', 'definition','Obrada sirovina radi uklanjanje nečistoća.', 0);
INSERT INTO property VALUES (1,7037, 'hr', 'definition','Vraćanje valova ili čestica s površina na kojima se pojavljuju.', 0);
INSERT INTO property VALUES (1,7038, 'hr', 'definition','Proučavanje refleksije svjetla ili neke druge energije zračenja.', 0);
INSERT INTO property VALUES (1,7042, 'hr', 'definition','Tvar koja prolaskom kroz promjenu u fazi (tekućine u plin, plina u tekućinu) oslobađa ili apsorbira veliku latentnu toplinu u odnosu prema svom volumenu, i time ima značajan učinak hlađenja.', 0);
INSERT INTO property VALUES (1,7044, 'hr', 'definition','Hlađenje tvari, obično hrane, ispod temperature okoliša u svrhu čuvanja. Rashlađivanje je odgovorno za najveću i najbrže rastuće korištenje CFC-a u zemljama u razvoju. Industrijske zemlje, kao i neke zemlje u razvoju, poduzele su izvanredne mjere za kontrolu i, u konačnici, zabranu proizvodnje CFC-a i drugih tvari koje oštećuju ozonski omotač do 2000. godine. Međutim, mnoge zemlje u razvoju nisu potpisale Montrealski protokol jer se boje da će cijena prelaska na alternativne tehnologije koje nisu štetne za ozon biti previsoka.', 0);
INSERT INTO property VALUES (1,7045, 'hr', 'definition','Uređaj, ormar ili prostorija za pohranjivanje hrane ili drugih tvari pri niskim temperaturama.', 0);
INSERT INTO property VALUES (1,7046, 'hr', 'definition','Ograničen i izoliran prostor u kojem su se biljke i životinje održale tijekom razdoblja kontinentalne klimatske promjene koja je učinila okolna područja nepogodnima za stanovanje; pogotovo područja bez leda ili koja nisu bila zaleđena unutar ili u neposrednoj blizini kontinentalne ledene ploče ili brdskih ledenih kapa, gdje su otporne biote uspjele opstati tijekom glacijalnog razdoblja. On je kasnije služio kao središte iz kojeg se širilo ponovno naseljavanje okolnih područja nakon klimatske prilagodbe.', 0);
INSERT INTO property VALUES (1,7047, 'hr', 'definition','Osoba koja se nalazi izvan svoje zemlje podrijetla i koja, zbog osnovanog straha od proganjanja, nije sposobna ili ne želi iskoristiti zaštitu te zemlje.', 0);
INSERT INTO property VALUES (1,7050, 'hr', 'definition','Posebna vozila konstruirana i opremljena za prikupljanje i prijevoz otpada na mjesto zbrinjavanja otpada.', 0);
INSERT INTO property VALUES (1,7051, 'hr', 'definition','Gorivo proizvedeno od kućanskog otpada, nakon što se iz njega ukloni staklo i metal, tako da se sabije u obliku briketa i upotrebljava kao gorivo za kotlove.', 0);
INSERT INTO property VALUES (1,7056, 'hr', 'definition','Kompost nastao razgradnjom organskih sastojaka krutih otpada i muljeva otpadnih voda. Glavni problemi javnog zdravlja povezani s kompostiranjem korištenjem krutog otpada pomiješanog s kanalizacijskog mulja su patogeni, teški metali i neugodni mirisi. Toplina koja se stvara za vrijeme kompostiranja, kao rezultat aktivnosti termofilnih organizama, sposobna je uništiti bakterije, viruse, protozoe i crijevne gliste prisutne u kanalizacijskom mulju. Metalni elementi u mulju koji su najopasniji za ljudsko zdravlje su kadmij, olovo, arsen, selen i živa. Samo kadmij se obično nalazi u kanalizacijskom mulju na razinama koje se, kada se primjenjuju na tlo, mogu apsorbirati u biljkama, te se akumulirati u jestivim dijelovima, čime ulaze u hranidbeni lanac.', 0);
INSERT INTO property VALUES (1,7057, 'hr', 'definition','Obnavljanje ili ponovna upotreba materijala kao što je aktivirani ugljen, smole za ionsku izmjenu i filtrati za čišćenje vode odgovarajućim sredstvima za uklanjanje organskih tvari, metala, krutih tvari itd.', 0);
INSERT INTO property VALUES (1,7058, 'hr', 'definition','Uvođenje promjena u starim industrijskim postrojenjima ugradnjom nove opreme i objekata za odlaganje emisija plinova u atmosferi, otpadnih voda i otpadnog materijala u tlu i vodi.', 0);
INSERT INTO property VALUES (1,7059, 'hr', 'definition','Određeno područje ili administrativno područje grada, županije ili većeg zemljopisnog teritorija koji je formuliran prema nekim biološkim, političkim, gospodarskim ili demografskim kriterijima.', 0);
INSERT INTO property VALUES (1,7063, 'hr', 'definition','Skupština nacionalnih delegata, delegata političkih stranaka ili organizacija koji zastupaju osobe ili interese određenoga zemljopisnog područja, ili pakt ili sporazum koji proizlazi iz takve skupštine.', 0);
INSERT INTO property VALUES (1,7066, 'hr', 'definition','Napredak ili napredovanje za veliko geografsko područje ili određeni dio zemlje ili države, posebno u gospodarskom rastu koji vodi do modernizacije i industrijalizacije.', 0);
INSERT INTO property VALUES (1,7073, 'hr', 'definition','Plan za područje prema nekim fiziografskim, biološkim, političkim, upravnim, gospodarskim, demografskim i drugim kriterijima.', 0);
INSERT INTO property VALUES (1,7075, 'hr', 'definition','Postupna metoda i postupak definiranja, razvoja i naznačivanje različitih mogućih smjerova aktivnosti kako bi se zadovoljile postojeće ili buduće potrebe, dugoročni i kratkoročni ciljevi za određeno područje ili administrativni dio grada, županije ili većega zemljopisnog područja.', 0);
INSERT INTO property VALUES (1,7077, 'hr', 'definition','Skup pravila ili naredbi koje je propisala vlada, uprava ili međunarodna organizacija ili ugovor koji se odnosi na određeni teritorij jedne ili više država ili je u njima na snazi.', 0);
INSERT INTO property VALUES (1,7080, 'hr', 'definition','Organizacija ili uređenje velikoga zemljopisnog područja ili određene podjele zemlje ili države koja se može formulirati u skladu s nekim administrativnim, biološkim, političkim, ekonomskim i demografskim kriterijima.', 0);
INSERT INTO property VALUES (1,7237, 'hr', 'definition','Sustavan proces vrednovanja i ocjenjivanja opasnosti od gubitka prema mogućnosti financijske dobiti ili profita.', 0);
INSERT INTO property VALUES (1,7238, 'hr', 'definition','Razmjena informacija o zdravstvenom stanju ili ekološkim rizicima između procjenitelja rizika i menadžera, javnosti, medija, interesnih skupina itd.', 0);
INSERT INTO property VALUES (1,7240, 'hr', 'definition','Subjektivna procjena od strane pojedinaca za koju je vjerojatno da neće imati mnogo veze sa statističkom vjerojatnosti od oštećenja ili ozljeda.', 0);
INSERT INTO property VALUES (1,7241, 'hr', 'definition','Svaki čin, pojava ili proces koji smanjuje vjerojatnost da će doći do štete za neko područje ili njegovo stanovništvo kao rezultat neke opasnosti.', 0);
INSERT INTO property VALUES (1,7244, 'hr', 'definition','Riječni tok vode koja teče u kanalu sa uzvisine na nisku zemlju i na kraju se ulijeva u jezero ili more, osim u pustinjskom području, gdje može nestati. Rijeka i sve njezine pritoke unutar jednog sliva nazivaju se poriječjem.', 0);
INSERT INTO property VALUES (1,7251, 'hr', 'definition','Svaki rast, razvoj ili promjena u kopnenom području koje se nalazi u poriječju neke rijeke i njezinih pritoka.', 0);
INSERT INTO property VALUES (1,7257, 'hr', 'definition','Izmjena prirodnog toka iskopavanjem, preusmjeravanjem, oblaganjem ili drugim sredstvima za pospješivanje vodenog toka.', 0);
INSERT INTO property VALUES (1,7266, 'hr', 'definition','Izravna ili neizravna promjena od strane čovjeka u biološkom, fizičkom, kemijskom ili radiološkom integritetu vode neke rijeke ili riječnog ekosustava.', 0);
INSERT INTO property VALUES (1,7269, 'hr', 'definition','Voda koja teče u kanalu sa uzvisine na nisku zemlju i na kraju se ulijeva u jezero ili more, osim u pustinjskom području, gdje može nestati.', 0);
INSERT INTO property VALUES (1,7272, 'hr', 'definition','Dugačak dio tvrdog tla kojim ljudi mogu voziti s jednog mjesta na drugo.', 0);
INSERT INTO property VALUES (1,7279, 'hr', 'definition','Skrb ili čišćenje i održavanje ulica, autocesta i drugih prometnih pravaca, uključujući i poboljšanje u poravnavanju, proširenje i označivanju, te rad sa podzemnim kablovima, vodovodom ili plinskom mrežom.', 0);
INSERT INTO property VALUES (1,7282, 'hr', 'definition','Sve mjere, tehnike ili planovi čija je svrha smanjiti rizik što ga predstavljaju vozila koja se gibaju izgrađenim putem na zemlji.', 0);
INSERT INTO property VALUES (1,7283, 'hr', 'definition','Sol koja se upotrebljava protiv nastanka leda na cestama; kada se višak soli ispere sa ceste, ona može otrovati raslinje koje raste uz cestu ili povećati koncentracije soli u potocima i rezervama podzemne vode. Također ubrzava propadanje betona i metala.', 0);
INSERT INTO property VALUES (1,7293, 'hr', 'definition','Kretanje motornih vozila i ljudi po cestovnoj mreži.', 0);
INSERT INTO property VALUES (1,7295, 'hr', 'definition','Disciplina koja uključuje projektiranje autocesta i pločnika,  proučavanje i primjenu prometne statistike i aspekte zaštite okoliša pri prijevozu robe i ljudi.', 0);
INSERT INTO property VALUES (1,7296, 'hr', 'definition','Prijevoz robe i osoba vozilima koja voze po cestovnoj mreži.', 0);
INSERT INTO property VALUES (1,7298, 'hr', 'definition','Svaki agregat minerala koji čine dio Zemljine kore. Može biti rahao, kao što su pijesak, glina ili blato, ili konsolidiran, kao što su granit, vapnenac ili ugljen.', 0);
INSERT INTO property VALUES (1,7302, 'hr', 'definition','Generički pojam za pustena ili isprepletena vlakna proizvedena puhanjem ili predenjem niti od rastaljenog kamena, troske ili stakla. Ovaj materijal upotrebljava se za toplinsku izolaciju.', 0);
INSERT INTO property VALUES (1,7306, 'hr', 'definition','Bilo koji od relativno malih sisavaca s posteljicom koji čine red Rodentia, koji imaju sjekutiće koji stalno rastu, a specijalizirani su za glodanje.', 0);
INSERT INTO property VALUES (1,7312, 'hr', 'definition','Organ vaskularne biljke koji apsorbira i služi za pričvršćivanje; na njemu ne rastu ni listovi ni cvjetovi i obično se nalazi ispod razine zemlje.', 0);
INSERT INTO property VALUES (1,7314, 'hr', 'definition','Okrugla peć za toplinsku obradu, koja tijekom toplinske obrade vrti obrađivani predmet oko osi peći; obrađivani komadi se prenose kroz peć duž kružne putanje.', 0);
INSERT INTO property VALUES (1,7319, 'hr', 'definition','Svaki uspostavljeni ili odabrani smjer za prolazak ili putovanje.', 0);
INSERT INTO property VALUES (1,7320, 'hr', 'definition','Djelatnost projektiranja, organiziranja ili pripremanja za izgradnju bulevara, kućica za naplatu cestarine, autocesta i drugih prometnica.', 0);
INSERT INTO property VALUES (1,7324, 'hr', 'definition','Krem do tamno smeđi elastični materijal dobiven koagulacijom i sušenjem lateksa iz pojedinih biljaka, a posebno stabla gumijevca (kaučukovca).', 0);
INSERT INTO property VALUES (1,7326, 'hr', 'definition','Sustavan niz aktivnosti u kojoj se čvrsta tvar koja nastaje iz stabla kaučukovca i biljaka otvrdnjava i kemijski obrađuje da bi dobila jačinu, elastičnost, otpornost i druge kvalitete potrebne za proizvodnju proizvoda kao što su gumice za brisanje, elastične trake, crijeva za vodu, električni izolacija i automobilske gume.', 0);
INSERT INTO property VALUES (1,7327, 'hr', 'definition','Gospodarski sektor koji okuplja komercijalna poduzeća koja se bave proizvodnjom i marketingom prirodnih ili sintetičkih gumenih proizvoda.', 0);
INSERT INTO property VALUES (1,7328, 'hr', 'definition','Svaki odbačeni ili neželjeni materijal od sintetičke ili prirodne gume, često nusprodukt pri obradi gume.', 0);
INSERT INTO property VALUES (1,7340, 'hr', 'definition','Brzina kojom se voda uklanja tako da teče preko površine tla. Ova brzina je određena teksturom tla, nagibom, klimom i pokrovom na korištenom zemljištu (npr. Popločane površine, trava, šuma, golo tlo).', 0);
INSERT INTO property VALUES (1,7345, 'hr', 'definition','Područje izvan granica bilo kojeg naselja sa statusom gradske općine ili bez njega, grada, sela, ili bilo kojeg drugog određenog stambenog ili poslovnog područja, kao što su  dijelovi grada, poslovni prostori ili shopping centar, ili općinska izgradnja.', 0);
INSERT INTO property VALUES (1,7348, 'hr', 'definition','Seoski okoliš.', 0);
INSERT INTO property VALUES (1,7352, 'hr', 'definition','Ukupan broj ljudi koji žive u poljoprivrednom ili seoskom kraju.', 0);
INSERT INTO property VALUES (1,7353, 'hr', 'definition','Skup nastambi smještenih u ruralnom području.', 0);
INSERT INTO property VALUES (1,7354, 'hr', 'definition','Odmor organiziran na farmi: jela se pripremaju s prirodnim proizvodima, a gosti se zabavljaju zanatima, sportskim i poljoprivrednim aktivnostima.', 0);
INSERT INTO property VALUES (1,7362, 'hr', 'definition','Sigurnost od štete, ozljede, opasnosti ili rizika, često kao rezultat planiranih mjera ili priprema.', 0);
INSERT INTO property VALUES (1,7363, 'hr', 'definition','Procesu proučavanja potrebe za, ili djelotvornosti, aktivnosti, postupaka ili uređaja namijenjenih smanjenju pojave ili opasnosti od ozljeda, gubitaka i opasnosti za osobe, imovinu ili okoliš.', 0);
INSERT INTO property VALUES (1,7364, 'hr', 'definition','Djelovanje, postupak ili naprava oblikovani tako da smanje pojave ili opasnosti od ozljeda, gubitaka i opasnosti za osobe, imovinu ili okoliš.', 0);
INSERT INTO property VALUES (1,7366, 'hr', 'definition','Načelo ili propis kojim se uređuju radnje, postupci ili uređaji namijenjeni smanjenju pojave ili opasnosti od ozljeda, gubitaka i opasnosti za osobe, imovinu ili okoliš.', 0);
INSERT INTO property VALUES (1,7367, 'hr', 'definition','Norma ili mjera primjenjiva u pravnim slučajevima za bilo koju radnju, postupak ili napravu, oblikovani tako da smanje pojave ili opasnosti od ozljeda, gubitaka i opasnosti za osobe, imovinu ili okoliš.', 0);
INSERT INTO property VALUES (1,7369, 'hr', 'definition','Skup pravila i propisa koje usvoje vlasti u vezi sa konstrukcijskim i mehaničkim standardima za sigurnost.', 0);
INSERT INTO property VALUES (1,7370, 'hr', 'definition','Istraživanje, detaljno ispitivanje i obično pisano izvješće o potrebi za aktivnostima, postupcima ili uređajima namijenjenih smanjenju pojave ili opasnosti od ozljeda, gubitaka i opasnosti za osobe, imovinu ili okoliš, ili o njihovoj djelotvornosti.', 0);
INSERT INTO property VALUES (1,7373, 'hr', 'definition','Svaki od različitih repatih vodozemaca, kao što su Salamandra salamandra iz središnje i južne Europe. Obično žive na kopnu, imaju izdužena tijela, a vraćaju se u vodu samo radi  razmnožavanja.', 0);
INSERT INTO property VALUES (1,7376, 'hr', 'definition','Nakupljanje topljivih soli isparavanjem vode koja ih je nosila do zone tla, u tlo sušnog i slabo odvodnjavanog područja.', 0);
INSERT INTO property VALUES (1,7384, 'hr', 'definition','Opći naziv za porodicu mikroorganizama, jednu od najvećih skupina bakterija, koja uključuje bakterije koje su najčešće uključene u trovanja hranom i gastroenteritis. Nehigijensko rukovanje i nedovoljna termička obrada peradi i mesa, nepravilno pohranjivanje hladnog mesa i, u novije vrijeme, kontaminacija jaja nesilica iz masovnog uzgoja u peradarnicima najčešći su izvori infekcije salmonelom.', 0);
INSERT INTO property VALUES (1,7390, 'hr', 'definition','Količina soli u otopini.', 0);
INSERT INTO property VALUES (1,7396, 'hr', 'definition','Područja bočate, plitke vode koja se obično nalazi u priobalnim područjima i u deltama. Također postoje i močvare u unutrašnjosti u sušnim područjima gdje voda ima visoku razinu soli zbog isparavanja. To su ekološki osjetljiva područja, iznimno osjetljiva na onečišćenja iz industrijskih i poljoprivrednih kemikalija, ili na toplinsko onečišćenje, koje se često pojavljuje kad se riječna voda upotrebljava kao rashladno sredstvo u elektranama i industrijskim postrojenjima.', 0);
INSERT INTO property VALUES (1,7397, 'hr', 'definition','Livada izložena preplavljivanju slanom vodom.', 0);
INSERT INTO property VALUES (1,7400, 'hr', 'definition','Masa soli koja se ubrizgava kao dijapir (masa kupolastog oblika u kojoj su pokrovne stijene probijene istiskivanjem plastičnih jezgrenih materijala) u pokrovne sedimentne stijene, čime ih buši i deformira. Mehanizam je sličan onome intruzivne magme, pri čemu se sol deformira i ponaša kao plastika pod pritiskom. To je od velike gospodarske važnosti, jer pomaže u formiranju strukture "zamke" za akumuliranje nafte, osim povezanih nalazišta anhidrita, gipsa i sumpora.', 0);
INSERT INTO property VALUES (1,7401, 'hr', 'definition','Proizvod reakcije kada metal zamjenjuje vodik u kiselini.', 0);
INSERT INTO property VALUES (1,7402, 'hr', 'definition','Morska voda, koja se odlikuje visokom slanošću.', 0);
INSERT INTO property VALUES (1,7404, 'hr', 'definition','Čin, postupak ili posao spašavanja plovila ili njihovih tereta od gubitka na moru.', 0);
INSERT INTO property VALUES (1,7407, 'hr', 'definition','Pribavljanje malih reprezentativnih količina materijala u svrhu analize.', 0);
INSERT INTO property VALUES (1,7410, 'hr', 'definition','Metoda nasumičnog odabira predmeta iz skupa na takav način da uzorak bude predstavnik cjeline.', 0);
INSERT INTO property VALUES (1,7413, 'hr', 'definition','Rahli materijal koji se sastoji od malih mineralnih čestica, ili stijena i mineralnih čestica, koje se razlikuju golim okom; zrnca variraju od gotovo sfernih do uglatih, s promjerom u rasponu od 1/16 do 2 milimetra.', 0);
INSERT INTO property VALUES (1,7419, 'hr', 'definition','Stabilizacija dina sadnjom trave marram grass (Ammophila arenaria), ili rižine trave, čiji dugački korijeni vežu duge površinskih slojeva pijeska i tako sprječavaju njegovo uklanjanje zbog vjetra. Metoda koja se bavi tim istim problemom u većim razmjerima  je pošumljavanje.', 0);
INSERT INTO property VALUES (1,7420, 'hr', 'definition','Nakupljanje slobodnog pijeska koji nagomilava vjetar, koje se obično nalaze duž niskih obala iznad razine plime, a rjeđe na granici velikih jezera ili riječnih dolina, kao i u različitim  pustinjskim regijama, gdje postoji izobilje pijeska na suhim površinama u nekom dijelu godine.', 0);
INSERT INTO property VALUES (1,7423, 'hr', 'definition','Vađenje pijeska eksploatacijom za građevinske svrhe i za vađenje teških minerala, kao što su rutil i cirkon.', 0);
INSERT INTO property VALUES (1,7425, 'hr', 'definition','Mjesto na kojem se pijesak vadi iz zemlje.', 0);
INSERT INTO property VALUES (1,7436, 'hr', 'definition','Oprema za osobnu higijenu i uklanjanje organskih otpadaka.', 0);
INSERT INTO property VALUES (1,7438, 'hr', 'definition','Tehnička metoda zbrinjavanja krutog otpada na kopnu na način koji štiti okoliš, rasprostiranjem otpada u tankim slojevima, njegovim sabijanjem do najmanjeg praktično izvedivog volumena i njegovo pokrivanje zbijenom zemljom na kraju svakoga radnog dana ili u češćim intervalima prema potrebi.', 0);
INSERT INTO property VALUES (1,7443, 'hr', 'definition','Proučavanje i upotreba praktičnih mjera za očuvanje javnog zdravlja.', 0);
INSERT INTO property VALUES (1,7444, 'hr', 'definition','Planovi za kontrolu fizičkih čimbenika u ljudskom okruženju koji mogu biti štetni za razvoj, zdravlje ili preživljavanje.', 0);
INSERT INTO property VALUES (1,7449, 'hr', 'definition','Oznaka ili mjera stupnja organskog onečišćenja.', 0);
INSERT INTO property VALUES (1,7450, 'hr', 'definition','Koji se odnosi na razvrstavanje organizama prema načinu na koji podnose onečišćenje.', 0);
INSERT INTO property VALUES (1,7452, 'hr', 'definition','Predmet koji kruži oko većega. Umjetni sateliti u Zemljinoj orbiti se upotrebljavaju za komunikaciju, prikupljanje vojnih obavještajnih podataka, praćenje vremenskih i drugih pojava u okolišu itd.', 0);
INSERT INTO property VALUES (1,7457, 'hr', 'definition','Iznos tekućeg dohotka koji se ne potroši za životne potrebe ili iz zadovoljstva.', 0);
INSERT INTO property VALUES (1,7458, 'hr', 'definition','Komadići drva načinjeni pilom pri piljenju.', 0);
INSERT INTO property VALUES (1,7464, 'hr', 'definition','Bolest u kojoj su ljudi napadnuti jednom od tri vrste krvnih metilja: Schistosoma mansoni, Schistosoma Haematobium i Schistosoma Japonicum; odrasli crvi nastanjuju se u krvnim žilama.', 0);
INSERT INTO property VALUES (1,7465, 'hr', 'definition','Ustanova ili zgrada za odgoj i obrazovanje djece i mladih ljudi.', 0);
INSERT INTO property VALUES (1,7470, 'hr', 'definition','Nastava ili izobrazba dobivena u odgojno-obrazovnoj ustanovi, osobito izobrazba osoba mlađih od fakultetske dobi.', 0);
INSERT INTO property VALUES (1,7472, 'hr', 'definition','Proučavanje fizičkog svemira i njegovog sadržaja putem promatranja, mjerenja i pokusa koji se mogu reproducirati i kako bi se utvrdili, provjerili ili mijenjali opći zakoni kojima se objašnjava njegova priroda i ponašanje.', 0);
INSERT INTO property VALUES (1,7477, 'hr', 'definition','Preneseno ili primljeno znanje koje se odnosi na sustavno proučavanje fizičkog svijeta ili na mehanička ili industrijska umijeća.', 0);
INSERT INTO property VALUES (1,7480, 'hr', 'definition','Tijek postupanja što ga usvaja i provodi vlada, poslovni subjekti ili neke druge organizacije, koji promiče ili određuje smjer za sustavno proučavanje, istraživanje i provedbu pokusa o određenom aspektu fizičkog ili materijalnog svijeta, što može uroditi znanstvenim doprinosima u grani znanja.', 0);
INSERT INTO property VALUES (1,7482, 'hr', 'definition','Propisani korak ili način postupanja u procjene utjecaja na okoliš, kojim se održava javna rasprava kako bi raspravljalo o informacijama koje treba razvijati, alternativama koje treba razmotriti i drugim važnim pitanjima zaštite okoliša.', 0);
INSERT INTO property VALUES (1,7483, 'hr', 'definition','Materijal prikladan za recikliranje nastao u bilo kojem procesu proizvodnje ili odbačeni proizvodi široke potrošnje.', 0);
INSERT INTO property VALUES (1,7484, 'hr', 'definition','Trgovina ili promet odbačenim ili neupotrebljivim materijalima koji se na neki način mogu ponovno upotrijebiti.', 0);
INSERT INTO property VALUES (1,7485, 'hr', 'definition','Iznos novca ili novčana vrijednost po kojoj se materijali odbačeni nakon proizvodnih operacija mogu kupiti ili prodati.', 0);
INSERT INTO property VALUES (1,7486, 'hr', 'definition','Svaki metalni materijal odbačen iz proizvodnih operacija i obično su pogodne za preradu.', 0);
INSERT INTO property VALUES (1,7489, 'hr', 'definition','Automobil isporučen za rastavljanje ili odbacivanje na drugi način.', 0);
INSERT INTO property VALUES (1,7491, 'hr', 'definition','Redukcija električnog polja oko jezgre nabojem prostora okolnih elektrona.', 0);
INSERT INTO property VALUES (1,7495, 'hr', 'definition','1) Općenito, morski dio zemaljske kugle, za razliku od kopnenog dijela.
INSERT INTO property VALUES (1,7497, 'hr', 'definition','Oceansko dno. Također poznato pod nazivom morsko dno. Oceansko dno se definira kao gotovo vodoravna površina oceanskog bazena.', 0);
INSERT INTO property VALUES (1,7498, 'hr', 'definition','Morski mineralni resursi protežu se daleko izvan onih koji se trenutno iskorištavaju; minerali nastaju iz dvije odvojene vrste morskih izvora: od sedimentnih naslaga ispod kontinentalnih šelfova i od obalnih naslaga na površini kontinentalnih šelfova. Daleko najvredniji mineralni resurs koji se eksploatira iz morske okoline je nafta. Priobalna nalazišta na površini kontinentalnih šelfova sadrže zlato, platinu i kositar. Na dnima svjetskih oceana nalaze se manganski čvorovi nastali pelagijskom sedimentacijom ili taloženjem; oni su male, nepravilne, crne do smeđe, drobive, laminirane stvrdnute mase koje se sastoje ponajprije od manganovih soli i minerala od manganovih oksida.', 0);
INSERT INTO property VALUES (1,7500, 'hr', 'definition','Djelatnost ili procesi povezani s iskorištavanjem mineralnih nalazišta s površine, ili ispod površine, oceanskog dna.', 0);
INSERT INTO property VALUES (1,7502, 'hr', 'definition','Horizontalno kretanje vode u oceanu velikih razmjera. Način na koji se energija iz Sunca, pohranjena u moru, prenosi po cijelom svijetu. Na primjer, zbog struja u lukama Ujedinjenog Kraljevstva u zimskim mjesecima nema leda, dok su St. Peterburgu, koji se nalazi na istoj geografskoj širini kao i Shetland Islands, potrebni ledolomci. Sve je više dokaza da je u svijetu kruženje vode u oceanima bilo vrlo različito za vrijeme posljednjeg ledenog doba, a promijenilo se nekoliko puta u dalekoj prošlosti, te je imalo dramatičan učinak na klimu. Oceani imaju vitalno značenje kao ''skladišta'', jer oni apsorbiraju više od polovice Sunčeve topline koja dosegne Zemlju. Ova toplina, koja se ponajprije apsorbira u blizini ekvatora, prenosi se u cijelom svijetu i ispušta drugdje, pri čemu se stvaraju struje koje traju i do 1.000 godina. Dok Zemlja rotira i vjetar djeluje na površinu, struje nose toplu tropsku vodu u hladnije dijelove svijeta. Na snagu i smjer struje djeluju kopnene mase, uska grla kroz uske tjesnace, pa čak i oblik morskog dna. Kada topla voda dosegne polarne predjele, njezina toplina isparava u atmosferu, pa se smanjuje njezina temperatura i povećava njezina gustoća.  Kada se morska voda zamrzne, ona ostavlja sol u nezamrznutoj vodi, i ta hladna voda tone u ocean i počinje teći natrag prema tropima. Naposlijetku se zagrije i ponovno započinje ciklus.', 0);
INSERT INTO property VALUES (1,7513, 'hr', 'definition','Razina površine oceana; osobito, srednja razina na pola puta između plime i oseke, koja se upotrebljava kao standard u računanju nadmorske visine ili morskih dubina.', 0);
INSERT INTO property VALUES (1,7514, 'hr', 'definition','Podizanja razine mora moguća su posljedica globalnog zatopljenja. S povećavanjem količine slobodne vode u oceanima, i sa zatopljavanjem vode, globalno zagrijavanje će se povećati. Osim toga, prema teoriji, zagrijavanje na polovima može smanjiti količinu vode zarobljenu u ledenjacima i ledenim kapama.', 0);
INSERT INTO property VALUES (1,7516, 'hr', 'definition','Brtviti, učiniti vodonepropusnim ili vodootpornim.', 0);
INSERT INTO property VALUES (1,7519, 'hr', 'definition','Svaka naprava ili sustav koji stvara nepropusni spoj između dva mehanička elementa ili elementa procesnog sustava.', 0);
INSERT INTO property VALUES (1,7520, 'hr', 'definition','Točka, lokacija ili zgrada u kojoj se otpadne vode ispuštaju u morsko vodno tijelo, kao što je more, ocean itd.', 0);
INSERT INTO property VALUES (1,7525, 'hr', 'definition','Morski resursi uključuju hranu, energiju i minerale.', 0);
INSERT INTO property VALUES (1,7528, 'hr', 'definition','Zona neutvrđenog materijala koji se proteže prema kopnu od linije niske vode do područja izrazite promjene u materijalu ili fiziografskom obliku ili do linije trajne vegetacije.', 0);
INSERT INTO property VALUES (1,7529, 'hr', 'definition','Jedno od četiri jednaka razdoblja u koje je godine podijeljena ekvinocijima i solsticijima, koja proizlaze iz prividnog kretanja sunca sjeverno i južno od ekvatora tijekom Zemljine orbite oko nje. Ta razdoblja (proljeće, ljeto, jesen i zima) imaju svoje karakteristične vremenske uvjete u različitim regijama, i događaju se u suprotna doba godine na sjevernoj i južnoj hemisfere.', 0);
INSERT INTO property VALUES (1,7531, 'hr', 'definition','Periodično kretanje stanovništva iz jednog područja ili klime na drugu, u skladu s godišnjim ciklusom vremenskih i temperaturnih promjena.', 0);
INSERT INTO property VALUES (1,7535, 'hr', 'definition','Vodene otopine soli u više ili manje konstantnom omjeru, čiji sastav ovisi o nekoliko čimbenika, među kojima prevladavaju živi organizmi, detritično taloženje i povezane kemijske reakcije. Morska voda zauzima više od 98% mase hidrosfere i pokriva nešto više od 70% zemaljske kugle. Zbog sastava i stabilnosti oceana, te načina na koji se oni kontroliraju, oceani su vrlo važni za klimu, a velika pozornost pridaje se proučavanju učinaka onečišćenja. Vjeruje se da čovjekove aktivnosti ubrzavaju promjenu u sastavu morske vode.', 0);
INSERT INTO property VALUES (1,7536, 'hr', 'definition','Uklanjanje soli iz oceanske ili bočate vode.', 0);
INSERT INTO property VALUES (1,7541, 'hr', 'definition','U slučaju narušavanja postojećeg biotopa, može biti stvoren sekundarni biotop kao naknada i zamjenska mjera za gubitak prirodnog biotopa.', 0);
INSERT INTO property VALUES (1,7542, 'hr', 'definition','Godine školovanja nakon osnovne škole i do kraja srednje škole.', 0);
INSERT INTO property VALUES (1,7548, 'hr', 'definition','Dio gospodarstva neke zemlje ili regije koji proizvodi robu uz malu neposrednu upotrebu prirodnih resursa.', 0);
INSERT INTO property VALUES (1,7551, 'hr', 'definition','Roba ili proizvodi koji su već bili upotrebljavani.', 0);
INSERT INTO property VALUES (1,7554, 'hr', 'definition','Dio  komercijalne, industrijske i financijske aktivnosti neke zemlje ili regije, koji se prema organizaciji rashoda dijele bilo na javne, korporativne i privatne, ili prema poljoprivredi, proizvodnji i vrsti uslužnog proizvoda.', 0);
INSERT INTO property VALUES (1,7557, 'hr', 'definition','Mjere, tehnike ili nacrti provedeni za zaštitu od oštećenja ili ograničavanje pristupa bilo nekom aparatu, stroju ili građevini koji su montirani ili spojeni za uporabu.', 0);
INSERT INTO property VALUES (1,7560, 'hr', 'definition','Geomorfološka značajka Zemlje u kojoj se površina spustila tijekom dužeg vremena, uključujući i duboko oceansko dno, interkontinentalni pukotina i povišene i unutarnja poriječja.', 0);
INSERT INTO property VALUES (1,7562, 'hr', 'definition','Odvajanje netopive krute tvari od tekućine u kojoj je  suspendirana taloženjem pod utjecajem gravitacije ili centrifugalne sile.', 0);
INSERT INTO property VALUES (1,7563, 'hr', 'definition','Čin ili proces formiranja ili nagomilavanja taloga u slojevima, uključujući i procese kao što je odvajanje čestica stijena iz materijala iz kojega sediment nastaje, prijenos tih čestica do mjesta taloženja, stvarno taloženje ili sjedanje čestica, kemijske i druge promjene koje se zbivaju u sedimentu, te, naposljetku, konsolidaciju sedimenata u živoj stijeni.', 0);
INSERT INTO property VALUES (1,7565, 'hr', 'definition','Bilo koji materijal što ga prenosi voda koji će se naposljetku  nataložiti na dno nakon što voda izgubi svoju moć prijenosa.', 0);
INSERT INTO property VALUES (1,7566, 'hr', 'definition','Pokretanje i nošenje sedimenta prirodnim sredstvima; osobito prijenos opterećenja vodenog toka suspenzijom, saltacijom (skakanjem), otopinom ili trenjem.', 0);
INSERT INTO property VALUES (1,7567, 'hr', 'definition','Zreli oplođeni sjemeni zametak biljke, koji se sastoji od embrija i njegovog rezervoara hrane okruženog zaštitnom sjemenom ljuskom (testa).', 0);
INSERT INTO property VALUES (1,7568, 'hr', 'definition','Kemikalija koja se nanosi prije sadnje da se zrna i sadnice zaštite od bolesti ili kukaca.', 0);
INSERT INTO property VALUES (1,7571, 'hr', 'definition','Sporo gibanje vode kroz male otvore i prostore na površini nezasićenog tla u vodno tijelo ili iz njega ili u podzemnu vodu.', 0);
INSERT INTO property VALUES (1,7574, 'hr', 'definition','Voda koja se sporo giba kroz male otvore poroznog materijala kao što je tlo ili količina vode koje se procjeđuje.', 0);
INSERT INTO property VALUES (1,7575, 'hr', 'definition','Pojava gibanja Zemlje.', 0);
INSERT INTO property VALUES (1,7578, 'hr', 'definition','Prikupljanje podataka o potresima u nekom području.', 0);
INSERT INTO property VALUES (1,7579, 'hr', 'definition','Veliki morski val koji nastaje poslije potresa i u stanju je izazvati znatna uništenja u nekim obalnim područjima, posebno tamo gdje se javljaju podmorski potresi. Iako na otvorenom oceanu visina vala može biti manja od jednog metra, oni se uzdižu do visina od 15 metara ili više pri ulasku u plitke priobalne vode. Duljina vala na otvorenom oceanu je reda veličine od 100 do 150 km, a brzina putovanja seizmičkog vala mora je između 640 i 960 km/h.', 0);
INSERT INTO property VALUES (1,7581, 'hr', 'definition','Službeno ili prema zakonu dopušteno oduzimanje stvari ili imovine, često zbog kršenje zakona ili izvršenja presude koju je donio sud.', 0);
INSERT INTO property VALUES (1,7582, 'hr', 'definition','Službeno ili prema zakonu dopušteno oduzimanje novčanih dobitaka ili viška stečenog ulaganjima ili dobivenog iz imovine ili povrata, poslovnih primitaka ili prihoda ili iz poslovnih transakcija.', 0);
INSERT INTO property VALUES (1,7586, 'hr', 'definition','Uzgoj životinja s poželjnim osobinama.', 0);
INSERT INTO property VALUES (1,7588, 'hr', 'definition','Uzgoj biljaka s poželjnim osobinama.', 0);
INSERT INTO property VALUES (1,7590, 'hr', 'definition','Vrlo toksičan, nemetalni element; upotrebljava se u analitičkoj kemiji, metalurgiji i fotoelektričnim ćelijama.', 0);
INSERT INTO property VALUES (1,7592, 'hr', 'definition','Niz koraka ili sustav usluga ili aktivnosti osmišljenih tako da omoguće pojedincu da sam sebi pomogne ili da se poboljša bez ovisnosti o pomoći drugh.', 0);
INSERT INTO property VALUES (1,7594, 'hr', 'definition','Prirodni proces organskog raspadanja kojim nastaju hranjive tvari koje upotrebljavaju autotrofni organizmi.', 0);
INSERT INTO property VALUES (1,7595, 'hr', 'definition','Sustav biološke zajednice i neživog okoliša koji međusobno djeluju jedan na drugoga u regijama koje imaju između 25,4 cm i 50,8 cm oborina i sposobne su osigurati preživljavanje nekim travama i grmlju, ali ne i šumama.', 0);
INSERT INTO property VALUES (1,7597, 'hr', 'definition','Kruta kristalna tvar čija električna vodljivost se nalazi između metala i izolatora i obično jako ovisi o temperaturi.', 0);
INSERT INTO property VALUES (1,7599, 'hr', 'definition','Proizvod koji je djelomično obrađen i upotrebljava se kao sirovina u naknadnoj fazi proizvodnje.', 0);
INSERT INTO property VALUES (1,7600, 'hr', 'definition','Element koji ima neka svojstva koja su značajka metala i drugih nemetala. Mnogi metaloidi proizvode amfoterne okside (npr. arsen ili antimon), a mnogi su poluvodiči.', 0);
INSERT INTO property VALUES (1,7603, 'hr', 'definition','Područja zemlje u kojima se mogu primijeniti posebne mjere za zaštitu prirodnih staništa koji predstavljaju visoku razinu osjetljivosti.', 0);
INSERT INTO property VALUES (1,7604, 'hr', 'definition','Formalizirani postupak za identifikaciju učinka promjena u različitim komponentama modela na rezultat modela. Analiza osjetljivosti je sastavni dio pokusne simulacije i može utjecati na formulacije modela. Ona se obično upotrebljava za ispitivanje ponašanja modela. Opći postupak je definiranje varijable rezultata modela koja predstavlja važan aspekt ponašanja modela. Vrijednosti različitih ulaza modela zatim se variraju i prati se rezultirajuća promjena u izlaznoj varijabli. Velike promjene u izlaznoj varijabli znači da je određena varirana ulazna vrijednost važna u kontroliranju ponašanja modela. Unutar ove opće definicije, analiza osjetljivosti primjenjuje se na različite ulaza modela, uključujući varijabla stanja, varijable okoliša i početne uvjete.', 0);
INSERT INTO property VALUES (1,7609, 'hr', 'definition','Prikupljanje pojedinih komponenti krutog otpada iz bilo kojeg izvora, obično podijeljenih u različite kontejnere za odlaganje, kako bi se materijal oporabio, ponovno iskoristio ili reciklirao ili da bi se olakšao njegov odvoz i odlaganje.', 0);
INSERT INTO property VALUES (1,7611, 'hr', 'definition','Odvajanje različitih vrsta otpada na mjestu nastanka (npr. odvajanje papira, metala i stakla od ostalog otpada) kako bi recikliranje bilo jednostavnije i učinkovitije.', 0);
INSERT INTO property VALUES (1,7612, 'hr', 'definition','Stroj za odvajanje tvari različite specifične težine s pomoću vode ili zraka.', 0);
INSERT INTO property VALUES (1,7613, 'hr', 'definition','Spremnik, obično podzemni, u koji teče kanalizacija, pri čemu se taložne tvari u cijelosti ili djelomično razgrađuju anaerobnim djelovanjem. Konačne otpadne vode mogu se pustiti da se upiju u tlo kroz sustav poljoprivrednih odvoda ako je tlo pogodno za tu svrhu. Kao alternativa, spremnik  u pravilnim vremenskim razmacima moraju prazniti posebni kamioni-cisterne.', 0);
INSERT INTO property VALUES (1,7616, 'hr', 'definition','1) Pravni pojam koji se općenito odnosi na čin privremenog oduzimanja vrijedne imovine od strane ovlaštenog službenika suda i njezinu pohanu, obično kako bi se spriječila prodaja ili zloporaba imovine prije rješavanja spora u vezi s vlasništvom nad tom imovinom.  
INSERT INTO property VALUES (1,7617, 'hr', 'definition','Gmazovi iz podreda Ophidia, koji obično imaju ljuskavo valjkasto tijelo bez udova, srasle kapke i čeljusti izmijenjene za gutanje velikog plijena: uključuje otrovne oblike, kao što su kobre i zvečarke, velike neotrovne udave, te male bezopasne vrste, kao što je bjelouška.', 0);
INSERT INTO property VALUES (1,7618, 'hr', 'definition','Područje što ga opslužuje neki javni objekt kao što je škola, knjižnica, policijska postaja, park itd.', 0);
INSERT INTO property VALUES (1,7621, 'hr', 'definition','Obavljanje poslova za kojima postoji stalna potražnja javnosti osiguravanjem radne snage i upotrebe alata odnosno opreme i sredstava.', 0);
INSERT INTO property VALUES (1,7622, 'hr', 'definition','Raspodjela ili ukupan broj zajednica, sela i kuća u određenom geografskom području.', 0);
INSERT INTO property VALUES (1,7625, 'hr', 'definition','Fizički obrazac širenja velikih urbanih područja niske gustoće prema tržišnim uvjetima u okolne poljoprivredne površine. Takvo širenje velikih urbanih područja prethodi glavnim pravcima rasta gradova i podrazumijeva slabo planiranje kontrole podjele zemljišta. Razvoj je nejednolik, raspršen i neravnomjeran, s tendencijom prekidanja jer se neka područja ''preskaču'', pri čemu ostaju poljoprivredne enklave.', 0);
INSERT INTO property VALUES (1,7627, 'hr', 'definition','Spremnik u koji se unosi dvofazna smjesa i unesene krute tvari se talože pod utjecajem Zemljine sile teže tijekom pohrane.', 0);
INSERT INTO property VALUES (1,7628, 'hr', 'definition','Sav otpad koji se prenosi tekućinom koja sadrži životinjske ili biljne tvari u obliku suspenzije ili otopine, tla i oborinske vode, ili kemikalije u otopini.', 0);
INSERT INTO property VALUES (1,7630, 'hr', 'definition','Zemljište na kojem se kanalizacija ili bilo koja druga vrsta otpadnih voda raširuje da bi se pročistila; to je vrsta pročišćavanja otpadnih voda.', 0);
INSERT INTO property VALUES (1,7632, 'hr', 'definition','Polu-tekući otpad s koncentracijom krute tvari većom od 2500 dijelova na milijun, dobivenih pročišćavanjem komunalnih otpadnih voda.', 0);
INSERT INTO property VALUES (1,7636, 'hr', 'definition','Pročišćavanje otpadnih voda događa se u dvije faze - primarnom i sekundarnom pročišćavanju. Primarna faza uključuje proces prosijavanja krutih tvari iz otpadnih voda, pri čemu nastaju mulj i relativno čista voda za daljnje pročišćavanje ili za ispuštanje u rijeke, more ili na kopno. U sekundarnoj fazi mulj se stalno miješa u velikim spremnicima kako bi se u smjesu dovelo više kisika, što omogućuje bakterijama da razgrađuju organske tvari, i preostaje neopasan ostatak koji pada u obliku taloga na dno spremnika. Nakon obrade bistra voda na vrhu spremnika ispušta se u rijeke, a sediment se upotrebljava kao odlagalište ili se ispušta u more.', 0);
INSERT INTO property VALUES (1,7641, 'hr', 'definition','Sustav cijevi, obično podzemnih, za prijenos otpadnih voda i ljudskih otpadaka dalje od kuća i drugih zgrada, na mjesto gdje se mogu sigurno odložiti.', 0);
INSERT INTO property VALUES (1,7651, 'hr', 'definition','Pregrada kojom se štetne tvari drža dalje od ljudi.', 0);
INSERT INTO property VALUES (1,7652, 'hr', 'definition','Poljoprivredni uzgoj pri kojem se upotrebljava rotacija polja, a ne usjeva, pa su kraća razdoblja uzgoja usjeva popraćena dugim razdobljima odmaranja tla (ugara) plodnost se  održava obnovom vegetacije.', 0);
INSERT INTO property VALUES (1,7654, 'hr', 'definition','Plovilo koje pokreće motor ili jedra za navigaciju na vodi, osobito veliko plovilo koje se ne može staviti na palubu drugog plovila, za razliku od čamca ili barke.', 0);
INSERT INTO property VALUES (1,7655, 'hr', 'definition','Umijeće ili poslovna djelatnost projektiranja i gradnje brodova.', 0);
INSERT INTO property VALUES (1,7659, 'hr', 'definition','Neočekivani događaj, kvar ili gubitak u koje je uključeno  plovilo ili njegov sadržaj tijekom komercijalnog prijevoza koja predstavlja potencijalnu štetu za osobe, imovinu ili okoliš.', 0);
INSERT INTO property VALUES (1,7662, 'hr', 'definition','Kućanski otpaci i operativni otpad koji nastaje radom broda, koji se ispušta stalno ili povremeno, a koji nastaje tijekom uobičajenog rada broda; obično ne uključuje otpad od svježe ribe iz ribolovnih operacija.', 0);
INSERT INTO property VALUES (1,7663, 'hr', 'definition','Ispuštanje brodskog otpada u more.', 0);
INSERT INTO property VALUES (1,7668, 'hr', 'definition','Područje namijenjeno gađanju u mete.', 0);
INSERT INTO property VALUES (1,7669, 'hr', 'definition','Mjesto, osobito manja zgrada, za maloprodaju robe i usluga.', 0);
INSERT INTO property VALUES (1,7670, 'hr', 'definition','Zatvoreni prostor s mnogobrojnim raznim trgovinama.', 0);
INSERT INTO property VALUES (1,7679, 'hr', 'definition','Izvedba, program ili izložba namijenjena zabavi za skupinu ljudi, koja se prikazuje ili kroz putem nekog medija masovnog priopćavanja, kao što su radio ili televizija, ili uživo, u muzeju ili kazalište.', 0);
INSERT INTO property VALUES (1,7680, 'hr', 'definition','Stroj za smanjenje veličine koji dere ili melje materijale na manje i više jednolike veličine čestica. Taj proces sjeckanja se također naziva smanjivanjem veličine, brušenjem, glodanjem, usitnjavanjem, pulverizacijom (drobljenjem u prah), struganjem, granuliranjem, razbijanjem, kidanjem na komadiće, drobljenjem, rezanjem, brušenjem/turpijanjem.', 0);
INSERT INTO property VALUES (1,7681, 'hr', 'definition','Stroj za mljevenje otpadaka u manje komade koja se upotrebljava za kidanje otpadaka u manje komadiće mljevenjem ili razbijanjem.', 0);
INSERT INTO property VALUES (1,7683, 'hr', 'definition','Drvenasta višegodišnja biljka, manja od drveta, s nekoliko velikih grana koje rastu blizu osnove glavnog debla.', 0);
INSERT INTO property VALUES (1,7684, 'hr', 'definition','Područje gdje se vagon ili vlak može odvojiti ili isključiti iz prometa ili premjestiti s jednog kolosijeka na drugi.', 0);
INSERT INTO property VALUES (1,7686, 'hr', 'definition','Skup simptoma, uključujući i glavobolje, umor, iritaciju očiju i vrtoglavicu, koji obično utječu na radnike u modernim hermetično zatvorenim poslovnim zgradama bez pristupa svježeg zraka, za koje se smatra da ih prouzročuju onečišćivači u zatvorenim prostorima, kao što su pare formaldehid, čestice, mikroorganizmi itd.', 0);
INSERT INTO property VALUES (1,7688, 'hr', 'definition','Svaki sekundarni učinak, osobito neželjeni.', 0);
INSERT INTO property VALUES (1,7690, 'hr', 'definition','Raspodjela krutih čestica po veličini na nizu standardnih sita sve manjih veličina, izraženo kao težinski postotak.', 0);
INSERT INTO property VALUES (1,7692, 'hr', 'definition','Svaka naprava za smanjenje buke, osobito naprava u ispušnom sustavu motornih vozila.', 0);
INSERT INTO property VALUES (1,7694, 'hr', 'definition','Krhak metaloidni element koji postoji u dva alotropna oblika; pojavljuje se najviše u pijesku, kvarcu, granitu, feldšpatu (glinencu) i glini. Obično je siva kristalna krutina, ali se nalazi i u obliku smeđeg amorfnog praha. Upotrebljava se u tranzistorima, ispravljačima, solarnim ćelijama i legurama. Njegovi spojevi se uvelike upotrebljavaju u proizvodnji stakla, građevinskoj industriji, te u obliku silikona.', 0);
INSERT INTO property VALUES (1,7697, 'hr', 'definition','Velik okrugli toranj na poljoprivrednom gospodarstvu za pohranjivanje žita ili silaže za stoku.', 0);
INSERT INTO property VALUES (1,7698, 'hr', 'definition','Sitnozrnati mineralnih materijal nastao erozijom komadića kamenja i nataložen u rijekama i jezerima. Njegove čestice su prijelazni oblik između pijeska i gline. Čestice mogu biti u rasponu veličine promjera između 0,01 i 0,05 mm.', 0);
INSERT INTO property VALUES (1,7699, 'hr', 'definition','Vrlo rastezljiv kovak sjajan sivkasto-bijeli element s najvećom električnom i toplinskom vodljivosti od svih metala. Pojavljuje se u slobodnom stanju i u argentitu i drugim rudama: upotrebljava se za izradu nakita, pribora za jelo, novca, električnih kontakata, te u galvanizaciji. Njegovih spojevi upotrebljavaju se u fotografiji.', 0);
INSERT INTO property VALUES (1,7702, 'hr', 'definition','Prikaz nekog problema, situacije u matematičkim izrazima, osobito pomoću računala.', 0);
INSERT INTO property VALUES (1,7707, 'hr', 'definition','Formiranje koherentne vezane mase zagrijavanjem metalnih prahova, bez taljenja, a upotrebljava se uglavnom u metalurgiji praha.', 0);
INSERT INTO property VALUES (1,7709, 'hr', 'definition','Proces odabiranja ili izabiranja neke lokacije ili područja za određenu svrhu.', 0);
INSERT INTO property VALUES (1,7712, 'hr', 'definition','Određivanje poprečnog prosjeka dijelova konstrukcije na temelju statike i čvrstoće materijala.', 0);
INSERT INTO property VALUES (1,7715, 'hr', 'definition','Spuštanje na snijegu na skijama, osobito kao sport.', 0);
INSERT INTO property VALUES (1,7716, 'hr', 'definition','Tkivo koje čini vanjski pokrov tijela kralježnjaka: sastoji se od dva sloja, od kojih vanjski može biti pokriven dlakom, ljuskama, perjem itd. Njezina funkcija je uglavnom zaštitna i osjetilna.', 0);
INSERT INTO property VALUES (1,7720, 'hr', 'definition','Nemetalni proizvod koji nastaje međusobnim djelovanjem fluksa i nečistoća u taljenju i rafiniranju metala.', 0);
INSERT INTO property VALUES (1,7724, 'hr', 'definition','Mjesto gdje se životinje kolju radi hrane.', 0);
INSERT INTO property VALUES (1,7725, 'hr', 'definition','Dijelovi tijela životinja odsječeni u pripremi za uporabu kao hrana. Ovaj otpad može doći iz nekoliko izvora, uključujući klaonice, restorane, trgovine i poljoprivredna gospodarstava.', 0);
INSERT INTO property VALUES (1,7726, 'hr', 'definition','Ubijanje životinja za hranu.', 0);
INSERT INTO property VALUES (1,7729, 'hr', 'definition','Povremeno stanje fiziološkog odmora za vrijeme kojeg svijest ne djeluje, a metabolizam se usporava.', 0);
INSERT INTO property VALUES (1,7731, 'hr', 'definition','Nagnuta površina iskopa.', 0);
INSERT INTO property VALUES (1,7733, 'hr', 'definition','1) Meki, sličan juhi ili blatan talog na dnu, kao što je onaj koji se nalazi na području plime i oseke ili u koritu potoka.
INSERT INTO property VALUES (1,7737, 'hr', 'definition','Obrada za stabilizaciju sirovog mulja. Obrada može biti anaerobni ili aerobni proces.', 0);
INSERT INTO property VALUES (1,7738, 'hr', 'definition','Metoda koja se upotrebljava za sušenje i smanjenje obujma i težine mulja. Budući da je za spaljivanje potrebno pomoćno gorivo za dobivanje i održavanje visoke temperature te za isparavanje vode koja je sastojak ulaznog mulja, prije spaljivanja treba primijeniti tehnike koncentracije. Spaljivanje mulja je dvostupanjski proces koji uključuje sušenje i izgaranje nakon prethodnog procesa uklanjanja vode, s pomoću filtara, podloga za sušenje ili centrifuga.', 0);
INSERT INTO property VALUES (1,7739, 'hr', 'definition','Bazen za uklanjanje krutih tvari koje se talože i kroz koje se propušta otpadna voda pri obradi.', 0);
INSERT INTO property VALUES (1,7740, 'hr', 'definition','Obrada mulja otpadnih voda kako bi postali neopasni. To se može učiniti aerobnom ili anaerobnom razgradnjom nakon sušenja u sloju pijeska, filtriranje i spaljivanje, filtriranje i sušenje, ili mokre oksidacije zrakom.', 0);
INSERT INTO property VALUES (1,7742, 'hr', 'definition','Vertikalna klizna vrata ili ventil za reguliranje protoka vode u kanalu ili zapornici.', 0);
INSERT INTO property VALUES (1,7755, 'hr', 'definition','Mala zatvorena konstrukcija koja sadrži izvor topline, a obično se upotrebljava za intenzivno grijanje. Većina tih peći je obložena vatrostalnim materijalom, izvor topline obično su električni elementi ili izgaranje plina, koksa ili ugljena.', 0);
INSERT INTO property VALUES (1,7756, 'hr', 'definition','Izvor u domaćinstvima koji uvodi male količine onečišćujućih tvari u javni sustav za obradu otpada.', 0);
INSERT INTO property VALUES (1,7759, 'hr', 'definition','Onečišćenje zraka se sastoji od dima i magle.Onečišćenje zraka izazvano djelovanjem Sunčeve svjetlosti na neizgorene ugljikovodike i dušikove okside, uglavnom iz ispuha automobila. Ono se pojavljuje na velikim industrijskim područjima i urbanim naseljima, te uzrokuje iritaciju oka, probleme s disanjem i oštećuje biljni svijet.', 0);
INSERT INTO property VALUES (1,7760, 'hr', 'definition','Čin, uređaj ili najava namijenjena davanju upozorenja ili obavijesti o razini onečišćenja u zraku obično povezanih s oksidantima u određenom području.', 0);
INSERT INTO property VALUES (1,7761, 'hr', 'definition','Raspršene čestice koje se sastoje od vidljivih čestica i plinova, koje nastaju nepotpunim izgaranjem materijala koji sadrže ugljik, kao što su drvo i fosilna goriva.', 0);
INSERT INTO property VALUES (1,7765, 'hr', 'definition','Udisanje i izdisanje karcinogenih dimova od biljnog materijala koji gori, obično duhana.', 0);
INSERT INTO property VALUES (1,7768, 'hr', 'definition','Zmije iz porodice Colubridae, koje uključuju mnoge bezopasne zmije, kao što su bjelouška i poljarica, koje pripadaju porodici Colubridae.', 0);
INSERT INTO property VALUES (1,7769, 'hr', 'definition','Najuobičajeniji oblik smrznute oborine, uglavnom pahuljice ili zvjezdasti kristalići, isprepletene ledene iglice, ili njihova kombinacija, a nerijetko i obložene injem.', 0);
INSERT INTO property VALUES (1,7771, 'hr', 'definition','Lavina relativno čistog snijega; može sadržavati i nešto kamenja i zemlje koji klize niz brdo.', 0);
INSERT INTO property VALUES (1,7774, 'hr', 'definition','Apsorpcija tekućine u krutom ili polukrutom materijalu.', 0);
INSERT INTO property VALUES (1,7776, 'hr', 'definition','Sredstvo za čišćenje, proizvedeno u obliku komada, granula, pahuljice, ili u tekućem obliku, izrađeno od mješavine natrijevih soli raznih masnih kiselina prirodnih ulja i masti.', 0);
INSERT INTO property VALUES (1,7781, 'hr', 'definition','Opće ophođenje osobe ili zajednice, način ponašanja ili djelovanja prema drugima kao pojedincima ili kao članovima različito identificiranih skupina.', 0);
INSERT INTO property VALUES (1,7784, 'hr', 'definition','Postojeća okolnost, situacija ili stanje koje utječe na život, dobrobit i odnose među ljudima u zajednici.', 0);
INSERT INTO property VALUES (1,7787, 'hr', 'definition','Cijena koja se plaća ili gubitak nastao radi stjecanja, proizvodnje ili održavanja nekog bližeg ili daljeg cilja u skupini, zajednici ili društvu.', 0);
INSERT INTO property VALUES (1,7788, 'hr', 'definition','Proces kojim se oni koji proizvode robu ili usluge koji imaju negativan učinak na okoliš i na društvo prisiljavaju da uključe  znanje o mogućim negativnim posljedicama u buduće ekonomske odluke.', 0);
INSERT INTO property VALUES (1,7790, 'hr', 'definition','Stanje naroda i povijesni procesi promjena kroz koje oni prolaze. Pojam razvoja podrazumijeva povezane kulturne i političke promjene, kao i mjere blagostanja koje odražavaju raspodjelu dobara, bogatstva i mogućnosti.', 0);
INSERT INTO property VALUES (1,7791, 'hr', 'definition','Pojam povezan s evolucijskim teorijama povijesti i sa strukturnim funkcionalizmom. Smatra se da se društva kreću od jednostavnih do složenih putem procesa društvene promjene na temelju strukturne diferencijacije.', 0);
INSERT INTO property VALUES (1,7793, 'hr', 'definition','Obrazac, promjena, razvoj i pokretačke sile neke ljudske skupine, zajednice ili društva.', 0);
INSERT INTO property VALUES (1,7795, 'hr', 'definition','Svaka zgrada projektirana, izgrađena ili montirana tako da osigura prostor za život ili interakciju među osobama u nekoj zajednici.', 0);
INSERT INTO property VALUES (1,7796, 'hr', 'definition','Skup ljudi koji stupaju u međusobne odnose i dijele neki osjećaj jedinstva.', 0);
INSERT INTO property VALUES (1,7798, 'hr', 'definition','Lako utvrdiva obilježja društva koja se mogu mjeriti, koja se razlikuju tijekom vremena, te se smatra da otkrivaju neki temeljni aspekt društvene stvarnosti. Općenito, najčešće  upotrebljavani pokazatelji izvode se iz službene statistike, a uključuju podatke o nezaposlenosti, zdravlju, smrtnosti, te o stopama kriminala.', 0);
INSERT INTO property VALUES (1,7803, 'hr', 'definition','Medicina primijenjena na liječenje bolesti koej se pojavljuju u određenim društvenim skupinama.', 0);
INSERT INTO property VALUES (1,7805, 'hr', 'definition','Organizirani napor značajnog broja ljudi da promijene neki veći aspekt ili aspekte društva (ili se odupru promjenama).', 0);
INSERT INTO property VALUES (1,7807, 'hr', 'definition','Udružena građanska aktivnost koju dijeli i obavlja značajan broj ljudi u zajednici ili općoj populaciji.', 0);
INSERT INTO property VALUES (1,7809, 'hr', 'definition','Način djelovanja koji usvoji i nastoji ostvariti vlada, poslovna ili neka druga organizacija, čiji je cilj osigurati da svi ljudi imaju prihvatljive radne ili životne uvjete pružanjem socijalne sigurnosti, socijalne skrbi, zdravstva, osiguranja, poštenih radnih odnosa, niskih cijena stanovanja ili obrazovnih mogućnosti.', 0);
INSERT INTO property VALUES (1,7810, 'hr', 'definition','Generički naziv za niz uvjeta i neprihvatljivih ponašanja za koje se smatra da su manifestacije društvene dezorganizacije i da ih je potrebno mijenjati nekim sredstvima socijalnog inženjeringa. Ti problemi obično uključuju mnoge oblike devijantnog ponašanja (kao što su kriminal, maloljetnička delinkvencija, prostitucija, duševne bolesti, ovisnosti o drogama, samoubojstva) i društvenog sukoba (etničke tenzije, nasilje u obitelji, radničke nemire i tako dalje).', 0);
INSERT INTO property VALUES (1,7811, 'hr', 'definition','Kontinuirano djelovanje, rad ili niz promjena koje se odvijaju na određeni način i koji se odnose na život, blagostanje i odnose ljudskih bića u nekoj zajednici.', 0);
INSERT INTO property VALUES (1,7813, 'hr', 'definition','Javna pomoć, osobito financijska potpora koja se daje osobama koje imaju posebne potrebe ili poteškoće.', 0);
INSERT INTO property VALUES (1,7816, 'hr', 'definition','Aktivnosti socijalne skrbi koje organizira država ili tijelo lokalne vlasti, a provodi osposobljeno osoblje.', 0);
INSERT INTO property VALUES (1,7817, 'hr', 'definition','Pojam koji se u širem smislu primjenjuje na obrazac društvenog ponašanja koji se ponavlja; ili, preciznije na uređene međusobne odnose između različitih elemenata društvenog sustava ili društva.', 0);
INSERT INTO property VALUES (1,7818, 'hr', 'definition','Prikupljanja podataka pri kojima se upotrebljavaju i intervjuiranje i uzimanje uzoraka za izradu kvantitativnih nizova podataka, podložnih računalnoj analizi.', 0);
INSERT INTO property VALUES (1,7819, 'hr', 'definition','Koncept sustava koji se pojavljuje u svim društvenim i prirodnim znanostima i koji je iznjedrio mnoga vlastita djela literature (opća teorija sustava). Sustav je svaki obrazac odnosa između elemenata, te se smatra da posjeduje nova  svojstva koja se pojavljuju povrh svojstava njezinih elemenata.', 0);
INSERT INTO property VALUES (1,7821, 'hr', 'definition','U vezi s društvenim vrijednostima, često se razaznaju razlike između vrijednosti - koje su jake, polutrajne, koje se nalaze u temeljima, a ponekad su i neeksplicitne dispozicije - te stavova - koji su plitki, slabo su branjeni, i vrlo su varijabilna stajališta i mišljenja. Društva obično mogu tolerirati vrlo raznolike stavove, a zahtijevaju određeni stupanj homogenosti i dosljednosti u ljudskim vrijednostima, te osiguravaju zajednički set vrijednosti (koje dijele jedinke u društvu) koje oblikuju društveni i politički konsenzus.', 0);
INSERT INTO property VALUES (1,7822, 'hr', 'definition','Prosperitet, blagostanje ili pogodnost neke zajednice. On obuhvaća osnovne društvene interese sigurnosti, poretka, morala, gospodarskog interesa, te nematerijalne i političke interese.', 0);
INSERT INTO property VALUES (1,7823, 'hr', 'definition','Manja ili veća, manje ili više složena skupina ljudi koju povezuje neki zajednički interes i koja se odlikuje određenim hijerarhijskim odnosima.', 0);
INSERT INTO property VALUES (1,7826, 'hr', 'definition','Bitan element u sastavu društva, organizaciji ili ponašanju koji kombinira financijske dimenzije s dinamikom na međuljudskoj ili međugrupnoj razini.', 0);
INSERT INTO property VALUES (1,7827, 'hr', 'definition','Biotehnologija je primjena bioloških i tehničkih rješenja na probleme, a često se odnosi na industrijsku upotrebu mikroorganizama (možda genetski izmijenjenih) za kemijsku obradu, na primjer, otpada ili vode, ili za proizvodnju hormona ili enzima za medicinske i komercijalne svrhe. Biotehnologija nudi velik potencijal za povećanje poljoprivredne proizvodnje i učinkovitost prerade hrane, smanjenje troškova hrane, poboljšanje kvalitete i sigurnosti hrane te povećanja međunarodne konkurentnosti.', 0);
INSERT INTO property VALUES (1,7830, 'hr', 'definition','Istraživanje o socijalnim pitanjima ili problemima, posebno ono usmjereno na kulturne i okolišne čimbenike.', 0);
INSERT INTO property VALUES (1,7831, 'hr', 'definition','Proučavanje razvoja, organizacije, funkcioniranja i razvrstavanja ljudskih društava.', 0);
INSERT INTO property VALUES (1,7832, 'hr', 'definition','Svaki dio, osobina ili kvaliteta nekog društva koja povezuje vladine politike s međuljudskom ili međugrupnom dinamikom.', 0);
INSERT INTO property VALUES (1,7839, 'hr', 'definition','Smanjenje tvrdoće vode uklanjanjem iona koji stvaraju tvrdoću (uglavnom kalcija i magnezija), taloženjem ili ionskom izmjenom, ili njihovim odvajanjem, npr. Njihovim kombiniranjem s tvarima kao što su određeni fosfati, koji tvore topive, ali neionizirane soli.', 0);
INSERT INTO property VALUES (1,7840, 'hr', 'definition','1) Tvar koja se dodaje drugoj tvari kako bi se povećala njezina mekoća, savitljivost ili plastičnost.
INSERT INTO property VALUES (1,7842, 'hr', 'definition','Softver je opći pojam koji se upotrebljava za opisivanje svih različitih programa koji se mogu upotrijebiti u računalnom sustavu. Softver se može podijeliti u četiri glavne kategorije: sistemski softver, razvojni softver, softver za korisničko sučelje, aplikacijski softver.', 0);
INSERT INTO property VALUES (1,7843, 'hr', 'definition','Gornji sloj kopnene površine Zemlje koji se sastoji od raspadnutih kamenih čestica, humusa, vode i zraka.', 0);
INSERT INTO property VALUES (1,7844, 'hr', 'definition','Proces koji se prirodno pojavljuje u vlažnim klimama i koji je dugo bio predmetom istraživanja, čiji rezultati upućuju na taloženje kiselina. Općeprihvaćeni utjecaj zakiseljavanja tala na produktivnost kopnenih biljaka može se ukratko prikazati kako slijedi: kako tlo postaje sve kiselije, osnovni kationi (Ca, Mg) na izmjeni tala zamjenjuju se vodikovim ionima ili metalima koji su postali više topivi. Bazični kation, sada u otopini, može se ispirati kroz tlo. S vremenom tlo postaje manje plodno i kiselije. Smanjeni pH tla koji je posljedica toga uzrokuju smanjenu, manje aktivnu populaciju mikroorganizama u tlu, što, s druge strane, usporuje razgradnju biljnih ostataka i kruženje važnih biljnih hraniva.', 0);
INSERT INTO property VALUES (1,7845, 'hr', 'definition','Zrak i drugi plinovi u prostorima u tlu; posebno oni koji se nalaze unutar zone prozračivanja. Također poznat kao atmosfera tla.', 0);
INSERT INTO property VALUES (1,7846, 'hr', 'definition','Upotreba brzih kemijskih analiza za određivanje stanja plodnosti tla. Upotrebljava se za identifikaciju hranjivih tvari ili tvari koje su prisutne bilo u nedovoljnim ili u prekomjernim količinama za optimalan rast biljaka. Analize se također upotrebljavaju za praćenje povećanja ili smanjenja plodnosti tla tijekom vremena.', 0);
INSERT INTO property VALUES (1,7847, 'hr', 'definition','Proučavanje živih organizama, uglavnom mikroorganizama i mikro-beskralježnjaka koji žive u tlu, i koji su u velikoj mjeri odgovorni za procese razgradnje od vitalnog značaja za plodnost tla.', 0);
INSERT INTO property VALUES (1,7848, 'hr', 'definition','Prikladnost tla za razne namjene, npr. Stalna proizvodnja kultiviranih usjeva, biljaka za ispašu i sl., ovisno o dubini, teksturi, vrstama minerala, slanosti, vrstama soli, kiselosti itd.', 0);
INSERT INTO property VALUES (1,7849, 'hr', 'definition','Proučavanje anorganskih i organskih komponenti tala i njihovih životnih ciklusa.', 0);
INSERT INTO property VALUES (1,7850, 'hr', 'definition','Povećanje gustoće tla (mase po jedinici volumena) i smanjenje poroznosti tla koje proizlazi iz primijenjenih opterećenja, vibracije ili pritiska. Više zbijena tla (ili drugi materijali) mogu podržati veća opterećenja (nosivost). Gustoća tla može se povećati kontrolom sadržaja vlage, silama zbijanja i postupcima obrade, kao i manipuliranjem vrstom materijala koji se zbija.', 0);
INSERT INTO property VALUES (1,7851, 'hr', 'definition','Opis stanja površine tla u trenutku promatranja, posebice u odnosu prema utjecaju kiše i snijega.', 0);
INSERT INTO property VALUES (1,7852, 'hr', 'definition','Upravljanje tlom radi sprječavanja ili smanjenja erozije tla i osiromašivanjem zbog vjetra i vode. Očuvanje tla od propadanja i gubitka njegovim korištenjem u okviru njegovih sposobnosti; primjena načina očuvanja potrebnih za zaštitu i poboljšanje.', 0);
INSERT INTO property VALUES (1,7853, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propisuje vlada radi zaštite i sprječavanja gubitka površinskog sloja područja razgrađenih stijena i organskih materijala, cijenjenog zbog njihovih hranjivih tvari i sposobnosti za podržavanje života.', 0);
INSERT INTO property VALUES (1,7855, 'hr', 'definition','Tlo oslabljeno kao posljedica ljudske djelatnosti. Istraživanjem što ga je financirao UNEP (Program Ujedinjenih naroda za zaštitu okoliša), o čemu je izvješće objavljeno 1992. god., utvrđeno je da je 10,5% površine pod raslinjem u svijetu ozbiljno oštećeno kao posljedica ljudske aktivnosti od 1945. god. Istraživanje je pokazalo da je velik dio te štete nije bio vidljiv bio prikriven općim porastom produktivnosti globalne poljoprivrede, koja je bila posljedica povećanog navodnjavanja, bolje biljnih sorti i veće upotrebe proizvodnih inputa, kao što su gnojiva i pesticidi. Više od 1/3 oštećenih zemljišta nalazilo se u Aziji, gotovo 1/3 u Africi i 1/4 u Srednjoj Americi. Neka zemljišta oštećena su u tolikoj mjeri da ih se više nije moglo popraviti. Najveći izvori degradacije tla su pretjerana ispaša, neprikladna poljoprivredna praksa i krčenje šuma.', 0);
INSERT INTO property VALUES (1,7856, 'hr', 'definition','Tehnologije koje se upotrebljavaju u uklanjanju PCB-a, PAH-ova, pesticida i, općenitije, organskih spojeva pomoću fizikalne, kemijske ili biološke obrade.', 0);
INSERT INTO property VALUES (1,7857, 'hr', 'definition','Tlo se može pogoršavati ili fizičkim kretanjem čestica tla iz određenog mjesta ili osiromašivanjem elemenata topivih u vodi u tlu, što pridonosi prihranjivanju usjeva, biljaka, trava, drveća i ostaloga ekonomski korisnog raslinja. Fizičko kretanje općenito se naziva erozijom. Vjetar, voda, ledenjački led, životinje i alati pri upotrebi mogu biti sredstva koja izazivaju eroziju.', 0);
INSERT INTO property VALUES (1,7858, 'hr', 'definition','Odvajanje i pomicanje gornjeg sloja tla ili materijala od kojeg se sastoji tlo iz gornjeg dijela profila, djelovanjem vjetra ili tekuće vode, posebno kao rezultat promjena izazvanih ljudskom aktivnošću, kao što su neprikladne ili loše obavljane poljoprivredne djelatnosti.', 0);
INSERT INTO property VALUES (1,7860, 'hr', 'definition','Stanje tla s obzirom na količinu i dostupnost biljkama elemenata potrebnih za rast biljaka.', 0);
INSERT INTO property VALUES (1,7861, 'hr', 'definition','Primjena bilo kojega organskog ili anorganskog materijala prirodnog ili sintetičkog podrijetla na tlu kako bi se unio jedan ili više elemenata bitnih za rast biljaka.', 0);
INSERT INTO property VALUES (1,7862, 'hr', 'definition','Kombinacija prirodnih procesa kojima nastaju tla. Poznata je i pod nazivom pedogeneza. Najvažniji čimbenici formiranja tla su matična stijena, teren, klima, aspekt (strana), vegetacijski pokrov, mikroorganizmi u tlu i starost zemljane površine. Neki pedolozi bi ovom popisu dodali utjecaj ljudskih aktivnosti. Svi navedeni čimbenici pokazuju različite stupnjeve međusobne povezanosti, pri čemu su neki važniji od drugih, a klima se često izdvaja kao najvažniji.', 0);
INSERT INTO property VALUES (1,7863, 'hr', 'definition','Postupak zaštite tla od prekomjerne erozije i postupak poboljšanja plodnosti i produktivnosti tla.', 0);
INSERT INTO property VALUES (1,7866, 'hr', 'definition','Razlikovni uzastopni slojevi tla nastali procesima unutarnje preraspodjele. Konvencionalni slojevi su podijeljeni u  horizonte A, B i C. Horizont A je gornji sloj, koji sadrži humus i iz njega se procjeđuju i/ili ispiru mnogi minerali. Horizont B stvara zonu taloženja i obogaćen je mineralima gline i oksidima željeza ili aluminija iz sloja A. Sloj C je matična stijena za sadašnje tlo i može biti kamen odnosno stijena djelomično nagrizena atmosferskim prilikama, naplavljeni glacijalni ili aluvijalni materijal ili ranije tlo.', 0);
INSERT INTO property VALUES (1,7867, 'hr', 'definition','U mehanici tla i građevinarstvu ovaj izraz se upotrebljava za označavanje povećane težine primijenjene na površinu zemlje.', 0);
INSERT INTO property VALUES (1,7868, 'hr', 'definition','Dvodimenzionalni prikaz koji pokazuje područni opseg ili raspodjelu tla u odnosu prema drugim značajkama zemaljske površine.', 0);
INSERT INTO property VALUES (1,7871, 'hr', 'definition','Proučavanje fizikalnih svojstava tla, posebno onih svojstava koja utječu na njegovu sposobnost da podnese težinu, kao što su sadržaj vode, gustoća, čvrstoća itd.', 0);
INSERT INTO property VALUES (1,7873, 'hr', 'definition','Proučavanje nastajanja, pojave, svojstava, sastava i razvrstavanja minerala prisutnih u tlu.', 0);
INSERT INTO property VALUES (1,7874, 'hr', 'definition','1) Voda pohranjena u tlima.
INSERT INTO property VALUES (1,7875, 'hr', 'definition','Vodni režim tla određuje se fizikalnim svojstvima i rasporedom čestica tla. Pore u tlu određuju njegova svojstva zadržavanja vode. Kada su sve pore pune vode, kaže se da je tlo zasićeno.', 0);
INSERT INTO property VALUES (1,7876, 'hr', 'definition','Organizmi koji žive u tlu.', 0);
INSERT INTO property VALUES (1,7878, 'hr', 'definition','Krute, tekuće i plinovite tvari koje štetno utječu na prirodno stanje tla i mijenjaju ga.', 0);
INSERT INTO property VALUES (1,7880, 'hr', 'definition','Izmjene obilježja tla ili, općenitije govoreći, njegove kemijske i biološke ravnoteže, zbog ispuštanja onečišćujućih tvari.', 0);
INSERT INTO property VALUES (1,7881, 'hr', 'definition','Glavni procesi u tlu su prirasti, gubici, prijenosi i preobrazbe organskih tvari, topljivih soli, karbonata, silikatnih glinenih minerala, seskvioksida (oksida s tri atoma kisika i dva atoma - ili radikala - neke druge tvari) i silicija. Prirasti se obično sastoje od dodataka organske tvari, te od kisika i vode kroz oksidaciju i hidrataciju, ali na nekim mjestima se sporo kontinuirano dodaju novi mineralni materijali na površini ili se iz podzemnih voda natalože topljivi materijali. Gubici su prisutni uglavnom kod materijala otopljenih ili suspendiranih u vodi koji se procjeđuju kroz profil ili otječu s površine.', 0);
INSERT INTO property VALUES (1,7882, 'hr', 'definition','Okomiti presjek tla, koji prikazuje horizonte (slojeve) i matičnu stijenu.', 0);
INSERT INTO property VALUES (1,7884, 'hr', 'definition','Sva trenutna pozitivna ili negativna svojstva s obzirom na  korištenje tla i funkcije tla.', 0);
INSERT INTO property VALUES (1,7889, 'hr', 'definition','Akumulacija topljivih mineralnih soli u blizini površine tla, obično prouzročeno kapilarnim protokom vode iz slane podzemne vode. Tamo gdje je količina površinskog isparavanja visoka, navodnjavanje može pogoršati problem vlaženjem tla, zbog čega se voda izvlači iz dubljih razina dok voda isparava s površine. Isparavanjem čiste vode ostaju soli koje se mogu nagomilavati, i mogu doseći koncentracije koje su otrovne za biljke, te tako steriliziraju zemlju.', 0);
INSERT INTO property VALUES (1,7890, 'hr', 'definition','Proučavanje svojstava, pojava i upravljanje tlom kao prirodnim resursom. Oono općenito uključuje kemiju, mikrobiologiju, fiziku, morfologiju i mineralogiju tla, kao i njihovu genezu i razvrstavanje.', 0);
INSERT INTO property VALUES (1,7892, 'hr', 'definition','Sjedanje tla uključuje zbijanje pojedinih zrna uglavnom sabijanjem prostora u porama i istiskivanjem zarobljene vode, a to obično izaziva težina gornjih sedimenata.', 0);
INSERT INTO property VALUES (1,7894, 'hr', 'definition','Kemijska ili mehanička obrada namijenjena povećanju ili održavanju stabilnosti mase tla ili drugačijeg poboljšanja njegovih tehničkih svojstava, na primjer povećanjem čvrstoće na smicanje, smanjenjem njegove tlačne čvrstoće, odnosno smanjenjem njegove sklonosti apsorbiranja vode. Metode stabilizacije uključuju fizičko zbijanje i obradu cementom, vapnom i bitumenom.', 0);
INSERT INTO property VALUES (1,7895, 'hr', 'definition','Spajanje ili agregiranje primarnih čestica tla u agregate ili skupine, koje su odvojene od susjednih agregata rahlim površinama. Struktura tla se razvrstava na temelju veličine, oblika i razlikovnih obilježja u razrede, vrste i stupnjeve.', 0);
INSERT INTO property VALUES (1,7896, 'hr', 'definition','Propadanje dijela Zemljine kore, općenito zbog podzemnih iskapanja.', 0);
INSERT INTO property VALUES (1,7897, 'hr', 'definition','Svaka aktivnost ili proces u kojem se površinska područja zemlje stisnu ili zatvore kako bi se spriječilo cijeđenje ili prolaz tekućine.', 0);
INSERT INTO property VALUES (1,7898, 'hr', 'definition','1) Odnosi se na relativne omjere skupina raznih veličina (pijesak, prah i glina) pojedinih čestica tla u masi tla. 
INSERT INTO property VALUES (1,7899, 'hr', 'definition','Faza ili podjela vrsta tla temeljena ponajprije na teksturi površinskog tla na dubini najmanje jednakoj dubini oranja  (oko 15 cm).', 0);
INSERT INTO property VALUES (1,7900, 'hr', 'definition','Funkcionalno korištenje tla za poljoprivredu, industriju ili stambene zgrade.', 0);
INSERT INTO property VALUES (1,7902, 'hr', 'definition','Voda pohranjena u tlima.', 0);
INSERT INTO property VALUES (1,7904, 'hr', 'definition','Uređaj za pretvaranje Sunčeve svjetlosti u električnu energiju pomoću poluvodiča osjetljivog na fotonaponski efekt. Solarne ćelije se upotrebljavaju na svemirskim satelitima za napajanje elektroničke opreme, i s opadanjem njihove cijene oni će se moći upotrebljavati za davanje energije na Zemlji.', 0);
INSERT INTO property VALUES (1,7905, 'hr', 'definition','Uređaj koji pretvara energiju iz svjetlosti u električnu energiju. Kolektorski sustav sadrži koncentrator i prijemnik. Koncentrator preusmjerava i fokusira Sunčevu svjetlost na prijemniku s pomoću zrcala ili leća, a prijemnik apsorbira Sunčevo zračenje i pretvara ga u toplinu.', 0);
INSERT INTO property VALUES (1,7906, 'hr', 'definition','Energija koja se prenosi od Sunca u obliku elektromagnetskog zračenja. Do sada su najuspješniji primjeri iskorištavanja energije Sunca solarne ćelije koje se upotrebljavaju u satelitima i solarnim kolektorima za zagrijavanje vode.', 0);
INSERT INTO property VALUES (1,7907, 'hr', 'definition','Solarna energija može se pretvoriti u koristan rad ili toplinu s pomoću kolektora za apsorbiranje Sunčevog zračenja, čime se omogućuje da se mnogo energije zračenja Sunca pretvori u toplinu. Ova toplina se može upotrebljavati izravno u kućanstvima te za industrijske svrhe i u poljoprivredi; može se pretvarati u mehaničku ili električnu energiju, odnosno primijeniti u kemijskim reakcijama za proizvodnju goriva i kemikalija.', 0);
INSERT INTO property VALUES (1,7908, 'hr', 'definition','Sustav grijanja kućanstava ili industrije koji izravno iskorištava solarnu energiju. Najjednostavniji oblik sastoji se od kolektora kroz koji se crpi tekućina. Sklop također sadrži neki oblik spremnika za toplinu i alternativni izvor energije koji osigurava energiju kada sunce ne sja. Kolektor se obično sastoji od crne površine kroz koju voda prolazi cijevima, a crna površina je zatvorena iza staklenih ploča kako bi se iskoristio efekt staklenika.', 0);
INSERT INTO property VALUES (1,7910, 'hr', 'definition','Pogon za proizvodnju energije pomoću Sunčevog zračenja.', 0);
INSERT INTO property VALUES (1,7912, 'hr', 'definition','Elektromagnetsko zračenje i čestice koje emitira Sunce.', 0);
INSERT INTO property VALUES (1,7918, 'hr', 'definition','Kristalni materijal, to jest, materijal u kojem su sastavni atomi raspoređeni u trodimenzionalnoj mreži, periodično u tri neovisna smjera.', 0);
INSERT INTO property VALUES (1,7919, 'hr', 'definition','Fizičko stanje materije u kojoj sastavne molekule, atomi ili ioni nemaju prijenosni pokret iako vibriraju oko fiksnih položaja koje zauzimaju u kristalnoj rešetki.', 0);
INSERT INTO property VALUES (1,7920, 'hr', 'definition','Odbačeni kruti materijali. Uključuje poljoprivredni otpad, rudarski otpad, industrijski otpad i komunalni otpad.', 0);
INSERT INTO property VALUES (1,7921, 'hr', 'definition','Uredno odbacivanje, ispuštanje, skupljanje, obrada ili oporaba neželjenih ili beskorisnih ne-tekućih, ne-topljivih otpadaka.', 0);
INSERT INTO property VALUES (1,7923, 'hr', 'definition','Sposobnost tvari da stvori otopinu s drugom tvari.', 0);
INSERT INTO property VALUES (1,7925, 'hr', 'definition','Tvar, uglavnom tekućina, koja može rastopiti drugu tvar.', 0);
INSERT INTO property VALUES (1,7926, 'hr', 'definition','Oporaba otapala je široko upotrebljavan oblik recikliranja, pri čemu se upotrijebljena otapala destiliraju i ponovno upotrebljavaju. Međutim, jeftinija otapala često se spaljuju ili odlažu na odlagalištima otpada za opasni otpad.', 0);
INSERT INTO property VALUES (1,7927, 'hr', 'definition','Sve ptice pjevačice iz podreda Oscines, s vrlo razvijenim  vokalnim organima i koje većinom pjevaju.', 0);
INSERT INTO property VALUES (1,7928, 'hr', 'definition','Buka koju izaziva udarni val koji izlazi iz zrakoplova ili nekog drugog objekta koji se giba brzinom zvuka ili brže od nje.', 0);
INSERT INTO property VALUES (1,7929, 'hr', 'definition','Nečisti crni ugljik s masnim spojevima koji nastaje nepotpunim izgaranjem smolastih materijala, ulja, drva ili ugljena.', 0);
INSERT INTO property VALUES (1,7930, 'hr', 'definition','Apsorpcija, obično tekućine ili plina, u tijelo drugog materijala (apsorbenta). Tako se, na primjer, onečišćujuća tvar u zraku može ukloniti apsorpcijom u prikladnom otapalu.', 0);
INSERT INTO property VALUES (1,7933, 'hr', 'definition','Slušni osjećaj što ga proizvode oscilacije, naprezanje, tlak, pomicanje čestica i brzina čestica u mediju s unutarnjim silama; varijacija tlaka koje može raspoznati ljudsko uho.', 0);
INSERT INTO property VALUES (1,7936, 'hr', 'definition','Širenje zvuka koji se emitira iz određenog izvora u okoliš.', 0);
INSERT INTO property VALUES (1,7937, 'hr', 'definition','Uvođenje u okoliš buke koja nastaje u različitim izvorima koji se mogu grupirati u aktivnosti prijevoza, industrijske djelatnosti i uobičajene dnevne aktivnosti.', 0);
INSERT INTO property VALUES (1,7938, 'hr', 'definition','Razina zvučnog tlaka (u decibelima) u točki zvučnog polja, uprosječena preko raspona zvučne frekvencije, tijekom vremenskog intervala.', 0);
INSERT INTO property VALUES (1,7939, 'hr', 'definition','Zbog velikih varijacija u veličinama zvuka, i budući da se čini da osjet ljudskog sluha varira na logaritamski način, logaritmi se upotrebljavaju za mjerenje zvuka. Razina zvučnog tlaka izražava se u decibelima (dB).', 0);
INSERT INTO property VALUES (1,7941, 'hr', 'definition','Smanjenje ili uklanjanje odjeka u prostoriji postavljanjem materijala koji upijaju zvuk na zidove i strop.', 0);
INSERT INTO property VALUES (1,7942, 'hr', 'definition','Putovanje zvučnih valova u atmosferi brzinom neovisnom o njihovoj amplitudi. Brzina ovisi samo o akustičkom mediju i proporcionalna je kvadratu apsolutne temperature za bilo koji dani medij.', 0);
INSERT INTO property VALUES (1,7945, 'hr', 'definition','Prolazak zvučnih valova po mediju ili nizu medija.', 0);
INSERT INTO property VALUES (1,7947, 'hr', 'definition','Kontinent na južnom dijelu zapadne hemisfere, koji opkoračuje ekvator i južnu obratnicu, omeđen Karipskim morem na sjeveru i između Atlantskog i Tihog oceana, povezan sa Sjevernom Amerikom  Panamskom prevlakom, i podijeljen na dvanaest zemalja: Argentinu, Boliviju, Brazil, Čile, Kolumbiju, Ekvador, Gvajanu, Paragvaj, Peru, Surinam, Urugvaj i Venezuelu.', 0);
INSERT INTO property VALUES (1,7948, 'hr', 'definition','Ocean južno od ekvatora između istočne obale Južne Amerike i zapadne obale Afrike koji se proteže prema jugu do antarktičkog kontinenta, uključujući Drakeov prolaz, otočje Južni Sandwich i Falkand.', 0);
INSERT INTO property VALUES (1,7949, 'hr', 'definition','Geografsko područje kontinentalne Azije, juga Kine, zapadno od južnog Tihog oceana, sjeverno od Indijskog oceans, te istočno od Bengalskog zaljeva i Indijskog potkontinenta, uključujući i Indokineski poluotok, Malajski poluotok i indonezijski i filipinski arhipelag, i zemlje kao što su Bruneji, Kambodža, Indonezija, Laos, Malezija, Mianmar, Filipini, Singapur, Tajland i Vijetnam.', 0);
INSERT INTO property VALUES (1,7950, 'hr', 'definition','Geografska regija afričkog kontinenta koji opkoračuje južnu obratnicu, uključujući Angolu, Bocvanu, Lesoto, Malavi, Mozambik, Namibiju, Južnu Afrika, Svaziland, Zambiju i Zimbabve, te pustinje Kalahari, rijeku Zambezi i rijeku Orange.', 0);
INSERT INTO property VALUES (1,7951, 'hr', 'definition','Geografska regija azijskog kontinenta koja je na sjeveru omeđena zemljama Srednje Azije, a na jugu Arapskim morem i Bengalskim zaljevom, koja se širi prema zapadu u Iran i na istok u Kinu, uključujući Afganistan, Pakistan, Indija, Nepal, Bangladeš , Burmu, Butan i Šri Lanku.', 0);
INSERT INTO property VALUES (1,7952, 'hr', 'definition','Ocean južno od ekvatora između jugoistočne Azije i Australije na istočnoj polutki i Južnoj Americi na zapadnoj polutki, koji se prostire prema jugu do Antarktičke regije, uključujući i Tasmanijsko i Koraljno more i brojne otoke, kao što su Galapagos, Solomon, Uskrsni otoci, Samoa, Fidži i Tonga, a također i do Novog Zelanda i otoka.', 0);
INSERT INTO property VALUES (1,7962, 'hr', 'definition','Prostor koji se proteže između Sunca i planeta u Sunčevom sustavu. Međuplanetarni prostor nije prazan, nego sadrži prašinu, čestice s električnim nabojem, te magnetsko polje Sunca (koji se naziva međuplanetarnim magnetskim poljem).', 0);
INSERT INTO property VALUES (1,7969, 'hr', 'definition','Prijevoz vozilima prilagođenima radu u slobodnom prostoru izvan Zemljine atmosfere.', 0);
INSERT INTO property VALUES (1,7970, 'hr', 'definition','Putovanje u prostor izvan Zemljine atmosfere u svrhu znanstvenog istraživanja.', 0);
INSERT INTO property VALUES (1,7972, 'hr', 'definition','Nefunkcionalni ostaci ljudskog podrijetla ostavljeni u mnoštvu orbita oko Zemlje kao rezultat istraživanja i korištenja okoliša izvan Zemljine atmosfere.', 0);
INSERT INTO property VALUES (1,7974, 'hr', 'definition','Stopa kretanja ili migracija određenog stanovništva u određenom vremenskom okviru.', 0);
INSERT INTO property VALUES (1,7975, 'hr', 'definition','Iznimno davanje ovlasti ili dopuštenja ili zakonski akt kojim se dopušta trošenje novca na vladine programe.', 0);
INSERT INTO property VALUES (1,7976, 'hr', 'definition','Evolucijska prilagodba posebnom načinu života ili staništu.', 0);
INSERT INTO property VALUES (1,7978, 'hr', 'definition','Zakon koji se odnosi na određene osobe ili stvari; zakon za pojedine slučajeve ili za određene mjesta ili područja; zakon koji se odnosi na odabranu klasu, a ne na javnost općenito. Zakon je poseban kad je drugačiji od drugih iste opće vrste općih ili oblikovan za određenu svrhu, ili je ograničenog raspona ili ograničen na propisano područje djelovanja ili rada.', 0);
INSERT INTO property VALUES (1,7981, 'hr', 'definition','Otpad kojim se mora rukovati na poseban način i za koji se primjenjuju posebna pravila.', 0);
INSERT INTO property VALUES (1,7982, 'hr', 'definition','Taksonomska kategorija koja se nalazi odmah ispod roda i koja uključuje usko povezane, morfološki slične jedinke koje se stvarno ili potencijalno razmnožavaju među sobom.', 0);
INSERT INTO property VALUES (1,7983, 'hr', 'definition','Kontrolirano korištenje, zaštita ili razvoj odabranih razreda biljaka ili životinja zbog njihova bogatstva, biološke raznolikosti i koristi za čovječanstvo.', 0);
INSERT INTO property VALUES (1,7984, 'hr', 'definition','Organizirane skupine aktivnosti i postupaka, koje često vodi vladina agencija ili neprofitna organizacija, za očuvanje i zaštitu živih organizama za koje se smatra da su rizične.', 0);
INSERT INTO property VALUES (1,7985, 'hr', 'definition','Gubitak vrsta zbog čimbenika kao što su klimatske promjene ili slučajni događaji, kao što su dugotrajne suše, prirodna katastrofa, pojava novog grabežljivca ili genetske mutacije.', 0);
INSERT INTO property VALUES (1,7986, 'hr', 'definition','Ponovno uvođenje divljih životinjskih i biljnih vrsta u njihovo prirodno stanište. Za ponovno uvođenja vrsta u nekoj regiji potrebne su preliminarne studije kojima se utvrđuju razlozi njihova nestanka te izmjene koje su se mogle dogodili u biotopima.', 0);
INSERT INTO property VALUES (1,7992, 'hr', 'definition','Grana fizike koje se bavi proizvodnjom, mjerenjem i tumačenjem elektromagnetskih spektara koji su rezultat ili emisije ili apsorpcije zračenja energije različitih tvari.', 0);
INSERT INTO property VALUES (1,7994, 'hr', 'definition','Skalarno mjerilo brzine kretanja tijela izražena bilo kao udaljenost koju tijelo proputuje podijeljenu vremenom potrebnim za prolaženje te udaljenosti (prosječna brzina) ili brzinom promjene položaja u odnosu prema vremenu u određenoj točki (trenutna brzina). Mjeri se u metrima u sekundi, kilometrima na sat, itd.', 0);
INSERT INTO property VALUES (1,7996, 'hr', 'definition','Najveća dopuštena brzina kojom se vozilo smije kretati na određenim cestama.', 0);
INSERT INTO property VALUES (1,7999, 'hr', 'definition','Svi grabežljivi paučnjaci iz reda Araneae koji proizvode svilene niti, sa četiri para nogu i zaobljenim nesegmentiranim tijelom koje se sastoji od zatka i glavopršnjaka.', 0);
INSERT INTO property VALUES (1,8000, 'hr', 'definition','Nekontrolirano istjecanje, curenje, kapanje ili prelijevanje tekućina ili tekućih tvari.', 0);
INSERT INTO property VALUES (1,8004, 'hr', 'definition','Mjesto odlaganja smeća i otpadnih minerala iskopanih u rudniku.', 0);
INSERT INTO property VALUES (1,8006, 'hr', 'definition','Spužve, red životinjskog carstva koje odlikuje prisutnost kanalnih sustava i komora kroz koje se voda upija i ispušta; ne nemaju tkiva i organi.', 0);
INSERT INTO property VALUES (1,8008, 'hr', 'definition','Skup individualnih ili skupnih aktivnosti koje se obavljaju radi vježbe ili iz zadovoljstva, često u obliku natjecanja.', 0);
INSERT INTO property VALUES (1,8014, 'hr', 'definition','Zgrade, građevine, objekti, organizirana područja i oprema za sportske aktivnosti u zatvorenom i na otvorenom prostoru.', 0);
INSERT INTO property VALUES (1,8019, 'hr', 'definition','Limenka aerosola za nanošenje boje, dezodoransa itd., u obliku sitno raspršenog spreja.', 0);
INSERT INTO property VALUES (1,8020, 'hr', 'definition','Azbest koji se ispušta u atmosferu prilikom raspršivanja.', 0);
INSERT INTO property VALUES (1,8025, 'hr', 'definition','Mjesto na kojem podzemna voda prirodno teče od stijene ili tla na površinu zemlje ili u površinsko vodno tijelo.', 0);
INSERT INTO property VALUES (1,8028, 'hr', 'definition','Voda dobivena iz podzemne formacije iz koje voda prirodno teče na površinu, ili bi prirodno tekla na površinu da se na nakuplja u podzemlju.', 0);
INSERT INTO property VALUES (1,8029, 'hr', 'definition','Nanošenje vode ili pesticida na usjeve raspršivanjem.', 0);
INSERT INTO property VALUES (1,8031, 'hr', 'definition','Otvoreni prostor u gradu, koji ponekad uključuje okolne zgrade.', 0);
INSERT INTO property VALUES (1,8032, 'hr', 'definition','Naseljavanje na zemljište ili posjed nad kojim osobe nemaju zakonsko vlasništvo.', 0);
INSERT INTO property VALUES (1,8036, 'hr', 'definition','Lagune u kojima se otpad ostavlja da se razgradi tijekom dugoga vremenskog razdoblja, a aeracija se obavlja samo djelovanjem vjetra. Dopušta se da Sunčeva svjetlost pada na  kanalizaciju da ga očisti.', 0);
INSERT INTO property VALUES (1,8037, 'hr', 'definition','Zgrada ili konstrukcija obično s boksovima za boravak i hranjenje konja, stoke ili drugih životinja.', 0);
INSERT INTO property VALUES (1,8039, 'hr', 'definition','Dio dimnjaka koji se diže iznad krova.', 0);
INSERT INTO property VALUES (1,8047, 'hr', 'definition','1) Nešto što ili neko tijelo smatra osnovom za usporedbu, ili sveopćim prihvaćanjem.
INSERT INTO property VALUES (1,8048, 'hr', 'definition','Usklađivanje s pravilom.', 0);
INSERT INTO property VALUES (1,8051, 'hr', 'definition','Norma ili mjerilo primjenjivo u pravnim slučajevima za bilo koje poduzeće uključeno u  gradnju, preuređenje ili završno uređenje zatvorenih građevina za boravak.', 0);
INSERT INTO property VALUES (1,8056, 'hr', 'definition','Hrana u nekoj zemlji ili zajednici koja se najčešće ili redovito jede, a koja čini osnovu ukupno potrebnih kalorija, posebno kod siromašnijih populacija i u vrijeme nestašice hrane.', 0);
INSERT INTO property VALUES (1,8057, 'hr', 'definition','Polisaharid koji je kombinacija više monosaharidnih molekula, nastalih fotosintezom i pohranjenih u obliku zrna škroba u mnogim biljkama.', 0);
INSERT INTO property VALUES (1,8062, 'hr', 'definition','Ljudi koji trajno nastavaju fiksno područje povezano običajnim pravom, navikama i običajima u jedno političko tijelo koje provodi, posredstvom organizirane vlade, neovisni suverenitet i kontrolu nad svim osobama i stvarima unutar svojih granica, ako se vlast ne preda odnosno sve dok se ne preda federaciji ili savezu drugih država.', 0);
INSERT INTO property VALUES (1,8067, 'hr', 'definition','Sve što je dostupno javnosti putem pisanog ili usmenog opisa, uporabom ili na bilo koji drugi način prije datuma prijave patenta, ili prijave podnesene u inozemstvu čiji prioritet je pravovaljano zatražen.', 0);
INSERT INTO property VALUES (1,8069, 'hr', 'definition','Pisani prikaz o razini cjelovitosti i uvjetima ekosustava i prirodnih resursa u određenom području, koji obično predstavlja službena osoba ili tijelo kojem su dane ovlasti za zaštitu ljudskog zdravlja i okoliša u toj regiji.', 0);
INSERT INTO property VALUES (1,8171, 'hr', 'definition','Sposobnost tvari da je zamijeni druga tvar, na primjer uporaba zaslađivača umjesto šećera.', 0);
INSERT INTO property VALUES (1,8173, 'hr', 'definition','Halogenirane spojeve, zbog njihove toksičnosti i postojanosti, treba zamijeniti ekološki prihvatljivim spojevima, kao što su otapala masti na bazi vode u industriji obrade metala ili sredstva za premazivanje na bazi vode.', 0);
INSERT INTO property VALUES (1,8174, 'hr', 'definition','Zamjena fosfata u deterdžentima ekološki sigurnijim tvarima, kao što je zeolit​​. Zamjena neće djelovati kao hranivo, i tako neće prouzročiti eutrofikaciju kao posljedica ubrzanog rasta biljaka i mikroorganizama ako se ispušta u vodotoke.', 0);
INSERT INTO property VALUES (1,8179, 'hr', 'definition','Sustav u kojem je prisutna interakcija biološke zajednice i njezinoga neživog okoliša u regijama koje graniče s tropima ili regijama između tropskih i umjerenih područja.', 0);
INSERT INTO property VALUES (1,8182, 'hr', 'definition','Područje za stanovanje smješteno na rubnim dijelovima grada ili mjesta.', 0);
INSERT INTO property VALUES (1,8185, 'hr', 'definition','Slatka tvar u obliku kristala ili praha, bijela u čistom obliku, koja se sastoji od saharoze dobivene uglavnom od šećerne trske i šećerne repe i koja se upotrebljava za poboljšanje okusa u mnogim vrstama hrane, pića i lijekova.', 0);
INSERT INTO property VALUES (1,8188, 'hr', 'definition','Poslovni subjekti koji se ponajprije bave preradom sirovog šećera od šećerne trske, šećerne repe ili škroba u gotovu saharozu, glukozu ili fruktozu. Nusproizvodi ove industrije su repina pulpa i nejestive melasa.', 0);
INSERT INTO property VALUES (1,8190, 'hr', 'definition','Sol ili ester sumporne kiseline, koji su široko rasprostranjeni u prirodi i često se nalaze u atmosferi.', 0);
INSERT INTO property VALUES (1,8192, 'hr', 'definition','Svaki spoj koji uključuje jedan ili više atoma sumpora s više elektropozitivnim elementom, bilo ugljik, metalni ili neki drugi atom koji nije kisikov.', 0);
INSERT INTO property VALUES (1,8198, 'hr', 'definition','Nemetalni element koji postoji u kristalnom ili amorfnom obliku i u četiri stabilna izotopa; upotrebljava se kao kemijski poluproizvod i fungicid, te u vulkanizaciji gume. Njegove naslage potječu od vulkanskih otvora i fumarola, a nalazi se i u sedimentnim stijenama, osobito s gipsom i vapnencem, te je povezan sa slanim kupolama. Sumpor u prirodnom obliku glavni je izvor sumpora za proizvodnju sumporne kiseline, a slijede ga kiseli plin (prirodni plin koji sadrži sumporovodik) i pirit. Sumpor je bitan makronutrijenat biljaka.', 0);
INSERT INTO property VALUES (1,8200, 'hr', 'definition','Sadržaj sumpora u otopini.', 0);
INSERT INTO property VALUES (1,8202, 'hr', 'definition','Emisije plina koje nastaju gorenjem fosilnih goriva u elektranama i drugim kotlovima. Nastaje sumporni dioksid jer je sumpor nečistoća u većini ugljena i ulja. Pri gorenju goriva vrući sumpor reagira s kisikom u atmosferi te stvara sumporni dioksid.', 0);
INSERT INTO property VALUES (1,8203, 'hr', 'definition','Otrovna, korozivna, jaka kiselina, bezbojna tekućina koja se može miješato s vodom i otapa većinu metala, a topi se pri 10C; upotrebljava se u industriji u proizvodnji kemikalija, umjetnih gnojiva i eksploziva, kao i u preradi nafte.', 0);
INSERT INTO property VALUES (1,8204, 'hr', 'definition','Oksidi sumpora, kao što su sumporni dioksid i sumporni trioksid; nastaju ponajprije izgaranjem fosilnih goriva; veliki su onečišćivači zraka i prouzročuju oštećenja dišnog sustava, kao i vegetacije.', 0);
INSERT INTO property VALUES (1,8212, 'hr', 'definition','Nadzor ili vodstvo u građenju i održavanju kuća, objekata, ureda i drugih građevina.', 0);
INSERT INTO property VALUES (1,8213, 'hr', 'definition','Nadzor ili vodstvo tijekom postupka postavljanja (montiranja) ili prilagodbe neke zgrade ili na mehaničkom ili električnom sustavu ili uređaju.', 0);
INSERT INTO property VALUES (1,8214, 'hr', 'definition','Imenovana ili službena skupina odgovorna za nadzor ili upravljanje redovnim radnim operacijama, posebnim projektima ili drugim funkcijama organizacije ili agencije.', 0);
INSERT INTO property VALUES (1,8219, 'hr', 'definition','Spremnost i sposobnost da se prodaju razne količine dobara po određenom rasponu cijena tijekom određenoga vremenskog razdoblja. Ponuda je jedna polovina procesa tržišne razmjene, a druga je potražnja.', 0);
INSERT INTO property VALUES (1,8222, 'hr', 'definition','Tvar koja, kad se upotrebljava u malim količinama, mijenja površinska svojstva tekućina ili krutina. Površinski aktivna tvar (surfaktant) smanjuje površinsku napetost u tekućini ili površinske napetosti između dvije tekućine koje se ne miješaju, kao što su ulje i voda. Surfaktanti su osobito korisni u postizanju vlaženja ili penetracije vodenih tekućina u krutine i služe kao deterdženti, emulzirajuća ili raspršujuća sredstva. Oni su u određenim situacijama učinkovitiji od sapuna, pa ih upotrebljavaju konzervatori u svrhe kao što su čišćenje, vlaženje i raspršivanje.', 0);
INSERT INTO property VALUES (1,8225, 'hr', 'definition','Voda koja putuje preko površine tla do najbližeg vodoroka (potoka) na površini; otjecanje iz sliva koji nije prošao ispod površine nakon što su oborine pale.', 0);
INSERT INTO property VALUES (1,8226, 'hr', 'definition','Sila koja djeluje na površini tekućine, koja obično smanjuje područje površine; kvantitativno, sila za koju se čini da djeluje preko linije jedinične duljine na površini. Također se zove međupovršinska sila, međupovršinska napetost ili intenzitet površine.', 0);
INSERT INTO property VALUES (1,8227, 'hr', 'definition','Svaka metoda obrade (tretiranja) materijala (metala, polimera ili drva) kako bi se promijenila površina, tako da može prihvaćati tinte, boje, lakove, ljepila i razne druge obrade, ili kako bi postala otporna na vremenske uvjete ili kemijski napad.', 0);
INSERT INTO property VALUES (1,8229, 'hr', 'definition','Sve vode na površini Zemlje koje nalazimo u potocima, rijekama, ribnjacima, jezerima, močvarama, močvarnom tlu, kao i u snijegu i ledu.', 0);
INSERT INTO property VALUES (1,8236, 'hr', 'definition','Bilo koje tkivo, krv ili sluz uklonjeni tijekom operacija ili obdukcija, zaprljani kirurški zavoji i gaze ili drugi materijali za koje su potrebni posebni postupci zbrinjavanja.', 0);
INSERT INTO property VALUES (1,8237, 'hr', 'definition','Razlika u iznosu potraživanja nad iznosom dugovanja, osobito dobit koja ostaje nakon oduzimanja troškova poslovanja, plaćanja poreza, kamata i troškova osiguranja.', 0);
INSERT INTO property VALUES (1,8239, 'hr', 'definition','Sustav koji omogućuje kontinuirano promatranje, mjerenje i vrednovanje napretka nekog procesa ili pojave u cilju poduzimanja korektivnih mjera.', 0);
INSERT INTO property VALUES (1,8240, 'hr', 'definition','Kritičko ispitivanje činjenica ili uvjeta kako bi se pružile informacije o nekoj situaciji. Obično se obavlja u obliku  intervjuiranja i/ili posjeta na terenu.', 0);
INSERT INTO property VALUES (1,8247, 'hr', 'definition','Razvoj koji na duži rok pruža gospodarske, socijalne i ekološke koristi s obzirom na potrebe života i budućih naraštaja. Definirala ga je Svjetska komisije za okoliš i razvoj 1987. god. kao razvoj koji zadovoljava potrebe sadašnjeg naraštaja bez ugrožavanja sposobnosti budućih naraštaja da zadovolje vlastite potrebe.', 0);
INSERT INTO property VALUES (1,8249, 'hr', 'definition','Statistički pokazatelji koji se upotrebljavaju za mjerenje održivog razvoja koji se mogu odabrati u širokom dijapazonu tema kao su, na primjer, kapacitet okoliša i kvaliteta života.', 0);
INSERT INTO property VALUES (1,8250, 'hr', 'definition','Korištenje okoliša i njegovih živih bogatstava po stopi koja ne prelazi kapacitet okoliša za obnovu kako bi se osigurala njihova dostupnost za buduće naraštaje.', 0);
INSERT INTO property VALUES (1,8252, 'hr', 'definition','Povremeno poplavljeno područje na nižem terenu, s grmolikom vegetacijom i drvećem, na kojem se stvara ili ne stvara treset.', 0);
INSERT INTO property VALUES (1,8255, 'hr', 'definition','Sredstvo za zaslađivanje, osobito takvo koje ne sadrži šećer.', 0);
INSERT INTO property VALUES (1,8259, 'hr', 'definition','Bliska i uzajamno korisna povezanost organizama različitih vrsta.', 0);
INSERT INTO property VALUES (1,8262, 'hr', 'definition','Proučavanje ekologije organizama, populacija, zajednica i sustava.', 0);
INSERT INTO property VALUES (1,8263, 'hr', 'definition','Ekološka povezanost u kojem su fiziološki procesi ponašanja pojedinca pojačani prisutnošću drugog organizma koji se nalazi u blizini.', 0);
INSERT INTO property VALUES (1,8265, 'hr', 'definition','1) Stanje u kojem je kombinirani učinak dviju ili više tvari veći od zbroja odvojenih učinaka.
INSERT INTO property VALUES (1,8268, 'hr', 'definition','Umjetno proizvedeno kruto ili tekuće sredstvo za čišćenje koja djeluje kao sapun, ali je jače, a sposobno je otapati masne materijale te ih raspršivati u vodi.', 0);
INSERT INTO property VALUES (1,8272, 'hr', 'definition','Materijal proizveden umjetno, kemijskom reakcijom.', 0);
INSERT INTO property VALUES (1,8275, 'hr', 'definition','Umjetno proizvedena nit ili nitasti suk (vlakno) kojim se koriste proizvođači za proizvodnju odjeće ili druge robe za koju je potrebno tkanje, pletenje ili filcanja, uključujući poliester, najlon, rajon i druge slične materijale.', 0);
INSERT INTO property VALUES (1,8278, 'hr', 'definition','Način organiziranja elemenata u integrirani analitički postupak i/ili postupak donošenja odluka za postizanje najboljih mogućih rezultata.', 0);
INSERT INTO property VALUES (1,8279, 'hr', 'definition','Analiza ili procjena u kojoj se ističu sličnosti i razlike u poslovanju poslovnih subjekata i organizacija.', 0);
INSERT INTO property VALUES (1,8280, 'hr', 'definition','Znanost koje se bavi općim proučavanjem struktura i ponašanja sustava koji se mogu primijeniti u različitim granama učenja.', 0);
INSERT INTO property VALUES (1,8283, 'hr', 'definition','U kaznenom pravu i deliktima, radnja zadržavanja dokaza, sa ili bez njihovog uklanjanja.', 0);
INSERT INTO property VALUES (1,8284, 'hr', 'definition','Kamion namijenjen prijevozu velikih količina tekućina ili plinova.', 0);
INSERT INTO property VALUES (1,8285, 'hr', 'definition','Brod namijenjen prijevozu velikih količina tekućina ili plinova.', 0);
INSERT INTO property VALUES (1,8286, 'hr', 'definition','Skladišni prostor za spremnike tekućina ili plinova.', 0);
INSERT INTO property VALUES (1,8289, 'hr', 'definition','Kemikalija iz skupine složenih organskih kemikalija koje se obično nalaze u lišću, nezrelom voću i kori drveća. Njihova funkcija je neizvjesna, iako neugodan okus može odbiti  životinje koje pasu. Neki tanini imaju komercijalnu uporabu, osobito u proizvodnji kože i tinte; upotrebljavaju se pri štavljenju kože, za nagrizanje u bojenju i u proizvodnji tinte.', 0);
INSERT INTO property VALUES (1,8296, 'hr', 'definition','Viskozni materijal koji se sastoji od kompleksnih spojeva  velike molekularne težine deriviranih destilacijom nafte ili destruktivnom destilacijom drva ili ugljena.', 0);
INSERT INTO property VALUES (1,8299, 'hr', 'definition','Skup odabranih ljudi i kojima neki subjekt prilazi s različitim ciljevima, uključujući i pomoć, zapošljavanje, širenje informacija, marketinga i istraživanja.', 0);
INSERT INTO property VALUES (1,8301, 'hr', 'definition','Razvrstani popis ili ljestvica naknada u bilo kojem privatnom ili javnom poslovanju.', 0);
INSERT INTO property VALUES (1,8303, 'hr', 'definition','Izrada tamne, teške, viskozne tvari ili taloga koji se dobiva destilacijom organskih materijala kao što su ugljen, drvo i nafta.', 0);
INSERT INTO property VALUES (1,8304, 'hr', 'definition','Pješčenjak koji sadrži ugljikovodike; lakši spojevi ispare, ostavljajući ostatak asfalta u porama stijena.', 0);
INSERT INTO property VALUES (1,8305, 'hr', 'definition','Svaka upotreba ili korištenje tamnih, teških, viskoznih tvari ili ostataka nastalih destilacijom pojedinih organskih materijala, često za proizvodnju benzena, sapuna, boja, kozmetike i drugih proizvoda.', 0);
INSERT INTO property VALUES (1,8307, 'hr', 'definition','Iznos novca koji se mora platiti državi za pomoć ili za korištenje određenih objekata ili usluga, a najčešće se određuje na dohodak, imovinu ili kupoprodaju.', 0);
INSERT INTO property VALUES (1,8308, 'hr', 'definition','Čin ili posljedica zahtijevanja države da se plati njezina pomoć ili za određene objekte ili usluge.', 0);
INSERT INTO property VALUES (1,8312, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propisuje država, a kojima se nalaže novčano plaćanje i njegov iznos te način prikupljanja tog novca koji država zahtijeva za za državnu pomoć, objekte i usluge, i obično se određuje na dohodak, imovinu, kupoprodaju ili druga financijska sredstva.', 0);
INSERT INTO property VALUES (1,8313, 'hr', 'definition','Grana biologije koja se bavi razvrstavanjem organizama u skupine na temelju sličnosti njihove građe, porijekla itd.', 0);
INSERT INTO property VALUES (1,8319, 'hr', 'definition','Prenošenje znanja ili vještina.', 0);
INSERT INTO property VALUES (1,8321, 'hr', 'definition','Postupak, tehnika ili sustav s detaljnim planovima za poučavanje ili prenošenje znanja.', 0);
INSERT INTO property VALUES (1,8326, 'hr', 'definition','Tehnička smjernica za opasne tvari: tehnička pravila za rukovanje opasnim materijalima.', 0);
INSERT INTO property VALUES (1,8330, 'hr', 'definition','Promjena industrijskih metoda uvođenjem nove tehnologije.', 0);
INSERT INTO property VALUES (1,8334, 'hr', 'definition','Svaka primjena praktičnih ili mehaničkih znanosti na industriju ili trgovinu koja može nanijeti štetu osobama, imovini ili okolišu.', 0);
INSERT INTO property VALUES (1,8339, 'hr', 'definition','Sustavno znanje o industrijskim procesima i njegova primjena; usko povezano s tehnikom i znanosti.', 0);
INSERT INTO property VALUES (1,8340, 'hr', 'definition','Odobrenje, povoljno prihvaćanje i postojeća uporaba novouvedenih uređaja i sustava, obično razvijenih iz nedavnih napredaka u tehničkim znanostima ili industrijskim vještinama.', 0);
INSERT INTO property VALUES (1,8341, 'hr', 'definition','Sustavna analiza predviđenog učinka pojedine tehnologije u odnosu prema njezinoj sigurnosti i učinkovitosti, kao i njezinih društvenih, političkih, ekonomskih i etičkih posljedica.', 0);
INSERT INTO property VALUES (1,8342, 'hr', 'definition','Prijenos razvoja i rada na projektiranju:) od matične tvrtke na podružnicu, možda u nekoj drugoj državi, gdje će biti plaćeni u obliku povrata dobiti ili naknadama od autorskih prava, b) iz jedne zemlje u drugu, kao oblik pomoći u svrhu promicanja razvoja i održivog rasta. Mnoge zemlje su postigle velik napredak u smislu snažnog transfera tehnologije.', 0);
INSERT INTO property VALUES (1,8344, 'hr', 'definition','Grana geologije koja se bavi širokom arhitekturom vanjskog dijela Zemlje, to jest, grupiranjem njihovih strukturnih ili deformacijskih značajki po regijama, proučavanjem njihovih međusobnih odnosa, podrijetla i povijesnog razvoja.', 0);
INSERT INTO property VALUES (1,8347, 'hr', 'definition','Prijenos slike, govora i drugih zvukova, obično na velikim udaljenostima, tehnološkim sredstvima, a osobito televizijom, telegrafom, telefonom ili radiom.', 0);
INSERT INTO property VALUES (1,8350, 'hr', 'definition','Povezivanje računalnih i komunikacijskih tehnologija, pa time i korištenje telefona ili radia za povezivanje računala i korištenje računala za slanje poruka putem telefona ili radijskih veza.', 0);
INSERT INTO property VALUES (1,8351, 'hr', 'definition','Korištenje radio valova, telefonskih linija itd., za prijenos očitanja mjernih instrumenata na uređaj na kojem se očitanja mogu prikazati ili zabilježiti.', 0);
INSERT INTO property VALUES (1,8353, 'hr', 'definition','Proces, oprema ili programiranje uključeni u pretvaranje slijeda audiovizualnih slika u odgovarajuće električne signale koji se prenose elektromagnetskim valovima u daleke prijemnike ili na ekrane, na kojima se signali mogu upotrijebiti za reprodukciju izvorne slike.', 0);
INSERT INTO property VALUES (1,8360, 'hr', 'definition','Sustav međusobnog djelovanja biološke zajednice i njezinog neživog okoliša u regijama s umjerenom klimom ili koje su s njima povezane, posrednik između tropske i polarne zone i koji ima različita topla do vruća ljeta i hladna zimska razdoblja.', 0);
INSERT INTO property VALUES (1,8361, 'hr', 'definition','Mješovite šume crnogorice i širokolisne bjelogorice, ili mješovita šuma crnogorice i širokolisnog zimzelenog drveća, ili samo širokolisnog bjelogoričnog drveća, ili samo širokolisnog zimzelenog drveća, koje se nalaze u umjerenim regijama širom svijeta; odlikuju se visokom razinom padalina, toplim ljetima, hladnim zimama povremeno ispod nule, izmjenama godišnjih doba; obično s gustim krošnjama, mladicama sa šikarom i visokim grmljem, velikim životinjama među kojima prevladavaju zvijeri odnosno mesožderi, vrlo bogate vrstama ptica.', 0);
INSERT INTO property VALUES (1,8362, 'hr', 'definition','Šume u kojima prevladavaju tvrde listače, koje su rasprostranjene na velikim površinama u srednjim geografskim širinama u Europi, Sjevernoj Americi i istočnoj Aziji, ali koja je na južnoj polutci ograničena na čileansku Patagoniju.', 0);
INSERT INTO property VALUES (1,8366, 'hr', 'definition','Svojstvo koje određuje smjer protoka topline kada se predmet dovede u toplinski dodir s drugim predmetima: toplina teče iz područja viših temperatura u područja nižih temperatura.', 0);
INSERT INTO property VALUES (1,8369, 'hr', 'definition','Jednostavni objekti za azil ili privremene nastambe za pojedince ili skupine za slučaj izvanrednih situacija.', 0);
INSERT INTO property VALUES (1,8371, 'hr', 'definition','Svaka pohrana ili zalihe robe, materijala ili otpada u objektu ili pogonu, kontejneru, spremniku ili nekoj drugoj fizičkoj lokaciji na kratko vrijeme ili u kratkom vremenskom razdoblju.', 0);
INSERT INTO property VALUES (1,8376, 'hr', 'definition','Proces u kojem nastaju abnormalnosti potomaka, obično kao posljedica oštećenja strukture embrija tijekom prvog tromjesečja trudnoće, što dovodi do deformacije fetusa.', 0);
INSERT INTO property VALUES (1,8378, 'hr', 'definition','Sposobnost ili sklonost proizvodnje anomalija u formiranju.', 0);
INSERT INTO property VALUES (1,8379, 'hr', 'definition','Tvari koje uzrokuju urođenu anomaliju ili nepravilnost u embriju.', 0);
INSERT INTO property VALUES (1,8380, 'hr', 'definition','Skup specijaliziranih riječi povezanih s određenom temom.', 0);
INSERT INTO property VALUES (1,8381, 'hr', 'definition','Kukci mekih tijela, reda Isoptera; jedinke se hrane celulozom i žive u kolonijama s kastinskim sustavom koji se sastoji od tri vrste funkcionalnih jedinki: neplodnih radnika i vojnika, te rasplodnih jedinki - spolnih životinja. Također ih nazivamo bijelim mravima.', 0);
INSERT INTO property VALUES (1,8386, 'hr', 'definition','Podjele kontinentalne površine  koje se razlikuju jedna od druge na temelju oblika, hrapavosti i sastava površine zemljišta.', 0);
INSERT INTO property VALUES (1,8387, 'hr', 'definition','Svaki izvor opskrbe nastao od biljaka, životinja, te drugih divljih životinja i biljaka koje nastanjuju zemlju ili tlo, koje ljudi mogu upotrebljavati za hranu, odjeću i druge potrepštine.', 0);
INSERT INTO property VALUES (1,8388, 'hr', 'definition','Svaki zemaljski okoliš, od malih do velikih, u kojima biljke i životinje uzajamno djeluju na kemijska i fizička obilježja okoliša.', 0);
INSERT INTO property VALUES (1,8398, 'hr', 'definition','Djelovanje koje usvoji i provodi vlada, poslovna ili neka druga organizacija, koja određuje sadašnje i buduće korištenje svake parcele zemljišta u nekom području.', 0);
INSERT INTO property VALUES (1,8401, 'hr', 'definition','Područje koje brani neka životinja ili skupina životinja, uglavnom od članova iste vrste.', 0);
INSERT INTO property VALUES (1,8403, 'hr', 'definition','Područje koje brani neka životinja ili skupina životinja, uglavnom od članova iste vrste.', 0);
INSERT INTO property VALUES (1,8405, 'hr', 'definition','Provođenje ispitivanja (o nekoj tvari, materijalu ili sustavu) primjenom nekih kemijskih ili fizikalnih procedura osmišljenih kako bi uputili na prisutnost tvari ili posjedovanje imovine.', 0);
INSERT INTO property VALUES (1,8407, 'hr', 'definition','Životinje na kojima se provode eksperimenti kako bi se osigurali dokazi za neku znanstvenu hipotezu ili protiv nje, odnosno kako bi se dokazala učinkovitost lijekova ili reakcija na određene proizvode.', 0);
INSERT INTO property VALUES (1,8415, 'hr', 'definition','Svaki životinjski organizam upotrijebljen za znanstveno istraživanje.', 0);
INSERT INTO property VALUES (1,8421, 'hr', 'definition','Industrija proizvodnje tkanina.', 0);
INSERT INTO property VALUES (1,8422, 'hr', 'definition','Materijal izrađen od prirodnih ili umjetnih vlakana koji se upotrebljava za proizvodnju predmeta kao što su odjeća i prevlake za namještaj.', 0);
INSERT INTO property VALUES (1,8428, 'hr', 'definition','Plavkastobijeli metal kovak poput kositra, ali malo mekši; upotrebljava se u legurama.', 0);
INSERT INTO property VALUES (1,8433, 'hr', 'definition','Koherentna skupina općih tvrdnji o ponudi i potražnji novca, kamatnim stopama, utjecaju protoka novca na cjelokupno gospodarstvo ili politike koje bi trebale usvojiti institucije koje kontroliraju ponudu novca.', 0);
INSERT INTO property VALUES (1,8434, 'hr', 'definition','Političke koncepcije vlasti u kapitalističkoj ekonomiji u kojoj je država odgovorna za to da osigura da će svi članovi društva dostići minimalne životne standarde putem redistribucije sredstava, progresivnog oporezivanja i socijalnih programa za sve, uključujući zdravstvenu zaštitu i obrazovanje.', 0);
INSERT INTO property VALUES (1,8435, 'hr', 'definition','Liječenje fizičkih, psihičkih ili socijalnih poremećaja ili bolesti.', 0);
INSERT INTO property VALUES (1,8440, 'hr', 'definition','Proces sprječavanja prolaza topline prema ili od tijela koje ga okružuju pomoću neprovodljivog materijala.', 0);
INSERT INTO property VALUES (1,8442, 'hr', 'definition','Prekomjerno podizanje ili spuštanje temperature vode iznad ili ispod normalnih raspona u pojedinim godišnjim dobima u potocima, jezerima, ili estuarijima ili oceanima kao rezultat ispuštanja toplih ili hladnih otpadnih voda u takve vode.', 0);
INSERT INTO property VALUES (1,8443, 'hr', 'definition','Postrojenje za proizvodnju električne energije u kojoj se upotrebljava toplina za proizvodnju energije. Takva postrojenja mogu spaljivati fosilna goriva ili se koristiti nuklearnom energijom za proizvodnju potrebne toplinske energije.', 0);
INSERT INTO property VALUES (1,8446, 'hr', 'definition','Koncept iskorištavanja temperaturnih razlika od 20 ° C ili više koje se javljaju između površine oceana i njegovih dubina kako bi se postigla kontinuirana opskrba energijom; ta temperaturna razlika može se naći u tropskim regijama svijeta. Izgrađena su razna mala postojenja u svrhu prikazivanja ovog načela.', 0);
INSERT INTO property VALUES (1,8447, 'hr', 'definition','1) Grijanje i hlađenje metala ili legure kako bi se dobila željena svojstva ili uvjeti.
INSERT INTO property VALUES (1,8448, 'hr', 'definition','Voda, općenito u izvoru ili gejziru, čija je temperatura znatno iznad lokalne srednje godišnje temperature zraka.', 0);
INSERT INTO property VALUES (1,8450, 'hr', 'definition','Grana fizike koja iz nekoliko temeljnih postulata nastoji izvući odnose između svojstava tvari, posebno onih povezanih s temperaturom, i opis pretvorbe energije iz jednog oblika u drugi.', 0);
INSERT INTO property VALUES (1,8452, 'hr', 'definition','Zaštitni znak naziva za tehnologiju termičke obrade otpada. Uplinjavanje je u nastajanju kao alternativa izgaranju u obradi i oporabi energije iz krutoga komunalnog otpada. Nekoliko inovativnih procesa i demonstracijskih postrojenja pokušava postići višu električnu učinkovitost i smanjiti emisije koristeći se ovom tehnologijom.', 0);
INSERT INTO property VALUES (1,8454, 'hr', 'definition','Kompilacija izraza koja prikazuje sinonime, povezane izraze i druge odnose i ovisnosti, a često se upotrebljava u obliku knjige ili standardiziranog, kontroliranog rječnika za sustav pohrane i traženja informacija.', 0);
INSERT INTO property VALUES (1,8470, 'hr', 'definition','Najviša koncentracija određene tvari kojoj bi radnik trebao biti izložen u određenom vremenskom razdoblju.', 0);
INSERT INTO property VALUES (1,8475, 'hr', 'definition','Mehanička snaga, koja se može pretvoriti u električnu energiju, koju stvara oceanska plima i oseka. Mogućnosti korištenja snage plime i oseke proučavane su tijekom mnogih naraštaja, ali jedini izvedivi planovi osmišljeni do sada temelje se na uporabi jednog ili više područja plime i oseke, odvojenog od mora branama, kao i hidrauličnih turbina kroz koje voda prolazi na putu između područja plime i oseke i mora.', 0);
INSERT INTO property VALUES (1,8478, 'hr', 'definition','Svaka voda čija razina se povremeno mijenja pod utjecajem plime i oseke.', 0);
INSERT INTO property VALUES (1,8480, 'hr', 'definition','Periodično podizanje i opadanje razine vode proizlaze iz gravitacijske interakcije između Sunca, Mjeseca i Zemlje. U svakom lunarnom danu od 24 sata i 49 minuta postoje dvije plime i dvije oseke.', 0);
INSERT INTO property VALUES (1,8486, 'hr', 'definition','Industrija povezana s rušenjem drveća i obradom trupaca.', 0);
INSERT INTO property VALUES (1,8487, 'hr', 'definition','1) Dimenzija fizičkog svemira koji, na određenom mjestu, određuje redoslijed događaja.
INSERT INTO property VALUES (1,8490, 'hr', 'definition','Metalni element, koji se pojavljuje u kasiteritu, koji ima nekoliko alotropa; taj metal koji je u uobičajenom obliku kovak srebrnastobijeli metal pri temperaturi ispod 13,2° C polako se mijenja u sivi prah. Široko se upotrebljava u legurama, osobito u bronci i kositru, te kao nekorodirajući premaz za čelik.', 0);
INSERT INTO property VALUES (1,8494, 'hr', 'definition','Dio organizma koji se sastoji od velikog broja stanica slične strukture i funkcije.', 0);
INSERT INTO property VALUES (1,8495, 'hr', 'definition','Jak kovak bijeli metalni element, koji je vrlo otporan na koroziju i pojavljuje se u rutilu i ilmenitu. Upotrebljava se u proizvodnji jakih laganih legura, posebice zrakoplovnih dijelova.', 0);
INSERT INTO property VALUES (1,8496, 'hr', 'definition','Bijeli prah netopiv u vodi koji se topi na 1560° C, i koji se komercijalno proizvodi od minerala titanovog dioksida ilmenita i rutila, koji se upotrebljavaju u bojama i kozmetici.', 0);
INSERT INTO property VALUES (1,8499, 'hr', 'definition','Svi bezrepi vodozemci iz razreda Bufonidae, kao Bufo bufo (smeđa krastača) u Europi. One su slične žabama, ali se više zadržavaju na kopnu i imaju više suhu bradavičastu kožu.', 0);
INSERT INTO property VALUES (1,8503, 'hr', 'definition','Siva, smeđa ili crnkasta smjesa plinova i suspendiranih čestica ugljika koja nastaje izgaranjem duhana. Duhanski dim se udiše i širi toksine po cijelom tijelu i uzrokuje velik broj raznih bolesti među pušačima i među nepušačima izloženima duhanskom dimu.', 0);
INSERT INTO property VALUES (1,8516, 'hr', 'definition','Stup zraka koji brzo rotira, koji se razvija o se oko vrlo intenzivnog središta niskog tlaka. Povezan je s tamnim oblakom ljevkastog oblika i izuzetno snažnim vjetrom (> 300 km/h) koji puše u spirali u smjeru suprotnom od kazaljke na satu, ali je popraćen žestokim naletima zraka prema tlu. Precizni mehanizmi još se ne razumiju u potpunosti, ali čini se da su za razvoj tornada potrebni sljedeći atmosferski uvjeti: sloj toploga vlažnog zraka pri niskim nadmorskim visinama; sloj suhog zraka na višoj nadmorskoj visini s temperaturom inverzijom na oko 1.000 m; mehanizam pokretanja, obično u obliku aktivne, intenzivne hladne fronte ili solarnog grijanja tla koji će stvoriti vrtlog.', 0);
INSERT INTO property VALUES (1,8519, 'hr', 'definition','Svi biljožderski kopneni gmizavi gmazovi iz porodice Testudinidae, koji žive u najtoplijim područjima, s teškim oklopom u obliku školjke i udovima s pandžama.', 0);
INSERT INTO property VALUES (1,8521, 'hr', 'definition','Svi parametri koji se moraju uzeti u obzir prilikom ocjenjivanja kvalitete vode (organoleptički čimbenici, fizikalno-kemijski čimbenici, toksične tvari, mikrobiološki parametri).', 0);
INSERT INTO property VALUES (1,8522, 'hr', 'definition','Privremeno kretanje ljudi na odredišta izvan njihovih uobičajenih mjesta rada i boravka, aktivnosti koje obavljaju za vrijeme svog boravka na tim odredištima i objektima stvorenima za zadovoljavanje njihovih potreba.', 0);
INSERT INTO property VALUES (1,8525, 'hr', 'definition','Sve službe odnosno usluge povezane s turizmom, osobito kada se promatra kao djelatnost.', 0);
INSERT INTO property VALUES (1,8527, 'hr', 'definition','Svaki dio neke regije koji privlači putnike, često zbog svojih prirodnih ljepota, znamenitosti ili rekreacijskih aktivnosti.', 0);
INSERT INTO property VALUES (1,8537, 'hr', 'definition','Posljedica unošenja otrovnih materijala ili dodira s njima.', 0);
INSERT INTO property VALUES (1,8538, 'hr', 'definition','Stupanj opasnosti koju neka tvar predstavlja za životinjski ili biljni svijet.', 0);
INSERT INTO property VALUES (1,8540, 'hr', 'definition','Metali (obično teški metali) koji otežavaju disanje, metabolizam ili rast organizama.', 0);
INSERT INTO property VALUES (1,8541, 'hr', 'definition','Postupak opisivanja i ocjenjivanja prirodne toksičnosti neke kemijske tvari, otrova itd.', 0);
INSERT INTO property VALUES (1,8542, 'hr', 'definition','Pokus za utvrđivanje prirodne toksičnosti neke kemikalije.', 0);
INSERT INTO property VALUES (1,8543, 'hr', 'definition','Znanost koja se bavi otrovima, njihovim djelovanjem, njihovim otkrivanjem i djelovanjem uvjeta koje oni stvaraju.', 0);
INSERT INTO property VALUES (1,8545, 'hr', 'definition','Bilo koji proizvod koji može prouzročiti akutne ili kronične ozljede na ljudskom tijelu ili za koji se sumnja da može prouzročiti bolest ili ozljedu pod nekim uvjetima.', 0);
INSERT INTO property VALUES (1,8546, 'hr', 'definition','Kemikalija ili smjesa koja može predstavljati rizik ili naštetiti zdravlju ili okolišu.', 0);
INSERT INTO property VALUES (1,8547, 'hr', 'definition','Otpaci koji predstavljaju značajnu opasnost za okoliš ili ljudsko zdravlje pri nepropisnom rukovanju; obuhvaća  kancerogeni, mutageni, teratogeni ili fitotoksični otpad, odnosno otpad štetan za vodene vrste, ili otrovni otpad.', 0);
INSERT INTO property VALUES (1,8549, 'hr', 'definition','Otrovna tvar općenito biljnog ili životinjskog podrijetla.', 0);
INSERT INTO property VALUES (1,8554, 'hr', 'definition','Bilo koji od različitih kemijskih elemenata koji se pojavljuju u vrlo malim količinama u organizmima i nužni su za mnoge fiziološke i biokemijske procese.', 0);
INSERT INTO property VALUES (1,8556, 'hr', 'definition','1) I metali i nemetali, koji su nužni za metaboličke procese algi, beskralježnjaka i kralježnjaka. Organizmi koji crpe  gotovo svu svoju energiju putem fotosinteze osobito su ovisni o otopljenih tvari u tragovima.
INSERT INTO property VALUES (1,8559, 'hr', 'definition','Velika skupina biljaka koje se odlikuju prisutnošću specijaliziranih provodnih tkiva (ksilem i floem) u korijenju, stabljikama i lišću.', 0);
INSERT INTO property VALUES (1,8562, 'hr', 'definition','Formulirana ili sustavna metoda za praćenje ili slijeđenje  pitanja ili problematike u vezi s okolišem.', 0);
INSERT INTO property VALUES (1,8563, 'hr', 'definition','Čin ili proces kupnje, prodaje ili razmjene roba i usluga, bilo na veliko ili na malo, unutar neke zemlje ili između zemalja.', 0);
INSERT INTO property VALUES (1,8564, 'hr', 'definition','Dozvole za trgovanje emisijama (emisijske dozvole) upotrebljavaju se u regulatornoj shemi zaštite okoliša gdje se izvorima onečišćujućih tvari koje će biti regulirane (najčešće onečišćujuće tvari u zrak) daju dozvole za ispuštanje određenog broja tona onečišćujuće tvari. Vlada izdaje samo ograničen broj dozvola u skladu sa željenom razinom emisija. Vlasnici dozvola mogu ih zadržati i ispuštati tvari, ili smanjiti svoje emisije i prodati dozvole. Činjenica da dozvole imaju vrijednost kao predmet za prodaju daje vlasniku poticaj da smanji svoje emisije.', 0);
INSERT INTO property VALUES (1,8566, 'hr', 'definition','Čin ili postupak kupnje, prodaje ili razmjene roba i korištenje dobara i usluga.', 0);
INSERT INTO property VALUES (1,8567, 'hr', 'definition','Umjetno ograničenje slobodne razmjene robe i usluga među narodima. Najčešće vrste trgovinskih zapreka su carine, kvote i devizna kontrola. Takve prepreke u trgovini obično nameće zemlja koja želi zaštititi domaće proizvode na domaćem tržištu od strane konkurencije, poboljšati uvjete svoje trgovine, smanjiti nezaposlenost u svojoj zemlji ili poboljšati stanje svoje platne bilance. Podizanje trgovinskih barijera jedne zemlje često provocira i stav drugih zemalja. Općenito, utjecaj trgovinskih zapreka je smanjenje obujma trgovine, uz istodobno povećanje domaće cijene zaštićenog dobra. Tako to dovodi do relativno neučinkovite raspodjele svjetskih resursa i smanjuje razinu ukupnog svjetskog prihoda i proizvodnje.', 0);
INSERT INTO property VALUES (1,8569, 'hr', 'definition','Čin ili posao kupnje i prodaje za novac. Trgovinsko ili komercijalno poslovanje u cjelini, ili kupnja i prodaja, odnosno razmjena, robe, bilo na veliko ili na malo, unutar zemlje ili između zemalja.', 0);
INSERT INTO property VALUES (1,8570, 'hr', 'definition','Učinci trgovine na okoliš mogu biti izravni, kao što su trgovina ugroženim vrstama, prirodnim resursima, prirodnih proizvodima, kao što su tropsko drvo itd., ili neizravni, kao što su krčenje šuma, gubitak staništa, onečišćenja iz rudarstva, iz proizvodnje energije, izlijevanja nafte, globalno zatopljenje, itd., povećanje prometne infrastrukture.', 0);
INSERT INTO property VALUES (1,8571, 'hr', 'definition','Način djelovanja što ga usvoji i provodi vlada, poslovna ili neka druga organizacija, kojim se promiče ili utvrđuje smjer za čin ili proces kupnje, prodaje ili razmjene dobara i usluga unutar neke zemlje ili između zemalja.', 0);
INSERT INTO property VALUES (1,8572, 'hr', 'definition','Čin ili proces kupnje, prodaje ili razmjene dobara, bilo na veliko ili na malo, unutar neke zemlje ili između zemalja.', 0);
INSERT INTO property VALUES (1,8574, 'hr', 'definition','Trgovinska diskriminacija koja se primjenjuje na izvozne proizvode određenih država, ali ne na sličnu robu iz nekih drugih država.', 0);
INSERT INTO property VALUES (1,8576, 'hr', 'definition','Organizacija čiji su svi članovi ili većina članova radnici i čiji glavni ciljevi uključuju reguliranje odnosa između radnika i poslodavaca ili udruga poslodavaca.', 0);
INSERT INTO property VALUES (1,8579, 'hr', 'definition','Naučeno, nenasumično, sustavno ponašanje i znanje koje se prenosi tijekom nekoliko naraštaja, a posebno običaji i vjerovanja nastali prije pojave moderne znanosti i tehnologije.', 0);
INSERT INTO property VALUES (1,8580, 'hr', 'definition','Sustav postupanja i liječenja od bolesti koji se temelji na kulturnim vjerovanjima i praksama koje se prenose s naraštaja na naraštaj.', 0);
INSERT INTO property VALUES (1,8582, 'hr', 'definition','1) Kretanje vozila, brodova, zrakoplova, osoba itd. u nekom području ili na nekom prometnom pravcu.
INSERT INTO property VALUES (1,8583, 'hr', 'definition','Neočekivani incident odnosno nesreća koji potencijalno može nanijeti štetu a koji nastaje tijekom kretanja ili zbog sudara brodova, vozila ili osoba na prometnom pravcu na zemlji, u vodi, zraku ili svemiru.', 0);
INSERT INTO property VALUES (1,8585, 'hr', 'definition','Organizacija učinkovitijeg kretanja prometa u određenoj cestovnoj mreži preraspodjelom prometnih tokova, kontroliranjem raskrižja i reguliranjem vremena i mjesta za parkiranje.', 0);
INSERT INTO property VALUES (1,8586, 'hr', 'definition','Način kontroliranja broja i brzine motornih vozila koja se kreću cestom.', 0);
INSERT INTO property VALUES (1,8588, 'hr', 'definition','Ispušni plinovi i pare koje ispuštaju motorna vozila.', 0);
INSERT INTO property VALUES (1,8589, 'hr', 'definition','Određivanje potrebnih kapaciteta i izgleda objekata na autocestama i ulicama koji mogu sigurno i ekonomično opsluživati kretanje vozila između određenih točaka.', 0);
