INSERT INTO property VALUES (1,8597, 'hr', 'definition','Prometna buka može se kontrolirati smanjenjem na izvoru,  opremanjem motornih vozila prigušivačima, instaliranjem zapreka (bukobrana) koje prekidaju izravni put zvuka ili izolacijom kuća i stanova izloženih visokim razinama buke, kao što su one nastale na autocestama ili zračnim lukama.', 0);
