INSERT INTO property VALUES (1,8606, 'hr', 'definition','Povezana skupina vagona, koje obično gura ili povlači  lokomotiva.', 0);
INSERT INTO property VALUES (1,8607, 'hr', 'definition','Proces kojim osoba ili skupina osoba postiže dogovoreni  standard znanja/vještine/stručnosti, putem prakse i poučavanja.', 0);
INSERT INTO property VALUES (1,8608, 'hr', 'definition','Mjesto na kojem se ljudi pripremaju za određenu svrhu.', 0);
INSERT INTO property VALUES (1,8610, 'hr', 'definition','Put koji opisuje objekt koji se kreće u zraku ili prostoru pod utjecajem sila kao što su potisak, otpor vjetra i sila teža, osobito zakrivljeni put projektila.', 0);
INSERT INTO property VALUES (1,8621, 'hr', 'definition','Onečišćeni zrak i voda, ili bilo koji drugi onečišćeni otpad, koji nastaje u jednoj zemlji i prenosi se u druge.', 0);
