INSERT INTO property VALUES (1,8731, 'hr', 'definition','Gumeni obruč koji se postavlja preko ruba kotača cestovnih vozila kako bi osigurao dobro prijanjanje i smanjili cestovni udari.', 0);
INSERT INTO property VALUES (1,8736, 'hr', 'definition','Odvajanje koloidnih ili vrlo sitnozrnatih krutih materijala filtracijom kroz mikroporozne ili polupropusne medije.', 0);
