INSERT INTO property VALUES (1,8755, 'hr', 'definition','Brtvljenje odlagališta relativno nepropusnom barijerom dizajniranom tako da zadrži procjedne vode. Materijali obloge uključuju plastiku i gustu glinu.', 0);
