INSERT INTO property VALUES (1,8776, 'hr', 'definition','Dobrovoljno udruženje oko 180 država potpisnica Povelje UN-a (1945. god.), čiji je primarni cilj održavanje međunarodnog mira i sigurnosti, rješavanje gospodarskih, društvenih i političkih problema putem međunarodne suradnje i promicanjem poštivanja ljudskih prava.', 0);
INSERT INTO property VALUES (1,8780, 'hr', 'definition','Benzin s niskim oktanskim brojem, bez olovnih aditiva i koji stoga stvara manje onečišćenja u zraku.', 0);
INSERT INTO property VALUES (1,8782, 'hr', 'definition','Područja gdje su postignuta značajna smanjenja onečišćujućih tvari koje stvaraju ozon posredstvom industrijskih inicijativa za kontrolu i/ili sprječavanje onečišćenja, provedbom planova poboljšanja prijevoza, nacionalnim naporima za smanjenje emisija iz automobilskih ispušnih cijevi i nižom volatilnošću (stopom isparavanja) benzina.', 0);
INSERT INTO property VALUES (1,8784, 'hr', 'definition','Neobrađena (nepročišćena) voda.', 0);
INSERT INTO property VALUES (1,8789, 'hr', 'definition','Tijelo dvodomnog zakonodavnog tijela koje se sastoji bilo od predstavnika država članica u federaciji ili odabranog broja pojedinaca iz nekih povlaštenih staleža ili društvenih klasa.', 0);
INSERT INTO property VALUES (1,8791, 'hr', 'definition','Vrlo toksičan i radioaktivan metalni element; upotrebljava se kao nuklearno gorivo.', 0);
INSERT INTO property VALUES (1,8794, 'hr', 'definition','Planirana, koordinirana skupina aktivnosti ili usluga namijenjenih poboljšanju urbanih središta kako bi se osigurali zdravi i sigurni životni uvjeti, učinkovit prijevoz i komunikacije, odgovarajući javni objekti i estetski uređen okoliš.', 0);
INSERT INTO property VALUES (1,8797, 'hr', 'definition','Područja unutar zakonom utvrđenih granica gradova i mjesta; prigradska naselja izgrađena za stambene, industrijske ili rekreacijske svrhe.', 0);
INSERT INTO property VALUES (1,8801, 'hr', 'definition','Stanje kada dio grada ili mjesta postane star ili prljav ili uništen, budući da su se poduzetnici i bogate obitelji odselili iz njega.', 0);
INSERT INTO property VALUES (1,8803, 'hr', 'definition','Plan, nacrt ili idejno rješenje grada ili mjesta.', 0);
INSERT INTO property VALUES (1,8804, 'hr', 'definition','Svako fizičko proširenje ili promjene u uporabi zemljišta u gradskim područjima, koji često uključuju podjelu na zone, izgradnju ili izmjenu zgrada, cesta, komunalnih i drugih objekata, uklanjanje stabala i drugih prepreka, kao i rast populacije i s njim povezane gospodarske, društvene i političke promjene.', 0);
INSERT INTO property VALUES (1,8805, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propisuje vlada radi reguliranja javnih službi i suprotstavljenih stambenih, poslovnih i industrijskih interesa u gradskim područjima, koja se uglavnom odlikuju umjerenom do visokom gustoćom naseljenosti.', 0);
INSERT INTO property VALUES (1,8807, 'hr', 'definition','Gradovi i mjesta koja se promatraju kao ekosustavi, koji imaju ulazne veličine materije i energije, recikliranje unutar sustava, te proizvedenu materiju i energiju koja odlazi u okoliš.', 0);
