INSERT INTO property VALUES (1,8824, 'hr', 'definition','Osobine, uzorak i struktura određenoga geografskog područja grada, uključujući i njegov biološki sastav, fizički okoliš i društvene obrasce.', 0);
INSERT INTO property VALUES (1,8826, 'hr', 'definition','Upravljanje, organizacija i planiranje koje se obavlja za gradove ili mjesta, osobito proces pretvaranja poljoprivrednog zemljišta ili neizgrađenog zemljišta u urede, poduzeća, stambeni prostor i drugi oblici izgradnje.', 0);
INSERT INTO property VALUES (1,8829, 'hr', 'definition','Djelatnost projektiranja, organiziranja ili pripremanja za budući izgled i stanje grada ili mjesta.', 0);
INSERT INTO property VALUES (1,8832, 'hr', 'definition','Način djelovanja koji usvoji i ostvaruje vlada, poslovni subjekt ili neka druga organizacija, kojoj je cilj poboljšanje ili razvoj gradova ili mjesta planiranjem korištenja zemljišta, gospodarenjem vodnim resursima, razvojem središta grada, posredstvom policije i kaznenog pravosuđa, odnosno kontrole onečišćenja.', 0);
INSERT INTO property VALUES (1,8833, 'hr', 'definition','Onečišćenje gusto naseljenih područja koje je uglavnom posljedica motornih vozila, industrijskih pogona, izgaranja i toplinskih elektrana itd.', 0);
INSERT INTO property VALUES (1,8834, 'hr', 'definition','Ukupan broj osoba koje žive u nekom gradu, gradskom području ili bilo kojem području u kojem ukupan broj stanovnika premašuje predviđeni broj.', 0);
INSERT INTO property VALUES (1,8839, 'hr', 'definition','Trajan proces preoblikovanja urbanih područja putem obnove i očuvanja, kao i poboljšanja. Programi urbane obnove u pravilu provode tijela javne vlasti, a obuhvaćaju one dijelove grada koji više ne zadovoljavaju trenutne standarde javne prihvatljivosti.', 0);
INSERT INTO property VALUES (1,8840, 'hr', 'definition','Obnova ili ponovna izgradnja područja gradova koja propadaju rušenjem ili poboljšanjem postojećih kuća i zgrada i opće poboljšanje uvjeta okoliša.', 0);
INSERT INTO property VALUES (1,8964, 'hr', 'definition','Format ili sustav koji se upotrebljava za snimanje i prijenos vizualnih i audiovizualnih informacija pretvaranjem pokretnih slika ili fotografija u električne signale.', 0);
INSERT INTO property VALUES (1,8967, 'hr', 'definition','Skupina kuća i drugih objekata, kao što je crkva, škola i neke trgovine, koji je manji od grada, obično u seoskom prostoru.', 0);
INSERT INTO property VALUES (1,8969, 'hr', 'definition','Ostatak tekućine iz destilacije alkoholnih pića, posebno ostatak koji zaostaje poslije fermentacije i destilacije melase od šećerne repe, koji je vrijedan jer od njega nastaju  kalijeve soli, amonijak itd.', 0);
INSERT INTO property VALUES (1,8974, 'hr', 'definition','Proučavanje izvanredno sitnih organizama nevidljivih mikroskopom koji se nazivaju virusima.', 0);
INSERT INTO property VALUES (1,8976, 'hr', 'definition','Izvanredno sitni organizmi nevidljivi mikroskopom koji zaražavaju biljke, životinje i bakterije, a ne mogu se razmnožavati izvan tkiva domaćina. Potpuno formiran virus sastoji se od nukleinske kiseline (DNK ili RNK) okružene proteinskom i lipidnom (masnom) ovojnicom. Nukleinske kiseline virusa ometaju mehanizam sinteze nukleinske kiseline stanice domaćina tako da ona zbog virusa proizvodi više virusne nukleinske kiseline. Virusi su uzročnici mnogih bolesti (npr. mozaične bolesti mnogih kultiviranih biljaka, miksomatoze, slinavke i šapa, prehlade, gripe, ospica, dječje paralize). Mnoge biljne viruse prenose insekti, a neke prenose nematode. Životinjski virusi šire se kontaktom, kapljičnom infekcijom ili kukcima vektorima, a neke se šire razmjenom tjelesnih tekućina.', 0);
INSERT INTO property VALUES (1,8977, 'hr', 'definition','Rasipanje energije i stvaranje napetosti u mediju deformacijom fluida (elemenata tekućine); kvantitativno, apsolutna viskoznost. Također se zove otpor protoku.', 0);
INSERT INTO property VALUES (1,8986, 'hr', 'definition','Organski spoj prisutan u različitim sitnim količinama u prirodnim prehrambenim proizvodima i nužan za normalne procese rasta i održavanje tijela.', 0);
INSERT INTO property VALUES (1,8987, 'hr', 'definition','Dio hortikulture koji se bavi vinogradarstvom, proučavanjem sorti grožđa, metoda uzgoja grožđa, kao i suzbijanjem kukaca i kontrolom bolesti.', 0);
INSERT INTO property VALUES (1,8990, 'hr', 'definition','Popis riječi ili izraza u nekom jeziku, tehničkom području ili nekom specijaliziranom području, obično poredanih abecednim redom, a često i popraćenih kratkim definicijama i prijevodima na strane jezike.', 0);
INSERT INTO property VALUES (1,8992, 'hr', 'definition','Posebna vrsta obuke za redovno zanimanje ili profesiju, a osobito takva za koju je osoba posebno pogodna ili ima odgovarajuće sposobnosti.', 0);
INSERT INTO property VALUES (1,8993, 'hr', 'definition','Organski spoj koji lako hlapi.', 0);
INSERT INTO property VALUES (1,8995, 'hr', 'definition','Svojstvo jedne ili više tvari da se pretvaraju u paru ili plin bez kemijske promjene.', 0);
INSERT INTO property VALUES (1,8999, 'hr', 'definition','Izbacivanje krutih, tekućih ili plinovitih materijala iz vulkana.', 0);
INSERT INTO property VALUES (1,9003, 'hr', 'definition','Procesi kojima se magma i s njom povezani plinovi podižu u koru i ekstrudiraju na površinu Zemlje i u atmosferu.', 0);
INSERT INTO property VALUES (1,9004, 'hr', 'definition','Otvor na površini Zemlje kroz koje se izbacuju magma i s njom povezani plinovi; isto tako, oblik ili struktura, obično konusna, koju proizvodi taj izbačeni materijal.', 0);
INSERT INTO property VALUES (1,9008, 'hr', 'definition','Ukupna količina otpadaka ili neupotrebljivog materijala koji ulazi u proces ili sustav.', 0);
INSERT INTO property VALUES (1,9010, 'hr', 'definition','Neplaćene aktivnosti koje obavljaju građani, često organizirani u udruge, za pružanje usluga drugima, a osobito  starijim i siromašnim ljudima, osobama s invaliditetom itd.', 0);
INSERT INTO property VALUES (1,9013, 'hr', 'definition','Kemijska reakcija sumpora (ili nekoga drugog vulkanizacijskog sredstva) s gumom ili plastikom kojom se stvaraju međusobno spajanje polimernih lanaca; time se povećava čvrstoća i otpornost polimera.', 0);
INSERT INTO property VALUES (1,9015, 'hr', 'definition','Vrsta za koju je vjerojatno da će postati ugrožena ako se ne poduzmu zaštitne mjere.', 0);
INSERT INTO property VALUES (1,9019, 'hr', 'definition','Waddensko more je plitko more koje se proteže uz obale Sjevernog mora Nizozemske, Njemačke i Danske. To je vrlo dinamičan ekosustav s plimnim kanalima, pijeskom, blatnim ravnicama, slanim močvarama, plažama, dinama, riječnim ušćima i prijelaznom zonom do Sjevernog mora, ''offshore'' zonom. Većina dijelova Waddenskog mora, osobito u Nizozemskoj i Donjoj Saskoj, zaklonjena je pregradnim otocima i sadrži manja ili šira područja plimnih ravnica. Sadašnji oblik Waddenskog mora je rezultat kako sila prirode, tako i djelovanja čovjeka. Dva puta na dan, u prosjeku, 15 km3 morske vode ulazi u Waddensko more. S vodom iz Sjevernog mora unose se velike količine pijeska i mulja koji se talože u mjestima s malim kretanjem vode. Tijekom oseke pojavljuju se veliki dijelovi Waddenskog mora. Te takozvane plimne ravnice pokrivaju oko 2/3 područja plime i oseke i jedno su od njezinih najkarakterističnijih obilježja. Nigdje u svijetu ne nalazi se tako veliki neprekinut dio plimnih ravnica. One čine 60% svih područja plime i oseke u Europi i Sjevernoj Africi.', 0);
INSERT INTO property VALUES (1,9022, 'hr', 'definition','Sustav koji kompenzira zaposlene fiksnim iznosom po komadu, satu, danu ili drugom vremenskom razdoblju, a koji pokriva sve naknade, uključujući plaće.', 0);
INSERT INTO property VALUES (1,9023, 'hr', 'definition','Vertikalna konstrukcija načinjena od kamena, opeke, drva itd., čija je duljina i visina mnogo veća od njezine debljine, a upotrebljava se za zatvaranje, podjelu ili podupiranje.', 0);
INSERT INTO property VALUES (1,9028, 'hr', 'definition','Sukob ili stanje neprijateljstva između dviju li više strana, nacija ili država, u kojem se upotrebljavaju oružane snage ili vojne operacije.', 0);
INSERT INTO property VALUES (1,9032, 'hr', 'definition','Životinja koja ima tjelesnu temperaturu koja ostaje ista i ne mijenja se s temperaturom okoline.', 0);
INSERT INTO property VALUES (1,9034, 'hr', 'definition','Plan/način ili metoda djelovanja razvijena unaprijed kako bi se što prije moglo obavijestiti pogođenu populaciju o bilo kojoj nagloj, hitnoj i obično neočekivanoj pojavi zbog koje je potrebno hitno djelovanje.', 0);
INSERT INTO property VALUES (1,9035, 'hr', 'definition','Svaki niz postupaka i naprava osmišljen za otkrivanje naglih ili potencijalnih prijetnja osobama, imovini ili okolišu, često korištenjem radarske tehnologije.', 0);
INSERT INTO property VALUES (1,9040, 'hr', 'definition','Rastrošno ili nepotrebno trošenje ili izdaci.', 0);
INSERT INTO property VALUES (1,9041, 'hr', 'definition','Materijal, često neupotrebljiv, ostao iza bilo kojeg proizvodnog, industrijskog, poljoprivrednog procesa ili drugih ljudskih aktivnosti; materijal oštećen ili izmijenjen tijekom proizvodnog procesa, koji je zatim ostao beskoristan.', 0);
INSERT INTO property VALUES (1,9042, 'hr', 'definition','Ispušni ili plinoviti zrak koji se ispušta iz bilo kojeg industrijskog, proizvodnog ili kemijskog procesa.', 0);
INSERT INTO property VALUES (1,9044, 'hr', 'definition','Istraživanje koje se provodi da bi se odlučilo kako je  prikladno organizirati rukovanje i zbrinjavanje različitih vrsta otpada.', 0);
INSERT INTO property VALUES (1,9048, 'hr', 'definition','Popis sveg otpada ili koji je proizveden ili oporabljen tijekom određenoga vremenskog razdoblja, razvrstanog prema vrstama i količinama.', 0);
INSERT INTO property VALUES (1,9049, 'hr', 'definition','Spremnik za smeće, otpatke itd.', 0);
INSERT INTO property VALUES (1,9051, 'hr', 'definition','Grupiranje otpada prema vrstama neupotrebljivih materijala koji su preostali poslije nekog procesa.', 0);
INSERT INTO property VALUES (1,9052, 'hr', 'definition','Utvrđena naknada, izdatak ili trošak za zbrinjavanje otpadaka ili neželjenih materijala zaostalih iza proizvodnog procesa.', 0);
INSERT INTO property VALUES (1,9053, 'hr', 'definition','Raspoređivanje neželjenih materijala zaostalih iza proizvodnih procesa ili otpaci sa mjesta na kojem obitavaju ljudi ili životinje u različite kategorije na temelju kemijskih i mikrobioloških sastojaka ili drugih zajedničkih obilježja.', 0);
INSERT INTO property VALUES (1,9054, 'hr', 'definition','Povremeno, ili na zahtjev, uklanjanje krutog otpada iz mjesta primarnih izvora uz upotrebu vozila za prikupljanje otpada, nakon čega slijedi odlaganje tog otpada u nekom središnjem objektu ili odlagalištu.', 0);
INSERT INTO property VALUES (1,9055, 'hr', 'definition','Skupljanje i prijevoz otpadaka na njihovom mjestu nastavka.', 0);
INSERT INTO property VALUES (1,9057, 'hr', 'definition','Vrste sastava komponenti materijala, prema postotku ili težini, koje su nastale na bilo kojem izvoru.', 0);
INSERT INTO property VALUES (1,9058, 'hr', 'definition','Svaki specijalizirani postupak ili metoda koja se upotrebljava za pretvaranje otpadaka (smeća) iz jednog stanja, oblika ili kemijskog sastava u drugo.', 0);
INSERT INTO property VALUES (1,9059, 'hr', 'definition','Uklanjanje plinovitih sastojaka iz otpada.', 0);
INSERT INTO property VALUES (1,9062, 'hr', 'definition','Uredan proces odbacivanja neželjenog ili beskorisnog materijala.', 0);
INSERT INTO property VALUES (1,9063, 'hr', 'definition','Zakon kojim se uređuju pravila u vezi sa zbrinjavanjem/odlaganjem, recikliranjem i obradom raznih vrsta otpada.', 0);
INSERT INTO property VALUES (1,9065, 'hr', 'definition','Iznos novca koji se mora platiti za uklanjanje ili oslobađanje od otpada ili neželjenih materijala zaostalih iz proizvodnog procesa.', 0);
INSERT INTO property VALUES (1,9067, 'hr', 'definition','Planirano ispuštanje, odlaganje ili zakapanje otpadaka (smeća) ili drugoga neupotrebljivog materijala na površini zemlje, na primjer u odlagalištu.', 0);
INSERT INTO property VALUES (1,9068, 'hr', 'definition','Iznos naknade, izdatka ili troška koji se mora platiti za uklanjanje ili oslobađanje od otpada ili neželjenih materijala zaostalih iz proizvodnog procesa.', 0);
INSERT INTO property VALUES (1,9069, 'hr', 'definition','Područje na kojem se otpad odlaže i spaljuje.', 0);
INSERT INTO property VALUES (1,9071, 'hr', 'definition','Odlaganje krutih otpada  bez ekološkog nadzora.', 0);
INSERT INTO property VALUES (1,9072, 'hr', 'definition','Razmjena dijela otpada koji se može reciklirati. Ovaj postupak omogućuje da se na najmanju mjeru smanji volumen otpada i cijene koje se odnose na zbrinjavanje otpada. Razmjena otpada zasniva se na konceptu da je "otpad jedne tvrtke sirovina druge tvrtke".', 0);
INSERT INTO property VALUES (1,9073, 'hr', 'definition','Prijevoz neželjenih materijala, uključujući one koji su zaostali poslije proizvodnih procesa, otpadaka ili smeća u druge države ili područja radi razmjene s inozemstvom.', 0);
INSERT INTO property VALUES (1,9216, 'hr', 'definition','Upravljanje kakvoćom vode obuhvaća četiri glavna elementa:  planirano korištenje vode (rekreacija, pitka voda, razmnožavanje ribe i životinjskog i biljnog svijeta, za industrijske ili poljoprivredne svrhe); kriteriji zaštite tih vrsta upotrebe; provedbeni planovi (za potrebna poboljšanja obrade industrijskog i komunalnog otpada) i planovi provedbe, kao i izjava o tome da se pazi da ne dođe do pogoršanja kakvoće vode kako bi se zaštitila postojeća visoka kakvoća vode.', 0);
INSERT INTO property VALUES (1,9217, 'hr', 'definition','Upravljanje ili rukovanje količinom raspoložive pitke vode.', 0);
INSERT INTO property VALUES (1,9220, 'hr', 'definition','Ovlasti vladine agencije ili njezinih upravitelja da upravljaju i provode propise, zakone i vladine politike koje se odnose na očuvanje i zaštitu vodnih resursa.', 0);
INSERT INTO property VALUES (1,9221, 'hr', 'definition','Voda u bilo kojem od svojih oblika, gdje god da se nalazi - u atmosferi, na površini ili pod zemljom - koja jest ili može biti od vrijednosti za čovjeka.', 0);
INSERT INTO property VALUES (1,9223, 'hr', 'definition','Kontrolirano korištenje ili zaštita bilo koje zalihe vode tako da bude potencijalno korisna za neku svrhu, kao što su  gospodarske, rekreacijske svrhe ili za održavanje života.', 0);
INSERT INTO property VALUES (1,9227, 'hr', 'definition','Korištenje procesnih otpadnih voda ili otpadnih voda iz postrojenja za pročišćavanje u različitom proizvodnom procesu.', 0);
INSERT INTO property VALUES (1,9229, 'hr', 'definition','Proces kojim voda postaje slana, na koji se nailazi osobito u  zemljama s vrućom klimom gdje se provodi navodnjavanje.', 0);
INSERT INTO property VALUES (1,9230, 'hr', 'definition','Gospodarenje vodnim resursima s ciljem osiguranja kontinuirane dostupnosti vode za ljudsku upotrebu i prirodne ekosustave.', 0);
INSERT INTO property VALUES (1,9231, 'hr', 'definition','Znanost koja se bavi pojavom, kruženjem, distribucijom i svojstvima voda na zemlji, kao i njihovom reakcijom s okolinom.', 0);
INSERT INTO property VALUES (1,9232, 'hr', 'definition','Tekućina od koje se sastoje potoci, jezera i mora, te koja izvire iz zemlje u izvorima.', 0);
INSERT INTO property VALUES (1,9234, 'hr', 'definition','Upotreba, regulacija i obrada vode i zemljišnih resursa nekog poriječja radi postizanja utvrđenih ciljeva.', 0);
INSERT INTO property VALUES (1,9235, 'hr', 'definition','Linija koja dijeli dva susjedna riječna sustava, kao što je greben.', 0);
INSERT INTO property VALUES (1,9236, 'hr', 'definition','Svaki fizički produžetak, ili promjene u uporabi zemljišta u obalnim (lučkim) područjima, na dokovima.', 0);
INSERT INTO property VALUES (1,9242, 'hr', 'definition','Obična tekućina (H2O) koja tvori kišu, rijeke, more i sl., i koja čini veliki dio tijela organizama.', 0);
INSERT INTO property VALUES (1,9244, 'hr', 'definition','Izvor ili volumen vode dostupne za korištenje; isto tako, sustav spremnika, bunara, vodova i postrojenja za pročišćavanje potrebne da bi voda dostupna i upotrebljiva.', 0);
INSERT INTO property VALUES (1,9245, 'hr', 'definition','Prijevoz robe i osoba brodovima koji putuju morem ili unutarnjim plovnim putovima.', 0);
INSERT INTO property VALUES (1,9246, 'hr', 'definition','Pročišćavanje vode kako bi ona postala prikladna za piće ili bilo koju drugu upotrebu.', 0);
INSERT INTO property VALUES (1,9251, 'hr', 'definition','Razlikuju se tri vrste upotrebe vode: a) povlačenje, gdje se voda uzima iz rijeke ili površinskog ili podzemnog spremnika, i nakon uporabe se voda vraća u prirodno vodno tijelo, npr. vodu za hlađenje u industrijskim procesima. Takvi povratni tokovi osobito su važni za daljnje korisnike kada se radi o  vodi uzetoj iz rijeka; b) potrošnja, koja počinje povlačenjem, ali u ovom slučaju bez povratka, npr. navodnjavanje, para koja ''bježi'' u atmosferu, voda koja se nalazi u gotovim proizvodima, tj. više nije dostupna izravno za naknadne upotrebe, c) ne-povlačenje, tj. in situ korištenje vodnog tijela za plovidbu (uključujući i plutanje trupaca u drvnoj industriji), ribolov, rekreacija, zbrinjavanje otpadnih voda i proizvodnja električne energije u hidroelektranama.', 0);
INSERT INTO property VALUES (1,9254, 'hr', 'definition','Rijeka, kanal ili neki drugi plovni kanal koji se upotrebljava kao sredstvo za putovanja ili prometno sredstvo.', 0);
INSERT INTO property VALUES (1,9255, 'hr', 'definition','Iskopani izvor za vađenje vode iz zone zasićenja.', 0);
INSERT INTO property VALUES (1,9257, 'hr', 'definition','Postrojenje za obradu i pročišćavanje vode prije nego što se ona crpi u cijevi za distribuciju u kuće, tvornice, škole itd.', 0);
INSERT INTO property VALUES (1,9259, 'hr', 'definition','Energija koja se dobiva gibanjem morskih valova pri obali.', 0);
INSERT INTO property VALUES (1,9262, 'hr', 'definition','Pokretni greben ili val/izbočina od vode koja nastaje blizu površine mora, a odlikuje se oscilirajućim i uzlaznim i silaznim pokretima, često kao rezultat otpora vjetra koji stvara trenje.', 0);
INSERT INTO property VALUES (1,9267, 'hr', 'definition','Instrument napada ili obrane u borbi, kao što su pištolj, projektil ili mač.', 0);
INSERT INTO property VALUES (1,9269, 'hr', 'definition','Svakodnevni meteorološki uvjeti, posebno temperatura, naoblaka i oborine, koji utječu na određeno mjesto.', 0);
INSERT INTO property VALUES (1,9270, 'hr', 'definition','Kompleks meteoroloških značajki u određenoj regiji.', 0);
INSERT INTO property VALUES (1,9274, 'hr', 'definition','Promjena prirodnih vremenskih pojava tehničkim sredstvima.', 0);
INSERT INTO property VALUES (1,9276, 'hr', 'definition','Povremeni ili kontinuirani nadzor ili analiza stanja atmosfere i klime, uključujući i varijable kao što su temperatura, vlaga, brzina vjetra i tlak zraka.', 0);
INSERT INTO property VALUES (1,9278, 'hr', 'definition','Čin ili proces predviđanja i isticanja meteoroloških uvjeta koji se očekuje za određeno vremensko razdoblje i za određeno područje ili dio zračnog prostora, korištenjem objektivnih modela na temelju određenih atmosferskih parametara, zajedno s vještinom i iskustvom meteorologa.', 0);
INSERT INTO property VALUES (1,9280, 'hr', 'definition','Svaka biljka koja raste divlje i obilato, a posebno ona koja raste između kultiviranih biljaka, lišavajući ih prostora, hrane, itd.', 0);
INSERT INTO property VALUES (1,9281, 'hr', 'definition','Oslobađanje površina zemljišta od korova s pomoću nekoliko sredstava, kao što su herbicidi, obrada, spaljivanje, košnja i sadnja drugih usjeva.', 0);
INSERT INTO property VALUES (1,9286, 'hr', 'definition','Gravitacijska sila kojom Zemlja privlači neko tijelo. I dalje, gravitacijska sila kojom zvijezda, planet ili satelit privlači neko tijelo u blizini.', 0);
INSERT INTO property VALUES (1,9289, 'hr', 'definition','Spajanje dvaju metala primjenom topline da se oni rastale i spoje, s metalnom popunom ili bez nje.', 0);
INSERT INTO property VALUES (1,9290, 'hr', 'definition','Rupa iskopana u zemlji kako bi se doprlo do zalihe vode, nafte, slane vode ili plina.', 0);
INSERT INTO property VALUES (1,9293, 'hr', 'definition','Geografska regija afričkog kontinenta omeđena na zapadu i jugu Atlantskim oceanom, uključujući republike Benin, Burkinu Faso, Kapverdske otoke, Obalu Slonovače, Gambiju, Ganu, Guineju Bisao, Liberiju, Mali, Mauritaniju, Niger, Nigeriju, Senegal, Siera Leone i Togo.', 0);
INSERT INTO property VALUES (1,9294, 'hr', 'definition','Azijska geografska regija koja uključuje Tursku, Iran i druge države Bliskog istoka i Arapskog poluotoka.', 0);
INSERT INTO property VALUES (1,9295, 'hr', 'definition','Geografska regija europskog kontinenta okružena Sjevernim morem, Atlantskim oceanom i Sredozemnim morem, uključujući Belgiju, Francusku, Njemačku, Veliku Britaniju, Grčku, Italiju, Luksemburg, Nizozemsku, Portugal, Španjolsku i druge države članice zapadne Europske unije.', 0);
INSERT INTO property VALUES (1,9297, 'hr', 'definition','Područja koja su preplavljena površinskom ili podzemnom vodom dovoljno često da podupiru prevladavanje vegetativnog ili vodenog života za čiji su rast i razmnožavanje potrebna zasićena ili sezonski zasićena tla.', 0);
INSERT INTO property VALUES (1,9298, 'hr', 'definition','Ekosustavi područja uglavnom poplavljenih vodom, ali koji nude povišena zemljišta kao stanište za divlje životinje. Ova područja obuhvaćaju močvare, i sezonske i trajne, močvarna područja, otvorenu slatku vodu, plitke slane lagune, ušća rijeka, naplavne ravnice i pješčane dine na obalama. Oni osiguravaju hranu, tlo za razmnožavanje, vodu i utočište za mnoge oblike riba, ptica i drugih životinja i biljnog svijeta. Oni su među najproduktivnijim ekosustavima za proizvodnju drveta, treseta i usjeva i bilja kao što su riža i razne bobice.', 0);
INSERT INTO property VALUES (1,9299, 'hr', 'definition','Proces koji se upotrebljava za uklanjanje čestica i/ili plinovitih onečišćivača pomoću vodene struje ili blata.', 0);
INSERT INTO property VALUES (1,9300, 'hr', 'definition','1) Uređaj za čišćenje zraka koji doslovno ispire prašinu. Ispušni zrak se utiskuje u komoru za raspršivanje, gdje zbog finih čestica vode prašina pada sa struje zraka. Voda puna prašine tada se pročišćuje kako bi se uklonio čvrsti materijal i često recirkulira.
INSERT INTO property VALUES (1,9301, 'hr', 'definition','Organski otpad ili materijal preostao nakon proizvodnog  procesa, koji se odlikuje prisutnošću vlage.', 0);
INSERT INTO property VALUES (1,9302, 'hr', 'definition','Veliki morski sisavci iz reda Cetacea; tijelo je aerodinamički oblikovano, širok plosnat rep služi za pogon, a udovi su dijelovi za držanje ravnoteže.', 0);
INSERT INTO property VALUES (1,9305, 'hr', 'definition','Hvatanje kitova kako bi se oni iskoristili kao hrana ili za ulje itd. Kitovi su najveći sisavci koji još postoje na svijetu. Love se uglavnom zbog njihovih ulja, iako se u nekim slučaju love za hranu. Neke vrste kitova su izumrle zbog prekomjernog lova i populacije mnogih postojećih vrsta opasno su se snizile. Komercijalni lov na kitove strogo je ograničen.', 0);
INSERT INTO property VALUES (1,9308, 'hr', 'definition','Posao prodaje robe trgovcima na malo u većim količinama nego što se prodaje krajnjim potrošačima, ali u manjim količinama nego što je kupljena od proizvođača.', 0);
INSERT INTO property VALUES (1,9310, 'hr', 'definition','Nepripitomljene životinje koje žive neovisno o čovjeku.', 0);
INSERT INTO property VALUES (1,9313, 'hr', 'definition','Životinje i biljke koje rastu bez utjecaja čovjeka, obično u prirodnim uvjetima (u divljini).', 0);
INSERT INTO property VALUES (1,9314, 'hr', 'definition','Niz mjera potrebnih za održavanje ili obnavljanje prirodnih staništa i populacija vrsta divlje faune i flore u povoljnom stanju.', 0);
INSERT INTO property VALUES (1,9315, 'hr', 'definition','Prikladna gorske ili močvarna područja koja potpomažu opstanak divljih životinja i biljnog svijeta.', 0);
INSERT INTO property VALUES (1,9318, 'hr', 'definition','Brojčane činjenice ili podaci prikupljeni raznim metodologijama, kao što su obilasci staništa radi procjene veličine populacije divljih životinja i biljaka u svrhu analiziranja trendova populacija.', 0);
INSERT INTO property VALUES (1,9321, 'hr', 'definition','1) Područje, najčešće u prirodnom stanju, koje je državna ili privatna agencija rezervirala (izdvojila) za zaštitu pojedinih vrsta životinja tijekom dijela godine ili cijele godine.
INSERT INTO property VALUES (1,9322, 'hr', 'definition','Prirodno stanje ili kvaliteta ili stanje kada životinja nije postala domaća, kada nije pripitomljena ni uzgojena.', 0);
INSERT INTO property VALUES (1,9323, 'hr', 'definition','Bilje koje raste u prirodnom okolišu (ne kultivirano).', 0);
INSERT INTO property VALUES (1,9327, 'hr', 'definition','Gibanje zraka u odnosu prema Zemljinoj površini; obično označava horizontalno gibanje zraka, za razliku od vertikalnog gibanja.', 0);
INSERT INTO property VALUES (1,9330, 'hr', 'definition','Razbijanje žive stijene na manje čestice i njihovo uklanjanje od strane vjetre. Može se pojaviti na bilo kojem tlu čija je površina suha, nezaštićena raslinjem (koje bi ga vezalo na razini korijena i zaklonilo  površinu), i sastoji se od laganih čestica. Mehanizmi uključuju izravno podizanje prašine i čestica tla zrakom i odvajanje ili abraziju površine materijala zbog utjecaja čestica kada su one već u zraku.', 0);
INSERT INTO property VALUES (1,9331, 'hr', 'definition','Stroj za mljevenje ili crpljenje koje pokreće niz podesivih lopatica ili jedara koja se okreću snagom vjetra.', 0);
INSERT INTO property VALUES (1,9335, 'hr', 'definition','Energija izvučena iz vjetra, tradicionalno u vjetrenjačama, ali sve više i pomoću kompliciranijih projektiranih objekata uključujući turbine, obično za proizvodnju električne energije, ali i za crpljenje vode. Snaga  vjetra je proporcionalna području zahvaćenom vjetrom na mjestu rotacije i kubne vrijednosti brzine vjetra, ali manje od polovice raspoložive energije može se iskoristiti.', 0);
INSERT INTO property VALUES (1,9336, 'hr', 'definition','Elektrana koja za pogon turbina za stvaranje energije upotrebljava vjetar.', 0);
INSERT INTO property VALUES (1,9344, 'hr', 'definition','Odraslo ljudsko biće ženskog spola.', 0);
INSERT INTO property VALUES (1,9347, 'hr', 'definition','Društveni položaj, sloj ili relativna važnost žena u društvu.', 0);
INSERT INTO property VALUES (1,9348, 'hr', 'definition','Drvo/komad drveta, osobito kao građevni materijal.', 0);
INSERT INTO property VALUES (1,9349, 'hr', 'definition','Stabla koja gusto rastu, veće od šumarka  i manja od velike šume.', 0);
INSERT INTO property VALUES (1,9352, 'hr', 'definition','Trajno posječena površina šume ili šumskog zemljišta. Na strmim padinama to može dovesti do snažnih erozija tla, osobito tamo gdje jake sezonske kiše ili topljenja snijega na višim razinama uzrokuje nagle snažne tokove vode. U vlažnim tropskim krajevima također može dovesti do ispuštanja ugljičnog dioksida iz tla.', 0);
INSERT INTO property VALUES (1,9353, 'hr', 'definition','Sustav biološke zajednice i njezinog neživog okoliša koji uzajamno djeluju jedan na drugoga u šumovitim područjima ili površinama zemljišta prekrivenima drvećem i grmljem.', 0);
INSERT INTO property VALUES (1,9356, 'hr', 'definition','Upotreba kemikalija kako bi se spriječilo ili usporilo propadanje drva, osobito zbog gljivica ili kukaca; konzervansi u širokoj upotrebi uključuju kreozot, smolu, natrij fluorid i katrana; posebno se upotrebljavaju na drvu koje se dodiruje s tlom.', 0);
INSERT INTO property VALUES (1,9362, 'hr', 'definition','Otpad preostao nakon obrade sirovog drva.', 0);
INSERT INTO property VALUES (1,9363, 'hr', 'definition','Tekstilno vlakno izrađeno od sirove vune, a odlikuje se svojstvom upijanja, otpornošću i izolacijom.', 0);
INSERT INTO property VALUES (1,9366, 'hr', 'definition','Nesreća koja se dogodi tijekom zaposlenja i koju prouzroče inherentni ili povezani čimbenici koji proizlaze iz rada materijala koji su dio nečijeg zanimanja.', 0);
INSERT INTO property VALUES (1,9367, 'hr', 'definition','Rudnik iz kojeg su izvađeni svi minerali koji bi se mogli iskoristiti uz zaradu.', 0);
INSERT INTO property VALUES (1,9369, 'hr', 'definition','Sve postojeće okolnosti koje djeluju na radnika na radnom mjestu, uključujući i radno vrijeme, fizičke aspekte, zakonska prava i odgovornosti.', 0);
INSERT INTO property VALUES (1,9371, 'hr', 'definition','Vrijeme posvećeno plaćenom zaposlenju ili aktivnostima povezanim s poslom, obično izračunato kao dnevna ili tjedna satnica.', 0);
INSERT INTO property VALUES (1,9373, 'hr', 'definition','Poljodjelstvo - oranje tla za poljoprivredne svrhe.', 0);
INSERT INTO property VALUES (1,9375, 'hr', 'definition','Bilo koje ili sva mjesta ili okruženja u kojima ljudi rade.', 0);
INSERT INTO property VALUES (1,9380, 'hr', 'definition','Zemlja sa svim svojim stanovnicima i svim što se na njoj nalazi.', 0);
INSERT INTO property VALUES (1,9382, 'hr', 'definition','Mjesta velikog kulturnog značaja i geografska područja izuzetne univerzalne vrijednosti. Ona uključuju piramide u Egiptu, Veliki kanjon u Sjedinjenim Američkim Državama, Taj Mahal u Indiji, Kineski zid itd.', 0);
INSERT INTO property VALUES (1,9389, 'hr', 'definition','Računovodstveni postupak koji se upotrebljava kada je potraživanje nenaplativo te se stoga otpisuje kao gubitak.', 0);
INSERT INTO property VALUES (1,9391, 'hr', 'definition','Radnja ili čin suprotan pravilima prirodne pravde ili pravosuđa.', 0);
INSERT INTO property VALUES (1,9392, 'hr', 'definition','Djelo koje izvrši državni dužnosnik ili službenik u provođenju policijske, ustavne, zakonodavne, upravne ili sudbene vlasti, kojim se krše prava drugoga i uzrokuje šteta, bez zaštite jednakog ili nadređenog prava.', 0);
INSERT INTO property VALUES (1,9395, 'hr', 'definition','Tvar koja se obično ne bi se našla u određenom okruženju, a obično znači toksična kemikalija koja je u potpunosti umjetna, kao što je klorinirani aromatski spoj ili organoživin spoj.', 0);
INSERT INTO property VALUES (1,9398, 'hr', 'definition','Elektromagnetsko zračenje koje prodire, obično koje nastaje ubrzanjem elektrona do velike brzine i naglim zaustavljanjem sudarom s krutim tijelom, ili prijelaza atoma s atomskim brojem većim od 10 u unutarnjoj ljusci; njihova valna duljina varira od oko 10 (-5) angstroma do 10 (3) angstroma, s prosječnom valnom duljinom koja se upotrebljava u istraživanjima od 1 angstroma.', 0);
INSERT INTO property VALUES (1,9403, 'hr', 'definition','Mnoge vrste jednostaničnih gljiva, od kojih većina pripada rodu Ascomycetes i reproduciraju se po pupanjem. Rod Saccharomyces upotrebljava se u proizvodnji piva i vina, budući da u niskim koncentracijama kisika proizvodi cimazu, sustav enzima koji razgrađuje šećere na alkohol i ugljični dioksid. Saccharomyces se također upotrebljavaju u proizvodnji kruha. Neki kvasci upotrebljavaju se kao izvor proteina i vitamina B skupine.', 0);
INSERT INTO property VALUES (1,9404, 'hr', 'definition','Dobit ili prihod stvoren ulaganjem ili poslovnom transakcijom.', 0);
INSERT INTO property VALUES (1,9406, 'hr', 'definition','Malo dijete u prvim godinama života.', 0);
INSERT INTO property VALUES (1,9407, 'hr', 'definition','Stanje kad je netko mlad; razdoblje između djetinjstva i odrasle dobi.', 0);
INSERT INTO property VALUES (1,9409, 'hr', 'definition','Mogućnosti zapošljavanja i zapošljavanje za mlade, bilo za novčanu naknadu ili obogaćivanje obrazovanja.', 0);
INSERT INTO property VALUES (1,9412, 'hr', 'definition','Krhak plavkasto-bijeli metalni element koji postaje obložen slojem otpornim na koroziju u vlažnom zraku i pojavljuje se uglavnom u obliku sfalerita i smitsonita. Sastavni je dio nekoliko legura, naročito mesinga i nikal-srebra, i upotrebljava se u lijevanju, pocinčavanju metala, te u baterijskim elektrodama.', 0);
INSERT INTO property VALUES (1,9417, 'hr', 'definition','Određivanje i rezervacije u sklopu master plana korištenja zemljišta za lake i teške industrije, stambene, uredske i druge zgrade; u upotrebi postoje ograničenja o vrstama građevina u svakoj zoni.', 0);
INSERT INTO property VALUES (1,9422, 'hr', 'definition','Područje u kojem se drže životinje, osobito divlje životinje, tako da ih ljudi mogu doći gledati ili ih proučavati.', 0);
INSERT INTO property VALUES (1,9425, 'hr', 'definition','Proučavanje životinja, uključujući njihovo razvrstavanje, građu, fiziologiju i povijest.', 0);
INSERT INTO property VALUES (1,9426, 'hr', 'definition','Bolesti životinja koje su biološki prilagođene životinjama i uobičajeno se nalaze kod životinja, ali se u određenim uvjetima zarazom prenose i na čovjeka.', 0);
INSERT INTO property VALUES (1,10002, 'hr', 'definition','Metoda bilježenja svih transakcija koje utječu na financijsko stanje poduzeća ili organizacije.', 0);
INSERT INTO property VALUES (1,10008, 'hr', 'definition','Gospodarski proizvod koji izravno zadovoljava ljudske želje.', 0);
INSERT INTO property VALUES (1,10012, 'hr', 'definition','Središnja državna organizacija koja ima ovlasti ili provodi nadzor nad vladinim aktivnostima koje se odnose na očuvanje i zaštitu ekoloških ili prirodnih resursa.', 0);
INSERT INTO property VALUES (1,10017, 'hr', 'definition','Posljedice buke na čovjekovo zdravlje, npr. gubitak sluha i psihološki učinci.', 0);
INSERT INTO property VALUES (1,10018, 'hr', 'definition','Ukupna fizička građa ljudskog bića.', 0);
INSERT INTO property VALUES (1,10019, 'hr', 'definition','Skupina znanosti, uključujući sociologiju, antropologiju, psihologiju, pedagogiju itd., za razliku od humanističke skupine znanosti.', 0);
INSERT INTO property VALUES (1,10024, 'hr', 'definition','Komunikacija ili prijenos podataka ili materijala u svrhu unaprjeđenja znanja od jedne osobe, mjesta ili položaja u drugi.', 0);
INSERT INTO property VALUES (1,10026, 'hr', 'definition','Akti koji se odnose na pravosuđe.', 0);
INSERT INTO property VALUES (1,10031, 'hr', 'definition','Istraživanje meteoroloških elemenata kao što su brzina i smjer vjetra, temperatura zraka i vlaga u zraku, tlak zraka, oborine, isparavanje, Sunčevo zračenje, vidljivost i oblaci radi prikupljanja podataka za vremenske prognoze ili za određene svrhe istraživanja.', 0);
INSERT INTO property VALUES (1,10032, 'hr', 'definition','Aktivno upravljanje područjima prirode kako bi se osiguralo da su biljni i životinjski svijet zaštićeni i da se održava kvaliteta okoliša.', 0);
INSERT INTO property VALUES (1,10034, 'hr', 'definition','Mjere opreza, radnje ili uređaji provedeni kako bi se spriječila vjerojatnost štete za ljude, imovinu ili prirodne resurse koju predstavljaju uvjeti ili događaji u okolišu koji nisu ni započeti ni nastali zbog ljudske aktivnosti.', 0);
INSERT INTO property VALUES (1,10039, 'hr', 'definition','Znanost koja se bavi učincima fizikalnih pojava na kemijska svojstva.', 0);
INSERT INTO property VALUES (1,10040, 'hr', 'definition','Kvantitativno određivanje prisutnosti, opsega ili vrste onečišćujućih tvari u okolišu pomoću mehaničkih sredstava, uključujući optičke, električne, akustičke i termodinamičke tehnike.', 0);
INSERT INTO property VALUES (1,10049, 'hr', 'definition','Uklanjanje proizvodnje opasnog otpada i emisije stakleničkih plinova na njihovom izvoru, unutar proizvodnog procesa. To se često može postići nizom raznih relativno jednostavnih strategija, uključujući i manje promjene u proizvodnim procesima, zamjenu proizvoda koji onečišćuju okoliš onima koji ga ne onečišćuju, te pojednostavljenjem ambalaže. Tvrtke koje su smanjile nastajanje otpada uštedjele su stotine milijuna dolara, koje su upotrijebile da potpomognu uključivanje  zaposlenika i eliminiraju potrebu za skupim filtriranjem na izljevu.', 0);
INSERT INTO property VALUES (1,10053, 'hr', 'definition','Proces procjene i odabira alternativnih regulatornih i neregulatornih odgovora da se pripremi za vjerojatnost slučajne nesreće i veličine njezine štete, uključujući i razmatranje pravnih i gospodarskih čimbenika te  čimbenika ponašanja.', 0);
INSERT INTO property VALUES (1,10054, 'hr', 'definition','Jedinstveni, koordinirani skup ili plan postupaka i uređaja namijenjenih za smanjenje pojave ili rizika od ozljeda, gubitaka i opasnosti za osobe, imovinu ili okoliš.', 0);
INSERT INTO property VALUES (1,10055, 'hr', 'definition','Proučavanje ponašanja temelja i struktura u odnosu na seizmička gibanja tla, te pokušaji da se ublaži učinak potresa na konstrukcije.', 0);
INSERT INTO property VALUES (1,10057, 'hr', 'definition','Proučavanje društva i odnosa pojedinih članova unutar društva, uključujući i ekonomiju, povijest, politologiju, psihologiju, antropologiju i sociologiju.', 0);
INSERT INTO property VALUES (1,10058, 'hr', 'definition','Upravljanje ili rukovanje vodama koje su prirodno otvorene prema atmosferi (rijekama, jezerima, akumulacijama, barama/ribnjacima, potocima, morima itd.).', 0);
INSERT INTO property VALUES (1,10064, 'hr', 'definition','Mjere opreza, postupci ili uređaji za sprječavanje ili smanjenje štete za životinje, biljke i druge organizme koji žive u svom prirodnom okolišu.', 0);
INSERT INTO property VALUES (1,10066, 'hr', 'definition','Dio grada ili mjesta u kojem su smještene glavne javne i povijesne zgrade.', 0);
INSERT INTO property VALUES (1,10067, 'hr', 'definition','Svaka aktivnost koja potiče ili podržava kupnju, prodaju ili razmjenu robe ili usluga s drugim državama, što može uključivati ​​marketing, diplomatski pritisak ili davanje izvoznih poticaja kao što su krediti i jamstva, državne subvencije, obuku i savjetovanja ili savjete.', 0);
INSERT INTO property VALUES (1,10068, 'hr', 'definition','Gradnja pomoću kamenja ili sličnih materijala, kao što su beton ili opeke.', 0);
INSERT INTO property VALUES (1,10071, 'hr', 'definition','Pješčana pličina u području plime i oseke, bez vegetacije. Ravnina u području plime i oseke stan je opsežan, gotovo horizontalan, močvarni ili neplodni pojas zemlje koji  naizmjenično pokriva i otkriva plima i oseka i sastoji se od nezbijenih sedimenata (uglavnom blata i pijeska). On može formirati površinsko tlo naslaga u deltama.', 0);
INSERT INTO property VALUES (1,10073, 'hr', 'definition','Vrste koje pripadaju životinjskom carstvu.', 0);
INSERT INTO property VALUES (1,10074, 'hr', 'definition','Vrste koje pripadaju biljnom carstvu.', 0);
INSERT INTO property VALUES (1,10078, 'hr', 'definition','Zajednička vjerovanja, prakse, običaji i druge kulturne sastavnice elementi neke etničke ili socijalne skupine koje su ukorijenjene u prošlosti, ali su se zadržale do sadašnjosti zahvaljujući sredstvima kao što su umjetnost i obrt, pjesme i glazba, ples, hrana, drama, pripovijedanje i određeni oblici usmene komunikacije.', 0);
INSERT INTO property VALUES (1,10084, 'hr', 'definition','Pod-odjeljak skupa načela i propisa koje utvrđuje vlade ili drugo tijelo vlasti, općenito definiranih njihovim djelokrugom ili primjenom.', 0);
INSERT INTO property VALUES (1,10085, 'hr', 'definition','Cijela mreža sudova u određenom području nadležnosti.', 0);
INSERT INTO property VALUES (1,10087, 'hr', 'definition','Kvantitativno određivanje prisutnosti, opsega ili vrste onečišćujućih tvari u okolišu proučavanjem djelovanja ili reakcija poznatih kemikalija na te onečišćivače.', 0);
INSERT INTO property VALUES (1,10089, 'hr', 'definition','Proces kumulativne reaktivne promjene nakon uvođenja neke onečišćujuće tvari u okoliš.', 0);
INSERT INTO property VALUES (1,10092, 'hr', 'definition','Buka koju emitiraju različiti izvori u urbanom okruženju.', 0);
INSERT INTO property VALUES (1,10097, 'hr', 'definition','Stanje čistoće i održavanja zdravlja.', 0);
INSERT INTO property VALUES (1,10099, 'hr', 'definition','Okruženje u kojem se obavljaju mnogobrojne aktivnosti povezane s proizvodnjom roba i usluga.', 0);
INSERT INTO property VALUES (1,10102, 'hr', 'definition','Vjerojatnost štete za zdravlje ljudi, imovinu ili okoliš koji predstavlja bilo koji aspekt fizičkog svijeta, osim ljudskog djelovanja.', 0);
INSERT INTO property VALUES (1,10103, 'hr', 'definition','Analiza vjerojatnosti da će se dogoditi, u određenom vremenskom razdoblju na određenom području, potencijalno štetna prirodna pojava.', 0);
INSERT INTO property VALUES (1,10104, 'hr', 'definition','Velika vjerojatnost da će neka opasnost ili situacija imati za posljedicu značajan broj izgubljenih života, ozlijeđenih osoba, oštećenja imovine, prekida gospodarske djelatnosti ili štetu za okoliš; ili bilo koji rezultat vjerojatnosti pojave i očekivane veličine oštećenja većeg od maksimalno prihvatljive razine.', 0);
INSERT INTO property VALUES (1,10106, 'hr', 'definition','Svaki niz postupaka i uređaja kojima se koristi osposobljeno osoblje za pružanje neposredne pomoći osobama koje su u opasnosti ili su ozlijeđene.', 0);
INSERT INTO property VALUES (1,10107, 'hr', 'definition','Tehnika, praksa ili znanost rukovanja ili upravljanja situacijama akutne teškoće, opasnosti ili nestabilnosti; odnosno ukupne mjere poduzete kako bi pružile rješenja za političke, ekonomske, ekološke i druge slične opasnosti i sukobe.', 0);
INSERT INTO property VALUES (1,10120, 'hr', 'definition','Koordinirane metode ili plan postupaka za utvrđivanje obvezne naknade u svrhu prikupljanja prihoda.', 0);
INSERT INTO property VALUES (1,10126, 'hr', 'definition','Razvoj na bilo kojoj razini općeg pojma okolnog ekosustava, njegova temeljna odnosa prema ljudskom životu i potrebe da se očuva njegova cjelovitost.', 0);
INSERT INTO property VALUES (1,10128, 'hr', 'definition','Znanost koja se bavi odnosom šumskog drveća i njihove okolinu, jednih prema drugima, i drugim biljkama i životinjama u šumi.', 0);
INSERT INTO property VALUES (1,10129, 'hr', 'definition','Istraživanje međuodnosa između živih organizama i organizama između i njihove okoline, korištenjem metoda ili teorija znanosti.', 0);
INSERT INTO property VALUES (1,10130, 'hr', 'definition','Pojam koji potječe iz biologije: grad se promatra kao ukupni okoliš, kao  sustav koji podržava život za velik broj ljudi koji su tamo koncentrirani, a unutar toga ljudi se sami organiziraju i prilagođuju okolišu koji se stalno mijenja. Smatra istim što je i ljudska ekologija.', 0);
INSERT INTO property VALUES (1,10131, 'hr', 'definition','Široko geografsko područje u kojem se nalaze specifični klimatski obrasci, oceanski uvjeti, vrste krajolika i vrste biljaka i životinja.', 0);
INSERT INTO property VALUES (1,10133, 'hr', 'definition','Grana inženjeringa koja se bavi okolišem i njegovim odgovarajućim upravljanjem. Glavne discipline ekološkog inženjeringa bave se vodoopskrbom, otpadnim vodama, oborinskom vodom, krutim otpadom, opasnim otpadom, radiologijom buke, industrijskom higijenom, oceanografijom i slično.', 0);
INSERT INTO property VALUES (1,10135, 'hr', 'definition','Primjena ekoloških koncepata na fosilne i sedimentne dokaze na proučavanje interakcije Zemljine površine, atmosfere i biosfere u ranija vremena.', 0);
INSERT INTO property VALUES (1,10136, 'hr', 'definition','Nasljeđe i očuvanje uravnotežene, integrirane funkcionalnosti Zemlje ili pojedine regije kao prirodnog staništa, s posebnom brigom za vodne resurse koji su nužni za održavanje ekosustava.', 0);
INSERT INTO property VALUES (1,10140, 'hr', 'definition','1) Općenito, pojam koji se upotrebljava za opisivanje topografskih visina.
INSERT INTO property VALUES (1,10142, 'hr', 'definition','1) Duboka šuplja udubina, ili kutak u stijeni ili strmoj planini, ili mala, ravna dolina koja se proteže u planinu ili niz planinu.
INSERT INTO property VALUES (1,10143, 'hr', 'definition','Dugačka, duboka, relativno uska dolina strmih strana zatvorena između visokih i strmoglavih zidova u zaravni ili planinskom području, često uz potok na dnu; slična klancu, ali veća od njega. Karakteristična je za sušna ili polusušna područja (kao što je zapadni SAD), gdje djelovanje vodene erozije uvelike premašuje trošenje zbog atmosferilija.', 0);
INSERT INTO property VALUES (1,10144, 'hr', 'definition','Rt ili predbrežje koje strši prema moru s obale, obično s velikom morskom liticom.', 0);
INSERT INTO property VALUES (1,10146, 'hr', 'definition','Dubok, strm zaobljen usjek ili šupljina, koju opisuju na različite načine kao potkovastu ili u obliku polumjeseca ili polukružnu, koja se nalazi visoko na strani planine i obično na početku ledenjačke doline i koju stvara erozivna aktivnost planinskog ledenjaka. Često sadrži malo okruglo jezero, i u njoj može, ali ne mora biti led ili snijeg.', 0);
INSERT INTO property VALUES (1,10148, 'hr', 'definition','Izbočina ljuske Zemljine kore, s površinom od nekoliko milijuna kvadratnih kilometara, i s dovoljno visine, tako da je velik dio kontinenta iznad razine mora.', 0);
INSERT INTO property VALUES (1,10149, 'hr', 'definition','Izdužena obala od pijeska ili šindre koja se nalazi paralelno s obalom i ne potapa je plima.  Ako je dovoljno visoka da se mogu stvarati dine, zovemo je barrier island?', 0);
INSERT INTO property VALUES (1,10152, 'hr', 'definition','Uska uvala ili zaljev, posebno u moru.', 0);
INSERT INTO property VALUES (1,10154, 'hr', 'definition','Lom ili zona prijeloma duž koje su se strane pomaknule jedna u odnosu prema drugoj paralelno s lomom.', 0);
INSERT INTO property VALUES (1,10155, 'hr', 'definition','Strmi obalni nagib koji može, ali ne mora biti strm, na kojem kut nagiba djelomično ovisi o spojevima, podlogama i tvrdoći materijala od kojih je litica formirana, a dijelom o erozijskim procesima koji ovdje djeluju. Gdje je napad vala dominantan, podnožje stijene će brzo erodirati i litica će se povući, posebno u nekonsolidiranim materijalima kao što su glina, pijesak itd. Pri tome često iza sebe  ostavlja abrazijsku platformu u podnožju litice.', 0);
INSERT INTO property VALUES (1,10158, 'hr', 'definition','Pučina leži izvan isključivih gospodarskih zona država. Sve države imaju jednaka prava na plovidbu, na preletavanje, polaganje podmorskih kablova, na izgradnju umjetnih otoka, ribarenje, kao i na provođenje znanstvenih istraživanja na pučini.', 0);
INSERT INTO property VALUES (1,10160, 'hr', 'definition','Obalni pojas plitke morske vode praktično odsječen od otvorenog mora koraljnim grebenom.', 0);
INSERT INTO property VALUES (1,10161, 'hr', 'definition','Kanal koji sadrži ili je ranije sadržavao vodu rijeke.', 0);
INSERT INTO property VALUES (1,10166, 'hr', 'definition','Materijal koji okružuje neki sustav, proces ili organizam.', 0);
INSERT INTO property VALUES (1,10170, 'hr', 'definition','Ravan ili blago nagnut predio ili donekle valovita kopnena površina nastala velikim taloženjem nanosa, obično uz rijeku koja povremeno preplavljuje svoje obale; može biti smještena na poplavnoj ravnici, u delti, ili aluvijalnoj lepezi.', 0);
INSERT INTO property VALUES (1,10175, 'hr', 'definition','Izdužena naslaga koralja smještena na razini oseke paralelno s obalom, ali odijeljen od nje širokom i dubokom lagunom ili tjesnacem. Smatra se da su se koralji formirali najprije na ravnoj površini: zatim, kad se razina mora povećala poslije ledenog doba, i time potopila nepravilne ravni koje su oblikovali valovi, rast koralja odvijao se zajedno s dizanjem razine oceana, pa su tako nastale vrlo debele naslage, o čemu i danas svjedoče mjesta kao što je Veliki koraljni greben u blizini istočne obale australske države Queensland u Australiji. On se proteže na više od 1900 km, a njegova širina varira od oko 30 km do 150 km.', 0);
INSERT INTO property VALUES (1,10176, 'hr', 'definition','Fizički oblik, konfiguracija ili opća neravnina dijela Zemljine površine, koja se promatra s obzirom na varijacije visine i nagiba ili nepravilnosti Zemljine površine; visine ili razlika u visini, promatrana kolektivno, neke kopnene površine.', 0);
INSERT INTO property VALUES (1,10189, 'hr', 'definition','Pravilnik ili uredba o području ekološkog staništa koje se odlikuje visokim stupnjem ujednačenosti u uvjetima okoline i u rasprostranjenosti biljaka i životinja.', 0);
INSERT INTO property VALUES (1,10190, 'hr', 'definition','Zemaljske ili vodene površine ili drugi krhak prirodni ambijent s jedinstvenim ili vrijednim ekološkim značajkama.', 0);
INSERT INTO property VALUES (1,10193, 'hr', 'definition','Nežive sastavnice okoliša (kamenje, minerali, tla, voda i klima).', 0);
INSERT INTO property VALUES (1,10194, 'hr', 'definition','Vode, uključujući močvare, koje služe kao stanište za međusobno povezane zajednice i populacije biljaka i životinja koje uzajamno djeluju jedne na druge.', 0);
INSERT INTO property VALUES (1,10197, 'hr', 'definition','Svaka parcela zemljišta, velika ili mala, pod javnom ili privatnom kontrolom, koji već ima poželjna ekološka svojstva, ili bi ih s pomoću korektivnih mjera mogla postići. Ta svojstva pridonose zadržavanju odnosno stvaranju staništa divljih životinja, stabilnosti tla, zadržavanju vode ili njezinom punjenju, vegetativnom pokrovu i sličnim vitalnim ekološkim funkcijama. Ekološki osjetljiva područja su u rasponu veličina od malih djelića do opsežnih značajki krajolika. Ona mogu uključivati rijetka ili uobičajena staništa, biljke i životinje.', 0);
INSERT INTO property VALUES (1,10198, 'hr', 'definition','Kontinentalni uvjeti, za razliku od morskih i atmosferskih. To je okruženje u kojem žive kopneni organizmi.', 0);
INSERT INTO property VALUES (1,10199, 'hr', 'definition','Trajni rezervat na morskom dnu za očuvanje vrsta.', 0);
INSERT INTO property VALUES (1,10200, 'hr', 'definition','Park kojim upravlja i vodi ga regija.', 0);
INSERT INTO property VALUES (1,10201, 'hr', 'definition','Svako područje kopna ili vode koja je izdvojeno za posebne namjene, često kako bi se spriječila ili smanjila šteta za njegove divlje životinje i biljke i ekosustave.', 0);
INSERT INTO property VALUES (1,10202, 'hr', 'definition','Područje kopna i/ili vode određeno da ima zaštićeni status u svrhu očuvanja određenih bioloških obilježja. Rezervatima se upravlja ponajprije kako bi se očuvala ta obilježja i kako bi se omogućilo istraživanje problema zbog kojih se upravlja prirodnim znamenitostima i biljnom i životinjskom populacijom. Propisi se redovno usvajaju u svrhu kontrole pristupa javnosti i sprječavanja ometanja životinja.', 0);
INSERT INTO property VALUES (1,10203, 'hr', 'definition','Šumska područja koja su zaštićena i koja se čuvaju od krčenja šuma zbog krhkosti njegovih ekosustava, i jer oni pružaju staništa za stotine vrsta biljaka i životinja.', 0);
INSERT INTO property VALUES (1,10208, 'hr', 'definition','Područje od nacionalnog interesa čija je zaštita odgovornost njegovog vlasnika kako bi se očuvao biljni i životinjski svijet, arheološka nalazišta i geološke znamenitosti.', 0);
INSERT INTO property VALUES (1,10210, 'hr', 'definition','Jezgra područja parka ili rezervata gdje se ne smije ometati prirodni ekosustav.', 0);
INSERT INTO property VALUES (1,10212, 'hr', 'definition','Morsko područje u kojem je zaštićen morski svijet.', 0);
INSERT INTO property VALUES (1,10214, 'hr', 'definition','Zona parka gdje su dopuštena znanstvena istraživanja. Osim toga, postoji i tampon zona koja štiti cijeli rezervat iz poljoprivrednog, industrijskog i urbanog razvoja.', 0);
INSERT INTO property VALUES (1,10215, 'hr', 'definition','Odabir i detaljni izgled prometnih pravaca javnog prijevoza u pogledu izgradnje, rada, usluga, tehnologija i gospodarskih kriterija.', 0);
INSERT INTO property VALUES (1,10217, 'hr', 'definition','Šumoviti seoski krajobraz koji je karakterističan za sjevernu Francusku, s malim poljima nepravilnog oblika i mnogim živicama i šikarama. U francuskom jeziku riječ bocage odnosi se i na samu živicu i na krajolik koji se sastoji od živice.', 0);
INSERT INTO property VALUES (1,10228, 'hr', 'definition','Parcela zemlje koja se sastoji od urednog i uravnoteženog rasporeda mnogo cvijeća, grmlja i drveća, uređenog prema britanskoj tradiciji ili stilu.', 0);
INSERT INTO property VALUES (1,10233, 'hr', 'definition','Krajobraz koji je nastao metodama kultiviranja zemljišta  usijecanjem terase ili klupa u padine kako bi se stvorila ravna područja. Ova praksa uobičajena je u planinskim područjima gdje je zemlja oskudna i padaline nesigurne.', 0);
INSERT INTO property VALUES (1,10237, 'hr', 'definition','Preventivne radnje, postupci ili uređenja poduzeta za sprječavanje ili smanjenje štete za cjelovitost okoliša nekoga fizičkog područja ili lokacije.', 0);
INSERT INTO property VALUES (1,10238, 'hr', 'definition','Oblik upravljanja okolišem osmišljen tako da se ublaži progresivna degradacija zemljišta zbog procesa obalne erozije. Radovi na obrani mora mogu se poduzeti kako bi se zemlja zaštitila od erozije i od zadiranja more i protiv poplava. To uključuje tehnička rješenja kao što su gatovi i nasipi, obalni zidovi, pregrade, utvrde i lukobrani.', 0);
INSERT INTO property VALUES (1,10240, 'hr', 'definition','Vraćanje u prvobitno stanje ekološke kvalitete nekog područja ili lokaliteta.', 0);
INSERT INTO property VALUES (1,10243, 'hr', 'definition','Lokalitet koji je proglašen zaštićenim zbog svojih prirodnih, krajobraznih, umjetničkih ili arheoloških značajki kako bi se osiguralo njegovo očuvanje, održavanje i vraćanje u prvobitno stanje.', 0);
INSERT INTO property VALUES (1,10244, 'hr', 'definition','Područje koje je službeno registrirano zbog njegovih jedinstvenih značajki; navodi se opis o njegovom položaju, veličini, zemljopisnoj širini, zemljopisnoj dužini, orijentaciji, nadmorskoj visini, granicama, biljnom i životinjskom svijetu, hidrološkim obilježjima i obilježjima tla itd.', 0);
INSERT INTO property VALUES (1,10251, 'hr', 'definition','Grafički prikaz gradskog područja ili nekog drugoga gusto naseljenog područja, koji raznim geografskim tehnikama prikazuje položaj skupina ili odabranih vrsta ljudi u svom okruženju.', 0);
INSERT INTO property VALUES (1,10255, 'hr', 'definition','Država, značaj ili ponašanje neke osobe promatrane kao člana ekosustava s popratnim pravima i odgovornostima, a osobito odgovornost za održavanje ekološke cjelovitosti i pravo na postojanje u zdravom okolišu.', 0);
INSERT INTO property VALUES (1,10256, 'hr', 'definition','Jedinstveno, kombinirano i koordinirano upravljanje problemima okoliša koje povezuje relevantne organizacije, skupine, pojedince i discipline objedinjavanjem dijelova kako bi se ostvario cjelovit pristup.', 0);
INSERT INTO property VALUES (1,10260, 'hr', 'definition','Davanje ili obećavanje novca za neki pothvat ili događaj od strane neke osoba, tvrtke, organizacije itd.', 0);
INSERT INTO property VALUES (1,10263, 'hr', 'definition','Formulirana ili sustavna metoda za upravljanje prirodnim ili ekološkim resursima grada ili mjesta.', 0);
INSERT INTO property VALUES (1,10274, 'hr', 'definition','Organiziran, sustavan napor putem raznih medija komunikacije za upoznavanje široke javnosti određenog područja s bilo kojim značajnim ili važnim pitanjem.', 0);
INSERT INTO property VALUES (1,10305, 'hr', 'definition','Ribe koje migriraju u skupini, često između mjesta razmnožavanja i područja zimskog prehranjivanja.', 0);
INSERT INTO property VALUES (1,10381, 'hr', 'definition','Grabežljivi sisavac, uključujući mačke, lavove, leoparde, tigrove, jaguare i geparde, koji obično imaju okrugle glave i  pandže koje se mogu uvlačiti.', 0);
INSERT INTO property VALUES (1,10421, 'hr', 'definition','Sisavci koji su se prilagodili životu u moru, kao što su kitovi, delfini, pliskavice itd.', 0);
INSERT INTO property VALUES (1,10444, 'hr', 'definition','Rogati sisavac preživač koji se uzgaja u mnogim pasminama zbog vune, jestivog mesa ili kože.', 0);
INSERT INTO property VALUES (1,10495, 'hr', 'definition','Pokrov ili zaštita, npr. od vremena ili opasnosti; utočište.', 0);
INSERT INTO property VALUES (1,10496, 'hr', 'definition','Mjesto gdje se ptice okupljaju da snesu jaja.', 0);
INSERT INTO property VALUES (1,10510, 'hr', 'definition','Linijski koridori (ceste, staze i živice) u kojima se ne nalaze unutarnja staništa, ali mogu poslužiti za kretanje organizama. Koridori također mogu biti učinkoviti migratorni putovi za životinje. Prisutnost ili nepostojanje prekida u koridorima može biti vrlo važan čimbenik u određivanju učinkovitosti njihovih  funkcija kao ''provodnika'' i zapreka.', 0);
INSERT INTO property VALUES (1,10511, 'hr', 'definition','Šteta koju okolišu nanesu životinje kao, na primjer, u slučaju pretjerane ispaše, gaženja i sl. Šteta od pretjerane ispaše umanjuje se pravilno smještenim pojilištima kako bi se smanjile dnevne migracije  stoke. Rotacija područja ispaše omogućuje davanje vremena za oporavak trave. Neka zemljišta mogu se lako oporaviti ako se ispaša dopušta samo tijekom jedne sezone. Životinje mogu prouzročiti štetu na usjevima kada poljoprivredno zemljište graniči s djevičanskim teritorijem ili rezervatima za divljač. Osim toga, divlje životinje mogu donijeti bolesti u vrijedna domaća stada. Prekomjerni uzgoj goveda izaziva ozbiljnu degradaciju staništa, pa je stočarstvo u tom smislu u određenoj mjeri kontraproduktivno.', 0);
INSERT INTO property VALUES (1,10512, 'hr', 'definition','Navika mnogih životinjskih vrsta da se kreće unutar svojih staništa da putuju, tijekom migracija, na različite biotope, koje se često nalaze na značajnoj udaljenosti; u vodenim sredinama premještanja se mogu događati vodoravno ili okomito, dok se u kopnenim uvjetima životinjske populacije koje se razmnožavaju u alpskoj ili subalpskoj zoni u ljeto, premještaju na niže razine u zimi; premještanja životinja obično slijede cirkadijurne ritmove, a odnose se na potrebu pronalaženja mjesta za razmnožavanje, odmor i hranilišta.', 0);
INSERT INTO property VALUES (1,10521, 'hr', 'definition','Vodeno područje gdje ribe dolaze svake godine da odlažu jajašca.', 0);
INSERT INTO property VALUES (1,10523, 'hr', 'definition','Mjesto u kojem životinja prirodno raste ili živi. To može biti ili zemljopisno područje preko kojeg se ono proteže, ili određeno uže mjesto na kojem se životinje nalaze.', 0);
INSERT INTO property VALUES (1,10535, 'hr', 'definition','Pravljenje gnijezda za liježenje jaja i uzgoj potomstva.', 0);
INSERT INTO property VALUES (1,10539, 'hr', 'definition','Skupina životinja koja nastava određeno područje.', 0);
INSERT INTO property VALUES (1,10548, 'hr', 'definition','Svaki od različitih procesa, bilo spolni ili nespolni, kojima životinja proizvodi jednu ili više jedinki sličnih sebi.', 0);
INSERT INTO property VALUES (1,10554, 'hr', 'definition','Radnja ili činjenica preživljavanja ili stanje nekoga tko je preživio.', 0);
INSERT INTO property VALUES (1,10562, 'hr', 'definition','Vrsta koja je autohtona za neku geografsku regiju i koja je na nju ograničena.', 0);
INSERT INTO property VALUES (1,10568, 'hr', 'definition','Bjelogorično drvo širokih listova, za razliku od iglica na četinjačama.', 0);
INSERT INTO property VALUES (1,10574, 'hr', 'definition','Zajednice morskog raslinja što ih formiraju zelene, smeđe i crvene makroskopske alge i morske cvjetnice kao što su Posidonia oceanica i Zostera noltii itd.', 0);
INSERT INTO property VALUES (1,10575, 'hr', 'definition','Velika makroskopska biljka. Izraz se upotrebljava osobito za vodene oblike kao što su velike morske trave (razne velike smeđe alge koje su izvor joda i kalijeva karbonata).', 0);
INSERT INTO property VALUES (1,10593, 'hr', 'definition','Biljke koje rastu uz rijeke i potoke.', 0);
INSERT INTO property VALUES (1,10605, 'hr', 'definition','Svako lisnato drvo sjevernih umjereno toplih širina iz porodice bukvi (Fagacea), iz roda Castanea, kao što su Castanea sativa (divlji kesten), koja proizvode cvjetove u dugim resama i plodove u bodljikavim ljuskama.', 0);
INSERT INTO property VALUES (1,10612, 'hr', 'definition','Vrlo velika porodica biljaka koja uključuje žitarice kao što su pšenica, kukuruz itd.', 0);
INSERT INTO property VALUES (1,10617, 'hr', 'definition','Biljka s izduženim, plošnim, zelenim lišćem dužine do 1,5 m. Cvijet se pojavljuje ujesen, ali ne svake godine. Plodovi su tamne kuglice, koje se često nalaze nanesene na plažama, nakon oluje. To isto događa se s lišćem, koje vene i otpada s biljke krajem ljeta. Raste na pješčanim podlogama, i ima podanak iz kojeg raste više biljaka. Kompaktni oblik njegovog rasta zadržava sedimente koje vuku struje po morskom dnu. Neptunova trava formira široke prerije, uvijek u kontinentalnom pojasu. Prisutnost biljke, osim što je ona odličan zaklon i zaliha hrane za mnoge vrste, upućuje na zrelost i dobro stanje cijeloga morskog ekosustava.', 0);
INSERT INTO property VALUES (1,10626, 'hr', 'definition','Segment vegetacije karakteristične za određenu nadmorsku visinu na određenoj zemljopisnoj širini.', 0);
INSERT INTO property VALUES (1,10633, 'hr', 'definition','Broj biljaka na nekom području.', 0);
INSERT INTO property VALUES (1,10851, 'hr', 'definition','Zgrade, oprema i usluge koje se osiguravaju za neku zajednicu.', 0);
INSERT INTO property VALUES (1,10860, 'hr', 'definition','Dio ceste ili posebna staza namijenjena vožnji biciklom.', 0);
INSERT INTO property VALUES (1,10861, 'hr', 'definition','Staza ili kosina za skijanje odnosno skijalište.', 0);
INSERT INTO property VALUES (1,10884, 'hr', 'definition','Utvrđena ili odabrana ruta za putovanja koja se obično sastoji  od sporednih cesta sa značajnim krajobraznim, kulturnim, povijesnim, geološkim ili prirodnim značajkama, uključujući i vidike, odmorišta i poučne staze koje se podudaraju s krajobraznim znamenitostima rute.', 0);
INSERT INTO property VALUES (1,10890, 'hr', 'definition','Privatna, neprofitna ustanova u kojoj se biljke i životinje mogu promatrati u prirodnom vanjskom okruženju.', 0);
INSERT INTO property VALUES (1,10891, 'hr', 'definition','Tradicionalna i uobičajena vjerovanja, praksa i običaji ljudi, koji se prenose s koljena na koljeno kao zajednički način života, često usmenom predajom, kao što su narodne priče, legende, anegdote, izreke, pošalice i drugi oblici komunikacije.', 0);
INSERT INTO property VALUES (1,10895, 'hr', 'definition','Mala kuća ili koliba na selu.', 0);
INSERT INTO property VALUES (1,10898, 'hr', 'definition','Osiguravanje smještaja za odmor ili za boravak u jednoj ili više soba ili u prebivalištu.', 0);
INSERT INTO property VALUES (1,10904, 'hr', 'definition','Zajednica ili narod u cjelini ili dio ili segment zajednice udružen u skupinu zbog nekoga zajedničkog interesa ili aktivnosti.', 0);
INSERT INTO property VALUES (1,10905, 'hr', 'definition','Put ili staza od jednog mjesta do drugoga.', 0);
INSERT INTO property VALUES (1,10906, 'hr', 'definition','Obalni puteljak ili staza.', 0);
INSERT INTO property VALUES (1,10907, 'hr', 'definition','Staza s objašnjenjima ili vodičem, osmišljena tako da objasni djeci neki dio sela, vrstu tla, floru, faunu, itd. Takve staze može biti takva da posjetitelji hodaju sami, tako da se služe   obavijestima s objašnjenjima postavljenima na razmacima ili brojevima označenim pločama koje se odnose na tiskani letak: u drugim slučajevima posjetitelje mogu voditi demonstratori ili vodiči/čuvari/nadzornici.', 0);
INSERT INTO property VALUES (1,10909, 'hr', 'definition','Mjesto kraj mora gdje ljudi provode odmor ili se zabavljaju.', 0);
INSERT INTO property VALUES (1,10910, 'hr', 'definition','Planinsko mjesto gdje ljudi provode odmor ili se zabavljaju.', 0);
INSERT INTO property VALUES (1,10911, 'hr', 'definition','Odmaralište za sportove na otvorenom, na snijegu ili ledu, posebno za skijanje.', 0);
INSERT INTO property VALUES (1,10915, 'hr', 'definition','Vozilo za prijevoz po zemlji konstruirano tako da se može upotrijebiti na bilo kakvoj cesti ili neravnom terenu i može poslužiti za mnoge svrhe, kao što su prijevoz robe, prijevoz ozlijeđenih osoba, prijevoz putnika itd.', 0);
INSERT INTO property VALUES (1,10918, 'hr', 'definition','Broj ljudi u omjeru s prostorom na kojem žive.', 0);
INSERT INTO property VALUES (1,10922, 'hr', 'definition','Broj ljudi koji su na raspolaganju i koji zadovoljavaju uvjete za zapošljavanje u određenom poduzeću, regiji ili državi.', 0);
INSERT INTO property VALUES (1,10925, 'hr', 'definition','Raspodjeljivanje različitog broja sati neke osobe u danu, tjednu ili godini dana za pojedine aktivnosti, osobito onih koji pripadaju kategorije rada i slobodnog vremena.', 0);
INSERT INTO property VALUES (1,10927, 'hr', 'definition','Roba koja ima umjereno dug rok upotrebe i koja se uglavnom ne troši upotrebom: npr. Hladnjak.', 0);
INSERT INTO property VALUES (1,10929, 'hr', 'definition','Roba koju kupe potrošači a koja obično traje kraće od godinu dana. Uobičajeni primjeri su hrana i odjeća. Važno je kod potrošnih dobara da ih potrošači obično kupuju bez obzira na uspone i padove poslovnog ciklusa.', 0);
INSERT INTO property VALUES (1,10930, 'hr', 'definition','Pojam varijabilnog sadržaja i značenja. Može uključivati ​​sve vrste osobnih stvari ili imovine. Artikli robe, zaliha, sirovina ili gotovih proizvoda. Ovaj pojam ne uključuje zemljište.', 0);
INSERT INTO property VALUES (1,10931, 'hr', 'definition','Ukupna gospodarska sredstva, uključujući i fizičke predmete ili predmete koji se mogu pohraniti i nematerijalna djela ljudske pomoći.', 0);
INSERT INTO property VALUES (1,10932, 'hr', 'definition','Određivanje ili planiranje za dodjelu vremena u satima, danima, tjednima itd.', 0);
INSERT INTO property VALUES (1,10933, 'hr', 'definition','Vanjski uvjeti ili okolina u kojoj ljudi žive ili rade.', 0);
INSERT INTO property VALUES (1,10935, 'hr', 'definition','Životni ciklus proizvoda obuhvaća sljedeće faze: nabavu sirovina, proizvodnju, pakiranje, distribuciju, upotrebu, recikliranje i odlaganje.', 0);
INSERT INTO property VALUES (1,10936, 'hr', 'definition','Znak, pečat ili pisana identifikacija pričvršćena ili postavljena na proizvode na kojima se daju određeni ekološki podaci koji omogućuju korisnicima da proizvode usporede s drugim sličnim proizvodima, ili upute o tome kako se sigurno koristiti proizvodom i ispravno ga reciklirati ili odbaciti i proizvod i pakiranje.', 0);
INSERT INTO property VALUES (1,10938, 'hr', 'definition','Formalna tvrdnja u pisanom obliku da je neke roba, usluga ili drugi proizvod stekao priznati i relativno visok stupanj ili razinu izvrsnosti.', 0);
INSERT INTO property VALUES (1,10939, 'hr', 'definition','Veličina stupnja razvoja u zemlji ili zajednici, mjereno  čimbenicima kao što su osobni dohodak, obrazovanje, očekivano trajanje života, potrošnja hrane, zdravstvena skrb, tehnologija i korištenje prirodnih resursa.', 0);
INSERT INTO property VALUES (1,10940, 'hr', 'definition','Odnos između količinu neke robe/proizvoda koja su dostupni za kupnju i želja ili sposobnosti potrošača da tu robu kupe ili kupuju, koji, teoretski, određuje cijenu robe na slobodnom tržištu.', 0);
INSERT INTO property VALUES (1,10959, 'hr', 'definition','Svaka neravnoteža ili razlika među stanovnicima iste životne sredine koja se smatra neprikladnom, nepravednom ili štetnom za cjelovitost tog okoliša.', 0);
INSERT INTO property VALUES (1,10960, 'hr', 'definition','Nejednaka nagrade ili mogućnosti za različite pojedince unutar skupine ili skupina unutar društva. Ako se jednakost ocjenjuje u smislu pravne jednakosti, jednakosti mogućnosti, ili jednakosti ishoda, onda je nejednakost stalna značajka ljudskog stanja.', 0);
INSERT INTO property VALUES (1,10964, 'hr', 'definition','Tradicionalna ili legendarna priča, obično o nadnaravnim bićima, precima, junacima ili događajima, to jest, sa utvrdivim činjenicama ili prirodnim objašnjenjem ili bez njih, ali se upotrebljava za objašnjenje nekog običaja, obreda ili prirodne pojave, ili kako bi se opravdalo postojanje neke socijalnog instituta.', 0);
INSERT INTO property VALUES (1,10968, 'hr', 'definition','Ne u mom dvorištu: izraz koji se upotrebljava za opisivanje ljudi koji potiču uređenje poljoprivrednog zemljišta za gradnju kuća ili tvornica, pod uvjetom da to ne bude u blizini mjesta gdje oni sami žive.', 0);
INSERT INTO property VALUES (1,10971, 'hr', 'definition','Studija o utjecajima na društvene strukture spoznaje i ponašanja, procesa interakcije licem u lice, kao i pregovorima o društvenom poretku.', 0);
INSERT INTO property VALUES (1,10972, 'hr', 'definition','Sustav vrijednosti, ideja i prakse utvrđenih da orijentiraju pojedince u njihovoj zajednici i kulturi te ih uputiti u nazivlje, razvrstavanje i sporazumijevanje.', 0);
INSERT INTO property VALUES (1,10976, 'hr', 'definition','Svijest, senzibilitet i suosjećajano poimanje fizičkog svijeta i njegovih prirodnih ljepota u njihovom neobrađenom stanju.', 0);
INSERT INTO property VALUES (1,10977, 'hr', 'definition','Ekonomska i socijalna struktura zajednica, porezne stope, karakteristične vrste razvoja.', 0);
INSERT INTO property VALUES (1,10980, 'hr', 'definition','Zakonsko pravo na pristup upravnim dokumentima ili mogućnost da se ljudi njima koriste.', 0);
INSERT INTO property VALUES (1,10981, 'hr', 'definition','Svaki formalan i legitiman korak koji poduzme ili odluka koju o pitanjima politike donese glavni ili drugi visokorangirani dužnosnik unutar organizacije.', 0);
INSERT INTO property VALUES (1,11113, 'hr', 'definition','Djelovanje, proces ili posljedica računanja.', 0);
INSERT INTO property VALUES (1,11116, 'hr', 'definition','Masa jediničnog volumena neke tvari.', 0);
INSERT INTO property VALUES (1,11118, 'hr', 'definition','Raspodjela ili skup geografskih opažanja koja predstavljaju vrijednosti ponašanja pojedine pojave ili značajke na mnogim mjestima na površini Zemlje.', 0);
INSERT INTO property VALUES (1,11124, 'hr', 'definition','Popis surogata zapisa poredanih po redoslijedu nekog svojstva koji se može izraziti u obliku koji omogućuje strojno razvrstavanje po redu.', 0);
INSERT INTO property VALUES (1,11125, 'hr', 'definition','Službeno povremeno brojenje stanovništva, uključujući informacije kao što su spol, dob, zanimanje itd.', 0);
INSERT INTO property VALUES (1,11127, 'hr', 'definition','Poredani slijed uzoraka podataka u brojčanom obliku koji se upotrebljava za predviđanje ili pokazivanje nekih kretanja (trendova) u  vremenu i prostoru.', 0);
INSERT INTO property VALUES (1,11128, 'hr', 'definition','Ispitivanje reprezentativnog uzorka velike skupine ljudi o nekim pitanjima u svrhu utvrđivanja općeg mišljenja skupine.', 0);
INSERT INTO property VALUES (1,11130, 'hr', 'definition','Iznos promjene u nekoj količini tijekom vremenskog intervala podijeljen dužinom vremenskog intervala.', 0);
INSERT INTO property VALUES (1,11133, 'hr', 'definition','U vremenskom nizu, onaj dio gibanja koji se dodjeljuje učinku godišnjih doba na neku godinu.', 0);
INSERT INTO property VALUES (1,11134, 'hr', 'definition','Organizirana skupina osoba izabrana ili imenovana za raspravljanje o znanstvenim temama.', 0);
INSERT INTO property VALUES (1,11146, 'hr', 'definition','Istraživanje usmjereno na korištenje znanja stečenog temeljnim istraživanjima kako bi se napravile stvari ili stvorila situacija koja će služiti praktičnoj ili utilitarističkoj svrsi.', 0);
INSERT INTO property VALUES (1,11148, 'hr', 'definition','Sustavno istraživanje u svrhu utvrđivanja činjenica ili načela koja se odnose na određenu znanstvenu temu.', 0);
INSERT INTO property VALUES (1,11152, 'hr', 'definition','Načela i postupci gospodarenja tlom i poboljšanje, upravljanje i proizvodnju poljskih usjeva i biljaka posebne namjene.', 0);
INSERT INTO property VALUES (1,11153, 'hr', 'definition','Ekosustav u kojem prevladava stalna poljoprivredna intervencija čovjeka.', 0);
INSERT INTO property VALUES (1,11157, 'hr', 'definition','Snažna, nagla i destruktivna promjena u okolišu koja ili utječe na obradu zemljišta ili uzgoj usjeva i stoke, ili je oni izazivaju.', 0);
INSERT INTO property VALUES (1,11158, 'hr', 'definition','Podcjenjivanje ili smanjenje vrijednosti poljoprivrednih ili ratarskih proizvoda i usluga.', 0);
INSERT INTO property VALUES (1,11159, 'hr', 'definition','Svaki postupak odnosno djelovanje namijenjeno promicanju gospodarskog rasta, modernizaciji, povećanju poljoprivredne proizvodnje i stvaranju okvira u kojem se zadovoljavaju osnovne potrebe, kao što su obrazovanje, zdravstvo i opskrba vode u ruralnim područjima. Ostvarivanje tih ciljeva ovisi općenito o vrsti administrativnih sustava predloženih za različite programe, kao i o nacionalnoj političkoj situaciji kada se radi, na primjer, o korištenju zemljišta, agrarnoj reformi, isplati potpora i prehrambenoj politici.', 0);
INSERT INTO property VALUES (1,11165, 'hr', 'definition','Znanost i tehnologija uključena u upravljanje vodnim resursima, kontrolu erozije i uklanjanje neželjenih voda.', 0);
INSERT INTO property VALUES (1,11170, 'hr', 'definition','Poljoprivredna djelatnost, umijeće ili vještina.', 0);
INSERT INTO property VALUES (1,11177, 'hr', 'definition','Uzgoj usjeva za prehranu stoke.', 0);
INSERT INTO property VALUES (1,11178, 'hr', 'definition','Djelatnost uzgoja voća i povrća za prodaju.', 0);
INSERT INTO property VALUES (1,11184, 'hr', 'definition','Oplođeni sjemeni zametak koji sadrži embrio iz kojeg poslije klijanja raste nova biljka.', 0);
INSERT INTO property VALUES (1,11186, 'hr', 'definition','Upotreba kemikalija kako bi se izbjegla šteta na usjevima zbog insekata ili korova.', 0);
INSERT INTO property VALUES (1,11187, 'hr', 'definition','Uklanjanje teških metala iz vode korištenjem biljaka ili obradom kojom biljni organizmi djeluju tako da dolazi do raspadanja opasnih organskih onečišćivača ili preobrazbe opasnih anorganskih kontaminanata u ekološki sigurne razine u tlima, podzemnim materijalima, vodi, muljevima i talozima.', 0);
INSERT INTO property VALUES (1,11192, 'hr', 'definition','Uzgoj, držanje i skrb za ptice.', 0);
INSERT INTO property VALUES (1,11198, 'hr', 'definition','Izraz koji se odnosi na sustave koji se upotrebljavaju u uzgoju životinja (ekstenzivnom i intenzivnom.', 0);
INSERT INTO property VALUES (1,11199, 'hr', 'definition','Sezonska migracija stoke na pogodnu ispašu.', 0);
INSERT INTO property VALUES (1,11201, 'hr', 'definition','Sve anorganske tvari koje se pojavljuju u prirodi s određenim kemijskim sastavom i obično kristalne strukture, kao što su stijene, koje se upotrebljavaju za stabilizaciju tla, poboljšanje njegove otpornosti na eroziju, teksture i propusnost.', 0);
INSERT INTO property VALUES (1,11204, 'hr', 'definition','Uklanjanje vode iz močvarnog područja umjetnim sredstvima, npr. uvođenjem odvoda.', 0);
INSERT INTO property VALUES (1,11206, 'hr', 'definition','Tradicionalni sustav poljoprivrednog uzgoja koji su upotrebljavali naraštaji poljoprivrednika u tropskim šumama i savanama na sjeveru i istoku Afrike. Poznato je da je to ekološki razborit oblik uzgoja, i budući da je tlo u tropskim prašumama loše, to je održiv način uzgoja. Danas se takav uzgoj još uvijek prakticira, ponajprije u zemljama u razvoju. Mala područja grmlja ili šume se krče i manje drveće se spaljuje. Time se oslobađaju hranjive tvari u vegetaciji i zemlja dobiva gnojivo koje biljke lako unose u sebe. Nekoliko godina kasnije tlo degradira, a poljoprivrednik se seli i radi to isto na drugom mjestu. Izvorna tlo ostavlja se na ugaru čak i do 20 godina, tako da se šuma može regenerirati. Uz porast broja stanovnika i naknadne potrebe za više poljoprivrednog zemljišta za proizvodnju hrane, ova se metoda sve više upotrebljava i danas kako bi se iskrčile velike površine tropskih šuma za uzgoj stoke na rančevima, te u većini slučajeva tlo ne ostane neobrađeno dovoljno dugo i, zbog modernih mehaniziranih sustava obrade tla, nema dovoljno panjeva ili pogodnih staništa za biljni svijet da bi se pokrenuo proces regeneracije.', 0);
INSERT INTO property VALUES (1,11207, 'hr', 'definition','Mekan, čist, zemljani, s finom teksturom, obično bijel do svijetlo sivi ili svijetložut, vapnenac morskog podrijetla, koji se gotovo u cijelosti (90-99%) sastoji od kalcita, formiran uglavnom taloženjem u plitkim vodama vapnenačkih lupinica plutajućih mikroorganizama (uglavnom foraminifera) i izdrobljenih ostataka vapnenačkih algi (kao što su kokoliti i rabdoliti), smješten u amorfnu matrica vrlo fino kristalnog kalcita. Stijena je porozna, pomalo drobiva, i slabo je povezana. Može uključivati ostatke oblika koji žive na dnu (npr. Amonita, ehinoderma i pelecipoda) i zrna kremena i pirita. Najpoznatije i najraširenije krede su iz istoimenog doba, kao što su one koje se mogu vidjeti u liticama s obje strane Engleskog kanala.', 0);
INSERT INTO property VALUES (1,11209, 'hr', 'definition','Gnojiva, prirodna ili umjetna, koja sadrže dušik za fiksaciju zelenilom, kao što su kalijev nitrat ili amonijev nitrat.', 0);
INSERT INTO property VALUES (1,11211, 'hr', 'definition','Spoj ili smjesa gnojiva koja sadrži raspoloživi (topivi) fosfat; primjeri su fosfatne stijene (fosforit), superfosfati ili trostruki superfosfati, nitrofosfati, kalijev fosfat ili NPK smjese.', 0);
INSERT INTO property VALUES (1,11212, 'hr', 'definition','Kemijsko gnojivo koja sadrži kalij. Kalij (K) potreban je za sav biljni i životinjski svijet. Biljkama je kalij potreban za fotosintezu, osmotsku regulaciju i aktivaciju sustava enzima.', 0);
INSERT INTO property VALUES (1,11214, 'hr', 'definition','Čin ili proces u kojem se dio zemlje oslobađa onečišćenja ili bilo koje vrste kontaminacije, često prirodnim procesima.', 0);
INSERT INTO property VALUES (1,11224, 'hr', 'definition','Uklanjanje vode ili bilo kojih topivih sastojaka iz tla. Ispiranje se često događa kod sastojaka zemlje kao što su nitratna gnojiva, a rezultat je da nitrati završe u pitkoj vodi.', 0);
INSERT INTO property VALUES (1,11226, 'hr', 'definition','Anorganski materijali koji imaju različit kemijski sastav, karakterističnu kristalnu strukturu, boju i tvrdoću.', 0);
INSERT INTO property VALUES (1,11227, 'hr', 'definition','Biljni i životinjski ostaci koji se razgrađuju i postaju dio tla.', 0);
INSERT INTO property VALUES (1,11231, 'hr', 'definition','Površinski potok ili površinska akumulacija, zajedno sa svim drugim takvim potocima i vodnim tijelima koja su njihove pritoke i pomoću kojih se regija odvodnjava. Umjetni sustav odvodnje uključuje i površinske i podzemne vodove.', 0);
INSERT INTO property VALUES (1,11232, 'hr', 'definition','Sustav umjetnih kanala koje je stvorio čovjek za dovođenje vode do zemlje kako bi se omogućio rast biljaka.', 0);
INSERT INTO property VALUES (1,11233, 'hr', 'definition','Mjerenje količine mineralne soli koja se nalazi u tlu. Mnoga polu-sušna i sušna područja prirodno su slana. Po definiciji to su područja znatnog nedostatka vode, gdje je evapotranspiracija veća od  oborina. Tako, dok u vlažnim područjima ima dovoljno vode koja se procjeđuje kroz tlo i ispire topive materijale iz tla i stijena u rijeke, a time i u more, u pustinjama to nije slučaj. Zbog toga soli imaju tendenciju da se nagomilavaju.', 0);
INSERT INTO property VALUES (1,11234, 'hr', 'definition','Novčana ili materijalna vrijednost po kojoj se kupci i prodavači dogovore da posluju s poljoprivrednom robom i uslugama.', 0);
INSERT INTO property VALUES (1,11243, 'hr', 'definition','Aktivnost ili proces nadzora i pripremanja za budući fizički raspored i stanje bilo kojeg poljoprivrednog ili pastoralnog područja, koji može uključivati ​​zaštitu i razvoj prirodnih i ljudskih resursa koji utječu na gospodarsku vitalnost nekog područja.', 0);
INSERT INTO property VALUES (1,11244, 'hr', 'definition','Uprava, promidžba, organizacija i planiranje za djelatnost ili industrija pružanja informacija, prijevoza, zabave, smještaja i ostale usluge za putnike ili posjetitelje.', 0);
INSERT INTO property VALUES (1,11246, 'hr', 'definition','Svako tijelo (subjekt) ili skupina pojedinaca koji su uključeni u stvaranje, upravljanje i očuvanje opsežnoga šumskog područja, često za proizvodnju proizvoda i pogodnosti kao što su drvo, čista voda, biološka raznolikost i rekreacija.', 0);
INSERT INTO property VALUES (1,11248, 'hr', 'definition','Formulirana ili sustavna metoda za opskrbu materijalnim potrepštinama kao što su alat, oprema, pribor ili proizvodi za unutarnje opremanje.', 0);
INSERT INTO property VALUES (1,11259, 'hr', 'definition','Način ili sredstva ulaska ili pristupa.', 0);
INSERT INTO property VALUES (1,11269, 'hr', 'definition','Mjesto uz put ili liniju na kojoj se vlak zaustavlja kako bi ukrcao ili iskrcao putnike ili robu, osobito s pomoćnim zgradama i službama.', 0);
INSERT INTO property VALUES (1,11270, 'hr', 'definition','Mjesto uz put ili liniju na kojoj se autobus zaustavlja da uzme gorivo ili kako bi ukrcao ili iskrcao putnike ili robu, osobito s pomoćnim zgradama i službama.', 0);
INSERT INTO property VALUES (1,11271, 'hr', 'definition','Ukupne sastavnice koje se upotrebljavaju za izgradnju ulica, autocesta i drugih putova, kao što su asfalt, beton, opeka, pijesak i šljunak.', 0);
INSERT INTO property VALUES (1,11276, 'hr', 'definition','Ukupan sustav raspodjele željezničkih infrastrukturnih kapaciteta u nekoj zemlji.', 0);
INSERT INTO property VALUES (1,11277, 'hr', 'definition','Sustav cesta kroz neku zemlju.', 0);
INSERT INTO property VALUES (1,11289, 'hr', 'definition','Znanost ili umijeće vođenja brodova ili zrakoplova s jednog mjesta na drugo, a osobito metoda određivanja položaja, smjera i proputovane udaljenosti preko površine Zemlje prema načelima geometrije i astronomije te s pomoću uređaja (kao što je radarski far ili instrumenti) s namjenom da posluže kao pomoćna oprema.', 0);
INSERT INTO property VALUES (1,11294, 'hr', 'definition','Prijevoz osoba i robe pomoću brodova koji plove duž fiksnih pravaca plovidbe.', 0);
INSERT INTO property VALUES (1,11296, 'hr', 'definition','Prijevoz u kojem se upotrebljava više od jednog načina prijevoza, tj. cestovni, željeznički i pomorski.', 0);
INSERT INTO property VALUES (1,11307, 'hr', 'definition','Skupina ljudi koji žive u gradu/mjestu.', 0);
INSERT INTO property VALUES (1,11308, 'hr', 'definition','Tiskani ili pisani tekst s prijedlozima ili postupcima za poboljšanje životnih uvjeta, posebno stambenih, za stanovnike grada ili gusto naseljenog područja.', 0);
INSERT INTO property VALUES (1,11309, 'hr', 'definition','Svaki širi prostor zemlje ili regije smješten na rubnim dijelovima grada ili mjesta.', 0);
INSERT INTO property VALUES (1,11312, 'hr', 'definition','Samostojeća stambena jedinica u kojoj živi odrasla osoba i još jedna ili više osoba u rodbinskim odnosima s njom.', 0);
INSERT INTO property VALUES (1,11321, 'hr', 'definition','Ključni element sveobuhvatnog plana; opisuje preporučeno mjesto i intenzitet razvoja za javno i privatno korištenje zemljišta, kao što su stambene, poslovne, industrijske, rekreacijske i poljoprivredne.', 0);
INSERT INTO property VALUES (1,11322, 'hr', 'definition','Aktivnost ili postupak pripremanja za buduću organizaciju i stanje urbanog središta, a osobito razvoj njegovoga fizičkog izgleda, koji će uključivati izgradnju, rekonstrukciju, prenamjenu, izmjenu ili proširenje zgrada i drugih građevina, te proširenje i upotrebu neizgrađenog zemljišta.', 0);
INSERT INTO property VALUES (1,11323, 'hr', 'definition','Odnos između vlasnika zemlje i poljoprivrednika zakupca koji obrađuje zemlju.', 0);
INSERT INTO property VALUES (1,11332, 'hr', 'definition','Svako od nekoliko nedavnih urbanih naselja koja su mali i u osnovi samodostatni gradovi s planiranim redoslijedom stambene, industrijske i komercijalne izgradnje.', 0);
INSERT INTO property VALUES (1,11333, 'hr', 'definition','Područja koja su predmet prava prvokupa koje je povlastica koju odobrava vlada stvarnom naseljenika na određeni ograničeni dio javnog dobra, da kupi takav pojas zemlje po fiksnoj cijeni uz isključivanje svih drugih podnositelja zahtjeva.', 0);
INSERT INTO property VALUES (1,11334, 'hr', 'definition','Svaki zemljišni prostor koji zahtijeva da osoba ili agencija odnosno ured s ovlastima intervenira ili da se miješa u način njegovog korištenje ili upravljanja.', 0);
INSERT INTO property VALUES (1,11355, 'hr', 'definition','Rušenje zgrada mehaničkim sredstvima.', 0);
INSERT INTO property VALUES (1,11359, 'hr', 'definition','Grana inženjeringa koja se bavi oblikom i funkcijama urbanih područja.', 0);
INSERT INTO property VALUES (1,11375, 'hr', 'definition','Ponovna uspostava točnog oblika i detalja zgrade, njezinih artefakata, te lokacije na kojoj se ona nalazi, najčešće u obliku kako je ta zgrada izgledala u određeno vrijeme.', 0);
INSERT INTO property VALUES (1,11427, 'hr', 'definition','Izjava dana u propisanom obliku pred mirovnim sucem, javnim bilježnikom ili drugom osobom  koja je ovlaštena zabilježiti prisegu.', 0);
INSERT INTO property VALUES (1,11430, 'hr', 'definition','Standard odnosno norma odobrena na temelju propisa normativnih tijela s kojima je Zajednica sklopila sporazume.', 0);
INSERT INTO property VALUES (1,11431, 'hr', 'definition','Dokumentirani sporazumi koji sadrže tehničke specifikacije ili druge precizne kriterije koji se trebaju dosljedno primjenjivati u obliku pravila, smjernica ili definicija značajki, kako bi se osiguralo da materijali, proizvodi, procesi i usluge odgovaraju njihovoj namjeni.', 0);
INSERT INTO property VALUES (1,11433, 'hr', 'definition','Pravilo koje je propisala vlada ili uprava i u kojem se utvrđuju detaljni ili strogi zahtjevi, bilo izravno ili upućivanjem na ili uključivanjem sadržaja standarda, tehničkih specifikacija ili pravila struke.', 0);
INSERT INTO property VALUES (1,11466, 'hr', 'definition','Proces prikupljanja i sustavno uređivanja, obično po predmetu, zakona države ili zemlje, ili pravila i propisa koji obuhvaćaju određeno područje ili predmet zakona ili prakse.', 0);
INSERT INTO property VALUES (1,11468, 'hr', 'definition','Pravilo, načelo, teorija ili postavka prava, kao doktrina spajanja, doktrina odnosa itd.', 0);
INSERT INTO property VALUES (1,11472, 'hr', 'definition','Pravila o kretanju robe ili ljudi morem, željeznicom ili cestom.', 0);
INSERT INTO property VALUES (1,11476, 'hr', 'definition','Obvezujuće pravilo ili skup pravila koje propisuje vlada, a odnosi se na pitanja od značaja za stanovnike rijetko naseljenih regija, posebno poljoprivrednih i drugih gospodarskih pitanja.', 0);
INSERT INTO property VALUES (1,11477, 'hr', 'definition','Opravdani zahtjevi, zakonska jamstva ili moralna načela odobrena za žive vrste (koje osjećaju) koje nisu ljudi, uključujući slobodu od zlostavljanja, potrošnje, obavljanja pokusa, korištenja kao odjeća ili zabavljanja ljudi.', 0);
INSERT INTO property VALUES (1,11478, 'hr', 'definition','Moralni, pravni ili etički zahtjevi potomstva prema sadašnjim ljudima, na temelju spoznaje da su mladi i nerođeni ranjivi prema donošenju odluka u sadašnjosti, posebice odluka koje imaju dugoročni utjecaj na društva i okoliš koji će oni naslijediti.', 0);
INSERT INTO property VALUES (1,11479, 'hr', 'definition','Priznata i zakonom zaštićena prava koja se odnose na pripadnike neke države.', 0);
INSERT INTO property VALUES (1,11489, 'hr', 'definition','Činjenične informacije, obavijest ili pisano upozorenje koje nekoj osobi prenese ovlašteni izvor, a koje se često prenosi zbog pravnih ili administrativnih pravila kojima se zahtijeva prijenos takvih informacija svim zainteresiranim stranama.', 0);
INSERT INTO property VALUES (1,11490, 'hr', 'definition','Memorandum, pismo ili obavijest, bilo u papirnatom ili elektroničkom obliku, koje se šalje svima u cijeloj organizaciji ili općem popisu zainteresiranih strana.', 0);
INSERT INTO property VALUES (1,11494, 'hr', 'definition','1) Usmjerenje ili naredba suda. U tom smislu često se upotrebljava kao sinonim za presudu.
INSERT INTO property VALUES (1,11497, 'hr', 'definition','Naputci o teoriji i praksi upravljanja uredom, poduzećem ili organizacijom.', 0);
INSERT INTO property VALUES (1,11498, 'hr', 'definition','Izobrazba, poučavanje ili disciplina koji se odnose na ili su povezani s mehaničkim ili industrijskim umijećima i primijenjenim znanostima.', 0);
INSERT INTO property VALUES (1,11499, 'hr', 'definition','Skup pravila o djelovanju ili ponašanju koje propisuje kontrolno tijelo i koje ima obvezujuću pravnu snagu.', 0);
INSERT INTO property VALUES (1,11502, 'hr', 'definition','Službena dozvola odobrena pojedincima ili komercijalnim poduzećima koja omogućuje i regulira s obzirom na vrijeme, mjesto, vrstu, veličinu ili broj divljih životinje ili divljači koja se može ubiti i odnijeti sa zemljišta unutar određene nadležnosti.', 0);
INSERT INTO property VALUES (1,11504, 'hr', 'definition','Ovlaštenje koje je potrebno dobiti od lokalnih tijela vlasti za izgradnju zatvorene strukture ili za veliku promjenu ili proširenje postojeće građevine.', 0);
INSERT INTO property VALUES (1,11506, 'hr', 'definition','Službena dozvola odobrena pojedincima ili komercijalnim poduzećima koja omogućuje i regulira s obzirom na vrijeme, mjesto, vrstu, veličinu ili količinu ribe koja se smije uhvatiti iz rijeka, jezera ili oceanskih voda unutar određene nadležnosti.', 0);
INSERT INTO property VALUES (1,11508, 'hr', 'definition','Oblik u kojem se predloženi zakoni, rezolucije ili posebni zakoni predstavljaju zakonodavnom tijelu prije nego što stupe na snagu odnosno usvoje.', 0);
INSERT INTO property VALUES (1,11514, 'hr', 'definition','Čin kojim se nešto uređuje; pravilo ili nalog propisan za upravljanje ili vladanje; načelo kojim se nešto uređuje; naredba. Proceduralno pravilo koje propisuje nadređeno ili nadležno tijelo koje se odnosi na djelovanje onih koji su pod njezinom kontrolom.', 0);
INSERT INTO property VALUES (1,11521, 'hr', 'definition','Svaki popust, smanjenje, dobrovoljno davanje ili udovoljavanje zahtjevu ili traženju, obično od strane vlade ili kontrolnog tijela, nekom pojedincu ili organizaciji.', 0);
INSERT INTO property VALUES (1,11522, 'hr', 'definition','Razmatranje, argumentiran komentar ili neformalna rasprava o nekom pitanju koje je otvoreno i od interesa za opću populaciju.', 0);
INSERT INTO property VALUES (1,11523, 'hr', 'definition','Upravni akt kojim se daje pravo na uzimanje privatne imovine za javnu upotrebu.', 0);
INSERT INTO property VALUES (1,11524, 'hr', 'definition','Općenito, zemlja i vode u vlasništvu pojedinaca ili korporacija za razliku od onih u vlasništvu države; u francuskom građanskom zakonu, svaka državna imovina koja može biti u vlasništvu ne-javnih subjekata, koja se ne može oduzeti i koja je ograničena na zakonski utvrđeno vlasništvo i uporabu.', 0);
INSERT INTO property VALUES (1,11525, 'hr', 'definition','Rijeke, kanali i jezera u vlasništvu države, za razliku od onih u vlasništvu pojedinaca ili korporacija.', 0);
INSERT INTO property VALUES (1,11526, 'hr', 'definition','Morska ili oceanska područja u vlasništvu države, za razliku od onih u vlasništvu pojedinaca ili korporacija.', 0);
INSERT INTO property VALUES (1,11530, 'hr', 'definition','Istraga, osobito formalna koja se provodi u vezi s nekim  javnim komunalnim tijelom od strane tijela što ga je za tu svrhu imenovala vlada, lokalna vlast ili neka druga organizacija.', 0);
INSERT INTO property VALUES (1,11531, 'hr', 'definition','Istraga, osobito formalna koja se provodi u vezi s nekim  javnim komunalnim tijelom od strane tijela što ga je za tu svrhu imenovala vlada, lokalna vlast ili neka druga organizacija.', 0);
INSERT INTO property VALUES (1,11532, 'hr', 'definition','Proces dodjeljivanja ili prenošenja ovlasti, odlučivanja ili posebne administrativne funkcije s jednog tijela na drugo.', 0);
INSERT INTO property VALUES (1,11535, 'hr', 'definition','Moć ili ovlast vlade da regulira ili izdaje naredbe industrijskim subjektima, organizacijama, programima, inicijativama i pojedincima.', 0);
INSERT INTO property VALUES (1,11536, 'hr', 'definition','Pravo korištenja tuđe imovine; teret nad zemljištem zbog kojega netko drugi osim vlasnika ima pravo pristupa.', 0);
INSERT INTO property VALUES (1,11540, 'hr', 'definition','Svaki počinjeni čin kojim se krše dužnosti koje pojedinac ima prema zajednici, te za čije kršenje zakon predviđa da počinitelj mora dati zadovoljštinu javnosti.', 0);
INSERT INTO property VALUES (1,11549, 'hr', 'definition','Zakonske odredbe kojima se uređuje postupak kojim se kaznena djela istražuju, gone, u vezi s kojima se održava suđenje i kojim se ona kažnjavaju.', 0);
INSERT INTO property VALUES (1,11556, 'hr', 'definition','Službena i autentična odluka suda o pravima i zahtjevima stranaka u vezi s nekim sudskim postupkom ili tužbom o kojoj se onda provodi parnica i o kojoj se donosi odluka. Konačna odluka suda kojom se rješava spor i utvrđuju prava i obveze stranaka. Posljednja riječ zakona u sudskom sporu, koja je konačna odluka suda o pravima stranaka o pitanjima podnesenima sudu u parnici ili sudskom postupku.', 0);
INSERT INTO property VALUES (1,11559, 'hr', 'definition','Stanje neslaganja ili suprotnosti između ideja, interesa i sl.', 0);
INSERT INTO property VALUES (1,11563, 'hr', 'definition','Sudski spor, sudski prijepor, parnica.', 0);
INSERT INTO property VALUES (1,11568, 'hr', 'definition','Nezavisni, specijalizirani sudski tribunal u kojem suci ili službene osobe koje je ovlastila vladina agencije provode rasprave i donose odluke u postupcima između vladine agencije i osoba, poduzeća ili druge organizacije za koje je on nadležan.', 0);
INSERT INTO property VALUES (1,11571, 'hr', 'definition','Institucija osnovana Rimskim ugovorom kako bi se osiguralo da će se u tumačenju i primjeni Ugovora poštovati zakon. Sastoji se od sudaca iz svake države članice, imenovanih na 6-godišnja razdoblja, uz pomoć tri pravna savjetnika EU (''Advocates General''). Sjedište mu je u Luksemburgu, i on daje svoje odluke u presudama kada ga se na to pozove u postupcima koje pokrenu države članice, institucije EZ i fizičke ili pravne osobe. Postupci su uglavnom istražni.', 0);
INSERT INTO property VALUES (1,11583, 'hr', 'definition','Pravilna primjena zakona za razliku od proizvoljne.', 0);
INSERT INTO property VALUES (1,11587, 'hr', 'definition','Sudsko ispitivanje i odlučivanje o pitanjima između stranaka u parnici, bez obzira na to radi li se o pravnim ili činjeničnim pitanjima. Sudsko ispitivanje, u skladu sa zakonom zemlje, o uzroku, bilo građanskom ili kaznenom, pitanja između stranaka, bilo pravne ili činjenične prirode, pred sudom s odgovarajućom nadležnošću.', 0);
INSERT INTO property VALUES (1,11594, 'hr', 'definition','Organ vlasti koji pripada pravosuđu čija je funkcija primjena zakona na prijepore koje mu se podnesu i javno provođenje pravde. Prisutnost dovoljnog broja članova takvog tijela koji se redovito sazivaju na ovlaštenom mjestu u određeno vrijeme, koji se bave potpunim i redovitim obavljanjem svojih funkcija. Tijelo u vladi kojem su prenesene ovlasti izvršavanja pravde. Tijelo ustrojeno u svrhu izvršavanja pravde, koje uključuje i suce i porotu. Politički entitet bez ustrojenog tijela sastavljen od jednog ili više sudaca, koji zasjedaju u određeno vrijeme i na određenim mjestima, uz prisutnost  odgovarajućih službenika, u skladu sa zakonskim ovlastima za izvršenje pravde. Organizirano tijelo s definiranim ovlastima, koje se sastaje u određeno vrijeme i na mjestima za rasprave i donošenje odluka o uzrocima i drugim pitanjima koja mu se podnose, i u čemu ima pomoć, u svom pravilnom postupanju, svojih odgovarajućih službenika, odvjetnika i pravnih savjetnika da izlažu i vode poslove, činovnika koji bilježe i ovjeravaju njegove akte i odluke, te ministarskih službenika da izvršavaju  njegove naredbe, te da osiguraju red u svom postupanju.', 0);
INSERT INTO property VALUES (1,11611, 'hr', 'definition','Svaki sporazum iz kojeg proizlazi odnos stanodavca i stanara (nekretnina) ili zakupodavca i najmoprimca (nekretnina ili osobne imovine). Ugovor za isključivo posjedovanje zemljišta ili zakupljenog imanja/kuće za izdavanje za određeno vremensko razdoblje. Ugovor za posjedovanje i dobit od zemlje i zakupljenog imanja/kuće za izdavanje bilo za život, ili za stanovito vremensko razdoblje, ili dok to strankama odgovara.', 0);
INSERT INTO property VALUES (1,11612, 'hr', 'definition','Formalna pisana potvrda o nekoj činjenici.', 0);
INSERT INTO property VALUES (1,11618, 'hr', 'definition','Odobrenje koje daje sudac za određena djela i dogovore kako bi oni postali više obvezujući i izvršni.', 0);
INSERT INTO property VALUES (1,11621, 'hr', 'definition','Informacija o nekoj činjenici, koju stvarno prenese nekoj osobi ovlaštena osoba.', 0);
INSERT INTO property VALUES (1,11623, 'hr', 'definition','Pravo prvog odbijanja pri kupnji zemljišta u slučaju da ga nositelj (davatelj) prava odluči prodati.', 0);
INSERT INTO property VALUES (1,11624, 'hr', 'definition','Stjecanje osobnog prava na korištenje puta, vode, svjetla i zraka zbog kontinuiranog korištenja. Dosjelost je neopoziva i trajna za svaku vrstu parnice, stvarne ili osobne, kada je vjerovnik neko vrijeme šutio bez postavljanja svog zahtjeva.', 0);
INSERT INTO property VALUES (1,11627, 'hr', 'definition','Čin, kao što je čin nositelja ovlasti ili vlasti, uhićenja ili zabranjivanje prenošenja ideja ili činjenica izraženih kroz djelovanje, pokret, publikaciju ili dokaz u sudskom postupku.', 0);
INSERT INTO property VALUES (1,11632, 'hr', 'definition','Čin dodjeljivanja ili povjeravanja ovlasti, ovlaštenja ili funkcija drugome u svojstvu zamjenika ili zastupnika, obično  podređenoj osobi u administrativnoj strukture organizacije ili institucije.', 0);
INSERT INTO property VALUES (1,11633, 'hr', 'definition','Kontinuirano kretanje prema naprijed ili širenje onečišćujućih tvari, odnosno stopa ili količina u kojoj se onečišćujuće tvari  kreću od jednog mjesta na drugo.', 0);
INSERT INTO property VALUES (1,11635, 'hr', 'definition','More i atmosfera su fluidi i u međusobnom su dodiru, ali u različitim stanjima energije - tekućem i plinovitom. Granica slobodne površine između njih inhibira, ali nipošto potpuno ne sprječava, njihovu uzajamnu razmjenu tvari i energije. Gotovo sve razmjene preko te granice odvijaju se najučinkovitije u turbulentnim uvjetima.  Uzburkana morska površina, velike razlike u svojstvima i vode i zraka, ili nestabilan stupac zraka olakšava prijenos količina zraka s morske površine visoko u atmosferu. I toplina i voda (para) sklone su migriranju preko granice u smjeru od mora prema zraku. Toplina se razmjenjuje trima procesima: zračenjem, provođenjem i isparavanjem. Najveća neto razmjena odvija se putem isparavanja, procesom prijenosa vode iz mora u zrak isparavanjem vode.', 0);
INSERT INTO property VALUES (1,11636, 'hr', 'definition','Nekontrolirano gibanje, prouzročeno procjeđivanjem ili drugim procesima, tekuće ili plinovite onečišćujuće tvari iz prvotnog izvora u druge dijelove ekosustava.', 0);
INSERT INTO property VALUES (1,11638, 'hr', 'definition','Onečišćenje izazvano izljevom nafte, neželjenog ispuštanja radioaktivnih tvari, imisijom u vodna tijela ili atmosferu kemijskih tvari koje potječu iz industrijskih aktivnosti.', 0);
INSERT INTO property VALUES (1,11639, 'hr', 'definition','Onečišćenje vode, tla i zraka patogenim bakterijama.', 0);
INSERT INTO property VALUES (1,11641, 'hr', 'definition','Onečišćenja iz široke lepeze aktivnosti bez jednog određenog izvora, npr. Kisele kiše, pesticidi, otjecanje otpadnih voda u gradovima itd.', 0);
INSERT INTO property VALUES (1,11643, 'hr', 'definition','Onečišćenja koja potječu iz svih vrsta rudarskih operacija i koja imaju nepovoljan učinak na vodeni svijet, opskrbu vodom i rekreacijsko korištenje voda.', 0);
INSERT INTO property VALUES (1,11644, 'hr', 'definition','Onečišćenje životinjskim ili biljnim materijalom nastalim u živim i mrtve organizmima koje može sadržavati patogene bakterije i negativno utječe na okoliš.', 0);
INSERT INTO property VALUES (1,11645, 'hr', 'definition','Onečišćenje zraka koje sadrži ozon i druge reaktivne kemijske spojeve nastale djelovanjem Sunčeve svjetlosti na dušikove okside i ugljikovodike, osobito one u ispušnim cijevima  automobila.', 0);
INSERT INTO property VALUES (1,11647, 'hr', 'definition','Onečišćenje otrovnim tvarima koje imaju štetan utjecaj na žive organizme fizičkim dodirom, gutanjem ili udisanjem.', 0);
INSERT INTO property VALUES (1,11648, 'hr', 'definition','Ispuštanje krutog, tekućeg ili plinovitog otpada u rijeku.', 0);
INSERT INTO property VALUES (1,11649, 'hr', 'definition','Ispuštanje, odlaganje ili emisija otpada ispod površine zemlje.', 0);
INSERT INTO property VALUES (1,11661, 'hr', 'definition','Mjere poduzete unaprijed, kako bi se spriječila pojava katastrofa ili sličnih izvanrednih situacija.', 0);
INSERT INTO property VALUES (1,11662, 'hr', 'definition','Svaka mjera predostrožnosti, postupak ili uređaj osmišljen ili poduzet kako bi se osobe, imovina ili okoliša zaštitili ili obranili od štete.', 0);
INSERT INTO property VALUES (1,11671, 'hr', 'definition','Visok stupanj ionizacije kiseline u vodenoj otopini.', 0);
INSERT INTO property VALUES (1,11672, 'hr', 'definition','Normalan metabolit koji, kada je prisutan u abnormalnim koncentracijama i određenim tjelesnim tekućinama, može upućivati na prisutnost određene bolesti ili toksikološkog stanja.', 0);
INSERT INTO property VALUES (1,11675, 'hr', 'definition','Prisutnost u okolišu živih organizama ili agensa koji potječu od virusa, bakterija, gljivice, te antigena sisavaca i ptica koji mogu prouzročiti mnoge učinke na zdravlje.', 0);
INSERT INTO property VALUES (1,11676, 'hr', 'definition','Dodavanje kemikalija ili prisutnost kemikalija u drugim tvarima u takvoj mjeri da ona postane neprikladnim za namjeravanu svrhu. Također se odnosi na rezultat(e) takvog dodavanja ili prisutnosti.', 0);
INSERT INTO property VALUES (1,11678, 'hr', 'definition','Učinci onečišćenja na žive organizme.', 0);
INSERT INTO property VALUES (1,11686, 'hr', 'definition','Svojstvo nekog proces koje sprječava da ponovno nastupi prethodno stanje.', 0);
INSERT INTO property VALUES (1,11691, 'hr', 'definition','Svaki bliži ili dalji cilj utvrđen za neki proizvod, uslugu ili napor usmjeren ostvarivanju relativno visokog stupnja ili razine izvrsnosti.', 0);
INSERT INTO property VALUES (1,11692, 'hr', 'definition','Svaka sićušna ili vrlo mala masa materijala koja ima konačan volumen i oblik i odupire se silama koje nastoje promijeniti njezin volumen ili oblik.', 0);
INSERT INTO property VALUES (1,11693, 'hr', 'definition','Regenerativna sposobnost sustava, tala, vode itd.', 0);
INSERT INTO property VALUES (1,11700, 'hr', 'definition','Generički naziv za uređaj koji osjeća bilo apsolutnu vrijednost ili promjenu u fizičkoj kvantiteti kao što je temperatura, tlak, brzina protoka ili pH, odnosno intenzitet svjetlosti, zvuka, ili omjer valova,  i pretvara tu promjenu u koristan ulazni signal za sustav prikupljanja informacija.', 0);
INSERT INTO property VALUES (1,11701, 'hr', 'definition','Projektiranje, proizvodnja i korištenje fizičkih instrumenata ili sustava instrumenata za otkrivanje, promatranje, mjerenje, automatsku kontrolu, automatsko računanje, komunikaciju ili obradu podataka.', 0);
INSERT INTO property VALUES (1,11702, 'hr', 'definition','Znanost o mjerenju', 0);
INSERT INTO property VALUES (1,11706, 'hr', 'definition','Umjetno stvoreni uređaj koji kruži u orbiti oko Zemlje, prima, obrađuje i prenosi signale i stvara slike kao što su slike vremenskih uvjeta.', 0);
INSERT INTO property VALUES (1,11713, 'hr', 'definition','Herbicid koji pripada skupini triazina, koji su u širokoj upotrebi, osobito za kukuruzne usjeve. Visoko je toksičan za slatkovodne alge i fitoplankton i, budući da je vrlo topiv u vodi, lako onečišćuje vodonosnike.', 0);
INSERT INTO property VALUES (1,11714, 'hr', 'definition','Osnovna hranjiva tvar u opskrbi hranom biljaka i u životinjskoj ishrani. Životinje ga dobivaju iz spojeva koji sadržavaju dušik, posebno aminokiselina. Iako plinoviti dušik čini gotovo 80% sastava atmosfere, vrlo malo organizama ima sposobnost da ga iskoristi u ovom obliku. Više biljke obično ga dobivaju iz  tla nakon što mikroorganizmi pretvore dušik u amonijak ili nitrate, koji se zatim mogu apsorbirati. Ova pretvorba dušika, poznata pod nazivom fiksacija dušika, ključna je za stvaranje aminokiselina koje su, s druge strane, sastavni elementi proteina.', 0);
INSERT INTO property VALUES (1,11718, 'hr', 'definition','Tvar koja sadržava halogene atome.', 0);
INSERT INTO property VALUES (1,11734, 'hr', 'definition','Plin pri uobičajenim temperaturama; proizvodi narančasto-crveni talog kada se ohladi na temperature tekućeg zraka; nastaje prolaskom električnog pražnjenja kroz mješavinu sumpornih para i sumpornog dioksida pri niskim temperaturama.', 0);
INSERT INTO property VALUES (1,11745, 'hr', 'definition','Kemijski spoj koji pripada skupini polikloriranih bifenila, a upotrebljava se za proizvodnju električne opreme za koju je potreban dielektrični fluid, kao što su električni transformatori i kondenzatori, kao i u hidrauličnim strojevima, vakuumskim pumpama, kompresorima i  tekućinama za izmjenjivače topline.', 0);
INSERT INTO property VALUES (1,11752, 'hr', 'definition','Nemaligna progresivna, ireverzibilna, plućna bolest, pri kojoj dolazi do difuzne plućne fibroze, a prouzročuje ju udisanje azbestnih vlakana.', 0);
INSERT INTO property VALUES (1,11763, 'hr', 'definition','Stanje otrovanosti; ovo stanje izaziva otrov koji se može progutati, udahnuti, ubrizgati, ili se apsorbira kroz kožu.', 0);
INSERT INTO property VALUES (1,11771, 'hr', 'definition','Laboratorijsko određivanje učinaka tvari na specifične žive organizme.', 0);
INSERT INTO property VALUES (1,11779, 'hr', 'definition','Količina ugljika koji je kovalentno vezan u organskim spojevima u uzorku vode.', 0);
INSERT INTO property VALUES (1,11781, 'hr', 'definition','Čin ili proces pojednostavljivanja ili razbijanja molekula na manje dijelove, bilo prirodno ili umjetno.', 0);
INSERT INTO property VALUES (1,11782, 'hr', 'definition','Pokusi, ispitivanja ili vrednovanja koja se obavljaju u laboratoriju.', 0);
INSERT INTO property VALUES (1,11786, 'hr', 'definition','Skala za prikazivanje kvalitete okoliša pokazivanjem vrsta organizama koje su u njemu prisutni (npr. kako je čista neka rijeka).', 0);
INSERT INTO property VALUES (1,11787, 'hr', 'definition','Tvar koja može stupiti u kemijsku reakciju s kisikom.', 0);
INSERT INTO property VALUES (1,11788, 'hr', 'definition','Količina otopljenog kisika u potoku, rijeci ili jezeru pokazatelj je stupnja zdravlja tog potoka i njegove sposobnosti da podržava uravnotežen vodeni ekosustav. Kisik dolazi iz atmosfere otapanjem i nastaje fotosintezom vodenih biljaka. Najveća količina kisika koja se može održati u otopini u potoku naziva se koncentracijom zasićenja i, budući da je ona funkcija temperature, što je veća temperatura, manji je iznos zasićenja. Ispuštanje organskog otpada u potok nameće potrošnju kisika na potok. Ako se u potoku nalazi prevelika količina organske tvari, oksidacija otpada mikroorganizmima potrošit će kisik brže nego što se on može nadopuniti. Kada se to dogodi, otopljeni kisik je potrošen, a to dovodi do smrti viših oblika života.', 0);
INSERT INTO property VALUES (1,11789, 'hr', 'definition','Sposobnost raspadanja produženim izlaganjem svjetlu.', 0);
INSERT INTO property VALUES (1,11791, 'hr', 'definition','Tvar koja ne može tvoriti otopine, osobito u vodi.', 0);
INSERT INTO property VALUES (1,11792, 'hr', 'definition','Tvar koja se ne može mijenjati iz krutog ili tekućeg stanja u paru.', 0);
INSERT INTO property VALUES (1,11793, 'hr', 'definition','Tvar koja se teško pretvara u drugu, obično manje kompleksnu tvar.', 0);
INSERT INTO property VALUES (1,11794, 'hr', 'definition','Tvar sposobna za brzo mijenjanje od čvrstog ili tekućeg oblika u paru; koja ima visok tlak pare i nisko vrelište.', 0);
INSERT INTO property VALUES (1,11795, 'hr', 'definition','Sklonost metala da izgrize drugi metal kemijskim napadom.', 0);
INSERT INTO property VALUES (1,11798, 'hr', 'definition','Analiza koja se temelji na fizikalnim promjenama povezanima s kemijskim reakcijama.', 0);
INSERT INTO property VALUES (1,11807, 'hr', 'definition','Studija/istraživanje koje se temelji na eksperimentiranju.', 0);
INSERT INTO property VALUES (1,11809, 'hr', 'definition','1) Određivanje različitih veličina zrna u zrnatom materijalu.
INSERT INTO property VALUES (1,11812, 'hr', 'definition','Pustiti gnoj iz čira ili rane, ili uzeti primjerak tkiva ili organske tekućine za pregled.', 0);
INSERT INTO property VALUES (1,11813, 'hr', 'definition','Tvar koja se odlikuje određenim molekularnim sastavom.', 0);
INSERT INTO property VALUES (1,11814, 'hr', 'definition','Materijal koji ima sposobnost stvaranja dovoljne koncentracije zapaljivih para da se proizvede plamen ako se zapali.', 0);
INSERT INTO property VALUES (1,11816, 'hr', 'definition','Svojstva koja se mogu opažati organima osjeta.', 0);
INSERT INTO property VALUES (1,11835, 'hr', 'definition','Sustav percepcije zvuka, koji se prenosi pomoću Cortijevog organa u uhu sisavaca ili odgovarajućim osjetilnim receptorima na lageni (organu za sluh i ravnotežu) kod nižih kralježnjaka, dok je kod čovjeka  normalno osjetljiv na zvučne vibracije između 16 i 27.000 Hz u sekundi, ali je najosjetljiviji prema vibracijama između 2.000 i 5.000 Hz po sekundi, centralno ga provodi kohlearni ogranak slušnog živca, a koordinira se osobito u medijalnoj genikulatnoj jezgri.', 0);
INSERT INTO property VALUES (1,11836, 'hr', 'definition','Strukture, uključujući srce i krvne žile, koje osiguravaju kanale za protok krvi.', 0);
INSERT INTO property VALUES (1,11852, 'hr', 'definition','Podskupina picorna virusa koji izazivaju zarazu gastrointestinalnog trakta i koji se izlučuju s fekalijama, uključujući koksaki viruse, echo viruse i polio viruse; mogu biti prisutni u respiratorne bolesti, meningitis i neurološke bolesti.', 0);
INSERT INTO property VALUES (1,11868, 'hr', 'definition','Znanstveno proučavanje prirodnih procesa životinja.', 0);
INSERT INTO property VALUES (1,11869, 'hr', 'definition','Znanstveno proučavanja prirodnih procesa u biljkama.', 0);
INSERT INTO property VALUES (1,11875, 'hr', 'definition','Ukupan broj gena ili iznos genetskih informacija koje posjeduju svi reproduktivni članovi populacije organizama koji se spolno razmnožavaju.', 0);
INSERT INTO property VALUES (1,11879, 'hr', 'definition','Učinkovitost sluha.', 0);
INSERT INTO property VALUES (1,11889, 'hr', 'definition','Jedinica za izražavanje relativne razlike u jakosti, obično između akustičnih ili električnih signala, koja je deset puta veća od zajedničkog logaritma odnosa dvaju intenziteta.', 0);
INSERT INTO property VALUES (1,11896, 'hr', 'definition','Značajka zatvorenog prostora koja određuje njegovu sposobnost da omogući da se jasno čuje glazba i govor u tom prostoru.', 0);
INSERT INTO property VALUES (1,11909, 'hr', 'definition','Raspon frekvencija u buci koju emitira neki izvor.', 0);
INSERT INTO property VALUES (1,11912, 'hr', 'definition','Gibanje, obično nenamjerno i često neželjeno, dijelova strojeva i zgrada.', 0);
INSERT INTO property VALUES (1,11918, 'hr', 'definition','Buka koja nastaje gibanjem velikih količina zraka i upotrebom visokotlačnog zraka.', 0);
INSERT INTO property VALUES (1,11921, 'hr', 'definition','Buka koja dolazi iz izvora osim izvora buke koji se nadzire.', 0);
INSERT INTO property VALUES (1,11922, 'hr', 'definition','Buka koja odjekuje i snažno odzvanja, a izaziva je trenje između automobilskih guma i ceste.', 0);
INSERT INTO property VALUES (1,11923, 'hr', 'definition','Opća buka iz lokalnog izvora (kao što je buka u tvornici) koja ometa ljude koji žive u okolici.', 0);
INSERT INTO property VALUES (1,11930, 'hr', 'definition','Formulirana ili sustavna metoda sprječavanja učinaka izloženosti glasnim ili oštrim zvukovima.', 0);
INSERT INTO property VALUES (1,11958, 'hr', 'definition','Elektrana u kojoj se električnu energiju stvara opadanje i rast plime i oseke (morske mijene). Princip rada ovakve elektrane je taj da se voda u fazi plime iza brane ispušta u fazi oseke te pokreće turbinu, a turbina pokreće generator.', 0);
INSERT INTO property VALUES (1,11967, 'hr', 'definition','Besplatni obnovljivi izvor energije koju daje voda koja pada i koja pogoni turbine. Hidroenergija je najvažniji obnovljivi izvor energije zbog njezine najviše energetske učinkovitosti u pretvorbi energije. Postoje dvije vrste hidroelektrana:) elektrana za korištenje protoka vode rijeka, b) elektrane na pogon vodom iz akumulacijskog jezera (elektrane sa spremnikom/akumulacijom), kod kojih se priljev može regulirati uz pomoć spremnika. Uglavnom se iskorištavaju veće razlike u visinama, kao što su planinske rječice. Elektrane s akumulacijskim jezerima općenito imaju brane sa zemljanom ispunom ili betonskom branom. Iako se hidroenergija općenito može nazvati prihvatljivom za okoliš, postoje i neki problemi: a) Promjena razine podzemnih voda i punjenje korita rijeke zemljom. b) Opasnost od rušenja brane. c) Velika potražnja za zemljišnim prostorom za akumulacijsko jezero. d) Smanjenje, ali i djelomično povećanje vrijednosti rekreacijskih površina. Kako se hidroenergija u svijetu sve više ograničava, potražnja za energijom u svijetu raste, međutim, konačno udio hidroenergije će se smanjiti.', 0);
INSERT INTO property VALUES (1,11974, 'hr', 'definition','Mala elektrana za proizvodnju energije na lokalnoj razini.', 0);
INSERT INTO property VALUES (1,11975, 'hr', 'definition','Mlin kojem snagu daje velik kotač što ga pokreće voda koja se giba, posebno voda rijeke.', 0);
INSERT INTO property VALUES (1,11990, 'hr', 'definition','Proces kojim elektrana naposljetku prekida svoj rad.', 0);
INSERT INTO property VALUES (1,12004, 'hr', 'definition','Prirodno radioaktivan element.', 0);
INSERT INTO property VALUES (1,12006, 'hr', 'definition','Nuklearni reaktor koji proizvodi više fisionog materijala nego što troši, koristeći se brzim neutronima i stvaranjem plutonija-239 od urana-238, a time i povećava učinkovitost reaktora.', 0);
INSERT INTO property VALUES (1,12007, 'hr', 'definition','Mjesto ili regija koja je oštećena, kojoj je nanesena šteta ili je nepodobna za korištenje zbog uvođenja neželjenih tvari, posebice mikroorganizama, kemikalija, toksičnih i radioaktivnih materijala i otpada.', 0);
INSERT INTO property VALUES (1,12008, 'hr', 'definition','Suspendirane čestice u zraku. Čestice mogu biti u obliku prašine, dima ili magle.  Aerosoli u atmosferi su oblik u kojem se raspršuju onečišćujuće tvari kao što je dim.', 0);
INSERT INTO property VALUES (1,12011, 'hr', 'definition','Plinovito, tekuće ili kruto gorivo koje ima energetsku vrijednost dobivenu iz biološkog izvora. Organska tvar koja tvori žive organizme pruža potencijalni izvor energije koji se počinje eksploatirati kako bi se osigurao izvor energije za sve veću potražnju za energijom u svijetu. Primjer biogoriva je repičino ulje, koje se može iskorištavati umjesto dizelskog goriva u izmijenjenim motorima. Metil ester ovog ulja, metil ester uljane repice (RME), može se iskorištavati u neizmijenjenom dizel motoru, a ponekad ga nazivaju biodizelom. Ostala biogoriva su bioplin i gasohol.', 0);
INSERT INTO property VALUES (1,12013, 'hr', 'definition','Smanjenje populacije stabala u šumama što ga prouzročuju kisele oborine, šumski požari, onečišćenja zraka, krčenje šuma, štetnici i bolesti stabala, biljni i životinjski svijet i dr.', 0);
INSERT INTO property VALUES (1,12017, 'hr', 'definition','Bezbojna tekućina koja se miješa s vodom, a upotrebljava se kao reagens i otapalo. Također poznat pod nazivima alkohol, etilni alkohol, žitni alkohol.', 0);
INSERT INTO property VALUES (1,12022, 'hr', 'definition','Onečišćenje izazvano plinova koji se ispuštaju u atmosferu iz vozila s pogonom na motor s unutarnjim izgaranjem.', 0);
INSERT INTO property VALUES (1,12026, 'hr', 'definition','Taloženje prašine ili lebdećih čestica iz atmosfere.', 0);
INSERT INTO property VALUES (1,12027, 'hr', 'definition','Vozila čijim radom se ne ispuštaju onečišćujuće tvari u atmosferu.', 0);
INSERT INTO property VALUES (1,12045, 'hr', 'definition','Proučavanje paleoklima kroz geološka doba, te uzroka njihovih varijacija, ili na lokalnoj ili na globalnoj razini. Uključuje tumačenje glacijalnih naslaga, fosila i sedimentoloških te drugih vrsta podataka.', 0);
INSERT INTO property VALUES (1,12060, 'hr', 'definition','Klima koja se odlikuje toplim ljetima, hladnim zimama i s malo kiše, tipična za unutrašnjost kontinenta.', 0);
INSERT INTO property VALUES (1,12061, 'hr', 'definition','Vrsta klime čija je značajka nedovoljna vlažnost za podržavanje značajnog biljnog života; to jest, klima ekstremne suhoće.', 0);
INSERT INTO property VALUES (1,12062, 'hr', 'definition','Klima koja se odlikuje stalnim temperaturama, obilnim kišama i vrlo kratkim suhim godišnjim dobom.', 0);
INSERT INTO property VALUES (1,12063, 'hr', 'definition','Tip klime za koju su karakteristična topla, suha, sunčana ljeta i zimska kišna sezona; u osnovi, ona je suprotna od monsunske klime. Također poznata kao etezijska klima.', 0);
INSERT INTO property VALUES (1,12064, 'hr', 'definition','Vrlo općenito, klima relativno visokih visina; planinske klima razlikuju se po odstupanju njihovih obilježja od onih okolnih nizina, i jedna zajednička osnova za ovu razliku jest razrjeđivanje atmosfere; osim toga, postoje velike razlike prema različitim geografskim širinama, nadmorskim visinama i izloženosti suncu; stoga ne postoji jedna, jasno definirana, planinska klima. Također poznata pod nazivom gorska klima.', 0);
INSERT INTO property VALUES (1,12065, 'hr', 'definition','Regionalna klima koja je pod prevladavajućim utjecajem mora, to jest, klima čije je obilježje utjecaj oceana; antiteza kontinentalne klime.', 0);
INSERT INTO property VALUES (1,12066, 'hr', 'definition','Klima srednjih geografskih širina; klima između krajnosti tropske klime i polarne klime.', 0);
INSERT INTO property VALUES (1,12067, 'hr', 'definition','Klima tipična za ekvatorska i tropska područja, to jest, klima s kontinuirano visokim temperaturama i znatnim oborinama, barem tijekom dijela godine.', 0);
INSERT INTO property VALUES (1,12082, 'hr', 'definition','Temperaturna inverzija u atmosferi u kojoj temperatura, umjesto da pada, raste s visinom iznad tla. Kada je hladniji i teži zrak dolje, ne postoji tendencija da se oblikuju struje prema gore i turbulencija se potiskuje. Inverzije se često formiraju u kasno poslijepodne, kada je zračenje emitirano sa tla veće od zračenja primljenog od sunca na zalazu. Inverzije uzrokuju i katabatski vjetrovi, to jest, hladni vjetrovi koji pušu niz padinu u dolinu, te anticiklone. U slojevima inverzije i vertikalne i horizontalne difuzije se inhibiraju pa onečišćujuće tvari ostaju ''zarobljene'', ponekad i duže vrijeme. Inverzije lakše ''zarobe'' ispuštanja štetnih tvari na niskim razinama nego ispuštanja na visokim razinama, pa se zbog toga na postrojenjima grade visoki dimnjaci. Nadalje, ispuštanja na visokoj razini u inverziju obično ostaju na visokoj razini jer nema vertikalnog miješanja.', 0);
INSERT INTO property VALUES (1,12084, 'hr', 'definition','Oluja koja nastaje snažnim zračnim strujama koje se dižu i čija su obilježja grmljavina i munje, a obično i jaka kiša ili tuča.', 0);
INSERT INTO property VALUES (1,12091, 'hr', 'definition','Sloj leda, općenito  proziran i gladak, ali obično sadrži neke zračne džepove, koji se stvaraju na izloženim predmetima smrzavanjem sloja jako ohlađene vode koju nanosi kiša, rosulja ili magla, ili eventualno kondenzirana iz vrlo ohlađene  vodene pare.', 0);
INSERT INTO property VALUES (1,12096, 'hr', 'definition','Proces vađenja podzemne vode iz izvora.', 0);
INSERT INTO property VALUES (1,12098, 'hr', 'definition','Gibanje površinske vode u tlo ili stijene kroz pukotine i pore.', 0);
INSERT INTO property VALUES (1,12104, 'hr', 'definition','Voda koja zauzima pore, šupljine, pukotine i druge prostore u kamenju Zemljine kore. Uključuje oborinsku vodu iz atmosfere koja se procijedila kroz tlo, vodu koja se digla iz dubokih magmatskih izvora oslobođena tijekom magmatske aktivnosti i fosilnu vodu zadržanu u sedimentnim stijenama od njihova formiranja. Prisutnost podzemnih voda nužna je za doslovno sve vremenske procese. Freatska voda (voda temeljnica) je sinonim za podzemne vode i najvažniji je izvor svake vodoopskrbe.', 0);
INSERT INTO property VALUES (1,12107, 'hr', 'definition','Mjere predostrožnosti, postupci ili uređaji za sprječavanje ili smanjenje štete za cjelovitost okoliša u područjima odvodnje koje se upotrebljavaju za crpljenje vode, kao spremnici ili bazeni.', 0);
INSERT INTO property VALUES (1,12108, 'hr', 'definition','Nivo podzemne vode je sam po sebi osjetljiv na onečišćenje od djelatnosti korištenja zemljišta. Sanacija je vrlo skupa i često nepraktična. Zbog toga je prevencija onečišćenja ključna u učinkovitom gospodarenju podzemnim vodama.', 0);
INSERT INTO property VALUES (1,12122, 'hr', 'definition','Ukupna količina vode koje teče iz poriječja u razdoblju poplave.', 0);
INSERT INTO property VALUES (1,12123, 'hr', 'definition','Punjenje ili djelomično punjenje muljem nekog spremnika koji prima sitnozrnati sediment što ga donose potoci i površinsko otjecanje.', 0);
INSERT INTO property VALUES (1,12128, 'hr', 'definition','Znanost i tehnologija koja se bavi mjerenjem specifične težine, osobito tekućina.', 0);
INSERT INTO property VALUES (1,12131, 'hr', 'definition','Hidrološki procesi kojima nastaju poplave rijeka opsežno su proučavani i predloženo je nekoliko koncepata modeliranja. Standardni postupak za predviđanje poplava sastoji se od kalibracije parametara modela određenoga riječnog sliva na reprezentativnoj skupini povijesnih hidrometrijskih podataka i nakon toga je ovaj kalibrirani model primijenjen u okruženju realnog vremena.', 0);
INSERT INTO property VALUES (1,12135, 'hr', 'definition','Konfiguracija ili tlocrtni raspored prirodnih vodotoka u nekom području. Odnosi se na lokalne geološke i geomorfološke značajke i povijest. Sinonim: način odvodnje.', 0);
INSERT INTO property VALUES (1,12139, 'hr', 'definition','Znanstveno proučavanje sedimentnih stijena i procesa kojima su one nastale; opis, klasifikacija, podrijetlo i tumačenje sedimenata.', 0);
INSERT INTO property VALUES (1,12145, 'hr', 'definition','1) Poriječje nekog vodotoka. 
INSERT INTO property VALUES (1,12151, 'hr', 'definition','Slatka voda u kojoj je kupanje izričito odobreno ili u kojoj nije zabranjeno i gdje se tradicionalno kupa velik broj kupača. Voda u takvim područjima mora ispunjavati određene  standarde kvalitete koji se odnose na kemijske, mikrobiološke i fizikalne parametre.', 0);
INSERT INTO property VALUES (1,12152, 'hr', 'definition','Voda čiji je vlasnik i upravitelj državno vladino tijelo ili služba.', 0);
INSERT INTO property VALUES (1,12153, 'hr', 'definition','Vodno tijelo koje je u vlasništvu i koje održava pojedinac ili tijelo koje nije državna vlast.', 0);
INSERT INTO property VALUES (1,12161, 'hr', 'definition','Uobičajeni izraz koji se u Škotskoj i Irskoj upotrebljava za  preplavljeno, spužvasto tlo, koje se uglavnom sastoji od biljnih tvari koje se raspadaju, osobito trske, šaša i tresetne  mahovine.', 0);
INSERT INTO property VALUES (1,12165, 'hr', 'definition','Svaki mehanički postupak ili proces kojim se dio soli ili sva sol uklanja iz vode.', 0);
INSERT INTO property VALUES (1,12166, 'hr', 'definition','Morska voda u kojoj je kupanje izričito odobreno ili u kojoj nije zabranjeno i gdje se tradicionalno kupa velik broj kupača. Voda u takvim područjima mora ispunjavati određene standarde kvalitete koji se odnose na kemijske, mikrobiološke i fizikalne parametre.', 0);
INSERT INTO property VALUES (1,12170, 'hr', 'definition','Ušće ili ispust iz rijeke, odvodna cijev, kanalizacijski odvod ili bilo koje drugo mjesto na kojem se nalazi odvod otpadnih voda ili se one ispuštaju u ocean.', 0);
INSERT INTO property VALUES (1,12171, 'hr', 'definition','Iskorištavanje prehrambenih resursa, mineralnih sirovina te energije i izvora vode iz oceana.', 0);
INSERT INTO property VALUES (1,12174, 'hr', 'definition','Redovno gibanje morskih valova koje stvaraju naleti vjetra na otvorenom koje se prenosi na znatne udaljenosti od polja nastanka i u drugo polje vjetra. Valovi se odlikuju relativno glatkim, uglavnom neprekinutim, visinama i prilično redovitim valnim duljinama, ali duljina vala se povećava, a visina smanjuje dok se on udaljava od mjesta nastanka. Valovi nastali djelovanjem lokalnog vjetra mogu se natkriliti nad visoke valove dok se ovi približavaju obali, pa stvaraju oštrije vrhove (kreste) vala i nemirno more.', 0);
INSERT INTO property VALUES (1,12183, 'hr', 'definition','Onečišćenje općenito širokog dijela potoka u blizini njegovog ispusta koji je pod utjecajem plime vodnog tijela u koje se ulijeva. Mnogi estuariji su postali jako onečišćen otpadom nastalim u gusto naseljenim područjima.', 0);
INSERT INTO property VALUES (1,12189, 'hr', 'definition','Upravljanje ili gospodarenje vodotokom ili tokom tekuće vode.', 0);
INSERT INTO property VALUES (1,12198, 'hr', 'definition','Uklanjanje nataloženih naslaga kamenja i drugih krhotina vodom koja teče velikom brzinom; upotrebljava se za čišćenje vodenih kanala i bušotina.', 0);
INSERT INTO property VALUES (1,12199, 'hr', 'definition','Komora s vratima na obje strane koja povezuje dva dijela kanala ili nekog drugog vodotoka, koja služi za podizanje ili spuštanje razine vode u svakoj sekciji.', 0);
INSERT INTO property VALUES (1,12202, 'hr', 'definition','Košenje s pomoću kose ili stroja u intervalima raslinja i trave koja raste na obalama i improviziranim nasipima kanala za navodnjavanje i odvodnju ili obrađivanim područjima.', 0);
INSERT INTO property VALUES (1,12204, 'hr', 'definition','Konstrukcijski radovi namijenjeni zaštiti obala rijeka ili padinama nasipa rijeka od erozije vodnog toka, poplava itd.', 0);
INSERT INTO property VALUES (1,12210, 'hr', 'definition','Bazen za pohranu vode.', 0);
INSERT INTO property VALUES (1,12213, 'hr', 'definition','Ispuštanje vode iz spremnika pomoću drenažnih cijevi koje se nalaze na dnu bazena i kontroliraju sustavom zapornica koje prema potrebi osiguravaju pražnjenje bazena u određenom vremenskom razdoblju s obzirom na uvjete nizvodno od spremnika.', 0);
INSERT INTO property VALUES (1,12214, 'hr', 'definition','Složen niz reakcija između vode i metalnih površina i materijala u kojima je voda pohranjena ili se prevozi. Proces korozije je reakcija oksidacije i redukcije koja vraća rafinirani ili prerađeni metal u njegovo stabilnije stanje rudače. S obzirom na korozijski potencijal pitke vode, najveću zabrinutost izaziva mogućnost prisutnosti toksičnih metala, kao što su olovo i bakar.', 0);
INSERT INTO property VALUES (1,12215, 'hr', 'definition','Napredak, ili poboljšanje, stanja okoliša i ravnoteže vode.', 0);
INSERT INTO property VALUES (1,12216, 'hr', 'definition','Okus vodi mogu dati strana tijela, kao što su organski spojevi, anorganske soli ili otopljeni plinovi. Ti  materijali mogu biti iz domaćih, poljoprivrednih ili prirodnih izvora. Neke tvari koje se u prirodnom obliku nalaze u podzemnim vodama, a koje nisu nužno štetne, mogu vodi davati neugodan okus ili neko nepoželjno svojstvo. Magnezijev sulfat, natrijev sulfat i natrijev klorid samo su neke od tih tvari. U trenutku korištenja prihvatljiva voda ne smije imati nikakav nepoželjan okus.', 0);
INSERT INTO property VALUES (1,12219, 'hr', 'definition','Rast zelenog morskog planktona koji je toksičan i često poguban za ribe, možda potaknut dodatavanjem hranjivih tvari.', 0);
INSERT INTO property VALUES (1,12220, 'hr', 'definition','Materijal suspendiran u tekućini komponentama turbulentnih struja koje se gibaju prema gore ili koloidnim suspenzijama.', 0);
INSERT INTO property VALUES (1,12226, 'hr', 'definition','Stupnj rastopljenih soli u vodi mjeri se u promilima težine.', 0);
INSERT INTO property VALUES (1,12231, 'hr', 'definition','Konstrukcija u kojoj se skuplja voda.', 0);
INSERT INTO property VALUES (1,12240, 'hr', 'definition','Mlaz vode koja se tjera prema gore u zrak kroz mali otvor, osobito kao ukras ili konstrukcija u jezeru ili bazenu iz kojeg istječe.', 0);
INSERT INTO property VALUES (1,12248, 'hr', 'definition','Umjetno ili prirodno vodeno područje koje se upotrebljava za pohranjivanje vode za upotrebu u kućanstvima ili industriji.', 0);
INSERT INTO property VALUES (1,12254, 'hr', 'definition','Uklanjanje minerala iz vode s pomoću kemikalija, ionskom izmjenom ili postupcima destilacije.', 0);
INSERT INTO property VALUES (1,12259, 'hr', 'definition','Proces u kojem se upotrijebljena ili potrošena voda obrađuje na mjestu nastanka ili gdje je proizvedena, pomoću septičke jame ili nekoga drugog sustava za uklanjanje ili smanjenje učinka sastavnica otpada na ljudsko zdravlje i okoliš.', 0);
INSERT INTO property VALUES (1,12263, 'hr', 'definition','Spremnik za čuvanje vode koja padne u obliku kiše za vrijeme oluje.', 0);
INSERT INTO property VALUES (1,12273, 'hr', 'definition','Kanali za uklanjanje kišnice.', 0);
INSERT INTO property VALUES (1,12275, 'hr', 'definition','Kanalizacijski sustav s posebnim cijevima za skupljanje površinskih voda i kanalizacijske otpadne vode.', 0);
INSERT INTO property VALUES (1,12276, 'hr', 'definition','Kanalizacija koja služi kao sanitarna odvodnja i kanalizacija za oborinske vode, ili kao industrijska kanalizacija i kanalizacija za oborinske vode.', 0);
INSERT INTO property VALUES (1,12277, 'hr', 'definition','Dodavanja zraka kanalizaciji ili vodi kako bi se povećala njihova razina otopljenog kisika.', 0);
INSERT INTO property VALUES (1,12278, 'hr', 'definition','Postupak u kojem se upotrebljava prirodni sustav ili mehanički uređaj za prikupljanje, obradu i ispuštanje te oporabu otpadnih voda iz pojedinačne stambene jedinice bez upotrebe komunalnog kanalizacijskog sustava ili središnjeg objekta za pročiščavanje.', 0);
INSERT INTO property VALUES (1,12279, 'hr', 'definition','Bazen u kojem se suspendirane tvari uklanjaju ili mirnim taloženjem ili kontinuiranim protokom uz velike brzine, te produženo vrijeme zadržavanja da se omogući taloženje.', 0);
INSERT INTO property VALUES (1,12764, 'hr', 'definition','Mjere ili tehnike koje se provode radi smanjenja rizika od ozljeda, gubitaka i opasnosti za osobe, imovinu ili okoliš i bilo koji objekt ili mjesto koje uključuju preradu, proizvodnju i obradu robe ili dobara.', 0);
INSERT INTO property VALUES (1,15027, 'hr', 'definition','Razvrstavanje po veličini ili razvrstavanje čestica  suspenzijom u tekućini (tekućih ili u obliku plina), pri čemu se veće čestice obično odvajaju tako što potonu na dno.', 0);
